INSERT INTO verses (id, number, text, chapter_id) VALUES (10254, 1, "Adão, Sete, Enos,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10255, 2, "Cainã, Malaleel, Jarede,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10256, 3, "Enoque, Matusalém, Lameque, Noé.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10257, 4, "Estes foram os filhos de Noé: Sem, Cam e Jafé.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10258, 5, "Estes foram os filhos de Jafé: Gômer, Magogue, Madai, Javã, Tubal, Meseque e Tirás.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10259, 6, "Estes foram os filhos de Gômer: Asquenaz, Rifate e Togarma.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10260, 7, "Estes foram os filhos de Javã: Elisá, Társis, Quitim e Rodanim.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10261, 8, "Estes foram os filhos de Cam: Cuxe, Mizraim, Fute e Canaã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10262, 9, "Estes foram os filhos de Cuxe: Sebá, Havilá, Sabtá, Raamá e Sabtecá. Estes foram os filhos de Raamá: Sabá e Dedã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10263, 10, "Cuxe gerou Ninrode, o primeiro homem poderoso na terra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10264, 11, "Mizraim gerou os luditas, os anamitas, os leabitas, os naftuítas,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10265, 12, "os patrusitas, os casluítas, dos quais se originaram os filisteus, e os caftoritas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10266, 13, "Canaã gerou Sidom, seu filho mais velho, e Hete,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10267, 14, "como também os jebuseus, os amorreus, os girgaseus,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10268, 15, "os heveus, os arqueus, os sineus,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10269, 16, "os arvadeus, os zemareus e os hamateus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10270, 17, "Estes foram os filhos de Sem: Elão, Assur, Arfaxade, Lude e Arã. Estes foram os filhos de Arã: Uz, Hul, Géter e Meseque.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10271, 18, "Arfaxade gerou Salá, e este gerou Héber.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10272, 19, "A Héber nasceram dois filhos: Um deles se chamou Pelegue, porque em sua época a terra foi dividida; seu irmão chamou-se Joctã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10273, 20, "Joctã gerou Almodá, Salefe, Hazarmavé, Jerá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10274, 21, "Hadorão, Uzal, Dicla,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10275, 22, "Obal, Abimael, Sabá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10276, 23, "Ofir, Havilá e Jobabe. Todos esses foram filhos de Joctã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10277, 24, "Sem, Arfaxade, Salá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10278, 25, "Héber, Pelegue, Reú,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10279, 26, "Serugue, Naor, Terá", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10280, 27, "e Abrão, que é Abraão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10281, 28, "Estes foram os filhos de Abraão: Isaque e Ismael.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10282, 29, "Foram estes os seus descendentes: Nebaiote, o filho mais velho de Ismael, Quedar, Adbeel, Mibsão,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10283, 30, "Misma, Dumá, Massá, Hadade, Temá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10284, 31, "Jetur, Nafis e Quedemá. Esses foram os filhos de Ismael.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10285, 32, "Estes foram os filhos de Abraão com sua concubina Quetura: Zinrã, Jocsã, Medã, Midiã, Isbaque e Suá. Foram estes os filhos de Jocsã: Sabá e Dedã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10286, 33, "Foram estes os filhos de Midiã: Efá, Éfer, Enoque, Abida e Elda. Todos esses foram descendentes de Quetura.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10287, 34, "Abraão gerou Isaque. Estes foram os filhos de Isaque: Esaú e Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10288, 35, "Estes foram os filhos de Esaú: Elifaz, Reuel, Jeús, Jalão e Corá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10289, 36, "Estes foram os filhos de Elifaz: Temã, Omar, Zefô, Gaetã e Quenaz; e Amaleque, de Timna, sua concumbina.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10290, 37, "Estes foram os filhos de Reuel: Naate, Zerá, Samá e Mizá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10291, 38, "Estes foram os filhos de Seir: Lotã, Sobal, Zibeão, Aná, Disom, Eser e Disã. Lotã tinha uma irmã chamada Timna.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10292, 39, "Estes foram os filhos de Lotã: Hori e Homã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10293, 40, "Estes foram os filhos de Sobal: Alvã, Manaate, Ebal, Sefô e Onã. Estes foram os filhos de Zibeão: Aiá e Aná.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10294, 41, "Este foi o filho de Aná: Disom. Estes foram os filhos de Disom: Hendã, Esbã, Itrã e Querã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10295, 42, "Estes foram os filhos de Eser: Bilã, Zaavã e Acã. Estes foram os filhos de Disã: Uz e Arã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10296, 43, "Estes foram os reis que reinaram no território de Edom antes que os israelitas tivessem um rei: Belá, filho de Beor. Sua cidade chamava-se Dinabá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10297, 44, "Belá morreu, e Jobabe, filho de Zerá, de Bozra, foi o seu sucessor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10298, 45, "Jobabe morreu, e Husã, da terra dos temanitas, foi o seu sucessor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10299, 46, "Husã morreu, e Hadade, filho de Bedade, que tinha derrotado os midianitas na terra de Moabe, foi o seu sucessor. Sua cidade chamava-se Avite.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10300, 47, "Hadade morreu, e Samlá de Masreca foi o seu sucessor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10301, 48, "Samlá morreu, e Saul, de Reobote, próxima ao Eufrates, foi o seu sucessor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10302, 49, "Saul morreu, e Baal-Hanã, filho de Acbor, foi o seu sucessor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10303, 50, "Baal-Hanã morreu, e Hadade foi o seu sucessor. Sua cidade chamava-se Paú, e o nome de sua mulher era Meetabel, filha de Matrede e neta de Mezaabe.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10304, 51, "Após a morte de Hadade, Edom foi governada pelos seguintes chefes: Timna, Alva, Jetete,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10305, 52, "Oolibama, Elá, Pinom,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10306, 53, "Quenaz, Temã, Mibzar,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10307, 54, "Magdiel e Irã. Foram esses os chefes de Edom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10308, 1, "Estes foram os filhos de Israel: Rúben, Simeão, Levi, Judá, Issacar, Zebulom,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10309, 2, "Dã, José, Benjamim, Naftali, Gade e Aser.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10310, 3, "Estes foram os filhos de Judá: Er, Onã e Selá. Ele teve esses três filhos com uma mulher cananéia, a filha de Suá. Mas o Senhor reprovou a conduta perversa de Er, filho mais velho de Judá, e por isso o matou.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10311, 4, "Tamar, nora de Judá, deu-lhe os filhos Perez e Zerá. A Judá nasceram ao todo cinco filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10312, 5, "Estes foram os filhos de Perez: Hezrom e Hamul.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10313, 6, "Estes foram os filhos de Zerá: Zinri, Etã, Hemã, Calcol e Darda. Foram cinco ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10314, 7, "O filho de Carmi foi Acar. Ele causou desgraça a Israel ao violar a proibição de se apossar das coisas consagradas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10315, 8, "Este foi o filho de Etã: Azarias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10316, 9, "Os filhos que nasceram a Hezrom foram Jerameel, Rão e Calebe.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10317, 10, "Rão gerou Aminadabe, e Aminadabe gerou Naassom, o líder da tribo de Judá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10318, 11, "Naassom gerou Salmom, Salmom gerou Boaz,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10319, 12, "Boaz gerou Obede, e Obede gerou Jessé.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10320, 13, "Jessé gerou Eliabe, o seu filho mais velho; o segundo foi Abinadabe, o terceiro Siméia,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10321, 14, "o quarto Natanael, o quinto Radai,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10322, 15, "o sexto Ozém, e o sétimo Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10323, 16, "As irmãs deles foram Zeruia e Abigail. Os três filhos de Zeruia foram Abisai, Joabe e Asael.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10324, 17, "Abigail deu à luz Amasa, filho do ismaelita Jéter.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10325, 18, "Calebe, filho de Hezrom, teve uma filha chamada Jeriote com sua mulher Azuba. Estes foram os filhos de Azuba: Jeser, Sobabe e Ardom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10326, 19, "Quando Azuba morreu, Calebe tomou por mulher a Efrate, com quem teve Hur.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10327, 20, "Hur gerou Uri, e Uri gerou Bezalel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10328, 21, "Depois disso, Hezrom, aos sessenta anos, tomou por mulher a filha de Maquir, pai de Gileade e ela deu-lhe um filho chamado Segube.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10329, 22, "Segube gerou Jair, que governou vinte e três cidades em Gileade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10330, 23, "Gesur e Arã conquistaram Havote-Jair, bem como Quenate e os povoados ao redor, ao todo sessenta cidades. Todos esses foram descendentes de Maquir, pai de Gileade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10331, 24, "Depois que Hezrom morreu em Calebe-Efrata, Abia, a mulher de Hezrom, deu-lhe Asur, fundador de Tecoa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10332, 25, "Estes foram os filhos de Jerameel, o filho mais velho de Hezrom: Rão, o mais velho, Buna, Orém, Ozém e Aías.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10333, 26, "Jerameel teve outra mulher, chamada Atara, que foi a mãe de Onã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10334, 27, "Estes foram os filhos de Rão, o filho mais velho de Jerameel: Maaz, Jamim e Equer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10335, 28, "Estes foram os filhos de Onã: Samai e Jada. Estes foram os filhos de Samai: Nadabe e Abisur.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10336, 29, "O nome da mulher de Abisur era Abiail. Ela deu-lhe dois filhos: Abã e Molide.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10337, 30, "Estes foram os filhos de Nadabe: Selede e Apaim. Selede morreu sem filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10338, 31, "O filho de Apaim foi Isi, pai de Sesã, pai de Alai.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10339, 32, "Estes foram os filhos de Jada, irmão de Samai: Jéter e Jônatas. Jéter morreu sem filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10340, 33, "Estes foram os filhos de Jônatas: Pelete e Zaza. Foram esses os descendentes de Jera-meel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10341, 34, "Sesã não teve filhos, apenas filhas. Tinha ele um escravo egípcio chamado Jará,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10342, 35, "a quem deu uma de suas filhas por mulher. E ela deu-lhe um filho chamado Atai.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10343, 36, "Atai gerou Natã, Natã gerou Zabade,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10344, 37, "Zabade gerou Eflal, Eflal gerou Obede,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10345, 38, "Obede gerou Jeú, Jeú gerou Azarias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10346, 39, "Azarias gerou Helez, Helez gerou Eleasa,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10347, 40, "Eleasa gerou Sismai, Sismai gerou Salum,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10348, 41, "Salum gerou Jecamias, e Jecamias gerou Elisama.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10349, 42, "Estes foram os filhos de Calebe, irmão de Jerameel: Messa, o mais velho, que foi o pai de Zife, e seu filho Maressa, pai de Hebrom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10350, 43, "Estes foram os filhos de Hebrom: Corá, Tapua, Requém e Sema.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10351, 44, "Sema gerou Raão, pai de Jorqueão. Requém gerou Samai.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10352, 45, "O filho de Samai foi Maom, e Maom foi o pai de Bete-Zur.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10353, 46, "A concubina de Calebe, Efá teve três filhos: Harã, Mosa e Gazez. Harã gerou Gazez.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10354, 47, "Estes foram os filhos de Jadai: Regém, Jotão, Gesã, Pelete, Efá e Saafe.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10355, 48, "A concubina de Calebe, Maaca teve dois filhos: Seber e Tiraná.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10356, 49, "Ela também teve Saafe, pai de Madmana, e Seva, pai de Macbena e de Gibeá. A filha de Calebe chamava-se Acsa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10357, 50, "Calebe teve também estes outros descendentes: Os filhos de Hur, o filho mais velho de Efrate: Sobal, fundador de Quiriate-Jearim,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10358, 51, "Salma, fundador de Belém, e Harefe, fundador de Bete-Gader.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10359, 52, "Os descendentes de Sobal, fundador de Quiriate-Jearim: O povo de Haroé, metade dos manaatitas,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10360, 53, "e os clãs de Quiriate-Jearim: os itritas, os fateus, os sumateus e os misraeus. Desses descenderam os zoratitas e os estaoleus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10361, 54, "Os descendentes de Salma: O povo de Belém e de Atrote-Bete-Joabe, os netofatitas, metade dos manaatitas, os zoreus,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10362, 55, "e os clãs dos escribas que viviam em Jabez: os tiratitas, os simeatitas e os sucatitas. Esses foram os queneus, descendentes de Hamate, antepassado da família de Recabe.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10363, 1, "Estes foram os filhos de Davi nascidos em Hebrom: O seu filho mais velho era Amnom, filho de Ainoã de Jezreel; o segundo, Daniel, de Abigail, de Carmelo;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10364, 2, "o terceiro, Absalão, de Maaca, filha de Talmai, rei de Gesur; o quarto, Adonias, de Hagite;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10365, 3, "o quinto, Sefatias, de Abital; e o sexto, Itreão, de sua mulher Eglá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10366, 4, "São esses os seis filhos de Davi que nasceram em Hebrom, onde ele reinou sete anos e seis meses. E, em Jerusalém,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10367, 5, "onde Davi reinou trinta e três anos, nasceram-lhe os seguintes filhos: Siméia, Sobabe, Natã e Salomão, os quatro filhos que ele teve com Bate-Seba, filha de Amiel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10368, 6, "Davi teve ainda mais nove filhos: Ibar, Elisua, Elpalete,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10369, 7, "Nogá, Nefegue, Jafia,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10370, 8, "Elisama, Eliada e Elifelete.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10371, 9, "Todos esses foram filhos de Davi, sem contar os filhos que teve com suas concubinas, e sua filha Tamar, irmã deles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10372, 10, "O filho de Salomão foi Roboão; o filho de Roboão foi Abias; o filho de Abias, Asa; o filho de Asa, Josafá;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10373, 11, "o filho de Josafá, Jeorão; o filho de Jeorão, Acazias; o filho de Acazias, Joás;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10374, 12, "o filho de Joás, Amazias; o filho de Amazias, Azarias; o filho de Azarias, Jotão;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10375, 13, "o filho de Jotão, Acaz; o filho de Acaz, Ezequias; o filho de Ezequias, Manassés;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10376, 14, "o filho de Manassés, Amom; o filho de Amom, Josias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10377, 15, "Os filhos de Josias foram: Joanã, o primeiro, Jeoaquim, o segundo, Zedequias, o terceiro, e Salum, o quarto.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10378, 16, "Os sucessores de Jeoaquim foram: Joaquim e Zedequias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10379, 17, "Estes foram os filhos de Joaquim, que foi levado para o cativeiro: Sealtiel,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10380, 18, "Malquirão, Pedaías, Senazar, Jecamias, Hosama e Nedabias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10381, 19, "Estes foram os filhos de Pedaías: Zorobabel e Simei. Estes foram os filhos de Zorobabel: Mesulão, Hananias e Selomite, irmã deles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10382, 20, "Teve ainda mais cinco filhos: Hasubá, Oel, Berequias, Hasadias e Jusabe-Hesede.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10383, 21, "Estes foram os descendentes de Hananias: Pelatias e Jesaías, e os filhos de Refaías, de Arnã, de Obadias e de Secanias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10384, 22, "Estes foram os descendentes de Secanias: Semaías e seus filhos Hatus, Igal, Bariá, Nearias e Safate; seis descendentes ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10385, 23, "Estes foram os três filhos de Nearias: Elioenai, Ezequias e Azricão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10386, 24, "Estes foram os sete filhos de Elioenai: Hodavias, Eliasibe, Pelaías, Acube, Joanã, Delaías e Anani.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10387, 1, "Estes foram os descendentes de Judá: Perez, Hezrom, Carmi, Hur e Sobal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10388, 2, "Reaías, filho de Sobal, gerou Jaate, e Jaate gerou Aumai e Laade. Esses foram os clãs dos zoratitas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10389, 3, "Estes foram os filhos de Etã: Jezreel, Isma e Idbás. A irmã deles chamava-se Hazelelponi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10390, 4, "Depois teve Penuel, pai de Gedor, e Ézer, pai de Husá. Esses foram os descendentes de Hur, o filho mais velho de Efrate e pai de Belém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10391, 5, "Asur, fundador de Tecoa, teve duas mulheres: Helá e Naará.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10392, 6, "Naará lhe deu Auzã, Héfer, Temeni e Haastari. Esses foram os filhos de Naará.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10393, 7, "Estes foram os filhos de Helá: Zerete, Zoar, Etnã", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10394, 8, "e Coz, que gerou Anube e Zobeba e os clãs de Aarel, filho de Harum.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10395, 9, "Jabez foi o homem mais respeitado de sua família. Sua mãe lhe deu o nome de Jabez, dizendo: ""Com muitas dores o dei à luz"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10396, 10, "Jabez orou ao Deus de Israel: ""Ah, abençoa-me e aumenta as minhas terras! Que a tua mão esteja comigo, guardando-me de males e livrando-me de dores"". E Deus atendeu ao seu pedido.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10397, 11, "Quelube, irmão de Suá, gerou Meir, pai de Estom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10398, 12, "Estom gerou Bete-Rafa, Paséia e Teína, fundador de Ir-Naás. Esses habitaram em Reca.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10399, 13, "Estes foram os filhos de Quenaz: Otoniel e Seraías. Estes foram os filhos de Otoniel: Hatate e Meonotai.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10400, 14, "Meonotai gerou Ofra. Seraías gerou Joabe, fundador de Ge-Harasim que recebeu esse nome porque os seus habitantes eram artesãos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10401, 15, "Estes foram os filhos de Calebe, filho de Jefoné: Iru, Elá e Naã. O filho de Elá foi Quenaz.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10402, 16, "Estes foram os filhos de Jealelel: Zife, Zifa, Tiria e Asareel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10403, 17, "Estes foram os filhos de Ezra: Jéter, Merede, Éfer e Jalom. Merede casou-se com Bitia, filha do faraó, e teve os seguintes filhos: Miriã, Samai e Isbá, fundador de Estemoa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10404, 18, "Sua mulher judia deu à luz a Jerede, fundador de Gedor, a Héber, fundador de Socó, e a Jecutiel, fundador de Zanoa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10405, 19, "Estes foram os filhos da mulher de Hodias, irmã de Naã: o pai de Queila, o garmita, e Estemoa, o maacatita.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10406, 20, "Estes foram os filhos de Simão: Amnom, Rina, Bene-Hanã e Tilom. Estes foram os filhos de Isi: Zoete e Ben-Zoete.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10407, 21, "Estes foram os filhos de Selá, filho de Judá: Er, pai de Leca; Lada, pai de Maressa. Selá também foi antepassado dos clãs dos que trabalhavam com linho em Bete-Asbéia,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10408, 22, "de Joquim, dos homens de Cozeba, de Joás e de Sarafe, que governavam em Moabe e Jasubi-Leém. ( Estes registros são de épocas antigas. )", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10409, 23, "Eles eram oleiros e habitavam em Netaim e em Gederá, perto do rei, para quem trabalhavam.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10410, 24, "Estes foram os filhos de Simeão: Nemuel, Jamim, Jaribe, Zerá e Saul;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10411, 25, "O filho de Saul era Salum, pai de Mibsão, que foi o pai de Misma.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10412, 26, "Estes foram os descendentes de Misma: seu filho Hamuel, pai de Zacur, que foi o pai de Simei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10413, 27, "Simei teve dezesseis filhos e seis filhas, mas seus irmãos não tiveram muitos filhos; por isso todos os seus clãs não se igualam em número à tribo de Judá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10414, 28, "Eles viviam em Berseba, Moladá, Hazar-Sual,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10415, 29, "Bila, Ezém, Tolade,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10416, 30, "Betuel, Hormá, Ziclague,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10417, 31, "Bete-Marcabote, Hazar-Susim, Bete-Biri e Saaraim. Essas foram as suas cidades até o reinado de Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10418, 32, "Tinham também as cinco cidades de Etã, Aim, Rimom, Toquém e Asã,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10419, 33, "com todos os povoados ao redor delas até Baalate. Nessas cidades viviam e mantinham um registro genealógico.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10420, 34, "Mesobabe, Janleque, Josa, filho de Amazias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10421, 35, "Joel, Jeú, filho de Josibias, neto de Seraías e bisneto de Asiel;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10422, 36, "também Elioenai, Jaacobá, Jesoaías, Asaías, Adiel, Jesimiel, Benaia,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10423, 37, "e Ziza, filho de Sifi, neto de Alom, bisneto de Jedaías, trineto de Sinri e tetraneto de Semaías.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10424, 38, "Essa é a lista dos líderes dos seus clãs. Suas famílias cresceram muito,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10425, 39, "e por isso foram para os arredores de Gedor, a leste do vale, em busca de pastagens para os seus rebanhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10426, 40, "Encontraram muitas pastagens boas, numa região vasta, pacífica e tranqüila, onde alguns camitas tinham vivido anteriormente.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10427, 41, "Durante o reinado de Ezequias, rei de Judá, esses homens aqui alistados chegaram e atacaram os camitas e os meunitas da região e os destruíram totalmente, como até hoje se pode ver. Depois ocuparam o lugar daqueles povos, pois havia pastagens para os seus rebanhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10428, 42, "E quinhentos desses simeonitas, liderados por Pelatias, Nearias, Refaías e Uziel, filhos de Isi, invadiram as colinas de Seir.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10429, 43, "Eles mataram o restante dos amalequitas que tinha escapado, e ali vivem até hoje.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10430, 1, "Estes são os filhos de Rúben, o filho mais velho de Israel. ( De fato, ele era o mais velho, mas, por ter desonrado o leito de seu pai, seus direitos de filho mais velho foram dados aos filhos de José, filho de Israel, de modo que não foi alistado nos registros genealógicos como o primeiro filho.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10431, 2, "Embora Judá tenha sido o mais poderoso de seus irmãos e dele tenha vindo um líder, os direitos de filho mais velho foram dados a José ).", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10432, 3, "Os filhos de Rúben, filho mais velho de Israel, foram: Enoque, Palu, Hezrom e Carmi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10433, 4, "Estes foram os descendentes de Joel: Seu filho Semaías, pai de Gogue, que foi o pai de Simei,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10434, 5, "pai de Mica, que foi o pai de Reaías, pai de Baal,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10435, 6, "que foi o pai de Beera, a quem Tiglate-Pileser, rei da Assíria levou para o exílio. Beera era um líder da tribo de Rúben.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10436, 7, "Estes foram os parentes dele, de acordo com seus clãs, alistados conforme os seus registros genealógicos: Jeiel, o chefe, Zacarias", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10437, 8, "e Belá, filho de Azaz, neto de Sema e bisneto de Joel. Eles foram viver na região que vai desde Aroer até o monte Nebo e Baal-Meom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10438, 9, "A leste ocuparam a terra que vai até o deserto que se estende na direção do rio Eufrates, pois os seus rebanhos tinham aumentado muito em Gileade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10439, 10, "Durante o reinado de Saul eles entraram em guerra contra os hagarenos e os derrotaram, passando a ocupar o acampamento deles por toda a região a leste de Gileade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10440, 11, "Ao lado da tribo de Rúben ficou a tribo de Gade, desde a região de Basã até Salcá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10441, 12, "Joel foi o primeiro chefe de clãs em Basã, Safã, o segundo; os outros foram Janai e Safate.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10442, 13, "Estes foram os parentes deles, por famílias: Micael, Mesulão, Seba, Jorai, Jacã, Zia e Héber. Eram sete ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10443, 14, "Eles eram descendentes de Abiail, filho de Huri, neto de Jaroa, bisneto de Gileade e trineto de Micael, que foi filho de Jesisai, neto de Jado e bisneto de Buz.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10444, 15, "Aí, filho de Abdiel e neto de Guni, foi o chefe dessas famílias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10445, 16, "A tribo de Gade habitou em Gileade, em Basã e seus povoados, e em toda a extensão das terras de pastagem de Sarom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10446, 17, "Todos esses entraram nos registros genealógicos durante os reinados de Jotão, rei de Judá, e de Jeroboão, rei de Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10447, 18, "As tribos de Rúben, Gade e a metade da tribo de Manassés tinham juntas quarenta e quatro mil e setecentos e sessenta homens de combate, capazes de empunhar escudo e espada, de usar o arco, e treinados para a guerra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10448, 19, "Eles entraram em guerra contra os hagarenos e seus aliados Jetur, Nafis e Nodabe.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10449, 20, "Durante a batalha clamaram a Deus, que os ajudou, entregando os hagarenos e todos os seus aliados nas suas mãos. Deus os atendeu, porque confiaram nele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10450, 21, "Tomaram dos hagarenos o rebanho de cinqüenta mil camelos, duzentas e cinqüenta mil ovelhas e dois mil jumentos. Também fizeram cem mil prisioneiros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10451, 22, "E muitos foram os inimigos mortos, pois a batalha era de Deus. Eles ocuparam aquela terra até a época do exílio.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10452, 23, "A metade da tribo de Manassés era numerosa e se estabeleceu na região que vai de Basã a Baal-Hermom, isto é, até Senir, o monte Hermom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10453, 24, "Estes eram os chefes das famílias dessa tribo: Éfer, Isi, Eliel, Azriel, Jeremias, Hodavias e Jadiel. Eram soldados valentes, homens famosos, e chefes das famílias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10454, 25, "Mas foram infiéis para com o Deus dos seus antepassados e se prostituíram, seguindo os deuses dos povos que Deus tinha destruído diante deles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10455, 26, "Por isso o Deus de Israel incitou Pul, que é Tiglate-Pileser, rei da Assíria, a levar as tribos de Rúben, de Gade e a metade da tribo de Manassés para Hala, para Habor, para Hara e para o rio Gozã, onde estão até hoje.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10456, 1, "Estes foram os filhos de Levi: Gérson, Coate e Merari.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10457, 2, "Estes foram os filhos de Coate: Anrão, Isar, Hebrom e Uziel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10458, 3, "Estes foram os filhos de Anrão: Arão, Moisés e Miriã. Estes foram os filhos de Arão: Nadabe, Abiú, Eleazar e Itamar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10459, 4, "Eleazar gerou Finéias, Finéias gerou Abisua,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10460, 5, "Abisua gerou Buqui, Buqui gerou Uzi,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10461, 6, "Uzi gerou Zeraías, Zeraías gerou Meraiote,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10462, 7, "Meraiote gerou Amarias, Amarias gerou Aitube,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10463, 8, "Aitube gerou Zadoque, Zadoque gerou Aimaás,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10464, 9, "Aimaás gerou Azarias, Azarias gerou Joanã,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10465, 10, "Joanã gerou Azarias que foi sacerdote no templo construído por Salomão em Jerusalém;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10466, 11, "Azarias gerou Amarias, Amarias gerou Aitube,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10467, 12, "Aitube gerou Zadoque, Zadoque gerou Salum,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10468, 13, "Salum gerou Hilquias, Hilquias gerou Azarias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10469, 14, "Azarias gerou Seraías, e Seraías gerou Jeozadaque.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10470, 15, "Jeozadaque foi levado prisioneiro quando o Senhor enviou Judá e Jerusalém para o exílio por meio de Nabucodonosor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10471, 16, "Estes foram os filhos de Levi: Gérson, Coate e Merari.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10472, 17, "Estes são os nomes dos filhos de Gérson: Libni e Simei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10473, 18, "Estes foram os filhos de Coate: Anrão, Isar, Hebrom e Uziel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10474, 19, "Estes foram os filhos de Merari: Mali e Musi. Estes são os clãs dos levitas alistados de acordo com os seus antepassados:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10475, 20, "De Gérson: Seu filho Libni, que foi o pai de Jaate, pai de Zima,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10476, 21, "que foi o pai de Joá, pai de Ido, pai de Zerá, que foi o pai de Jeaterai.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10477, 22, "De Coate: Seu filho Aminadabe, pai de Corá, que foi o pai de Assir,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10478, 23, "pai de Elcana, pai de Ebiasafe, que foi o pai de Assir,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10479, 24, "pai de Taate, pai de Uriel, pai de Uzias, que foi o pai de Saul.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10480, 25, "De Elcana: Amasai, Aimote", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10481, 26, "e Elcana, pai de Zofai, pai de Naate,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10482, 27, "que foi o pai de Eliabe, pai de Jeroão, pai de Elcana, que foi o pai de Samuel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10483, 28, "De Samuel: Joel, o mais velho, e Abias, o segundo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10484, 29, "De Merari: Mali, pai de Libni, pai de Simei, que foi o pai de Uzá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10485, 30, "pai de Siméia, pai de Hagias, que foi o pai de Asaías.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10486, 31, "Estes são os homens a quem Davi encarregou de dirigir os cânticos no templo do Senhor depois que a arca foi levada para lá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10487, 32, "Eles ministravam o louvor diante do tabernáculo, da Tenda do Encontro, até quando Salomão construiu o templo do Senhor em Jerusalém. Eles exerciam suas funções de acordo com as normas estabelecidas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10488, 33, "Estes são os que ministravam, junto com seus filhos: Dentre os coatitas: O músico Hemã, filho de Joel, filho de Samuel,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10489, 34, "filho de Elcana, filho de Jeroão, filho de Eliel, filho de Toá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10490, 35, "filho de Zufe, filho de Elcana, filho de Maate, filho de Amasai,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10491, 36, "filho de Elcana, filho de Joel, filho de Azarias, filho de Sofonias", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10492, 37, "filho de Taate, filho de Assir, filho de Ebiasafe, filho de Corá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10493, 38, "filho de Isar, filho de Coate, filho de Levi, filho de Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10494, 39, "À direita de Hemã, ficava seu parente Asafe, filho de Berequias, filho de Siméia,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10495, 40, "filho de Micael, filho de Baaséias, filho de Malquias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10496, 41, "filho de Etni, filho de Zerá, filho de Adaías,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10497, 42, "filho de Etã, filho de Zima, filho de Simei,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10498, 43, "filho de Jaate, filho de Gérson, filho de Levi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10499, 44, "Dentre os meraritas: À esquerda de Hemã, parente dos meraritas, ficava Etã, filho de Quisi, filho de Abdi, filho de Maluque,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10500, 45, "filho de Hasabias, filho de Amazias, filho de Hilquias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10501, 46, "filho de Anzi, filho de Bani, filho de Sêmer,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10502, 47, "filho de Mali, filho de Musi, filho de Merari, filho de Levi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10503, 48, "Seus parentes, os outros levitas, foram encarregados de cuidar de todo o serviço do tabernáculo, o templo de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10504, 49, "Mas era Arão e seus descendentes que cuidavam dos sacrifícios no altar do holocausto e das ofertas no altar de incenso e de todo o serviço do Lugar Santíssimo, e dos sacrifícios de propiciação por Israel, conforme tudo o que Moisés, servo de Deus, tinha ordenado.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10505, 50, "Estes foram os descendentes de Arão: o seu filho Eleazar, pai de Finéias, que foi o pai de Abisua,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10506, 51, "pai de Buqui, pai de Uzi, que foi o pai de Zeraías,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10507, 52, "pai de Meraiote, pai de Amarias, que foi o pai de Aitube,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10508, 53, "pai de Zadoque, pai de Aimaás.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10509, 54, "Estas foram as cidades e as regiões dadas aos levitas para nelas habitarem. Dentre os descendentes de Arão, o clã coatita foi sorteado primeiro;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10510, 55, "foi-lhe dada Hebrom, em Judá, com suas pastagens ao redor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10511, 56, "Mas os campos e os povoados em torno da cidade foram dados a Calebe, filho de Jefoné.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10512, 57, "Assim os descendentes de Arão receberam Hebrom, cidade de refúgio, e Libna, Jatir, Estemoa,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10513, 58, "Hilém, Debir,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10514, 59, "Asã, Jutá e Bete-Semes, com suas respectivas pastagens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10515, 60, "E da tribo de Benjamim receberam Gibeão, Geba, Alemete e Anatote, com suas respectivas pastagens. Ao todo treze cidades foram distribuídas entre os seus clãs.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10516, 61, "Para os demais descendentes de Coate foram sorteadas dez cidades pertencentes aos clãs da metade da tribo de Manassés.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10517, 62, "Para os descendentes de Gérson, clã por clã, foram sorteadas treze cidades das tribos de Issacar, de Aser e de Naftali, e da metade da tribo de Manassés que fica em Basã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10518, 63, "Para os descendentes de Merari, clã por clã, foram sorteadas doze cidades das tribos de Rúben, de Gade e de Zebulom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10519, 64, "Assim os israelitas deram aos levitas essas cidades com suas respectivas pastagens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10520, 65, "As cidades anteriormente mencionadas dos territórios de Judá, de Simeão e de Benjamim também lhes foram dadas por sorteio.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10521, 66, "Alguns dos clãs coatitas receberam as seguintes cidades no território da tribo de Efraim:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10522, 67, "Siquém, cidade de refúgio, nos montes de Efraim, e Gezer,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10523, 68, "Jocmeão, Bete-Horom,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10524, 69, "Aijalom e Gate-Rimom, com suas respectivas pastagens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10525, 70, "E da metade da tribo de Manassés o restante dos clãs coatitas recebeu Aner e Bileã, com suas respectivas pastagens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10526, 71, "Os gersonitas receberam as seguintes cidades: Do clã da meia tribo de Manassés, Golã, em Basã, e também Asterote, com suas respectivas pastagens;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10527, 72, "da tribo de Issacar, Quedes, Daberate,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10528, 73, "Ramote e Aném, com suas respectivas pastagens;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10529, 74, "da tribo de Aser, Masal, Abdom,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10530, 75, "Hucoque e Reobe, com suas respectivas pastagens;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10531, 76, "e da tribo de Naftali, Quedes, na Galiléia, Hamom e Quiriataim, com suas respectivas pastagens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10532, 77, "E estas foram as cidades que os outros meraritas receberam: Da tribo de Zebulom, Rimono e Tabor, com suas respectivas pastagens;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10533, 78, "da tribo de Rúben, do outro lado do Jordão, a leste de Jericó, Bezer, no deserto, Jaza,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10534, 79, "Quedemote e Mefaate, com suas respectivas pastagens;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10535, 80, "e da tribo de Gade, Ramote, em Gileade, Maanaim,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10536, 81, "Hesbom e Jazar, com suas respectivas pastagens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10537, 1, "Estes foram os quatro filhos de Issacar: Tolá, Puá, Jasube e Sinrom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10538, 2, "Estes foram os filhos de Tolá: Uzi, Refaías, Jeriel, Jamai, Ibsão e Samuel, chefes dos seus clãs. No reinado de Davi, os descendentes de Tolá alistados em suas genealogias como homens de combate chegavam a 22. 600.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10539, 3, "O filho de Uzi foi Israías. Estes foram os filhos de Israías: Micael, Obadias, Joel e Issias. Todos os cinco eram chefes", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10540, 4, "que tinham muitas mulheres e filhos. Por isso, conforme a genealogia de sua família, eles contavam com 36. 000 homens prontos para o combate.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10541, 5, "Juntamente com seus parentes, os homens de combate de todos os clãs de Issacar, conforme alistados em sua genealogia, chegavam ao todo a 87. 000.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10542, 6, "Estes foram os três filhos de Benjamim: Belá, Bequer e Jediael.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10543, 7, "Estes foram os filhos de Belá: Esbom, Uzi, Uziel, Jeremote e Iri, cinco chefes de famílias. Seu registro genealógico alistava 22. 034 homens de combate.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10544, 8, "Estes foram os filhos de Bequer: Zemira, Joás, Eliézer, Elioenai, Onri, Jeremote, Abias, Anatote e Alemete. Todos esses eram filhos de Bequer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10545, 9, "O registro genealógico deles alistava os chefes de famílias e 20. 200 homens de combate.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10546, 10, "O filho de Jediael foi Bilã. Estes foram os filhos de Bilã: Jeús, Benjamim, Eúde, Quenaaná, Zetã, Társis e Aisaar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10547, 11, "Todos esses descendentes de Jediael eram chefes de famílias que contavam com 17. 200 homens de combate prontos para a guerra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10548, 12, "Supim e Hupim eram filhos de Ir; e Husim era filho de Aer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10549, 13, "Estes foram os filhos de Naftali: Jaziel, Guni, Jezer e Silém, netos de Bila.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10550, 14, "Estes foram os descendentes de Manassés: Asriel, filho de sua concubina araméia, que também deu à luz Maquir, pai de Gileade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10551, 15, "Maquir casou-se com Maaca, irmã de Hupim e Supim. Outro descendente de Manassés chamava-se Zelofeade, o qual só teve filhas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10552, 16, "Maaca, mulher de Maquir, deu à luz um filho, a quem deu o nome de Perez. O nome de seu irmão era Seres, cujos filhos chamavam-se Ulão e Requém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10553, 17, "O filho de Ulão foi Bedã. Esses foram os descendentes de Gileade, filho de Maquir, e neto de Manassés.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10554, 18, "Sua irmã Hamolequete deu à luz Isode, Abiezer e Maalá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10555, 19, "Estes foram os filhos de Semida: Aiã, Siquém, Liqui e Anião.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10556, 20, "Estes foram os descendentes de Efraim: Sutela, que foi o pai de Berede, pai de Taate, pai de Eleada, que foi o pai de Taate,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10557, 21, "pai de Zabade, pai de Sutela. Ézer e Eleade, filhos de Efraim, foram mortos por homens da cidade de Gate, quando tentavam roubar os rebanhos deles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10558, 22, "Efraim chorou muitos dias por eles, e seus parentes vieram consolá-lo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10559, 23, "Depois ele se deitou de novo com sua mulher, e ela engravidou e deu à luz um filho. Ele o chamou Berias, pois tinha acontecido uma desgraça em sua família.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10560, 24, "Sua filha chamava-se Seerá. Foi ela que fundou Bete-Horom Alta e Bete-Horom Baixa e também Uzém-Seerá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10561, 25, "O filho de Berias foi Refa, pai de Resefe, que foi o pai de Telá, pai de Taã,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10562, 26, "pai de Ladã, pai de Amiúde, que foi o pai de Elisama,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10563, 27, "pai de Num, que foi o pai de Josué.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10564, 28, "Suas terras e cidades incluíam Betel e os povoados ao redor, Naarã a leste, Gezer e seus povoados a oeste, e Siquém e Aiá com os seus povoados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10565, 29, "A tribo de Manassés controlava as cidades de Bete-Seã, Taanaque, Megido e Dor, com seus respectivos povoados. Os descendentes de José, filho de Israel, viviam nessas cidades.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10566, 30, "Estes foram os filhos de Aser: Imna, Isvá, Isvi e Berias. A irmã deles chamava-se Sera.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10567, 31, "Estes foram os filhos de Berias: Héber e Malquiel, que foi o pai de Birzavite.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10568, 32, "Héber gerou Jaflete, Somer e Hotão, e da irmã deles, Suá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10569, 33, "Estes foram os filhos de Jaflete: Pasaque, Bimal e Asvate. Esses foram os filhos de Jaflete.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10570, 34, "Estes foram os filhos de Somer: Aí, Roga, Jeubá e Arã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10571, 35, "Estes foram os filhos de Helém, irmão de Somer: Zofa, Imna, Seles e Amal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10572, 36, "Estes foram os filhos de Zofa: Suá, Harnefer, Sual, Beri, Inra,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10573, 37, "Bezer, Hode, Samá, Silsa, Itrã e Beera.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10574, 38, "Estes foram os filhos de Jéter: Jefoné, Pispa e Ara.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10575, 39, "Estes foram os filhos de Ula: Ara, Haniel e Rizia.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10576, 40, "Todos esses foram descendentes de Aser. Eram chefes de famílias, homens escolhidos, soldados valentes e líderes de destaque. O número dos alistados para combate no exército deles chegou a 26. 000.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10577, 1, "Benjamim gerou Belá, seu filho mais velho; Asbel, seu segundo filho, Aará, o terceiro,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10578, 2, "Noá, o quarto, e Rafa, o quinto.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10579, 3, "Estes foram os filhos de Belá: Adar, Gera, pai de Eúde,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10580, 4, "Abisua, Naamã, Aoá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10581, 5, "Gera, Sefufá e Hurão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10582, 6, "Estes foram os descendentes de Eúde, chefes das famílias dos habitantes de Geba, que foram deportados para Manaate:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10583, 7, "Naamã, Aías e Gera. Esse Gera, pai de Uzá e de Aiúde, foi quem os deportou.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10584, 8, "Depois de ter se divorciado de suas mulheres Husim e Baara, Saaraim teve filhos na terra de Moabe.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10585, 9, "Com sua mulher Hodes ele gerou Jobabe, Zíbia, Messa, Malcã,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10586, 10, "Jeús, Saquias e Mirma. Esses foram seus filhos, chefes de famílias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10587, 11, "Com Husim ele gerou Abitube e Elpaal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10588, 12, "Estes foram os filhos de Elpaal: Héber, Misã, Semede, que fundou Ono e Lode com seus povoados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10589, 13, "Berias e Sema foram os chefes das famílias dos habitantes de Aijalom, e foram eles que expulsaram os habitantes de Gate.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10590, 14, "Aiô, Sasaque, Jeremote,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10591, 15, "Zebadias, Arade, Éder,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10592, 16, "Micael, Ispa e Joá foram descendentes de Berias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10593, 17, "Zebadias, Mesulão, Hizqui, Héber,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10594, 18, "Ismerai, Izlias e Jobabe foram descendentes de Elpaal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10595, 19, "Jaquim, Zicri, Zabdi,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10596, 20, "Elienai, Ziletai, Eliel,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10597, 21, "Adaías, Beraías e Sinrate foram descendentes de Simei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10598, 22, "Ispã, Héber, Eliel,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10599, 23, "Abdom, Zicri, Hanã,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10600, 24, "Hananias, Elão, Antotias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10601, 25, "Ifdéias e Penuel foram descendentes de Sasaque.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10602, 26, "Sanserai, Searias, Atalias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10603, 27, "Jaaresias, Elias e Zicri foram descendentes de Jeroão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10604, 28, "Todos esses foram chefes de famílias, líderes conforme alistados em suas genealogias, e moravam em Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10605, 29, "Jeiel, pai de Gibeom morou na cidade de Gibeom. O nome de sua mulher era Maaca,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10606, 30, "o de seu filho mais velho, Abdom, e o de seus outros filhos, Zur, Quis, Baal, Ner, Nadabe,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10607, 31, "Gedor, Aiô, Zequer", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10608, 32, "e Miclote, que gerou Siméia. Eles também moravam perto de seus parentes, em Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10609, 33, "Ner gerou Quis, que gerou Saul. Saul gerou Jônatas, Malquisua, Abinadabe e Esbaal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10610, 34, "O filho de Jônatas foi Meribe-Baal, que gerou Mica.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10611, 35, "Estes foram os filhos de Mica: Pitom, Meleque, Taréia e Acaz.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10612, 36, "Acaz gerou Jeoada, Jeoada gerou Alemete, Azmavete e Zinri, e Zinri gerou Mosa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10613, 37, "Mosa gerou Bineá, pai de Rafa, que foi o pai de Eleasa, pai de Azel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10614, 38, "Azel teve seis filhos chamados Azricão, Bocru, Ismael, Searias, Obadias e Hanã. Todos esses foram filhos de Azel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10615, 39, "Estes foram os filhos de Eseque, seu irmão: Ulão, o mais velho, Jeús, o segundo e Elifelete, o terceiro.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10616, 40, "Os filhos de Ulão eram soldados valentes e bons flecheiros. Tiveram muitos filhos e netos; eram cento e cinqüenta ao todo. Todos esses foram descendentes de Benjamim.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10617, 1, "Todos os israelitas foram alistados nas genealogias dos registros históricos dos reis de Israel. O povo de Judá foi levado prisioneiro para a Babilônia por sua infidelidade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10618, 2, "Os primeiros a voltarem às suas propriedades e às suas cidades foram algumas pessoas do povo e alguns sacerdotes, levitas e servidores do templo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10619, 3, "Os de Judá, de Benjamim e de Efraim e Manassés que se instalaram em Jerusalém foram:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10620, 4, "Utai, filho de Amiúde, neto de Onri, bisneto de Inri e trineto de Bani, um descendente de Perez, filho de Judá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10621, 5, "Dos descendentes de Selá: O primogênito Asaías com seus filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10622, 6, "Dos descendentes de Zerá: Jeuel. Os de Judá chegaram a 690.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10623, 7, "Dos benjamitas: Salu, filho de Mesulão, neto de Hodavias e bisneto de Hassenua;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10624, 8, "Ibnéias, filho de Jeroão; Elá, filho de Uzi, filho de Micri; e Mesulão, filho de Sefatias, filho de Reuel, filho de Ibnias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10625, 9, "Da tribo de Benjamim, relacionados em sua genealogia, eram 956. Todos esses homens eram chefes de suas famílias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10626, 10, "Dos sacerdotes: Jedaías, Jeoiaribe, Jaquim;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10627, 11, "Azarias, filho de Hilquias, neto de Mesulão, bisneto de Zadoque, trineto de Meraiote e tetraneto de Aitube, o líder encarregado do templo de Deus;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10628, 12, "Adaías, filho de Jeroão, neto de Pasur e bisneto de Malquias; e Masai, filho de Adiel, neto de Jazera, bisneto de Mesulão, trineto de Mesilemite e tetraneto de Imer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10629, 13, "O número de sacerdotes que eram chefes de famílias era 1. 760. Eram homens capazes, e sua responsabilidade era ministrar no templo de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10630, 14, "Dos levitas: Semaías, filho de Hassube, neto de Azricão e bisneto de Hasabias, um merarita;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10631, 15, "Baquebacar, Heres, Galal e Matanias, filho de Mica, neto de Zicri e bisneto de Asafe;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10632, 16, "Obadias, filho de Semaías, neto de Galal e bisneto de Jedutum; e Berequias, filho de Asa e neto de Elcana, que vivia nos povoados dos netofatitas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10633, 17, "Os guardas das portas eram: Salum, o chefe, Acube, Talmom, Aimã e os irmãos deles, sendo até hoje", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10634, 18, "os guardas da porta do Rei, a leste. Salum era o chefe. Esses eram os guardas das portas que pertenciam ao acampamento dos levitas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10635, 19, "Salum, filho de Coré, neto de Ebiasafe e bisneto de Corá, e seus parentes, os coreítas, guardas das portas, responsáveis por guardar as entradas da Tenda, como os seus antepassados tinham sido responsáveis por guardar a entrada da habitação do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10636, 20, "Naquela época, Finéias, filho de Eleazar, estivera encarregado dos guardas das portas, e o Senhor estava com ele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10637, 21, "Zacarias, filho de Meselemias, era o guarda das portas da entrada da Tenda do Encontro.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10638, 22, "A soma total dos escolhidos para serem guardas das portas, registrados nas genealogias dos seus povoados, era de 212. Eles haviam sido designados para esses postos de confiança por Davi e pelo vidente Samuel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10639, 23, "Eles e os seus descendentes foram encarregados de vigiar as portas do templo do Senhor, o templo chamado Tenda.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10640, 24, "Os guardas vigiavam as portas nos quatro lados: norte, sul, leste e oeste.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10641, 25, "Seus parentes, residentes em seus povoados, tinham que vir de tempos em tempos e trabalhar com eles por períodos de sete dias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10642, 26, "Mas os quatro principais guardas das portas, que eram levitas, receberam a responsabilidade de tomar conta das salas e da tesouraria do templo de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10643, 27, "Eles passavam a noite perto do templo de Deus, pois tinham o dever de vigiá-la e de abrir as portas todas as manhãs.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10644, 28, "Alguns levitas estavam encarregados dos utensílios utilizados no culto no templo; eles os contavam quando eram retirados e quando eram devolvidos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10645, 29, "Outros eram responsáveis pelos móveis e por todos os demais utensílios do santuário, bem como pela farinha, pelo vinho, pelo óleo, pelo incenso e pelas especiarias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10646, 30, "E ainda outros cuidavam da manipulação das especiarias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10647, 31, "Um levita chamado Matitias, filho mais velho do coreíta Salum, tinha a responsabilidade de assar os pães para as ofertas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10648, 32, "E dentre os coatitas, seus irmãos, alguns estavam encarregados de preparar os pães que são postos sobre a mesa todo sábado.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10649, 33, "Os cantores, chefes de famílias levitas, permaneciam nas salas do templo e estavam isentos de outros deveres, pois dia e noite se dedicavam à sua própria tarefa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10650, 34, "Todos esses eram chefes de famílias levitas, alistados como líderes em suas genealogias, e moravam em Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10651, 35, "Jeiel, pai de Gibeom, morava em Gibeom. O nome de sua mulher era Maaca,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10652, 36, "e o de seu filho mais velho, Abdom. Depois nasceram Zur, Quis, Baal, Ner, Nadabe,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10653, 37, "Gedor, Aiô, Zacarias e Miclote.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10654, 38, "Miclote gerou Siméia. Eles também moravam perto de seus parentes em Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10655, 39, "Ner gerou Quis, Quis gerou Saul, Saul gerou Jônatas, Malquisua, Abinadabe e Esbaal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10656, 40, "Este foi o filho de Jônatas: Meribe-Baal, que gerou Mica.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10657, 41, "Estes foram os filhos de Mica: Pitom, Meleque, Taréia e Acaz.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10658, 42, "Acaz gerou Jadá, Jadá gerou Alemete, Azmavete e Zinri, e Zinri gerou Mosa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10659, 43, "Mosa gerou Bineá, cujo filho foi Refaías, o filho deste foi Eleasa, pai de Azel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10660, 44, "Azel teve seis filhos, e os nomes deles foram: Azricão, Bocru, Ismael, Searias, Obadias e Hanã. Esses foram os filhos de Azel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10661, 1, "E aconteceu que, em combate com os filisteus, os israelitas foram postos em fuga, e muitos caíram mortos no monte Gilboa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10662, 2, "Os filisteus perseguiram Saul e seus filhos, e mataram Jônatas, Abinadabe e Malquisua, filhos de Saul.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10663, 3, "O combate foi se tornando cada vez mais violento em torno de Saul, até que os flecheiros o alcançaram e o feriram gravemente.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10664, 4, "Então Saul ordenou ao seu escudeiro: ""Tire sua espada e mate-me com ela, senão sofrerei a vergonha de cair nas mãos desses incircuncisos"". Mas o seu escudeiro estava apavorado e não quis fazê-lo. Saul, então, apanhou a própria espada e jogou-se sobre ela.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10665, 5, "Quando o escudeiro viu que Saul estava morto, jogou-se também sobre sua espada e morreu.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10666, 6, "Dessa maneira Saul e seus três filhos morreram, e, assim, toda a descendência real.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10667, 7, "Quando os israelitas que habitavam no vale viram que o exército tinha fugido e que Saul e seus filhos estavam mortos, fugiram abandonando suas cidades. Depois os filisteus foram ocupá-las.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10668, 8, "No dia seguinte, quando os filisteus foram saquear os mortos, encontraram Saul e seus filhos caídos no monte Gilboa.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10669, 9, "Cortaram a cabeça de Saul, pegaram suas armas e enviaram mensageiros por toda a terra dos filisteus proclamando a notícia entre os seus ídolos e o seu povo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10670, 10, "Expuseram suas armas num dos templos dos seus deuses e penduraram sua cabeça no templo de Dagom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10671, 11, "Quando os habitantes de Jabes-Gileade ficaram sabendo o que os filisteus haviam feito com Saul,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10672, 12, "os mais corajosos dentre eles foram e apanharam os corpos de Saul e de seus filhos e os levaram a Jabes. Lá sepultaram seus ossos sob a Grande Árvore, e jejuaram por sete dias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10673, 13, "Saul morreu porque foi infiel ao Senhor; não guardou a palavra do Senhor e chegou a consultar uma médiun em busca de orientação,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10674, 14, "em vez de consultar o Senhor. Por isso o Senhor o entregou à morte e deu o reino a Davi, filho de Jessé.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10675, 1, "Todo o Israel reuniu-se com Davi em Hebrom e disse: ""Somos sangue do teu sangue.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10676, 2, "No passado, mesmo quando Saul era rei, eras tu quem liderava Israel em suas batalhas. E o Senhor, o teu Deus, te disse: ‘Você pastoreará o meu povo Israel, e será o seu governante’ "".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10677, 3, "Então todas as autoridades de Israel foram ao encontro do rei Davi em Hebrom, e ele fez um acordo com eles em Hebrom perante o Senhor, e eles ungiram Davi rei de Israel, conforme o Senhor havia anunciado por meio de Samuel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10678, 4, "Davi e todos os israelitas marcharam para Jerusalém, que é Jebus. Os jebuseus, habitantes da cidade,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10679, 5, "disseram a Davi: ""Você não entrará aqui"". No entanto, Davi conquistou a fortaleza de Sião, a cidade de Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10680, 6, "Naquele dia Davi tinha dito: ""O primeiro que atacar os jebuseus se tornará o comandante do exército"". Joabe, filho de Zeruia, foi o primeiro, e por isso recebeu o comando do exército.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10681, 7, "Davi passou a morar na fortaleza, e por isso ela foi chamada cidade de Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10682, 8, "Ele reconstruiu a cidade ao redor da fortaleza, desde os muros de arrimo até os muros ao redor, e Joabe restaurou o restante da cidade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10683, 9, "E Davi foi se tornando cada vez mais poderoso, pois o Senhor dos Exércitos estava com ele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10684, 10, "Estes foram os chefes dos principais guerreiros de Davi, que junto com todo Israel, deram um grande apoio para estender o seu reinado a todo o país, conforme o Senhor havia prometido.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10685, 11, "Esta é a lista deles: Jasobeão, um hacmonita, era o chefe dos oficiais; foi ele que, empunhando sua lança, matou trezentos homens numa mesma batalha.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10686, 12, "Depois, Eleazar, filho de Dodô, de Aoí, um dos três principais guerreiros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10687, 13, "Ele estava com Davi na plantação de cevada de Pas-Damim, onde os filisteus se reuniram para a guerra. As tropas israelitas fugiram dos filisteus,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10688, 14, "mas eles mantiveram sua posição no meio da plantação. Eles o defenderam e feriram os filisteus, e o Senhor lhes deu uma grande vitória.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10689, 15, "Quando um grupo de filisteus estava acampado no vale de Refaim, três chefes do pelotão dos trinta foram encontrar Davi na rocha que há perto da caverna de Adulão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10690, 16, "Estando Davi nessa fortaleza, e o destacamento filisteu em Belém,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10691, 17, "Davi expressou seu desejo: ""Quem me dera me trouxessem água da cisterna que fica junto à porta de Belém! """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10692, 18, "Então aqueles três infiltraram-se no acampamento filisteu, tiraram água daquela cisterna e a trouxeram a Davi. Mas ele se recusou a beber; em vez disso, derramou-a como uma oferta ao Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10693, 19, """Longe de mim fazer isso, ó meu Deus! "", disse Davi. ""Esta água representa o sangue desses homens que arriscaram a própria vida! "" Eles arriscaram a vida para trazê-la. E não quis bebê-la. Foram essas as proezas dos três principais guerreiros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10694, 20, "Abisai, o irmão de Joabe, era o chefe do batalhão dos trinta. Com uma lança enfrentou trezentos homens e os matou, tornando-se tão famoso quanto os três.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10695, 21, "Foi honrado duas vezes mais do que o batalhão dos trinta, e se tornou chefe deles, mas nunca igualou-se aos três principais guerreiros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10696, 22, "Benaia, filho de Joiada, era um corajoso soldado de Cabzeel, e realizou grandes feitos. Matou dois dos melhores guerreiros de Moabe e, num dia de neve, desceu em uma cova e matou um leão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10697, 23, "Também matou um egípcio de dois metros e vinte e cinco centímetros de altura. Embora o egípcio tivesse na mão uma lança parecida com uma lançadeira de tecelão, Benaia o enfrentou com um cajado. Arrancou a lança da mão do egípcio e com ela o matou.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10698, 24, "Esses foram os grandes feitos de Benaia, filho de Joiada, que também foi tão famoso quanto os três principais guerreiros de Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10699, 25, "Foi mais honrado do que qualquer dos trinta, mas nunca igualou-se aos três. E Davi lhe deu o comando da sua guarda pessoal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10700, 26, "Os outros guerreiros foram: Asael, irmão de Joabe; Elanã, filho de Dodô, de Belém;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10701, 27, "Samote, de Haror; Helez, de Pelom;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10702, 28, "Ira, filho de Iques, de Tecoa; Abiezer, de Anatote;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10703, 29, "Sibecai, de Husate; Ilai, de Aoí;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10704, 30, "Maarai, de Netofate; Helede, filho de Baaná, de Netofate;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10705, 31, "Itai, filho de Ribai, de Gibeá de Benjamim; Benaia, de Piratom;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10706, 32, "Hurai, dos riachos de Gaás; Abiel, de Arbate;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10707, 33, "Azmavete, de Baarum; Eliaba, de Saalbom;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10708, 34, "os filhos de Hasém, de Gizom; Jônatas, filho de Sage, de Harar;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10709, 35, "Aião, filho de Sacar, de Harar; Elifal, filho de Ur;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10710, 36, "Héfer, de Mequerate; Aías, de Pelom;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10711, 37, "Hezro, de Carmelo; Naarai, filho de Ezbai;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10712, 38, "Joel, irmão de Natã; Mibar, filho de Hagri;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10713, 39, "o amonita Zeleque; Naarai, de Beerote, escudeiro de Joabe, filho de Zeruia;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10714, 40, "Ira e Garebe, de Jatir;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10715, 41, "Urias, o hitita; Zabade, filho de Alai;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10716, 42, "Adina, filho de Siza, de Rúben, chefe dos rubenitas e do grupo dos trinta;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10717, 43, "Hanã, filho de Maaca; Josafá, de Mitene;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10718, 44, "Uzia, de Asterate; Sama e Jeiel, filhos de Hotão, de Aroer;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10719, 45, "Jediael, filho de Sinri; seu irmão, Joá, de Tiz;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10720, 46, "Eliel, de Maave; Jeribai e Josavias, filhos de Elnaão; Itma, um moabita,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10721, 47, "e Eliel, Obede e Jaasiel, de Mezoba.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10722, 1, "Estes são os que se juntaram a Davi em Ziclague, onde se escondia de Saul, filho de Quis. Eles estavam entre os combatentes que o ajudaram na guerra;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10723, 2, "utilizavam arco e flecha e a funda para atirar pedras tanto com a mão direita como com a esquerda; pertenciam à tribo de Benjamim e eram parentes de Saul:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10724, 3, "Aiezer, o chefe deles, e Joás, filhos de Semaá, de Gibeate; Jeziel e Pelete, filhos de Azmavete; Beraca, Jeú, de Anatote,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10725, 4, "e Ismaías, de Gibeom, um grande guerreiro do pelotão dos trinta, e chefe deles; Jeremias, Jaaziel, Joanã, Jozabade, de Gederate,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10726, 5, "Eluzai, Jeremote, Bealías, Semarias e Sefatias, de Harufe;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10727, 6, "os coreítas Elcana, Issias, Azareel, Joezer e Jasobeão;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10728, 7, "e Joela e Zebadias, filhos de Jeroão de Gedor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10729, 8, "Da tribo de Gade alguns aliaram-se a Davi em sua fortaleza no deserto. Eram guerreiros corajosos, prontos para o combate, e sabiam lutar com escudo e com lança. Tinham a bravura de um leão, e eram ágeis como gazelas nos montes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10730, 9, "Ézer era o primeiro; Obadias, o segundo; Eliabe, o terceiro;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10731, 10, "Mismana, o quarto; Jeremias, o quinto;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10732, 11, "Atai, o sexto; Eliel, o sétimo;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10733, 12, "Joanã, o oitavo; Elzabade, o nono;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10734, 13, "Jeremias, o décimo; e Macbanai era o décimo primeiro.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10735, 14, "Todos esses de Gade eram chefes de exército; o menor valia por cem, e o maior enfrentava mil.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10736, 15, "Foram eles que atravessaram o Jordão no primeiro mês do ano, quando o rio transborda em todas as suas margens, e puseram em fuga todos os que moravam nos vales, a leste e a oeste.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10737, 16, "Alguns outros benjamitas e certos homens de Judá também vieram a Davi em sua fortaleza.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10738, 17, "Davi saiu ao encontro deles e lhes disse: ""Se vocês vieram em paz, para me ajudarem, estou pronto para recebê-los. Mas, se querem trair-me e entregar-me aos meus inimigos quando minhas mãos não cometeram violência, que o Deus de nossos antepassados veja isso e julgue vocês"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10739, 18, "Então o Espírito veio sobre Amasai, chefe do pelotão dos trinta, e ele disse: ""Somos teus, ó Davi! Estamos contigo, ó filho de Jessé! Paz, paz seja contigo, e aos teus aliados, pois o teu Deus te ajudará"". Davi os recebeu e os nomeou chefes de seus grupos de ataque.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10740, 19, "Alguns soldados de Manassés desertaram para Davi quando ele foi com os filisteus guerrear contra Saul. Mas eles não ajudaram os filisteus, porque os seus chefes os aconselharam e os mandaram embora, dizendo: ""Pagaremos com a vida, caso Davi deserte e passe para Saul, seu senhor"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10741, 20, "Estes foram os homens de Manassés que desertaram para Davi quando ele foi a Ziclague: Adna, Jozabade, Jediael, Micael, Jozabade, Eliú e Ziletai, chefes de batalhões de mil em Manassés.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10742, 21, "Eles ajudaram Davi contra grupos de ataque, pois todos eles eram guerreiros valentes, e eram líderes no exército dele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10743, 22, "De fato, diariamente chegavam soldados para ajudar Davi, até que seu exército tornou-se tão grande como o exército de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10744, 23, "Este é o número dos soldados armados para a guerra que vieram a Davi em Hebrom para entregar a ele o reino de Saul, conforme o Senhor tinha dito:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10745, 24, "Da tribo de Judá, 6. 800 armados para a guerra, com escudo e lança;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10746, 25, "Da tribo de Simeão, 7. 100 guerreiros prontos para o combate;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10747, 26, "Da tribo de Levi, 4. 600,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10748, 27, "inclusive Joiada, líder da família de Arão, com 3. 700 homens,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10749, 28, "e Zadoque, um jovem e valente guerreiro, com 22 oficiais de sua família;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10750, 29, "Da tribo de Benjamim, parentes de Saul, 3. 000, a maioria dos quais era até então fiel à família de Saul;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10751, 30, "Da tribo de Efraim, 20. 800 soldados valentes, famosos em seus próprios clãs;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10752, 31, "Da metade da tribo de Manassés, 18. 000, indicados por nome para fazerem Davi rei;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10753, 32, "Da tribo de Issacar, 200 chefes que sabiam como Israel devia agir em qualquer circunstância. Comandavam todos os seus parentes;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10754, 33, "Da tribo de Zebulom, 50. 000 soldados experientes, preparados para guerrear com qualquer tipo de arma, totalmente decididos a ajudar Davi;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10755, 34, "Da tribo de Naftali, 1. 000 líderes com 37. 000 homens armados de escudos e lanças;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10756, 35, "Da tribo de Dã, 28. 600 prontos para o combate;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10757, 36, "Da tribo de Aser, 40. 000 soldados experientes, preparados para o combate;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10758, 37, "e do leste do Jordão, das tribos de Rúben e de Gade, e da metade da tribo de Manassés, 120. 000 completamente armados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10759, 38, "Todos esses eram homens de combate que se apresentaram voluntariamente para servirem nas fileiras. Foram a Hebrom totalmente decididos a fazerem de Davi rei sobre todo o Israel. E todos os outros israelitas tinham esse mesmo propósito.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10760, 39, "Ficaram com Davi três dias, comendo e bebendo, pois as suas famílias haviam fornecido provisões para eles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10761, 40, "Os habitantes das tribos vizinhas e também de lugares distantes como Issacar, Zebulom e Naftali, trouxeram-lhes muitas provisões em jumentos, camelos, mulas e bois: farinha, bolos de figo, bolos de uvas passas, vinho, azeite, bois e ovelhas, pois havia grande alegria em Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10762, 1, "Depois de consultar todos os seus oficiais, os comandantes de mil e de cem,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10763, 2, "Davi disse a toda a assembléia de Israel: ""Se vocês estão de acordo e se esta é a vontade do Senhor nosso Deus, enviemos uma mensagem a nossos irmãos em todo o território de Israel, e também aos sacerdotes e aos levitas que estão com eles em suas cidades, para virem unir-se a nós.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10764, 3, "Vamos trazer de volta a arca de nosso Deus, pois não nos importamos com ela durante o reinado de Saul"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10765, 4, "Toda a assembléia concordou, pois isso pareceu bem a todo o povo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10766, 5, "Então Davi reuniu todos os israelitas, desde o rio Sior, no Egito, até Lebo-Hamate, para trazerem de Quiriate-Jearim a arca de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10767, 6, "Davi e todos os israelitas foram a Baalá, que é Quiriate-Jearim, em Judá, para buscar a arca de Deus, o Senhor, entronizado entre os querubins; a arca sobre a qual seu nome é invocado.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10768, 7, "Da casa de Abinadabe levaram a arca de Deus num carroção novo, conduzido por Uzá e Aiô.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10769, 8, "Davi e todos os israelitas iam dançando e cantando com todo o vigor diante de Deus, ao som de harpas, liras, tamborins, címbalos e cornetas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10770, 9, "Quando chegaram à eira de Quidom, Uzá esticou o braço e segurou a arca, porque os bois haviam tropeçado.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10771, 10, "A ira do Senhor acendeu-se contra Uzá, e ele o feriu por ter tocado na arca. Uzá morreu ali mesmo, diante de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10772, 11, "Davi ficou contrariado porque o Senhor, em sua ira, havia fulminado Uzá. Até hoje aquele lugar é chamado Perez-Uzá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10773, 12, "Naquele dia, Davi teve medo de Deus e se perguntou: ""Como vou conseguir levar a arca de Deus? """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10774, 13, "Por isso desistiu de levar a arca para a cidade de Davi. Em vez disso, levou-a para a casa de Obede-Edom, de Gate.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10775, 14, "A arca de Deus ficou na casa dele por três meses, e o Senhor abençoou sua família e tudo o que possuía.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10776, 1, "Hirão, rei de Tiro, enviou a Davi uma delegação, que lhe trouxe toras de cedro, e também pedreiros e carpinteiros, para lhe construírem um palácio.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10777, 2, "Então Davi teve certeza de que o Senhor o confirmara como rei de Israel e que seu reino estava prosperando por amor de seu povo Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10778, 3, "Em Jerusalém Davi tomou para si mais mulheres e gerou mais filhos e filhas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10779, 4, "Estes são os nomes dos que lhe nasceram ali: Samua, Sobabe, Natã, Salomão,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10780, 5, "Ibar, Elisua, Elpelete,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10781, 6, "Nogá, Nefegue, Jafia,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10782, 7, "Elisama, Beeliada e Elifelete.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10783, 8, "Quando os filisteus ficaram sabendo que Davi tinha sido ungido rei de todo o Israel, foram com todo o exército prendê-lo, mas Davi soube disso e saiu para enfrentá-los.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10784, 9, "Tendo os filisteus invadido o vale de Refaim,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10785, 10, "Davi perguntou a Deus: ""Devo atacar os filisteus? Tu os entregarás nas minhas mãos? "" O Senhor lhe respondeu: ""Vá, eu os entregarei nas suas mãos"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10786, 11, "Então Davi e seus soldados foram a Baal-Perazim, e Davi os derrotou e disse: ""Assim como as águas de uma enchente causam destruição, pelas minhas mãos Deus destruiu os meus inimigos"". Então, aquele lugar passou a ser chamado Baal-Perazim.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10787, 12, "Como os filisteus haviam abandonado os seus ídolos ali, Davi ordenou que fossem queimados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10788, 13, "Os filisteus voltaram a atacar o vale;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10789, 14, "então Davi consultou Deus de novo, que lhe respondeu: ""Não ataque pela frente, mas dê a volta por trás deles e ataque-os em frente das amoreiras.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10790, 15, "Assim que você ouvir um som de passos por cima das amoreiras, saia para o combate, pois este é o sinal de que Deus saiu à sua frente para ferir o exército filisteu"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10791, 16, "E Davi fez como Deus lhe tinha ordenado, e eles derrotaram o exército filisteu, por todo o caminho, desde Gibeom até Gezer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10792, 17, "Assim, a fama de Davi espalhou-se por todas as terras, e o Senhor fez com que todas as nações o temessem.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10793, 1, "Depois de Davi ter construído casas para si na cidade de Davi, ele preparou um lugar para a arca de Deus e armou uma tenda para ela.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10794, 2, "Então Davi disse: ""Somente os levitas poderão carregar a arca de Deus, pois o Senhor os escolheu para transportarem a arca do Senhor e para ficarem sempre ao seu serviço"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10795, 3, "Davi reuniu todo o Israel em Jerusalém para levar a arca do Senhor para o lugar que ele lhe havia preparado.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10796, 4, "Reuniu também os descendentes de Arão e os levitas:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10797, 5, "Dos descendentes de Coate, Uriel, liderando outros 120;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10798, 6, "dos descendentes de Merari, Asaías, liderando outros 220;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10799, 7, "dos descendentes de Gérson, Joel, liderando outros 130;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10800, 8, "dos descendentes de Elisafã, Semaías, liderando outros 200;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10801, 9, "dos descendentes de Hebrom, Eliel, liderando outros 80;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10802, 10, "dos descendentes de Uziel, Aminadabe, liderando outros 112.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10803, 11, "Então Davi convocou os sacerdotes Zadoque e Abiatar, e os levitas Uriel, Asaías, Joel, Semaías, Eliel e Aminadabe, e", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10804, 12, "lhes disse: ""Vocês são os chefes das famílias levitas; vocês e seus companheiros levitas deverão consagrar-se e levar a arca do Senhor, o Deus de Israel, para o local que preparei para ela.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10805, 13, "Pelo fato de vocês não terem carregado a arca na primeira vez, a ira do Senhor nosso Deus causou destruição entre nós. Nós não o tínhamos consultado sobre como proceder"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10806, 14, "Então os sacerdotes e os levitas se consagraram para transportar a arca do Senhor, o Deus de Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10807, 15, "E os levitas carregaram a arca de Deus apoiando as varas da arca sobre os ombros, conforme Moisés tinha ordenado, de acordo com a palavra do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10808, 16, "Davi também ordenou aos líderes dos levitas que encarregassem os músicos que havia entre eles de cantar músicas alegres, acompanhados por instrumentos musicais: liras, harpas e címbalos sonoros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10809, 17, "Assim, os levitas escolheram Hemã, filho de Joel, e Asafe, um parente dele; e dentre os meraritas, seus parentes, escolheram Etã, filho de Cuxaías;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10810, 18, "e com eles seus parentes que estavam no segundo escalão: Zacarias, Jaaziel, Semiramote, Jeiel, Uni, Eliabe, Benaia, Maaséias, Matitias, Elifeleu, Micnéias, Obede-Edom e Jeiel, os porteiros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10811, 19, "Os músicos Hemã, Asafe e Etã deviam tocar os címbalos de bronze;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10812, 20, "Zacarias, Aziel, Semiramote, Jeiel, Uni, Eliabe, Maaséias e Benaia deviam tocar as liras, acompanhando o soprano,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10813, 21, "e Matitias, Elifeleu, Micnéias, Obede-Edom, Jeiel e Azazias deviam tocar as harpas em oitava, marcando o ritmo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10814, 22, "Quenanias, o chefe dos levitas, ficou encarregado dos cânticos; essa era sua responsabilidade, pois ele era capaz nisso.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10815, 23, "Berequias e Elcana seriam porteiros. Eles deveriam proteger a arca.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10816, 24, "Os sacerdotes Sebanias, Josafá, Natanael, Amasai, Zacarias, Benaia e Eliézer deviam tocar as cornetas diante da arca de Deus. Obede-Edom e Jeías também deviam ser porteiros, para vigiar a arca.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10817, 25, "Assim, com grande festa, foram Davi, as autoridades de Israel e os líderes de batalhões de mil buscar a arca da aliança do Senhor na casa de Obede-Edom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10818, 26, "Como Deus havia poupado os levitas que carregavam a arca da aliança do Senhor, sete novilhos e sete carneiros foram sacrificados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10819, 27, "E Davi vestia um manto de linho fino, assim como também todos os levitas que carregavam a arca, os músicos e Quenanias, chefe dos músicos. E Davi vestia também o colete sacerdotal de linho.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10820, 28, "E todo Israel acompanhou a arca da aliança do Senhor alegremente, ao som de trombetas, cornetas e címbalos, ao toque de liras e de harpas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10821, 29, "Aconteceu que, entrando a arca da aliança do Senhor na cidade de Davi, Mical, filha de Saul, observava de uma janela. E, ao ver o rei Davi dançando e comemorando, ela o desprezou em seu coração.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10822, 1, "Eles trouxeram a arca de Deus e a colocaram na tenda que Davi lhe havia preparado, e ofereceram holocaustos e sacrifícios de comunhão diante de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10823, 2, "Após oferecer os holocaustos e os sacrifícios de comunhão, Davi abençoou o povo em nome do Senhor,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10824, 3, "e deu um pão, um bolo de tâmaras e um bolo de uvas passas a cada homem e a cada mulher israelita.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10825, 4, "Davi nomeou alguns dos levitas para ministrarem diante da arca do Senhor, fazendo petições, dando graças, e louvando o Senhor, o Deus de Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10826, 5, "Desses, Asafe era o chefe, Zacarias vinha em seguida, e depois Jeiel, Semiramote, Jeiel, Matitias, Eliabe, Benaia, Obede-Edom e Jeiel. Eles deviam tocar lira e harpa, enquanto Asafe tocava os címbalos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10827, 6, "Os sacerdotes Benaia e Jaaziel deviam tocar diariamente as trombetas diante da arca da aliança de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10828, 7, "Foi naquele dia que pela primeira vez Davi encarregou a Asafe e seus parentes de louvar ao Senhor com salmos de gratidão:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10829, 8, "Dêem graças ao Senhor, clamem pelo seu nome, divulguem entre as nações o que ele tem feito.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10830, 9, "Cantem para ele, louvem-no; contem todos os seus atos maravilhosos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10831, 10, "Gloriem-se no seu santo nome; alegrem-se os corações dos que buscam o Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10832, 11, "Olhem para o Senhor e para a sua força; busquem sempre a sua face.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10833, 12, "Lembrem-se das maravilhas que ele fez, dos seus prodígios e das ordenanças que pronunciou,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10834, 13, "ó descendentes de Israel, seu servo, ó filhos de Jacó, seus escolhidos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10835, 14, "Ele é o Senhor, o nosso Deus; seus domínio alcança toda a terra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10836, 15, "Para sempre se lembra da sua aliança, da palavra que ordenou para mil gerações,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10837, 16, "da aliança que fez com Abraão, do juramento que fez a Isaque,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10838, 17, "que confirmou para Jacó como um decreto, e para Israel como uma aliança eterna, dizendo:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10839, 18, """A vocês darei a terra de Canaã, a herança que possuirão"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10840, 19, "Quando eles ainda eram poucos, muito poucos, e estrangeiros nela,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10841, 20, "e vagueavam de nação em nação, de um reino a outro,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10842, 21, "ele não permitiu que ninguém os oprimisse; por causa deles repreendeu reis, ordenando:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10843, 22, """Não maltratem os meus ungidos; não façam mal aos meus profetas"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10844, 23, "Cantem ao Senhor, todas as terras! Proclamem a sua salvação dia após dia!", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10845, 24, "Anunciem a sua glória entre as nações, seus feitos maravilhosos entre todos os povos!", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10846, 25, "Pois o Senhor é grande e muitíssimo digno de louvor; ele deve ser mais temido que todos os deuses.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10847, 26, "Pois todos os deuses das nações não passam de ídolos, mas o Senhor fez os céus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10848, 27, "O esplendor e a majestade estão diante dele; força e alegria na sua habitação.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10849, 28, "Dêem ao Senhor, ó famílias das nações, dêem ao Senhor glória e força.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10850, 29, "Dêem ao Senhor a glória devida ao seu nome. Tragam ofertas e venham à sua presença. Adorem o Senhor no esplendor da sua santidade,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10851, 30, "tremam diante dele, todas as nações! Firmou o mundo, que não se abalará!", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10852, 31, "Que os céus se alegrem e a terra exulte, e diga-se entre as nações: ""O Senhor reina! """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10853, 32, "Ressoe o mar, e tudo o que nele existe; exultem os campos, e tudo o que neles há!", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10854, 33, "Então as árvores da floresta cantarão de alegria, cantarão diante do Senhor, pois ele vem julgar a terra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10855, 34, "Rendam graças ao Senhor, pois ele é bom; o seu amor dura para sempre.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10856, 35, "Clamem: ""Salva-nos, ó Deus, nosso Salvador! Reúne-nos e livra-nos das nações, para que demos graças ao teu santo nome e façamos do teu louvor a nossa glória"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10857, 36, "Bendito seja o Senhor, o Deus de Israel, de eternidade a eternidade. Então todo o povo exclamou: ""Amém! "" e ""Louvado seja o Senhor! """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10858, 37, "Então Davi deixou Asafe e seus parentes diante da arca da aliança do Senhor para ali ministrarem regularmente, de acordo com as prescrições para cada dia.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10859, 38, "Também deixou Obede-Edom e seus sessenta e oito parentes para ministrarem com eles. Obede-Edom, filho de Jedutum, e também Hosa foram porteiros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10860, 39, "Davi deixou o sacerdote Zadoque e seus parentes sacerdotes diante do tabernáculo do Senhor em Gibeom", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10861, 40, "para regularmente, de manhã e à tarde, apresentarem holocaustos no altar de holocaustos, de acordo com tudo o que está escrito na Lei do Senhor, que ele deu a Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10862, 41, "Com eles estavam Hemã e Jedutum e os outros designados para darem graças ao Senhor, exclamando: ""O seu amor dura para sempre"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10863, 42, "Hemã e Jedutum eram responsáveis pelas trombetas, pelos címbalos e pelos outros instrumentos musicais para o culto. Os filhos de Jedutum foram designados como porteiros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10864, 43, "Então todo o povo partiu, cada um para a sua casa, e Davi voltou para casa para abençoar sua família.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10865, 1, "O rei Davi já morava em seu palácio quando, certo dia disse ao profeta Natã: ""Aqui estou eu, morando num palácio de cedro, enquanto a arca da aliança do Senhor permanece numa simples tenda"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10866, 2, "Natã respondeu a Davi: ""Faze o que tiveres em mente, pois Deus está contigo"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10867, 3, "E naquela mesma noite Deus falou a Natã:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10868, 4, """Vá dizer ao meu servo Davi que assim diz o Senhor: Não será você quem vai construir uma casa para eu morar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10869, 5, "Não tenho morado em nenhuma casa, desde o dia em que tirei Israel do Egito, mas fui de uma tenda para outra, e de um tabernáculo para outro.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10870, 6, "Por onde tenho acompanhado todo o Israel, alguma vez perguntei a algum líder deles, a quem ordenei que pastoreasse o meu povo: Por que você não me construiu um templo de cedro? """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10871, 7, """Agora pois, diga ao meu servo Davi: ‘Assim diz o Senhor dos Exércitos: Eu o tirei das pastagens, onde cuidava dos rebanhos, para ser o soberano do meu povo Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10872, 8, "Sempre estive com você por onde você andou, e eliminei todos os seus inimigos. Agora eu o farei tão famoso quanto os homens mais importantes da terra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10873, 9, "E providenciarei um lugar para o meu povo Israel e os plantarei lá, para que tenham o seu próprio lar; e não mais sejam incomodados. Povos ímpios não mais os oprimirão, como fizeram no início", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10874, 10, "e têm feito desde a época em que nomeei juízes sobre meu povo Israel. Também subjugarei todos os seus inimigos. Saiba também que eu, o Senhor, lhe estabelecerei uma dinastia.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10875, 11, "Quando a sua vida chegar ao fim e você se juntar aos seus antepassados, escolherei um dos seus filhos para sucedê-lo, e eu estabelecerei o reino dele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10876, 12, "Será ele quem construirá um templo para mim, e eu firmarei o trono dele para sempre.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10877, 13, "Eu serei seu pai, e ele será meu filho. Nunca retirarei dele o meu amor, como retirei de Saul.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10878, 14, "Eu o farei líder do meu povo e do meu reino para sempre; seu reinado será estabelecido para sempre’. """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10879, 15, "E Natã transmitiu a Davi tudo o que o Senhor lhe tinha falado e revelado.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10880, 16, "Então o rei Davi entrou no tabernáculo, assentou-se diante do Senhor, e orou: ""Quem sou eu, ó Senhor Deus, e o que é a minha família, para que me trouxesses a este ponto?", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10881, 17, "E, como se isso não bastasse para ti, ó Deus, tu falaste sobre o futuro da família deste teu servo. Tens me tratado como um homem de grande importância, ó Senhor Deus"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10882, 18, """O que mais Davi poderá dizer-te por honrares o teu servo? Tu conheces o teu servo,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10883, 19, "ó Senhor. Por amor do teu servo e de acordo com tua vontade, realizaste este feito grandioso e tornaste conhecidas todas essas grandes promessas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10884, 20, """Não há ninguém como tu, ó Senhor, nem há outro Deus além de ti, conforme tudo o que sabemos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10885, 21, "E quem é como o teu povo Israel, a única nação da terra que tu, ó Deus, resgataste para ti mesmo, e assim tornaste o teu nome famoso, realizaste grandes e impressionantes maravilhas ao expulsar nações de diante do povo que libertaste do Egito?", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10886, 22, "Tu fizeste do teu povo Israel o teu povo particular para sempre, e tu, ó Senhor, te tornaste o seu Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10887, 23, """Agora, Senhor, que a promessa que fizeste a respeito de teu servo e de sua descendência se confirme para sempre. Faze conforme prometeste,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10888, 24, "para que tudo se confirme, para que o teu nome seja engrandecido para sempre e os homens digam: ‘O Senhor dos Exércitos, o Deus de Israel, é Deus para Israel! ’ E a descendência de teu servo Davi se manterá firme diante de ti.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10889, 25, """Tu, meu Deus, revelaste a teu servo que formarás uma dinastia para ele. Por isso teu servo achou coragem para orar a ti.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10890, 26, "Ó Senhor, tu és Deus! Tu fizeste essa boa promessa a teu servo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10891, 27, "Agora, por tua bondade, abençoa a família de teu servo, para que ela continue para sempre na tua presença; pois o que tu, Senhor, abençoas, abençoado está para sempre"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10892, 1, "Depois disso, Davi derrotou os filisteus e os subjugou, e tirou do controle deles a cidade de Gate e seus povoados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10893, 2, "Davi derrotou também os moabitas, que ficaram sujeitos a ele, pagando-lhe impostos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10894, 3, "Além disso, Davi derrotou Hadadezer, rei de Zobá, nas proximidades de Hamate, quando Hadadezer tentava obter o controle na região do rio Eufrates.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10895, 4, "Davi se apossou de mil dos seus carros de guerra, sete mil cavaleiros e vinte mil soldados de infantaria. Ainda levou cem cavalos de carros de guerra, e aleijou todos os outros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10896, 5, "Quando os arameus de Damasco vieram ajudar Hadadezer, rei de Zobá, Davi matou vinte e dois mil deles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10897, 6, "Em seguida estabeleceu guarnições militares no reino dos arameus de Damasco, sujeitando-os a lhe pagarem impostos. E o Senhor dava vitórias a Davi aonde quer que ele fosse.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10898, 7, "Davi também levou para Jerusalém os escudos de ouro usados pelos oficiais de Hadadezer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10899, 8, "De Tebá e Cum, cidades que pertenciam a Hadadezer, o rei Davi levou grande quantidade de bronze, que Salomão utilizou para fazer o tanque de bronze, as colunas e vários utensílios.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10900, 9, "Quando Toú, rei de Hamate, soube que Davi tinha derrotado todo o exército de Hadadezer, rei de Zobá,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10901, 10, "enviou seu filho Hadorão ao rei Davi para saudá-lo e parabenizá-lo por sua vitória na batalha contra Hadadezer, que tinha estado em guerra com Toú. E, com Hadorão, mandou todo tipo de utensílios de ouro, de prata e de bronze.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10902, 11, "O rei Davi consagrou esses utensílios ao Senhor, como fizera com a prata e o ouro tomados de todas estas nações: Edom e Moabe, os amonitas e os filisteus, e Amaleque.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10903, 12, "Abisai, filho de Zeruia, derrotou dezoito mil edomitas no vale do Sal.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10904, 13, "Depois colocou guarnições militares em Edom, sujeitando todos os edomitas a Davi. O Senhor dava vitórias a Davi aonde quer que ele fosse.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10905, 14, "Davi reinou sobre todo o Israel, administrando o direito e a justiça a todo o seu povo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10906, 15, "Joabe, filho de Zeruia, era comandante do exército; Josafá, filho de Ailude, era o arquivista real;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10907, 16, "Zadoque, filho de Aitube, e Aimeleque, filho de Abiatar, eram sacerdotes; Sausa era secretário;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10908, 17, "Benaia, filho de Joiada, comandava os queretitas e os peletitas; e os filhos do rei Davi eram seus principais oficiais.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10909, 1, "Algum tempo depois, Naás, rei dos amonitas, morreu, e seu filho foi o seu sucessor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10910, 2, "Davi pensou: ""Serei bondoso com Hanum, filho de Naás, porque seu pai foi bondoso comigo"". Então Davi enviou uma delegação para transmitir a Hanum seu pesar pela morte do pai. Mas, quando os mensageiros de Davi chegaram à terra dos amonitas para expressar condolências a Hanum,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10911, 3, "os líderes amonitas lhe disseram: ""Achas que Davi está honrando teu pai ao enviar mensageiros para expressar condolências? Não é nada disso! Davi os enviou como espiões para examinar o país e destruí-lo"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10912, 4, "Então Hanum prendeu os mensageiros de Davi, rapou-lhes a barba, cortou metade de suas roupas até as nádegas, e os mandou embora.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10913, 5, "Quando Davi soube disso, enviou mensageiros ao encontro deles, pois haviam sido profundamente humilhados, e lhes mandou dizer: ""Fiquem em Jericó até que a barba cresça, e então voltem para casa"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10914, 6, "Vendo Hanum e os amonitas que tinham atraído sobre si o ódio de Davi, alugaram da Mesopotâmia, de Arã Maaca e de Zobá, carros de guerra e condutores de carros, por trinta e cinco toneladas de prata.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10915, 7, "Alugaram trinta e dois mil carros e seus condutores, e contrataram o rei de Maaca com suas tropas, o qual veio e acampou perto de Medeba, e os amonitas foram convocados de suas cidades e partiram para a batalha.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10916, 8, "Ao saber disso, Davi ordenou a Joabe que marchasse com todo o exército.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10917, 9, "Os amonitas saíram e se puseram em posição de combate na entrada da cidade, e os reis que tinham vindo posicionaram-se em campo aberto.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10918, 10, "Vendo Joabe que estava cercado pelas linhas de combate, escolheu alguns dos melhores soldados de Israel e os posicionou contra os arameus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10919, 11, "Pôs o restante dos homens sob o comando de seu irmão Abisai e os posicionou contra os amonitas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10920, 12, "E Joabe disse a Abisai: ""Se os arameus forem fortes demais para mim, venha me ajudar; mas, se os amonitas forem fortes demais para você, eu irei ajudá-lo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10921, 13, "Seja forte e lutemos com bravura pelo nosso povo e pelas cidades do nosso Deus. E que o Senhor faça o que for de sua vontade"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10922, 14, "Então Joabe e seus soldados avançaram contra os arameus, que fugiram dele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10923, 15, "Quando os amonitas viram que os arameus estavam fugindo de Joabe, também fugiram de seu irmão Abisai e entraram na cidade. Assim, Joabe voltou para Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10924, 16, "Ao perceberem os arameus que haviam sido derrotados por Israel, enviaram mensageiros para trazer arameus que viviam do outro lado do Eufrates, e Sofaque, o comandante do exército de Hadadezer, veio à frente deles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10925, 17, "Informado disso, Davi reuniu todo o Israel e atravessou o Jordão; avançou contra eles e formou linhas de combate defronte deles. Mas, começado o combate,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10926, 18, "eles fugiram de diante de Israel, e Davi matou sete mil dos seus condutores de carros de guerra e quarenta mil dos seus soldados de infantaria. Também matou Sofaque, o comandante do exército deles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10927, 19, "Quando os vassalos de Hadadezer viram que tinham sido derrotados por Israel, fizeram a paz com Davi e se sujeitaram a ele. E os arameus não quiseram mais ajudar os amonitas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10928, 1, "Na primavera seguinte, na época em que os reis saem à guerra, Joabe conduziu o seu exército até a terra dos amonitas e a arrasou. Enquanto Davi ainda estava em Jerusalém, Joabe cercou Rabá, a capital, atacou-a e deixou-a em ruínas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10929, 2, "Davi tirou a coroa da cabeça de Milcon, uma coroa de ouro de trinta e cinco quilos, ornamentada com pedras preciosas. E ela foi colocada na cabeça de Davi. Ele levou uma grande quantidade de bens da cidade,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10930, 3, "e levou também os seus habitantes, designando-lhes trabalhos com serras, picaretas de ferro e machados. Davi fez assim com todas as cidades amonitas. Depois voltou com todo seu exército para Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10931, 4, "E houve depois disso uma guerra com os filisteus, em Gezer. Naquela época, Sibecai, de Husate, matou Sipai, um dos descendentes dos refains, e os filisteus foram subjugados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10932, 5, "Noutra batalha contra os filisteus, Elanã, filho de Jair, matou Lami, irmão de Golias, de Gate, que possuía uma lança cuja haste parecia uma lançadeira de tecelão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10933, 6, "Noutra batalha, em Gate, havia um homem de grande estatura e que tinha seis dedos em cada mão e seis dedos em cada pé; vinte e quatro dedos ao todo. Ele também era descendente de Rafa,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10934, 7, "e ele desafiou Israel, mas Jônatas, filho de Siméia, irmão de Davi, o matou.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10935, 8, "Esses eram descendentes de Rafa, em Gate, e foram mortos por Davi e seus soldados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10936, 1, "Satanás levantou-se contra Israel e levou Davi a fazer um recenseamento do povo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10937, 2, "Davi disse a Joabe e aos outros comandantes do exército: ""Vão e contem os israelitas desde Berseba até Dã e tragam-me um relatório para que eu saiba quantos são"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10938, 3, "Joabe, porém, respondeu: ""Que o Senhor multiplique o povo dele por cem. Ó rei, meu senhor, não são, porventura, todos eles súditos do meu senhor? Por que o meu senhor deseja fazer isso? Por que deveria trazer culpa sobre Israel? """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10939, 4, "Mas a palavra do rei prevaleceu, de modo que Joabe partiu, percorreu todo o Israel e então voltou a Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10940, 5, "Joabe apresentou a Davi o relatório com o número dos homens de combate: Em todo o Israel havia um milhão e cem mil homens habilitados para o serviço militar, sendo quatrocentos e setenta mil de Judá.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10941, 6, "Mas Joabe não incluiu as tribos de Levi e de Benjamim na contagem, pois a ordem do rei lhe parecera absurda.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10942, 7, "Essa ordem foi reprovada por Deus, e por isso ele puniu Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10943, 8, "Então Davi disse a Deus: ""Pequei gravemente com o que fiz. Agora eu te imploro que perdoes o pecado do teu servo, porque cometi uma grande loucura! """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10944, 9, "O Senhor disse a Gade, o vidente de Davi:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10945, 10, """Vá dizer a Davi: ‘Assim diz o Senhor: Estou lhe dando três opções. Escolha uma delas, e eu a executarei contra você’ "".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10946, 11, "Então Gade foi a Davi e lhe disse: ""Assim diz o Senhor: ‘Escolha:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10947, 12, "três anos de fome, três meses fugindo de seus adversários, perseguido pela espada deles, ou três dias da espada do Senhor, isto é, três dias de praga, com o anjo do Senhor assolando todas as regiões de Israel’. Decida agora como devo responder àquele que me enviou"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10948, 13, "Davi respondeu: ""É grande a minha angustia! Prefiro cair nas mãos do Senhor, pois é grande a sua misericórdia, e não nas mãos dos homens"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10949, 14, "Então o Senhor enviou uma praga sobre Israel, e setenta mil homens de Israel morreram.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10950, 15, "E Deus enviou um anjo para destruir Jerusalém. Mas, quando o anjo ia fazê-lo, o Senhor olhou e arrependeu-se de trazer a catástrofe, e ele disse ao anjo destruidor: ""Pare! Já basta! "" Naquele momento o anjo do Senhor estava perto da eira de Araúna, o jebuseu.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10951, 16, "Davi olhou para cima e viu o anjo do Senhor entre o céu e a terra, com uma espada na mão erguida sobre Jerusalém. Então Davi e as autoridades de Israel, vestidos de luto, prostraram-se, rosto em terra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10952, 17, "Davi disse a Deus: ""Não fui eu que ordenei contar o povo? Fui eu que pequei e fiz o mal. Estes não passam de ovelhas. O que eles fizeram? Ó Senhor meu Deus, que o teu castigo caia sobre mim e sobre a minha família, mas não sobre o teu povo! """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10953, 18, "Então o anjo do Senhor mandou Gade dizer a Davi que construísse um altar na eira de Araúna, o jebuseu.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10954, 19, "Davi foi para lá, em obediência à palavra que Gade havia falado em nome do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10955, 20, "Araúna estava debulhando o trigo; virando-se, viu o anjo, e ele e seus quatro filhos que estavam com ele se esconderam.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10956, 21, "Nisso chegou Davi e, quando Araúna o viu, saiu da eira e prostrou-se diante de Davi, rosto em terra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10957, 22, "E Davi lhe pediu: ""Ceda-me o terreno da sua eira para eu construir um altar em honra do Senhor, para que cesse a praga sobre o povo. Venda-me o terreno pelo preço justo"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10958, 23, "Mas Araúna disse a Davi: ""Considera-o teu! Que o meu rei e senhor faça dele o que desejar. Eu darei os bois para os holocaustos, o debulhador para servir de lenha, e o trigo para a oferta de cereal. Tudo isso eu dou a ti"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10959, 24, "O rei Davi, porém, respondeu a Araúna: ""Não! Faço questão de pagar o preço justo. Não darei ao Senhor aquilo que pertence a você, nem oferecerei um holocausto que não me custe nada"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10960, 25, "Então Davi pagou a Araúna sete quilos e duzentos gramas de ouro pelo terreno.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10961, 26, "E Davi edificou ali um altar ao Senhor e ofereceu holocaustos e sacrifícios de comunhão. Davi invocou o Senhor, e o Senhor lhe respondeu com fogo que veio do céu sobre o altar de holocaustos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10962, 27, "E o Senhor ordenou ao anjo que pusesse a espada na bainha.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10963, 28, "Nessa ocasião viu Davi que o Senhor lhe havia respondido na eira de Araúna, o jebuseu, e passou a oferecer sacrifícios ali.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10964, 29, "Naquela época, o tabernáculo do Senhor que Moisés fizera no deserto, e o altar de holocaustos, estavam em Gibeom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10965, 30, "Mas Davi não podia consultar a Deus lá, pois tinha medo da espada do anjo do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10966, 1, "Então disse Davi: ""Este é o lugar para o templo de Deus, o Senhor, e do altar de holocaustos para Israel"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10967, 2, "Então Davi deu ordens para que se reunissem os estrangeiros que viviam em Israel, e dentre eles designou cortadores de pedra para prepararem pedras lavradas para a construção do templo de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10968, 3, "Ele providenciou grande quantidade de ferro para a fabricação de pregos e dobradiças para as portas, e mais bronze do que se podia pesar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10969, 4, "Também providenciou mais toras de cedro do que se podia contar, pois os sidônios e os tírios haviam trazido muito cedro para Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10970, 5, "Davi pensava: ""Meu filho Salomão é jovem e inexperiente, e o templo que será construído para o Senhor deve ser extraordinariamente magnífico, famoso e cheio de esplendor à vista de todas as nações. Por isso deixarei tudo preparado para a construção"". Assim, Davi deixou tudo preparado antes de morrer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10971, 6, "Davi mandou chamar seu filho Salomão e ordenou que ele construísse um templo para o Senhor, o Deus de Israel,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10972, 7, "dizendo: ""Meu filho, eu tinha no coração o propósito de construir um templo em honra do nome do Senhor, o meu Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10973, 8, "Mas veio a mim esta palavra do Senhor: ‘Você matou muita gente e empreendeu muitas guerras. Por isso você não construirá um templo em honra do meu nome, pois derramou muito sangue na terra, diante de mim.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10974, 9, "Mas você terá um filho que será um homem de paz, e eu farei com que ele tenha paz com todos os inimigos ao redor dele. Seu nome será Salomão, e eu darei paz e tranqüilidade a Israel durante o reinado dele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10975, 10, "É ele que vai construir um templo em honra do meu nome. Eu serei seu pai e ele será meu filho. E eu firmarei para sempre o trono do reinado dele sobre Israel’.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10976, 11, """Agora, meu filho, que o Senhor seja com você, para que você consiga construir o templo do Senhor, o seu Deus, conforme ele disse que você faria.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10977, 12, "Que o Senhor lhe dê prudência e entendimento para que você obedeça à lei do Senhor, o seu Deus, quando ele o puser como líder de Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10978, 13, "E você prosperará se for cuidadoso em obedecer aos decretos e às leis que o Senhor deu a Israel por meio de Moisés. Seja forte e corajoso! Não tenha medo nem se desanime!", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10979, 14, """Com muito esforço providenciei para o templo do Senhor três mil e quinhentas toneladas de ouro, trinta e cinco mil toneladas de prata, e tanto bronze e ferro que nem dá para calcular, além de madeira e pedra. E você ainda poderá aumentar a quantidade desse material.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10980, 15, "Você tem muitos trabalhadores: cortadores de pedras, pedreiros e carpinteiros, bem como especialistas em todo tipo de trabalho", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10981, 16, "em ouro, prata, bronze e ferro. Agora comece o trabalho, e que o Senhor esteja com você"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10982, 17, "Então Davi ordenou a todos os líderes de Israel que ajudassem seu filho Salomão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10983, 18, "Disse ele: ""Certamente o Senhor, o seu Deus, está com vocês, e lhes concedeu paz. Pois ele entregou os habitantes dessa terra em minhas mãos, e ela foi submetida ao Senhor e ao seu povo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10984, 19, "Agora consagrem o coração e a alma para buscarem o Senhor, o seu Deus. Comecem a construir o santuário de Deus, o Senhor, para que vocês possam trazer a arca da aliança do Senhor e os utensílios sagrados que pertencem a Deus para dentro do templo que será construído em honra do nome do Senhor"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10985, 1, "Já envelhecido e de idade avançada, Davi fez do seu filho Salomão rei sobre Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10986, 2, "Ele reuniu todos os líderes de Israel, bem como os sacerdotes e os levitas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10987, 3, "Os levitas de trinta anos para cima foram contados, e o número total deles chegou a trinta e oito mil.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10988, 4, "Davi escolheu vinte e quatro mil desses para supervisionarem o trabalho do templo do Senhor e seis mil para serem oficiais e juízes;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10989, 5, "quatro mil para serem guardas das portas e quatro mil para louvarem o Senhor com os instrumentos musicais que Davi preparou com esse propósito.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10990, 6, "Davi repartiu os levitas em grupos que descendiam dos filhos de Levi, Gérson, Coate e Merari.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10991, 7, "Dos filhos de Gérson: Ladã e Simei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10992, 8, "Estes foram os filhos de Ladã: Jeiel, o primeiro, Zetã e Joel, três ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10993, 9, "Estes foram os filhos de Simei: Selomote, Haziel e Harã, três ao todo. Esses foram os chefes das famílias de Ladã.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10994, 10, "E os filhos de Simei foram: Jaate, Ziza, Jeús e Berias. Esses foram os filhos de Simei, quatro ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10995, 11, "Jaate foi o primeiro e Ziza, o segundo, mas Jeús e Berias não tiveram muitos descendentes, por isso foram contados como uma única família.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10996, 12, "Dos filhos de Coate: Anrão, Isar, Hebrom e Uziel, quatro ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10997, 13, "Estes foram os filhos de Anrão: Arão e Moisés. Arão foi separado, ele e seus descendentes para sempre, para consagrar as coisas santíssimas, oferecer sacrifícios ao Senhor, ministrar diante dele e pronunciar bênçãos em seu nome, para sempre.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10998, 14, "Os filhos de Moisés, homem de Deus, foram contados como parte da tribo de Levi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (10999, 15, "Estes foram os filhos de Moisés: Gérson e Eliézer.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11000, 16, "Sebuel foi o chefe dos descendentes de Gérson.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11001, 17, "Reabias foi o chefe dos descendentes de Eliézer. Eliézer não teve nenhum outro filho, mas Reabias teve muitos filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11002, 18, "Selomite foi o chefe dos filhos de Isar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11003, 19, "Estes foram os filhos de Hebrom: Jerias foi o primeiro, Amarias, o segundo, Jaaziel, o terceiro, e Jecameão foi o quarto.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11004, 20, "Estes foram os filhos de Uziel: Mica, o primeiro, e Issias, o segundo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11005, 21, "Dos filhos de Merari: Mali e Musi. Estes foram os filhos de Mali: Eleazar e Quis.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11006, 22, "Eleazar morreu sem ter filhos, pois teve apenas filhas. Os primos delas, os filhos de Quis, casaram-se com elas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11007, 23, "Estes foram os filhos de Musi: Mali, Éder e Jeremote, três ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11008, 24, "Esses foram os descendentes de Levi pelas suas famílias: os chefes de famílias conforme registrados por seus nomes e contados individualmente, ou seja, os de vinte anos para cima, que serviam no templo do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11009, 25, "Pois Davi dissera: ""Uma vez que o Senhor, o Deus de Israel, concedeu descanso ao seu povo e veio habitar para sempre em Jerusalém,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11010, 26, "os levitas não mais precisam carregar o tabernáculo nem os utensílios usados em seu serviço"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11011, 27, "De acordo com as instruções finais de Davi, foram contados os levitas de vinte anos para cima.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11012, 28, "O dever dos levitas era ajudar os descendentes de Arão no serviço do templo do Senhor. Encarregavam-se dos pátios, das salas laterais, da purificação de todas as coisas sagradas e dos outros deveres na casa de Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11013, 29, "Estavam encarregados do pão consagrado, da farinha para as ofertas de cereal, dos bolos sem fermento, de assar o pão e misturar a massa, e de todos os pesos e medidas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11014, 30, "Além disso, deviam se apresentar todas as manhãs e todas as tardes para agradecer e louvar ao Senhor, e fazer o mesmo", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11015, 31, "sempre que holocaustos fossem apresentados ao Senhor nos sábados, nas festas da lua nova e nas festas fixas. Deviam servir regularmente diante do Senhor, conforme o número prescrito para eles.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11016, 32, "Dessa maneira os levitas ficaram responsáveis pela Tenda do Encontro, pelo Lugar Santo e, pela assistência aos seus irmãos, os descendentes de Arão, e pelo serviço do templo do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11017, 1, "Os filhos de Arão foram assim agrupados: Os filhos de Arão foram Nadabe, Abiú, Eleazar e Itamar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11018, 2, "Mas Nadabe e Abiú morreram antes de seu pai e não tiveram filhos; apenas Eleazar e Itamar serviram como sacerdotes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11019, 3, "Com a ajuda de Zadoque, descendente de Eleazar, e de Aimeleque, descendente de Itamar, Davi os dividiu em grupos para que cumprissem as suas responsabilidades.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11020, 4, "Havia um número maior de chefes de família entre os descendentes de Eleazar do que entre os de Itamar, e por isso eles foram assim divididos: dezesseis chefes de famílias dentre os descendentes de Eleazar e oito, dentre os descendentes de Itamar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11021, 5, "Eles foram divididos de maneira imparcial mediante sorteio, pois havia líderes do santuário e líderes de Deus tanto entre os descendentes de Eleazar como entre os de Itamar.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11022, 6, "O escriba Semaías, filho do levita Natanael, registrou os nomes deles na presença do rei, dos líderes, dos sacerdotes Zadoque e Aimeleque, filho de Abiatar, e dos chefes de famílias dos sacerdotes e dos levitas; as famílias de Eleazar e de Itamar foram sorteadas alternadamente.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11023, 7, "A primeira sorte caiu para Jeoiaribe, a segunda para Jedaías,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11024, 8, "a terceira para Harim, a quarta para Seorim,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11025, 9, "a quinta para Malquias, a sexta para Miamim,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11026, 10, "a sétima para Hacoz, a oitava para Abias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11027, 11, "a nona para Jesua, a décima para Secanias,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11028, 12, "a décima primeira para Eliasibe, a décima segunda para Jaquim,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11029, 13, "a décima terceira para Hupá, a décima quarta para Jesebeabe,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11030, 14, "a décima quinta para Bilga, a décima sexta para Imer,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11031, 15, "a décima sétima para Hezir, a décima oitava para Hapises,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11032, 16, "a décima nona para Petaías, a vigésima para Jeezquel,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11033, 17, "a vigésima primeira para Jaquim, a vigésima segunda para Gamul,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11034, 18, "a vigésima terceira para Delaías, e a vigésima quarta para Maazias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11035, 19, "Conforme essa ordem eles deveriam ministrar quando entrassem no templo do Senhor, de acordo com as prescrições que Arão, antepassado deles, lhes deixou, conforme o Senhor, o Deus de Israel, havia lhe ordenado.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11036, 20, "Estes foram os chefes dos outros levitas: dos descendentes de Anrão: Subael; dos descendentes de Subael: Jedias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11037, 21, "Quanto a Reabias, Issias foi o chefe dos seus filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11038, 22, "Dos descendentes de Isar: Selomote; dos filhos de Selomote: Jaate.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11039, 23, "Dos descendentes de Hebrom: Jerias, o primeiro, Amarias, o segundo, Jaaziel, o terceiro, e Jecameão, o quarto.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11040, 24, "Dos descendentes de Uziel: Mica; dos filhos de Mica: Samir.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11041, 25, "Dos descendentes de Issias, irmão de Mica, Zacarias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11042, 26, "Dos filhos de Merari: Mali e Musi. Dos filhos de Jaazias: Beno.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11043, 27, "Os descendentes de Merari por Jaazias: Beno, Soão, Zacur e Ibri.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11044, 28, "De Mali: Eleazar, que não teve filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11045, 29, "De Quis: Jerameel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11046, 30, "E foram estes os filhos de Musi: Mali, Éder e Jeremote. Esses foram os levitas, de acordo com as suas famílias.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11047, 31, "Eles também tiraram sortes na presença do rei Davi e de Zadoque, de Aimeleque, e dos chefes de famílias dos sacerdotes e dos levitas, assim como fizeram seus irmãos, os descendentes de Arão. As famílias dos irmãos mais velhos foram tratadas da mesma maneira que as dos mais novos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11048, 1, "Davi, junto com os comandantes do exército, separou alguns dos filhos de Asafe, de Hemã e de Jedutum para o ministério de profetizar ao som de harpas, liras e címbalos. Esta é a lista dos escolhidos para essa função:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11049, 2, "Dos filhos de Asafe: Zacur, José, Netanias e Asarela. Os filhos de Asafe estavam sob a sua supervisão que, por sua vez, profetizava sob a supervisão do rei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11050, 3, "Dos filhos de Jedutum: Gedalias, Zeri, Jesaías, Simei, Hasabias e Matitias, seis ao todo, sob a supervisão de seu pai, Jedutum, que profetizava ao som da harpa para agradecer e louvar ao Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11051, 4, "Dos filhos de Hemã: Buquias, Matanias, Uziel, Sebuel, Jeremote, Hananias, Hanani, Eliata, Gidalti, Romanti-Ézer, Josbecasa, Maloti, Hotir e Maaziote.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11052, 5, "Todos esses eram filhos de Hemã, o vidente do rei. Esses lhe nasceram conforme as promessas de que Deus haveria de torná-lo poderoso. E Deus deu a Hemã catorze filhos e três filhas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11053, 6, "Todos esses homens estavam sob a supervisão de seus pais quando ministravam a música do templo do Senhor, com címbalos, liras e harpas, na casa de Deus. Asafe, Jedutum e Hemã estavam sob a supervisão do rei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11054, 7, "Junto com seus parentes, todos capazes e preparados para o ministério do louvor do Senhor, totalizavam 288.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11055, 8, "Então tiraram sortes entre jovens e velhos, mestres e discípulos para designar-lhes suas responsabilidades.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11056, 9, "A primeira sorte, caiu para José, filho de Asafe, com seus filhos e parentes; eram ao todo 12; a segunda, para Gedalias, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11057, 10, "a terceira, para Zacur, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11058, 11, "a quarta, para Izri, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11059, 12, "a quinta, para Netanias, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11060, 13, "a sexta, para Buquias, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11061, 14, "a sétima, para Jesarela, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11062, 15, "a oitava, para Jesaías, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11063, 16, "a nona, para Matanias, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11064, 17, "a décima, para Simei, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11065, 18, "a décima primeira, para Azareel, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11066, 19, "a décima segunda, para Hasabias, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11067, 20, "a décima terceira, para Subael, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11068, 21, "a décima quarta, para Matitias, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11069, 22, "a décima quinta, para Jeremote, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11070, 23, "a décima sexta, para Hananias, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11071, 24, "a décima sétima, para Josbecasa, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11072, 25, "a décima oitava, para Hanani, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11073, 26, "a décima nona, para Maloti, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11074, 27, "a vigésima, para Eliata, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11075, 28, "a vigésima primeira, para Hotir, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11076, 29, "a vigésima segunda, para Gidalti, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11077, 30, "a vigésima terceira, para Maaziote, com seus filhos e parentes, eram ao todo 12;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11078, 31, "a vigésima quarta, para Romanti-Ézer, com seus filhos e parentes, eram ao todo 12.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11079, 1, "Esta é a relação dos grupos dos porteiros: Dos coreítas, Meselemias, filho de Coré, da família de Asafe.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11080, 2, "Foram estes os filhos de Meselemias: Zacarias, o primeiro, Jediael, o segundo, Zebadias, o terceiro, Jatniel, o quarto,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11081, 3, "Elão, o quinto, Joanã, o sexto, e Elioenai, o sétimo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11082, 4, "Foram estes os filhos de Obede-Edom: Semaías, o primeiro, Jeozabade, o segundo, Joá, o terceiro, Sacar, o quarto, Natanael, o quinto,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11083, 5, "Amiel, o sexto, Issacar, o sétimo, e Peuletai, o oitavo. Pois Deus havia abençoado Obede-Edom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11084, 6, "Seu filho Semaías também teve filhos, que foram líderes na família do seu pai, pois eram homens capazes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11085, 7, "Foram estes os filhos de Semaías: Otni, Rafael, Obede e Elzabade. Os parentes dele, Eliú e Semaquias, também foram homens capazes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11086, 8, "Todos esses foram descendentes de Obede-Edom; eles e os seus filhos e parentes eram capazes e aptos para a obra. Eram ao todo 62 descendentes de Obede-Edom.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11087, 9, "Meselemias teve 18 filhos e parentes chegados, todos eles homens capazes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11088, 10, "Foram estes os filhos de Hosa, o merarita: Sinri, que foi nomeado chefe por seu pai, mesmo não sendo o mais velho,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11089, 11, "Hilquias, o segundo, Tebalias, o terceiro, e Zacarias, o quarto. Os filhos e parentes de Hosa foram 13 ao todo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11090, 12, "Essas divisões dos porteiros, feitas pelos chefes deles; eles cumpriam tarefas no serviço do templo do Senhor, assim como seus parentes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11091, 13, "Tiraram sortes entre as famílias, incluindo jovens e velhos, para que cuidassem de cada porta.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11092, 14, "E a porta leste coube a Selemias. Então tiraram sortes para seu filho Zacarias, sábio conselheiro, e a porta norte foi sorteada para ele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11093, 15, "A sorte da porta sul saiu para Obede-Edom, e a do depósito, para seus filhos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11094, 16, "A sorte da porta oeste e da porta Salequete, na rua de cima, saíram para Supim e Hosa. Os guardas ficavam um ao lado do outro:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11095, 17, "Havia seis levitas por dia no leste, quatro no norte, quatro no sul e dois de cada vez no depósito.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11096, 18, "Quanto ao pátio a oeste, havia quatro na rua e dois no próprio pátio.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11097, 19, "Foram essas as divisões dos porteiros, descendentes de Coré e Merari.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11098, 20, "Outros dos seus irmãos levitas estavam encarregados dos depósitos dos tesouros do templo de Deus e do depósito das dádivas sagradas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11099, 21, "Os gersonitas, descendentes de Ladã, que eram chefes de famílias pertencentes a Ladã, foram Jeieli", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11100, 22, "e seus filhos Zetã e seu irmão Joel. Estavam encarregados da tesouraria do templo do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11101, 23, "Dos filhos de Anrão, de Isar, de Hebrom e de Uziel:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11102, 24, "Sebuel, um descendente de Gérson, filho de Moisés, era o oficial encarregado dos depósitos dos tesouros.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11103, 25, "Seus parentes por parte de Eliézer foram seu filho Reabias, que foi o pai de Jesaías, o avô de Jorão, o bisavô de Zicri, o tataravô de Selomote.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11104, 26, "Selomote e seus parentes estavam encarregados de todos os tesouros consagrados pelo rei Davi, pelos chefes de famílias que eram os comandantes de mil e de cem, e pelos outros líderes do exército.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11105, 27, "Eles consagravam parte dos despojos tomados em combate para a manutenção do templo do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11106, 28, "E todas as dádivas consagradas pelo vidente Samuel e por Saul, filho de Quis, por Abner, filho de Ner, e por Joabe, filho de Zeruia, e todas as demais dádivas sagradas estavam sob os cuidados de Selomote e seus parentes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11107, 29, "Dos filhos de Isar, Quenanias e seus filhos ficaram responsáveis pelos negócios públicos de Israel, atuando como oficiais e juízes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11108, 30, "Dos filhos de Hebrom, Hasabias e seus parentes, que ficaram responsáveis em Israel, a oeste do Jordão, por todo o trabalho do Senhor e pelo serviço do rei; ao todo eram mil e setecentos homens capazes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11109, 31, "De acordo com os registros genealógicos das famílias hebronitas, Jerias foi o chefe delas. No ano quarenta do reinado de Davi fez-se uma busca nos registros, e entre os descendentes de Hebrom encontraram-se homens capazes, em Jazar de Gileade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11110, 32, "Jerias tinha dois mil e setecentos parentes, homens capazes e chefes de famílias, que o rei Davi encarregou de todas as questões pertinentes a Deus e aos negócios do rei nas tribos de Rúben e de Gade, e da metade da tribo de Manassés.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11111, 1, "Esta é a lista dos israelitas, chefes de famílias, comandantes de mil e comandantes de cem; oficiais que serviam o rei na supervisão das divisões do exército que estavam de serviço mês a mês, durante o ano. Cada divisão era constituída por 24. 000 homens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11112, 2, "Encarregado da primeira divisão de 24. 000 homens, para o primeiro mês, estava Jasobeão, filho de Zabdiel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11113, 3, "Ele era descendente de Perez e chefe de todos os oficiais do exército para o primeiro mês.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11114, 4, "Encarregado da divisão para o segundo mês estava Dodai, descendente de Aoí; Miclote era o líder da sua divisão que contava 24. 000 homens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11115, 5, "O terceiro comandante do exército, para o terceiro mês, foi Benaia, filho do sacerdote Joiada. Ele era chefe da sua divisão de 24. 000 homens.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11116, 6, "Esse Benaia foi guerreiro, chefe dos Trinta. Seu filho Amizabade estava encarregado da sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11117, 7, "O quarto, para o quarto mês, foi Asael, irmão de Joabe; seu filho Zebadias foi seu sucessor. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11118, 8, "O quinto, para o quinto mês, foi o comandante Samute, o izraíta. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11119, 9, "O sexto, para o sexto mês, foi Ira, filho de Iques, de Tecoa. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11120, 10, "O sétimo, para o sétimo mês, foi Helez, de Pelom, descendente de Efraim. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11121, 11, "O oitavo, para o oitavo mês, foi Sibecai, de Husate, da família de Zerá. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11122, 12, "O nono, para o nono mês, foi Abiezer, de Anatote, da tribo de Benjamim. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11123, 13, "O décimo, para o décimo mês, foi Maarai, de Netofate, da família de Zerá. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11124, 14, "O décimo primeiro, para o décimo primeiro mês, foi Benaia, de Piratom, descendente de Efraim. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11125, 15, "O décimo segundo, para o décimo segundo mês, foi Heldai, de Netofate, da família de Otoniel. Havia 24. 000 homens em sua divisão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11126, 16, "Estes foram os líderes das tribos de Israel: de Rúben: Eliézer, filho de Zicri; de Simeão: Sefatias, filho de Maaca;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11127, 17, "de Levi: Hasabias, filho de Quemuel; de Arão: Zadoque;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11128, 18, "de Judá: Eliú, irmão de Davi; de Issacar: Onri, filho de Micael;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11129, 19, "de Zebulom: Ismaías, filho de Obadias; de Naftali: Jeremote, filho de Azriel;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11130, 20, "dos descendentes de Efraim: Oséias, filho de Azazias; da metade da tribo de Manassés: Joel, filho de Pedaías;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11131, 21, "da outra metade da tribo de Manassés, em Gileade: Ido, filho de Zacarias; de Benjamim: Jaasiel, filho de Abner;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11132, 22, "de Dã: Azareel, filho de Jeroão. Foram esses os líderes das tribos de Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11133, 23, "Davi não contou os homens com menos de vinte anos, pois o Senhor havia prometido tornar Israel tão numeroso quanto as estrelas do céu.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11134, 24, "Joabe, filho de Zeruia, começou a contar os homens, mas não pôde terminar. A ira divina caiu sobre Israel por causa desse recenseamento, e o resultado não entrou nos registros históricos do rei Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11135, 25, "Azmavete, filho de Adiel, estava encarregado dos tesouros do palácio. Jônatas, filho de Uzias, estava encarregado dos depósitos do rei nos distritos distantes, nas cidades, nos povoados e nas torres de sentinela.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11136, 26, "Ezri, filho de Quelube, estava encarregado dos trabalhadores rurais, que cultivavam a terra.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11137, 27, "Simei, de Ramá, estava encarregado das vinhas. Zabdi, de Sifá, estava encarregado do vinho que era armazenado em tonéis.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11138, 28, "Baal-Hanã, de Gederá, estava encarregado das oliveiras e das figueiras bravas, na Sefelá. Joás estava encarregado do fornecimento de azeite.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11139, 29, "Sitrai, de Sarom, estava encarregado dos rebanhos que pastavam em Sarom. Safate, filho de Adlai, estava encarregado dos rebanhos nos vales.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11140, 30, "O ismaelita Obil estava encarregado dos camelos. Jedias, de Meronote, estava encarregado dos jumentos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11141, 31, "O hagareno Jaziz estava encarregado das ovelhas. Todos esses eram encarregados de cuidar dos bens do rei Davi.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11142, 32, "Jônatas, tio de Davi, era conselheiro; homem sábio e também escriba. Jeiel, filho de Hacmoni, cuidava dos filhos do rei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11143, 33, "Aitofel era conselheiro do rei. Husai, o arquita, era amigo do rei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11144, 34, "Aitofel foi sucedido por Joiada, filho de Benaia, e por Abiatar. Joabe era o comandante do exército real.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11145, 1, "Davi reuniu em Jerusalém todos os líderes de Israel: os líderes das tribos, os líderes das divisões a serviço do rei, os comandantes de mil e de cem, e os líderes encarregados de todos os bens e rebanhos que pertenciam ao rei e a seus filhos, juntamente com os funcionários do palácio, os principais guerreiros e todos os soldados valentes.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11146, 2, "O rei Davi se pôs de pé e disse: ""Escutem-me, meus irmãos e meu povo. Eu tinha no coração o propósito de construir um templo para nele colocar a arca da aliança do Senhor, o estrado dos pés de nosso Deus; fiz planos para construí-lo,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11147, 3, "mas Deus me disse: ‘Você não construirá um templo em honra do meu nome, pois você é um guerreiro e matou muita gente’.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11148, 4, """No entanto, o Senhor, o Deus de Israel, escolheu-me dentre toda a minha família para ser rei em Israel, para sempre. Ele escolheu Judá como líder, e da tribo de Judá escolheu minha família, e entre os filhos de meu pai ele quis fazer-me rei de todo o Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11149, 5, "E, dentre todos os muitos filhos que me deu, ele escolheu Salomão para sentar-se no trono de Israel, o reino do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11150, 6, "Ele me disse: ‘Seu filho Salomão é quem construirá o meu templo e os meus pátios, pois eu o escolhi para ser meu filho, e eu serei o pai dele.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11151, 7, "Firmarei para sempre o reino dele, se ele continuar a obedecer os meus mandamentos e as minhas ordenanças, como faz agora’.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11152, 8, """Por isso agora declaro-lhes perante todo o Israel e a assembléia do Senhor, e diante dos ouvidos de nosso Deus: Tenham o cuidado de obedecer a todos os mandamentos do Senhor, do seu Deus, para que mantenham a posse dessa boa terra e a dêem por herança aos seus descendentes para sempre.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11153, 9, """E você, meu filho Salomão, reconheça o Deus de seu pai, e sirva-o de todo o coração e espontaneamente, pois o Senhor sonda todos os corações e conhece a motivação dos pensamentos. Se você o buscar, o encontrará, mas, se você o abandonar, ele o rejeitará para sempre.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11154, 10, "Veja que o Senhor o escolheu para construir um templo que me sirva de santuário. Seja forte e mãos ao trabalho! """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11155, 11, "Então Davi deu a seu filho Salomão a planta do pórtico do templo, para os seus edifícios, para os seus depósitos, para os andares superiores e suas salas, e para o lugar do propiciatório.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11156, 12, "Entregou-lhe também as plantas de tudo o que o Espírito havia posto em seu coração acerca dos pátios do templo do Senhor e de todas as salas ao redor, para os depósitos dos tesouros do templo de Deus e para os depósitos das dádivas sagradas.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11157, 13, "Deu-lhe instruções sobre as divisões dos sacerdotes e dos levitas, e sobre à execução de todas as tarefas no templo do Senhor, e os utensílios que seriam utilizados.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11158, 14, "Determinou o peso do ouro para todos os utensílios de ouro e o peso da prata para todos os utensílios de prata que seriam utilizados nas diferentes tarefas:", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11159, 15, "o peso de ouro para cada candelabro e suas lâmpadas; e o peso de prata para cada candelabro de prata e suas lâmpadas, de acordo com a finalidade de cada um;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11160, 16, "o peso de ouro para cada mesa de pães consagrados; o peso de prata para as mesas de prata;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11161, 17, "o peso de ouro puro para os garfos, para as bacias de aspersão e para os jarros; o peso de ouro para cada tigela de ouro; o peso de prata para cada tigela de prata;", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11162, 18, "e o peso de ouro refinado para o altar de incenso. Também lhe deu o desenho do carro dos querubins de ouro que, com suas asas estendidas, abrigam a arca da aliança do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11163, 19, """Tudo isso"", disse Davi a Salomão, ""foi me dado por escrito pela mão do Senhor, e ele me deu entendimento para executar todos esses projetos. """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11164, 20, "E acrescentou: ""Seja forte e corajoso! Mãos ao trabalho! Não tenha medo nem se desanime, pois Deus, o Senhor, o meu Deus, está com você. Ele não o deixará nem o abandonará até que se termine toda a construção do templo do Senhor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11165, 21, "As divisões dos sacerdotes e dos levitas estão definidas para todas as tarefas que se farão no templo de Deus, e você receberá ajuda de homens peritos em todo tipo de serviço. Os líderes e todo o povo obedecerão a todas as suas ordens"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11166, 1, "Então o rei Davi disse a toda a assembléia: ""Meu filho Salomão, e só ele, foi quem Deus escolheu. Mas ele é jovem e inexperiente e a tarefa é grande, pois o palácio não será feito para homens, mas para o Senhor Deus.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11167, 2, "Forneci grande quantidade de recursos para o trabalho do templo do meu Deus: ouro, prata, bronze, ferro e madeira, bem como ônix para os engastes, e ainda turquesas, pedras de várias cores e todo tipo de pedras preciosas, e mármore.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11168, 3, "Além disso, pelo meu amor ao templo do meu Deus, agora entrego das minhas próprias riquezas, ouro e prata para o templo do meu Deus, além de tudo o que já tenho dado para este santo templo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11169, 4, "Ofereço, pois, cento e cinco toneladas de ouro puro de Ofir e duzentos e quarenta e cinco toneladas de prata refinada, para o revestimento das paredes do templo,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11170, 5, "para o trabalho em ouro e em prata, e para todo o trabalho dos artesãos. Agora, quem hoje está disposto a ofertar dádivas ao Senhor? """, 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11171, 6, "Então os chefes das famílias, os líderes das tribos de Israel, os comandantes de mil e de cem, e os oficiais encarregados do trabalho do rei ofertaram espontaneamente.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11172, 7, "Para a obra do templo de Deus eles deram cento e setenta e cinco toneladas de ouro e dez mil moedas de ouro, trezentas e cinqüenta toneladas de prata, seiscentas e trinta toneladas de bronze e três mil e quinhentas toneladas de ferro.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11173, 8, "Quem tinha pedras preciosas deu-as para o depósito dos tesouros do templo do Senhor, cujo responsável era Jeiel, o gersonita.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11174, 9, "O povo alegrou-se diante da atitude de seus líderes, pois fizeram essas ofertas voluntariamente e de coração íntegro ao Senhor. E o rei Davi também encheu-se de alegria.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11175, 10, "Davi louvou o Senhor na presença de toda a assembléia, dizendo: ""Bendito sejas, ó Senhor, Deus de Israel, nosso pai, de eternidade a eternidade.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11176, 11, "Teus, ó Senhor, são a grandeza, o poder, a glória, a majestade e o esplendor, pois tudo o que há nos céus e na terra é teu. Teu, ó Senhor, é o reino; tu estás acima de tudo.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11177, 12, "A riqueza e a honra vêm de ti; tu dominas sobre todas as coisas. Nas tuas mãos estão a força e o poder para exaltar e dar força a todos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11178, 13, "Agora, nosso Deus, damos-te graças, e louvamos o teu glorioso nome.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11179, 14, """Mas quem sou eu, e quem é o meu povo para que pudéssemos contribuir tão generosamente como fizemos? Tudo vem de ti, e nós apenas te demos o que vem das tuas mãos.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11180, 15, "Diante de ti somos estrangeiros e forasteiros, como os nossos antepassados. Os nossos dias na terra são como uma sombra, sem esperança.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11181, 16, "Ó Senhor, nosso Deus, toda essa riqueza que ofertamos para construir um templo em honra do teu santo nome vem das tuas mãos, e toda ela pertence a ti.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11182, 17, "Sei, ó meu Deus, que sondas o coração e que te agradas com a integridade. Tudo o que dei foi espontaneamente e com integridade de coração. E agora vi com alegria com quanta disposição o teu povo, que aqui está, tem contribuído.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11183, 18, "Ó Senhor, Deus de nossos antepassados Abraão, Isaque e Israel, conserva para sempre este desejo no coração de teu povo, e mantém o coração deles leal a ti.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11184, 19, "E dá ao meu filho Salomão um coração íntegro para obedecer aos teus mandamentos, aos teus preceitos e aos teus decretos, a fim de construir este templo para o qual fiz os preparativos necessários"".", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11185, 20, "Então Davi disse a toda a assembléia: ""Louvem o Senhor, o seu Deus"". E todos eles louvaram o Senhor, o Deus dos seus antepassados, inclinando-se e prostrando-se diante do Senhor e diante do rei.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11186, 21, "No dia seguinte fizeram sacrifícios ao Senhor e lhe apresentaram holocaustos: mil novilhos, mil carneiros e mil cordeiros, acompanhados de ofertas derramadas, e muitos outros sacrifícios, em favor de todo o Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11187, 22, "Naquele dia, comeram e beberam com grande alegria na presença do Senhor. Então pela segunda vez proclamaram Salomão, filho de Davi, como rei, ungindo-o diante do Senhor como soberano, e Zadoque como sacerdote.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11188, 23, "De maneira que Salomão assentou-se como rei no trono do Senhor, em lugar de Davi, seu pai. Ele prosperou, e todo o Israel lhe obedecia.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11189, 24, "Todos os líderes e principais guerreiros, bem como todos os filhos do rei Davi prometeram submissão ao rei Salomão.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11190, 25, "O Senhor exaltou muitíssimo Salomão em todo o Israel e concedeu-lhe tal esplendor em seu reinado como nenhum rei de Israel jamais tivera.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11191, 26, "Davi, filho de Jessé, reinou sobre todo Israel.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11192, 27, "Ele reinou quarenta anos em Israel: sete anos em Hebrom e trinta e três em Jerusalém.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11193, 28, "Morreu em boa velhice, tendo desfrutado vida longa, riqueza e honra. Seu filho Salomão foi o seu sucessor.", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11194, 29, "Os feitos do rei Davi, desde o início até o fim do seu reinado, estão escritos nos registros do vidente Samuel, do profeta Natã e do vidente Gade,", 12);
INSERT INTO verses (id, number, text, chapter_id) VALUES (11195, 30, "incluindo os detalhes do seu reinado e do seu poder, e os acontecimentos relacionados a ele, com Israel e com os reinos das outras terras.", 12);