INSERT INTO verses (id, number, text, chapter_id) VALUES (8719, 1, "Quando o rei Davi envelheceu, já de idade bem avançada, cobriam-no de cobertores, mas ele não se aquecia.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8720, 2, "Por isso os seus servos lhe propuseram: ""Nós vamos procurar uma jovem virgem para servir e cuidar do rei. Ela se deitará ao seu lado, afim de aquecer o rei"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8721, 3, "Então procuraram em todo o território de Israel uma jovem que fosse bonita e encontraram Abisague, uma sunamita, e a levaram ao rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8722, 4, "A jovem, muito bonita, cuidava do rei e o servia, mas o rei não teve relações com ela.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8723, 5, "Ora, Adonias, cuja mãe se chamava Hagite, tomou a dianteira e disse: ""Eu serei o rei"". Providenciou uma carruagem e cavalos, além de cinqüenta homens para correrem à sua frente.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8724, 6, "Seu pai nunca o havia contrariado; nunca lhe perguntava: ""Por que você age assim? "" Adonias também tinha boa aparência e havia nascido depois de Absalão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8725, 7, "Adonias fez acordo com Joabe, filho de Zeruia, e com o sacerdote Abiatar, e eles, o seguiram e o apoiaram.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8726, 8, "Mas o sacerdote Zadoque, Benaia, filho de Joiada, o profeta Natã, Simei, Reí e a guarda especial de Davi não deram apoio a Adonias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8727, 9, "Então Adonias sacrificou ovelhas, bois e novilhos gordos junto à pedra de Zoelete, próximo a En-Rogel. Convidou todos os seus irmãos, filhos do rei, e todos os homens de Judá que eram conselheiros do rei,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8728, 10, "mas não convidou o profeta Natã nem Benaia nem a guarda especial nem o seu irmão Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8729, 11, "Natã perguntou então a Bate-Seba, mãe de Salomão: ""Você ainda não sabe que Adonias, o filho de Hagite, tornou-se rei, sem que o nosso senhor Davi ficasse sabendo?", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8730, 12, "Agora, vou dar-lhe um conselho para salvar a sua vida e também a vida do seu filho Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8731, 13, "Vá perguntar ao rei Davi: Ó rei, meu senhor, não juraste a esta tua serva, prometendo: ‘Tenha certeza que o seu filho Salomão me sucederá como rei, e se assentará no meu trono? ’ Por que foi então, que Adonias se tornou rei?", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8732, 14, "Enquanto você ainda estiver conversando com o rei, eu entrarei e confirmarei as suas palavras"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8733, 15, "Então Bate-Seba foi até o quarto do rei, já idoso, onde a sunamita Abisague cuidava dele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8734, 16, "Bate-Seba ajoelhou-se e prostrou-se diante do rei. ""O que você quer? "", o rei perguntou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8735, 17, "Ela respondeu: ""Meu senhor, tu mesmo juraste a esta tua serva, pelo Senhor o teu Deus: ‘Seu filho Salomão o sucederá como rei e se assentará no meu trono’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8736, 18, "Mas agora Adonias se tornou rei, sem que o rei meu senhor o soubesse.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8737, 19, "Ele sacrificou muitos bois, novilhos gordos e ovelhas, e convidou todos os filhos do rei, o sacerdote Abiatar, e Joabe, o comandante do exército, mas não convidou o teu servo Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8738, 20, "Agora, ó rei, meu senhor, os olhos de todo o Israel estão sobre ti para saber de tua parte, quem sucederá ao rei, meu senhor, no trono.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8739, 21, "De outro modo, tão logo o rei, meu senhor descanse com os seus antepassados, eu e o meu filho Salomão seremos tratados como traidores"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8740, 22, "Ela ainda conversava com o rei, quando o profeta Natã chegou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8741, 23, "Assim que informaram o rei que o profeta Natã havia chegado, ele entrou e prostrou-se, rosto em terra, diante do rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8742, 24, "E Natã lhe perguntou: ""Ó rei, meu senhor, por acaso declaraste que Adonias te sucederia como rei e que ele se assentaria no teu trono?", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8743, 25, "Hoje ele foi matar muitos bois, novilhos gordos e ovelhas. Convidou todos os filhos do rei, os comandantes do exército e o sacerdote Abiatar. Agora eles estão comendo e bebendo com ele e comemorando: ‘Viva o rei Adonias! ’", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8744, 26, "Mas ele não convidou a mim, que sou teu servo, nem ao sacerdote Zadoque nem a Benaia, filho de Joiada, nem a teu servo Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8745, 27, "Seria isto algo que o rei, meu senhor, fez sem deixar que os seus conselheiros soubessem quem sucederia ao rei, meu senhor, no trono? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8746, 28, "Então o rei Davi ordenou: ""Chamem Bate-Seba"". Ela entrou e ficou de pé diante dele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8747, 29, "O rei fez então um juramento: ""Juro pelo nome do Senhor, o qual me livrou de todas as adversidades,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8748, 30, "que sem dúvida hoje mesmo vou executar o que jurei pelo Senhor, o Deus de Israel. O seu filho Salomão me sucederá como rei e se assentará no meu trono em meu lugar"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8749, 31, "Então Bate-Seba prostrou-se, rosto em terra, e, ajoelhando-se diante do rei, disse: ""Que o rei Davi, meu senhor, viva para sempre! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8750, 32, "O rei Davi ordenou: ""Chamem o sacerdote Zadoque, o profeta Natã e Benaia, filho de Joiada"". Quando chegaram à presença do rei,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8751, 33, "ele os instruiu: ""Levem os conselheiros do seu senhor com vocês e ponham o meu filho Salomão sobre a minha mula e levem-no a Giom.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8752, 34, "Ali o sacerdote Zadoque e o profeta Natã o ungirão rei sobre Israel. Então toquem a trombeta e gritem: Viva o rei Salomão!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8753, 35, "Depois acompanhem-no, e ele virá assentar-se no meu trono e reinará em meu lugar. Eu o designei para governar Israel e Judá"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8754, 36, "Benaia, filho de Joiada, respondeu ao rei: ""Assim se fará! Que o Senhor, o Deus do rei, meu senhor, o confirme.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8755, 37, "Assim como o Senhor esteve com o rei, meu senhor, também esteja ele com Salomão para que ele tenha um reinado ainda mais glorioso do que o meu senhor, o rei Davi! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8756, 38, "Então o sacerdote Zadoque, o profeta Natã, Benaia, filho de Joiada, os queretitas e os peletitas fizeram Salomão montar a mula do rei Davi e o escoltaram até Giom.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8757, 39, "O sacerdote Zadoque pegou na Tenda o chifre com óleo e ungiu Salomão. Então tocaram a trombeta e todo o povo gritou: ""Viva o rei Salomão! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8758, 40, "E todo o povo o acompanhou, tocando flautas e comemorando, de tal forma que o chão tremia com o barulho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8759, 41, "Adonias e todos os seus convidados souberam disso quando estavam terminando o banquete. Ao ouvir o toque da trombeta, Joabe perguntou: ""O que significa esta gritaria, esse alvoroço na cidade? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8760, 42, "Falava ele ainda, quando chegou Jônatas, filho do sacerdote Abiatar. E Adonias lhe disse: ""Entre, pois um homem digno como você deve estar trazendo boas notícias! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8761, 43, """De modo algum"", respondeu Jônatas a Adonias. ""Davi, o nosso rei e senhor, constituiu rei a Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8762, 44, "O rei enviou com ele o sacerdote Zadoque, o profeta Natã, Benaia, filho de Joiada, os queretitas e os peletitas, e eles o fizeram montar a mula do rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8763, 45, "Depois o sacerdote Zadoque e o profeta Natã o ungiram rei em Giom. De lá eles saíram comemorando, e a cidade está alvoroçada. É esse o barulho que vocês ouvem.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8764, 46, "Além disso, Salomão já se assentou no trono real.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8765, 47, "Até mesmo os oficiais do rei foram cumprimentar Davi, o nosso rei e senhor, dizendo: ‘Que o teu Deus torne o nome de Salomão mais famoso que o teu, e o seu reinado mais glorioso do que o teu! ’ E o rei curvou-se reverentemente em sua cama,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8766, 48, "e disse: ‘Bendito seja o Senhor, o Deus de Israel, que permitiu que os meus olhos vissem hoje um sucessor em meu trono’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8767, 49, "Diante disso, todos os convidados de Adonias entraram em pânico e se dispersaram.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8768, 50, "Mas Adonias, com medo de Salomão, foi agarrar-se às pontas do altar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8769, 51, "Então informaram a Salomão: ""Adonias está com medo do rei Salomão e está agarrado às pontas do altar. Ele diz: ‘Que o rei Salomão me jure que não matará o seu servo pela espada’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8770, 52, "Salomão respondeu: ""Se ele se mostrar confiável, não cairá nem um só fio de cabelo da sua cabeça; mas se nele se descobrir alguma maldade, ele morrerá"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8771, 53, "Então o rei enviou alguns soldados, e eles o fizeram descer do altar. E Adonias veio e se curvou solenemente perante o rei Salomão, que lhe disse: ""Vá para casa"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8772, 1, "Quando se aproximava o dia de sua morte, Davi deu instruções ao seu filho Salomão:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8773, 2, """Estou para seguir o caminho de toda a terra. Por isso, seja forte e seja homem.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8774, 3, "Obedeça ao que o Senhor, o seu Deus, exige: Ande nos seus caminhos e obedeça aos seus decretos, aos seus mandamentos, às suas ordenanças e aos seus testemunhos, conforme se acham escritos na Lei de Moisés; assim você prosperará em tudo o que fizer e por onde quer que for,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8775, 4, "e o Senhor manterá a promessa que me fez: ‘Se os seus descendentes cuidarem de sua conduta, e se me seguirem fielmente de todo o coração e de toda a alma, você jamais ficará sem descendente no trono de Israel’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8776, 5, """Você sabe muito bem o que Joabe, filho de Zeruia, me fez; o que fez com os dois comandantes dos exércitos de Israel, Abner, filho de Ner, e Amasa, filho de Jéter. Ele os matou, derramando sangue em tempos de paz; agiu como se estivesse em guerra, e com aquele sangue manchou o seu cinto e as suas sandálias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8777, 6, "Proceda com a sabedoria que você tem, e não o deixe envelhecer e descer em paz à sepultura.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8778, 7, """Mas seja bondoso com os filhos de Barzilai, de Gileade, admita-os entre os que comem à mesa com você, pois eles me apoiaram quando fugi do seu irmão Absalão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8779, 8, """Saiba que também está com você Simei, filho de Gera, o benjamita de Baurim. Ele lançou terríveis maldições contra mim no dia em que fui a Maanaim. Mas depois desceu ao meu encontro no Jordão e lhe prometi jurando pelo Senhor que não o mataria à espada.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8780, 9, "Mas, agora, não o considere inocente. Você é um homem sábio e saberá o que fazer com ele; apesar de ele já ser idoso, faça-o descer ensangüentado à sepultura"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8781, 10, "Então Davi descansou com os seus antepassados e foi sepultado na cidade de Davi.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8782, 11, "Ele reinou quarenta anos em Israel: sete anos em Hebrom e trinta e três em Jerusalém.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8783, 12, "Salomão assentou-se no trono de Davi, seu pai, e o seu reinado foi firmemente estabelecido.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8784, 13, "Adonias, o filho de Hagite, foi até Bate-Seba, mãe de Salomão. E esta lhe perguntou: ""Você vem em paz? "" Ele respondeu: ""Sim"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8785, 14, "E acrescentou: ""Tenho algo para lhe dizer"". Ela disse: Fale!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8786, 15, """Você sabe"", disse ele, ""que o reino era meu. Todo o Israel me via como o seu rei. Mas as circunstâncias mudaram, e o reino foi para o meu irmão; pois o Senhor o concedeu a ele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8787, 16, "Agora, quero fazer-lhe um pedido e espero que não me seja negado. "" Ela disse: ""Fale! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8788, 17, "Então ele prosseguiu: ""Peça, por favor, ao rei Salomão que me dê a sunamita Abisague por mulher, pois ele não deixará de atender você"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8789, 18, """Está bem"", respondeu Bate-Seba, ""falarei com o rei em seu favor. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8790, 19, "Quando Bate-Seba foi falar ao rei em favor de Adonias, Salomão levantou-se para recebê-la e inclinou-se diante dela. Depois assentou-se no seu trono, mandou que trouxessem um trono para a sua mãe, e ela se assentou à sua direita.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8791, 20, """Tenho um pequeno pedido para lhe fazer"", disse ela. ""Não deixe de me atender. "" O rei respondeu: ""Faça o pedido, minha mãe; não deixarei de atendê-lo"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8792, 21, "Então ela disse: ""Dê a sunamita Abisague por mulher a seu irmão Adonias"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8793, 22, "O rei Salomão perguntou à sua mãe: ""Por que você pede somente a sunamita Abisague para Adonias? Peça logo o reino para ele, para o sacerdote Abiatar e para Joabe, filho de Zeruia; afinal ele é o meu irmão mais velho! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8794, 23, "Então o rei Salomão jurou pelo Senhor: ""Que Deus me castigue com todo o rigor, se isso que Adonias falou não lhe custar a sua própria vida!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8795, 24, "E agora eu juro pelo nome do Senhor, que me estabeleceu no trono de meu pai Davi, e, conforme prometeu, fundou uma dinastia para mim, que hoje mesmo Adonias será morto! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8796, 25, "E o rei Salomão deu ordem a Benaia, filho de Joiada, e este feriu e matou Adonias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8797, 26, "Ao sacerdote Abiatar o rei ordenou: ""Vá para Anatote, para as suas terras! Você merece morrer, mas hoje eu não o matarei, pois você carregou a arca do Soberano Senhor diante de Davi, meu pai, e partilhou de todas as aflições dele"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8798, 27, "Então Salomão expulsou Abiatar do sacerdócio do Senhor, cumprindo a palavra que o Senhor tinha dito em Siló a respeito da família de Eli.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8799, 28, "Quando a notícia chegou a Joabe, que havia conspirado com Adonias, ainda que não com Absalão, ele fugiu para a Tenda do Senhor e agarrou-se às pontas do altar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8800, 29, "Foi dito ao rei Salomão que Joabe havia se refugiado na Tenda do Senhor e estava ao lado do altar. Então Salomão ordenou a Benaia, filho de Joiada: ""Vá matá-lo! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8801, 30, "Então Benaia entrou na Tenda do Senhor e disse a Joabe: ""O rei lhe ordena que saia"". ""Não"", respondeu ele, ""Vou morrer aqui"". Benaia relatou ao rei a resposta de Joabe.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8802, 31, "Então o rei ordenou a Benaia: ""Faça o que ele diz. Mate-o e sepulte-o, e assim você retirará de mim e da minha família a culpa do sangue inocente que Joabe derramou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8803, 32, "O Senhor fará recair sobre a cabeça dele o sangue que derramou: ele atacou dois homens mais justos e melhores do que ele, sem o conhecimento de meu pai Davi, e os matou à espada. Os dois homens eram Abner, filho de Ner, comandante do exército de Israel, e Amasa, filho de Jéter, comandante do exército de Judá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8804, 33, "Que o sangue deles recaia sobre a cabeça de Joabe e sobre a dos seus descendentes para sempre. Mas que a paz do Senhor esteja para sempre sobre Davi, sobre os seus descendentes, sobre a sua dinastia e sobre o seu trono"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8805, 34, "Então Benaia, filho de Joiada, foi e atacou Joabe e o matou, e ele foi sepultado em sua casa no campo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8806, 35, "No lugar dele o rei nomeou Benaia, filho de Joiada, no comando do exército, e o sacerdote Zadoque no lugar de Abiatar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8807, 36, "Depois o rei mandou chamar Simei e lhe ordenou: ""Construa para você uma casa em Jerusalém. Você morará nela e não poderá ir para nenhum outro lugar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8808, 37, "Esteja certo de que no dia em que sair e atravessar o vale de Cedrom, você será morto; e você será responsável por sua própria morte"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8809, 38, "Simei respondeu ao rei: ""A ordem do rei é boa! O teu servo lhe obedecerá"". E Simei permaneceu em Jerusalém por muito tempo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8810, 39, "Mas três anos depois, dois escravos de Simei fugiram para a casa de Aquis, filho de Maaca, rei de Gate. Alguém contou a Simei: ""Seus escravos estão em Gate"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8811, 40, "Então Simei selou um jumento e foi até Aquis, em Gate, procurar os seus escravos. E de lá Simei trouxe os escravos de volta.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8812, 41, "Quando Salomão soube que Simei tinha ido a Gate e voltado a Jerusalém,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8813, 42, "mandou chamá-lo e lhe perguntou: ""Eu não fiz você jurar pelo Senhor e não o adverti: No dia em que você for para qualquer outro lugar, esteja certo que você morrerá? E você me respondeu: ‘Esta ordem é boa! Obedecerei’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8814, 43, "Por que não manteve o juramento ao Senhor e não obedeceu à ordem que lhe dei? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8815, 44, "E acrescentou: ""No seu coração você sabe como você prejudicou o meu pai Davi. Agora o Senhor faz recair sua maldade sobre a sua cabeça.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8816, 45, "Mas o rei Salomão será abençoado, e o trono de Davi será estabelecido perante o Senhor para sempre"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8817, 46, "Então o rei deu ordem a Benaia, filho de Joiada, e este atacou Simei e o matou. Assim o reino ficou bem estabelecido nas mãos de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8818, 1, "Salomão aliou-se ao faraó, rei do Egito, casando-se com a filha dele. Ele a trouxe à cidade de Davi até terminar a construção do seu palácio e do templo do Senhor, e do muro em torno de Jerusalém.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8819, 2, "O povo, porém, sacrificava nos lugares sagrados, pois ainda não tinha sido construído um templo em honra do nome do Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8820, 3, "Salomão amava o Senhor, pois andava de acordo com os decretos do seu pai Davi; todavia oferecia sacrifícios e queimava incenso nos lugares sagrados.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8821, 4, "O rei Salomão foi a Gibeom para oferecer sacrifícios, pois ali ficava o principal lugar sagrado, e ofereceu naquele altar mil holocaustos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8822, 5, "Em Gibeom o Senhor apareceu a Salomão num sonho, à noite, e lhe disse: ""Peça-me o que quiser, e eu lhe darei"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8823, 6, "Salomão respondeu: ""Tu foste muito bondoso para com o teu servo, o meu pai Davi, pois ele foi fiel a ti, e foi justo e reto de coração. Tu sustentaste grande bondade para com ele e lhe deste um filho que hoje se assenta no seu trono.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8824, 7, """Agora, Senhor meu Deus, fizeste o teu servo reinar em lugar de meu pai Davi. Mas eu não passo de um jovem e não sei o que fazer.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8825, 8, "Teu servo está aqui entre o povo que escolheste, um povo tão grande que nem se pode contar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8826, 9, "Dá, pois, ao teu servo um coração cheio de discernimento para governar o teu povo e capaz de distinguir entre o bem e o mal. Pois, quem pode governar este teu grande povo? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8827, 10, "O pedido que Salomão fez agradou ao Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8828, 11, "Por isso Deus lhe disse: ""Já que você pediu isto e não uma vida longa nem riqueza, nem pediu a morte dos seus inimigos, mas discernimento para ministrar a justiça,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8829, 12, "farei o que você pediu. Eu lhe darei um coração sábio e capaz de discernir, de modo que nunca houve nem haverá ninguém como você.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8830, 13, "Também lhe darei o que você não pediu: riquezas e fama; de forma que não haverá rei igual a você durante toda a sua vida.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8831, 14, "E, se você andar nos meus caminhos e obedecer aos meus decretos e aos meus mandamentos, como o seu pai Davi, eu prolongarei a sua vida"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8832, 15, "Então Salomão acordou e percebeu que tinha sido um sonho. Depois voltou a Jerusalém, pôs-se perante a arca da aliança do Senhor, sacrificou holocaustos e apresentou ofertas de comunhão. Então deu um banquete para toda a sua corte.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8833, 16, "Certo dia duas prostitutas compareceram diante do rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8834, 17, "Uma delas disse: ""Ah meu senhor! Esta mulher mora comigo na mesma casa. Eu dei à luz um filho e ela estava comigo na casa.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8835, 18, "Três dias depois de nascer o meu filho, esta mulher também deu à luz um filho. Estávamos sozinhas, e não havia mais ninguém na casa.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8836, 19, """Certa noite esta mulher se deitou sobre o seu filho, e ele morreu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8837, 20, "Então ela se levantou no meio da noite e pegou o meu filho enquanto eu, tua serva, dormia, e o pôs ao seu lado. E pôs o filho dela, morto, ao meu lado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8838, 21, "Ao levantar-me de madrugada para amamentar o meu filho, ele estava morto. Mas quando olhei bem para ele de manhã, vi que não era o filho que eu dera à luz"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8839, 22, "A outra mulher disse: ""Não! O que está vivo é meu filho; o morto é seu"". Mas a primeira insistia: ""Não! O morto é seu; o vivo é meu"". Assim elas discutiram diante do rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8840, 23, "O rei disse: ""Esta afirma: ‘Meu filho está vivo, e o seu filho está morto’, enquanto aquela diz: ‘Não! Seu filho está morto, e o meu está vivo’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8841, 24, "Então o rei ordenou: ""Tragam-me uma espada"". Trouxeram-lhe.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8842, 25, "Ele então ordenou: ""Cortem a criança viva ao meio e dêem metade a uma e metade à outra"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8843, 26, "A mãe do filho que estava vivo, movida pela compaixão materna, clamou: ""Por favor, meu senhor, dê a criança viva a ela! Não a mate! "" A outra, porém, disse: ""Não será nem minha nem sua. Cortem-na ao meio! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8844, 27, "Então o rei deu o seu veredicto: ""Não matem a criança! Dêem-na à primeira mulher. Ela é a mãe"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8845, 28, "Quando todo o Israel ouviu o veredicto do rei, passou a respeitá-lo profundamente, pois viu que a sabedoria de Deus estava nele para fazer justiça.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8846, 1, "E assim o rei Salomão tornou-se rei sobre todo o Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8847, 2, "E estes foram os seus principais assessores: Azarias, filho de Zadoque: o sacerdote;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8848, 3, "Eliorefe e Aías, filhos de Sisa: secretários; Josafá, filho de Ailude: arquivista real;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8849, 4, "Benaia, filho de Joiada: comandante do exército; Zadoque e Abiatar: sacerdotes;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8850, 5, "Azarias, filho de Natã: responsável pelos governadores distritais; Zabude, filho de Natã: sacerdote e conselheiro pessoal do rei;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8851, 6, "Aisar: responsável pelo palácio; Adonirão, filho de Abda: chefe de trabalhos forçados.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8852, 7, "Salomão tinha também, doze governadores distritais em todo o Israel, que forneciam provisões para o rei e para o palácio real. Cada um deles tinha que fornecer suprimentos durante um mês do ano.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8853, 8, "Estes são os seus nomes: Ben-Hur, nos montes de Efraim;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8854, 9, "Ben-Dequer, em Macaz, Saalbim, Bete-Semes e Elom-Bete-Hanã;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8855, 10, "Ben-Hesede, em Arubote, Socó e toda a terra de Héfer;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8856, 11, "Ben-Abinadabe, em Nafote-Dor. Tafate, filha de Salomão, era sua mulher;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8857, 12, "Baaná, filho de Ailude, em Taanaque e Megido e toda Bete-Seã, próxima de Zaretã, abaixo de Jezreel, desde Bete-Seã até Abel-Meolá, indo além dos limites de Jocmeão;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8858, 13, "Ben-Geder, em Ramote-Gileade e nos povoados de Jair, filho de Manassés, em Gileade, bem como o distrito de Argobe, em Basã, e as suas sessenta grandes cidades muradas com trancas de bronze nos portões;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8859, 14, "Ainadabe, filho de Ido, em Maanaim;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8860, 15, "Aimaás, em Naftali. Ele se casou com Basemate, filha de Salomão;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8861, 16, "Baaná, filho de Husai, em Aser e em Bealote;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8862, 17, "Josafá, filho de Parua, em Issacar;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8863, 18, "Simei, filho de Elá, em Benjamim;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8864, 19, "Geber, filho de Uri, em Gileade, a terra de Seom, rei dos amorreus, e de Ogue, rei de Basã. Ele era o único governador desse distrito.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8865, 20, "Judá e Israel eram tão numerosos como a areia da praia; eles comiam, bebiam e eram felizes.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8866, 21, "E Salomão governava todos os reinos, desde o Rio até a terra dos filisteus, chegando até a fronteira do Egito. Esses reinos traziam tributos e foram submissos a Salomão durante toda a sua vida.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8867, 22, "As provisões diárias de Salomão eram trinta tonéis da melhor farinha e sessenta tonéis de farinha comum,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8868, 23, "dez cabeças de gado engordado em cocheiras, vinte de gado engordado no pasto e cem ovelhas e bodes, bem como cervos, gazelas, corças e aves escolhidas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8869, 24, "Ele governava todos os reinos a oeste do Rio, desde Tifsa até Gaza, e tinha paz em todas as fronteiras.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8870, 25, "Durante a vida de Salomão, Judá e Israel viveram em segurança, cada homem debaixo da sua videira e da sua figueira, desde Dã até Berseba.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8871, 26, "Salomão possuía quatro mil cocheiras para cavalos de carros de guerra, e doze mil cavalos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8872, 27, "Todo mês um dos governadores distritais fornecia provisões ao rei Salomão e a todos os que vinham participar de sua mesa. Cuidavam para que não faltasse nada.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8873, 28, "Também traziam ao devido lugar suas quotas de cevada e de palha para os cavalos de carros de guerra e para os outros cavalos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8874, 29, "Deus deu a Salomão sabedoria, discernimento extraordinário e uma abrangência de conhecimento tão imensurável quanto a areia do mar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8875, 30, "A sabedoria de Salomão era maior do que a de todos os homens do oriente, bem como de toda a sabedoria do Egito.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8876, 31, "Ele era mais sábio do que qualquer outro homem, mais do que o ezraíta Etã; mais sábio do que Hemã, Calcol e Darda, filhos de Maol. E a sua fama espalhou-se por todas as nações em redor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8877, 32, "Ele compôs três mil provérbios, e os seus cânticos chegaram a mil e cinco.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8878, 33, "Descreveu as plantas, desde o cedro do Líbano até o hissopo que brota nos muros. Também discorreu sobre os quadrúpedes, as aves, os animais que se movem rente ao chão e os peixes.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8879, 34, "Homens de todas as nações vinham ouvir a sabedoria de Salomão. Eram enviados por todos os reis que tinham ouvido falar de sua sabedoria.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8880, 1, "Quando Hirão, rei de Tiro, soube que Salomão tinha sido ungido rei, mandou seus conselheiros a Salomão, pois sempre tinha sido amigo leal de Davi.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8881, 2, "Salomão enviou esta mensagem a Hirão:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8882, 3, """Tu bem sabes que foi por causa das guerras travadas de todos os lados contra meu pai Davi que ele não pôde construir um templo em honra do nome do Senhor, o seu Deus, até que o Senhor pusesse os seus inimigos debaixo dos seus pés.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8883, 4, "Mas agora o Senhor, o meu Deus, concedeu-me paz em todas as fronteiras, e não tenho que enfrentar nem inimigos nem calamidades.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8884, 5, "Pretendo, por isso, construir um templo em honra do nome do Senhor, do meu Deus, conforme o Senhor disse a meu pai Davi: ‘O seu filho, a quem colocarei no trono em seu lugar, construirá o templo em honra do meu nome’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8885, 6, """Agora te peço que ordenes que cortem para mim cedros do Líbano. Os meus servos trabalharão com os teus, e eu pagarei a teus servos o salário que determinares. Sabes que não há entre nós ninguém tão hábil em cortar árvores quanto os sidônios"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8886, 7, "Hirão ficou muito alegre quando ouviu a mensagem de Salomão, e exclamou: ""Bendito seja o Senhor, pois deu a Davi um filho sábio para governar essa grande nação"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8887, 8, "E Hirão respondeu a Salomão: ""Recebi a mensagem que me enviaste e atenderei ao teu pedido, enviando-lhe madeira de cedro e de pinho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8888, 9, "Meus servos levarão a madeira do Líbano até o mar, e eu a farei flutuar em jangadas no mar até o lugar que me indicares. Ali eu a deixarei e tu poderás levá-la. E em troca, fornecerás alimento para a minha corte"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8889, 10, "Assim Hirão se tornou fornecedor de toda a madeira de cedro e de pinho que Salomão desejava,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8890, 11, "e Salomão deu a Hirão vinte mil tonéis de trigo para suprir de mantimento a sua corte, além de vinte mil tonéis de azeite de oliva puro. Era o que Salomão dava anualmente a Hirão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8891, 12, "O Senhor deu sabedoria a Salomão, como lhe havia prometido. Houve paz entre Hirão e Salomão, e os dois fizeram um tratado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8892, 13, "O rei Salomão arregimentou trinta mil trabalhadores de todo o Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8893, 14, "Ele os mandou para o Líbano em grupos de dez mil por mês, e eles se revezavam: passavam um mês no Líbano e dois em casa. Adonirão chefiava o trabalho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8894, 15, "Salomão tinha setenta mil carregadores e oitenta mil cortadores de pedra nas colinas,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8895, 16, "bem como três mil e trezentos capatazes que supervisionavam o trabalho e comandavam os operários.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8896, 17, "Por ordem do rei retiravam da pedreira grandes blocos de pedra de ótima qualidade para servirem de alicerce de pedras lavradas para o templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8897, 18, "Os construtores de Salomão e de Hirão e os homens de Gebal cortavam e preparavam a madeira e as pedras para a construção do templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8898, 1, "Quatrocentos e oitenta anos depois que os israelitas saíram do Egito, no quarto ano do reinado de Salomão em Israel, no mês de zive, o segundo mês, ele começou a construir o templo do Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8899, 2, "O templo que o rei Salomão construiu para o Senhor media vinte e sete metros de comprimento, nove metros de largura e treze metros e meio de altura.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8900, 3, "O pórtico da entrada do santuário tinha a largura do templo, que era de nove metros, e avançava quatro metros e meio à frente do templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8901, 4, "Ele fez para o templo, janelas com grades estreitas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8902, 5, "Junto às paredes do átrio principal e do santuário interior, construiu uma estrutura em torno do edifício, na qual havia salas laterais.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8903, 6, "O andar inferior tinha dois metros e vinte e cinco centímetros de largura, o andar intermediário tinha dois metros e setenta centímetros e o terceiro andar tinha três metros e quinze centímetros. Ele fez saliências de apoio nas paredes externas do templo, de modo que não houve necessidade de perfurar as paredes.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8904, 7, "Na construção do templo só foram usados blocos lavrados nas pedreiras, e não se ouviu no templo nenhum barulho de martelo, nem de talhadeira, nem de qualquer outra ferramenta de ferro durante a sua construção.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8905, 8, "A entrada para o andar inferior ficava no lado sul do templo; uma escada conduzia até o andar intermediário e dali ao terceiro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8906, 9, "Assim ele construiu o templo e o terminou, fazendo-lhe um forro com vigas e tábuas de cedro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8907, 10, "E fez as salas laterais ao longo de todo o templo. Cada uma tinha dois metros e vinte e cinco centímetros de altura, e elas estavam ligadas ao templo por vigas de cedro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8908, 11, "E a palavra do Senhor veio a Salomão dizendo:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8909, 12, """Quanto a este templo que você está construindo, se você seguir os meus decretos, executar os meus juízos e obedecer a todos os meus mandamentos, cumprirei por meio de você a promessa que fiz ao seu pai Davi,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8910, 13, "viverei no meio dos israelitas e não abandonarei Israel, o meu povo"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8911, 14, "E assim Salomão concluiu a construção do templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8912, 15, "Forrou as paredes do templo por dentro com tábuas de cedro, cobrindo-as desde o chão até o teto, e fez o soalho do templo com tábuas de pinho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8913, 16, "Separou nove metros na parte de trás do templo, fazendo uma divisão com tábuas de cedro, do chão ao teto, para formar dentro do templo o santuário interno, o Lugar Santíssimo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8914, 17, "O átrio principal em frente dessa sala media dezoito metros de comprimento.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8915, 18, "O interior do templo era de cedro, com figuras entalhadas de frutos e flores abertas. Tudo era de cedro; não se via pedra alguma.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8916, 19, "Preparou também o santuário interno no templo para ali colocar a arca da aliança do Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8917, 20, "O santuário interno tinha nove metros de comprimento, nove de largura e nove de altura. Ele revestiu o interior de ouro puro, e também revestiu de ouro o altar de cedro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8918, 21, "Salomão cobriu o interior do templo de ouro puro, e estendeu correntes de ouro em frente do santuário interno, que também foi revestido de ouro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8919, 22, "Assim, revestiu de ouro todo o interior do templo e também o altar que pertencia ao santuário interno.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8920, 23, "No santuário interno ele esculpiu dois querubins de madeira de oliveira, cada um com quatro metros e meio de altura.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8921, 24, "As asas abertas dos querubins mediam dois metros e vinte e cinco centímetros: quatro metros e meio da ponta de uma asa à ponta da outra.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8922, 25, "Os dois querubins tinham a mesma medida e a mesma forma.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8923, 26, "A altura de cada querubim era de quatro metros e meio.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8924, 27, "Ele colocou os querubins, com as asas abertas, no santuário interno do templo. A asa de um querubim encostava numa parede, e a do outro encostava na outra. As suas outras asas encostavam uma na outra no meio do santuário.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8925, 28, "Ele revestiu os querubins de ouro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8926, 29, "Nas paredes ao redor do templo, tanto na parte interna como na externa, ele esculpiu querubins, tamareiras e flores abertas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8927, 30, "Também revestiu de ouro os pisos, tanto na parte interna como na externa do templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8928, 31, "Para a entrada do santuário interno fez portas de oliveira com batentes de cinco lados.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8929, 32, "E nas duas portas de madeira de oliveira esculpiu querubins, tamareiras e flores abertas, e revestiu os querubins e as tamareiras de ouro batido.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8930, 33, "Também fez pilares de quatro lados, de madeira de oliveira para a entrada do templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8931, 34, "Fez também duas portas de pinho, cada uma com duas folhas que se articulavam por meio de dobradiças.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8932, 35, "Entalhou figuras de querubins, de tamareiras e de flores abertas nas portas e as revestiu todas igualmente de ouro batido.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8933, 36, "E construiu o pátio interno com três camadas de pedra lavrada e uma de vigas de cedro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8934, 37, "O alicerce do templo do Senhor foi lançado no mês de zive do quarto ano.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8935, 38, "No mês de bul, o oitavo mês, do décimo primeiro ano, o templo foi terminado em todos os seus detalhes, de acordo com as suas especificações. Ele levou sete anos para construí-lo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8936, 1, "Salomão levou treze anos para terminar a construção do seu palácio.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8937, 2, "Ele construiu o Palácio da Floresta do Líbano com quarenta e cinco metros de comprimento, vinte e dois metros e meio de largura e treze metros e meio de altura, sustentado por quatro fileiras de colunas de cedro sobre as quais apoiavam-se vigas de cedro aparelhadas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8938, 3, "O forro que ficava sobre as vigas que se apoiavam nas colunas era de cedro, eram quarenta e cinco vigas, quinze por fileira.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8939, 4, "Havia janelas dispostas de três em três, uma em frente da outra.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8940, 5, "Todas as portas tinham estrutura retangular; ficavam na parte da frente, dispostas de três em três, uma em frente da outra.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8941, 6, "Fez um pórtico de colunas de vinte e dois metros e meio de comprimento e treze metros e meio de largura. Em frente havia outro pórtico com colunas e uma cobertura que se estendia além das colunas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8942, 7, "Construiu a Sala do Trono, isto é, a Sala da Justiça, onde iria julgar, e revestiu-a de cedro desde o teto até o chão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8943, 8, "E o palácio para sua moradia, no outro pátio, tinha um formato semelhante. Salomão fez também um palácio como esse para a filha do faraó, com quem tinha se casado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8944, 9, "Todas essas construções, desde o lado externo até o grande pátio e do alicerce até o beiral, foram feitas de pedra de qualidade superior, cortadas sob medida e desbastadas com uma serra nos lados interno e externo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8945, 10, "Os alicerces foram lançados com pedras grandes de qualidade superior, algumas medindo quatro metros e meio e outras três metros e sessenta centímetros.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8946, 11, "Na parte de cima havia pedras de qualidade superior, cortadas sob medida, e vigas de cedro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8947, 12, "O grande pátio era cercado por um muro de três camadas de pedras lavradas e uma camada de vigas de cedro aparelhadas, da mesma maneira que o pátio interior do templo do Senhor, com o seu pórtico.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8948, 13, "O rei Salomão enviara mensageiros a Tiro e trouxera Hurão,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8949, 14, "filho de uma viúva da tribo de Naftali e de um cidadão de Tiro, um artífice em bronze. Hurão era extremamente hábil, experiente e sabia fazer todo tipo de trabalho em bronze. Apresentou-se ao rei Salomão e fez depois todo o trabalho que lhe foi designado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8950, 15, "Ele fundiu duas colunas de bronze, cada uma com oito metros e dez centímetros de altura e cinco metros e quarenta centímetros de circunferência, medidas pelo fio apropriado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8951, 16, "Também fez dois capitéis de bronze fundido para colocar no alto das colunas; cada capitel tinha dois metros e vinte e cinco centímetros de altura.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8952, 17, "Conjuntos de correntes entrelaçadas ornamentavam os capitéis no alto das colunas, sete em cada capitel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8953, 18, "Fez também romãs em duas fileiras que circundavam cada conjunto de correntes para cobrir os capitéis no alto das colunas. Fez o mesmo com cada capitel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8954, 19, "Os capitéis no alto das colunas do pórtico tinham o formato de lírios, com um metro e oitenta centímetros de altura.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8955, 20, "Nos capitéis das duas colunas, acima da parte que tinha formato de taça, perto do conjunto de correntes, havia duzentas romãs enfileiradas ao redor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8956, 21, "Ele levantou as colunas na frente do pórtico do templo. Deu o nome de Jaquim à coluna ao sul e de Boaz à coluna ao norte.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8957, 22, "Os capitéis no alto tinham a forma de lírios. E assim completou-se o trabalho das colunas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8958, 23, "Fez o tanque de metal fundido, redondo, medindo quatro metros e meio de diâmetro e dois metros e vinte e cinco centímetros de altura. Era preciso um fio de treze metros e meio para medir a sua circunferência.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8959, 24, "Abaixo da borda e ao seu redor havia duas fileiras de frutos, de cinco em cinco centímetros, fundidas numa só peça com o tanque.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8960, 25, "O tanque ficava sobre doze touros, três voltados para o norte, três para o oeste, três para o sul e três para o leste. Ficava em cima deles, e as pernas traseiras dos touros eram voltadas para o centro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8961, 26, "A espessura do tanque era de quatro dedos, e sua borda era como a borda de um cálice, como uma flor de lírio. Tinha capacidade de quarenta mil litros.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8962, 27, "Também fez dez carrinhos de bronze; cada um tinha um metro e oitenta centímetros de comprimento e de largura, e um metro e trinta e cinco centímetros de altura.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8963, 28, "Os carrinhos eram feitos assim: Tinham placas laterais presas a armações.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8964, 29, "Nas placas, entre as armações, havia figuras de leões, bois e querubins: Sobre as armações, acima e abaixo dos leões e bois, havia grinaldas de metal batido.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8965, 30, "Em cada carrinho havia quatro rodas de bronze com eixos de bronze, cada um com uma bacia apoiada em quatro pés e fundida ao lado de cada grinalda.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8966, 31, "Do lado de dentro do carrinho havia uma abertura circular com quarenta e cinco centímetros de profundidade. Essa abertura era redonda, e com sua base media setenta centímetros. Havia esculturas em torno da abertura. As placas dos carrinhos eram quadradas, e não redondas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8967, 32, "As quatro rodas ficavam sob as placas, e os eixos das rodas ficavam presos ao estrado. O diâmetro de cada roda era de setenta centímetros.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8968, 33, "As rodas eram feitas como rodas de carros; os eixos, os aros, os raios e os cubos eram todos de metal fundido.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8969, 34, "Havia quatro cabos que se projetavam do carrinho, um em cada canto.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8970, 35, "No alto do carrinho havia uma lâmina circular de vinte e dois centímetros de comprimento. Os apoios e as placas estavam fixados no alto do carrinho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8971, 36, "Ele esculpiu figuras de querubins, leões e tamareiras na superfície dos apoios e nas placas, em cada espaço disponível, com grinaldas ao redor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8972, 37, "Foi assim que fez os dez carrinhos. Foram todos fundidos nos mesmos moldes e eram idênticos no tamanho e na forma.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8973, 38, "Depois ele fez dez pias de bronze, cada uma com capacidade de oitocentos litros, medindo um metro e oitenta centímetros de diâmetro; uma pia para cada um dos dez carrinhos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8974, 39, "Ele pôs cinco carrinhos no lado sul do templo e cinco no lado norte. Pôs o tanque no lado sul, no canto sudeste do templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8975, 40, "Também fez os jarros, as pás e as bacias para aspersão. Assim, Hurão completou todo o trabalho de que fora encarregado pelo rei Salomão, no templo do Senhor:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8976, 41, "as duas colunas; os dois capitéis em forma de taça no alto das colunas; os dois conjuntos de correntes que decoravam os dois capitéis;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8977, 42, "as quatrocentas romãs para os dois conjuntos de correntes, sendo duas fileiras de romãs para cada conjunto;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8978, 43, "os dez carrinhos com as suas dez pias;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8979, 44, "o tanque e os doze touros debaixo dele;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8980, 45, "e os jarros, as pás e as bacias de aspersão. Todos esses utensílios que Hurão fez a pedido do rei Salomão para o templo do Senhor, eram de bronze polido.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8981, 46, "Foi na planície do Jordão, entre Sucote e Zaretã, que o rei os mandou fundir, em moldes de barro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8982, 47, "Salomão não mandou pesar esses utensílios, tão grande era o seu número que o peso do bronze não foi determinado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8983, 48, "Além desses, Salomão mandou fazer também todos estes outros utensílios para o templo do Senhor: o altar de ouro; a mesa de ouro sobre a qual ficavam os pães da Presença;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8984, 49, "os candelabros de ouro puro, cinco à direita e cinco à esquerda, em frente do santuário interno; as flores, as lâmpadas e as tenazes de ouro;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8985, 50, "as bacias, os cortadores de pavio, as bacias para aspersão, as tigelas e os incensários; e as dobradiças de ouro para as portas da sala interna, o Lugar Santíssimo, e também para as portas do átrio principal.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8986, 51, "Terminada toda a obra que Salomão realizou para o templo do Senhor, ele trouxe tudo que seu pai havia consagrado e colocou junto com os tesouros do templo do Senhor: a prata, o ouro e os utensílios.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8987, 1, "Então o rei Salomão reuniu em Jerusalém as autoridades de Israel, todos os líderes das tribos e os chefes das famílias israelitas, para levarem de Sião, a cidade de Davi, a arca da aliança do Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8988, 2, "E todos os homens de Israel uniram-se ao rei Salomão por ocasião da festa, no mês de etanim, que é o sétimo mês.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8989, 3, "Quando todas as autoridades de Israel chegaram, os sacerdotes pegaram", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8990, 4, "a arca do Senhor e a levaram, com a Tenda do Encontro e com todos os seus utensílios sagrados. Foram os sacerdotes e os levitas que levaram tudo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8991, 5, "O rei Salomão e toda a comunidade de Israel que se havia reunido a ele diante da arca, sacrificaram tantas ovelhas e bois que nem era possível contar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8992, 6, "Os sacerdotes levaram a arca da aliança do Senhor para o seu lugar no santuário interno do templo, no Lugar Santíssimo, e a colocaram debaixo das asas dos querubins.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8993, 7, "Os querubins tinham suas asas estendidas sobre o lugar da arca e cobriam a arca e as varas utilizadas para o transporte.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8994, 8, "Essas varas eram tão compridas que as suas pontas, que se estendiam para fora da arca, podiam ser vistas da frente do santuário interno, mas não de fora dele; e elas estão lá até hoje.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8995, 9, "Na arca havia só as duas tábuas de pedra que Moisés tinha colocado quando estava em Horebe, onde o Senhor fez uma aliança com os israelitas depois que saíram do Egito.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8996, 10, "Quando os sacerdotes se retiraram do Lugar Santo, uma nuvem encheu o templo do Senhor,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8997, 11, "de forma que os sacerdotes não podiam desempenhar o seu serviço, pois a glória do Senhor encheu o seu templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8998, 12, "E Salomão exclamou: ""O Senhor disse que habitaria numa nuvem escura!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (8999, 13, "Na realidade construí para ti um templo magnífico, um lugar para nele habitares para sempre! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9000, 14, "Depois o rei virou-se e abençoou toda a assembléia de Israel, que estava ali de pé.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9001, 15, "E disse: ""Bendito seja o Senhor, o Deus de Israel, que com a sua mão cumpriu o que prometeu com sua própria boca a meu pai Davi, quando lhe disse:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9002, 16, "‘Desde o dia em que tirei o meu povo Israel do Egito, não escolhi nenhuma cidade das tribos de Israel para nela construir um templo em honra do meu nome. Mas escolhi Davi para governar Israel, o meu povo’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9003, 17, """Meu pai Davi tinha no coração o propósito de construir um templo em honra do nome do Senhor, o Deus de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9004, 18, "Mas o Senhor lhe disse: ‘Você fez bem em ter no coração o plano de construir um templo em honra do meu nome;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9005, 19, "no entanto, não será você que o construirá, mas o seu filho, que procederá de você; ele construirá o templo em honra do meu nome’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9006, 20, """E o Senhor cumpriu a sua promessa: Sou o sucessor de meu pai Davi, e agora ocupo o trono de Israel, como o Senhor tinha prometido, e construí o templo em honra do nome do Senhor, o Deus de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9007, 21, "Providenciei nele um lugar para a arca, na qual estão as tábuas da aliança do Senhor, aliança que fez com os nossos antepassados quando os tirou do Egito"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9008, 22, "Depois Salomão colocou-se diante do altar do Senhor, diante de toda a assembléia de Israel, levantou as mãos para o céu", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9009, 23, "e orou: ""Senhor, Deus de Israel, não há Deus como tu em cima nos céus nem embaixo na terra! Tu que guardas a tua aliança de amor com os teus servos que, de todo o coração, andam segundo a tua vontade.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9010, 24, "Cumpriste a tua promessa a teu servo Davi, meu pai; com tua boca prometeste e com tua mão a cumpriste, conforme hoje se vê.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9011, 25, """Agora, Senhor, Deus de Israel, cumpre a outra promessa que fizeste a teu servo Davi, meu pai, quando disseste: ‘Você nunca deixará de ter, diante de mim, um descendente que se assente no trono de Israel, se tão-somente os seus descendentes tiverem o cuidado de, em tudo, andarem segundo a minha vontade, como você tem feito’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9012, 26, "Agora, ó Deus de Israel, que se confirme a palavra que falaste a teu servo Davi, meu pai.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9013, 27, """Mas será possível que Deus habite na terra? Os céus, mesmo os mais altos céus, não podem conter-te. Muito menos este templo que construí!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9014, 28, "Ainda assim, atende à oração do teu servo e ao seu pedido de misericórdia, ó Senhor, meu Deus. Ouve o clamor e a oração que o teu servo faz hoje na tua presença.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9015, 29, "Estejam os teus olhos voltados dia e noite para este templo, lugar do qual disseste que nele porias o teu nome, para que ouças a oração que o teu servo fizer voltado para este lugar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9016, 30, "Ouve as súplicas do teu servo e de Israel, teu povo, quando orarem voltados para este lugar. Ouve desde os céus, lugar da tua habitação, e quando ouvires, dá-lhes o teu perdão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9017, 31, """Quando um homem pecar contra seu próximo e tiver que fazer um juramento, e vier jurar diante do teu altar neste templo,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9018, 32, "ouve dos céus e age. Julga os teus servos; condena o culpado, fazendo recair sobre a sua própria cabeça o resultado da sua conduta, e declara sem culpa o inocente, dando-lhe o que a sua inocência merece.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9019, 33, """Quando Israel, o teu povo, for derrotado por um inimigo por ter pecado contra ti, e voltar-se para ti e invocar o teu nome, orando e suplicando a ti neste templo,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9020, 34, "ouve dos céus e perdoa o pecado de Israel, teu povo, e traze-o de volta à terra que deste aos seus antepassados.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9021, 35, """Quando fechar-se o céu, e não houver chuva por haver o teu povo pecado contra ti, e, se o teu povo, voltado para este lugar, invocar o teu nome e afastar-se do seu pecado por o haveres castigado,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9022, 36, "ouve dos céus e perdoa o pecado dos teus servos, do teu povo Israel. Ensina-lhes o caminho certo e envia chuva sobre a tua terra, que deste por herança ao teu povo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9023, 37, """Quando houver fome ou praga no país, ferrugem e mofo, gafanhotos peregrinos e gafanhotos devastadores, ou quando inimigos sitiarem suas cidades, quando, em meio a qualquer praga ou epidemia,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9024, 38, "uma oração ou súplica por misericórdia for feita por um israelita ou por todo o teu povo Israel, cada um sentindo as suas próprias aflições e dores, estendendo as mãos na direção deste templo,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9025, 39, "ouve dos céus, o lugar da tua habitação. Perdoa e age; trata cada um de acordo com o que merece, visto que conheces o seu coração. Sim, só tu conheces o coração do homem.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9026, 40, "Assim eles te temerão durante todo o tempo em que viverem na terra que deste aos nossos antepassados.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9027, 41, """Quanto ao estrangeiro, que não pertence ao teu povo Israel, e que veio de uma terra distante por causa do teu nome —", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9028, 42, "pois ouvirão acerca do teu grande nome, da tua mão poderosa e do teu braço forte — quando ele vier e orar voltado para este templo,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9029, 43, "ouve dos céus, lugar da tua habitação, e atende o pedido do estrangeiro, a fim de que todos os povos da terra conheçam o teu nome e te temam, como faz Israel, teu povo, e saibam que este templo que construí traz o teu nome.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9030, 44, """Quando o teu povo for à guerra contra os seus inimigos, por onde quer que tu o enviares, e orar ao Senhor voltado para a cidade que escolheste e para o templo que construí em honra do teu nome,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9031, 45, "então ouve dos céus a sua oração e a sua súplica, e defende a sua causa.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9032, 46, """Quando pecarem contra ti, pois não há ninguém que não peque, e ficares irado com eles e os entregares ao inimigo, que os leve prisioneiros para a sua terra, distante ou próxima;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9033, 47, "se eles caírem em si, na terra para a qual foram deportados, e se arrependerem e lá orarem: ‘Pecamos, praticamos o mal e fomos rebeldes’;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9034, 48, "e se lá eles se voltarem para ti de todo o coração e de toda a sua alma, na terra dos inimigos que os levaram como prisioneiros, e orarem voltados para a terra que deste aos seus antepassados, para a cidade que escolheste e para o templo que construí em honra do teu nome,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9035, 49, "então, desde os céus, o lugar da tua habitação, ouve a sua oração e a sua súplica, e defende a sua causa.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9036, 50, "Perdoa o teu povo, que pecou contra ti; perdoa todas as transgressões que cometeram contra ti, e faze com que os seus conquistadores tenham misericórdia deles;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9037, 51, "pois são o teu povo e a tua herança, que tiraste do Egito, da fornalha de fundição.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9038, 52, """Que os teus olhos estejam abertos para a súplica do teu servo e para a súplica do teu povo Israel, e que os ouças sempre que clamarem a ti.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9039, 53, "Pois tu os escolheste dentre todos os povos da terra para serem a tua herança, assim como declaraste por meio do teu servo Moisés, quando tu, ó Soberano Senhor, tiraste os nossos antepassados do Egito"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9040, 54, "Quando Salomão terminou a oração e a súplica ao Senhor, levantou-se diante do altar do Senhor, onde tinha se ajoelhado e estendido as mãos para o céu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9041, 55, "Pôs-se de pé e abençoou em voz alta toda a assembléia de Israel, dizendo:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9042, 56, """Bendito seja o Senhor, que deu descanso a Israel, seu povo, como havia prometido. Não ficou sem cumprimento nem uma de todas as boas promessas que ele fez por meio do seu servo Moisés.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9043, 57, "Que o Senhor, o nosso Deus, esteja conosco, assim como esteve com os nossos antepassados. Que ele jamais nos deixe nem nos abandone!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9044, 58, "E faça com que de coração nos voltemos para ele, a fim de andarmos em todos os seus caminhos e obedecermos aos seus mandamentos, decretos e ordenanças, que deu aos nossos antepassados.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9045, 59, "E que as palavras da minha súplica ao Senhor tenham acesso ao Senhor, ao nosso Deus, dia e noite, para que ele defenda a causa do seu servo e a causa de Israel, seu povo, de acordo com o que precisarem.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9046, 60, "Assim, todos os povos da terra saberão que o Senhor é Deus e que não há nenhum outro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9047, 61, "Mas vocês, tenham coração íntegro para com o Senhor, o nosso Deus, para viverem por seus decretos e obedecerem aos seus mandamentos, como acontece hoje"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9048, 62, "Então o rei Salomão e todo o Israel ofereceram sacrifícios ao Senhor;", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9049, 63, "ele ofereceu em sacrifício de comunhão ao Senhor vinte e dois mil bois e cento e vinte mil ovelhas. Assim o rei e todos os israelitas fizeram a dedicação do templo do Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9050, 64, "Naquele mesmo dia o rei consagrou a parte central do pátio, que ficava na frente do templo do Senhor, e ali ofereceu holocaustos, ofertas de cereal e a gordura das ofertas de comunhão, pois o altar de bronze diante do Senhor era pequeno demais para comportar os holocaustos, as ofertas de cereal e a gordura das ofertas de comunhão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9051, 65, "E foi assim que Salomão, junto com todo o Israel, celebrou a festa naquela data; era uma grande multidão, gente vinda desde Lebo-Hamate até o ribeiro do Egito. Comemoraram diante do Senhor, o nosso Deus, durante sete dias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9052, 66, "No oitavo dia Salomão mandou o povo para casa. Eles abençoaram o rei e foram embora, jubilosos e de coração alegre por todas as coisas boas que o Senhor havia feito por seu servo Davi e por seu povo Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9053, 1, "Quando Salomão acabou de construir o templo do Senhor, o palácio real e tudo mais que desejara construir,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9054, 2, "o Senhor lhe apareceu pela segunda vez, como lhe havia aparecido em Gibeom.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9055, 3, "O Senhor lhe disse: ""Ouvi a oração e a súplica que você fez diante de mim; consagrei este templo que você construiu, para que nele habite o meu nome para sempre. Os meus olhos e o meu coração nele sempre estarão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9056, 4, """E se você andar segundo a minha vontade, com integridade de coração e com retidão, como fez o seu pai Davi, se fizer tudo o que eu lhe ordeno, obedecendo aos meus decretos e às minhas ordenanças,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9057, 5, "firmarei para sempre sobre Israel o seu trono, conforme prometi a Davi, seu pai, quando lhe disse: Você nunca deixará de ter um descendente para governar Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9058, 6, """Mas, se vocês ou seus filhos se afastarem de mim e não obedecerem aos mandamentos e aos decretos que lhes dei, e prestarem culto a outros deuses e adorá-los,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9059, 7, "desarraigarei Israel da terra que lhes dei, e lançarei para longe da minha presença este templo que consagrei ao meu nome. Israel se tornará então motivo de zombaria entre todos os povos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9060, 8, "E, embora agora este templo seja imponente, todos os que passarem por ele ficarão espantados e perguntarão: ‘Por que o Senhor fez uma coisa dessas a esta terra e a este templo? ’", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9061, 9, "E a resposta será: ‘Porque abandonaram o Senhor, o seu Deus, que tirou os seus antepassados do Egito, e se apegaram a outros deuses, adorando-os e prestando-lhes culto; por isso o Senhor trouxe sobre eles toda esta desgraça’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9062, 10, "Depois de vinte anos, durante os quais construiu estes dois edifícios, o templo do Senhor e o palácio real,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9063, 11, "o rei Salomão deu vinte cidades da Galiléia a Hirão, rei de Tiro, pois Hirão lhe havia fornecido toda a madeira de cedro e de pinho e o ouro que ele precisou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9064, 12, "Mas, quando este veio de Tiro para ver as cidades que Salomão lhe dera, não gostou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9065, 13, """Que cidades são essas que você me deu, meu irmão? "", ele perguntou. E as chamou terra de Cabul, nome que elas têm até hoje.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9066, 14, "Hirão tinha enviado ao rei quatro mil e duzentos quilos de ouro!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9067, 15, "O rei Salomão impôs trabalhos forçados para que se construísse o templo do Senhor, seu próprio palácio, o Milo, o muro de Jerusalém, como também Hazor, Megido e Gezer.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9068, 16, "O faraó, rei do Egito, havia atacado e conquistado Gezer. Incendiou a cidade e matou os seus habitantes, que eram cananeus, e a deu como presente de casamento à sua filha, mulher de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9069, 17, "E Salomão reconstruiu Gezer. Ele construiu Bete-Horom Baixa,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9070, 18, "Baalate, e Tadmor, no deserto dessa região,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9071, 19, "bem como todas as cidades-armazéns e as cidades onde ficavam os seus carros de guerra e os seus cavalos. Construiu tudo o que desejou em Jerusalém, no Líbano e em todo o território que governou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9072, 20, "Todos os não israelitas, descendentes dos amorreus, dos hititas, dos ferezeus, dos heveus e dos jebuseus,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9073, 21, "que não tinham sido mortos pelos israelitas, Salomão recrutou para o trabalho forçado, como continuam até hoje.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9074, 22, "Mas Salomão não obrigou nenhum israelita a trabalhos forçados; eles eram seus homens de guerra, os seus capitães, e os comandantes dos seus carros de guerra e os condutores de carros.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9075, 23, "Também eram israelitas os principais funcionários encarregados das construções de Salomão: quinhentos e cinqüenta funcionários que supervisionavam os trabalhadores.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9076, 24, "Somente depois que a filha do faraó mudou-se da cidade de Davi para o palácio que Salomão havia construído para ela, foi que ele construiu o Milo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9077, 25, "Três vezes por ano Salomão oferecia holocaustos e sacrifícios de comunhão no altar que havia construído para o Senhor, e ao mesmo tempo queimava incenso diante do Senhor. Assim, ele concluiu o templo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9078, 26, "O rei Salomão também construiu navios em Eziom-Geber, que fica perto de Elate, na terra de Edom, às margens do mar Vermelho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9079, 27, "E Hirão enviou em navios os seus marinheiros, homens experimentados que conheciam o mar, para trabalharem com os marinheiros de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9080, 28, "Navegaram até Ofir, e de lá trouxeram catorze toneladas e setecentos quilos de ouro para o rei Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9081, 1, "A rainha de Sabá soube da fama que Salomão tinha alcançado, graças ao nome do Senhor, e foi a Jerusalém para pô-lo à prova com perguntas difíceis.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9082, 2, "Quando chegou, acompanhada de uma enorme caravana, com camelos carregados de especiarias, grande quantidade de ouro e pedras preciosas, foi até Salomão e lhe fez todas as perguntas que tinha em mente.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9083, 3, "Salomão respondeu a todas; nenhuma lhe foi tão difícil que não pudesse responder.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9084, 4, "Vendo toda a sabedoria de Salomão, bem como o palácio que ele havia construído,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9085, 5, "o que era servido em sua mesa, o lugar de seus oficiais, os criados e copeiros, todos uniformizados, e os holocaustos que ele fazia no templo do Senhor, ela ficou impressionada.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9086, 6, "Disse ela então ao rei: ""Tudo o que ouvi em meu país acerca de tuas realizações e de tua sabedoria era verdade.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9087, 7, "Mas eu não acreditava no que diziam, até ver com os meus próprios olhos. Na realidade, não me contaram nem a metade; tu ultrapassas em muito o que ouvi, tanto em sabedoria como em riqueza.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9088, 8, "Como devem ser felizes os homens da tua corte, que continuamente estão diante de ti e ouvem a tua sabedoria!", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9089, 9, "Bendito seja o Senhor, o teu Deus, que se agradou de ti e te colocou no trono de Israel. Por causa do amor eterno do Senhor para com Israel, ele te fez rei, para manter a justiça e a retidão"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9090, 10, "E ela deu ao rei quatro toneladas e duzentos quilos de ouro e grande quantidade de especiarias e pedras preciosas. E nunca mais foram trazidas tantas especiarias quanto as que a rainha de Sabá deu ao rei Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9091, 11, "( Os navios de Hirão, que carregavam ouro de Ofir, também trouxeram de lá grande quantidade de madeira de junípero e pedras preciosas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9092, 12, "O rei utilizou a madeira para fazer a escadaria do templo do Senhor e a do palácio real, além de harpas e liras para os músicos. Nunca mais foi importada nem se viu tanta madeira de junípero. )", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9093, 13, "O rei Salomão deu à rainha de Sabá tudo o que ela desejou e pediu, além do que já lhe tinha dado por sua generosidade real. Então ela e os seus servos voltaram para o seu país.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9094, 14, "O peso do ouro que Salomão recebia anualmente era de vinte e três toneladas e trezentos quilos,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9095, 15, "fora os impostos pagos por mercadores e comerciantes e por todos os reis da Arábia e pelos governadores do país.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9096, 16, "O rei Salomão fez duzentos escudos grandes de ouro batido, utilizando três quilos e seiscentos gramas de ouro em cada um.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9097, 17, "Também fez trezentos escudos pequenos de ouro batido, com um quilo e oitocentos gramas de ouro em cada um. O rei os colocou no Palácio da Floresta do Líbano.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9098, 18, "O rei mandou fazer ainda um grande trono de marfim revestido de ouro puro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9099, 19, "O trono tinha seis degraus, e o seu encosto tinha a parte alta arredondada. Nos dois lados do assento havia braços, com um leão junto a cada braço.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9100, 20, "Doze leões ficavam nos seis degraus, um de cada lado. Nada igual havia sido feito em nenhum outro reino.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9101, 21, "Todas as taças do rei Salomão eram de ouro, bem como todos os utensílios do Palácio da Floresta do Líbano. Não havia nada de prata, pois a prata quase não tinha valor nos dias de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9102, 22, "O rei tinha no mar uma frota de navios mercantes junto com os navios de Hirão. Cada três anos a frota voltava, trazendo ouro, prata, marfim, macacos e pavões.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9103, 23, "O rei Salomão era o mais rico e o mais sábio de todos os reis da terra.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9104, 24, "Gente de todo o mundo pedia audiência a Salomão para ouvir a sabedoria que Deus lhe tinha dado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9105, 25, "Ano após ano, todos os que vinham traziam algum presente: utensílios de prata e de ouro, mantos, armas e especiarias, cavalos e mulas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9106, 26, "Salomão juntou carros e cavalos; possuía mil e quatrocentos carros e doze mil cavalos, dos quais mantinha uma parte nas guarnições de algumas cidades e a outra perto dele, em Jerusalém.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9107, 27, "O rei tornou a prata tão comum em Jerusalém quanto as pedras, e o cedro tão numeroso quanto as figueiras bravas da Sefelá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9108, 28, "Os cavalos de Salomão eram importados do Egito e da Cilícia, onde os fornecedores do rei os compravam.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9109, 29, "Importavam do Egito um carro por sete quilos e duzentos gramas de prata, e um cavalo por um quilo e oitocentos gramas, e os exportavam para todos os reis dos hititas e dos arameus.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9110, 1, "O rei Salomão amou muitas mulheres estrangeiras, além da filha do faraó. Eram mulheres moabitas, amonitas, edomitas, sidônias e hititas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9111, 2, "Elas eram das nações sobre as quais o Senhor tinha dito aos israelitas: ""Vocês não poderão tomar mulheres dentre essas nações, porque elas os farão desviar-se para seguir os seus deuses"". No entanto, Salomão apegou-se amorosamente a elas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9112, 3, "Casou com setecentas princesas e trezentas concubinas, e as suas mulheres o levaram a desviar-se.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9113, 4, "À medida que Salomão foi envelhecendo, suas mulheres o induziram a voltar-se para outros deuses, e o seu coração já não era totalmente dedicado ao Senhor, o seu Deus, como fora o coração do seu pai Davi.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9114, 5, "Ele seguiu os postes sagrados, a deusa dos sidônios, e Moloque, o repugnante deus dos amonitas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9115, 6, "Dessa forma Salomão fez o que o Senhor reprova; não seguiu completamente o Senhor, como o seu pai Davi.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9116, 7, "No monte que fica a leste de Jerusalém, Salomão construiu um altar para Camos, o repugnante deus de Moabe, e para Moloque, o repugnante deus dos amonitas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9117, 8, "Também fez altares para os deuses de todas as suas outras mulheres estrangeiras, que queimavam incenso e ofereciam sacrifícios a eles.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9118, 9, "O Senhor irou-se contra Salomão por ter-se desviado do Senhor, o Deus de Israel, que lhe havia aparecido duas vezes.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9119, 10, "Embora ele tivesse proibido Salomão de seguir outros deuses, Salomão não obedeceu à ordem do Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9120, 11, "Então o Senhor disse a Salomão: ""Já que essa é a sua atitude e você não obedeceu à minha aliança e aos meus decretos, os quais lhe ordenei, certamente lhe tirarei o reino e o darei a um dos seus servos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9121, 12, "No entanto, por amor a Davi, seu pai, não farei isso enquanto você viver. Eu o tirarei da mão do seu filho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9122, 13, "Mas, não tirarei dele o reino inteiro, eu lhe darei uma tribo por amor de Davi, meu servo, e por amor de Jerusalém, a cidade que escolhi"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9123, 14, "Então o Senhor levantou contra Salomão um adversário, o edomita Hadade, da linhagem real de Edom.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9124, 15, "Anteriormente, quando Davi estava lutando contra Edom, Joabe, o comandante do exército, que tinha ido para lá enterrar os mortos, exterminara todos os homens de Edom.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9125, 16, "Joabe e todo o exército israelita permaneceram lá seis meses, até matarem todos os edomitas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9126, 17, "Mas Hadade, sendo ainda um menino, fugiu para o Egito com alguns dos oficiais edomitas que tinham servido a seu pai.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9127, 18, "Partiram de Midiã e foram a Parã. Lá reuniram alguns homens e foram ao Egito, até o faraó, rei do Egito, que deu uma casa e terras a Hadade e lhe forneceu alimento.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9128, 19, "O faraó acolheu bem a Hadade, a ponto de dar-lhe em casamento uma irmã de sua própria mulher, a rainha Tafnes.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9129, 20, "A irmã de Tafnes deu-lhe um filho, chamado Genubate, que fora criado por Tafnes no palácio real. Ali Genubate viveu com os próprios filhos do faraó.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9130, 21, "Enquanto estava no Egito, Hadade soube que Davi tinha descansado com seus antepassados e que Joabe, o comandante do exército, também estava morto. Então Hadade disse ao faraó: ""Deixa-me voltar para a minha terra"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9131, 22, """O que lhe falta aqui para que você queira voltar para a sua terra? "", perguntou o faraó. ""Nada me falta"", respondeu Hadade, ""mas deixa-me ir! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9132, 23, "E Deus fez um outro adversário levantar-se contra Salomão: Rezom, filho de Eliada, que tinha fugido do seu senhor, Hadadezer, rei de Zobá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9133, 24, "Quando Davi destruiu o exército de Zobá, Rezom reuniu alguns homens e tornou-se líder de um bando de rebeldes. Eles foram para Damasco, onde se instalaram e assumiram o controle.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9134, 25, "Rezom foi adversário de Israel enquanto Salomão viveu, e trouxe-lhe muitos problemas, além dos causados por Hadade. Assim Rezom governou a Síria e foi hostil para com Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9135, 26, "Também Jeroboão, filho de Nebate, rebelou-se contra o rei. Ele era um dos oficiais de Salomão, um efraimita de Zeredá, e a sua mãe era uma viúva chamada Zerua.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9136, 27, "E foi assim que ele se revoltou contra o rei: Salomão tinha construído o Milo e havia tapado a abertura no muro da cidade de Davi, seu pai.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9137, 28, "Ora, Jeroboão era homem capaz, e, quando Salomão viu como ele fazia bem o seu trabalho, encarregou-o de todos os que faziam trabalho forçado, pertencentes às tribos de José.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9138, 29, "Naquela ocasião, Jeroboão saiu de Jerusalém, e Aías, o profeta de Siló, que estava usando uma capa nova, encontrou-se com ele no caminho. Os dois estavam sozinhos no campo,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9139, 30, "e Aías segurou firmemente a capa que estava usando e a rasgou em doze pedaços.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9140, 31, "Então disse a Jeroboão: ""Apanhe dez pedaços para você, pois assim diz o Senhor, o Deus de Israel: ‘Saiba que vou tirar o reino das mãos de Salomão e dar a você dez tribos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9141, 32, "Mas, por amor ao meu servo Davi e à cidade de Jerusalém, a qual escolhi dentre todas as tribos de Israel, ele terá uma tribo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9142, 33, "Farei isso porque eles me abandonaram e adoraram os postes sagrados, deusa dos sidônios, Camos, deus dos moabitas, e Moloque, deus dos amonitas, e não andaram nos meus caminhos, nem fizeram o que eu aprovo, nem obedeceram aos meus decretos e as minhas ordenanças, como fez Davi, pai de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9143, 34, """ ‘Mas não tirarei o reino todo das mãos de Salomão; eu o fiz governante todos os dias de sua vida por amor ao meu servo Davi, a quem escolhi e que obedeceu aos meus mandamentos e aos meus decretos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9144, 35, "Tirarei o reino das mãos do seu filho e darei dez tribos a você.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9145, 36, "Darei uma tribo ao seu filho a fim de que o meu servo Davi sempre tenha diante de mim um descendente no trono em Jerusalém, a cidade onde eu quis pôr o meu nome.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9146, 37, "Quanto a você, eu o farei governar tudo o que o seu coração desejar; você será rei de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9147, 38, "Se você fizer tudo o que eu lhe ordenar e andar nos meus caminhos e fizer o que eu aprovo, obedecendo aos meus decretos e aos meus mandamentos, como fez o meu servo Davi, estarei com você. Edificarei para você uma dinastia tão permanente quanto a que edifiquei para Davi, e darei Israel a você.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9148, 39, "Humilharei os descendentes de Davi por causa disso, mas não para sempre’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9149, 40, "Salomão tentou matar Jeroboão, mas ele fugiu para o Egito, para o rei Sisaque, e lá permaneceu até a morte de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9150, 41, "Os demais acontecimentos do reinado de Salomão, tudo o que fez e a sabedoria que teve, estão todos escritos nos registros históricos de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9151, 42, "Salomão reinou quarenta anos em Jerusalém sobre todo o Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9152, 43, "Então descansou com os seus antepassados e foi sepultado na cidade de Davi, seu pai. E o seu filho Roboão foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9153, 1, "Roboão foi a Siquém, onde todos os israelitas tinham se reunido para proclamá-lo rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9154, 2, "Assim que Jeroboão, filho de Nebate, que estava no Egito fugindo do rei Salomão, soube disso, voltou de lá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9155, 3, "E mandaram chamá-lo. Então ele e toda a assembléia de Israel foram ao encontro de Roboão e disseram:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9156, 4, """Teu pai colocou sobre nós um jugo pesado, mas agora diminui o trabalho árduo e este jugo pesado, e nós te serviremos"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9157, 5, "Roboão respondeu: ""Voltem a mim daqui a três dias"". Então o povo foi embora.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9158, 6, "O rei Roboão perguntou às autoridades que haviam servido ao seu pai Salomão durante a vida dele: ""Como vocês me aconselham a responder a este povo? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9159, 7, "Eles responderam: ""Se hoje fores um servo desse povo e servi-lo, dando-lhe uma resposta favorável, eles sempre serão teus servos"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9160, 8, "Roboão, contudo, rejeitou o conselho que as autoridades de Israel lhe tinham dito e consultou os jovens que haviam crescido com ele e o estavam servindo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9161, 9, "Perguntou-lhes: ""Qual é o conselho de vocês? Como devemos responder a este povo que me diz: ‘Diminui o jugo que teu pai colocou sobre nós’? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9162, 10, "Os jovens que haviam crescido com ele responderam: ""A este povo que te disse: ‘Teu pai colocou sobre nós um jugo pesado; torna-o mais leve’ — dize: ‘Meu dedo mínimo é mais grosso do que a cintura do meu pai.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9163, 11, "Pois bem, meu pai lhes impôs um jugo pesado; eu o tornarei ainda mais pesado. Meu pai os castigou com simples chicotes; eu os castigarei com chicotes pontiagudos’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9164, 12, "Três dias depois, Jeroboão e todo o povo voltaram a Roboão, segundo a orientação dada pelo rei: ""Voltem a mim daqui a três dias"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9165, 13, "Mas o rei lhes respondeu asperamente. Rejeitando o conselho das autoridades de Israel,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9166, 14, "seguiu o conselho dos jovens e disse: ""Meu pai lhes tornou pesado o jugo; eu o tornarei ainda mais pesado. Meu pai os castigou com simples chicotes; eu os castigarei com chicotes pontiagudos"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9167, 15, "E o rei não ouviu o povo, pois esta mudança nos acontecimentos vinha da parte do Senhor, para que se cumprisse a palavra que o Senhor havia falado a Jeroboão, filho de Nebate, por meio do silonita Aías.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9168, 16, "Quando todo o Israel viu que o rei se recusava a ouvi-los, responderam ao rei: ""Que temos em comum com Davi? Que temos em comum com o filho de Jessé? Para as suas tendas, ó Israel! Cuide da sua própria casa, ó Davi! "" E assim os israelitas foram para as suas casas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9169, 17, "Quanto, porém, aos israelitas que moravam nas cidades de Judá, Roboão continuou como rei deles.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9170, 18, "O rei Roboão enviou Adonirão, chefe de trabalhos forçados, mas todo o Israel o apedrejou até à morte. O rei, contudo, conseguiu subir em sua carruagem e fugir para Jerusalém.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9171, 19, "Desta forma Israel se rebelou contra a dinastia de Davi, e assim permanece até hoje.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9172, 20, "Quando todos os israelitas souberam que Jeroboão tinha voltado, mandaram chamá-lo para a reunião da comunidade e o fizeram rei sobre todo o Israel. Somente a tribo de Judá permaneceu leal à dinastia de Davi.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9173, 21, "Quando Roboão chegou em Jerusalém, convocou cento e oitenta mil homens de combate, das tribos de Judá e de Benjamim, para guerrearem contra Israel e recuperarem o reino para Roboão, filho de Salomão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9174, 22, "Entretanto, veio esta palavra de Deus a Semaías, homem de Deus:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9175, 23, """Diga a Roboão, filho de Salomão, rei de Judá, às tribos de Judá e Benjamim, e ao restante do povo:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9176, 24, "‘Assim diz o Senhor: Não saiam à guerra contra os seus irmãos israelitas. Voltem para casa, todos vocês, pois fui eu que fiz isso’ "". E eles obedeceram à palavra do Senhor e voltaram para as suas casas, conforme o Senhor tinha ordenado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9177, 25, "Jeroboão fortificou Siquém, nos montes de Efraim, onde passou a morar. Depois ele saiu e fortificou Peniel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9178, 26, "Jeroboão pensou: ""O reino agora provavelmente voltará para a dinastia de Davi.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9179, 27, "Se esse povo subir a Jerusalém para oferecer sacrifícios no templo do Senhor, novamente dedicarão sua lealdade ao senhor deles, Roboão, rei de Judá. Eles vão me matar e voltar para o rei Roboão"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9180, 28, "Depois de aconselhar-se, o rei fez dois bezerros de ouro e disse ao povo: ""Vocês já subiram muito a Jerusalém. Aqui estão os seus deuses, ó Israel, que tiraram vocês do Egito"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9181, 29, "Mandou por um bezerro em Betel, e o outro em Dã.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9182, 30, "E isso veio a ser um pecado, pois o povo ia até Dã para adorar aquele bezerro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9183, 31, "Jeroboão construiu altares idólatras e designou sacerdotes dentre o povo, apesar de não serem levitas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9184, 32, "Instituiu uma festa no dia quinze do oitavo mês, semelhante à festa realizada em Judá, e ofereceu sacrifícios no altar. Ele fez isso em Betel, onde também sacrificou aos bezerros que havia feito. Também estabeleceu lá sacerdotes nos seus altares idólatras.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9185, 33, "No dia quinze do oitavo mês, data que ele mesmo escolheu, ofereceu sacrifícios no altar que havia construído em Betel. Assim ele instituiu a festa para os israelitas e foi ao altar para queimar incenso.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9186, 1, "Por ordem do Senhor um homem de Deus foi de Judá a Betel, quando Jeroboão estava de pé junto ao altar para queimar incenso.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9187, 2, "Ele clamou contra o altar, segundo a ordem do Senhor: ""Ó altar, ó altar! Assim diz o Senhor: ‘Um filho nascerá na família de Davi e se chamará Josias. Sobre você ele sacrificará os sacerdotes dos altares idólatras que agora queimam incenso aqui, e ossos humanos serão queimados sobre você’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9188, 3, "Naquele mesmo dia o homem de Deus deu um sinal: ""Este é o sinal que o Senhor declarou: O altar se fenderá, e as cinzas que estão sobre ele se derramarão"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9189, 4, "Quando o rei Jeroboão ouviu o que o homem de Deus proclamava contra o altar de Betel, apontou para ele e ordenou: ""Prendam-no! "" Mas o braço que ele tinha estendido ficou paralisado, e não voltava ao normal.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9190, 5, "Além disso, o altar se fendeu, e as suas cinzas se derramaram, conforme o sinal dado pelo homem de Deus por ordem do Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9191, 6, "Então o rei disse ao homem de Deus: ""Interceda junto ao Senhor, o seu Deus, e ore por mim para que meu braço se recupere"". O homem de Deus intercedeu junto ao Senhor, e o braço do rei recuperou-se e voltou ao normal.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9192, 7, "O rei disse ao homem de Deus: ""Venha à minha casa e coma algo, e eu o recompensarei"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9193, 8, "Mas o homem de Deus respondeu ao rei: ""Mesmo que me desse a metade dos seus bens, eu não iria com você, nem comeria nem beberia nada neste lugar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9194, 9, "Pois recebi estas ordens pela palavra do Senhor: ‘Não coma pão nem beba água nem volte pelo mesmo caminho por onde veio’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9195, 10, "Por isso, quando ele voltou, não foi pelo caminho pelo qual tinha vindo a Betel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9196, 11, "Ora, havia um certo profeta, já idoso, morando em Betel. Seus filhos lhe contaram tudo o que o homem de Deus havia feito naquele dia e também o que ele dissera ao rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9197, 12, "O pai lhes perguntou: ""Por qual caminho ele foi? "" E os seus filhos lhe mostraram por onde tinha ido o homem de Deus que viera de Judá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9198, 13, "Então disse aos filhos: ""Selem o jumento para mim"". E, depois de selarem o jumento, ele montou", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9199, 14, "e cavalgou à procura do homem de Deus, até que o encontrou sentado embaixo da Grande Árvore. E lhe perguntou: ""Você é o homem de Deus que veio de Judá? "" ""Sou"", respondeu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9200, 15, "Então o profeta lhe disse: ""Venha à minha casa, comer alguma coisa"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9201, 16, "O homem de Deus disse: ""Não posso ir com você, nem posso comer pão ou beber água neste lugar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9202, 17, "A palavra do Senhor deu-me esta ordem: ‘Não coma pão nem beba água lá, nem volte pelo mesmo caminho por onde você foi’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9203, 18, "O profeta idoso respondeu: ""Eu também sou profeta como você. E um anjo me disse por ordem do Senhor: ‘Faça-o voltar com você para a sua casa para que coma pão e beba água’ "". Mas ele estava mentindo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9204, 19, "E o homem de Deus voltou com ele e foi comer e beber em sua casa.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9205, 20, "Enquanto ainda estavam sentados à mesa, a palavra do Senhor veio ao profeta idoso que o havia feito voltar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9206, 21, "Ele bradou ao homem de Deus que tinha vindo de Judá: ""Assim diz o Senhor: ‘Você desafiou a palavra do Senhor e não obedeceu à ordem que o Senhor, o seu Deus, lhe deu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9207, 22, "Você voltou e comeu pão e bebeu água no lugar onde ele lhe falou que não comesse nem bebesse. Por isso o seu corpo não será sepultado no túmulo dos seus antepassados’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9208, 23, "Quando o homem de Deus acabou de comer e beber, o outro profeta selou seu jumento para ele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9209, 24, "No caminho, um leão o atacou e o matou, e o seu corpo ficou estendido no chão, ao lado do leão e do jumento.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9210, 25, "Algumas pessoas que passaram viram o cadáver estendido ali, com o leão ao lado, e foram dar a notícia na cidade onde o profeta idoso vivia.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9211, 26, "Quando o outro profeta soube disso, exclamou: ""É o homem de Deus que desafiou a palavra do Senhor! O Senhor o entregou ao leão, que o feriu e o matou, conforme a palavra do Senhor o tinha advertido"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9212, 27, "O profeta disse aos seus filhos: ""Selem o jumento para mim"", e eles o fizeram.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9213, 28, "Ele foi e encontrou o cadáver caído no caminho, com o jumento e o leão ao seu lado. O leão não tinha comido o corpo nem ferido o jumento.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9214, 29, "O profeta apanhou o corpo do homem de Deus, colocou-o sobre o jumento, e o levou de volta para Betel, a fim de chorar por ele e sepultá-lo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9215, 30, "Ele o pôs no seu próprio túmulo, e lamentaram por ele, exclamando: ""Ah, meu irmão! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9216, 31, "Depois de sepultá-lo, disse aos seus filhos: ""Quando eu morrer, enterrem-me no túmulo onde está sepultado o homem de Deus; ponham os meus ossos ao lado dos ossos dele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9217, 32, "Pois a mensagem que declarou por ordem do Senhor contra o altar de Betel e contra todos os altares idólatras das cidades de Samaria certamente se cumprirá"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9218, 33, "Mesmo depois disso Jeroboão não mudou o seu mau procedimento, mas continuou a nomear dentre o povo sacerdotes para os altares idólatras. Ele consagrava para esses altares todo aquele que quisesse tornar-se sacerdote.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9219, 34, "Esse foi o pecado da família de Jeroboão, que levou à sua queda e à sua eliminação da face da terra.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9220, 1, "Naquela época, Abias, filho de Jeroboão, ficou doente,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9221, 2, "e este disse à sua mulher: ""Use um disfarce para não ser reconhecida como a mulher de Jeroboão, e vá a Siló, onde vive o profeta Aías, aquele que me disse que eu seria rei sobre este povo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9222, 3, "Leve para ele dez pães, alguns bolos e uma garrafa de mel. Ele lhe dirá o que vai acontecer com o menino"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9223, 4, "A mulher de Jeroboão atendeu o seu pedido e foi à casa de Aías, em Siló. Ora, Aías já não conseguia enxergar; tinha ficado cego por causa da idade.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9224, 5, "Mas o Senhor lhe tinha dito: ""A mulher de Jeroboão está vindo para lhe perguntar acerca do filho dela, pois ele está doente, e você deve responder-lhe assim e assim. Quando ela chegar, vai fingir que é outra pessoa"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9225, 6, "Quando Aías ouviu o som dos passos junto da porta, disse: ""Entre, mulher de Jeroboão. Por que esse fingimento? Fui encarregado de lhe dar más notícias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9226, 7, "Vá dizer a Jeroboão que é isto o que o Senhor, o Deus de Israel, diz: ‘Tirei-o dentre o povo e o tornei líder sobre o meu povo Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9227, 8, "Tirei o reino da família de Davi e o dei a você, mas você não tem sido como o meu servo Davi, que obedecia aos meus mandamentos e me seguia de todo o coração, fazendo apenas o que eu aprovo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9228, 9, "Você tem feito mais mal do que todos os que viveram antes de você, pois fez para si outros deuses, ídolos de metal; você provocou a minha ira e voltou as costas para mim.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9229, 10, """ ‘Por isso, trarei desgraça à família de Jeroboão. Matarei de Jeroboão até o último indivíduo do sexo masculino em Israel, seja escravo ou livre. Queimarei a família de Jeroboão até o fim como quem queima esterco.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9230, 11, "Dos que pertencem a Jeroboão, os cães comerão os que morrerem na cidade, e as aves do céu se alimentarão dos que morrerem no campo. O Senhor falou! ’", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9231, 12, """Quanto a você, volte para casa. Quando você puser os pés na cidade, o menino morrerá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9232, 13, "Todo o Israel chorará por ele e o sepultará. Ele é o único da família de Jeroboão que será sepultado, pois é o único da família de Jeroboão em quem o Senhor, o Deus de Israel, encontrou alguma coisa boa.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9233, 14, """O Senhor levantará para si um rei sobre Israel que eliminará a família de Jeroboão. Este é o dia! Como? Sim, agora mesmo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9234, 15, "E o Senhor ferirá Israel, de maneira que ficará como um junco balançando na água. Ele desarraigará Israel desta boa terra que deu aos seus antepassados e os espalhará para além do Rio, pois provocaram a ira do Senhor com os postes sagrados que fizeram.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9235, 16, "E ele abandonará Israel por causa dos pecados que Jeroboão cometeu e tem feito Israel cometer"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9236, 17, "Então a mulher de Jeroboão levantou-se e voltou para Tirza. Assim que entrou em casa, o menino morreu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9237, 18, "Eles o sepultaram, e todo o Israel chorou por ele, conforme o Senhor predissera por meio do seu servo, o profeta Aías.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9238, 19, "Os demais acontecimentos do reinado de Jeroboão, suas guerras e como governou, estão escritos nos registros históricos dos reis de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9239, 20, "Ele reinou durante vinte e dois anos, e então descansou com os seus antepassados. E o seu filho Nadabe foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9240, 21, "Roboão, filho de Salomão, foi rei de Judá. Tinha quarenta e um anos de idade quando começou a reinar, e reinou dezessete anos em Jerusalém, cidade que o Senhor havia escolhido dentre todas as tribos de Israel para nela pôr o seu nome. Sua mãe, uma amonita, chamava-se Naamá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9241, 22, "Judá fez o que o Senhor reprova. Pelos pecados que cometeram, eles despertaram a sua ira zelosa mais do que os seus antepassados o tinham feito.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9242, 23, "Também construíram para si altares idólatras, colunas sagradas e postes sagrados sobre todos os montes e debaixo de todas as árvores frondosas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9243, 24, "Havia no país até prostitutos cultuais; o povo se envolvia em todas as práticas detestáveis das nações que o Senhor havia expulsado de diante dos israelitas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9244, 25, "No quinto ano do reinado de Roboão, Sisaque, rei do Egito, atacou Jerusalém.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9245, 26, "Levou embora todos os tesouros do templo do Senhor e do palácio real, inclusive os escudos de ouro que Salomão havia feito.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9246, 27, "Por isso o rei Roboão mandou fazer escudos de bronze para substituí-los, e os entregou aos chefes da guarda da entrada do palácio real.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9247, 28, "Sempre que o rei ia ao templo do Senhor, os guardas empunhavam os escudos, e, em seguida, os devolviam à sala da guarda.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9248, 29, "Os demais acontecimentos do reinado de Roboão, e tudo o que fez, estão escritos nos registros históricos dos reis de Judá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9249, 30, "Houve guerra constante entre Roboão e Jeroboão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9250, 31, "Roboão descansou com os seus antepassados e foi sepultado com eles na cidade de Davi. Sua mãe, uma amonita, chamava-se Naamá. E o seu filho Abias foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9251, 1, "No décimo oitavo ano do reinado de Jeroboão, filho de Nebate, Abias tornou-se rei de Judá,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9252, 2, "e reinou três anos em Jerusalém. O nome de sua mãe era Maaca, filha de Absalão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9253, 3, "Ele cometeu todos os pecados que o seu pai tinha cometido; seu coração não era inteiramente consagrado ao Senhor, ao seu Deus, quanto fora o coração de Davi, seu antepassado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9254, 4, "No entanto, por amor de Davi, o Senhor, o seu Deus, concedeu-lhe uma lâmpada em Jerusalém dando-lhe um filho como sucessor e fortalecendo Jerusalém.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9255, 5, "Pois Davi fizera o que o Senhor aprova e não deixara de obedecer a nenhum dos mandamentos do Senhor durante todos os dias da sua vida, exceto no caso de Urias, o hitita.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9256, 6, "E houve guerra entre Roboão e Jeroboão durante toda a vida de Abias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9257, 7, "Os demais acontecimentos do reinado de Abias e todas as suas realizações estão escritos nos registros históricos dos reis de Judá. Também houve guerra entre Abias e Jeroboão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9258, 8, "E Abias descansou com os seus antepassados e foi sepultado na cidade de Davi. E o seu filho Asa foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9259, 9, "No vigésimo ano do reinado de Jeroboão, rei de Israel, Asa tornou-se rei de Judá,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9260, 10, "e reinou quarenta e um anos em Jerusalém. O nome da sua avó era Maaca, filha de Absalão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9261, 11, "Asa fez o que o Senhor aprova, tal como Davi, seu predecessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9262, 12, "Expulsou do país os prostitutos cultuais e se desfez de todos os ídolos que seu pai havia feito.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9263, 13, "Chegou até a depor sua avó Maaca da posição de rainha-mãe, pois ela havia feito um poste sagrado repugnante. Asa derrubou o poste e o queimou no vale do Cedrom.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9264, 14, "Embora os altares idólatras não tenham sido eliminados, o coração de Asa foi totalmente dedicado ao Senhor durante toda a sua vida.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9265, 15, "Ele trouxe para o templo do Senhor a prata, o ouro e os utensílios que ele e seu pai haviam consagrado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9266, 16, "Houve guerra entre Asa e Baasa, rei de Israel, durante todo o reinado deles.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9267, 17, "Baasa, rei de Israel, invadiu Judá e fortificou Ramá, para que ninguém pudesse entrar nem sair do território de Asa, rei de Judá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9268, 18, "Então Asa ajuntou a prata e o ouro que haviam sobrado no tesouro do templo do Senhor e do seu próprio palácio. Confiou tudo isso a alguns dos seus oficiais e os enviou a Ben-Hadade, filho de Tabriom e neto de Heziom, rei da Síria, que governava em Damasco,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9269, 19, "com uma mensagem que dizia: ""Façamos um tratado, como fizeram meu pai e o teu. Estou te enviando como presente prata e ouro. Agora, rompe o tratado que tens com Baasa, rei de Israel, para que ele saia do meu país"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9270, 20, "Ben-Hadade aceitou a proposta do rei Asa e ordenou aos comandantes das suas forças que atacassem as cidades de Israel. Ele conquistou Ijom, Dã, Abel-Bete-Maaca e todo o Quinerete, além de Naftali.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9271, 21, "Quando Baasa soube disso, abandonou a construção dos muros de Ramá e foi para Tirza.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9272, 22, "Então o rei Asa reuniu todos homens de Judá — ninguém foi isentado — e eles retiraram de Ramá as pedras e a madeira que Baasa estivera usando. Com esse material Asa fortificou Geba, em Benjamim, e também Mispá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9273, 23, "Os demais acontecimentos do reinado de Asa, todas as suas realizações, tudo o que fez e todas as cidades que construiu, tudo isso está escrito nos registros históricos dos reis de Judá. Na velhice Asa sofreu uma doença nos pés,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9274, 24, "e quando descansou com os seus antepassados, foi sepultado com eles na cidade de Davi, seu pai. E seu filho Josafá foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9275, 25, "Nadabe, filho de Jeroboão, tornou-se rei de Israel no segundo ano do reinado de Asa, rei de Judá, e reinou dois anos sobre Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9276, 26, "Fez o que o Senhor reprova, andando nos caminhos do seu pai e no pecado que ele tinha levado Israel a cometer.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9277, 27, "Baasa, filho de Aías, da tribo de Issacar, conspirou contra ele, e o matou na cidade filistéia de Gibetom, enquanto Nadabe e todo o exército de Israel a sitiavam.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9278, 28, "Baasa matou Nadabe no terceiro ano do reinado de Asa, rei de Judá, e foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9279, 29, "Assim que começou a reinar, matou toda a família de Jeroboão. Dos pertencentes a Jeroboão não deixou ninguém vivo, mas destruiu a todos, de acordo com a palavra do Senhor anunciada por seu servo, o silonita Aías.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9280, 30, "Isso aconteceu por causa dos pecados que Jeroboão havia cometido e havia feito Israel cometer, e porque ele tinha provocado a ira do Senhor, o Deus de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9281, 31, "Os demais acontecimentos do reinado de Nadabe e tudo o que fez, estão escritos nos registros históricos dos reis de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9282, 32, "Houve guerra entre Asa e Baasa, rei de Israel, durante todo o reinado deles.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9283, 33, "No terceiro ano do reinado de Asa, rei de Judá, Baasa, filho de Aías, tornou-se rei de todo o Israel, em Tirza, e reinou vinte e quatro anos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9284, 34, "Fez o que o Senhor reprova, andando nos caminhos de Jeroboão e nos pecados que ele tinha levado Israel a cometer.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9285, 1, "Então a palavra do Senhor contra Baasa veio a Jeú, filho de Hanani:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9286, 2, """Eu o levantei do pó e o tornei líder de Israel, meu povo, mas você andou nos caminhos de Jeroboão e fez o meu povo pecar e provocar a minha ira por causa dos pecados deles.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9287, 3, "Por isso estou na iminência de destruir Baasa e a sua família, fazendo a ela o que fiz à de Jeroboão, filho de Nebate.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9288, 4, "Cães comerão os da família de Baasa que morrerem na cidade, e as aves do céu se alimentarão dos que morrerem no campo"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9289, 5, "Os demais acontecimentos do reinado de Baasa, o que fez e as suas realizações, estão escritos nos registros históricos dos reis de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9290, 6, "Baasa descansou com os seus antepassados e foi sepultado em Tirza. E seu filho Elá foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9291, 7, "A palavra do Senhor veio por meio do profeta Jeú, filho de Hanani, a Baasa e sua família, por terem feito o que o Senhor reprova, provocando a sua ira, tornando-se como a família de Jeroboão — e também porque Baasa destruiu a família de Jeroboão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9292, 8, "No vigésimo sexto ano do reinado de Asa, rei de Judá, Elá, filho de Baasa, tornou-se rei de Israel, e reinou dois anos em Tirza.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9293, 9, "Zinri, um dos seus oficiais, que comandava metade dos seus carros de guerra, conspirou contra ele. Elá estava em Tirza naquela ocasião, embriagando-se na casa de Arsa, o encarregado do palácio de Tirza.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9294, 10, "Zinri entrou e o feriu e o matou, no vigésimo sétimo ano do reinado de Asa, rei de Judá. E foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9295, 11, "Assim que começou a reinar, logo que se assentou no trono, eliminou toda a família de Baasa. Não poupou uma só pessoa do sexo masculino, fosse parente ou amigo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9296, 12, "Assim Zinri destruiu toda a família de Baasa, de acordo com a palavra do Senhor falada contra Baasa pelo profeta Jeú,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9297, 13, "por causa de todos os pecados que Baasa e seu filho Elá haviam cometido e levado Israel a cometer, pois, com os seus ídolos inúteis, provocaram a ira do Senhor, o Deus de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9298, 14, "Os demais acontecimentos do reinado de Elá e tudo o que fez estão escritos nos registros históricos dos reis de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9299, 15, "No vigésimo sétimo ano do reinado de Asa, rei de Judá, Zinri reinou sete dias em Tirza. O exército estava acampado perto da cidade filistéia de Gibetom.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9300, 16, "Quando os acampados souberam que Zinri havia conspirado contra o rei e o tinha assassinado, proclamaram ali no acampamento, naquele mesmo dia, Onri, o comandante do exército, rei sobre Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9301, 17, "Então Onri e todo o seu exército saíram de Gibetom e sitiaram Tirza.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9302, 18, "Quando Zinri viu que a cidade tinha sido tomada, entrou na cidadela do palácio real e incendiou o palácio em torno de si, e morreu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9303, 19, "Tudo por causa dos pecados que ele havia cometido, fazendo o que o Senhor reprova e andando nos caminhos de Jeroboão e no pecado que ele tinha cometido e levado Israel a cometer.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9304, 20, "Os demais acontecimentos do reinado de Zinri e a rebelião que liderou estão escritos nos registros históricos dos reis de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9305, 21, "Então o povo de Israel dividiu-se em duas facções; metade apoiava Tibni, filho de Ginate, para fazê-lo rei, e a outra metade apoiava Onri.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9306, 22, "Mas os seguidores de Onri revelaram-se mais fortes do que os de Tibni, filho de Ginate. E aconteceu que Tibni morreu e Onri tornou-se rei.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9307, 23, "No trigésimo primeiro ano do reinado de Asa, rei de Judá, Onri tornou-se rei de Israel e reinou doze anos, seis deles em Tirza.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9308, 24, "Por setenta quilos de prata ele comprou de Sêmer a colina de Samaria, onde construiu uma cidade, a qual chamou Samaria, por causa de Sêmer, o nome do antigo proprietário da colina.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9309, 25, "Onri, porém, fez o que o Senhor reprova e pecou mais do que todos que reinaram antes dele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9310, 26, "Andou nos caminhos de Jeroboão, filho de Nebate, e no pecado que ele tinha levado Israel a cometer, e assim, com os seus ídolos inúteis, provocou a ira do Senhor, o Deus de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9311, 27, "Os demais acontecimentos do reinado de Onri, o que fez e as suas realizações, tudo está escrito nos registros históricos dos reis de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9312, 28, "Onri descansou com os seus antepassados e foi sepultado em Samaria. E seu filho Acabe foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9313, 29, "No trigésimo oitavo ano do reinado de Asa, rei de Judá, Acabe, filho de Onri, tornou-se rei de Israel, e reinou vinte e dois anos sobre Israel, em Samaria.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9314, 30, "Acabe, filho de Onri, fez o que o Senhor reprova, mais do que qualquer outro antes dele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9315, 31, "Ele não apenas achou que não tinha importância cometer os pecados de Jeroboão, filho de Nebate, mas também se casou com Jezabel, filha de Etbaal, rei dos sidônios, e passou a prestar culto a Baal e adorá-lo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9316, 32, "No templo de Baal, que ele mesmo tinha construído em Samaria, Acabe ergueu um altar para Baal.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9317, 33, "Fez também um poste sagrado. Ele provocou a ira do Senhor, o Deus de Israel, mais do que todos os reis de Israel antes dele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9318, 34, "Durante o seu reinado, Hiel, de Betel, reconstruiu Jericó. Lançou os alicerces à custa do seu filho mais velho, Abirão, e instalou as suas portas à custa do seu filho mais novo, Segube, de acordo com a palavra que o Senhor tinha falado por meio de Josué, filho de Num.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9319, 1, "Ora, Elias, o tesbita da Tisbe de Gileade, disse a Acabe: ""Juro pelo nome do Senhor, o Deus de Israel, a quem sirvo, que não cairá orvalho nem chuva nos anos seguintes, exceto mediante a minha palavra"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9320, 2, "Depois disso a palavra do Senhor veio a Elias:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9321, 3, """Saia daqui, vá para o leste e esconda-se perto do riacho de Querite, a leste do Jordão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9322, 4, "Você beberá do riacho, e dei ordens aos corvos para o alimentarem lá"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9323, 5, "E ele fez o que o Senhor lhe tinha dito. Foi para o riacho de Querite, a leste do Jordão, e ficou por lá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9324, 6, "Os corvos lhe traziam pão e carne de manhã e de tarde, e ele bebia água do riacho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9325, 7, "Algum tempo depois, o riacho secou-se por falta de chuva.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9326, 8, "Então a palavra do Senhor veio a Elias:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9327, 9, """Vá imediatamente para a cidade de Sarepta de Sidom e fique por lá. Ordenei a uma viúva daquele lugar que lhe forneça comida"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9328, 10, "E ele foi. Quando chegou à porta da cidade, encontrou uma viúva que estava colhendo gravetos. Ele a chamou e perguntou: ""Pode me trazer um pouco d’água numa jarra para eu beber? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9329, 11, "Enquanto ela ia indo buscar água, ele gritou: ""Por favor, traga também um pedaço de pão"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9330, 12, """Juro pelo nome do Senhor, o teu Deus"", ela respondeu, ""não tenho nenhum pedaço de pão; só um punhado de farinha num jarro e um pouco de azeite numa botija. Estou colhendo uns dois gravetos para levar para casa e preparar uma refeição para mim e para o meu filho, para que a comamos e depois morramos. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9331, 13, "Elias, porém, lhe disse: ""Não tenha medo. Vá para casa e faça o que disse. Mas primeiro faça um pequeno bolo com o que você tem e traga para mim, e depois faça algo para você e para o seu filho.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9332, 14, "Pois assim diz o Senhor, o Deus de Israel: ‘A farinha na vasilha não se acabará e o azeite na botija não se secará até o dia em que o Senhor fizer chover sobre a terra’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9333, 15, "Ela foi e fez conforme Elias lhe dissera. E aconteceu que a comida durou todos os dias para Elias e para a mulher e sua família.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9334, 16, "Pois a farinha na vasilha não se acabou e o azeite na botija não se secou, conforme a palavra do Senhor proferida por Elias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9335, 17, "Algum tempo depois o filho da mulher, dona da casa, ficou doente, foi piorando e finalmente parou de respirar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9336, 18, "E a mulher reclamou a Elias: ""Que foi que eu te fiz, ó homem de Deus? Vieste para lembrar-me do meu pecado e matar o meu filho? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9337, 19, """Dê-me o seu filho"", respondeu Elias. Ele o apanhou dos braços dela, levou-o para o quarto de cima onde estava hospedado, e o pôs em cima da cama.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9338, 20, "Então clamou ao Senhor: ""Ó Senhor, meu Deus, trouxeste também desgraça sobre esta viúva, com quem estou hospedado, fazendo morrer o seu filho? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9339, 21, "Então ele se deitou sobre o menino três vezes e clamou ao Senhor: ""Ó Senhor, meu Deus, faze voltar a vida a este menino! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9340, 22, "O Senhor ouviu o clamor de Elias, e a vida voltou ao menino, e ele viveu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9341, 23, "Então Elias levou o menino para baixo, entregou-o à mãe e disse: ""Veja, seu filho está vivo! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9342, 24, "Então a mulher disse a Elias: ""Agora sei que tu és um homem de Deus e que a palavra do Senhor, vinda da tua boca, é a verdade"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9343, 1, "Depois de um longo tempo, no terceiro ano da seca, a palavra do Senhor veio a Elias: ""Vá apresentar-se a Acabe, pois enviarei chuva sobre a terra"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9344, 2, "E Elias foi. Como a fome era grande em Samaria,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9345, 3, "Acabe convocou Obadias, o responsável por seu palácio, homem que temia muito ao Senhor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9346, 4, "Enquanto Jezabel estava eliminando os profetas do Senhor, Obadias reuniu cem profetas e os escondeu em duas cavernas, cinqüenta em cada uma, e lhes forneceu comida e água.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9347, 5, "Certa vez Acabe disse a Obadias: ""Vamos a todas as fontes e vales do país. Talvez consigamos achar um pouco de capim para manter vivos os cavalos e as mulas e assim não será preciso matar nenhum animal"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9348, 6, "De modo que dividiram o território que iam percorrer; Acabe foi numa direção e Obadias noutra.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9349, 7, "Quando Obadias estava a caminho, Elias o encontrou. Obadias o reconheceu, inclinou-se até o chão e perguntou: ""És tu mesmo, meu senhor Elias? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9350, 8, """Sou"", respondeu Elias. ""Vá dizer ao seu senhor: Elias está aqui. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9351, 9, """O que eu fiz de errado"", perguntou Obadias, ""para que entregues o teu servo a Acabe para ser morto?", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9352, 10, "Juro pelo nome do Senhor, o teu Deus, que não há uma só nação ou reino aonde o rei, meu senhor, não enviou alguém para procurar por ti. E, sempre que uma nação ou reino afirmava que tu não estavas lá, ele os fazia jurar que não conseguiram encontrar-te.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9353, 11, "Mas agora me dizes para ir até o meu senhor e dizer-lhe: ‘Elias está aqui’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9354, 12, "Não sei para onde o Espírito do Senhor poderá levar-te quando eu te deixar. Se eu for dizer a Acabe e ele não te encontrar, ele me matará. E eu, que sou teu servo, tenho adorado o Senhor desde a minha juventude.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9355, 13, "Por acaso não ouviste, meu senhor, o que eu fiz enquanto Jezabel estava matando os profetas do Senhor? Escondi cem dos profetas do Senhor em duas cavernas, cinqüenta em cada uma, e os abasteci de comida e água.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9356, 14, "E agora me dizes para ir ao meu senhor e dizer-lhe: ‘Elias está aqui’. Ele vai me matar! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9357, 15, "E disse Elias: ""Juro pelo nome do Senhor dos Exércitos, a quem eu sirvo, que hoje eu me apresentarei a Acabe"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9358, 16, "Então Obadias dirigiu-se a Acabe e passou-lhe a informação, e Acabe foi ao encontro de Elias.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9359, 17, "Quando viu Elias, disse-lhe: ""É você mesmo, perturbador de Israel? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9360, 18, """Não tenho perturbado Israel"", Elias respondeu. ""Mas você e a família do seu pai têm. Vocês abandonaram os mandamentos do Senhor e seguiram os baalins.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9361, 19, "Agora convoque todo o povo de Israel para encontrar-se comigo no monte Carmelo. E traga os quatrocentos e cinqüenta profetas de Baal e os quatrocentos profetas de Aserá, que comem à mesa de Jezabel. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9362, 20, "Acabe convocou então todo o Israel e reuniu os profetas no monte Carmelo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9363, 21, "Elias dirigiu-se ao povo e disse: ""Até quando vocês vão oscilar entre duas opiniões? Se o Senhor é Deus, sigam-no; mas, se Baal é Deus, sigam-no"". O povo, porém, nada respondeu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9364, 22, "Disse então Elias: ""Eu sou o único que restou dos profetas do Senhor, mas Baal tem quatrocentos e cinqüenta profetas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9365, 23, "Tragam dois novilhos. Escolham eles um, e cortem-no em pedaços e o ponham sobre a lenha, mas não acendam fogo. Eu prepararei o outro novilho e o colocarei sobre a lenha, e também não acenderei fogo nela.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9366, 24, "Então vocês invocarão o nome do seu deus, e eu invocarei o nome do Senhor. O deus que responder por meio do fogo, esse é Deus"". Então todo o povo disse: ""O que você disse é bom"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9367, 25, "Elias disse aos profetas de Baal: ""Escolham um dos novilhos e preparem-no primeiro, visto que vocês são tantos. Clamem pelo nome do seu deus, mas não acendam o fogo"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9368, 26, "Então pegaram o novilho que lhes foi dado e o prepararam. E clamaram pelo nome de Baal desde a manhã até o meio-dia. ""Ó Baal, responde-nos! "", gritavam. E dançavam em volta do altar que haviam feito. Mas não houve nenhuma resposta; ninguém respondeu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9369, 27, "Ao meio-dia Elias começou a zombar deles. ""Gritem mais alto! "", dizia, ""já que ele é um deus. Quem sabe está meditando, ou ocupado, ou viajando. Talvez esteja dormindo e precise ser despertado. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9370, 28, "Então passaram a gritar ainda mais alto e a ferir-se com espadas e lanças, de acordo com o costume deles, até sangrarem.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9371, 29, "Passou o meio-dia, e eles continuaram profetizando em transe até a hora do sacrifício da tarde. Mas não houve resposta alguma; ninguém respondeu, ninguém deu atenção.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9372, 30, "Então Elias disse a todo o povo: ""Aproximem-se de mim"". O povo aproximou-se, e Elias reparou o altar do Senhor, que estava em ruínas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9373, 31, "Depois apanhou doze pedras, uma para cada tribo dos descendentes de Jacó, a quem a palavra do Senhor tinha sido dirigida, e disse: ""Seu nome será Israel"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9374, 32, "Com as pedras construiu um altar em honra do nome do Senhor, e cavou ao redor do altar uma valeta com capacidade de duas medidas de semente.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9375, 33, "Depois arrumou a lenha, cortou o novilho em pedaços e o pôs sobre a lenha. Então lhes disse: ""Encham de água quatro jarras grandes e derramem-na sobre o holocausto e sobre a lenha"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9376, 34, """Façam-no novamente"", disse, e eles o fizeram de novo. ""Façam-no pela terceira vez"", ordenou, e eles o fizeram pela terceira vez.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9377, 35, "A água escorria do altar, chegando a encher a valeta.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9378, 36, "À hora do sacrifício, o profeta Elias colocou-se à frente e orou: ""Ó Senhor, Deus de Abraão, de Isaque e de Israel, que hoje fique conhecido que tu és Deus em Israel e que sou o teu servo e que fiz todas estas coisas por ordem tua.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9379, 37, "Responde-me, ó Senhor, responde-me, para que este povo saiba que tu, ó Senhor, és Deus, e que fazes o coração deles voltar para ti"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9380, 38, "Então o fogo do Senhor caiu e queimou completamente o holocausto, a lenha, as pedras e o chão, e também secou totalmente a água na valeta.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9381, 39, "Quando o povo viu isso, todos caíram prostrados e gritaram: ""O Senhor é Deus! O Senhor é Deus! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9382, 40, "Então Elias ordenou-lhes: ""Prendam os profetas de Baal. Não deixem nenhum escapar! "" Eles os prenderam, e Elias os fez descer ao riacho de Quisom e lá os matou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9383, 41, "E Elias disse a Acabe: ""Vá comer e beber, pois já ouço o barulho de chuva pesada"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9384, 42, "Então Acabe foi comer e beber, mas Elias subiu até o alto do Carmelo, dobrou-se até o chão e pôs o rosto entre os joelhos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9385, 43, """Vá e olhe na direção do mar"", disse ao seu servo. E ele foi e olhou. ""Não há nada lá"", disse ele. Sete vezes Elias mandou: ""Volte para ver"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9386, 44, "Na sétima vez o servo disse: ""Uma nuvem tão pequena quanto a mão de um homem está se levantando do mar"". Então Elias disse: ""Vá dizer a Acabe: Prepare o seu carro e desça, antes que a chuva o impeça"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9387, 45, "Enquanto isso, nuvens escuras apareceram no céu, começou a ventar e começou a chover forte, e Acabe partiu de carro para Jezreel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9388, 46, "O poder do Senhor veio sobre Elias, e este, prendendo a capa com o cinto, correu à frente de Acabe por todo o caminho até Jezreel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9389, 1, "Ora, Acabe contou a Jezabel tudo o que Elias tinha feito e como havia matado todos aqueles profetas à espada.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9390, 2, "Por isso Jezabel mandou um mensageiro a Elias para dizer-lhe: ""Que os deuses me castiguem com todo o rigor, caso amanhã nesta hora eu não faça com a sua vida o que você fez com a deles"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9391, 3, "Elias teve medo e fugiu para salvar a vida. Em Berseba de Judá ele deixou o seu servo", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9392, 4, "e entrou no deserto, caminhando um dia. Chegou a um pé de giesta, sentou-se debaixo dele e orou, pedindo a morte. ""Já tive o bastante, Senhor. Tira a minha vida; não sou melhor do que os meus antepassados. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9393, 5, "Depois se deitou debaixo da árvore e dormiu. De repente um anjo tocou nele e disse: ""Levante-se e coma"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9394, 6, "Elias olhou ao redor e ali, junto à sua cabeça, havia um pão assado sobre brasas quentes e um jarro de água. Ele comeu, bebeu e deitou-se de novo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9395, 7, "O anjo do Senhor voltou, tocou nele e disse: ""Levante-se e coma, pois a sua viagem será muito longa"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9396, 8, "Então ele se levantou, comeu e bebeu. Fortalecido com aquela comida, viajou quarenta dias e quarenta noites, até que chegou a Horebe, o monte de Deus.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9397, 9, "Ali entrou numa caverna e passou a noite. E a palavra do Senhor veio a ele: ""O que você está fazendo aqui, Elias? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9398, 10, "Ele respondeu: ""Tenho sido muito zeloso pelo Senhor, Deus dos Exércitos. Os israelitas rejeitaram a tua aliança, quebraram os teus altares, e mataram os teus profetas à espada. Sou o único que sobrou, e agora também estão procurando matar-me"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9399, 11, "O Senhor lhe disse: ""Saia e fique no monte, na presença do Senhor, pois o Senhor vai passar"". Então veio um vento fortíssimo que separou os montes e esmigalhou as rochas diante do Senhor, mas o Senhor não estava no vento. Depois do vento houve um terremoto, mas o Senhor não estava no terremoto.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9400, 12, "Depois do terremoto houve um fogo, mas o Senhor não estava nele. E depois do fogo houve o murmúrio de uma brisa suave.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9401, 13, "Quando Elias ouviu, puxou a capa para cobrir o rosto, saiu e ficou à entrada da caverna. E uma voz lhe perguntou: ""O que você está fazendo aqui, Elias? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9402, 14, "Ele respondeu: ""Tenho sido muito zeloso pelo Senhor, Deus dos Exércitos. Os israelitas rejeitaram a tua aliança, quebraram os teus altares, e mataram os teus profetas à espada. Sou o único que sobrou, e agora também estão procurando matar-me"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9403, 15, "O Senhor lhe disse: ""Volte pelo caminho por onde veio, e vá para o deserto de Damasco. Chegando lá, unja Hazael como rei da Síria.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9404, 16, "Unja também Jeú, filho de Ninsi, como rei de Israel, e unja Eliseu, filho de Safate, de Abel-Meolá, para suceder a você como profeta.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9405, 17, "Jeú matará todo aquele que escapar da espada de Hazael, e Eliseu matará todo aquele que escapar da espada de Jeú.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9406, 18, "No entanto, fiz sobrar sete mil em Israel, todos aqueles cujos joelhos não se inclinaram diante de Baal e todos aqueles cujas bocas não o beijaram"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9407, 19, "Então Elias saiu de lá e encontrou Eliseu, filho de Safate. Ele estava arando com doze parelhas de bois, e estava conduzindo a décima-segunda parelha. Elias o alcançou e lançou a sua capa sobre ele.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9408, 20, "Eliseu deixou os bois e correu atrás de Elias. ""Deixa-me dar um beijo de despedida em meu pai e minha mãe"", disse, ""e então irei contigo. "" ""Vá e volte"", respondeu Elias, ""pelo que lhe fiz. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9409, 21, "E Eliseu voltou, apanhou a sua parelha de bois e os matou. Queimou o equipamento de arar para cozinhar a carne e a deu ao povo, e eles comeram. Depois partiu com Elias, e se tornou o seu auxiliar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9410, 1, "O rei Ben-Hadade, da Síria, convocou todo o seu exército e, acompanhado de trinta e dois reis com seus cavalos e carros de guerra, cercou e atacou Samaria.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9411, 2, "Ele enviou mensageiros à cidade, a Acabe, o rei de Israel, que lhe disseram: ""Isto é o que diz Ben-Hadade:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9412, 3, "‘A sua prata e o seu ouro são meus, e o melhor de suas mulheres e filhos também’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9413, 4, "O rei respondeu: ""Que seja conforme tu dizes, ó rei, meu senhor. Eu e tudo o que tenho somos teus"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9414, 5, "Os mensageiros voltaram e disseram: ""Assim diz Ben-Hadade: ‘Mandei tomar sua prata e seu ouro, suas mulheres e seus filhos.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9415, 6, "Mas amanhã, a esta hora, enviarei meus oficiais para vasculharem o seu palácio e as casas dos seus oficiais. Eles me trarão tudo o que você considera de valor’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9416, 7, "O rei de Israel convocou todas as autoridades de Israel e lhes disse: ""Vejam como esse homem está querendo a nossa desgraça! Quando mandou tomar as minhas mulheres e os meus filhos, a minha prata e o meu ouro, eu não lhe neguei! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9417, 8, "As autoridades e todo o povo responderam: ""Não lhe dês atenção nem concordes com as suas exigências"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9418, 9, "E ele respondeu aos mensageiros de Ben-Hadade: ""Digam ao rei, meu senhor: Teu servo fará tudo o que exigiste na primeira vez, mas não posso atender a esta exigência"". E eles levaram a resposta a Ben-Hadade.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9419, 10, "Então Ben-Hadade mandou esta outra mensagem a Acabe: ""Que os deuses me castiguem com todo o rigor, caso fique em Samaria pó suficiente para dar um punhado a cada um dos meus homens"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9420, 11, "O rei de Israel respondeu: ""Digam-lhe: ‘Quem está vestindo a sua armadura não deve se gabar como aquele que a está tirando’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9421, 12, "Ben-Hadade recebeu essa mensagem quando ele e os reis estavam bebendo em suas tendas, e ordenou aos seus homens: ""Preparem-se para atacar a cidade"". E eles lhe obedeceram.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9422, 13, "Nessa ocasião, um profeta foi até Acabe, rei de Israel, e anunciou: ""Assim diz o Senhor: ‘Vê este exército enorme? Hoje eu o entregarei nas suas mãos, e então você saberá que eu sou o Senhor’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9423, 14, """Mas quem fará isso? "", perguntou Acabe. O profeta respondeu: ""Assim diz o Senhor: ‘Os jovens soldados dos líderes das províncias o farão’ "". ""E quem começará a batalha? "", perguntou. O profeta respondeu: ""Você"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9424, 15, "Então Acabe convocou os jovens soldados dos líderes das províncias, duzentos e trinta e dois homens. Em seguida reuniu o restante dos israelitas, sete mil ao todo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9425, 16, "Eles partiram ao meio-dia, enquanto Ben-Hadade e os trinta e dois reis aliados a ele estavam se embriagando nas suas tendas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9426, 17, "Os jovens soldados dos líderes das províncias saíram primeiro. Nisso, uma patrulha de Ben-Hadade informou: ""Saíram alguns homens de Samaria"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9427, 18, "Ele disse: ""Quer tenham saído para a paz, quer para a guerra, tragam-nos vivos"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9428, 19, "Os jovens soldados dos líderes das províncias marcharam para fora da cidade, com o exército na retaguarda,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9429, 20, "e cada um matou o seu adversário. Diante disso, os arameus fugiram, perseguidos pelos israelitas. Mas Ben-Hadade, rei da Síria, escapou a cavalo com alguns de seus cavaleiros.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9430, 21, "O rei de Israel avançou e destruiu os cavalos e carros de guerra e infligiu pesadas baixas aos arameus.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9431, 22, "Depois disso, o profeta foi ao rei de Israel e disse: ""Fortaleça a sua posição e veja o que deve ser feito, pois na próxima primavera o rei da Síria o atacará de novo"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9432, 23, "Enquanto isso, os conselheiros do rei da Síria lhe diziam: ""Os deuses deles são deuses das montanhas. É por isso que eles foram fortes demais para nós. Mas, se os combatermos nas planícies, com certeza seremos mais fortes do que eles.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9433, 24, "Deves tirá-los todos os reis dos seus comandos e substituí-los por outros oficiais.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9434, 25, "Também deves organizar um exército como o que perdeste, cavalo por cavalo e carro por carro, para que possamos combater Israel nas planícies. Então é certo que os venceremos"". Ele concordou com eles e fez como foi aconselhado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9435, 26, "Na primavera seguinte Ben-Hadade convocou os arameus e marchou até Afeque para lutar contra Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9436, 27, "Os israelitas foram convocados e, tendo recebido provisões, saíram para enfrentar os arameus. Os israelitas acamparam no lado oposto como dois pequenos rebanhos de cabras, enquanto que os arameus cobriam todo o campo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9437, 28, "O homem de Deus foi ao rei de Israel e lhe disse: ""Assim diz o Senhor: ‘Como os arameus pensam que o Senhor é um deus das montanhas e não um deus dos vales, eu entregarei esse exército enorme nas suas mãos, e vocês saberão que eu sou o Senhor’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9438, 29, "Durante sete dias estiveram acampados em frente um do outro, e no sétimo dia entraram em combate. Num só dia os israelitas mataram cem mil soldados de infantaria arameus.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9439, 30, "O restante deles escapou para a cidade de Afeque, onde o muro caiu sobre vinte e sete mil deles. Ben-Hadade também fugiu para a cidade e se escondeu, ora num aposento, ora noutro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9440, 31, "Seus oficiais lhe disseram: ""Soubemos que os reis do povo de Israel são misericordiosos. Nós vamos até o rei de Israel vestidos com panos de saco e com cordas no pescoço. Talvez ele poupe a tua vida"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9441, 32, "Vestindo panos de saco e tendo cordas envolvendo o pescoço, foram ao rei de Israel e disseram: ""Teu servo Ben-Hadade diz: ‘Rogo-te que me deixes viver’ "". O rei respondeu: ""Ele ainda está vivo? Ele é meu irmão! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9442, 33, "Os homens interpretaram isso como um bom sinal e de imediato aproveitaram o que ele tinha dito. ""Isso mesmo, teu irmão Ben-Hadade! "", disseram. ""Tragam-no aqui"", disse o rei. Quando Ben-Hadade chegou, Acabe o fez subir no seu carro.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9443, 34, """Devolverei as cidades que o meu pai tomou do teu pai"", ofereceu Ben-Hadade. ""Tu poderás estabelecer os teus próprios mercados em Damasco, como fez meu pai em Samaria. "" Acabe disse: ""Mediante um tratado, libertarei você"". Então fizeram um tratado, e Acabe o deixou ir.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9444, 35, "Por ordem do Senhor um dos discípulos dos profetas disse ao seu companheiro: ""Fira-me"", mas o homem se recusou a fazê-lo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9445, 36, "Então o profeta disse: ""Como você não obedeceu ao Senhor, assim que você sair daqui um leão o ferirá"". E, logo que o homem partiu, um leão o atacou e o feriu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9446, 37, "O profeta encontrou outro homem e lhe disse: ""Fira-me, por favor"". Este o atingiu e o feriu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9447, 38, "Então o profeta saiu e ficou ao lado da estrada, à espera do rei. Ele se disfarçou, cobrindo os olhos com sua testeira.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9448, 39, "Quando o rei ia passando, o profeta gritou para ele: ""Teu servo entrou no auge do combate, e alguém veio a mim com um prisioneiro e me disse: ‘Vigie este homem. Se ele escapar, será a sua vida pela dele, ou você deverá pagar trinta e cinco quilos de prata’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9449, 40, "Enquanto o teu servo estava ocupado com outras coisas, o homem desapareceu"". ""Essa é a sua sentença"", disse o rei de Israel. ""Você mesmo a pronunciou. """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9450, 41, "Então o profeta rapidamente removeu a testeira dos olhos, e o rei o reconheceu como um dos profetas.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9451, 42, "Ele disse ao rei: ""Assim diz o Senhor: ‘Você libertou um homem que eu havia decidido que devia morrer. Por isso, é a sua vida pela vida dele, o seu povo pelo povo dele’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9452, 43, "Aborrecido e irritado, o rei de Israel voltou para o seu palácio em Samaria.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9453, 1, "Algum tempo depois houve um incidente envolvendo uma vinha que pertencia a Nabote, de Jezreel. A vinha ficava em Jezreel, ao lado do palácio de Acabe, rei de Samaria.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9454, 2, "Acabe tinha dito a Nabote: ""Dê-me a sua vinha para eu usar como horta, já que fica ao lado do meu palácio. Em troca eu lhe darei uma vinha melhor ou, se preferir, eu lhe pagarei, seja qual for o seu valor"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9455, 3, "Nabote, contudo, respondeu: ""O SENHOR me livre de dar a ti a herança dos meus pais! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9456, 4, "Então Acabe foi para casa, aborrecido e indignado porque Nabote, de Jezreel, lhe dissera: ""Não te darei a herança dos meus pais"". Deitou-se na cama, virou o rosto para a parede e recusou-se a comer.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9457, 5, "Sua mulher Jezabel entrou e lhe perguntou: ""Por que você está tão aborrecido? Por que não come? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9458, 6, "Ele respondeu-lhe: ""Porque eu disse a Nabote, de Jezreel: Venda-me a sua vinha; ou, se preferir, eu lhe darei outra vinha em lugar dessa. Mas ele disse: ‘Não te darei minha vinha’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9459, 7, "Disse-lhe Jezabel, sua mulher: ""É assim que você age como rei de Israel? Levante-se e coma! Anime-se. Conseguirei para você a vinha de Nabote, de Jezreel"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9460, 8, "Então ela escreveu cartas em nome de Acabe, pôs nelas o selo do rei, e as enviou às autoridades e aos nobres da cidade de Nabote.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9461, 9, "Naquelas cartas ela escreveu: ""Decretem um dia de jejum e ponham Nabote sentado num lugar de destaque entre o povo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9462, 10, "E mandem dois homens vadios sentar-se em frente dele e façam com que testemunhem que ele amaldiçoou tanto a Deus quanto ao rei. Levem-no para fora e apedrejem-no até a morte"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9463, 11, "As autoridades e os nobres da cidade de Nabote fizeram conforme Jezabel os orientara nas cartas que lhes tinha escrito.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9464, 12, "Decretaram jejum e fizeram Nabote sentar-se num local destacado no meio do povo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9465, 13, "Então dois homens vadios vieram e se sentaram em frente dele e o acusaram diante do povo, dizendo: ""Nabote amaldiçoou tanto a Deus quanto ao rei"". Por isso o levaram para fora da cidade e o apedrejaram até a morte.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9466, 14, "Então mandaram informar a Jezabel: ""Nabote foi apedrejado e está morto"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9467, 15, "Assim que Jezabel soube que Nabote tinha sido apedrejado até a morte, disse a Acabe: ""Levante-se e tome posse da vinha que Nabote, de Jezreel, recusou-se a vender-lhe. Ele não está mais vivo; está morto! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9468, 16, "Quando Acabe ouviu que Nabote estava morto, levantou-se e foi tomar posse da vinha.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9469, 17, "Então a palavra do Senhor veio ao tesbita Elias:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9470, 18, """Vá encontrar-se com Acabe, o rei de Israel, que reina em Samaria. Agora ele está na vinha de Nabote para tomar posse dela.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9471, 19, "Diga-lhe: Assim diz o Senhor: Você assassinou um homem e ainda se apossou de sua propriedade? E acrescentou: Assim diz o Senhor: No local onde os cães lamberam o sangue de Nabote, lamberão também o seu sangue; isso mesmo, o seu sangue! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9472, 20, "Acabe disse a Elias: ""Então você me encontrou, meu inimigo! "" ""Eu o encontrei"", ele respondeu, ""porque você se vendeu para fazer o que o Senhor reprova.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9473, 21, "‘Vou trazer desgraça sobre você. Devorarei os seus descendentes e eliminarei da sua família todos os do sexo masculino em Israel, sejam escravos ou livres.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9474, 22, "Farei à sua família o que fiz à de Jeroboão, filho de Nebate, e à de Baasa, filho de Aías, pois você provocou a minha ira e fez Israel pecar. ’", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9475, 23, """E, acerca de Jezabel, o Senhor diz: ‘Os cães devorarão Jezabel junto ao muro de Jezreel’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9476, 24, """Os que pertencem a Acabe e que morrerem na cidade os cães comerão, e as aves do céu se alimentarão dos que morrerem no campo"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9477, 25, "( Nunca existiu ninguém como Acabe, que se vendeu para fazer o que o Senhor reprova, pressionado por sua mulher Jezabel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9478, 26, "Ele se comportou da maneira mais detestável possível, indo atrás de ídolos, como faziam os amorreus, que o Senhor tinha expulsado de diante de Israel. )", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9479, 27, "Quando Acabe ouviu essas palavras, rasgou as suas vestes, vestiu-se de pano de saco e jejuou. Passou a dormir sobre panos de saco e agia com mansidão.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9480, 28, "Então a palavra do Senhor veio ao tesbita Elias:", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9481, 29, """Você notou como Acabe se humilhou diante de mim? Visto que se humilhou, não trarei essa desgraça durante o seu reinado, mas durante o reinado de seu filho"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9482, 1, "Durante três anos não houve guerra entre a Síria e Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9483, 2, "Mas no terceiro ano, Josafá, rei de Judá, foi visitar o rei de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9484, 3, "Este havia perguntado aos seus oficiais: ""Por acaso vocês não sabem que Ramote-Gileade nos pertence, e ainda assim não estamos fazendo nada para retomá-la do rei da Síria? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9485, 4, "Então perguntou a Josafá: ""Irás comigo lutar contra Ramote-Gileade? "" Josafá respondeu ao rei de Israel: ""Sou como tu, e meu povo é como o teu povo, e os meus cavalos são como se fossem teus"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9486, 5, "Mas acrescentou: ""Peço-te que busques primeiro o conselho do Senhor"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9487, 6, "Então o rei de Israel reuniu quatrocentos profetas, e lhes perguntou: ""Devo ir à guerra contra Ramote-Gileade, ou não? "" Eles responderam: ""Sim, pois o Senhor a entregará nas mãos do rei"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9488, 7, "Josafá, porém, perguntou: ""Não existe aqui mais nenhum profeta do Senhor, a quem possamos consultar? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9489, 8, "O rei de Israel respondeu a Josafá: ""Ainda há um homem por meio de quem podemos consultar o Senhor, mas eu o odeio, porque nunca profetiza coisas boas a meu respeito, mas sempre coisas ruins. É Micaías, filho de Inlá"". ""O rei não deveria dizer isso"", Josafá respondeu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9490, 9, "Então o rei de Israel chamou um dos seus oficiais e disse: ""Traga Micaías, filho de Inlá, imediatamente"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9491, 10, "Usando vestes reais, o rei de Israel e Josafá, rei de Judá, estavam sentados em seus tronos, na eira, junto à porta de Samaria, e todos os profetas estavam profetizando em transe diante deles.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9492, 11, "E Zedequias, filho de Quenaaná, tinha feito chifres de ferro, e declarou: ""Assim diz o Senhor: ‘Com estes chifres tu ferirás os arameus até que sejam destruídos’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9493, 12, "Todos os outros profetas estavam profetizando a mesma coisa, dizendo: ""Ataca Ramote-Gileade, e serás vitorioso, pois o Senhor a entregará nas mãos do rei"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9494, 13, "O mensageiro que tinha ido chamar Micaías lhe disse: ""Veja, todos os outros profetas estão predizendo que o rei terá sucesso. Sua palavra também deve ser favorável"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9495, 14, "Micaías, porém, disse: ""Juro pelo nome do Senhor, que direi o que o Senhor me mandar"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9496, 15, "Quando ele chegou, o rei lhe perguntou: ""Micaías, devemos ir à guerra contra Ramote-Gileade, ou não? "" Ele respondeu: ""Ataca, e serás vitorioso, pois o Senhor a entregará nas mãos do rei"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9497, 16, "O rei lhe disse: ""Quantas vezes devo fazer você jurar que irá me dizer somente a verdade em nome do Senhor? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9498, 17, "Então Micaías respondeu: ""Vi todo o Israel espalhado pelas colinas, como ovelhas sem pastor, e o Senhor dizer: ‘Estes não têm dono. Cada um volte para casa em paz’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9499, 18, "O rei de Israel disse a Josafá: ""Não lhe disse que ele nunca profetiza nada de bom a meu respeito, mas apenas coisas ruins? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9500, 19, "Micaías prosseguiu: ""Ouça a palavra do Senhor: Vi o Senhor assentado em seu trono, com todo o exército dos céus ao seu redor, à sua direita e à sua esquerda.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9501, 20, "E o Senhor disse: ‘Quem enganará Acabe para que ataque Ramote-Gileade e morra lá? ’ ""E um sugeria uma coisa, outro sugeria outra,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9502, 21, "até que, finalmente, um espírito colocou-se diante do Senhor e disse: ‘Eu o enganarei’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9503, 22, """ ‘De que maneira? ’, perguntou o Senhor. ""Ele respondeu: ‘Irei e serei um espírito mentiroso na boca de todos os profetas do rei’. ""Disse o Senhor: ‘Você conseguirá enganá-lo; vá e engane-o’.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9504, 23, """E o Senhor pôs um espírito mentiroso na boca destes seus profetas. O Senhor decretou a sua desgraça"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9505, 24, "Então Zedequias, filho de Quenaaná, aproximou-se, deu um tapa no rosto de Micaías e perguntou: ""Por qual caminho foi o espírito da parte do Senhor, quando saiu de mim para falar a você? """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9506, 25, "Micaías respondeu: ""Você descobrirá no dia em que estiver se escondendo de quarto em quarto"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9507, 26, "O rei então ordenou: ""Enviem Micaías de volta a Amom, o governador da cidade, e a Joás, filho do rei,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9508, 27, "e digam: ‘Assim diz o rei: Ponham este homem na prisão a pão e água, até que eu volte em segurança’ "".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9509, 28, "Micaías declarou: ""Se você de fato voltar em segurança, o Senhor não falou por meu intermédio"". E acrescentou: ""Ouçam o que estou dizendo, todos vocês! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9510, 29, "Então o rei de Israel e Josafá, rei de Judá, foram atacar Ramote-Gileade.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9511, 30, "E o rei de Israel disse a Josafá: ""Entrarei disfarçado em combate, mas tu, usa as tuas vestes reais"". O rei de Israel disfarçou-se, e ambos foram para o combate.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9512, 31, "O rei da Síria havia ordenado aos seus trinta e dois chefes de carros de guerra: ""Não lutem contra ninguém, seja soldado seja oficial, senão contra o rei de Israel"".", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9513, 32, "Quando os chefes dos carros viram Josafá, pensaram: ""É o rei de Israel"", e o cercaram para atacá-lo, mas Josafá gritou,", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9514, 33, "e quando os comandantes dos carros viram que não era o rei de Israel, deixaram de persegui-lo.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9515, 34, "De repente, um soldado disparou seu arco ao acaso, e atingiu o rei de Israel entre os encaixes da sua armadura. Então o rei disse ao condutor do seu carro: ""Tire-me do combate. Fui ferido! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9516, 35, "A batalha foi violenta durante todo o dia, e assim, o rei teve que enfrentar os arameus em pé no seu carro. O sangue de seu ferimento ficou escorrendo até o piso do carro de guerra, e ao cair da tarde, ele morreu.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9517, 36, "Quando o sol estava se pondo, propagou-se um grito por todo o exército: ""Cada homem para a sua cidade; cada um para a sua terra! """, 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9518, 37, "Assim o rei morreu e foi levado para Samaria, e ali o sepultaram.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9519, 38, "Lavaram o seu carro de guerra num açude em Samaria onde as prostitutas se banhavam, e os cães lamberam o seu sangue, conforme a palavra do Senhor havia declarado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9520, 39, "Os demais acontecimentos do reinado de Acabe, e tudo o que fez, o palácio que construiu com revestimento de marfim, e as cidades que fortificou, tudo está escrito nos registros históricos dos reis de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9521, 40, "Acabe descansou com os seus antepassados, e o seu filho Acazias foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9522, 41, "Josafá, filho de Asa, tornou-se rei de Judá no quarto ano do reinado de Acabe, rei de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9523, 42, "Josafá tinha trinta e cinco anos de idade quando se tornou rei, e reinou vinte e cinco anos em Jerusalém. O nome da sua mãe era Azuba, filha de Sili.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9524, 43, "Em tudo andou nos caminhos de seu pai Asa, e não se desviou deles; fez o que o Senhor aprova. Contudo, não acabou com os altares idólatras, nos quais o povo continuou a oferecer sacrifícios e a queimar incenso.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9525, 44, "Josafá teve paz com o rei de Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9526, 45, "Os demais acontecimentos do reinado de Josafá, as suas realizações e façanhas militares, tudo está escrito nos registros históricos dos reis de Judá.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9527, 46, "Ele livrou o país dos prostitutos cultuais que restaram depois do reinado do seu pai Asa.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9528, 47, "Ora, na época não havia rei em Edom, mas sim um governador nomeado.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9529, 48, "Josafá construiu uma frota de navios mercantes para buscar ouro em Ofir, mas nunca foram, pois eles naufragaram em Eziom-Geber.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9530, 49, "Naquela ocasião, Acazias, filho de Acabe, disse a Josafá: ""Os meus marinheiros poderão navegar com os teus"", mas Josafá recusou.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9531, 50, "Josafá descansou com seus antepassados e foi sepultado junto deles na cidade de Davi, seu pai. E o seu filho Jeorão foi o seu sucessor.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9532, 51, "Acazias, filho de Acabe, tornou-se rei de Israel em Samaria no décimo sétimo ano do reinado de Josafá, rei de Judá, e reinou dois anos sobre Israel.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9533, 52, "Fez o que o Senhor reprova, pois andou nos caminhos de seu pai e de sua mãe e nos caminhos de Jeroboão, filho de Nebate, que fez Israel pecar.", 10);
INSERT INTO verses (id, number, text, chapter_id) VALUES (9534, 53, "Prestou culto a Baal e adorou-o, e provocou a ira do Senhor, o Deus de Israel, como o seu pai tinha feito.", 10);