INSERT INTO verses (id, number, text, chapter_id) VALUES (30700, 1, "Revelação de Jesus Cristo, que Deus lhe deu para mostrar aos seus servos o que em breve há de acontecer. Ele enviou o seu anjo para torná-la conhecida ao seu servo João,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30701, 2, "que dá testemunho de tudo o que viu, isto é, a palavra de Deus e o testemunho de Jesus Cristo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30702, 3, "Feliz aquele que lê as palavras desta profecia e felizes aqueles que ouvem e guardam o que nela está escrito, porque o tempo está próximo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30703, 4, "João às sete igrejas da província da Ásia: A vocês, graça e paz da parte daquele que é, que era e que há de vir, dos sete espíritos que estão diante do seu trono,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30704, 5, "e de Jesus Cristo, que é a testemunha fiel, o primogênito dentre os mortos e o soberano dos reis da terra. Ele que nos ama e nos libertou dos nossos pecados por meio do seu sangue,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30705, 6, "e nos constituiu reino e sacerdotes para servir a seu Deus e Pai. A ele sejam glória e poder para todo o sempre! Amém.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30706, 7, "Eis que ele vem com as nuvens, e todo olho o verá, até mesmo aqueles que o traspassaram; e todos os povos da terra se lamentarão por causa dele. Assim será! Amém.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30707, 8, """Eu sou o Alfa e o Ômega"", diz o Senhor Deus, ""o que é, o que era e o que há de vir, o Todo-poderoso"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30708, 9, "Eu, João, irmão e companheiro de vocês no sofrimento, no Reino e na perseverança em Jesus, estava na ilha de Patmos, por causa da palavra de Deus e do testemunho de Jesus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30709, 10, "No dia do Senhor achei-me no Espírito e ouvi por trás de mim uma voz forte, como de trombeta,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30710, 11, "que dizia: ""Escreva num livro o que você vê e envie a estas sete igrejas: Éfeso, Esmirna, Pérgamo, Tiatira, Sardes, Filadélfia e Laodicéia"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30711, 12, "Voltei-me para ver quem falava comigo. Voltando-me, vi sete candelabros de ouro", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30712, 13, "e entre os candelabros alguém ""semelhante a um filho de homem"", com uma veste que chegava aos seus pés e um cinturão de ouro ao redor do peito.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30713, 14, "Sua cabeça e seus cabelos eram brancos como a lã, tão brancos quanto a neve, e seus olhos eram como chama de fogo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30714, 15, "Seus pés eram como o bronze numa fornalha ardente e sua voz como o som de muitas águas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30715, 16, "Tinha em sua mão direita sete estrelas, e da sua boca saía uma espada afiada de dois gumes. Sua face era como o sol quando brilha em todo o seu fulgor.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30716, 17, "Quando o vi, caí aos seus pés como morto. Então ele colocou sua mão direita sobre mim e disse: ""Não tenha medo. Eu sou o primeiro e o último.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30717, 18, "Sou aquele que vive. Estive morto mas agora estou vivo para todo o sempre! E tenho as chaves da morte e do Hades.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30718, 19, """Escreva, pois, as coisas que você viu, tanto as presentes como as que estão por vir.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30719, 20, "Este é o mistério das sete estrelas que você viu em minha mão direita e dos sete candelabros: as sete estrelas são os anjos das sete igrejas, e os sete candelabros são as sete igrejas"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30720, 1, "Ao anjo da igreja em Éfeso escreva: Estas são as palavras daquele que tem as sete estrelas em sua mão direita e anda entre os sete candelabros de ouro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30721, 2, "Conheço as suas obras, o seu trabalho árduo e a sua perseverança. Sei que você não pode tolerar homens maus, que pôs à prova os que dizem ser apóstolos mas não são, e descobriu que eles eram impostores.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30722, 3, "Você tem perseverado e suportado sofrimentos por causa do meu nome, e não tem desfalecido.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30723, 4, "Contra você, porém, tenho isto: você abandonou o seu primeiro amor.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30724, 5, "Lembre-se de onde caiu! Arrependa-se e pratique as obras que praticava no princípio. Se não se arrepender, virei a você e tirarei o seu candelabro do seu lugar.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30725, 6, "Mas há uma coisa a seu favor: você odeia as práticas dos nicolaítas, como eu também as odeio.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30726, 7, "Aquele que tem ouvidos ouça o que o Espírito diz às igrejas. Ao vencedor darei o direito de comer da árvore da vida, que está no paraíso de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30727, 8, "Ao anjo da igreja em Esmirna escreva: Estas são as palavras daquele que é o Primeiro e o Último, que morreu e tornou a viver.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30728, 9, "Conheço as suas aflições e a sua pobreza; mas você é rico! Conheço a blasfêmia dos que se dizem judeus mas não são, sendo antes sinagoga de Satanás.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30729, 10, "Não tenha medo do que você está prestes a sofrer. Saibam que o diabo lançará alguns de vocês na prisão para prová-los, e vocês sofrerão perseguição durante dez dias. Seja fiel até a morte, e eu lhe darei a coroa da vida.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30730, 11, "Aquele que tem ouvidos ouça o que o Espírito diz às igrejas. O vencedor de modo algum sofrerá a segunda morte.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30731, 12, "Ao anjo da igreja em Pérgamo escreva: Estas são as palavras daquele que tem a espada afiada de dois gumes.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30732, 13, "Sei onde você vive, onde está o trono de Satanás. Contudo, você permanece fiel ao meu nome e não renunciou à sua fé em mim, nem mesmo quando Antipas, minha fiel testemunha, foi morto nessa cidade, onde Satanás habita.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30733, 14, "No entanto, tenho contra você algumas coisas: você tem aí pessoas que se apegam aos ensinos de Balaão, que ensinou Balaque a armar ciladas contra os israelitas, induzindo-os a comer alimentos sacrificados a ídolos e a praticar imoralidade sexual.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30734, 15, "De igual modo você tem também os que se apegam aos ensinos dos nicolaítas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30735, 16, "Portanto, arrependa-se! Se não, virei em breve até você e lutarei contra eles com a espada da minha boca.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30736, 17, "Aquele que tem ouvidos ouça o que o Espírito diz às igrejas. Ao vencedor darei do maná escondido. Também lhe darei uma pedra branca com um novo nome nela inscrito, conhecido apenas por aquele que o recebe.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30737, 18, "Ao anjo da igreja em Tiatira escreva: Estas são as palavras do Filho de Deus, cujos olhos são como chama de fogo e os pés como bronze reluzente.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30738, 19, "Conheço as suas obras, o seu amor, a sua fé, o seu serviço e a sua perseverança, e sei que você está fazendo mais agora do que no princípio.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30739, 20, "No entanto, contra você tenho isto: você tolera Jezabel, aquela mulher que se diz profetisa. Com os seus ensinos, ela induz os meus servos à imoralidade sexual e a comerem alimentos sacrificados aos ídolos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30740, 21, "Dei-lhe tempo para que se arrependesse da sua imoralidade sexual, mas ela não quer se arrepender.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30741, 22, "Por isso, vou fazê-la adoecer e trarei grande sofrimento aos que cometem adultério com ela, a não ser que se arrependam das obras que ela pratica.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30742, 23, "Matarei os filhos dessa mulher. Então, todas as igrejas saberão que eu sou aquele que sonda mentes e corações, e retribuirei a cada um de vocês de acordo com as suas obras.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30743, 24, "Aos demais que estão em Tiatira, a vocês que não seguem a doutrina dela e não aprenderam, como eles dizem, os profundos segredos de Satanás, digo: não porei outra carga sobre vocês;", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30744, 25, "tão-somente apeguem-se com firmeza ao que vocês têm, até que eu venha.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30745, 26, "Àquele que vencer e fizer a minha vontade até o fim darei autoridade sobre as nações.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30746, 27, """Ele as governará com cetro de ferro e as despedaçará a um vaso de barro""", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30747, 28, "Eu lhes darei a mesma autoridade que recebi autoridade de meu Pai. Também lhe darei a estrela da manhã.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30748, 29, "Aquele que tem ouvidos ouça o que o Espírito diz às igrejas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30749, 1, "Ao anjo da igreja em Sardes escreva: Estas são as palavras daquele que tem os sete espíritos de Deus e as sete estrelas. Conheço as suas obras; você tem fama de estar vivo, mas está morto.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30750, 2, "Esteja atento! Fortaleça o que resta e que estava para morrer, pois não achei suas obras perfeitas aos olhos do meu Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30751, 3, "Lembre-se, portanto, do que você recebeu e ouviu; obedeça e arrependa-se. Mas se você não estiver atento, virei como um ladrão e você não saberá a que hora virei contra você.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30752, 4, "No entanto, você tem aí em Sardes uns poucos que não contaminaram as suas vestes. Eles andarão comigo, vestidos de branco, pois são dignos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30753, 5, "O vencedor será igualmente vestido de branco. Jamais apagarei o seu nome do livro da vida, mas o reconhecerei diante do meu Pai e dos seus anjos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30754, 6, "Aquele que tem ouvidos ouça o que o Espírito diz às igrejas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30755, 7, "Ao anjo da igreja em Filadélfia escreva: Estas são as palavras daquele que é santo e verdadeiro, que tem a chave de Davi. O que ele abre ninguém pode fechar, e o que ele fecha ninguém pode abrir.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30756, 8, "Conheço as suas obras. Eis que coloquei diante de você uma porta aberta que ninguém pode fechar. Sei que você tem pouca força, mas guardou a minha palavra e não negou o meu nome.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30757, 9, "Vejam o que farei com aqueles que são sinagoga de Satanás e que se dizem judeus e não são, mas são mentirosos. Farei que se prostrem aos seus pés e reconheçam que eu amei você.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30758, 10, "Visto que você guardou a minha palavra de exortação à perseverança, eu também o guardarei da hora da provação que está para vir sobre todo o mundo, para pôr à prova os que habitam na terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30759, 11, "Venho em breve! Retenha o que você tem, para que ninguém tome a sua coroa.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30760, 12, "Farei do vencedor uma coluna no santuário do meu Deus, e dali ele jamais sairá. Escreverei nele o nome do meu Deus e o nome da cidade do meu Deus, a nova Jerusalém, que desce do céu da parte de Deus; e também escreverei nele o meu novo nome.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30761, 13, "Aquele que tem ouvidos ouça o que o Espírito diz às igrejas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30762, 14, "Ao anjo da igreja em Laodicéia escreva: Estas são as palavras do Amém, a testemunha fiel e verdadeira, o soberano da criação de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30763, 15, "Conheço as suas obras, sei que você não é frio nem quente. Melhor seria que você fosse frio ou quente!", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30764, 16, "Assim, porque você é morno, nem frio nem quente, estou a ponto de vomitá-lo da minha boca.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30765, 17, "Você diz: Estou rico, adquiri riquezas e não preciso de nada. Não reconhece, porém, que é miserável, digno de compaixão, pobre, cego e que está nu.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30766, 18, "Dou-lhe este aconselho: Compre de mim ouro refinado no fogo e você se tornará rico; compre roupas brancas e vista-se para cobrir a sua vergonhosa nudez; e compre colírio para ungir os seus olhos e poder enxergar.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30767, 19, "Repreendo e disciplino aqueles que eu amo. Por isso, seja diligente e arrependa-se.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30768, 20, "Eis que estou à porta e bato. Se alguém ouvir a minha voz e abrir a porta, entrarei e cearei com ele, e ele comigo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30769, 21, "Ao vencedor darei o direito de sentar-se comigo em meu trono, assim como eu também venci e sentei-me com meu Pai em seu trono.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30770, 22, "Aquele que tem ouvidos ouça o que o Espírito diz às igrejas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30771, 1, "Depois dessas coisas olhei, e diante de mim estava uma porta aberta no céu. A voz que eu tinha ouvido no princípio, falando comigo como trombeta, disse: ""Suba para cá, e lhe mostrarei o que deve acontecer depois dessas coisas"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30772, 2, "Imediatamente me vi tomado pelo Espírito, e diante de mim estava um trono no céu e nele estava assentado alguém.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30773, 3, "Aquele que estava assentado era de aspecto semelhante a jaspe e sardônio. Um arco-íris, parecendo uma esmeralda, circundava o trono,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30774, 4, "ao redor do qual estavam outros vinte e quatro tronos, e assentados neles havia vinte e quatro anciãos. Eles estavam vestidos de branco e tinham na cabeça coroas de ouro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30775, 5, "Do trono saíam relâmpagos, vozes e trovões. Diante dele estavam acesas sete lâmpadas de fogo, que são os sete espíritos de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30776, 6, "Também diante do trono havia algo parecido com um mar de vidro, claro como cristal. No centro, ao redor do trono, havia quatro seres viventes cobertos de olhos, tanto na frente como atrás.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30777, 7, "O primeiro ser parecia um leão, o segundo parecia um boi, o terceiro tinha rosto como de homem, o quarto parecia uma águia quando em vôo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30778, 8, "Cada um deles tinha seis asas e era cheio de olhos, tanto ao redor como por baixo das asas. Dia e noite repetem sem cessar: ""Santo, santo, santo é o Senhor, o Deus todo-poderoso, que era, que é e que há de vir"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30779, 9, "Toda vez que os seres viventes dão glória, honra e graças àquele que está assentado no trono e que vive para todo o sempre,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30780, 10, "os vinte e quatro anciãos se prostram diante daquele que está assentado no trono e adoram aquele que vive para todo o sempre. Eles lançam as suas coroas diante do trono, dizem:", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30781, 11, """Tu, Senhor e Deus nosso, és digno de receber a glória, a honra e o poder, porque criaste todas as coisas, e por tua vontade elas existem e foram criadas"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30782, 1, "Então vi na mão direita daquele que está assentado no trono um livro em forma de rolo escrito de ambos os lados e selado com sete selos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30783, 2, "Vi um anjo poderoso, proclamando em alta voz: ""Quem é digno de romper os selos e de abrir o livro? """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30784, 3, "Mas não havia ninguém, nem no céu nem na terra nem debaixo da terra, que podia abrir o livro, ou sequer olhar para ele.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30785, 4, "Eu chorava muito, porque não se encontrou ninguém que fosse digno de abrir o livro e de olhar para ele.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30786, 5, "Então um dos anciãos me disse: ""Não chore! Eis que o Leão da tribo de Judá, a Raiz de Davi, venceu para abrir o livro e os seus sete selos"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30787, 6, "Então vi um Cordeiro, que parecia ter estado morto, de pé, no centro do trono, cercado pelos quatro seres viventes e pelos anciãos. Ele tinha sete chifres e sete olhos, que são os sete espíritos de Deus enviados a toda a terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30788, 7, "Ele se aproximou e recebeu o livro da mão direita daquele que estava assentado no trono.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30789, 8, "Ao recebê-lo, os quatro seres viventes e os vinte e quatro anciãos prostraram-se diante do Cordeiro. Cada um deles tinha uma harpa e taças de ouro cheias de incenso, que são as orações dos santos;", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30790, 9, "e eles cantavam um cântico novo: ""Tu és digno de receber o livro e de abrir os seus selos, pois foste morto, e com teu sangue compraste para Deus homens de toda tribo, língua, povo e nação.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30791, 10, "Tu os constituíste reino e sacerdotes para o nosso Deus, e eles reinarão sobre a terra"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30792, 11, "Então olhei e ouvi a voz de muitos anjos, milhares de milhares e milhões de milhões. Eles rodeavam o trono, bem como os seres viventes e os anciãos,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30793, 12, "e cantavam em alta voz: ""Digno é o Cordeiro que foi morto de receber poder, riqueza, sabedoria, força, honra, glória e louvor! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30794, 13, "Depois ouvi todas as criaturas existentes no céu, na terra, debaixo da terra e no mar, e tudo o que neles há, que diziam: ""Àquele que está assentado no trono e ao Cordeiro sejam o louvor, a honra, a glória e o poder, para todo o sempre! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30795, 14, "Os quatro seres viventes disseram: ""Amém"", e os anciãos prostraram-se e o adoraram.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30796, 1, "Observei quando o Cordeiro abriu o primeiro dos sete selos. Então ouvi um dos seres viventes dizer com voz de trovão: ""Venha! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30797, 2, "Olhei, e diante de mim estava um cavalo branco! Seu cavaleiro empunhava um arco, e foi-lhe dada uma coroa; ele cavalgava como vencedor determinado a vencer.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30798, 3, "Quando o Cordeiro abriu o segundo selo, ouvi o segundo ser vivente dizer: ""Venha! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30799, 4, "Então saiu outro cavalo; e este era vermelho. Seu cavaleiro recebeu poder para tirar a paz da terra e fazer que os homens se matassem uns aos outros. E lhe foi dada uma grande espada.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30800, 5, "Quando o Cordeiro abriu o terceiro selo, ouvi o terceiro ser vivente dizer: ""Venha! "" Olhei, e diante de mim estava um cavalo preto. Seu cavaleiro tinha na mão uma balança.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30801, 6, "Então ouvi o que parecia uma voz entre os quatro seres viventes, dizendo: ""Um quilo de trigo por um denário, e três quilos de cevada por um denário, e não danifique o azeite e o vinho! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30802, 7, "Quando o Cordeiro abriu o quarto selo, ouvi a voz do quarto ser vivente dizer: ""Venha! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30803, 8, "Olhei, e diante de mim estava um cavalo amarelo. Seu cavaleiro chamava-se Morte, e o Hades o seguia de perto. Foi-lhes dado poder sobre um quarto da terra para matar pela espada, pela fome, por pragas e por meio dos animais selvagens da terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30804, 9, "Quando ele abriu o quinto selo, vi debaixo do altar as almas daqueles que haviam sido mortos por causa da palavra de Deus e do testemunho que deram.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30805, 10, "Eles clamavam em alta voz: ""Até quando, ó Soberano santo e verdadeiro, esperarás para julgar os habitantes da terra e vingar o nosso sangue? """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30806, 11, "Então cada um deles recebeu uma veste branca, e foi-lhes dito que esperassem um pouco mais, até que se completasse o número dos seus conservos e irmãos, que deveriam ser mortos como eles.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30807, 12, "Observei quando ele abriu o sexto selo. Houve um grande terremoto. O sol ficou escuro como tecido de crina negra, toda a lua tornou-se vermelha como sangue,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30808, 13, "e as estrelas do céu caíram sobre a terra como figos verdes caem da figueira quando sacudidos por um vento forte.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30809, 14, "O céu foi se recolhendo como se enrola um pergaminho, e todas as montanhas e ilhas foram removidas de seus lugares.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30810, 15, "Então os reis da terra, os príncipes, os generais, os ricos, os poderosos — todos os homens, quer escravos, quer livres, esconderam-se em cavernas e entre as rochas das montanhas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30811, 16, "Eles gritavam às montanhas e às rochas: ""Caiam sobre nós e escondam-nos da face daquele que está assentado no trono e da ira do Cordeiro!", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30812, 17, "Pois chegou o grande dia da ira deles; e quem poderá suportar? """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30813, 1, "Depois disso vi quatro anjos de pé nos quatro cantos da terra, retendo os quatro ventos, para impedir que qualquer vento soprasse na terra, no mar ou em qualquer árvore.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30814, 2, "Então vi outro anjo subindo do Oriente, tendo o selo do Deus vivo. Ele bradou em alta voz aos quatro anjos a quem havia sido dado poder para danificar a terra e o mar:", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30815, 3, """Não danifiquem nem a terra, nem o mar nem as árvores, até que selemos as testas dos servos do nosso Deus"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30816, 4, "Então ouvi o número dos que foram selados: cento e quarenta e quatro mil, de todas as tribos de Israel.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30817, 5, "Da tribo de Judá foram selados doze mil, da tribo de Rúben, doze mil, da tribo de Gade, doze mil,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30818, 6, "da tribo de Aser, doze mil, da tribo de Naftali, doze mil, da tribo de Manassés, doze mil,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30819, 7, "da tribo de Simeão, doze mil, da tribo de Levi, doze mil, da tribo de Issacar, doze mil,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30820, 8, "da tribo de Zebulom, doze mil, da tribo de José, doze mil, da tribo de Benjamim, doze mil.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30821, 9, "Depois disso olhei, e diante de mim estava uma grande multidão que ninguém podia contar, de todas as nações, tribos, povos e línguas, de pé, diante do trono e do Cordeiro, com vestes brancas e segurando palmas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30822, 10, "E clamavam em alta voz: ""A salvação pertence ao nosso Deus, que se assenta no trono, e ao Cordeiro"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30823, 11, "Todos os anjos estavam de pé ao redor do trono, dos anciãos e dos quatro seres viventes. Eles se prostraram com o rosto em terra diante do trono e adoraram a Deus,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30824, 12, "dizendo: ""Amém! Louvor e glória, sabedoria, ação de graças, honra, poder e força sejam ao nosso Deus para todo o sempre. Amém! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30825, 13, "Então um dos anciãos me perguntou: ""Quem são estes que estão vestidos de branco, e de onde vieram? """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30826, 14, "Respondi: ""Senhor, tu o sabes"". E ele disse: ""Estes são os que vieram da grande tribulação e lavaram as suas vestes e as branquearam no sangue do Cordeiro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30827, 15, "Por isso, eles estão diante do trono de Deus e o servem dia e noite em seu santuário; e aquele que está assentado no trono estenderá sobre eles o seu tabernáculo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30828, 16, "Nunca mais terão fome, nunca mais terão sede. Não cairá sobre eles sol, e nenhum calor abrasador,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30829, 17, "pois o Cordeiro que está no centro do trono será o seu Pastor; ele os guiará às fontes de água viva. E Deus enxugará dos seus olhos toda lágrima"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30830, 1, "Quando ele abriu o sétimo selo, houve silêncio no céu por volta de meia hora.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30831, 2, "Vi os sete anjos que se acham em pé diante de Deus; a eles foram dadas sete trombetas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30832, 3, "Outro anjo, que trazia um incensário de ouro, aproximou-se e se colocou de pé junto ao altar. A ele foi dado muito incenso para oferecer com as orações de todos os santos sobre o altar de ouro diante do trono.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30833, 4, "E da mão do anjo subiu diante de Deus a fumaça do incenso juntamente com as orações dos santos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30834, 5, "Então o anjo pegou o incensário, encheu-o com fogo do altar e lançou-o sobre a terra; e houve trovões, vozes, relâmpagos e um terremoto.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30835, 6, "Então os sete anjos, que tinham as sete trombetas, prepararam-se para tocá-las.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30836, 7, "O primeiro anjo tocou a sua trombeta, e granizo e fogo misturado com sangue foram lançados sobre a terra. Foi queimado um terço da terra, um terço das árvores e toda a planta verde.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30837, 8, "O segundo anjo tocou a sua trombeta, e algo como um grande monte em chamas foi lançado ao mar. Um terço do mar transformou-se em sangue,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30838, 9, "morreu um terço das criaturas vivas do mar e foi destruído um terço das embarcações.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30839, 10, "O terceiro anjo tocou a sua trombeta, e caiu do céu uma grande estrela, queimando como tocha, sobre um terço dos rios e das fontes de águas;", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30840, 11, "o nome da estrela é Absinto. Tornou-se amargo um terço das águas, e muitos morreram pela ação das águas que se tornaram amargas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30841, 12, "O quarto anjo tocou a sua trombeta, e foi ferido um terço do sol, um terço da lua e um terço das estrelas, de forma que um terço deles escureceu. Um terço do dia ficou sem luz, e também um terço da noite.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30842, 13, "Enquanto eu olhava, ouvi uma águia que voava pelo meio do céu e dizia em alta voz: ""Ai, ai, ai dos que habitam na terra, por causa do toque das trombetas que está prestes a ser dado pelos três outros anjos! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30843, 1, "O quinto anjo tocou a sua trombeta, e vi uma estrela que havia caído do céu sobre a terra. À estrela foi dada a chave do poço do Abismo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30844, 2, "Quando ela abriu o Abismo, subiu dele fumaça como a de uma gigantesca fornalha. O sol e o céu escureceram com a fumaça que saía do abismo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30845, 3, "Da fumaça saíram gafanhotos que vieram sobre a terra, e lhes foi dado poder como o dos escorpiões da terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30846, 4, "Eles receberam ordens para não causar dano nem à relva da terra nem a qualquer planta ou árvore, mas apenas àqueles que não tinham o selo de Deus na testa.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30847, 5, "Não lhes foi dado poder para matá-los, mas sim para causar-lhes tormento durante cinco meses. A agonia que eles sofreram era como a da picada do escorpião.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30848, 6, "Naqueles dias os homens procurarão a morte, mas não a encontrarão; desejarão morrer, mas a morte fugirá deles.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30849, 7, "Os gafanhotos pareciam cavalos preparados para a batalha. Tinham sobre a cabeça algo como coroas de ouro, e o rosto deles parecia rosto humano.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30850, 8, "Os cabelos deles eram como os de mulheres e os dentes como os de leão.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30851, 9, "Tinham couraças como couraças de ferro, e o som das suas asas era como o barulho de muitos cavalos e carruagens correndo para a batalha.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30852, 10, "Tinham caudas e ferrões como de escorpiões, e na cauda tinham poder para causar tormento aos homens durante cinco meses.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30853, 11, "Tinham um rei sobre eles, o anjo do Abismo, cujo nome, em hebraico, é Abadom, e, em grego, Apoliom.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30854, 12, "O primeiro ai passou; dois outros ais ainda estão por vir.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30855, 13, "O sexto anjo tocou a sua trombeta, e ouvi uma voz que vinha das pontas do altar de ouro que está diante de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30856, 14, "Ela disse ao sexto anjo que tinha a trombeta: ""Solte os quatro anjos que estão amarrados junto ao grande rio Eufrates"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30857, 15, "Os quatro anjos, que estavam preparados para aquela hora, dia, mês e ano, foram soltos para matar um terço da humanidade.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30858, 16, "O número dos cavaleiros que compunham os exércitos era de duzentos milhões; eu ouvi o seu número.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30859, 17, "Os cavalos e os cavaleiros que vi em minha visão tinham este aspecto: as suas couraças eram vermelhas como o fogo, azul-escuras, e amarelas como o enxofre. A cabeça dos cavalos parecia a cabeça de um leão, e da boca lançavam fogo, fumaça e enxofre.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30860, 18, "Um terço da humanidade foi morto pelas três pragas de fogo, fumaça e enxofre que saíam das suas bocas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30861, 19, "O poder dos cavalos estava na boca e na cauda; pois as suas caudas eram como cobras; tinham cabeças com as quais feriam as pessoas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30862, 20, "O restante da humanidade que não morreu por essas pragas, nem assim se arrependeu das obras das suas mãos; eles não pararam de adorar os demônios e os ídolos de ouro, prata, bronze, pedra e madeira, ídolos que não podem ver nem ouvir nem andar.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30863, 21, "Também não se arrependeram dos seus assassinatos, das suas feitiçarias, da sua imoralidade sexual e dos seus roubos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30864, 1, "Então vi outro anjo poderoso, que descia do céu. Ele estava envolto numa nuvem, e havia um arco-íris acima de sua cabeça. Sua face era como o sol, e suas pernas eram como colunas de fogo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30865, 2, "E ele segurava um livrinho, que estava aberto em sua mão. Colocou o pé direito sobre o mar e o pé esquerdo sobre a terra,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30866, 3, "e deu um alto brado, como o rugido de um leão. Quando ele bradou, os sete trovões falaram.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30867, 4, "Logo que os sete trovões falaram, eu estava prestes a escrever, mas ouvi uma voz do céu, que disse: ""Sele o que disseram os sete trovões, e não o escreva"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30868, 5, "Então o anjo que eu tinha visto de pé sobre o mar e sobre a terra levantou a mão direita para o céu", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30869, 6, "e jurou por aquele que vive para todo o sempre, que criou os céus e tudo o que neles há, a terra e tudo o que nela há, e o mar e tudo o que nele há, dizendo: ""Não haverá mais demora!", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30870, 7, "Mas, nos dias em que o sétimo anjo estiver para tocar sua trombeta, vai se cumprir o mistério de Deus, da forma como ele o anunciou aos seus servos, os profetas"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30871, 8, "Depois falou comigo mais uma vez a voz que eu tinha ouvido falar do céu: ""Vá, pegue o livro aberto que está na mão do anjo que se encontra de pé sobre o mar e sobre a terra"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30872, 9, "Assim me aproximei do anjo e lhe pedi que me desse o livrinho. Ele me disse: ""Pegue-o e coma-o! Ele será amargo em seu estômago, mas em sua boca será doce como mel"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30873, 10, "Peguei o livrinho da mão do anjo e o comi. Ele me pareceu doce como mel em minha boca; mas, ao comê-lo, senti que o meu estômago ficou amargo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30874, 11, "Então me foi dito: ""É preciso que você profetize de novo acerca de muitos povos, nações, línguas e reis"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30875, 1, "Deram-me um caniço semelhante a uma vara de medir, e me foi dito: ""Vá e meça o templo de Deus e o altar, e conte os adoradores que lá estiverem.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30876, 2, "Exclua, porém, o pátio exterior; não o meça, pois ele foi dado aos gentios. Eles pisarão a cidade santa durante quarenta e dois meses.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30877, 3, "Darei poder às minhas duas testemunhas, e elas profetizarão durante mil duzentos e sessenta dias, vestidas de pano de saco"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30878, 4, "Estas são as duas oliveiras e os dois candelabros que permanecem diante do Senhor da terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30879, 5, "Se alguém quiser lhes causar dano, da boca deles sairá fogo que devorará os seus inimigos. É assim que deve morrer qualquer pessoa que quiser causar-lhes dano.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30880, 6, "Estes homens têm poder para fechar o céu, de modo que não chova durante o tempo em que estiverem profetizando, e têm poder para transformar a água em sangue e ferir a terra com toda sorte de pragas, quantas vezes desejarem.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30881, 7, "Quando eles tiverem terminado o seu testemunho, a besta que vem do Abismo os atacará. E irá vencê-los e matá-los.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30882, 8, "Os seus cadáveres ficarão expostos na rua principal da grande cidade, que figuradamente é chamada Sodoma e Egito, onde também foi crucificado o seu Senhor.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30883, 9, "Durante três dias e meio, homens de todos povos, tribos, línguas e nações contemplarão os seus cadáveres e não permitirão que sejam sepultados.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30884, 10, "Os habitantes da terra se alegrarão por causa deles e festejarão, enviando presentes uns aos outros, pois esses dois profetas haviam atormentado os que habitam na terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30885, 11, "Mas, depois dos três dias e meio, entrou neles um sopro de vida da parte de Deus, e eles ficaram de pé, e um grande terror tomou conta daqueles que os viram.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30886, 12, "Então eles ouviram uma forte voz do céu que lhes disse: ""Subam para cá"". E eles subiram para o céu numa nuvem, enquanto os seus inimigos olhavam.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30887, 13, "Naquela mesma hora houve um forte terremoto, e um décimo da cidade ruiu. Sete mil pessoas foram mortas no terremoto; os sobreviventes ficaram aterrorizados e deram glória ao Deus do céu.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30888, 14, "O segundo ai passou; o terceiro ai virá em breve.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30889, 15, "O sétimo anjo tocou a sua trombeta, e houve altas vozes no céu que diziam: ""O reino do mundo se tornou de nosso Senhor e do seu Cristo, e ele reinará para todo o sempre"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30890, 16, "Os vinte e quatro anciãos que estavam assentados em seus tronos diante de Deus prostraram-se sobre seus rostos e adoraram a Deus,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30891, 17, "dizendo: ""Graças te damos, Senhor Deus todo-poderoso, que és e que eras, porque assumiste o teu grande poder e começaste a reinar.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30892, 18, "As nações se iraram; e chegou a tua ira. Chegou o tempo de julgares os mortos e de recompensares os teus servos, os profetas, os teus santos e os que temem o teu nome, tanto pequenos como grandes, e de destruir os que destroem a terra"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30893, 19, "Então foi aberto o santuário de Deus no céu, e ali foi vista a arca da sua aliança. Houve relâmpagos, vozes, trovões, um terremoto e um grande temporal de granizo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30894, 1, "Apareceu no céu um sinal extraordinário: uma mulher vestida do sol, com a lua debaixo dos seus pés e uma coroa de doze estrelas sobre a cabeça.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30895, 2, "Ela estava grávida e gritava de dor, pois estava para dar à luz.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30896, 3, "Então apareceu no céu outro sinal: um enorme dragão vermelho com sete cabeças e dez chifres, tendo sobre as cabeças sete coroas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30897, 4, "Sua cauda arrastou consigo um terço das estrelas do céu, lançando-as na terra. O dragão colocou-se diante da mulher que estava para dar à luz, para devorar o seu filho no momento em que nascesse.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30898, 5, "Ela deu à luz um filho, um homem, que governará todas as nações com cetro de ferro. Seu filho foi arrebatado para junto de Deus e de seu trono.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30899, 6, "A mulher fugiu para o deserto, para um lugar que lhe havia sido preparado por Deus, para que ali a sustentassem durante mil duzentos e sessenta dias.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30900, 7, "Houve então uma guerra no céu. Miguel e seus anjos lutaram contra o dragão, e o dragão e os seus anjos revidaram.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30901, 8, "Mas estes não foram suficientemente fortes, e assim perderam o seu lugar no céu.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30902, 9, "O grande dragão foi lançado fora. Ele é a antiga serpente chamada diabo ou Satanás, que engana o mundo todo. Ele e os seus anjos foram lançado à terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30903, 10, "Então ouvi uma forte voz do céu que dizia: ""Agora veio a salvação, o poder e o Reino do nosso Deus, e a autoridade do seu Cristo, pois foi lançado fora o acusador dos nossos irmãos, que os acusa diante do nosso Deus, dia e noite.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30904, 11, "Eles o venceram pelo sangue do Cordeiro e pela palavra do testemunho que deram; diante da morte, não amaram a própria vida.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30905, 12, "Portanto, celebrem, ó céus, e os que neles habitam! Mas, ai da terra e do mar, pois o diabo desceu até vocês! Ele está cheio de fúria, pois sabe que lhe resta pouco tempo"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30906, 13, "Quando o dragão viu que havia sido lançado à terra, começou a perseguir a mulher que dera à luz o menino.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30907, 14, "Foram dadas à mulher as duas asas da grande águia, para que ela pudesse voar para o lugar que lhe havia sido preparado no deserto, onde seria sustentada durante um tempo, tempos e meio tempo, fora do alcance da serpente.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30908, 15, "Então a serpente fez jorrar da sua boca água como um rio, para alcançar a mulher e arrastá-la com a correnteza.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30909, 16, "A terra, porém, ajudou a mulher, abrindo a boca e engolindo o rio que o dragão fizera jorrar da sua boca.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30910, 17, "O dragão irou-se contra a mulher e saiu para guerrear contra o restante da sua descendência, os que obedecem aos mandamentos de Deus e se mantêm fiéis ao testemunho de Jesus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30911, 18, "Então o dragão se pôs em pé na areia do mar.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30912, 1, "Vi uma besta que saía do mar. Tinha dez chifres e sete cabeças, com dez coroas, uma sobre cada chifre, e em cada cabeça um nome de blasfêmia.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30913, 2, "A besta que vi era semelhante a um leopardo, mas tinha pés como os de urso e boca como a de leão. O dragão deu à besta o seu poder, o seu trono e grande autoridade.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30914, 3, "Uma das cabeças da besta parecia ter sofrido um ferimento mortal, mas o ferimento mortal foi curado. Todo o mundo ficou maravilhado e seguiu a besta.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30915, 4, "Adoraram o dragão, que tinha dado autoridade à besta, e também adoraram a besta, dizendo: ""Quem é como a besta? Quem pode guerrear contra ela? """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30916, 5, "À besta foi dada uma boca para falar palavras arrogantes e blasfemas, e lhe foi autoridade para agir durante quarenta e dois meses.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30917, 6, "Ela abriu a boca para blasfemar contra Deus e amaldiçoar o seu nome e o seu tabernáculo, os que habitam no céu.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30918, 7, "Foi-lhe dado poder para guerrear contra os santos e vencê-los. Foi-lhe dada autoridade sobre toda tribo, povo, língua e nação.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30919, 8, "Todos os habitantes da terra adorarão a besta, a saber, todos aqueles que não tiveram seus nomes escritos no livro da vida do Cordeiro que foi morto desde a criação do mundo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30920, 9, "Aquele que tem ouvidos ouça:", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30921, 10, "Se alguém há de ir para o cativeiro, para o cativeiro irá. Se alguém há de ser morto à espada, à espada haverá de ser morto. Aqui estão a perseverança e a fidelidade dos santos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30922, 11, "Então vi outra besta que saía da terra, com dois chifres como cordeiro, mas que falava como dragão.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30923, 12, "Exercia toda a autoridade da primeira besta, em nome dela, e fazia a terra e seus habitantes adorarem a primeira besta, cujo ferimento mortal havia sido curado.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30924, 13, "E realizava grandes sinais, chegando a fazer descer fogo do céu à terra, à vista dos homens.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30925, 14, "Por causa dos sinais que lhe foi permitido realizar em nome da primeira besta, ela enganou os habitantes da terra. Ordenou-lhes que fizessem uma imagem em honra da besta que fora ferida pela espada e contudo revivera.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30926, 15, "Foi-lhe dado poder para dar fôlego à imagem da primeira besta, de modo que ela podia falar e fazer que fossem mortos todos os que se recusassem a adorar a imagem.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30927, 16, "Também obrigou todos, pequenos e grandes, ricos e pobres, livres e escravos, a receberem certa marca na mão direita ou na testa,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30928, 17, "para que ninguém pudesse comprar nem vender, a não ser quem tivesse a marca, que é o nome da besta ou o número do seu nome.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30929, 18, "Aqui há sabedoria. Aquele que tem entendimento calcule o número da besta, pois é número de homem. Seu número é seiscentos e sessenta e seis.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30930, 1, "Então olhei, e diante de mim estava o Cordeiro, de pé sobre o monte Sião, e com ele cento e quarenta e quatro mil que traziam escritos na testa o nome dele e o nome de seu Pai.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30931, 2, "Ouvi um som do céu como o de muitas águas e de um forte trovão. Era como o de harpistas tocando suas harpas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30932, 3, "Eles cantavam um cântico novo diante do trono, dos quatro seres viventes e dos anciãos. Ninguém podia aprender o cântico, a não ser os cento e quarenta e quatro mil que haviam sido comprados da terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30933, 4, "Estes são os que não se contaminaram com mulheres, pois se conservaram castos e seguem o Cordeiro por onde quer que ele vá. Foram comprados dentre os homens e ofertados como primícias a Deus e ao Cordeiro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30934, 5, "Mentira nenhuma foi encontrada em suas bocas; são imaculados.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30935, 6, "Então vi outro anjo, que voava pelo céu e tinha na mão o evangelho eterno para proclamar aos que habitam na terra, a toda nação, tribo, língua e povo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30936, 7, "Ele disse em alta voz: ""Temam a Deus e glorifiquem-no, pois chegou a hora do seu juízo. Adorem aquele que fez os céus, a terra, o mar e as fontes das águas"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30937, 8, "Um segundo anjo o seguiu, dizendo: ""Caiu! Caiu a grande Babilônia que fez todas as nações beberem do vinho da fúria da sua prostituição! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30938, 9, "Um terceiro anjo os seguiu, dizendo em alta voz: ""Se alguém adorar a besta e a sua imagem e receber a sua marca na testa ou na mão,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30939, 10, "também beberá do vinho do furor de Deus que foi derramado sem mistura no cálice da sua ira. Será ainda atormentado com enxofre ardente na presença dos santos anjos e do Cordeiro,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30940, 11, "e a fumaça do tormento de tais pessoas sobe para todo o sempre. Para todos os que adoram a besta e a sua imagem, e para quem recebe a marca do seu nome, não há descanso, dia e noite"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30941, 12, "Aqui está a perseverança dos santos que obedecem aos mandamentos de Deus e permanecem fiéis a Jesus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30942, 13, "Então ouvi uma voz do céu dizendo: ""Escreva: Felizes os mortos que morrem no Senhor de agora em diante"". Diz o Espírito: ""Sim, eles descansarão das suas fadigas, pois as suas obras os seguirão"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30943, 14, "Olhei, e diante de mim estava uma nuvem branca e, assentado sobre a nuvem, alguém ""semelhante a um filho de homem"". Ele estava com uma coroa de ouro na cabeça e uma foice afiada na mão.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30944, 15, "Então saiu do santuário um outro anjo, que bradou em alta voz àquele que estava assentado sobre a nuvem: ""Tome a sua foice e faça a colheita, pois a safra da terra está madura; chegou a hora de colhê-la"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30945, 16, "Assim, aquele que estava assentado sobre a nuvem passou sua foice pela terra, e a terra foi ceifada.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30946, 17, "Outro anjo saiu do santuário do céu, trazendo também uma foice afiada.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30947, 18, "E ainda outro anjo, que tem autoridade sobre o fogo, saiu do altar e bradou em alta voz àquele que tinha a foice afiada: ""Tome sua foice afiada e ajunte os cachos de uva da videira da terra, porque as suas uvas estão maduras! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30948, 19, "O anjo passou a foice pela terra, ajuntou as uvas e as lançou no grande lagar da ira de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30949, 20, "Elas foram pisadas no lagar, fora da cidade, e correu sangue do lagar, chegando ao nível dos freios dos cavalos, numa distância de cerca de trezentos quilômetros.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30950, 1, "Vi no céu outro sinal, grande e maravilhoso: sete anjos com as sete últimas pragas, pois com elas se completa a ira de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30951, 2, "Vi algo semelhante a um mar de vidro misturado com fogo, e, de pé, junto ao mar, os que tinham vencido a besta, a sua imagem e o número do seu nome. Eles seguravam harpas que lhes haviam sido dadas por Deus,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30952, 3, "e cantavam o cântico de Moisés, servo de Deus, e o cântico do Cordeiro: ""Grandes e maravilhosas são as tuas obras, Senhor Deus todo-poderoso. Justos e verdadeiros são os teus caminhos, ó Rei das nações.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30953, 4, "Quem não te temerá, ó Senhor? Quem não glorificará o teu nome? Pois tu somente és santo. Todas as nações virão à tua presença e te adorarão, pois os teus atos de justiça se tornaram manifestos"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30954, 5, "Depois disso olhei, e vi que se abriu no céu o santuário, o tabernáculo da aliança.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30955, 6, "Saíram do santuário os sete anjos com as sete pragas. Eles estavam vestidos de linho puro e resplandecente, e tinham cinturões de ouro ao redor do peito.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30956, 7, "E um dos quatro seres viventes deu aos sete anjos sete taças de ouro cheias da ira de Deus, que vive para todo o sempre.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30957, 8, "O santuário ficou cheio da fumaça da glória de Deus e do seu poder, e ninguém podia entrar no santuário enquanto não se completassem as sete pragas dos sete anjos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30958, 1, "Então ouvi uma forte voz que vinha do santuário dizendo aos sete anjos: ""Vão derramar sobre a terra as sete taças da ira de Deus"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30959, 2, "O primeiro anjo foi e derramou a sua taça pela terra, e abriram-se feridas malignas e dolorosas naqueles que tinham a marca da besta e adoravam a sua imagem.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30960, 3, "O segundo anjo derramou a sua taça no mar, e este se transformou em sangue como de um morto, e morreu toda criatura que vivia no mar.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30961, 4, "O terceiro anjo derramou a sua taça nos rios e nas fontes de águas, e eles se transformaram em sangue.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30962, 5, "Então ouvi o anjo que tem autoridade sobre as águas dizer: ""Tu és justo, tu, o Santo, que és e que eras, porque julgaste estas coisas;", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30963, 6, "pois eles derramaram o sangue dos teus santos e dos teus profetas, e tu lhes deste sangue para beber, como eles merecem"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30964, 7, "E ouvi o altar responder: ""Sim, Senhor Deus todo-poderoso, verdadeiros e justos são os teus juízos"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30965, 8, "O quarto anjo derramou a sua taça no sol, e foi dado poder ao sol para queimar os homens com fogo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30966, 9, "Estes foram queimados pelo forte calor e amaldiçoaram o nome de Deus, que tem domínio sobre estas pragas; contudo se recusaram a se arrepender e a glorificá-lo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30967, 10, "O quinto anjo derramou a sua taça sobre o trono da besta, cujo reino ficou em trevas. De tanta agonia, os homens mordiam a própria língua,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30968, 11, "e blasfemavam contra o Deus do céu, por causa das suas dores e das suas feridas; contudo, recusaram-se a arrepender-se das obras que haviam praticado.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30969, 12, "O sexto anjo derramou a sua taça sobre o grande rio Eufrates, e secaram-se as suas águas para que fosse preparado o caminho para os reis que vêm do Oriente.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30970, 13, "Então vi saírem da boca do dragão, da boca da besta e da boca do falso profeta três espíritos imundos semelhantes a rãs.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30971, 14, "São espíritos de demônios que realizam sinais miraculosos; eles vão aos reis de todo o mundo, a fim de reuni-los para a batalha do grande dia do Deus todo-poderoso.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30972, 15, """Eis que venho como ladrão! Feliz aquele que permanece vigilante e conserva consigo as suas vestes, para que não ande nu e não seja vista a sua vergonha"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30973, 16, "Então os três espíritos os reuniram no lugar que, em hebraico, é chamado Armagedom.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30974, 17, "O sétimo anjo derramou a sua taça no ar, e do santuário saiu uma forte voz que vinha do trono, dizendo: ""Está feito! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30975, 18, "Houve, então, relâmpagos, vozes, trovões e um forte terremoto. Nunca havia ocorrido um terremoto tão forte como esse desde que o homem existe sobre a terra.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30976, 19, "A grande cidade foi fracionada em três partes, e as cidades das nações se desmoronaram. Deus lembrou-se da grande Babilônia e lhe deu o cálice do vinho do furor da sua ira.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30977, 20, "Todas as ilhas fugiram, e as montanhas desapareceram.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30978, 21, "Caíram sobre os homens, vindas do céu, enormes pedras de granizo, de cerca de trinta e cinco quilos cada; eles blasfemaram contra Deus por causa do granizo, pois a praga fora terrível.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30979, 1, "Um dos sete anjos que tinham as sete taças aproximou-se e me disse: ""Venha, eu lhe mostrarei o julgamento da grande prostituta que está sentada sobre muitas águas,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30980, 2, "com quem os reis da terra se prostituíram; os habitantes da terra se embriagaram com o vinho da sua prostituição"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30981, 3, "Então o anjo me levou no Espírito para um deserto. Ali vi uma mulher montada numa besta vermelha, que estava coberta de nomes blasfemos e que tinha sete cabeças e dez chifres.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30982, 4, "A mulher estava vestida de azul e vermelho, e adornada de ouro, pedras preciosas e pérolas. Segurava um cálice de ouro, cheio de coisas repugnantes e da impureza da sua prostituição.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30983, 5, "Em sua testa havia esta inscrição: MISTÉRIO: BABILÔNIA, A GRANDE; A MÃE DAS PROSTITUTAS E DAS PRÁTICAS REPUGNANTES DA TERRA.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30984, 6, "Vi que a mulher estava embriagada com o sangue dos santos, o sangue das testemunhas de Jesus. Quando a vi, fiquei muito admirado.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30985, 7, "Então o anjo me disse: ""Por que você está admirado? Eu lhe explicarei o mistério dessa mulher e da besta sobre a qual ela está montada, que tem sete cabeças e dez chifres.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30986, 8, "A besta que você viu, era e já não é. Ela está para subir do abismo e caminha para a perdição. Os habitantes da terra, cujos nomes não foram escritos no livro da vida desde a criação do mundo, ficarão admirados quando virem a besta, porque ela era, agora não é, e entretanto virá.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30987, 9, """Aqui se requer mente sábia. As sete cabeças são sete colinas sobre as quais está sentada a mulher.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30988, 10, "São também sete reis. Cinco já caíram, um ainda existe, e o outro ainda não surgiu; mas, quando surgir, deverá permanecer durante pouco tempo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30989, 11, "A besta que era, e agora não é, é o oitavo rei. É um dos sete, e caminha para a perdição.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30990, 12, """Os dez chifres que você viu são dez reis que ainda não receberam reino, mas que por uma hora receberão autoridade como reis, juntamente com a besta.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30991, 13, "Eles têm um único propósito, e darão seu poder e sua autoridade à besta.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30992, 14, "Guerrearão contra o Cordeiro, mas o Cordeiro os vencerá, pois é o Senhor dos senhores e o Rei dos reis; e vencerão com ele os seus chamados, escolhidos e fiéis"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30993, 15, "Então o anjo me disse: ""As águas que você viu, onde está sentada a prostituta, são povos, multidões, nações e línguas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30994, 16, "A besta e os dez chifres que você viu odiarão a prostituta. Eles a levarão à ruína e a deixarão nua, comerão a sua carne e a destruirão com fogo,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30995, 17, "pois Deus colocou no coração deles o desejo de realizar o propósito que ele tem, levando-os a concordarem em dar à besta o poder que eles têm para reinar até que se cumpram as palavras de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30996, 18, "A mulher que você viu é a grande cidade que reina sobre os reis da terra"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30997, 1, "Depois disso vi outro anjo que descia do céu. Tinha grande autoridade, e a terra foi iluminada por seu esplendor.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30998, 2, "E ele bradou com voz poderosa: ""Caiu! Caiu a grande Babilônia! Ela se tornou habitação de demônios e antro de todo espírito imundo antro de toda ave impura e detestável,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (30999, 3, "pois todas as nações beberam do vinho da fúria da sua prostituição. Os reis da terra se prostituíram com ela; à custa do seu luxo excessivo os negociantes da terra se enriqueceram"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31000, 4, "Então ouvi outra voz do céu que dizia: ""Saiam dela, vocês, povo meu, para que vocês não participem dos seus pecados, para que as pragas que vão cair sobre ela não os atinjam!", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31001, 5, "Pois os pecados da Babilônia acumularam-se até o céu, e Deus se lembrou dos seus crimes.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31002, 6, "Retribuam-lhe na mesma moeda; paguem-lhe em dobro pelo que fez; misturem para ela uma porção dupla no seu próprio cálice.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31003, 7, "Façam-lhe sofrer tanto tormento e tanta aflição como a glória e o luxo a que ela se entregou. Em seu coração ela se vangloriava: ‘Estou sentada como rainha; não sou viúva e jamais terei tristeza’.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31004, 8, "Por isso num só dia as suas pragas a alcançarão: morte, tristeza e fome, e o fogo a consumirá, pois poderoso é o Senhor Deus que a julga.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31005, 9, """Quando os reis da terra, que se prostituíram com ela e participaram do seu luxo, virem a fumaça do seu incêndio, chorarão e se lamentarão por ela.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31006, 10, "Amedrontados por causa do tormento dela, ficarão de longe e gritarão: ‘Ai! A grande cidade! Babilônia, cidade poderosa! Em apenas uma hora chegou a sua condenação! ’", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31007, 11, """Os negociantes da terra chorarão e se lamentarão por causa dela, porque ninguém mais compra a sua mercadoria:", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31008, 12, "artigos como ouro, prata, pedras preciosas e pérolas; linho fino, púrpura, seda e tecido vermelho; todo tipo de madeira de cedro e peças de marfim, madeira preciosa, bronze, ferro e mármore;", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31009, 13, "canela e outras especiarias, incenso, mirra e perfumes, vinho e azeite de oliva; farinha fina e trigo, bois e ovelhas, cavalos e carruagens, e corpos e almas de seres humanos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31010, 14, """Eles dirão: ‘Foram-se as frutas que tanto lhe apeteciam! Todas as suas riquezas e todo o seu esplendor se desvaneceram; nunca mais serão recuperados’.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31011, 15, "Os negociantes dessas coisas, que enriqueceram à custa dela, ficarão de longe, amedrontados com o tormento dela, e chorarão e se lamentarão,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31012, 16, "gritando: ‘Ai! A grande cidade, vestida de linho fino, de roupas de púrpura e vestes vermelhas, adornada de ouro, pedras preciosas e pérolas!", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31013, 17, "Em apenas uma hora, tamanha riqueza foi arruinada! ’ ""Todos os pilotos, todos os passageiros e marinheiros dos navios e todos os que ganham a vida no mar ficarão de longe.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31014, 18, "Ao verem a fumaça do incêndio dela, exclamarão: ‘Que outra cidade jamais se igualou a esta grande cidade? ’", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31015, 19, "Lançarão pó sobre a cabeça, e lamentando-se e chorando, gritarão: ‘Ai! A grande cidade! Graças à sua riqueza, nela prosperaram todos os que tinham navios no mar! Em apenas uma hora ela ficou em ruínas!", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31016, 20, "Celebre o que se deu com ela, ó céus! Celebrem, ó santos, apóstolos e profetas! Deus a julgou, retribuindo-lhe o que ela fez a vocês ’ "".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31017, 21, "Então um anjo poderoso levantou uma pedra do tamanho de uma grande pedra de moinho, lançou-a ao mar e disse: ""Com igual violência será lançada por terra a grande cidade da Babilônia, para nunca mais ser encontrada.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31018, 22, "Nunca mais se ouvirá em seu meio o som de harpistas, dos músicos, dos flautistas e dos tocadores de trombeta. Nunca mais se achará dentro de seus muros artífice algum, de qualquer profissão. Nunca mais se ouvirá em seu meio o ruído das pedras de moinho.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31019, 23, "Nunca mais brilhará dentro de seus muros a luz da candeia. Nunca mais se ouvirá ali a voz do noivo e da noiva. Seus mercadores eram os grandes do mundo. Todas as nações foram seduzidas por suas feitiçarias.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31020, 24, "Nela foi encontrado sangue de profetas e de santos, e de todos os que foram assassinados na terra"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31021, 1, "Depois disso ouvi no céu algo semelhante à voz de uma grande multidão, que exclamava: ""Aleluia! A salvação, a glória e o poder pertencem ao nosso Deus,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31022, 2, "pois verdadeiros e justos são os seus juízos. Ele condenou a grande prostituta que corrompia a terra com a sua prostituição. Ele cobrou dela o sangue dos seus servos"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31023, 3, "E mais uma vez a multidão exclamou: ""Aleluia! A fumaça que dela vem, sobe para todo o sempre"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31024, 4, "Os vinte e quatro anciãos e os quatro seres viventes prostraram-se e adoraram a Deus, que estava assentado no trono, e exclamaram: ""Amém, Aleluia! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31025, 5, "Então veio do trono uma voz, conclamando: ""Louvem o nosso Deus, todos vocês, seus servos, vocês que o temem, tanto pequenos como grandes! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31026, 6, "Então ouvi algo semelhante ao som de uma grande multidão, como o estrondo de muitas águas e fortes trovões, que bradava: ""Aleluia! pois reina o Senhor, o nosso Deus, o Todo-poderoso.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31027, 7, "Regozijemo-nos! Vamos nos alegrar e dar-lhe glória! Pois chegou a hora do casamento do Cordeiro, e a sua noiva já se aprontou.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31028, 8, "Foi-lhe dado para vestir-se linho fino, brilhante e puro"". O linho fino são os atos justos dos santos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31029, 9, "E o anjo me disse: ""Escreva: Felizes os convidados para o banquete do casamento do Cordeiro! "" E acrescentou: ""Estas são as palavras verdadeiras de Deus"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31030, 10, "Então caí aos seus pés para adorá-lo, mas ele me disse: ""Não faça isso! Sou servo como você e como os seus irmãos que se mantêm fiéis ao testemunho de Jesus. Adore a Deus! O testemunho de Jesus é o espírito de profecia"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31031, 11, "Vi o céu aberto e diante de mim um cavalo branco, cujo cavaleiro se chama Fiel e Verdadeiro. Ele julga e guerreia com justiça.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31032, 12, "Seus olhos são como chamas de fogo, e em sua cabeça há muitas coroas e um nome que só ele conhece, e ninguém mais.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31033, 13, "Está vestido com um manto tingido de sangue, e o seu nome é Palavra de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31034, 14, "Os exércitos do céu o seguiam, vestidos de linho fino, branco e puro, e montados em cavalos brancos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31035, 15, "De sua boca sai uma espada afiada, com a qual ferirá as nações. ""Ele as governará com cetro de ferro"". Ele pisa o lagar do vinho do furor da ira do Deus todo-poderoso.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31036, 16, "Em seu manto e em sua coxa está escrito este nome: REI DOS REIS E SENHOR DOS SENHORES.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31037, 17, "Vi um anjo que estava de pé no sol e que clamava em alta voz a todas as aves que voavam pelo meio do céu: ""Venham, reúnam-se para o grande banquete de Deus,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31038, 18, "para comerem carne de reis, generais e poderosos, carne de cavalos e seus cavaleiros, carne de todos: livres e escravos, pequenos e grandes"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31039, 19, "Então vi a besta, os reis da terra e os seus exércitos reunidos para guerrearem contra aquele que está montado no cavalo e contra o seu exército.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31040, 20, "Mas a besta foi presa, e com ela o falso profeta que havia realizado os sinais miraculosos em nome dela, com os quais ele havia enganado os que receberam a marca da besta e adoraram a imagem dela. Os dois foram lançados vivos no lago de fogo que arde com enxofre.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31041, 21, "Os demais foram mortos com a espada que saía da boca daquele que está montado no cavalo. E todas as aves se fartaram com a carne deles.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31042, 1, "Vi descer do céu um anjo que trazia na mão a chave do abismo e uma grande corrente.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31043, 2, "Ele prendeu o dragão, a antiga serpente, que é o diabo, Satanás, e o acorrentou por mil anos;", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31044, 3, "lançou-o no abismo, fechou-o e pôs um selo sobre ele, para assim impedi-lo de enganar as nações até que terminassem os mil anos. Depois disso, é necessário que ele seja solto por um pouco de tempo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31045, 4, "Vi tronos em que se assentaram aqueles a quem havia sido dada autoridade para julgar. Vi as almas dos que foram decapitados por causa do testemunho de Jesus e da palavra de Deus. Eles não tinham adorado a besta nem a sua imagem, e não tinham recebido a sua marca na testa nem nas mãos. Eles ressuscitaram e reinaram com Cristo durante mil anos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31046, 5, "( O restante dos mortos não voltou a viver até se completarem os mil anos. ) Esta é a primeira ressurreição.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31047, 6, "Felizes e santos os que participam da primeira ressurreição! A segunda morte não tem poder sobre eles; serão sacerdotes de Deus e de Cristo, e reinarão com ele durante mil anos.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31048, 7, "Quando terminarem os mil anos, Satanás será solto da sua prisão", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31049, 8, "e sairá para enganar as nações que estão nos quatro cantos da terra, Gogue e Magogue, a fim de reuni-las para a batalha. Seu número é como a areia do mar.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31050, 9, "As nações marcharam por toda a superfície da terra e cercaram o acampamento dos santos, a cidade amada; mas um fogo desceu do céu e as devorou.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31051, 10, "O diabo, que as enganava, foi lançado no lago de fogo que arde com enxofre, onde já haviam sido lançados a besta e o falso profeta. Eles serão atormentados dia e noite, para todo o sempre.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31052, 11, "Depois vi um grande trono branco e aquele que nele estava assentado. A terra e o céu fugiram da sua presença, e não se encontrou lugar para eles.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31053, 12, "Vi também os mortos, grandes e pequenos, de pé diante do trono, e livros foram abertos. Outro livro foi aberto, o livro da vida. Os mortos foram julgados de acordo com o que tinham feito, segundo o que estava registrado nos livros.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31054, 13, "O mar entregou os mortos que nele havia, e a morte e o Hades entregaram os mortos que neles havia; e cada um foi julgado de acordo com o que tinha feito.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31055, 14, "Então a morte e o Hades foram lançados no lago de fogo. O lago de fogo é a segunda morte.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31056, 15, "Se o nome de alguém não foi encontrado no livro da vida, este foi lançado no lago de fogo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31057, 1, "Então vi um novo céu e uma nova terra, pois o primeiro céu e a primeira terra tinham passado; e o mar já não existia.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31058, 2, "Vi a cidade santa, a nova Jerusalém, que descia do céu, da parte de Deus, preparada como uma noiva adornada para o seu marido.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31059, 3, "Ouvi uma forte voz que vinha do trono e dizia: ""Agora o tabernáculo de Deus está com os homens, com os quais ele viverá. Eles serão os seus povos; o próprio Deus estará com eles e será o seu Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31060, 4, "Ele enxugará dos seus olhos toda lágrima. Não haverá mais morte, nem tristeza, nem choro, nem dor, pois a antiga ordem já passou"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31061, 5, "Aquele que estava assentado no trono disse: ""Estou fazendo novas todas as coisas! "" E acrescentou: ""Escreva isto, pois estas palavras são verdadeiras e dignas de confiança"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31062, 6, "Disse-me ainda: ""Está feito. Eu sou o Alfa e o Ômega, o Princípio e o Fim. A quem tiver sede, darei de beber gratuitamente da fonte da água da vida.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31063, 7, "O vencedor herdará tudo isto, e eu serei seu Deus e ele será meu filho.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31064, 8, "Mas os covardes, os incrédulos, os depravados, os assassinos, os que cometem imoralidade sexual, os que praticam feitiçaria, os idólatras e todos os mentirosos — o lugar deles será no lago de fogo que arde com enxofre. Esta é a segunda morte"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31065, 9, "Um dos sete anjos que tinham as sete taças cheias das últimas sete pragas aproximou-se e me disse: ""Venha, eu lhe mostrarei a noiva, a esposa do Cordeiro"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31066, 10, "Ele me levou no Espírito a um grande e alto monte e mostrou-me a Cidade Santa, Jerusalém, que descia do céu, da parte de Deus.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31067, 11, "Ela resplandecia com a glória de Deus, e o seu brilho era como o de uma jóia muito preciosa, como jaspe, clara como cristal.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31068, 12, "Tinha uma grande e alta muralha com doze portas e doze anjos junto às portas. Nas portas estavam escritos os nomes das doze tribos de Israel.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31069, 13, "Havia três portas ao oriente, três ao norte, três ao sul e três ao ocidente.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31070, 14, "A muralha da cidade tinha doze fundamentos, e neles estavam os nomes dos doze apóstolos do Cordeiro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31071, 15, "O anjo que falava comigo tinha como medida uma vara feita de ouro, para medir a cidade, suas portas e seus muros.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31072, 16, "A cidade era quadrangular, de comprimento e largura iguais. Ele mediu a cidade com a vara; tinha dois mil e duzentos quilômetros de comprimento; a largura e a altura eram iguais ao comprimento.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31073, 17, "Ele mediu a muralha, e deu sessenta e cinco metros de espessura, segundo a medida humana que o anjo estava usando.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31074, 18, "A muralha era feita de jaspe e a cidade de ouro puro, semelhante ao vidro puro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31075, 19, "Os fundamentos das muros da cidade eram ornamentados com toda sorte de pedras preciosas. O primeiro fundamento era ornamentado com jaspe; o segundo com safira; o terceiro com calcedônia; o quarto com esmeralda;", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31076, 20, "o quinto com sardônio; o sexto com sárdio; o sétimo com crisólito; o oitavo com berilo; o nono com topázio; o décimo com crisópraso; o décimo primeiro com jacinto; e o décimo segundo com ametista.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31077, 21, "As doze portas eram doze pérolas, cada porta feita de uma única pérola. A rua principal da cidade era de ouro puro, como vidro transparente.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31078, 22, "Não vi templo algum na cidade, pois o Senhor Deus todo-poderoso e o Cordeiro são o seu templo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31079, 23, "A cidade não precisa de sol nem de lua para brilharem sobre ela, pois a glória de Deus a ilumina, e o Cordeiro é a sua candeia.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31080, 24, "As nações andarão em sua luz, e os reis da terra lhe trarão a sua glória.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31081, 25, "Suas portas jamais se fecharão de dia, pois ali não haverá noite.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31082, 26, "A glória e a honra das nações lhe serão trazidas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31083, 27, "Nela jamais entrará algo impuro, nem ninguém que pratique o que é vergonhoso ou enganoso, mas unicamente aqueles cujos nomes estão escritos no livro da vida do Cordeiro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31084, 1, "Então o anjo me mostrou o rio da água da vida que, claro como cristal, fluía do trono de Deus e do Cordeiro,", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31085, 2, "no meio da rua principal da cidade. De cada lado do rio estava a árvore da vida, que dá doze colheitas, dando fruto todos os meses. As folhas da árvore servem para a cura das nações.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31086, 3, "Já não haverá maldição nenhuma. O trono de Deus e do Cordeiro estará na cidade, e os seus servos o servirão.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31087, 4, "Eles verão a sua face, e o seu nome estará em suas testas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31088, 5, "Não haverá mais noite. Eles não precisarão de luz de candeia nem da luz do sol, pois o Senhor Deus os iluminará; e eles reinarão para todo o sempre.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31089, 6, "O anjo me disse: ""Estas palavras são dignas de confiança e verdadeiras. O Senhor, o Deus dos espíritos dos profetas, enviou o seu anjo para mostrar aos seus servos as coisas que em breve hão de acontecer"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31090, 7, """Eis que venho em breve! Feliz é aquele que guarda as palavras da profecia deste livro"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31091, 8, "Eu, João, sou aquele que ouviu e viu estas coisas. Tendo-as ouvido e visto, caí aos pés do anjo que me mostrou tudo aquilo para mim, para adorá-lo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31092, 9, "Mas ele me disse: ""Não faça isso! Sou servo como você e seus irmãos, os profetas, e como os que guardam as palavras deste livro. Adore a Deus! """, 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31093, 10, "Então me disse: ""Não sele as palavras da profecia deste livro, pois o tempo está próximo.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31094, 11, "Continue o injusto a praticar injustiça; continue o imundo na imundícia; continue o justo a praticar justiça; e continue o santo a santificar-se"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31095, 12, """Eis que venho em breve! A minha recompensa está comigo, e eu retribuirei a cada um de acordo com o que fez.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31096, 13, "Eu sou o Alfa e o Ômega, o Primeiro e o Último, o Princípio e o Fim.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31097, 14, """Felizes os que lavam as suas vestes, para que tenham direito à árvore da vida e possam entrar na cidade pelas portas.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31098, 15, "Fora ficam os cães, os que praticam feitiçaria, os que cometem imoralidades sexuais, os assassinos, os idólatras e todos os que amam e praticam a mentira.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31099, 16, """Eu, Jesus, enviei o meu anjo para dar a vocês este testemunho concernente às igrejas. Eu sou a Raiz e o Descendente de Davi, e a resplandecente Estrela da Manhã"".", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31100, 17, "O Espírito e a noiva dizem: ""Vem! "" E todo aquele que ouvir diga: ""Vem! "" Quem tiver sede, venha; e quem quiser, beba de graça da água da vida.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31101, 18, "Declaro a todos os que ouvem as palavras da profecia deste livro: se alguém lhe acrescentar algo, Deus lhe acrescentará as pragas descritas neste livro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31102, 19, "Se alguém tirar alguma palavra deste livro de profecia, Deus tirará dele a sua parte na árvore da vida e na cidade santa, que são descritas neste livro.", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31103, 20, "Aquele que dá testemunho destas coisas diz: ""Sim, venho em breve! "" Amém. Vem, Senhor Jesus!", 65);
INSERT INTO verses (id, number, text, chapter_id) VALUES (31104, 21, "A graça do Senhor Jesus seja com todos. Amém.", 65);