INSERT INTO verses (id, number, text, chapter_id) VALUES (22293, 1, "A palavra do Senhor que veio a Joel, filho de Petuel.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22294, 2, "Ouçam isto, anciãos; escutem, todos os habitantes do país. Já aconteceu algo assim nos seus dias? Ou nos dias dos seus antepassados?", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22295, 3, "Contem o que aconteceu aos seus filhos, e eles aos seus netos, e os seus netos, à geração seguinte.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22296, 4, "O que o gafanhoto cortador deixou o gafanhoto peregrino comeu; o que o gafanhoto peregrino deixou o gafanhoto devastador comeu; o que o gafanhoto devastador deixou o gafanhoto devorador comeu.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22297, 5, "Acordem, bêbados, e chorem! Lamentem todos vocês, bebedores de vinho; gritem por causa do vinho novo, pois ele foi tirado dos seus lábios.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22298, 6, "Uma nação invadiu a minha terra, poderosa e inumerável; seus dentes são dentes de leão, suas presas são de leoa.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22299, 7, "Arrasou as minhas videiras e arruinou as minhas figueiras. Arrancou-lhes a casca, e derrubou-as, deixando brancos os seus galhos.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22300, 8, "Pranteiem como uma virgem em vestes de luto que lamenta pelo noivo da sua mocidade.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22301, 9, "As ofertas de cereal e as ofertas derramadas foram eliminadas do templo do Senhor. Os sacerdotes, que ministram diante do Senhor, estão de luto.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22302, 10, "Os campos estão arruinados, a terra está seca; o trigo está destruído, o vinho novo acabou, o azeite está em falta.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22303, 11, "Desesperem-se, agricultores, chorem, produtores de vinho; fiquem aflitos pelo trigo e pela cevada, porque a colheita foi destruída.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22304, 12, "A vinha está seca, e a figueira murchou; a romãzeira, a palmeira e a macieira, todas as árvores do campo secaram. Secou-se, mais ainda, a alegria dos homens.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22305, 13, "Ponham vestes de luto, ó sacerdotes, e pranteiem; chorem alto, vocês que ministram perante o altar. Venham, passem a noite vestidos de luto, vocês que ministram perante o meu Deus; pois as ofertas de cereal e as ofertas derramadas foram suprimidas do templo do seu Deus.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22306, 14, "Decretem um jejum santo; convoquem uma assembléia sagrada. Reúnam as autoridades e todos os habitantes do país no templo do Senhor, do seu Deus, e clamem ao Senhor.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22307, 15, "Ah! Aquele dia! Sim, o dia do Senhor está próximo; como destruição poderosa da parte do Todo-poderoso, ele virá.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22308, 16, "Não é verdade que a comida foi eliminada diante dos nossos próprios olhos, e que a alegria e a satisfação foram suprimidas, do templo do nosso Deus?", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22309, 17, "As sementes estão murchas debaixo dos torrões de terra. Os celeiros estão em ruínas, os depósitos de cereal foram derrubados, pois a colheita se perdeu.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22310, 18, "Como está mugindo o gado! As manadas andam agitadas porque não têm pasto; até os rebanhos de ovelhas estão sendo castigados.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22311, 19, "A ti, Senhor, eu clamo, pois o fogo devorou as pastagens e as chamas consumiram todas as árvores do campo.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22312, 20, "Até os animais do campo clamam a ti, pois os canais de água se secaram e o fogo devorou as pastagens.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22313, 1, "Toquem a trombeta em Sião; dêem o alarme no meu santo monte. Tremam todos os habitantes do país, pois o dia do Senhor está chegando. Está próximo!", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22314, 2, "É dia de trevas e de escuridão, dia de nuvens e negridão. Assim como a luz da aurora estende-se pelos montes, um grande e poderoso exército se aproxima, como nunca antes se viu nem jamais se verá nas gerações futuras.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22315, 3, "Diante deles o fogo devora, atrás deles arde uma chama. Diante deles a terra é como o jardim do Éden, atrás deles, um deserto arrasado; nada lhes escapa.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22316, 4, "Eles têm a aparência de cavalos; como cavalaria, atacam galopando.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22317, 5, "Com um barulho semelhante ao de carros saltam sobre os cumes dos montes, como um fogo crepitante que consome o restolho, como um exército poderoso em posição de combate.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22318, 6, "Diante deles povos se contorcem angustiados; todos os rostos ficam pálidos de medo.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22319, 7, "Eles atacam como guerreiros; escalam muralhas como soldados. Todos eles marcham em linha, sem desviar-se do curso.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22320, 8, "Não se empurram uns aos outros; cada um marcha sempre em frente. Avançam por entre os dardos sem desfazer a formação.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22321, 9, "Lançam-se sobre a cidade; correm ao longo da muralha. Sobem nas casas; como ladrões entram pelas janelas.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22322, 10, "Diante deles a terra treme, o céu estremece, o sol e a lua escurecem, e as estrelas param de brilhar.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22323, 11, "O Senhor levanta a sua voz à frente do seu exército; Como é grande o seu exército! Como são poderosos os que obedecem à sua ordem! Como é grande o dia do Senhor! Como será terrível! Quem poderá suportá-lo?", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22324, 12, """Agora, porém"", declara o Senhor, ""voltem-se para mim de todo o coração, com jejum, lamento e pranto. """, 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22325, 13, "Rasguem o coração, e não as vestes. Voltem-se para o Senhor, para o seu Deus, pois ele é misericordioso e compassivo, muito paciente e cheio de amor; arrepende-se, e não envia a desgraça.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22326, 14, "Talvez ele volte atrás, arrependa-se, e ao passar deixe uma bênção. Assim vocês poderão fazer ofertas de cereal e ofertas derramadas para o Senhor, o seu Deus.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22327, 15, "Toquem a trombeta em Sião, decretem jejum santo, convoquem uma assembléia sagrada.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22328, 16, "Reúnam o povo, consagrem a assembléia; ajuntem os anciãos, reúnam as crianças, aquelas que mamam no peito. Até os recém-casados devem deixar os seus aposentos.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22329, 17, "Que os sacerdotes, que ministram perante o Senhor, chorem entre o pórtico do templo e o altar, orando: ""Poupa o teu povo, Senhor. Não faças da tua herança motivo de zombaria e de piada entre as nações. Porque se haveria de dizer entre os povos: ‘Onde está o Deus deles? ’ """, 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22330, 18, "Então o Senhor mostrou zelo por sua terra e teve piedade do seu povo.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22331, 19, "O Senhor respondeu ao seu povo: ""Estou lhes enviando trigo, vinho novo e azeite, o suficiente para satisfazê-los plenamente; nunca mais farei de vocês motivo de zombaria para as nações.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22332, 20, """Levarei o invasor que vem do norte para longe de vocês, empurrando-o para uma terra seca e estéril, a vanguarda para o mar oriental e a retaguarda para o mar ocidental. E a sua podridão subirá; o seu mau cheiro se espalhará"". Ele tem feito coisas grandiosas!", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22333, 21, "Não tenha medo, ó terra; regozije-se e alegre-se. O Senhor tem feito coisas grandiosas!", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22334, 22, "Não tenham medo, animais do campo, pois as pastagens estão ficando verdes. As árvores estão dando os seus frutos; a figueira e a videira ficam carregadas.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22335, 23, "Ó povo de Sião, alegre-se e regozije-se no Senhor, no seu Deus, pois ele lhe dá as chuvas de outono, conforme a sua justiça. Ele lhe envia muitas chuvas, as de outono e as de primavera, como antes fazia.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22336, 24, "As eiras ficarão cheias de trigo; os tonéis transbordarão de vinho novo e de azeite.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22337, 25, """Vou compensá-los pelos anos de colheitas que os gafanhotos destruíram: o gafanhoto peregrino, o gafanhoto devastador, o gafanhoto devorador e o gafanhoto cortador, o meu grande exército que enviei contra vocês.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22338, 26, "Vocês comerão até ficarem satisfeitos, e louvarão o nome do Senhor, o seu Deus, que fez maravilhas em favor de vocês; nunca mais o meu povo será humilhado.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22339, 27, "Então vocês saberão que eu estou no meio de Israel. Eu sou o Senhor, o seu Deus, e não há nenhum outro; nunca mais o meu povo será humilhado.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22340, 28, """E, depois disso, derramarei do meu Espírito sobre todos os povos. Os seus filhos e as suas filhas profetizarão, os velhos terão sonhos, os jovens terão visões.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22341, 29, "Até sobre os servos e as servas derramarei do meu Espírito naqueles dias.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22342, 30, "Mostrarei maravilhas no céu e na terra, sangue, fogo e nuvens de fumaça.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22343, 31, "O sol se tornará em trevas, e a lua em sangue; antes que venha o grande e terrível dia do Senhor.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22344, 32, "E todo aquele que invocar o nome do Senhor será salvo, pois, conforme prometeu o Senhor, no monte Sião e em Jerusalém haverá livramento para os sobreviventes, para aqueles a quem o Senhor chamar.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22345, 1, """Sim, naqueles dias e naquele tempo, quando eu restaurar a sorte de Judá e de Jerusalém,", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22346, 2, "reunirei todos os povos e os farei descer ao vale de Josafá. Ali os julgarei por causa da minha herança, Israel, o meu povo pois espalharam o meu povo entre as nações e repartiram entre si a minha terra.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22347, 3, "Tiraram sortes sobre o meu povo e deram meninos em troca de prostitutas; venderam meninas por vinho, para se embriagarem.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22348, 4, """Que é que vocês têm contra mim, Tiro, Sidom, e todas as regiões da Filístia? Vocês estão me retribuindo por algo que eu lhes fiz? Se estão querendo vingar-se de mim, com agilidade e rapidez me vingarei do que vocês têm feito.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22349, 5, "Pois roubaram a minha prata e o meu ouro e levaram para os seus templos os meus tesouros mais valiosos.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22350, 6, "Vocês venderam o povo de Judá e de Jerusalém aos gregos, mandando-os para longe da sua terra natal.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22351, 7, """Vou tirá-los dos lugares para onde os venderam, e sobre vocês farei cair o que fizeram:", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22352, 8, "Venderei os filhos e as filhas de vocês ao povo de Judá, e eles os venderão à distante nação dos sabeus"". Assim disse o Senhor.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22353, 9, "Proclamem isto entre as nações: Preparem-se para a guerra! Despertem os guerreiros! Todos os homens de guerra aproximem-se e ataquem.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22354, 10, "Forjem os seus arados, fazendo deles espadas; e de suas foices, façam lanças. Diga o fraco: ""Sou um guerreiro! """, 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22355, 11, "Venham depressa, vocês, nações vizinhas, e reúnam-se ali. Faze descer os teus guerreiros, ó Senhor!", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22356, 12, """Despertem, nações; avancem para o vale de Josafá, pois ali me sentarei para julgar todas as nações vizinhas.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22357, 13, "Lancem a foice, pois a colheita está madura. Venham, pisem com força as uvas, pois o lagar está cheio e os tonéis transbordam, tão grande é a maldade dessas nações! """, 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22358, 14, "Multidões, multidões no vale da Decisão! Pois o dia do Senhor está próximo, no vale da Decisão.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22359, 15, "O sol e a lua escurecerão, e as estrelas já não brilharão.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22360, 16, "O Senhor rugirá de Sião e de Jerusalém levantará a sua voz; a terra e o céu tremerão. Mas o Senhor será um refúgio para o seu povo, uma fortaleza para Israel.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22361, 17, """Então vocês saberão que eu sou o Senhor, o seu Deus, que habito em Sião, o meu santo monte. Jerusalém será santa; e estrangeiros jamais a conquistarão"".", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22362, 18, """Naquele dia os montes gotejarão vinho novo; das colinas manará leite; todos os ribeiros de Judá terão água corrente. Uma fonte fluirá do templo do Senhor e regará o vale das Acácias.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22363, 19, "Mas o Egito ficará desolado, Edom será um deserto arrasado, por causa da violência feita ao povo de Judá, em cuja terra derramaram sangue inocente.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22364, 20, "Judá será habitada para sempre e Jerusalém por todas as gerações.", 28);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22365, 21, "Sua culpa de sangue, ainda não perdoada, eu a perdoarei. "" O Senhor habita em Sião!", 28);