INSERT INTO verses (id, number, text, chapter_id) VALUES (18948, 1, "As palavras de Jeremias, filho de Hilquias, um dos sacerdotes de Anatote, no território de Benjamim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18949, 2, "A palavra do Senhor veio a ele no décimo terceiro ano do reinado de Josias, filho de Amom, rei de Judá,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18950, 3, "e durante o reinado de Jeoaquim, filho de Josias, rei de Judá, até o quinto mês do décimo primeiro ano de Zedequias, filho de Josias, rei de Judá, quando os habitantes de Jerusalém foram levados para o exílio.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18951, 4, "A palavra do Senhor veio a mim, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18952, 5, """Antes de formá-lo no ventre eu o escolhi; antes de você nascer, eu o separei e o designei profeta às nações"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18953, 6, "Mas eu disse: ""Ah, Soberano Senhor! Eu não sei falar, pois ainda sou muito jovem"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18954, 7, "O Senhor, porém, me disse: ""Não diga que é muito jovem. A todos a quem eu o enviar você irá e dirá tudo o que eu lhe ordenar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18955, 8, "Não tenha medo deles, pois eu estou com você para protegê-lo"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18956, 9, "O Senhor estendeu a mão, tocou a minha boca e disse-me: ""Agora ponho em sua boca as minhas palavras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18957, 10, "Veja! Eu hoje dou a você autoridade sobre nações e reinos, para arrancar, despedaçar, arruinar e destruir; para edificar e para plantar"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18958, 11, "E a palavra do Senhor veio a mim: ""O que você vê, Jeremias? "" ""Vejo o ramo de uma amendoeira"", respondi.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18959, 12, "O Senhor me disse: ""Você viu bem, pois estou vigiando para que a minha palavra se cumpra"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18960, 13, "A palavra do Senhor veio a mim pela segunda vez, dizendo: ""O que você vê? "" E eu respondi: ""Vejo uma panela fervendo; ela está inclinada do norte para cá"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18961, 14, "O Senhor me disse: ""Do norte se derramará a desgraça sobre todos os habitantes desta terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18962, 15, "Estou convocando todos os povos dos reinos do norte"", diz o Senhor. ""Cada um virá e colocará o seu trono diante das portas de Jerusalém, virão contra todas as muralhas que a cercam e contra todas as cidades de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18963, 16, "Pronunciarei a minha sentença contra o meu povo por todas as suas maldades; porque me abandonaram, queimaram incenso a outros deuses, e adoraram deuses que as suas mãos fizeram"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18964, 17, """E você, prepare-se! Vá dizer-lhes tudo o que eu ordenar. Não fique aterrorizado por causa deles, senão eu o aterrorizarei diante deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18965, 18, "E hoje eu faço de você uma cidade fortificada, uma coluna de ferro e um muro de bronze, contra toda a terra: contra os reis de Judá, seus oficiais, seus sacerdotes e o povo da terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18966, 19, "Eles lutarão contra você, mas não o vencerão, pois eu estou com você e o protegerei"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18967, 1, "A palavra do Senhor veio a mim:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18968, 2, """Vá proclamar aos ouvidos de Jerusalém: ""Eu me lembro de sua fidelidade quando você era jovem: como noiva, você me amava e me seguia pelo deserto, por uma terra não semeada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18969, 3, "Israel era santo para o Senhor, os primeiros frutos de sua colheita; todos os que o devoravam eram considerados culpados, e a desgraça os alcançava"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18970, 4, "Ouçam a palavra do Senhor, ó comunidade de Jacó, todos os clãs da comunidade de Israel.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18971, 5, "Assim diz o Senhor: ""Que falta os seus antepassados encontraram em mim, para que me deixassem e se afastassem de mim? Eles seguiram ídolos sem valor, tornando-se eles próprios sem valor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18972, 6, "Eles não perguntaram: ‘Onde está o Senhor, que nos trouxe do Egito e nos conduziu pelo deserto, por uma terra árida e cheia de covas, terra de seca e de trevas, terra pela qual ninguém passa e onde ninguém vive? ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18973, 7, "Eu trouxe vocês a uma terra fértil, para que comessem dos seus frutos e dos seus bons produtos. Entretanto, vocês contaminaram a minha terra; tornaram a minha herança repugnante.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18974, 8, "Os sacerdotes não perguntaram pelo Senhor; os intérpretes da lei não me conheciam, e os líderes do povo se rebelaram contra mim. Os profetas profetizavam em nome de Baal, seguindo deuses inúteis.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18975, 9, """Por isso, eu ainda faço denúncias contra vocês"", diz o Senhor, ""e farei denúncias contra os seus descendentes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18976, 10, "Atravessem o mar até o litoral de Chipre e vejam; mandem observadores a Quedar e reparem de perto; e vejam se alguma vez aconteceu algo assim:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18977, 11, "Alguma nação já trocou os seus deuses? E eles nem sequer são deuses! Mas o meu povo trocou a sua Glória por deuses inúteis.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18978, 12, "Espantem-se diante disso, ó céus! Fiquem horrorizados e abismados"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18979, 13, """O meu povo cometeu dois crimes: eles me abandonaram, a mim, a fonte de água viva; e cavaram as suas próprias cisternas, cisternas rachadas que não retêm água.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18980, 14, "Acaso é Israel escravo, escravo de nascimento? Por que foi então que se tornou presa", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18981, 15, "de leões que rugem e urram contra ela? Arrasaram a sua terra, queimaram as suas cidades e as deixaram desabitadas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18982, 16, "Até mesmo os homens de Mênfis e de Tafnes raparam o seu crânio.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18983, 17, "Não foi você mesma a responsável pelo que lhe aconteceu, ao abandonar o Senhor, o seu Deus?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18984, 18, "Agora, por que você vai ao Egito para beber água do Nilo? E por que vai à Assíria para beber água do Eufrates?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18985, 19, "O seu crime a castigará e a sua rebelião a repreenderá. Compreenda e veja como é mau e amargo abandonar o Senhor, o seu Deus, e não ter temor de mim"", diz o Soberano, o Senhor dos Exércitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18986, 20, """Há muito tempo, eu quebrei o seu jugo e despedacei as correias que a prendiam. Mas você disse: ""Eu não servirei! "" Ao contrário, em todo monte elevado e debaixo de toda árvore verdejante, você se deitava como uma prostituta.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18987, 21, "Eu a plantei como uma videira seleta, de semente absolutamente pura. Como, então, contra mim você se tornou uma videira degenerada e selvagem?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18988, 22, "Mesmo que você se lave com soda e com muito sabão, a mancha da sua iniqüidade permanecerá diante de mim"", diz o Soberano Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18989, 23, """Como você pode dizer que não está contaminada e que não correu atrás dos baalins? Reveja o seu procedimento no vale e considere o que você tem feito. Você é como uma camela jovem e arisca que corre para todos os lados;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18990, 24, "como uma jumenta selvagem habituada ao deserto, cheirando o vento em seu desejo. Quem é capaz de controlá-la quando está no cio? Os machos que a procuram não precisam se cansar, porque logo encontrarão a que está no mês do cio.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18991, 25, "Não deixe que os seus pés se esfolem nem que a sua garganta fique seca. Mas você disse: ‘Não adianta! Eu amo os deuses estrangeiros, e continuarei a ir atrás deles’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18992, 26, """Assim como o ladrão fica envergonhado quando é apanhado em flagrante, também a comunidade de Israel ficará envergonhada: seus reis e oficiais, seus sacerdotes e profetas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18993, 27, "Pois dizem à madeira: ‘Você é meu pai’ e à pedra: ‘Você me deu à luz’. Voltaram para mim as costas e não o rosto, mas na hora da adversidade dizem: ‘Vem salvar-nos! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18994, 28, "E onde estão os deuses que você fabricou para si? Que eles venham, se puderem salvá-la na hora da adversidade! Porque os seus deuses são tão numerosos como as suas cidades, ó Judá!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18995, 29, """Por que vocês fazem denúncias contra mim? Todos vocês se rebelaram contra mim"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18996, 30, """De nada adiantou castigar o seu povo, eles não aceitaram a correção. A sua espada tem destruído os seus profetas como um leão devorador.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18997, 31, """Vocês, desta geração, considerem a palavra do Senhor: ""Tenho sido um deserto para Israel? Uma terra de grandes trevas? Por que o meu povo diz: ‘Nós assumimos o controle! Não mais viremos a ti’?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18998, 32, "Será que uma jovem se esquece das suas jóias, ou uma noiva, de seus enfeites nupciais? Contudo, o meu povo esqueceu-se de mim, por dias sem fim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (18999, 33, "Com quanta habilidade você busca o amor! Mesmo as mulheres da pior espécie aprenderam com o seu procedimento.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19000, 34, "Nas suas roupas encontrou-se o sangue de pobres inocentes, os quais não foram flagrados arrombando casas. Contudo, apesar de tudo isso,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19001, 35, "você diz: ‘Sou inocente; ele não está irado comigo’. Mas eu passarei sentença contra você porque você disse que não pecou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19002, 36, "Por que você não leva a sério a sua mudança de rumo? Você ficará decepcionada com o Egito, como ficou com a Assíria.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19003, 37, "Você também deixará aquele lugar, com as mãos na cabeça, pois o Senhor rejeitou aqueles em quem você confia; você não receberá a ajuda deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19004, 1, """Se um homem se divorciar de sua mulher, e, se ela, depois de deixá-lo, casar-se com outro homem, poderá o primeiro marido voltar para ela? Não seria a terra totalmente contaminada? Mas você tem se prostituído com muitos amantes e, agora, quer voltar para mim? "", pergunta o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19005, 2, """Olhe para o campo e veja: Há algum lugar onde você não foi desonrada? À beira do caminho você se assentou à espera de amantes, assentou-se como um nômade no deserto. Você contaminou a terra com sua prostituição e impiedade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19006, 3, "Por isso as chuvas foram retidas, e não veio chuva na primavera. Mas você, apresentando-se declaradamente como prostituta, recusa-se a corar de vergonha.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19007, 4, "Você não acabou de me chamar: ‘Meu pai, amigo da minha juventude,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19008, 5, "ficarás irado para sempre? Teu ressentimento permanecerá até o fim? ’ É assim que você fala, mas faz todo o mal que pode. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19009, 6, "Durante o reinado do rei Josias, o Senhor me disse: ""Você viu o que fez Israel, a infiel? Subiu todo monte elevado e foi para debaixo de toda árvore verdejante para prostituir-se.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19010, 7, "Depois de ter feito tudo isso, pensei que ela voltaria para mim, mas não voltou. E a sua irmã traidora, Judá, viu essas coisas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19011, 8, "Viu também que dei à infiel Israel uma certidão de divórcio e a mandei embora, por causa de todos os seus adultérios. Entretanto, a sua irmã Judá, a traidora, e também se prostituiu, sem temor algum.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19012, 9, "E por ter feito pouco caso da imoralidade, Judá contaminou a terra, cometendo adultério com ídolos de pedra e madeira.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19013, 10, "Apesar de tudo isso, sua irmã Judá, a traidora, não voltou para mim todo o coração, mas sim com fingimento"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19014, 11, "O Senhor me disse: ""Israel, a infiel, é melhor do que Judá, a traidora.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19015, 12, "Vá e proclame esta mensagem para os lados do norte: "" ‘Volte, ó infiel Israel’, declara o Senhor, ‘Não mais franzirei a testa cheio de ira contra vocês, pois eu sou fiel’, declara o Senhor, ‘Não ficarei irado para sempre.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19016, 13, "Mas reconheça o seu pecado: você se rebelou contra o Senhor, o seu Deus, e ofereceu os seus favores a deuses estranhos, debaixo de toda árvore verdejante, e não me obedeceu’ "", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19017, 14, """Voltem, filhos rebeldes! Pois eu sou o senhor de vocês"", declara o Senhor. ""Tomarei vocês, um de cada cidade e dois de cada clã, e os trarei de volta a Sião.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19018, 15, "Então eu lhes darei governantes conforme a minha vontade, que os dirigirão com sabedoria e com entendimento.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19019, 16, "Quando vocês aumentarem e se multiplicarem na sua terra naqueles dias"", declara o Senhor, ""não dirão mais: ‘A arca da aliança do Senhor’. Não pensarão mais nisso nem se lembrarão dela; não sentirão sua falta nem será feita outra arca.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19020, 17, "Naquela época, chamarão Jerusalém ‘O Trono do Senhor’, e todas as nações se reunirão para honrar o nome do Senhor em Jerusalém. Não mais viverão segundo a obstinação de seus corações para fazer o mal.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19021, 18, "Naqueles dias a comunidade de Judá caminhará com a comunidade de Israel, e juntas voltarão do norte para a terra que dei como herança aos seus antepassados. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19022, 19, """Eu mesmo disse: ""Com que alegria eu a trataria como se tratam filhos e lhe daria uma terra aprazível, a mais bela herança entre as nações. Pensei que você me chamaria de ‘Pai’ e que não deixaria de seguir-me.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19023, 20, "Mas, como a mulher que trai o marido, assim vocês têm sido infiéis comigo, ó comunidade de Israel"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19024, 21, "Ouve-se um choro no campo, o pranto de súplica dos israelitas, porque perverteram os seus caminhos e esqueceram o Senhor, o seu Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19025, 22, """Voltem, filhos rebeldes! Eu os curarei da sua rebeldia. ""Sim! Nós viremos a ti, pois tu és o Senhor, o nosso Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19026, 23, "De fato, a agitação idólatra nas colinas e o murmúrio nos montes é um engano. No Senhor, no nosso Deus, está a salvação de Israel.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19027, 24, "Desde a nossa juventude, Baal, o deus da vergonha, tem consumido o fruto do trabalho dos nossos antepassados: as ovelhas, os bois, os seus filhos e as suas filhas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19028, 25, "Seja a vergonha a nossa cama e a desonra, o nosso cobertor. Pecamos contra o Senhor, o nosso Deus, tanto nós como os nossos antepassados, desde a nossa juventude até ao dia de hoje; e não temos obedecido ao Senhor, ao nosso Deus. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19029, 1, """Se você voltar, ó Israel, volte para mim"", diz o Senhor. ""Se você afastar para longe de minha vista os seus ídolos detestáveis, e não se desviar,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19030, 2, "se você jurar pelo nome do Senhor, com fidelidade, justiça e retidão, então as nações serão por ele abençoadas e nele se gloriarão. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19031, 3, "Assim diz o Senhor ao povo de Judá e de Jerusalém: ""Lavrem seus campos não arados e não semeiem entre espinhos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19032, 4, "Purifiquem-se para o Senhor, sejam fiéis à aliança, homens de Judá e habitantes de Jerusalém! Se não fizerem isso, a minha ira se acenderá e queimará como fogo, por causa do mal que vocês fizeram; queimará e ninguém conseguirá apagá-la.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19033, 5, """Anunciem em Judá! Proclamem em Jerusalém: ‘Toquem a trombeta por toda esta terra! ’ Gritem bem alto e digam: ‘Reúnam-se! Fujamos para as cidades fortificadas! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19034, 6, "Ergam o sinal indicando Sião. Fujam sem demora em busca de abrigo! Porque do Norte eu estou trazendo desgraça, uma grande destruição"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19035, 7, "Um leão saiu da sua toca, um destruidor de nações se pôs a caminho. Ele saiu de onde vive para arrasar a sua terra. Suas cidades ficarão em ruínas e sem habitantes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19036, 8, "Por isso, ponham vestes de lamento, chorem e gritem, pois o fogo da ira do Senhor não se desviou de nós.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19037, 9, """Naquele dia"", diz o Senhor, ""o rei e os seus oficiais perderão a coragem, os sacerdotes ficarão horrorizados e os profetas, perplexos. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19038, 10, "Então eu disse: ""Ah, Soberano Senhor, como enganaste completamente este povo e a Jerusalém dizendo: ‘Vocês terão paz’, quando a espada está em nossa garganta"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19039, 11, "Naquela época será dito a este povo e a Jerusalém: ""Um vento escaldante, que vem das dunas do deserto, sopra na direção da minha filha, do meu povo, mas não para peneirar nem para limpar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19040, 12, "É um vento forte demais, que vem da minha parte. Agora eu pronunciarei as minhas sentenças contra eles"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19041, 13, "Vejam! Ele avança como as nuvens; os seus carros de guerra são como um furacão e os seus cavalos são mais velozes do que as águias. Ai de nós! Estamos perdidos!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19042, 14, "Ó Jerusalém, lave o mal do seu coração para que você seja salva. Até quando você vai acolher projetos malignos no íntimo?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19043, 15, "Ouve-se uma voz proclamando desde Dã, desde os montes de Efraim se anuncia calamidade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19044, 16, """Relatem isso a esta nação e proclamem contra Jerusalém: ‘Um exército inimigo está vindo de uma terra distante, dando seu grito de guerra contra as cidades de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19045, 17, "Eles a cercam como homens que guardam um campo, pois ela se rebelou contra mim’ "", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19046, 18, """A sua própria conduta e as suas ações trouxeram isso sobre você. Como é amargo este seu castigo! Ele atinge até o seu coração! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19047, 19, "Ah, minha angústia, minha angústia! Eu me contorço de dor. Ó paredes do meu coração! O meu coração dispara dentro de mim; não posso ficar calado. Ouvi o som da trombeta, ouvi o grito de guerra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19048, 20, "Um desastre depois do outro; toda a minha terra foi devastada. Num instante as minhas tendas foram destruídas, e os meus abrigos num momento.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19049, 21, "Até quando verei o sinal levantado e ouvirei o som da trombeta?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19050, 22, """O meu povo é tolo, eles não me conhecem. São crianças insensatas que nada compreendem. São hábeis para praticar o mal, mas não sabem fazer o bem. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19051, 23, "Olhei para a terra e ela era sem forma e vazia; e para os céus, e a sua luz tinha desaparecido.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19052, 24, "Olhei para os montes e eles estavam tremendo; todas as colinas estavam oscilando.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19053, 25, "Olhei, e não havia mais gente; todas as aves do céu tinham fugido em revoada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19054, 26, "Olhei, e a terra fértil era deserto; todas as suas cidades estavam em ruínas por causa do Senhor, por causa do fogo da sua ira.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19055, 27, "Assim diz o Senhor: ""Toda esta terra ficará devastada, embora eu não vá destruí-la completamente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19056, 28, "Por causa disso, a terra ficará de luto e o céu, em cima, se escurecerá; porque eu falei, e não me arrependi, decidi, e não voltei atrás"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19057, 29, "Quando se ouvem os cavaleiros e os flecheiros, todos os habitantes da cidade fogem. Alguns vão para o meio dos arbustos; outros escalam as rochas. Todas as cidades são abandonadas, e ficam sem habitantes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19058, 30, "O que você está fazendo, ó cidade devastada? Por que se veste de vermelho e se enfeita com jóias de ouro? Por que você passa sombra nos olhos? Você se embeleza em vão, pois os seus amantes a desprezam e querem tirar-lhe a vida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19059, 31, "Ouvi um grito, como de mulher em trabalho de parto, como a agonia de uma mulher ao dar à luz o primeiro filho. É o grito da cidade de Sião, que está ofegante e estende as mãos, dizendo: ""Ai de mim! Estou desfalecendo. Minha vida está nas mãos de assassinos! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19060, 1, """Percorram as ruas de Jerusalém, olhem e observem. Procurem em suas praças para ver se podem encontrar alguém que aja com honestidade e que busque a verdade. Então eu perdoarei a cidade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19061, 2, "Embora digam: ‘Juro pelo nome do Senhor’, ainda assim estão jurando falsamente. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19062, 3, "Senhor, não é fidelidade que os teus olhos procuram? Tu os feriste, mas eles nada sentiram; tu os deixaste esgotados, mas eles recusaram a correção. Endureceram o rosto, mais que a rocha e recusaram arrepender-se.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19063, 4, "Pensei: Esses são apenas pobres e ignorantes, não conhecem o caminho do Senhor, as exigências do seu Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19064, 5, "Irei aos nobres e falarei com eles, pois, sem dúvida, eles conhecem o caminho do Senhor, as exigências do seu Deus. Mas todos eles também quebraram o jugo e romperam as amarras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19065, 6, "Por isso, um leão da floresta os atacará, um lobo da estepe os arrasará, um leopardo ficará à espreita, nos arredores das suas cidades, para despedaçar qualquer pessoa que delas sair. Porque a rebeldia deles é grande e muitos são os seus desvios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19066, 7, """Por que deveria eu perdoar-lhe isso? Seus filhos me abandonaram e juraram por aqueles que não são deuses. Embora eu tenha suprido as suas necessidades, eles cometeram adultério e freqüentaram as casas de prostituição.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19067, 8, "Eles são garanhões bem-alimentados e excitados, cada um relinchando para a mulher do próximo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19068, 9, "Não devo eu castigá-los por isso? "", pergunta o Senhor. ""Não devo eu vingar-me de uma nação como esta?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19069, 10, """Vão por entre as suas vinhas e destruam-nas, mas não acabem totalmente com elas. Cortem os seus ramos, pois eles não pertencem ao Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19070, 11, "Porque a comunidade de Israel e a comunidade de Judá têm-me traído"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19071, 12, "Mentiram acerca do Senhor, dizendo: ""Ele não vai fazer nada! Nenhum mal nos acontecerá; jamais veremos espada ou fome.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19072, 13, "Os profetas não passam de vento, e a palavra não está neles; por isso aconteça com eles o que dizem"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19073, 14, "Portanto, assim diz o Senhor dos Exércitos: ""Porque falaram essas palavras, farei com que as minhas palavras em sua boca sejam fogo, e este povo seja a lenha que o fogo consome.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19074, 15, """Ó comunidade de Israel"", declara o Senhor. ""estou trazendo de longe uma nação para atacá-la: uma nação muito antiga e invencível, uma nação cuja língua você não conhece e cuja fala você não entende.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19075, 16, "Sua aljava é como um túmulo aberto; toda ela é composta de guerreiros.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19076, 17, "Devorarão as suas colheitas e os seus alimentos; devorarão os seus filhos e as suas filhas; devorarão as suas ovelhas e os seus bois; devorarão as suas videiras e as suas figueiras. Destruirão pela espada as cidades fortificadas nas quais vocês confiam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19077, 18, """Contudo, mesmo naqueles dias não os destruirei completamente"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19078, 19, """E, quando perguntarem: ‘Por que o Senhor, o nosso Deus, fez isso conosco? ’, você lhes dirá: ‘Assim como vocês me abandonaram e serviram deuses estrangeiros em sua própria terra, também agora vocês servirão estrangeiros numa terra que não é de vocês’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19079, 20, """Anunciem isto à comunidade de Jacó e proclamem-no em Judá:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19080, 21, "Ouçam isto, vocês, povo tolo e insensato, que têm olhos mas não vêem, têm ouvidos mas não ouvem:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19081, 22, "Acaso vocês não me temem? "", pergunta o Senhor. ""Não tremem diante da minha presença? Porque fui eu que fiz da areia um limite para o mar, um decreto eterno que ele não pode ultrapassar. As ondas podem quebrar, mas não podem prevalecer, podem bramir, mas não podem ultrapassá-lo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19082, 23, "Mas este povo tem coração obstinado e rebelde; eles se afastaram e foram embora.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19083, 24, "Não dizem no seu íntimo: ‘Temamos o Senhor, o nosso Deus: aquele que dá as chuvas do outono e da primavera no tempo certo, e assegura-nos as semanas certas da colheita’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19084, 25, "Mas os pecados de vocês têm afastado essas coisas; as faltas de vocês os têm privado desses bens.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19085, 26, """Há ímpios no meio do meu povo: homens que ficam à espreita como num esconderijo de caçadores de pássaros; preparam armadilhas para capturar gente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19086, 27, "Suas casas estão cheias de engano, como gaiolas cheias de pássaros. E assim eles se tornaram poderosos e ricos,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19087, 28, "estão gordos e bem-alimentados. Não há limites para as suas obras más. Não se empenham pela causa do órfão, nem defendem os direitos do pobre.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19088, 29, "Não devo eu castigá-los? "", pergunta o Senhor. ""Não devo eu vingar-me de uma nação como essa?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19089, 30, """Uma coisa espantosa e horrível acontece nesta terra:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19090, 31, "Os profetas profetizam mentiras, os sacerdotes governam por sua própria autoridade, e o meu povo gosta dessas coisas. Mas o que vocês farão quando tudo isso chegar ao fim?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19091, 1, """Fuja para um lugar seguro, povo de Benjamim! Fuja de Jerusalém! Toquem a trombeta em Tecoa! Ponham sinal em Bete-Haquerém! Porque já se vê a desgraça que vem do Norte, uma terrível destruição!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19092, 2, "Destruirei a cidade de Sião; você é como uma bela pastagem,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19093, 3, "para onde os pastores vêm com os seus rebanhos; armam as suas tendas ao redor dela e apascentam, cada um no seu lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19094, 4, """Preparem-se para enfrentá-la na batalha! Vamos, ataquemos ao meio-dia! Ai de nós! O dia declina e as sombras da tarde já se estendem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19095, 5, "Vamos, ataquemos de noite! Destruamos as suas fortalezas! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19096, 6, "Assim diz o Senhor dos Exércitos: ""Derrubem as árvores e construam rampas de cerco contra Jerusalém. Ó cidade da falsidade! Ela está cheia de opressão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19097, 7, "Assim como um poço produz água, também ela produz sua maldade. Violência! Destruição! É o que se ouve dentro dela; doenças e feridas estão sempre diante de mim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19098, 8, "Ouça a minha advertência, ó Jerusalém! Do contrário eu me afastarei inteiramente de você e farei de você uma desolação, uma terra desabitada"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19099, 9, "Assim diz o Senhor dos Exércitos: ""Rebusque-se o remanescente de Israel tão completamente, como se faz com uma videira, como faz quem colhe uvas: repassa os ramos cacho por cacho"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19100, 10, "A quem posso eu falar ou advertir? Quem me escutará? Os ouvidos deles são obstinados, e eles não podem ouvir. A palavra do Senhor é para eles desprezível, não encontram nela motivo de prazer.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19101, 11, "Mas a ira do Senhor dentro de mim transborda, já não posso retê-la. ""Derrama-a sobre as crianças na rua e sobre os jovens reunidos em grupos; pois eles também serão pegos juntos com os maridos e as mulheres, os velhos e os de idade bem avançada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19102, 12, "As casas deles serão entregues a outros, juntamente com os seus campos e as suas mulheres, quando eu estender a minha mão contra os que vivem nesta terra"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19103, 13, """Desde o menor até o maior, todos são gananciosos; profetas e sacerdotes igualmente, todos praticam o engano.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19104, 14, "Eles tratam da ferida do meu povo como se não fosse grave. ‘Paz, paz’, dizem, quando não há paz alguma.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19105, 15, "Ficarão eles envergonhados da sua conduta detestável? Não, eles não sentem vergonha alguma, nem mesmo sabem corar. Portanto, cairão entre os que caem; serão humilhados quando eu os castigar"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19106, 16, "Assim diz o Senhor: ""Ponham-se nas encruzilhadas e olhem; perguntem pelos caminhos antigos, perguntem pelo bom caminho. Sigam-no e acharão descanso"". Mas vocês disseram: ‘Não seguiremos! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19107, 17, "Coloquei sentinelas entre vocês e disse: Prestem atenção ao som da trombeta! Mas vocês disseram: ‘Não daremos atenção’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19108, 18, "Vejam, ó nações; observe, ó assembléia, o que acontecerá a eles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19109, 19, "Ouça, ó terra: Vou trazer desgraça sobre este povo, o fruto das suas maquinações, porque não deram atenção às minhas palavras e rejeitaram a minha lei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19110, 20, "De que me serve o incenso trazido de Sabá, ou o cálamo aromático de uma terra distante? Os seus holocaustos não são aceitáveis nem me agradam as suas ofertas"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19111, 21, "Assim diz o Senhor: ""Estou colocando obstáculos diante deste povo. Pais e filhos tropeçarão neles; vizinhos e amigos perecerão"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19112, 22, "Assim diz o Senhor: ""Veja! Um exército vem do Norte; uma grande nação está sendo mobilizada desde os confins da terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19113, 23, "Eles empunham o arco e a lança; são cruéis e sem misericórdia, e o barulho que fazem é como o bramido do mar. Vêm montando os seus cavalos em formação de batalha, para atacá-la, ó cidade de Sião. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19114, 24, "Ouvimos os relatos sobre eles, e as nossas mãos amoleceram. A angústia tomou conta de nós, dores como as da mulher que está dando à luz.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19115, 25, "Não saiam aos campos nem andem pelas estradas, pois o inimigo traz a espada e há terror por todos os lados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19116, 26, "Ó minha filha, meu povo, ponha vestes de lamento e revolva-se em cinza. Lamente-se com choro amargurado, como quem chora por um filho único, pois subitamente o destruidor virá sobre nós.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19117, 27, """Eu o designei como examinador de metais, provador do meu povo para que você examine e ponha à prova a conduta deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19118, 28, "Todos eles são rebeldes obstinados, e propagadores de calúnias. Estão endurecidos como o bronze e o ferro. Todos eles são corruptos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19119, 29, "O fole sopra com força para separar o chumbo com o fogo, mas o refino prossegue em vão; os ímpios não são expurgados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19120, 30, "São chamados prata rejeitada, porque o Senhor os rejeitou. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19121, 1, "Esta é a palavra que veio a Jeremias da parte do Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19122, 2, """Fique junto à porta o templo do Senhor e proclame esta mensagem: "" ‘Ouçam a palavra do Senhor, todos vocês de Judá que atravessam estas portas para adorar o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19123, 3, "Assim diz o Senhor dos Exércitos, o Deus de Israel: Corrijam a sua conduta e as suas ações, eu os farei habitar neste lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19124, 4, "Não confiem em palavras enganosas: ""Este é o templo do Senhor, o templo do Senhor, o templo do Senhor! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19125, 5, "Mas se vocês realmente corrigirem a sua conduta e as suas ações, e se, de fato, tratarem uns aos outros com justiça,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19126, 6, "se não oprimirem o estrangeiro, o órfão e a viúva e não derramarem sangue inocente neste lugar, e se vocês não seguirem outros deuses para a sua própria ruína,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19127, 7, "então eu os farei habitar neste lugar, na terra que dei aos seus antepassados desde a antigüidade e para sempre.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19128, 8, "Mas vejam! Vocês confiam em palavras enganosas e inúteis.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19129, 9, """ ‘Vocês pensam que podem roubar e matar, cometer adultério e jurar falsamente, queimar incenso a Baal e seguir outros deuses que vocês não conheceram,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19130, 10, "e depois vir e permanecer perante mim neste templo, que leva o meu nome, e dizer: Estamos seguros!, seguros para continuar com todas essas práticas repugnantes?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19131, 11, "Esse templo, que leva o meu nome, tornou-se para vocês um covil de ladrões? Cuidado! Eu mesmo estou vendo isso’ "", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19132, 12, """ ‘Portanto, vão agora a Siló, o meu lugar de adoração, onde primeiro fiz uma habitação em honra do meu nome, e vejam o que eu lhe fiz por causa da impiedade de Israel, meu povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19133, 13, "Mas agora, visto que vocês fizeram todas essas coisas, diz o Senhor, apesar de eu lhes ter falado repetidas vezes, e vocês não me terem dado atenção, e de eu lhes ter chamado, e vocês não me terem respondido,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19134, 14, "eu farei a este templo que leva o meu nome, no qual vocês confiam, o lugar de adoração que dei a vocês e aos seus antepassados, o mesmo que fiz a Siló.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19135, 15, "Expulsarei vocês da minha presença, como fiz com todos os seus compatriotas, o povo de Efraim’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19136, 16, """Mas a você, Jeremias, não ore por este povo nem faça súplicas ou pedidos em favor dele, nem interceda por ele junto a mim, pois eu não o ouvirei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19137, 17, "Não vê o que estão fazendo nas cidades de Judá e nas ruas de Jerusalém?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19138, 18, "Os filhos ajuntam a lenha, os pais acendem o fogo, e as mulheres preparam a massa e fazem bolos para a Rainha dos Céus. Além disso, derramam ofertas a outros deuses para provocarem a minha ira.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19139, 19, "Mas será que é a mim que eles estão provocando?, pergunta o Senhor. Não é a si mesmos, para a sua própria vergonha?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19140, 20, """ ‘Portanto, assim diz o Soberano Senhor: A minha ardente ira será derramada sobre este lugar, sobre os homens, os animais, e as árvores do campo, como também sobre o produto do solo; ela arderá como fogo, e não poderá ser extinguida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19141, 21, """ ‘Assim diz o Senhor dos Exércitos, o Deus de Israel: Juntem os seus holocaustos aos outros sacrifícios e comam a carne vocês mesmos!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19142, 22, "Quando tirei do Egito os seus antepassados, nada lhes falei nem lhes ordenei quanto a holocaustos e sacrifícios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19143, 23, "Dei-lhes, entretanto, esta ordem: Obedeçam-me, e eu serei o seu Deus e vocês serão o meu povo. Vocês andarão em todo o caminho que eu lhes ordenar, para que tudo lhes vá bem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19144, 24, "Mas eles não me ouviram nem me deram atenção. Antes, seguiram o raciocínio rebelde dos seus corações maus. Andaram para trás e não para a frente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19145, 25, "Desde a época em que os seus antepassados saíram do Egito até o dia de hoje, eu lhes enviei os meus servos, os profetas, dia após dia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19146, 26, "Mas eles não me ouviram nem me deram atenção. Antes tornaram-se obstinados e foram piores do que os antepassados deles’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19147, 27, """Quando você lhes disser tudo isso, eles não o escutarão; quando você os chamar, não responderão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19148, 28, "Portanto, diga a eles: ‘Esta é uma nação que não obedeceu ao Senhor, ao seu Deus, nem aceitou a correção. A verdade foi destruída e desapareceu dos seus lábios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19149, 29, "Cortem os seus cabelos consagrados e joguem-os fora. Lamentem-se sobre os montes estéreis, pois o Senhor rejeitou e abandonou esta geração que provocou a sua ira.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19150, 30, """ ‘Os de Judá fizeram o que eu reprovo, declara o Senhor. Profanaram o templo que leva o meu nome, colocando nele as imagens dos seus ídolos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19151, 31, "Construíram o alto de Tofete no vale de Ben-Hinom, para queimarem em sacrifício os seus filhos e as suas filhas, coisa que nunca ordenei e que jamais me veio à mente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19152, 32, "Por isso, certamente vêm os dias, declara o Senhor, em que não mais chamarão este lugar Tofete ou vale de Ben-Hinom, mas vale da Matança, pois ali enterrarão cadáveres até que não haja mais lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19153, 33, "Então os cadáveres deste povo servirão de comida para as aves e para os animais, e não haverá quem os afugente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19154, 34, "Darei fim às vozes de júbilo e de alegria, às vozes do noivo e da noiva nas cidades de Judá e nas ruas de Jerusalém, pois esta terra se tornará um deserto.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19155, 1, """ ‘Naquele tempo, declara o Senhor, os ossos dos reis e dos líderes de Judá, os ossos dos sacerdotes e dos profetas e os ossos do povo de Jerusalém serão retirados dos seus túmulos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19156, 2, "Serão expostos ao sol e à lua e a todos os astros do céu, que eles amaram, aos quais prestaram culto e os quais seguiram, consultaram e adoraram. Não serão ajuntados nem enterrados, mas se tornarão esterco sobre o solo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19157, 3, "Todos os sobreviventes dessa nação má preferirão a morte à vida, em todos os lugares para onde eu os expulsar, diz o Senhor dos Exércitos’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19158, 4, """Diga a eles: ‘Assim diz o Senhor. "" ‘Quando os homens caem, não se levantam mais? Quando alguém se desvia do caminho, não retorna a ele?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19159, 5, "Por que será, então, que este povo se desviou? Por que Jerusalém persiste em desviar-se? Eles apegam-se ao engano e recusam-se a voltar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19160, 6, "Eu ouvi com atenção, mas eles não dizem o que é certo. Ninguém se arrepende de sua maldade e diz: ""O que foi que eu fiz? "" Cada um se desvia e segue seu próprio curso, como um cavalo que se lança com ímpeto na batalha.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19161, 7, "Até a cegonha no céu conhece as estações que lhe estão determinadas, e a pomba, a andorinha e o tordo observam a época de sua migração. Mas o meu povo não conhece as exigências do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19162, 8, """ ‘Como vocês podem dizer ""Somos sábios, pois temos a lei do Senhor"", quando na verdade a pena mentirosa dos escribas a transformou em mentira?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19163, 9, "Os sábios serão envergonhados; ficarão amedrontados e serão pegos na armadilha. Visto que rejeitaram a palavra do Senhor, que sabedoria é essa que eles têm?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19164, 10, "Por isso, entregarei as suas mulheres a outros homens, e darei os seus campos a outros proprietários. Desde o menor até o maior, todos são gananciosos; tanto os sacerdotes como os profetas, todos praticam a falsidade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19165, 11, "Eles tratam da ferida do meu povo como se ela não fosse grave. ""Paz, paz"", dizem, quando não há paz alguma.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19166, 12, "Ficaram eles envergonhados de sua conduta detestável? Não, eles não sentem vergonha, nem mesmo sabem corar. Portanto, cairão entre os que caem; serão humilhados quando eu os castigar, declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19167, 13, """ ‘Eu quis recolher a colheita deles, declara o Senhor. Mas não há uvas na videira nem figos na figueira; as folhas estão secas. O que lhes dei será tomado deles’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19168, 14, """Por que estamos sentados aqui? Reúnam-se! Fujamos para as cidades fortificadas e pereçamos ali! Pois o Senhor, o nosso Deus, condenou-nos a perecer e nos deu água envenenada para beber, porque temos pecado contra ele.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19169, 15, "Esperávamos a paz, mas não veio bem algum; esperávamos um tempo de cura, mas há somente terror.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19170, 16, "O resfolegar dos seus cavalos pode-se ouvir desde Dã; ao relinchar dos seus garanhões a terra toda treme. Vieram para devorar esta terra e tudo o que nela existe, a cidade e todos os que nela habitam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19171, 17, """Vejam, estou enviando contra vocês serpentes venenosas, que ninguém consegue encantar; elas morderão vocês, e não haverá remédio"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19172, 18, "A tristeza tomou conta de mim; o meu coração desfalece.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19173, 19, "Ouça o grito de socorro da minha filha, do meu povo, grito que se estende por toda esta terra: ""O Senhor não está em Sião? Não se acha mais ali o seu rei? "" ""Por que eles me provocaram à ira com os seus ídolos, com os seus inúteis deuses estrangeiros? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19174, 20, "Passou a época da colheita, acabou o verão, e não estamos salvos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19175, 21, "Estou arrasado com a devastação sofrida pelo meu povo. Choro muito, e o pavor se apodera de mim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19176, 22, "Não há bálsamo em Gileade? Não há médico? Por que, então, não há sinal de cura para a ferida do meu povo?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19177, 1, "Ah, se a minha cabeça fosse uma fonte de água e os meus olhos um manancial de lágrimas! Eu choraria noite e dia pelos mortos do meu povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19178, 2, "Ah, se houvesse um alojamento para mim no deserto, para que eu pudesse deixar o meu povo e afastar-me dele. São todos adúlteros, um bando de traidores!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19179, 3, """A língua deles é como um arco pronto para atirar. É a falsidade, não a verdade, que prevalece nesta terra. Eles vão de um crime a outro; eles não me reconhecem"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19180, 4, """Cuidado com os seus amigos, não confie em seus parentes. Porque cada parente é um enganador, e cada amigo um caluniador.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19181, 5, "Amigo engana amigo, ninguém fala a verdade. Eles treinaram a língua para mentir; e, sendo perversos, eles se cansam demais para se converterem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19182, 6, "De opressão em opressão, de engano em engano, eles se recusam a reconhecer-me"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19183, 7, "Portanto, assim diz o Senhor dos Exércitos: ""Vejam, sou eu que os refinarei e os provarei. Que mais posso eu fazer pelo meu povo?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19184, 8, "A língua deles é uma flecha mortal; eles falam traiçoeiramente. Cada um mostra-se cordial com o seu próximo, mas no íntimo lhe prepara uma armadilha.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19185, 9, "Deixarei eu de castigá-los? "", pergunta o Senhor. ""Não me vingarei de uma nação como essa? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19186, 10, "Chorarei, prantearei e me lamentarei pelos montes por causa das pastagens da estepe; pois estão abandonadas e ninguém mais as percorre. Não se ouve o mugir do gado; tanto as aves como os animais fugiram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19187, 11, """Farei de Jerusalém um amontoado de ruínas, uma habitação de chacais. Devastarei as cidades de Judá até não restar nenhum morador. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19188, 12, "Quem é bastante sábio para compreender isso? Quem foi instruído pelo Senhor, que possa explicá-lo? Por que a terra está arruinada e devastada como um deserto pelo qual ninguém passa?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19189, 13, "O Senhor disse: ""Foi porque abandonaram a minha lei, que estabeleci diante deles; não me obedeceram nem seguiram a minha lei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19190, 14, "Em vez disso, seguiram a dureza de seus próprios corações, indo atrás dos baalins, como os seus antepassados lhes ensinaram"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19191, 15, "Por isso, assim diz o Senhor dos Exércitos, o Deus de Israel: ""Vejam! Farei este povo comer comida amarga e beber água envenenada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19192, 16, "Eu os espalharei entre nações que nem eles nem os seus antepassados conheceram; e enviarei contra eles a espada até exterminá-los"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19193, 17, "Assim diz o Senhor dos Exércitos: ""Considerem: Chamem as pranteadoras profissionais; mandem chamar as mais hábeis entre elas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19194, 18, "Venham elas depressa e lamentem por nós, até que os nossos olhos transbordem de lágrimas e águas corram de nossas pálpebras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19195, 19, "O som de lamento se ouve desde Sião: ‘Como estamos arruinados! Como é grande a nossa humilhação! Deixamos a nossa terra porque as nossas casas estão em ruínas’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19196, 20, "Ó mulheres, ouçam agora a palavra do Senhor; abram os ouvidos às palavras de sua boca. Ensinem suas filhas a lamentar-se; ensinem umas as outras a prantear.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19197, 21, "A morte subiu e penetrou pelas nossas janelas e invadiu as nossas fortalezas, eliminando das ruas as crianças e das praças, os rapazes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19198, 22, """Diga: ‘Assim declara o Senhor: "" ‘Cadáveres ficarão estirados como esterco em campo aberto, como o trigo deixado para trás pelo ceifeiro, sem que ninguém o ajunte’. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19199, 23, "Assim diz o Senhor: ""Não se glorie o sábio em sua sabedoria nem o forte em sua força nem o rico em sua riqueza,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19200, 24, "mas quem se gloriar, glorie-se nisto: em compreender-me e conhecer-me, pois eu sou o Senhor, e ajo com lealdade, com justiça e com retidão sobre a terra, pois é dessas coisas que me agrado"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19201, 25, """Vêm chegando os dias"", declara o Senhor, ""em que castigarei todos os que são circuncidados apenas no corpo,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19202, 26, "como também o Egito, Judá, Edom, Amom, Moabe e todos os que rapam a cabeça e vivem no deserto; porque todas essas nações são incircuncisas, e a comunidade de Israel tem o coração obstinado. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19203, 1, "Ouçam o que o Senhor diz a vocês, ó comunidade de Israel!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19204, 2, "Assim diz o Senhor: ""Não aprendam as práticas das nações nem se assustem com os sinais no céu, embora as nações se assustem com eles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19205, 3, "Os costumes religiosos das nações são inúteis: corta-se uma árvore da floresta, um artesão a modela com seu formão;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19206, 4, "enfeitam-na com prata e ouro, prendendo tudo com martelo e pregos para que não balance.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19207, 5, "Como um espantalho numa plantação de pepinos, os ídolos são incapazes de falar, e têm que ser transportados porque não conseguem andar. Não tenham medo deles, pois não podem fazer nem mal nem bem"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19208, 6, "Não há absolutamente ninguém comparável a ti, ó Senhor; tu és grande, e grande é o poder do teu nome.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19209, 7, "Quem não te temerá, ó rei das nações? Esse temor te é devido. Entre todos os sábios das nações e entre todos os seus reinos não há absolutamente ninguém comparável a ti.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19210, 8, "São todos insensatos e tolos; querem ser ensinados por ídolos inúteis. Os deuses deles não passam de madeira.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19211, 9, "Prata batida é trazida de Társis, e ouro, de Ufaz. A obra do artesão e do ourives é vestida de azul e de vermelho; tudo não passa de obra de hábeis artesãos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19212, 10, "Mas o Senhor é o Deus verdadeiro; ele é o Deus vivo; o rei eterno. Quando ele se ira, a terra treme; as nações não podem suportar o seu furor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19213, 11, """Digam-lhes isto: ‘Esses deuses, que não fizeram nem os céus nem a terra, desaparecerão da terra e de debaixo dos céus’. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19214, 12, "Mas foi Deus quem fez a terra com o seu poder, firmou o mundo com a sua sabedoria e estendeu os céus com o seu entendimento.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19215, 13, "Ao som do seu trovão, as águas no céu rugem, e formam-se nuvens desde os confins da terra. Ele faz os relâmpagos para a chuva e dos seus depósitos faz sair o vento.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19216, 14, "Esses homens todos são estúpidos e ignorantes; cada ourives é envergonhado pela imagem que esculpiu. Suas imagens esculpidas são uma fraude, elas não têm fôlego de vida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19217, 15, "São inúteis, são objetos de zombaria. Quando vier o julgamento delas, perecerão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19218, 16, "Aquele que é a porção de Jacó nem se compara a essas imagens, pois ele é quem forma todas as coisas, e Israel é a tribo de sua propriedade, o Senhor dos Exércitos é o seu nome.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19219, 17, "Ajunte os seus pertences para deixar a terra, você que vive sitiada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19220, 18, "Porque assim diz o Senhor: ""Desta vez lançarei fora os que vivem nesta terra. Trarei aflição sobre eles, e serão capturados"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19221, 19, "Ai de mim! Estou ferido! O meu ferimento é incurável! Apesar disso eu dizia: Esta é a minha enfermidade e tenho que suportá-la.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19222, 20, "A minha tenda foi destruída; todas as cordas da minha tenda estão arrebentadas. Os meus filhos me deixaram e já não existem; não restou ninguém para armar a minha tenda e montar o meu abrigo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19223, 21, "Os líderes do povo são insensatos e não consultam o Senhor; por isso não prosperam e todo o seu rebanho está disperso.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19224, 22, "Escutem! Estão chegando notícias: uma grande agitação vem do norte! As cidades de Judá serão arrasadas e transformadas em morada de chacais.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19225, 23, "Eu sei, Senhor, que a vida do homem não lhe pertence; não compete ao homem dirigir os seus passos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19226, 24, "Corrige-me, Senhor, mas somente com justiça, não com ira, para que não me reduzas a nada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19227, 25, "Derrama a tua ira sobre as nações que não te conhecem, sobre os povos que não invocam o teu nome; pois eles devoraram Jacó, devoraram-no completamente e destruíram a sua terra natal.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19228, 1, "Esta é a palavra que veio a Jeremias da parte do Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19229, 2, """Ouçam os termos desta aliança; e, você, repita-os ao povo de Judá e aos habitantes de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19230, 3, "Diga-lhes que assim diz o Senhor, o Deus de Israel: ‘Maldito é aquele que não obedecer aos termos desta aliança,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19231, 4, "os quais ordenei aos antepassados de vocês, quando eu os tirei do Egito, da fornalha de fundir ferro’. Eu disse: Obedeçam-me e façam tudo o que lhes ordeno, e vocês serão o meu povo, e eu serei o seu Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19232, 5, "Então cumprirei a promessa que fiz sob juramento aos antepassados de vocês, de dar-lhes uma terra onde manam leite e mel, a terra que vocês hoje possuem"". Então respondi: ""Amém, Senhor"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19233, 6, "O Senhor me disse: ""Proclame todas estas palavras nas cidades de Judá e nas ruas de Jerusalém: ‘Ouçam os termos desta aliança e cumpram-nos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19234, 7, "Desde a época em que tirei os seus antepassados do Egito até hoje, repetidas vezes os adverti, dizendo: Obedeçam-me.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19235, 8, "Mas eles não me ouviram nem me deram atenção; pelo contrário, seguiram os seus corações duros e maus. Por isso eu trouxe sobre eles todas as maldições desta aliança, que eu tinha ordenado que cumprissem, mas que eles não cumpriram’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19236, 9, "Então o Senhor me disse: ""Há uma conspiração entre o povo de Judá e os habitantes de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19237, 10, "Eles retornaram aos pecados de seus antepassados, que recusaram das ouvidos às minhas palavras e seguiram outros deuses para prestar-lhes culto. Tanto a comunidade de Israel como a de Judá quebraram a aliança que eu fiz com os antepassados deles"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19238, 11, "Por isso, assim diz o Senhor: ""Trarei sobre eles uma desgraça da qual não poderão escapar. Ainda que venham a clamar a mim, eu não os ouvirei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19239, 12, "Então as cidades de Judá e os habitantes de Jerusalém clamarão aos deuses, aos quais queimam incenso, mas eles não poderão salvá-los quando a desgraça os atingir.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19240, 13, "Você tem tantos deuses quantas são as suas cidades, ó Judá; e os altares que você construiu para queimar incenso àquela coisa vergonhosa chamada Baal são tantos quantas são as ruas de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19241, 14, """E você, Jeremias, não ore em favor desse povo nem ofereça súplica ou petição alguma por eles, porque eu não ouvirei quando clamarem a mim na hora da desgraça. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19242, 15, """O que a minha amada faz no meu templo, com intenção enganosa? Será que os votos e a carne consagrada evitarão o castigo? Poderá você, então, exultar? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19243, 16, "O Senhor a chamou de oliveira verdejante, ornada de belos e bons frutos. Mas com o estrondo de um grande tumulto, ele a incendiará, e os seus ramos serão quebrados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19244, 17, "O Senhor dos Exércitos, que a plantou, anunciou-lhe desgraça, porque a comunidade de Israel e a comunidade de Judá fizeram o que é reprovável e provocaram a minha ira, queimando incenso a Baal.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19245, 18, "Fiquei sabendo porque o Senhor me revelou; tu me mostraste o que eles estavam fazendo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19246, 19, "Eu era como um cordeiro manso levado ao matadouro; não tinha percebido que tramavam contra mim, dizendo: ""Destruamos a árvore e a sua seiva, vamos cortá-lo da terra dos viventes para que o seu nome não seja mais lembrado"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19247, 20, "Ó Senhor dos Exércitos, justo juiz que provas o coração e a mente, espero ver a tua vingança sobre eles, pois a ti expus a minha causa.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19248, 21, "Em vista disso, assim diz o Senhor a respeito dos homens de Anatote que querem tirar a sua vida, e que dizem: ""Não profetize em nome do Senhor, se não nós o mataremos"";", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19249, 22, "assim diz o Senhor dos Exércitos: ""Eu os castigarei. Seus jovens morrerão à espada; seus filhos e suas filhas, de fome.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19250, 23, "Nem mesmo um remanescente lhes restará, porque trarei a desgraça sobre os homens de Anatote no ano do seu castigo"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19251, 1, "Tu és justo, Senhor, quando apresento uma causa diante de ti. Contudo, eu gostaria de discutir contigo sobre a tua justiça. Por que o caminho dos ímpios prospera? Por que todos os traidores vivem sem problemas?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19252, 2, "Tu os plantaste, e eles criaram raízes; crescem e dão fruto. Tu estás sempre perto dos seus lábios, mas longe dos seus corações.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19253, 3, "Tu, porém, me conheces, Senhor; tu me vês e provas a minha atitude para contigo. Arranca os ímpios como a ovelhas destinadas ao matadouro! Reserva-os para o dia da matança!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19254, 4, "Até quando a terra ficará de luto e a relva de todo o campo estará seca? Perecem os animais e as aves por causa da maldade dos que habitam nesta terra, pois eles disseram: ‘Ele não verá o fim que nos espera’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19255, 5, """Se você correu com homens e eles o cansaram, como poderá competir com cavalos? Se você tropeça em terreno seguro, o que fará nos matagais junto ao Jordão?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19256, 6, "Até mesmo os seus irmãos e a sua própria família traíram você e o perseguem aos gritos. Não confie neles, mesmo quando lhe dizem coisas boas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19257, 7, """Abandonei a minha família, deixei a minha propriedade e entreguei aquela a quem amo nas mãos dos seus inimigos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19258, 8, "O povo de minha propriedade tornou-se para mim como um leão na floresta. Ele ruge contra mim, por isso eu o odeio.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19259, 9, "O povo de minha propriedade tornou-se para mim como uma toca de hiena, sobre pairam a qual as aves de rapina. Reúnam todos os animais selvagens; tragam-nos para o banquete.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19260, 10, "A minha vinha foi destruída por muitos pastores, os quais pisotearam a minha propriedade. Eles tornaram a minha preciosa propriedade num deserto devastado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19261, 11, "Fizeram dela uma terra devastada; e devastada ela pranteia diante de mim. A terra toda foi devastada, mas não há quem se importe com isso.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19262, 12, "Destruidores vieram sobre todas as planícies do deserto, pois a espada do Senhor devora esta terra de uma extremidade à outra; ninguém está seguro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19263, 13, "Semearam trigo, mas colheram espinhos; cansaram-se de trabalhar para nada produzir. Estão desapontados com a colheita por causa do fogo da ira do Senhor. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19264, 14, "Assim diz o Senhor a respeito de todos os meus vizinhos, as nações ímpias que se apoderam da herança que dei a Israel, meu povo: ""Eu os arrancarei da sua terra, e arrancarei Judá do meio deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19265, 15, "Mas, depois de arrancá-los, terei compaixão de novo e os farei voltar, cada um à sua propriedade e à sua terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19266, 16, "E se aprenderem a comportar-se como meu povo, e jurarem pelo nome do Senhor, dizendo: ‘Juro pelo nome do Senhor’ — como antes ensinaram o meu povo a jurar por Baal — então eles serão estabelecidos no meio do meu povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19267, 17, "Mas se não me ouvirem, eu arrancarei completamente aquela nação e a destruirei"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19268, 1, "Assim me disse o Senhor: ""Vá comprar um cinto de linho e ponha-o em volta da cintura, mas não o deixe encostar na água"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19269, 2, "Comprei um cinto e o pus em volta da cintura, como o Senhor me havia instruído.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19270, 3, "O Senhor me dirigiu a palavra pela segunda vez, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19271, 4, """Pegue o cinto que você comprou e está usando, vá agora a Perate e esconda-o ali numa fenda da rocha"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19272, 5, "Assim, fui e o escondi em Perate, conforme o Senhor me havia ordenado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19273, 6, "Depois de muitos dias, o Senhor me disse: ""Vá agora a Perate e pegue o cinto que lhe ordenei que escondesse ali"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19274, 7, "Então fui a Perate, desenterrei o cinto e o tirei do lugar em que o havia escondido. O cinto estava podre e se tornara completamente inútil.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19275, 8, "E o Senhor dirigiu-me a palavra, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19276, 9, """Assim diz o Senhor: Do mesmo modo também arruinarei o orgulho de Judá e o orgulho desmedido de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19277, 10, "Este povo ímpio, que se recusa a ouvir as minhas palavras, que age segundo a dureza de seus corações, seguindo outros deuses para prestar-lhes culto e adorá-los, que este povo seja como aquele cinto: completamente inútil!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19278, 11, "Assim como um cinto se apega à cintura de um homem, da mesma forma fiz com que toda a comunidade de Israel e toda a comunidade de Judá se apegasse a mim, para que fosse o meu povo para o meu renome, louvor e honra. Mas eles não me ouviram"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19279, 12, """Diga-lhes também: ‘Assim diz o Senhor, o Deus de Israel: Deve-se encher de vinho toda vasilha de couro’. E, se eles lhe disserem: ‘Será que não sabemos que se deve encher de vinho toda vasilha de couro? ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19280, 13, "Então você lhes dirá: ‘Assim diz o Senhor: Farei com que fiquem totalmente embriagados todos os habitantes desta terra, bem como os reis que se assentam no trono de Davi, os sacerdotes, os profetas e todos os habitantes de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19281, 14, "Eu os despedaçarei, colocando uns contra os outros, os pais e os filhos, diz o Senhor. Nem a piedade, nem a misericórdia nem a compaixão me impedirão de destruí-los’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19282, 15, "Escutem e dêem atenção, não sejam arrogantes, pois o Senhor falou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19283, 16, "Dêem glória ao Senhor, ao seu Deus, antes que ele traga trevas, antes que os pés de vocês tropecem nas colinas ao escurecer. Vocês esperam a luz, mas ele fará dela uma escuridão profunda sim, ele a transformará em densas trevas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19284, 17, "Mas, se vocês não ouvirem, eu chorarei em segredo por causa do orgulho de vocês. Chorarei amargamente, e de lágrimas os meus olhos transbordarão, porque o rebanho do Senhor foi levado para o cativeiro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19285, 18, "Diga-se ao rei e à rainha-mãe: ""Desçam do trono, pois as suas coroas gloriosas caíram de suas cabeças"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19286, 19, "As cidades do Neguebe estão bloqueadas e não há quem nelas consiga entrar. Todo o Judá foi levado para o exílio, todos os exilados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19287, 20, "Erga os olhos, Jerusalém, e veja aqueles que vêm do norte. Onde está o rebanho que lhe foi confiado, as ovelhas das quais você se orgulhava?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19288, 21, "O que você dirá quando sobre você dominarem aqueles que você sempre teve como aliados? Você não irá sentir dores como as de uma mulher em trabalho de parto?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19289, 22, "E se você se perguntar: ‘Por que aconteceu isso comigo? ’, saiba que foi por causa dos seus muitos pecados que as suas vestes foram levantadas e você foi violentada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19290, 23, "Será que o etíope pode mudar a sua pele? Ou o leopardo as suas pintas? Assim também vocês são incapazes de fazer o bem, vocês que estão acostumados a praticar o mal.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19291, 24, """Espalharei vocês como a palha levada pelo vento do deserto.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19292, 25, "Esta é a sua parte, a porção que lhe determinei"", declara o Senhor, ""porque você se esqueceu de mim e confiou em deuses falsos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19293, 26, "Eu mesmo levantarei as suas vestes até o seu rosto para que as suas vergonhas sejam expostas,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19294, 27, "Tenho visto os seus atos repugnantes os seus adultérios, os seus relinchos, a sua prostituição desavergonhada sobre as colinas e nos campos. Ai de você, Jerusalém! Até quando você continuará impura? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19295, 1, "Esta é a palavra que o Senhor dirigiu a Jeremias acerca da seca:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19296, 2, """Judá pranteia, as suas cidades estão definhando e os seus habitantes se lamentam, prostrados no chão! O grito de Jerusalém sobe.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19297, 3, "Os nobres mandam os seus servos à procura de água; eles vão às cisternas mas nada encontram. Voltam com os potes vazios, e, decepcionados e desesperados, cobrem a cabeça.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19298, 4, "A terra nada produziu, porque não houve chuva; e os lavradores, decepcionados, cobrem a cabeça.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19299, 5, "Até mesmo a corça no campo abandona a cria recém-nascida, porque não há capim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19300, 6, "Os jumentos selvagens permanecem nos altos, farejando o vento como os chacais, mas a sua visão falha, por falta de pastagem"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19301, 7, "Embora os nossos pecados nos acusem, age por amor do teu nome, ó Senhor! Nossas infidelidades são muitas; temos pecado contra ti.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19302, 8, "Ó Esperança de Israel, tu que o salvas na hora da adversidade, por que te comportas como um estrangeiro na terra, ou como um viajante que fica somente uma noite?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19303, 9, "Por que ages como um homem que foi pego de surpresa, como um guerreiro que não pode salvar? Tu estás em nosso meio, ó Senhor, e nós pertencemos a ti; não nos abandones!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19304, 10, "Assim diz o Senhor acerca deste povo: ""Eles gostam muito de vaguear; não controlam os pés. Por isso o Senhor não os aceita; agora ele se lembrará da iniqüidade deles e os castigará por causa dos seus pecados"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19305, 11, "Então o Senhor me disse: ""Não ore pelo bem-estar deste povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19306, 12, "Ainda que jejuem, não escutarei o clamor deles; ainda que ofereçam holocaustos e ofertas de cereal, não os aceitarei. Mas eu os destruirei pela guerra, pela fome e pela peste"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19307, 13, "Mas eu disse: ""Ah, Soberano Senhor, os profetas estão dizendo a eles: ‘Vocês não verão a guerra nem a fome; eu lhes darei prosperidade duradoura neste lugar’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19308, 14, "Então o Senhor me disse: ""É mentira o que os profetas estão profetizando em meu nome. Eu não os enviei nem lhes dei ordem nenhuma, nem falei com eles. Eles estão profetizando para vocês falsas visões, adivinhações inúteis e ilusões de suas próprias mentes"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19309, 15, "Por isso, assim diz o Senhor: ""Quanto aos profetas que estão profetizando em meu nome, embora eu não os tenha enviado, e que dizem: ‘Nem guerra nem fome alcançarão esta terra’, aqueles mesmos profetas perecerão pela guerra e pela fome!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19310, 16, "E aqueles a quem estão profetizando serão jogados nas ruas de Jerusalém, por causa da fome e da guerra. E não haverá ninguém para sepultá-los, nem para sepultar as suas mulheres, os seus filhos e as suas filhas. Despejarei sobre eles o castigo que merecem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19311, 17, """Diga-lhes isto: "" ‘Que os meus olhos derramem lágrimas, noite e dia sem cessar; pois a minha filha virgem, o meu povo, sofreu um ferimento terrível, um golpe fatal.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19312, 18, "Se vou para o campo, vejo os que morreram pela espada; se entro na cidade, vejo a devastação da fome. Tanto o profeta como o sacerdote percorrem a terra sem nada compreender’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19313, 19, "Rejeitaste Judá completamente? Desprezaste Sião? Por que nos feriste a ponto de não podermos ser curados? Esperávamos a paz, mas não veio bem algum; esperávamos um tempo de cura, mas há somente terror.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19314, 20, "Senhor, reconhecemos a nossa impiedade e a iniqüidade dos nossos pais; temos de fato pecado contra ti.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19315, 21, "Por amor do teu nome não nos desprezes; não desonres o teu trono glorioso. Lembra-te da tua aliança conosco e não a quebres.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19316, 22, "Entre os ídolos inúteis das nações, existe algum que possa trazer chuva? Podem os céus, por si mesmos, produzir chuvas copiosas? Somente tu o podes, Senhor, nosso Deus! Portanto, a nossa esperança está em ti, pois tu fazes todas essas coisas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19317, 1, "Então o Senhor me disse: ""Ainda que Moisés e Samuel estivessem diante de mim, intercedendo por este povo, eu não lhes mostraria favor. Expulse-os da minha presença! Que saiam!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19318, 2, "E, se lhe perguntarem: ‘Para onde iremos? ’, diga-lhes: ‘Assim diz o Senhor: "" ‘Os destinados à morte, para a morte; os destinados à espada, para a espada; os destinados à fome, para a fome; os destinados ao cativeiro, para o cativeiro’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19319, 3, """Enviarei quatro tipos de destruidores contra eles"", declara o Senhor: ""a espada para matar, os cães para dilacerar, as aves do céu e os animais selvagens para devorar e destruir.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19320, 4, "Eu farei deles uma causa de terror para todas as nações da terra, por tudo o que Manassés, filho de Ezequias, rei de Judá, fez em Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19321, 5, """Quem terá compaixão de você, ó Jerusalém? Quem se lamentará por você? Quem vai parar e perguntar como você está?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19322, 6, "Você me rejeitou"", diz o Senhor; ""Você vive se desviando. Por isso, porei as mãos em você e a destruirei; cansei-me de mostrar compaixão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19323, 7, "Eu os espalhei ao vento como palha nas cidades desta terra. Deixei-os sem filhos; destruí o meu povo, pois não se converteram de seus caminhos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19324, 8, "Fiz com que as suas viúvas se tornassem mais numerosas do que a areia do mar. Ao meio-dia, trouxe um destruidor contra as mães dos jovens guerreiros; fiz cair sobre elas repentina angústia e pavor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19325, 9, "A mãe de sete filhos desmaiou e está ofegante. Para ela o sol se pôs enquanto ainda era dia; ela foi envergonhada e humilhada. Entregarei os sobreviventes à espada diante dos seus inimigos"", declara o Senhor", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19326, 10, "Ai de mim, minha mãe, por me haver dado à luz! Pois sou um homem em luta e em contenda com a terra toda! Nunca emprestei nem tomei emprestado, e assim mesmo todos me amaldiçoam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19327, 11, "O Senhor disse: ""Eu certamente o fortaleci para o bem e intervim por você, na época da desgraça e da adversidade, por causa do inimigo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19328, 12, """Será alguém capaz de quebrar o ferro, o ferro que vem do norte, ou o bronze?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19329, 13, "Diga a esse povo: Darei de graça a sua riqueza e os seus tesouros como despojo, por causa de todos os seus pecados, por toda a sua terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19330, 14, "Eu os tornarei escravos de seus inimigos, numa terra que vocês não conhecem, pois a minha ira acenderá um fogo que arderá contra vocês"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19331, 15, "Tu me conheces, Senhor; lembra-te de mim e vem em meu auxílio e vinga-me dos meus perseguidores. Que, pela tua paciência para com eles, eu não seja eliminado. Sabes que sofro afronta por tua causa.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19332, 16, "Quando as tuas palavras foram encontradas eu as comi; elas são a minha alegria e o meu júbilo, pois pertenço a ti, Senhor Deus dos Exércitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19333, 17, "Jamais me sentei na companhia dos que se divertem, nunca festejei com eles. Sentei-me sozinho, porque a tua mão estava sobre mim e me encheste de indignação.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19334, 18, "Por que é permanente a minha dor, e a minha ferida é grave e incurável? Por que te tornaste para mim como um riacho seco, cujos mananciais falham?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19335, 19, "Assim respondeu o Senhor: ""Se você se arrepender, eu o restaurarei para que possa me servir; se você disser palavras de valor, e não indignas, será o meu porta-voz. Deixe esse povo voltar-se para você, mas não se volte para eles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19336, 20, "Eu farei de você uma muralha de bronze fortificada diante deste povo; lutarão contra você, mas não o vencerão, pois estou com você para resgatá-lo e salvá-lo"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19337, 21, """Eu o livrarei das mãos dos ímpios e o resgatarei das garras dos violentos"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19338, 1, "Então o Senhor me dirigiu a palavra, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19339, 2, """Não se case nem tenha filhos ou filhas neste lugar"";", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19340, 3, "porque assim diz o Senhor a respeito dos filhos e filhas nascidos nesta terra, e a respeito das mulheres que forem suas mães e dos homens que forem seus pais:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19341, 4, """Eles morrerão de doenças graves; ninguém pranteará por eles; não serão sepultados, mas servirão de esterco para o solo. Perecerão pela espada e pela fome, e os seus cadáveres serão o alimento das aves e dos animais"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19342, 5, "Porque assim diz o Senhor: ""Não entre numa casa onde há luto; não vá prantear nem mostrar condolências, porque retirei a minha paz, o meu amor leal e a minha compaixão desse povo"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19343, 6, """Tanto grandes como pequenos morrerão nesta terra; não serão sepultados nem se pranteará por eles; não se farão incisões nem se rapará a cabeça por causa deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19344, 7, "Ninguém oferecerá comida para fortalecer os que pranteiam pelos mortos; ninguém dará de beber do cálice da consolação nem mesmo pelo pai ou pela mãe.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19345, 8, """Não entre numa casa em que há um banquete para se assentar com eles a fim de comer e beber.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19346, 9, "Porque assim diz o Senhor dos Exércitos, o Deus de Israel: Farei cessar neste lugar, diante dos olhos de vocês e durante a vida de vocês, a voz de júbilo e a voz de alegria, a voz do noivo e a voz da noiva.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19347, 10, """Quando você falar todas essas coisas a este povo e eles lhe perguntarem: ‘Por que o Senhor determinou uma desgraça tão terrível contra nós? Que delito ou pecado cometemos contra o Senhor, contra o nosso Deus? ’,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19348, 11, "diga-lhes: ‘Foi porque os seus antepassados me abandonaram’, diz o Senhor, ‘e seguiram outros deuses, aos quais prestaram culto e adoraram. Eles me abandonaram e não obedeceram à minha lei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19349, 12, "Mas vocês têm feito coisas piores do que os seus antepassados: cada um segue a rebeldia do seu coração mau, em vez de obedecer-me.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19350, 13, "Por isso os lançarei fora desta terra, para uma terra que vocês e os seus antepassados desconhecem; lá vocês servirão a outros deuses dia e noite, pois não terei misericórdia de vocês’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19351, 14, """Contudo, vêm dias"", declara o Senhor, ""quando já não mais se dirá: ‘Juro pelo nome do Senhor, que trouxe os israelitas do Egito’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19352, 15, "Antes dirão: ‘Juro pelo nome do Senhor, que trouxe os israelitas do norte e de todos os países para onde ele os havia expulsado’. Eu os conduzirei de volta para a sua terra, terra que dei aos seus antepassados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19353, 16, """Mas agora mandarei chamar muitos pescadores"", declara o Senhor, ""e eles os pescarão. Depois disso mandarei chamar muitos caçadores, e eles os caçarão em cada monte e colina e nas fendas das rochas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19354, 17, "Os meus olhos vêem todos os seus caminhos; eles não estão escondidos de mim, nem a sua iniqüidade está oculta aos meus olhos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19355, 18, "Eu lhes retribuirei em dobro pela sua impiedade e pelo seu pecado, porque contaminaram a minha terra com as carcaças de seus ídolos detestáveis e encheram a minha herança com as suas abominações. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19356, 19, "Senhor, minha força e minha fortaleza, meu abrigo seguro na hora da adversidade, a ti virão as nações desde os confins da terra e dirão: ""Nossos antepassados possuíam deuses falsos, ídolos inúteis, que não lhes fizeram bem algum.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19357, 20, "Pode o homem mortal fazer os seus próprios deuses? Sim, mas estes não seriam deuses! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19358, 21, """Portanto eu lhes ensinarei; desta vez eu lhes ensinarei sobre o meu poder e sobre a minha força. Então saberão que o meu nome é Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19359, 1, """O pecado de Judá está escrito com estilete de ferro, gravado com ponta de diamante, nas tábuas dos seus corações e nas pontas dos seus altares.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19360, 2, "Os filhos deles se lembram dos seus altares e dos postes de Aserá, ao lado das árvores verdejantes e sobre os montes altos", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19361, 3, "e sobre as montanhas do campo. As riquezas de vocês e todos os seus tesouros, eu os darei como despojo, como preço por todos os seus pecados nos altares idólatras, por toda a sua terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19362, 4, "Você mesmo perdeu a posse da herança que eu lhe tinha dado. Eu o farei escravo de seus inimigos numa terra que você não conhece, pois acendeu-se a minha ira, que arderá para sempre. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19363, 5, "Assim diz o Senhor: ""Maldito é o homem que confia nos homens, que faz da humanidade mortal a sua força, mas cujo coração se afasta do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19364, 6, "Ele será como um arbusto no deserto; não verá quando vier algum bem. Habitará nos lugares áridos do deserto, numa terra salgada onde não vive ninguém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19365, 7, """Mas bendito é o homem cuja confiança está no Senhor, cuja confiança nele está.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19366, 8, "Ele será como uma árvore plantada junto às águas e que estende as suas raízes para o ribeiro. Ela não temerá quando chegar o calor, porque as suas folhas estão sempre verdes; não ficará ansiosa no ano da seca nem deixará de dar fruto"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19367, 9, "O coração é mais enganoso que qualquer outra coisa e sua doença é incurável. Quem é capaz de compreendê-lo?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19368, 10, """Eu sou o Senhor que sonda o coração e examina a mente, para recompensar a cada um de acordo com a sua conduta, de acordo com as suas obras. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19369, 11, "O homem que obtém riquezas por meios injustos é como a perdiz que choca ovos que não pôs. Quando a metade da sua vida tiver passado, elas o abandonarão, e, no final, ele se revelará um tolo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19370, 12, "Um trono glorioso, exaltado desde o início, é o lugar de nosso santuário.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19371, 13, "Ó Senhor, esperança de Israel, todos os que te abandonarem Sofrerão vergonha; aqueles que se desviarem de ti terão os seus nomes escritos no pó, pois abandonaram o Senhor, a fonte de água viva.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19372, 14, "Cura-me, Senhor, e serei curado; salva-me, e serei salvo, pois tu és aquele a quem eu louvo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19373, 15, "Há os que vivem me dizendo: ""Onde está a palavra do Senhor? Que ela se cumpra! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19374, 16, "Mas não insisti eu contigo para que afastasses a desgraça? Tu sabes que não desejei o dia do desespero. Sabes o que saiu de meus lábios, pois está diante de ti.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19375, 17, "Não sejas motivo de pavor para mim; tu és o meu refúgio no dia da desgraça.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19376, 18, "Que os meus perseguidores sejam humilhados, mas não eu; que eles sejam aterrorizados, mas não eu. Traze sobre eles o dia da desgraça; destrói-os com destruição dobrada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19377, 19, "Assim me disse o Senhor: ""Vá colocar-se à porta do Povo, por onde entram e saem os reis de Judá; faça o mesmo junto a todas as portas de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19378, 20, "Diga-lhes: ‘Ouçam a palavra do Senhor, reis de Judá, todo o Judá e todos os habitantes de Jerusalém, vocês que passam por essas portas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19379, 21, "Assim diz o Senhor: Por amor à vida de vocês, tenham o cuidado de não levar cargas nem de fazê-las passar pelas portas de Jerusalém no dia de sábado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19380, 22, "Não levem carga alguma para fora de casa nem façam nenhum trabalho no sábado, mas guardem o dia de sábado como dia consagrado, como ordenei aos seus antepassados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19381, 23, "Contudo, eles não me ouviram nem me deram atenção; foram obstinados e não quiseram ouvir nem aceitar a disciplina.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19382, 24, "Mas se vocês tiverem o cuidado de obedecer-me, diz o Senhor, e não fizerem passar carga alguma pelas portas desta cidade no sábado, mas guardarem o dia de sábado como dia consagrado, deixando de realizar nele todo e qualquer trabalho,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19383, 25, "então os reis que se assentarem no trono de Davi entrarão pelas portas desta cidade em companhia de seus conselheiros. Eles e os seus conselheiros virão em carruagens e cavalos, acompanhados dos homens de Judá e dos habitantes de Jerusalém; e esta cidade será habitada para sempre.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19384, 26, "Virá gente das cidades de Judá e dos povoados ao redor de Jerusalém, do território de Benjamim e da Sefelá, das montanhas e do Neguebe, trazendo holocaustos e sacrifícios, ofertas de cereal, incenso e ofertas de ação de graças ao templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19385, 27, "Mas, se vocês não me obedecerem e deixarem de guardar o sábado como dia consagrado, fazendo passar cargas pelas portas de Jerusalém no dia de sábado, porei fogo nas suas portas, que consumirá os seus palácios’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19386, 1, "Esta é a palavra que veio a Jeremias da parte do Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19387, 2, """Vá à casa do oleiro, e ali você ouvirá a minha mensagem"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19388, 3, "Então fui à casa do oleiro, e o vi trabalhando com a roda.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19389, 4, "Mas o vaso de barro que ele estava formando se estragou-se em suas mãos; e ele o refez, moldando outro vaso de acordo com a sua vontade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19390, 5, "Então o Senhor dirigiu-me a palavra:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19391, 6, """Ó comunidade de Israel, será que não posso eu agir com vocês como fez o oleiro? "", pergunta o Senhor. ""Como barro nas mãos do oleiro, assim são vocês nas minhas mãos, ó comunidade de Israel.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19392, 7, "Se em algum momento eu decretar que uma nação ou um reino seja arrancado, despedaçado e arruinado,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19393, 8, "e se essa nação que eu adverti converter-se da sua perversidade, então eu me arrependerei e não trarei sobre ela a desgraça que eu tinha planejado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19394, 9, "E, se noutra ocasião eu decretar que uma nação ou um reino seja edificado e plantado,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19395, 10, "e se ele fizer o que eu reprovo e não me obedecer, então me arrependerei do bem que eu pretendia fazer em favor dele.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19396, 11, """Agora, portanto, diga ao povo de Judá e aos habitantes de Jerusalém: ‘Assim diz o Senhor: Estou preparando uma desgraça e fazendo um plano contra vocês. Por isso, converta-se cada um de seu mau procedimento e corrija a sua conduta e as suas ações’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19397, 12, "Mas eles responderão: ‘Não adianta. Continuaremos com os nossos próprios planos; cada um de nós seguirá a rebeldia do seu coração mau’. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19398, 13, "Portanto, assim diz o Senhor: ""Perguntem entre as nações se alguém já ouviu uma coisa dessas; coisa tremendamente horrível fez a virgem, Israel!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19399, 14, "Poderá desaparecer a neve do Líbano de suas encostas rochosas? Poderá parar de fluir suas águas frias, vindas de lugares distantes?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19400, 15, "Contudo, o meu povo esqueceu-se de mim: queimam incenso a ídolos inúteis, que os fazem tropeçar em seus caminhos e nas antigas veredas, para que andem em desvios, em estradas não aterradas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19401, 16, "A terra deles ficará deserta e será tema de permanente zombaria. Todos os que por ela passarem ficarão chocados e balançarão a cabeça.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19402, 17, "Como o vento leste, eu os dispersarei diante dos inimigos; eu lhes mostrarei as costas e não o rosto, no dia da sua derrota"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19403, 18, "Então disseram: ""Venham! Façamos planos contra Jeremias, pois não cessará o ensino da lei pelo sacerdote nem o conselho do sábio nem a mensagem do profeta. Venham! Façamos acusações contra ele e não ouçamos nada do que ele disser"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19404, 19, "Atende-me, ó Senhor; ouve o que os meus acusadores estão dizendo!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19405, 20, "Acaso se paga o bem com o mal? Mas eles cavaram uma cova para mim. Lembra-te de que eu compareci diante de ti para interceder em favor deles, para que desviasses deles a tua ira.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19406, 21, "Por isso entrega os filhos deles à fome e ao poder da espada. Que as suas mulheres fiquem sem filhos e viúvas; que os seus homens sejam mortos, e os seus rapazes sejam mortos pela espada na batalha.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19407, 22, "Seja ouvido o grito que vem de suas casas, quando repentinamente trouxeres invasores contra eles; pois cavaram uma cova para me capturarem e esconderam armadilhas para os meus pés.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19408, 23, "Mas tu conheces, ó Senhor, todas as suas conspirações para me matarem. Não perdoes os seus crimes nem apagues de diante da tua vista os seus pecados. Sejam eles derrubados diante de ti; age contra eles na hora da tua ira!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19409, 1, "Assim diz o Senhor: ""Vá comprar um vaso de barro de um oleiro. Leve com você alguns líderes do povo e alguns sacerdotes", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19410, 2, "e vá em direção ao vale de Ben-Hinom, perto da entrada da porta dos Cacos. Proclame ali as palavras que eu lhe disser.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19411, 3, "Diga: ‘Ouçam a palavra do Senhor, reis de Judá e habitantes de Jerusalém. Assim diz o Senhor dos Exércitos, Deus de Israel: Trarei desgraça tal sobre este lugar que fará retinir os ouvidos daqueles que ouvirem isso.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19412, 4, "Porque eles me abandonaram e profanaram este lugar, oferecendo sacrifícios a deuses estranhos, que nem eles nem seus antepassados nem os reis de Judá conheceram; e encheram este lugar com o sangue de inocentes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19413, 5, "Construíram nos montes os altares dedicados a Baal, para queimarem os seus filhos como holocaustos oferecidos a Baal, coisa que não ordenei, da qual nunca falei nem jamais me veio à mente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19414, 6, "Por isso, certamente vêm os dias, declara o Senhor, em que não mais chamarão este lugar Tofete ou vale de Ben-Hinom, mas vale da Matança.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19415, 7, """ ‘Esvaziarei neste lugar os planos de Judá e de Jerusalém: eu os farei morrer pela espada perante os seus inimigos, pelas mãos daqueles que os perseguem; e darei os seus cadáveres como comida para as aves e os animais.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19416, 8, "Farei com que esta cidade fique deserta e seja tema de zombaria. Todos os que por ela passarem ficarão chocados e zombarão de todos os seus ferimentos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19417, 9, "Eu farei com que comam a carne dos seus filhos e das suas filhas; e cada um comerá a carne do seu próximo, por causa do sofrimento que lhes infligirão durante o cerco feito pelos inimigos, os quais procuram tirar-lhes a vida’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19418, 10, """Depois quebre o vaso de barro diante dos homens que o acompanharam,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19419, 11, "e diga-lhes: ‘Assim diz o Senhor dos Exércitos: Assim como se quebra o vaso de oleiro, que não pode ser mais restaurado, quebrarei este povo e esta cidade, e os mortos em Tofete serão sepultados até que não haja mais lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19420, 12, "Assim farei a este lugar e aos seus habitantes"", declara o Senhor, ""tornarei esta cidade como Tofete.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19421, 13, "As casas de Jerusalém e os palácios reais de Judá serão profanados, como este lugar de Tofete: todas as casas em cujos terraços queimaram incenso a todos os corpos celestes, e derramaram ofertas de bebidas aos seus deuses estrangeiros’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19422, 14, "Jeremias, voltou então de Tofete para onde o Senhor o mandara profetizar e, entrando no pátio do templo do Senhor, disse a todo o povo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19423, 15, """Assim diz o Senhor dos Exércitos, o Deus de Israel: ‘Ouçam! Trarei sobre esta cidade, e sobre todos os povoados ao redor, todas as desgraças contra eles anunciadas, porque se obstinaram e não quiseram obedecer às minhas palavras’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19424, 1, "Quando o sacerdote Pasur, filho de Imer, o mais alto funcionário do templo do Senhor, ouviu Jeremias profetizando essas coisas,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19425, 2, "mandou espancar o profeta e prendê-lo no tronco, que havia junto à porta Superior de Benjamim, no templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19426, 3, "Na manhã seguinte, quando Pasur mandou soltá-lo do tronco, Jeremias lhe disse: ""O Senhor já não o chama Pasur, e sim Magor-Missabibe.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19427, 4, "Pois assim diz o Senhor: ‘Farei de você um terror para si mesmo e para todos os seus amigos: você verá com os próprios olhos quando eles forem mortos pela espada dos seus inimigos. Entregarei todo o povo de Judá nas mãos do rei da Babilônia, que os levará para a Babilônia e os matará à espada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19428, 5, "Eu entregarei nas mãos dos seus inimigos toda a riqueza desta cidade: toda a sua produção, todos os seus bens de valor e todos os tesouros dos reis de Judá. Levarão tudo como despojo para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19429, 6, "E você, Pasur, e todos os que vivem em sua casa irão para o exílio, para a Babilônia. Lá vocês morrerão e serão sepultados, você e todos os seus amigos a quem você tem profetizado mentiras’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19430, 7, "Senhor, tu me enganaste, e eu fui enganado; foste mais forte do que eu e prevaleceste. Sou ridicularizado o dia inteiro; todos zombam de mim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19431, 8, "Sempre que falo, é para gritar que há violência e destruição. Por isso a palavra do Senhor trouxe-me insulto e censura o tempo todo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19432, 9, "Mas, se eu digo: ""Não o mencionarei nem mais falarei em seu nome"", é como se um fogo ardesse em meu coração, um fogo dentro de mim. Estou exausto tentando contê-lo; já não posso mais!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19433, 10, "Ouço muitos comentando: ""Terror por todos os lados! Denunciem-no! Vamos denunciá-lo! "" Todos os meus amigos estão esperando que eu tropece, e dizem: ""Talvez ele se deixe enganar; então nós o venceremos e nos vingaremos dele"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19434, 11, "Mas o Senhor está comigo, como um forte guerreiro! Portanto, aqueles que me perseguem tropeçarão e não prevalecerão. O seu fracasso lhes trará completa vergonha; a sua desonra jamais será esquecida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19435, 12, "Ó Senhor dos Exércitos, tu que examinas o justo e vês o coração e a mente, deixa-me ver a tua vingança sobre eles, pois a ti expus a minha causa.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19436, 13, "Cantem ao Senhor! Louvem ao Senhor! Porque ele salva o pobre das mãos dos ímpios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19437, 14, "Maldito seja o dia em que eu nasci! Jamais seja abençoado o dia em que minha mãe me deu à luz!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19438, 15, "Maldito seja o homem que levou a notícia a meu pai, e o deixou muito alegre, quando disse: ""Você é pai de um menino! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19439, 16, "Seja aquele homem como as cidades que o Senhor destruiu sem piedade. Que ele ouça gritos de socorro pela manhã, e gritos de guerra ao meio-dia;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19440, 17, "pois ele não me matou no ventre materno nem fez da minha mãe o meu túmulo, e tampouco a deixou permanentemente grávida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19441, 18, "Por que saí do ventre materno? Só para ver dificuldades e tristezas, e terminar os meus dias na maior decepção?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19442, 1, "Esta é a palavra que veio a Jeremias da parte do Senhor, quando o rei Zedequias enviou-lhe Pasur, filho de Malquias, e o sacerdote Sofonias, filho de Maaséias. Eles disseram:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19443, 2, """Consulte agora o Senhor por nós porque Nabucodonosor, rei da Babilônia, está nos atacando. Talvez o Senhor faça por nós uma de suas maravilhas e, assim, ele se retire de nós"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19444, 3, "Jeremias, porém, respondeu-lhes: ""Digam a Zedequias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19445, 4, "‘Assim diz o Senhor, o Deus de Israel: Estou a ponto de voltar contra vocês as armas de guerra que estão em suas mãos, as quais vocês estão usando para combater o rei da Babilônia e os babilônios, os quais cercam vocês do lado de fora do muro. E eu os reunirei dentro desta cidade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19446, 5, "Eu mesmo lutarei contra vocês com mão poderosa e braço forte, com ira, furor e grande indignação.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19447, 6, "Matarei os habitantes desta cidade, tanto homens como animais; eles morrerão de uma peste terrível.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19448, 7, "Depois disso, declara o Senhor, entregarei Zedequias, rei de Judá, seus conselheiros e o povo desta cidade que sobreviver, à peste, à espada e à fome, e nas mãos de Nabucodonosor, rei da Babilônia, dos inimigos deles e daqueles que querem tirar-lhes a vida. Ele os matará à espada sem piedade nem misericórdia; não terá deles nenhuma compaixão’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19449, 8, """Diga a este povo: ‘Assim diz o Senhor: Ponho diante de vocês o caminho da vida e o caminho da morte.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19450, 9, "Todo aquele que ficar nesta cidade morrerá pela espada, pela fome ou pela peste. Mas todo o que sair e render-se aos babilônios, que os cercam vocês, viverá; este escapará com vida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19451, 10, "Decidi fazer o mal e não o bem a esta cidade, diz o Senhor. Ela será entregue nas mãos do rei da Babilônia, e ele a incendiará’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19452, 11, """Digam à casa real de Judá: ‘Ouçam a palavra do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19453, 12, "Ó dinastia de Davi, assim diz o Senhor: "" ‘Administrem justiça cada manhã: livrem o explorado das mãos do opressor; se não a minha ira se acenderá e queimará como fogo inextinguível, por causa do mal que vocês têm feito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19454, 13, "Eu estou contra você, Jerusalém! Você que está entronizada acima deste vale, na rocha do planalto, declara o Senhor, vocês que dizem: ""Quem nos atacará? Quem poderá invadir nossas moradas? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19455, 14, "Eu os castigarei de acordo com as suas obras, diz o Senhor, Porei fogo em sua floresta, que consumirá tudo ao redor’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19456, 1, "Assim diz o Senhor: ""Desça ao palácio do rei de Judá e proclame ali esta mensagem:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19457, 2, "‘Ouça a palavra do Senhor, ó rei de Judá, tu que te assentas no trono de Davi; tu e teus conselheiros, e teu povo que passa por estas portas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19458, 3, "Assim diz o Senhor: Administrem a justiça e o direito: livrem o explorado das mãos do opressor. Não oprimam nem maltratem o estrangeiro, o órfão ou a viúva; nem derramem sangue inocente neste lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19459, 4, "Porque, se vocês tiverem o cuidado de cumprir essas ordens, então os reis que se assentarem no trono de Davi entrarão pelas portas deste palácio em carruagens e cavalos, em companhia de seus conselheiros e de seu povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19460, 5, "Mas se vocês desobedecerem a essas ordens, declara o Senhor, juro por mim mesmo que este palácio ficará deserto’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19461, 6, "Porque assim diz o Senhor a respeito do palácio real de Judá: ""Apesar de você ser para mim como Gileade e como o topo do Líbano, certamente farei de você um deserto, uma cidade desabitada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19462, 7, "Prepararei destruidores contra você, cada um com as suas armas; eles cortarão o melhor dos seus cedros e o lançarão ao fogo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19463, 8, """De numerosas nações muitos passarão por esta cidade e perguntarão uns aos outros: ‘Por que o Senhor fez uma coisa dessas a esta grande cidade? ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19464, 9, "E lhes responderão: ‘Foi porque abandonaram a aliança do Senhor, do seu Deus, e adoraram outros deuses e prestaram-lhes culto’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19465, 10, "Não chorem pelo rei morto nem lamentem sua perda. Chorem amargamente, porém, por aquele que está indo para o exílio, porque jamais voltará nem verá sua terra natal.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19466, 11, "Porque assim diz o Senhor acerca de Salum, rei de Judá, sucessor de seu pai Josias, que partiu deste lugar: ""Ele jamais voltará.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19467, 12, "Morrerá no lugar para onde o levaram prisioneiro; não verá novamente esta terra"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19468, 13, """Ai daquele que constrói o seu palácio por meios corruptos, seus aposentos, pela injustiça, fazendo os seus compatriotas trabalharem por nada, sem pagar-lhes o devido salário.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19469, 14, "Ele diz: ‘Construirei para mim um grande palácio, com aposentos espaçosos’. Faz amplas janelas, reveste o palácio de cedro e pinta-o de vermelho.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19470, 15, """Você acha que acumular cedro faz de você um rei? O seu pai não teve comida e bebida? Ele fez o que era justo e certo, e tudo ia bem com ele.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19471, 16, "Ele defendeu a causa do pobre e do necessitado, e, assim, tudo corria bem. Não é isso que significa conhecer-me? "", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19472, 17, """Mas você não vê nem pensa noutra coisa além de lucro desonesto, derramar sangue inocente, opressão e extorsão. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19473, 18, "Portanto, assim diz o Senhor a respeito de Jeoaquim, filho de Josias, rei de Judá: ""Não se lamentarão por ele, clamando: ‘Ah, meu irmão! ’ ou ‘Ah, minha irmã! ’ Nem se lamentarão, clamando: ‘Ah, meu senhor! ’ ou ‘Ah, sua majestade! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19474, 19, "Ele terá o enterro de um jumento: arrastado e lançado fora das portas de Jerusalém!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19475, 20, """Jerusalém, suba ao Líbano e clame, seja ouvida a sua voz em Basã, clame desde Abarim, pois todos os seus aliados foram esmagados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19476, 21, "Eu a adverti quando você se sentiu segura, mas você não quis ouvir-me. Esse foi sempre o seu procedimento, pois desde a sua juventude você não me obedece.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19477, 22, "O vento conduzirá para longe todos os governantes que conduzem você, e os seus aliados irão para o exílio. Então você será envergonhada e humilhada por causa de todas as suas maldades.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19478, 23, "Você que está entronizada no Líbano, que está aninhada em prédios de cedro, como você gemerá quando lhe vierem as dores de parto, dores como as de uma mulher que está para dar à luz!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19479, 24, """Juro pelo meu nome"", diz o Senhor, ""que ainda que você, Joaquim, filho de Jeoaquim, rei de Judá, fosse um anel de selar em minha mão direita, eu o arrancaria.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19480, 25, "Eu o entregarei nas mãos daqueles que querem tirar-lhe a vida, daqueles que você teme, nas mãos de Nabucodonosor, rei da Babilônia, e dos babilônios", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19481, 26, "Expulsarei você e sua mãe, a mulher que lhe deu à luz, para um outro país onde vocês não nascerão, e no qual ambos morrerão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19482, 27, "Jamais retornarão à terra à qual anseiam voltar"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19483, 28, "É Joaquim um vaso desprezível e quebrado, um utensílio que ninguém quer? Por que ele e os seus descendentes serão expulsos e lançados num país que não conhecem?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19484, 29, "Ó terra, terra, terra, ouça a palavra do Senhor!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19485, 30, "Assim diz o Senhor: ""Registrem esse homem como homem sem filhos. Ele não prosperará em toda a sua vida; nenhum dos seus descendentes prosperará nem se assentará no trono de Davi nem governará em Judá"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19486, 1, """Ai dos pastores que destróem e dispersam as ovelhas do meu pasto! "", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19487, 2, "Portanto, assim diz o Senhor, Deus de Israel, aos pastores que tomam conta do meu povo: ""Foram vocês que dispersaram e expulsaram o meu rebanho, e não cuidaram dele. Mas eu cuidarei de vocês pelos seus maus procedimentos"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19488, 3, """Eu mesmo reunirei os remanescentes do meu rebanho de todas as terras para onde os expulsei e os trarei de volta à sua pastagem, a fim de que cresçam e se multipliquem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19489, 4, "Estabelecerei sobre eles pastores que cuidarão deles. E eles não mais terão medo ou pavor, e nenhum deles faltará"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19490, 5, """Dias virão"", declara o Senhor, ""em que levantarei para Davi um Renovo justo, um rei que reinará com sabedoria e fará o que é justo e certo na terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19491, 6, "Em seus dias Judá será salva, Israel viverá em segurança, e este é o nome pelo qual será chamado: O Senhor é a Nossa Justiça.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19492, 7, """Portanto, vêm dias"", diz o Senhor, ""em que não mais se dirá: ‘Juro pelo nome do Senhor, que trouxe os israelitas do Egito’,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19493, 8, "mas se dirá: ‘Juro pelo nome do Senhor, que trouxe os descendentes de Israel da terra do norte e de todas as nações para onde os expulsou’. E eles viverão na sua própria terra. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19494, 9, "Acerca dos profetas: Meu coração está partido dentro de mim; todos os meus ossos tremem. Sou como um bêbado, como um homem dominado pelo vinho, por causa do Senhor e de suas santas palavras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19495, 10, "A terra está cheia de adúlteros, e por causa disso a terra chora e as pastagens do deserto estão secas. Seu modo de vida é perveso e o seu poder é ilegítimo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19496, 11, """Tanto o profeta como o sacerdote são profanos; até no meu templo encontro as iniqüidades deles"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19497, 12, """Por isso, o caminho deles será como lugares escorregadios nas trevas, para as quais serão banidos, e nelas cairão. Trarei a desgraça sobre eles, no ano do seu castigo"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19498, 13, """Entre os profetas de Samaria vi algo repugnante: Eles profetizaram por Baal e desviaram Israel, o meu povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19499, 14, "E entre os profetas de Jerusalém vi algo horrível: Eles cometem adultério e vivem uma mentira. Encorajam os que praticam o mal, para que nenhum deles se converta de sua impiedade. Para mim são todos como Sodoma; o povo de Jerusalém é como Gomorra. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19500, 15, "Por isso assim diz o Senhor dos Exércitos acerca dos profetas: ""Eu os farei comer comida amarga e beber água envenenada, porque dos profetas de Jerusalém a impiedade se espalhou por toda esta terra"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19501, 16, "Assim diz o Senhor dos Exércitos: ""Não ouçam o que os profetas estão profetizando para vocês; eles os enchem de falsas esperanças. Falam de visões inventadas por eles mesmos, e que não vêm da boca do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19502, 17, "Vivem dizendo àqueles que desprezam a palavra do Senhor: ‘Vocês terão paz’. E a todos os que seguem a obstinação dos seus corações dizem: ‘Vocês não sofrerão desgraça alguma’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19503, 18, "Mas qual deles esteve no conselho do Senhor para ver ou ouvir a sua palavra? Quem deu atenção e obedeceu à minha palavra?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19504, 19, "Vejam, a tempestade do Senhor! A sua fúria está à solta! Um vendaval vem sobre a cabeça dos ímpios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19505, 20, "A ira do Senhor não se afastará até que ele tenha completado os seus propósitos. Em dias vindouros vocês o compreenderão claramente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19506, 21, "Não enviei esses profetas, mas eles foram correndo levar sua mensagem; não falei com eles, mas eles profetizaram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19507, 22, "Mas se eles tivessem comparecido ao meu conselho, anunciariam as minhas palavras ao meu povo e teriam feito com que se convertessem do seu mau procedimento e das suas obras más.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19508, 23, """Sou eu apenas um Deus de perto"", pergunta o Senhor, ""e não também um Deus de longe?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19509, 24, "Poderá alguém esconder-se sem que eu o veja? "", pergunta o Senhor. ""Não sou eu aquele que enche os céus e a terra? "", pergunta o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19510, 25, """Ouvi o que dizem os profetas, que profetizam mentiras em meu nome, dizendo: ‘Tive um sonho! Tive um sonho! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19511, 26, "Até quando os profetas continuarão a profetizar mentiras e as ilusões de suas próprias mentes?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19512, 27, "Eles imaginam que os sonhos que contam uns aos outros farão o povo esquecer o meu nome, assim como os seus antepassados esqueceram o meu nome por causa de Baal.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19513, 28, "O profeta que tem um sonho, conte o sonho, e o que tem a minha palavra, fale a minha palavra com fidelidade. Pois o que tem a palha com o trigo? "", pergunta o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19514, 29, """Não é a minha palavra como o fogo"", pergunta o Senhor, ""e como um martelo que despedaça a rocha?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19515, 30, """Portanto"", declara o Senhor, ""estou contra os profetas que roubam uns dos outros as minhas palavras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19516, 31, "Sim"", declara o Senhor, ""estou contra os profetas que com as suas próprias línguas declaram oráculos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19517, 32, "Sim, estou contra os que profetizam sonhos falsos"", declara o Senhor. ""Eles os relatam e com as suas mentiras irresponsáveis desviam o meu povo. Eu não os enviei nem lhes autorizei; e eles não trazem benefício algum a este povo"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19518, 33, """Quando este povo ou um profeta ou um sacerdote lhe perguntar: ‘Qual é a mensagem pesada da qual o Senhor lhe encarregou? ’, diga-lhes: ‘Vocês são o peso! E eu os abandonarei’, declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19519, 34, "Se um profeta ou um sacerdote ou alguém do povo afirmar ‘Esta é a mensagem da qual o Senhor me encarregou’, eu castigarei esse homem e a sua família.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19520, 35, "Assim dirá cada um de vocês ao seu amigo ou parente: ‘O que o Senhor respondeu? O que o Senhor falou? ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19521, 36, "Nunca mais mencionem a expressão ‘Esta é a mensagem da qual o Senhor me encarregou’, senão essa palavra se tornará uma ‘carga’ para aquele que a proferir; porque vocês distorcem as palavras do Deus vivo, do Senhor dos Exércitos, do nosso Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19522, 37, "É assim que vocês dirão ao profeta: ‘Qual é a resposta do Senhor para você? ’ ou ‘O que o Senhor falou? ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19523, 38, "Mas se vocês disserem ‘Esta é a mensagem da qual o Senhor me encarregou’, assim diz o Senhor: Vocês dizem: ‘Esta é a mensagem da qual o Senhor me encarregou’, quando eu lhes adverti que não dissessem isso.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19524, 39, "Por isso me esquecerei de vocês e os lançarei fora da minha presença, juntamente com a cidade que dei a vocês e aos seus antepassados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19525, 40, "Trarei sobre vocês humilhação perpétua, vergonha permanente, que jamais será esquecida. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19526, 1, "E o Senhor mostrou-me dois cestos de figos postos diante do templo do Senhor. Isso aconteceu depois que Nabucodonosor levou de Jerusalém, para o exílio na Babilônia, Joaquim, filho de Jeoaquim, rei de Judá, os líderes de Judá, e os artesãos e artífices.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19527, 2, "Um cesto continha figos muito bons, como os que amadurecem no princípio da colheita; os figos do outro cesto eram ruins e intragáveis.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19528, 3, "Então o Senhor me perguntou: ""O que você vê, Jeremias? "" Eu respondi: ""Figos. Os bons são muitos bons, mas os ruins são intragáveis. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19529, 4, "Então o Senhor me dirigiu a palavra, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19530, 5, """Assim diz o Senhor, o Deus de Israel: ‘Considero como esses figos bons os exilados de Judá, os quais expulsei deste lugar para a terra dos babilônios, a fim de fazer-lhes bem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19531, 6, "Olharei favoravelmente para eles, e não os trarei de volta a esta terra. Eu os edificarei e não os derrubarei; eu os plantarei e não os arrancarei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19532, 7, "Eu lhes darei coração capaz de conhecer-me e de saber que eu sou o Senhor. Serão o meu povo, e eu serei o seu Deus, pois eles se voltarão para mim de todo o coração.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19533, 8, """ ‘Mas como se faz com os figos ruins e intragáveis’, diz o Senhor, ‘assim lidarei com Zedequias, rei de Judá, com os seus líderes e com os sobreviventes de Jerusalém, tanto os que permanecem nesta terra como os que vivem no Egito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19534, 9, "Eu os tornarei objeto de terror e de desgraça para todos os reinos da terra. Para onde quer que eu os expulsar, serão uma afronta e servirão de exemplo, ridículo e maldição.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19535, 10, "Enviarei contra eles a guerra, a fome e a peste até que sejam eliminados da terra que dei a eles e aos seus antepassados’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19536, 1, "A palavra veio a Jeremias a respeito de todo o povo de Judá no quarto ano de Jeoaquim, filho de Josias, rei de Judá, que foi o primeiro ano de Nabucodonosor, rei da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19537, 2, "O que o profeta Jeremias anunciou a todo o povo de Judá e aos habitantes de Jerusalém foi isto:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19538, 3, "Durante vinte e três anos a palavra do Senhor tem vindo a mim, desde o décimo terceiro ano de Josias, filho de Amom, rei de Judá, até o dia de hoje. E eu a tenho anunciado a vocês, dia após dia, mas vocês não me deram ouvidos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19539, 4, "Embora o Senhor tenha enviado a vocês os seus servos, os profetas, dia após dia, vocês não os ouviram nem lhes deram atenção,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19540, 5, "quando disseram: ""Converta-se cada um do seu caminho mau e de suas más obras, e vocês permanecerão na terra que o Senhor deu a vocês e aos seus antepassados para sempre.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19541, 6, "Não sigam outros deuses para prestar-lhes culto e adorá-los; não provoquem a minha ira com ídolos feitos por vocês. E eu não trarei desgraça sobre vocês"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19542, 7, """Mas vocês não me deram ouvidos e me provocaram à ira com os ídolos que vocês fizeram, trazendo desgraça sobre si mesmos"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19543, 8, "Portanto, assim diz o Senhor dos Exércitos: ""Visto que vocês não ouviram as minhas palavras,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19544, 9, "convocarei todos os povos do norte e o meu servo Nabucodonosor, rei da Babilônia"", declara o Senhor, ""e os trarei para atacar esta terra, os seus habitantes e todas as nações ao redor. Eu os destruirei completamente e os farei um objeto de pavor e de zombaria, e uma ruína permanente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19545, 10, "Darei fim às vozes de júbilo e de alegria, às vozes do noivo e da noiva, ao som do moinho e à luz das candeias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19546, 11, "Toda esta terra se tornará uma ruína desolada, e essas nações estarão sujeitas ao rei da Babilônia durante setenta anos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19547, 12, """Quando se completarem os setenta anos, castigarei o rei da Babilônia e a sua nação, a terra dos babilônios, por causa de suas iniqüidades"", declara o Senhor, ""e a deixarei arrasada para sempre.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19548, 13, "Cumprirei naquela terra tudo o que falei contra ela, tudo o que está escrito neste livro e que Jeremias profetizou contra todas as nações.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19549, 14, "Porque os próprios babilônios serão escravizados por muitas nações e grandes reis; eu lhes retribuirei conforme as suas ações e as suas obras"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19550, 15, "Assim me disse o Senhor, o Deus de Israel: ""Pegue de minha mão este cálice com o vinho da minha ira e faça com que bebam dele todas as nações a quem eu o envio.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19551, 16, "Quando o beberem, ficarão cambaleando, enlouquecidos por causa da espada que enviarei entre elas"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19552, 17, "Então peguei o cálice da mão do Senhor, e fiz com que dele bebessem todas as nações às quais o Senhor me enviou:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19553, 18, "Jerusalém e as cidades de Judá, seus reis e seus líderes, para fazer deles uma desolação e um objeto de pavor, zombaria e maldição, como hoje acontece;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19554, 19, "o faraó, o rei do Egito, seus conselheiros e seus líderes, todo o seu povo,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19555, 20, "e todos os estrangeiros que lá residem; todos os reis de Uz; todos os reis dos filisteus: de Ascalom, Gaza, Ecrom e o povo que restou em Asdode;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19556, 21, "Edom, Moabe e os amonitas,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19557, 22, "os reis de Tiro e de Sidom; os reis das ilhas e das terras de além do mar;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19558, 23, "Dedã, Temá, Buz e todos os que rapam a cabeça;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19559, 24, "e os reis da Arábia e todos os reis dos estrangeiros que vivem no deserto;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19560, 25, "todos os reis de Zinri, de Elão e da Média;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19561, 26, "e todos os reis do norte, próximos ou distantes, um após outro; e todos os reinos da face da terra. Depois de todos eles, o rei de Sesaque também beberá do cálice.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19562, 27, """A seguir diga-lhes: ‘Assim diz o Senhor dos Exércitos, o Deus de Israel: Bebam, embriaguem-se, vomitem, caiam e não mais se levantem, por causa da espada que envio no meio de vocês’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19563, 28, "Mas se eles se recusarem a beber, diga-lhes: ‘Assim diz o Senhor dos Exércitos: Vocês vão bebê-lo!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19564, 29, "Começo a trazer desgraça sobre a cidade que leva o meu nome; e vocês sairiam impunes? Sem dúvida não ficarão sem castigo! Estou trazendo a espada contra todos os habitantes da terra’, declara o Senhor dos Exércitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19565, 30, """E você, profetize todas estas palavras contra eles, dizendo: "" ‘O Senhor ruge do alto; troveja de sua santa morada; ruge poderosamente contra a sua propriedade. Ele grita como os que pisam as uvas; grita contra todos os habitantes da terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19566, 31, "Um tumulto ressoa até os confins da terra, pois o Senhor faz acusações contra as nações, e julga toda a humanidade: ele entregará os ímpios à espada’, declara o Senhor. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19567, 32, "Assim diz o Senhor: ""Vejam! A desgraça está se espalhando de nação em nação; uma terrível tempestade se levanta desde os confins da terra"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19568, 33, "Naquela dia, os mortos pelo Senhor estarão em todo lugar, de um lado ao outro da terra. Ninguém pranteará por eles, e não serão recolhidos e sepultados, mas servirão de esterco sobre o solo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19569, 34, "Lamentem-se e gritem, pastores! Rolem no pó, chefes do rebanho! Porque chegou para vocês o dia da matança e da sua dispersão; vocês cairão e serão esmigalhados como vasos finos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19570, 35, "Não haverá refúgio para os pastores nem escapatória para os chefes do rebanho.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19571, 36, "Ouvem-se os gritos dos pastores e o lamento dos chefes do rebanho, pois o Senhor está destruindo as pastagens deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19572, 37, "Os pastos tranqüilos estão devastados por causa do fogo da ira do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19573, 38, "Como um leão, ele saiu de sua toca; a terra deles ficou devastada, por causa da espada do opressor e do fogo de sua ira.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19574, 1, "No início do reinado de Jeoaquim, filho de Josias, rei de Judá, veio esta palavra da parte do Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19575, 2, """Assim diz o Senhor: Coloque-se no pátio do templo do Senhor e fale a todo o povo das cidades de Judá que vem adorar no templo do Senhor. Diga-lhes tudo o que eu lhe ordenar; não omita uma só palavra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19576, 3, "Talvez eles escutem e cada um se converta de sua má conduta. Então eu me arrependerei e não trarei sobre eles a desgraça que estou planejando por causa do mal que eles têm praticado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19577, 4, "Diga-lhes: ‘Assim diz o Senhor: Se vocês não me escutarem nem seguirem a minha lei, que dei a vocês,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19578, 5, "e se não ouvirem as palavras dos meus servos, os profetas, os quais tenho enviado a vocês vez após vez, embora vocês não os tenham ouvido,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19579, 6, "então farei deste templo o que fiz do santuário de Siló, e desta cidade um objeto de maldição entre todas as nações da terra’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19580, 7, "Os sacerdotes, os profetas e todo o povo ouviram Jeremias falar essas palavras no templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19581, 8, "E assim que Jeremias acabou de dizer ao povo tudo o que o Senhor lhe tinha ordenado, os sacerdotes, os profetas e todo o povo o prenderam e disseram: ""Você certamente morrerá!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19582, 9, "Por que você profetiza em nome do Senhor e afirma que este templo será como Siló e que esta cidade ficará arrasada e abandonada? "" E todo o povo se ajuntou em volta de Jeremias no templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19583, 10, "Quando os líderes de Judá souberam disso, foram do palácio real até o templo do Senhor e se assentaram para julgar, à entrada da porta Nova do templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19584, 11, "E os sacerdotes e os profetas disseram aos líderes e a todo o povo: ""Este homem deve ser condenado à morte porque profetizou contra esta cidade. Vocês o ouviram com os seus próprios ouvidos! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19585, 12, "Disse então Jeremias a todos os líderes e a todo o povo: ""O Senhor enviou-me para profetizar contra este templo e contra esta cidade tudo o que vocês ouviram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19586, 13, "Agora, corrijam a sua conduta e as suas ações e obedeçam ao Senhor, ao seu Deus. Então o Senhor se arrependerá da desgraça que pronunciou contra vocês.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19587, 14, "Quanto a mim, estou nas mãos de vocês; façam comigo o que acharem bom e certo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19588, 15, "Entretanto, estejam certos de que, se me matarem, vocês, esta cidade e os seus habitantes, serão responsáveis por derramar sangue inocente, pois, na verdade, o Senhor enviou-me a vocês para anunciar-lhes essas palavras"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19589, 16, "Então os líderes e todo o povo disseram aos sacerdotes e aos profetas: ""Este homem não deve ser condenado à morte! Ele nos falou em nome do Senhor, do nosso Deus"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19590, 17, "Alguns dos líderes da terra se levantaram e disseram a toda a assembléia do povo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19591, 18, """Miquéias de Moresete profetizou nos dias de Ezequias, rei de Judá, dizendo a todo o povo de Judá: ‘Assim diz Senhor dos Exércitos: "" ‘Sião será arada como um campo. Jerusalém se tornará um monte de entulho, a colina do templo um monte coberto de mato’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19592, 19, """Acaso Ezequias, rei de Judá, ou alguém do povo de Judá o matou? Ezequias não temeu ao Senhor e não buscou o seu favor? E o Senhor não se arrependeu da desgraça que pronunciara contra eles? Estamos a ponto de trazer uma terrível desgraça sobre nós! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19593, 20, "Outro homem que profetizou em nome do Senhor foi Urias, filho de Semaías, de Quiriate-Jearim. Ele profetizou contra esta cidade e contra esta terra as mesmas coisas anunciadas por Jeremias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19594, 21, "Ouvindo isso, o rei Jeoaquim, todos os seus homens de guerra e os seus oficiais, o rei procurou matá-lo. Quando Urias soube disso, teve medo e fugiu para o Egito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19595, 22, "Mas o rei Jeoaquim mandou ao Egito Elnatã, filho de Acbor, e com ele alguns homens,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19596, 23, "os quais trouxeram Urias do Egito e o levaram ao rei Jeoaquim, que o mandou matar à espada. Depois, jogaram o corpo dele numa vala comum.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19597, 24, "Mas Aicam, filho de Safã, protegeu Jeremias, impedindo que ele fosse entregue ao povo para ser executado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19598, 1, "No início do reinado de Zedequias, filho de Josias, rei de Judá, veio esta palavra a Jeremias da parte do Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19599, 2, "Assim me ordenou o Senhor: ""Faça para você um jugo com cordas e madeira e ponha-o sobre o pescoço.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19600, 3, "Depois mande uma mensagem aos reis de Edom, de Moabe, de Amom, de Tiro e de Sidom, por meio dos embaixadores que vieram a Jerusalém para ver Zedequias, rei de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19601, 4, "Esta é a mensagem que deverão transmitir aos seus senhores: Assim diz o Senhor dos Exércitos, o Deus de Israel:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19602, 5, "‘Eu fiz a terra, os seres humanos e os animais que nela estão, com o meu grande poder e com meu braço estendido, e eu a dou a quem eu quiser.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19603, 6, "Agora, sou eu mesmo que entrego todas essas nações nas mãos do meu servo Nabucodonosor, rei da Babilônia; sujeitei a ele até mesmo os animais selvagens.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19604, 7, "Todas as nações estarão sujeitas a ele, a seu filho e a seu neto; até que chegue a hora em que a terra dele seja subjugada por muitas nações e por reis poderosos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19605, 8, """ ‘Se, porém, alguma nação ou reino não se sujeitar a Nabucodonosor, rei da Babilônia, nem colocar o pescoço sob o seu jugo, eu castigarei aquela nação com a guerra, a fome e a peste’, declara o Senhor, ‘e por meio dele eu a destruirei completamente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19606, 9, "Não ouçam os seus profetas, os seus adivinhos, os seus intérpretes de sonhos, os seus médiuns e os seus feiticeiros, os quais lhes dizem para não se sujeitar ao rei da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19607, 10, "Porque são mentiras o que eles profetizam para vocês, o que os levará para longe de sua terra. Eu banirei vocês, e vocês perecerão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19608, 11, "Mas, se alguma nação colocar o pescoço sob o jugo do rei da Babilônia e a ele se sujeitar, então deixarei aquela nação permanecer na sua própria terra para cultivá-la e nela viver’, declara o Senhor"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19609, 12, "Entreguei a mesma mensagem a Zedequias, rei de Judá, dizendo-lhe: Coloquem o pescoço sob o jugo do rei da Babilônia, sujeitem-se a ele e ao seu povo, e vocês viverão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19610, 13, "Por que razão você e o seu povo morreriam pela guerra, pela fome e pela peste, com as quais o Senhor ameaça a nação que não se sujeitar ao rei da Babilônia?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19611, 14, "Não dêem atenção às palavras dos profetas que dizem que vocês não devem sujeitar-se ao rei da Babilônia; estão profetizando mentiras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19612, 15, """Eu não os enviei! "", declara o Senhor. ""Eles profetizam mentiras em meu nome. Por isso, eu banirei vocês, e vocês perecerão juntamente com os profetas que lhes estão profetizando"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19613, 16, "Então eu disse aos sacerdotes e a todo este povo: Assim diz o Senhor: ""Não ouçam os seus profetas que dizem que em breve os utensílios do templo do Senhor serão trazidos de volta da Babilônia. Eles estão profetizando mentiras"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19614, 17, "Não os ouçam. Sujeitem-se ao rei da Babilônia, e vocês viverão. Por que deveria esta cidade ficar em ruínas?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19615, 18, "Se eles são profetas e têm a palavra do Senhor, que implorem ao Senhor dos Exércitos, pedindo que os utensílios que restam no templo do Senhor no palácio do rei de Judá e em Jerusalém não sejam levados para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19616, 19, "Porque assim diz o Senhor dos Exércitos acerca das colunas, do tanque, dos suportes e dos outros utensílios que foram deixados nesta cidade,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19617, 20, "os quais Nabucodonosor, rei da Babilônia, não levou consigo de Jerusalém para a Babilônia, quando exilou Joaquim, filho de Jeoaquim, rei de Judá, com os nobres de Judá e de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19618, 21, "Sim, assim diz o Senhor dos Exércitos, Deus de Israel, acerca dos utensílios que restaram no templo do Senhor e no palácio do rei de Judá e em Jerusalém:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19619, 22, """Serão levados para a Babilônia e ali ficarão até o dia em que eu os quiser buscar"", declara o Senhor. ""Então os trarei de volta e os restabelecerei a este lugar"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19620, 1, "No quinto mês daquele mesmo ano, o quarto ano, no início do reinado de Zedequias, rei de Judá, Hananias, filho de Azur, profeta natural de Gibeom, disse-me no templo do Senhor, na presença dos sacerdotes e de todo o povo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19621, 2, """Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Quebrarei o jugo do rei da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19622, 3, "Em dois anos trarei de volta a este lugar todos os utensílios do templo do Senhor que Nabucodonosor, rei da Babilônia, tirou daqui e levou para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19623, 4, "Também trarei de volta para este lugar Joaquim, filho de Jeoaquim, rei de Judá, e todos os exilados de Judá que foram para a Babilônia’, diz o Senhor, ‘pois quebrarei o jugo do rei da Babilônia’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19624, 5, "Então o profeta Jeremias respondeu ao profeta Hananias diante dos sacerdotes e de todo o povo que estava no templo do Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19625, 6, """Amém! Que assim faça o Senhor! Que o Senhor cumpra as palavras que você profetizou, trazendo os utensílios do templo do Senhor e todos os exilados da Babilônia para este lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19626, 7, "Entretanto, ouça o que tenho a dizer a você e a todo o povo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19627, 8, "Os profetas que precederam a você e a mim, desde os tempos antigos, profetizaram guerra, desgraça e peste contra muitas nações e grandes reinos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19628, 9, "Mas o profeta que profetiza prosperidade será reconhecido como verdadeiro enviado do Senhor se aquilo que profetizou se realizar"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19629, 10, "Então o profeta Hananias tirou o jugo do pescoço de Jeremias e o quebrou,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19630, 11, "e disse diante de todo o povo: ""Assim diz o Senhor: ‘É deste modo que quebrarei o jugo de Nabucodonosor, rei da Babilônia, e o tirarei do pescoço de todas as nações no prazo de dois anos’ "". Diante disso, o profeta Jeremias retirou-se.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19631, 12, "Depois que o profeta Hananias quebrou o jugo do pescoço do profeta Jeremias, o Senhor dirigiu a palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19632, 13, """Vá dizer a Hananias: ‘Assim diz o Senhor: Você quebrou um jugo de madeira, mas em seu lugar você fará um jugo de ferro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19633, 14, "Assim diz o Senhor dos Exércitos, o Deus de Israel: Porei um jugo sobre o pescoço de todas essas nações, para fazê-las sujeitas a Nabucodonosor, rei da Babilônia, e elas se sujeitarão a ele. Até mesmo os animais selvagens estarão sujeitos a ele’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19634, 15, "Disse, pois, o profeta Jeremias ao profeta Hananias: ""Escute, Hananias! O Senhor não o enviou, mas assim mesmo você persuadiu esta nação a confiar em mentiras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19635, 16, "Por isso, assim diz Senhor: ‘Vou tirá-lo da face da terra. Este ano você morrerá, porque pregou rebelião contra o Senhor’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19636, 17, "E o profeta Hananias morreu no sétimo mês daquele mesmo ano.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19637, 1, "Este é o conteúdo da carta que o profeta Jeremias enviou de Jerusalém aos líderes, que ainda restavam entre os exilados, aos sacerdotes, aos profetas e a todo o povo que Nabucodonosor deportara de Jerusalém para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19638, 2, "Isso aconteceu depois que o rei Joaquim e a rainha-mãe, os oficiais do palácio real, os líderes de Judá e Jerusalém, os artesãos e os artífices foram sido deportados de Jerusalém para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19639, 3, "Ele enviou a carta por intermédio de Eleasá, filho de Safã, e Gemarias, filho de Hilquias, os quais Zedequias, rei de Judá, mandou a Nabucodonosor, rei da Babilônia. A carta dizia o seguinte:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19640, 4, "Assim diz o Senhor dos Exércitos, o Deus de Israel, a todos os exilados, que deportei de Jerusalém para a Babilônia:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19641, 5, """Construam casas e habitem nelas; plantem jardins e comam de seus frutos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19642, 6, "Casem-se e tenham filhos e filhas; escolham mulheres para casar-se com seus filhos e dêem as suas filhas em casamento, para que também tenham filhos e filhas. Multipliquem-se e não diminuam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19643, 7, "Busquem a prosperidade da cidade para a qual eu os deportei e orem ao Senhor em favor dela, porque a prosperidade de vocês depende da prosperidade dela"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19644, 8, "Porque assim diz o Senhor dos Exércitos, o Deus de Israel: ""Não deixem que os profetas e adivinhos que há no meio de vocês os enganem. Não dêem atenção aos sonhos que vocês os encorajam a terem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19645, 9, "Eles estão profetizando mentiras em meu nome. Eu não os enviei"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19646, 10, "Assim diz o Senhor: ""Quando se completarem os setenta anos da Babilônia, eu cumprirei a minha promessa em favor de vocês, de trazê-los de volta para este lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19647, 11, "Porque sou eu que conheço os planos que tenho para vocês"", diz o Senhor, ""planos de fazê-los prosperar e não de lhes causar dano, planos de dar-lhes esperança e um futuro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19648, 12, "Então vocês clamarão a mim, virão orar a mim, e eu os ouvirei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19649, 13, "Vocês me procurarão e me acharão quando me procurarem de todo o coração.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19650, 14, "Eu me deixarei ser encontrado por vocês"", declara o Senhor, ""e os trarei de volta do cativeiro. Eu os reunirei de todas as nações e de todos os lugares para onde eu os dispersei, e os trarei de volta para o lugar de onde os deportei"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19651, 15, "Vocês podem dizer: ""O Senhor levantou profetas para nós na Babilônia"",", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19652, 16, "mas assim diz o Senhor sobre o rei que se assenta no trono de Davi e sobre todo o povo que permanece nesta cidade, seus compatriotas que não foram com vocês para o exílio;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19653, 17, "assim diz o Senhor dos Exércitos: ""Enviarei a guerra, a fome e a peste contra eles; lidarei com eles como se lida com figos ruins, que são intragáveis.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19654, 18, "Eu os perseguirei com a guerra, a fome e a peste; farei deles objeto de terror para todos os reinos da terra, maldição e exemplo, zombaria e afronta entre todas as nações para onde eu os dispersei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19655, 19, "Porque eles não deram atenção às minhas palavras"", declara o Senhor, ""palavras que lhes enviei pelos meus servos, os profetas. E vocês também não deram atenção! "", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19656, 20, "Ouçam, agora, a palavra do Senhor, todos vocês exilados, que deportei de Jerusalém para a Babilônia!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19657, 21, "Assim diz o Senhor dos Exércitos, o Deus de Israel, a respeito de Acabe, filho de Colaías, e a respeito de Zedequias, filho de Maaséias, os quais estão profetizando mentiras a vocês em meu nome: ""Eu os entregarei nas mãos de Nabucodonosor, rei da Babilônia, e ele os matará diante de vocês.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19658, 22, "Em razão disso, os exilados de Judá que estão na Babilônia usarão esta maldição: ‘Que o Senhor o trate como tratou Zedequias e Acabe, os quais o rei da Babilônia queimou vivos’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19659, 23, "Porque cometeram loucura em Israel: adulteraram com as mulheres de seus amigos e em meu nome falaram mentiras, que eu não ordenei que falassem. Mas eu estou sabendo; sou testemunha disso"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19660, 24, "Diga a Semaías, de Neelam:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19661, 25, """Assim diz o Senhor dos Exércitos, o Deus de Israel: Você enviou cartas em seu próprio nome a todo o povo de Jerusalém, a Sofonias, filho do sacerdote Maaséias, e a todos os sacerdotes. Você disse a Sofonias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19662, 26, "‘O Senhor o designou sacerdote em lugar de Joiada como encarregado do templo do Senhor; você deveria prender no tronco, com correntes de ferro, qualquer doido que agisse como profeta.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19663, 27, "E por que você não repreendeu Jeremias de Anatote, que se apresenta como profeta entre vocês?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19664, 28, "Ele até mandou esta mensagem para nós que estamos na Babilônia: ""O exílio será longo. Construam casas e habitem nelas; plantem jardins e comam de seus frutos"" ’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19665, 29, "O sacerdote Sofonias leu a carta ao profeta Jeremias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19666, 30, "Então o Senhor dirigiu a palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19667, 31, """Envie esta mensagem a todos os exilados: ‘Assim diz o Senhor sobre Semaías, de Neelam: Embora eu não o tenha enviado, Semaías profetizou a vocês e fez com que vocês cressem numa mentira,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19668, 32, "por isso, assim diz o Senhor: Castigarei Semaías, de Neelam, e os seus descendentes. Não lhe restará ninguém entre este povo, e ele não verá as coisas boas que vou fazer em favor de meu povo’, declara o Senhor, ‘porque ele pregou rebelião contra o Senhor’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19669, 1, "Esta é a palavra que veio a Jeremias da parte do Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19670, 2, """Assim diz o Senhor, o Deus de Israel: ‘Escreva num livro todas as palavras que eu lhe falei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19671, 3, "Certamente vêm os dias’, diz o Senhor, ‘em que mudarei a sorte do meu povo, Israel e Judá, e os farei retornar à terra que dei aos seus antepassados, e eles a possuirão’, declara o Senhor"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19672, 4, "Estas são as palavras que o Senhor falou acerca de Israel e de Judá:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19673, 5, """Assim diz o Senhor: "" ‘Ouvem-se gritos de pânico, de pavor e não de paz.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19674, 6, "Pergunte e veja: Pode um homem dar à luz? Por que vejo, então, todos os homens com as mãos no estômago, como uma mulher em trabalho de parto? Por que estão pálidos todos os rostos?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19675, 7, "Como será terrível aquele dia! Sem comparação! Será tempo de angústia para Jacó; mas ele será salvo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19676, 8, """ ‘Naquele dia’, declara o Senhor dos Exércitos, ‘quebrarei o jugo que está sobre o pescoço deles e arrebentarei as suas correntes; não mais serão escravizados pelos estrangeiros.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19677, 9, "Servirão ao Senhor, ao seu Deus, e a Davi, seu rei, que darei a eles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19678, 10, """ ‘Por isso, não tema Jacó, meu servo! Não fique assustado, ó Israel! ’, declara o Senhor. ‘Eu o salvarei de um lugar distante, e os seus descendentes, da terra do seu exílio. Jacó voltará e ficará em paz e em segurança; ninguém o inquietará.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19679, 11, "Porque eu estou com você e o salvarei’, diz o Senhor. ‘Destruirei completamente todas as nações entre as quais eu o dispersei; mas a você não destruirei completamente. Eu o disciplinarei, como você merece. Não o deixarei impune. ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19680, 12, """Assim diz o Senhor: "" ‘Seu ferimento é grave, sua ferida, incurável.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19681, 13, "Não há quem defenda a sua causa; não há remédio para a sua ferida, que não cicatriza.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19682, 14, "Todos os seus amantes esqueceram-se de você; eles não se importam com você. Eu a golpeei como faz um inimigo; dei-lhe um castigo cruel, porque é grande a sua iniqüidade e numerosos são os seus pecados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19683, 15, "Por que você grita por causa do seu ferimento, por sua ferida incurável? Fiz essas coisas a você porque é grande a sua iniqüidade e numerosos são os seus pecados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19684, 16, """ ‘Mas todos os que a devoram serão devorados; todos os seus adversários irão para o exílio. Aqueles que a saqueiam serão saqueados; eu despojarei todos os que a despojam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19685, 17, "Farei cicatrizar o seu ferimento e curarei as suas feridas’, declara o Senhor, ‘porque a você, Sião, chamam de rejeitada, aquela por quem ninguém se importa’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19686, 18, """Assim diz o Senhor: "" ‘Mudarei a sorte das tendas de Jacó e terei compaixão das suas moradas. A cidade será reconstruída sobre as suas ruínas e o palácio no seu devido lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19687, 19, "Deles virão ações de graça e o som de regozijo. Eu os farei aumentar e eles não diminuirão; eu os honrarei e eles não serão desprezados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19688, 20, "Seus filhos serão como nos dias do passado, e a sua comunidade será firmada diante de mim; castigarei todos aqueles que os oprimem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19689, 21, "Seu líder será um dentre eles; seu governante virá do meio deles. Eu o trarei para perto e ele se aproximará de mim; pois quem se arriscaria a aproximar-se de mim? ’, pergunta o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19690, 22, "Por isso vocês serão o meu povo, e eu serei o seu Deus’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19691, 23, "Vejam, a tempestade do Senhor! Sua fúria está a solta! Um vendaval vem sobre a cabeça dos ímpios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19692, 24, "A ira do Senhor não se afastará até que ele tenha completado os seus propósitos. Em dias vindouros vocês compreenderão isso.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19693, 1, """Naquele tempo"", diz o Senhor, ""serei o Deus de todas as famílias de Israel, e eles serão o meu povo. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19694, 2, "Assim diz o Senhor: ""O povo que escapou da morte achou favor no deserto"". Quando Israel buscava descanso,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19695, 3, "o Senhor lhe apareceu no passado, dizendo: ""Eu a amei com amor eterno; com amor leal a atrai.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19696, 4, "Eu a edificarei mais uma vez, ó virgem, Israel! Você será reconstruída! Mais uma vez você se enfeitará com guizos e sairá dançando com os que se alegram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19697, 5, "De novo você plantará videiras nas colinas de Samaria; videiras que os lavradores que as tinham plantado antes profanaram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19698, 6, "Porque vai chegando o dia em que os sentinelas gritarão nas colinas de Efraim: ‘Venham e subamos a Sião, à presença do Senhor, do nosso Deus’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19699, 7, "Assim diz o Senhor: ""Cantem de alegria por causa de Jacó; gritem, exaltando a principal das nações! Proclamem e dêem louvores dizendo: ‘O Senhor salvou o seu povo, o remanescente de Israel’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19700, 8, "Vejam, eu os trarei da terra do norte e os reunirei dos confins da terra. Entre eles estarão o cego e o aleijado, mulheres grávidas e em trabalho de parto; uma grande multidão voltará.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19701, 9, "Voltarão com choro, mas eu os conduzirei em consolações. Eu os conduzirei às correntes de água por um caminho plano, onde não tropeçarão, porque sou pai para Israel e Efraim é o meu filho mais velho.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19702, 10, """Ouçam a palavra do Senhor, ó nações, e proclamem nas ilhas distantes: ‘Aquele que dispersou Israel os reunirá e, como pastor, vigiará o seu rebanho’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19703, 11, "O Senhor resgatou Jacó e o libertou das mãos do que é mais forte do que ele.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19704, 12, "Eles virão e cantarão de alegria nos altos de Sião; ficarão radiantes de alegria pelos muitos bens dados pelo Senhor: o cereal, o vinho novo, o azeite puro, as crias das ovelhas e das vacas. Serão como um jardim bem regado, e não mais se entristecerão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19705, 13, "Então as moças dançarão de alegria, como também os jovens e os velhos. Transformarei o lamento deles em júbilo; eu lhes darei consolo e alegria em vez de tristeza.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19706, 14, "Satisfarei os sacerdotes com fartura; e o meu povo será saciado pela minha bondade"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19707, 15, "Assim diz o Senhor: ""Ouve-se uma voz em Ramá, pranto e amargo choro; é Raquel que chora por seus filhos e recusa ser consolada, porque os seus filhos já não existem"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19708, 16, "Assim diz o Senhor: ""Contenha o seu choro e as suas lágrimas, pois o seu sofrimento será recompensado"", declara o Senhor. ""Eles voltarão da terra do inimigo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19709, 17, "Por isso há esperança para o seu futuro"", declara o Senhor. ""Seus filhos voltarão para a sua pátria.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19710, 18, """Ouvi claramente Efraim lamentando-se: ‘Tu me disciplinaste como a um bezerro indomado, e fui disciplinado. Traze-me de volta, e voltarei, porque tu és o Senhor, o meu Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19711, 19, "De fato, depois de desviar-me, eu me arrependi; depois que entendi, bati no meu peito. Estou envergonhado e humilhado porque trago sobre mim a desgraça da minha juventude’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19712, 20, "Não é Efraim o meu filho querido? O filho em quem tenho prazer? Cada vez que eu falo sobre ele, mais intensamente me lembro dele. Por isso o meu coração por ele anseia; tenho por ele grande compaixão"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19713, 21, """Coloque marcos e ponha sinais nas estradas, Preste atenção no caminho que você trilhou. Volte, ó Virgem, Israel! Volte para as suas cidades.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19714, 22, "Até quando você vagará, ó filha rebelde? O Senhor criou algo novo nesta terra: uma mulher abraça um guerreiro. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19715, 23, "Assim diz o Senhor dos Exércitos, o Deus de Israel: ""Quando eu os trouxer de volta do cativeiro, o povo de Judá e de suas cidades dirá novamente: ‘O Senhor a abençoe, ó morada justa, ó monte sagrado’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19716, 24, "O povo viverá em Judá e em todas as suas cidades, tanto os lavradores como os que conduzem os rebanhos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19717, 25, "Restaurarei o exausto e saciarei o enfraquecido"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19718, 26, "Então acordei e olhei em redor. Meu sono tinha sido agradável.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19719, 27, """Virão dias"", diz o Senhor, ""em que semearei na comunidade de Israel e na comunidade de Judá homens e animais.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19720, 28, "Assim como os vigiei para arrancar e despedaçar, para derrubar, destruir e trazer a desgraça, também os vigiarei para edificar e plantar"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19721, 29, """Naqueles dias não se dirá mais: ‘Os pais comeram uvas verdes, e os dentes dos filhos se mancharam’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19722, 30, "Ao contrário, cada um morrerá por causa do seu próprio pecado. Os dentes de todo aquele que comer uvas verdes se mancharam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19723, 31, """Estão chegando os dias"", declara o Senhor, ""quando farei uma nova aliança com a comunidade de Israel e com a comunidade de Judá"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19724, 32, """Não será como a aliança que fiz com os seus antepassados quando os tomei pela mão para tirá-los do Egito; porque quebraram a minha aliança, apesar de eu ser o Senhor deles"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19725, 33, """Esta é a aliança que farei com a comunidade de Israel depois daqueles dias"", declara o Senhor: ""Porei a minha lei no íntimo deles e a escreverei nos seus corações. Serei o Deus deles, e eles serão o meu povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19726, 34, "Ninguém mais ensinará ao seu próximo nem ao seu irmão, dizendo: ‘Conheça ao Senhor’, porque todos eles me conhecerão, desde o menor até o maior"", diz o Senhor. ""Porque eu lhes perdoarei a maldade e não me lembrarei mais dos seus pecados. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19727, 35, "Assim diz o Senhor, aquele que designou o sol para brilhar de dia, que decretou que a lua e as estrelas brilhem de noite, que agita o mar para que as suas ondas rujam; o seu nome é o Senhor dos Exércitos:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19728, 36, """Somente se esses decretos desaparecerem de diante de mim"", declara o Senhor, ""deixarão os descendentes de Israel de ser uma nação diante de mim para sempre"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19729, 37, "Assim diz o Senhor: ""Se os céus em cima puderem ser medidos, e os alicerces da terra embaixo puderem ser sondados, então eu rejeitarei os descendentes de Israel, por causa de tudo o que eles têm feito"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19730, 38, """Estão chegando os dias"", declara o Senhor, ""em que esta cidade será reconstruída para o Senhor, desde a torre de Hananeel até à porta da Esquina.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19731, 39, "A corda de medir será estendida diretamente até a colina de Garebe, indo na direção de Goa.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19732, 40, "Todo o vale, onde cadáveres e cinzas são jogados, e todos os terraços que dão para o vale do Cedrom a leste, até a esquina da porta dos Cavalos, serão consagrados ao Senhor. A cidade nunca mais será arrasada ou destruída. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19733, 1, "Esta é a palavra que o Senhor dirigiu a Jeremias no décimo ano do reinado de Zedequias, rei de Judá, que foi o ano décimo oitavo de Nabucodonosor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19734, 2, "Naquela época, o exército do rei da Babilônia sitiava Jerusalém e o profeta Jeremias estava preso no pátio da guarda, no palácio real de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19735, 3, "Zedequias, rei de Judá, havia aprisionado Jeremias acusando-o de profetizar que o Senhor iria entregar a cidade nas mãos do rei da Babilônia, e que este a conquistaria;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19736, 4, "que Zedequias, rei de Judá, não escaparia das mãos dos babilônios, mas certamente seria entregue nas mãos do rei da Babilônia, falaria com ele face a face, e o veria com os próprios olhos;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19737, 5, "e que ele levaria Zedequias para a Babilônia, onde este ficaria até que o Senhor cuidasse da situação dele; e, ainda, que se eles lutassem contra os babilônios não seriam bem-sucedidos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19738, 6, "E Jeremias disse: ""O Senhor dirigiu-me a palavra nos seguintes termos:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19739, 7, "Hanameel, filho de seu tio Salum, virá ao seu encontro e dirá: ‘Compre a propriedade que tenho em Anatote, porque, sendo o parente mais próximo, você tem o direito e o dever de comprá-la’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19740, 8, """Conforme o Senhor tinha dito, meu primo Hanameel veio ao meu encontro no pátio da guarda e disse: ‘Compre a propriedade que tenho em Anatote, no território de Benjamim, porque é seu o direito de posse e de resgate. Compre-a! ’ ""Então, compreendi que essa era a palavra do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19741, 9, "Assim, comprei do meu primo Hanameel a propriedade que ele possuía em Anatote. Pesei a prata e lhe paguei dezessete peças de prata.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19742, 10, "Assinei e selei a escritura, e pesei a prata na balança, diante de testemunhas por mim chamadas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19743, 11, "Peguei a escritura, a cópia selada com os termos e condições da compra, bem como a cópia não selada,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19744, 12, "e entreguei essa escritura de compra a Baruque, filho de Nerias, filho de Maaséias, na presença de meu primo Hanameel, das testemunhas que tinham assinado a escritura e de todos os judeus que estavam sentados no pátio da guarda.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19745, 13, """Na presença deles dei as seguintes instruções a Baruque:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19746, 14, "‘Assim diz o Senhor dos Exércitos, Deus de Israel: Tome estes documentos, tanto a cópia selada como a não selada da escritura de compra, e coloque-os num jarro de barro para que se conservem por muitos anos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19747, 15, "Porque assim diz o Senhor dos Exércitos, Deus de Israel: Casas, campos e vinhas tornarão a ser comprados nesta terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19748, 16, """Depois que entreguei a escritura de compra a Baruque, filho de Nerias, orei ao Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19749, 17, """Ah! Soberano Senhor, tu fizeste os céus e a terra pelo teu grande poder e por teu braço estendido. Nada é difícil demais para ti.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19750, 18, "Mostras bondade até mil gerações, mas lanças os pecados dos pais sobre os seus filhos. Ó grande e poderoso Deus, cujo nome é o Senhor dos Exércitos,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19751, 19, "grandes são os teus propósitos e poderosos os teus feitos. Os teus olhos estão atentos aos atos dos homens; tu retribuis a cada um de acordo com a sua conduta, de acordo com os efeitos das suas obras.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19752, 20, "Realizaste sinais e maravilhas no Egito e continuas a fazê-los até hoje, tanto em Israel como entre toda a humanidade, e alcançaste o renome que hoje tens.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19753, 21, "Tiraste o teu povo do Egito com sinais e maravilhas, com mão poderosa e braço estendido, causando grande pavor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19754, 22, "Deste a eles esta terra, que sob juramento prometeste aos seus antepassados; uma terra onde manam leite e mel.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19755, 23, "Eles vieram e tomaram posse dela, mas não te obedeceram nem seguiram a tua lei. Não fizeram nada daquilo que lhes ordenaste. Por isso trouxeste toda esta desgraça sobre eles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19756, 24, """As rampas de cerco são erguidas pelos inimigos para tomarem a cidade, e pela guerra, pela fome e pela peste, ela será entregue nas mãos dos babilônios que a atacam. Cumpriu-se aquilo que disseste, como vês.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19757, 25, "Ainda assim, ó Soberano Senhor, tu me mandaste comprar a propriedade e convocar testemunhas do negócio, embora a cidade esteja entregue nas mãos dos babilônios! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19758, 26, "A palavra do Senhor veio a mim, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19759, 27, """Eu sou o Senhor, o Deus de toda a humanidade. Há alguma coisa difícil demais para mim?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19760, 28, "Portanto, assim diz o Senhor: ‘Estou entregando esta cidade nas mãos dos babilônios e de Nabucodonosor, rei da Babilônia, que a conquistará.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19761, 29, "Os babilônios, que estão atacando esta cidade, entrarão e a incendiarão. Eles a queimarão juntamente com as casas nas quais o povo provocou a minha ira queimando incenso a Baal nos seus terraços, e derramando ofertas de bebida em honra de outros deuses.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19762, 30, """Desde a sua juventude o povo de Israel e de Judá nada tem feito senão aquilo que eu considero mau; de fato, o povo de Israel nada tem feito além de provocar-me à ira"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19763, 31, "Desde o dia em que foi construída até hoje, esta cidade tem despertado o meu furor de tal forma que tenho que tirá-la da minha frente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19764, 32, "O povo de Israel e de Judá tem provocado a minha ira por causa de todo o mal que tem feito, tanto eles como os seus reis e os seus líderes, os seus sacerdotes e os seus profetas, os homens de Judá e os habitantes de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19765, 33, "Voltaram as costas para mim e não o rosto; embora eu os tenha ensinado vez após vez, não quiseram ouvir-me nem aceitaram a correção.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19766, 34, "Profanaram o templo que leva o meu nome, colocando nele as imagens de seus ídolos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19767, 35, "Construíram o alto para Baal no vale de Ben-Hinom, para sacrificarem a Moloque os seus filhos e as suas filhas, coisa que nunca ordenei, prática repugnante que jamais imaginei; e, assim, levaram Judá a pecar"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19768, 36, "Portanto, assim diz o Senhor a esta cidade, sobre a qual vocês estão dizendo que será entregue nas mãos dos babilônios por meio da guerra, da fome e da peste:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19769, 37, """Certamente eu os reunirei de todas as terras para onde os dispersei na minha ardente ira e no meu grande furor; eu os trarei de volta a este lugar e permitirei que vivam em segurança.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19770, 38, "Eles serão o meu povo, e eu serei o seu Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19771, 39, "Darei a eles um só pensamento e uma só conduta, para que me temam durante toda a sua vida, para o seu próprio bem e o de seus filhos e descendentes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19772, 40, "Farei com eles uma aliança permanente: Jamais deixarei de fazer o bem a eles, e farei com que me temam de coração, para que jamais se desviem de mim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19773, 41, "Terei alegria em fazer-lhes o bem, e os plantarei firmemente nesta terra de todo o meu coração e de toda a minha alma. Sim, é o que farei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19774, 42, """Assim diz o Senhor: Assim como eu trouxe toda esta grande desgraça sobre este povo, também lhes darei a prosperidade que lhes prometo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19775, 43, "De novo serão compradas propriedades nesta terra da qual vocês dizem: ‘É uma terra arrasada, sem homens nem animais, pois foi entregue nas mãos dos babilônios’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19776, 44, "Propriedades serão compradas por prata e escrituras serão assinadas, seladas diante de testemunhas no território de Benjamim, nos povoados ao redor de Jerusalém, nas cidades de Judá, e nas cidades dos montes, da Sefelá e do Neguebe, porque eu restaurarei a sorte deles"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19777, 1, "Jeremias ainda estava preso no pátio da guarda quando o Senhor lhe dirigiu a palavra pela segunda vez:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19778, 2, """Assim diz o Senhor que fez a terra, o Senhor que a formou e a firmou; seu nome é Senhor:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19779, 3, "‘Clame a mim e eu responderei e lhe direi coisas grandiosas e insondáveis que você não conhece’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19780, 4, "Porque assim diz o Senhor, o Deus de Israel, a respeito das casas desta cidade e dos palácios reais de Judá, que foram derrubados para servirem de defesa contra as rampas de cerco e a espada,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19781, 5, "na luta contra os babilônios: ‘Elas ficarão cheias de cadáveres dos homens que matarei no meu furor. Ocultarei desta cidade o meu rosto por causa de toda a sua maldade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19782, 6, """ ‘Todavia, trarei restauração e cura para ela; curarei o meu povo e lhe darei muita prosperidade e segurança.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19783, 7, "Mudarei a sorte de Judá e de Israel e os reconstruirei como antigamente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19784, 8, "Eu os purificarei de todo o pecado que cometeram contra mim e perdoarei todos os seus pecados de rebelião contra mim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19785, 9, "Então Jerusalém será para mim uma fonte de alegria, de louvor e de glória, diante de todas as nações da terra que ouvirem acerca de todos os benefícios que faço por ela. Elas temerão e tremerão diante da paz e da prosperidade que eu lhe concedo’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19786, 10, """Assim diz o Senhor: ‘Vocês dizem que este lugar está devastado, e ficará sem homens nem animais. Contudo, nas cidades de Judá e nas ruas de Jerusalém, que estão devastadas, desabitadas, sem homens nem animais, mais uma vez se ouvirão", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19787, 11, "as vozes de júbilo e de alegria, do noivo e da noiva, e as vozes daqueles que trazem ofertas de ação de graças para o templo do Senhor, dizendo: ""Dêem graças ao Senhor dos Exércitos, pois ele é bom; o seu amor leal dura para sempre"". Porque eu mudarei a sorte desta terra como antigamente’, declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19788, 12, """Assim diz o Senhor dos Exércitos: ‘Neste lugar desolado, sem homens nem animais, haverá novamente pastagens onde os pastores farão descansar os seus rebanhos, em todas as suas cidades.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19789, 13, "Tanto nas cidades dos montes, da Sefelá, do Neguebe e do território de Benjamim, como nos povoados ao redor de Jerusalém e nas cidades de Judá, novamente passarão ovelhas sob as mãos daquele que as conta’, diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19790, 14, """ ‘Dias virão’, declara o Senhor, ‘em que cumprirei a promessa que fiz à comunidade de Israel e à comunidade de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19791, 15, """ ‘Naqueles dias e naquela época farei brotar um Renovo justo da linhagem de Davi; ele fará o que é justo e certo na terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19792, 16, "Naqueles dias Judá será salva e Jerusalém viverá em segurança, e este é o nome pelo qual ela será chamada: O Senhor é a Nossa Justiça’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19793, 17, """Porque assim diz o Senhor: ‘Davi jamais deixará de ter um descendente que se assente no trono de Israel,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19794, 18, "nem os sacerdotes, que são levitas, deixarão de ter descendente que esteja diante de mim para oferecer, continuamente, holocaustos, queimar ofertas de cereal e apresentar sacrifícios’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19795, 19, "O Senhor dirigiu a palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19796, 20, """Assim diz o Senhor: ‘Se vocês puderem romper a minha aliança com o dia e a minha aliança com a noite, de modo que nem o dia nem a noite aconteçam no tempo que lhes está determinado,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19797, 21, "então poderá ser quebrada a minha aliança com o meu servo Davi, e neste caso ele não mais terá um descendente que reine no seu trono; e também será quebrada a minha aliança com os levitas que são sacerdotes e que me servem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19798, 22, "Farei os descendentes do meu servo Davi e os levitas, que me servem, tão numerosos como as estrelas do céu e incontáveis como a areia das praias do mar’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19799, 23, "O Senhor dirigiu a palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19800, 24, """Você reparou que essas pessoas estão dizendo que o Senhor rejeitou os dois reinos que tinha escolhido? Por isso desprezam o meu povo e não mais o considera como nação.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19801, 25, "Assim diz o Senhor: ‘Se a minha aliança com o dia e com a noite não mais vigorasse, se eu não tivesse estabelecido as leis fixas do céu e da terra,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19802, 26, "então eu rejeitaria os descendentes de Jacó e do meu servo Davi, e não escolheria um dos seus descendentes para que governasse os descendentes de Abraão, de Isaque e de Jacó. Mas eu restaurarei a sorte deles e lhes manifestarei a minha compaixão’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19803, 1, "Quando Nabucodonosor, rei da Babilônia, todo o seu exército e todos os reinos e povos do império que ele governava lutavam contra Jerusalém, e contra todas as cidades ao redor, o Senhor dirigiu esta palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19804, 2, """Assim diz o Senhor, o Deus de Israel: Vá ao rei Zedequias de Judá e lhe diga: ‘Assim diz o Senhor: Estou entregando esta cidade nas mãos do rei da Babilônia, e ele a incendiará.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19805, 3, "Você não escapará, mas será capturado e entregue nas mãos dele. Com os seus próprios olhos você verá o rei da Babilônia, e ele falará com você face a face. E você irá para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19806, 4, """ ‘Ouça, porém, a promessa do Senhor, ó Zedequias, rei de Judá. Assim diz o Senhor a seu respeito: Você não morrerá pela espada,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19807, 5, "mas morrerá em paz. E assim como o povo queimou incenso em honra dos seus antepassados, os reis que o precederam, também queimarão incenso em sua honra, e se lamentarão, clamando: ""Ah, meu senhor! "" Sim, eu mesmo faço essa promessa’, declara o Senhor"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19808, 6, "O profeta Jeremias disse todas essas palavras ao rei Zedequias de Judá, em Jerusalém,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19809, 7, "enquanto o exército do rei da Babilônia lutava contra Jerusalém e contra as outras cidades de Judá que ainda resistiam, Láquis e Azeca, pois só restaram essas cidades fortificadas em Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19810, 8, "O Senhor dirigiu a palavra a Jeremias depois do acordo que o rei Zedequias fez com todo o povo de Jerusalém de proclamar a libertação dos escravos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19811, 9, "Todos teriam que libertar seus escravos e escravas hebreus; ninguém poderia escravizar um compatriota judeu.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19812, 10, "Assim, todos os líderes e o povo que firmaram esse acordo de libertação dos escravos, concordaram em deixá-los livres e não mais os escravizaram; o povo obedeceu e libertou os escravos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19813, 11, "Mas, depois disso, mudou de idéia e tomou de volta os homens e as mulheres que havia libertado e tornou a escravizá-los.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19814, 12, "Então o Senhor dirigiu a palavra a Jeremias, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19815, 13, """Assim diz o Senhor, o Deus de Israel: Fiz uma aliança com os seus antepassados quando os tirei do Egito, da terra da escravidão. Eu disse:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19816, 14, "‘Ao fim de sete anos, cada um de vocês libertará todo compatriota hebreu que se vendeu a vocês. Depois de o servir por seis anos, você o libertará’. Mas os seus antepassados não me obedeceram nem me deram atenção.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19817, 15, "Recentemente vocês se arrependeram e fizeram o que eu aprovo: Cada um de vocês proclamou liberdade para os seus compatriotas. Vocês até fizeram um acordo diante de mim no templo que leva o meu nome.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19818, 16, "Mas, agora, vocês voltaram atrás e profanaram o meu nome, pois cada um de vocês tomou de volta os homens e as mulheres que tinham libertado. Vocês voltaram a escravizá-los"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19819, 17, """Portanto, assim diz o Senhor: Vocês não me obedeceram; não proclamaram libertação cada um para o seu compatriota e para o seu próximo. Por isso, eu agora proclamo libertação para vocês, diz o Senhor, pela espada, pela peste e pela fome. Farei com que vocês sejam um objeto de terror para todos os reinos da terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19820, 18, "Entregarei os homens que violaram a minha aliança e não cumpriram os termos da aliança que fizeram na minha presença, quando cortaram o bezerro em dois e andaram entre as partes do animal;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19821, 19, "isto é, os líderes de Judá e de Jerusalém, os oficiais do palácio real, os sacerdotes e todo o povo da terra que andou entre as partes do bezerro,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19822, 20, "sim, eu os entregarei nas mãos dos inimigos que desejam tirar-lhes a vida. Seus cadáveres servirão de comida para as aves e para os animais.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19823, 21, """Eu entregarei Zedequias, rei de Judá, e os seus líderes, nas mãos dos inimigos que desejam tirar-lhes a vida, e do exército do rei da Babilônia, que retirou o cerco de vocês.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19824, 22, "Darei a ordem"", declara o Senhor, ""e os trarei de volta a esta cidade. Eles lutarão contra ela, e vão conquistá-la e incendiá-la. Farei com que as cidades de Judá fiquem devastadas e desabitadas. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19825, 1, "Durante o reinado de Jeoaquim, filho de Josias, rei de Judá, o Senhor dirigiu esta palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19826, 2, """Vá à comunidade dos recabitas e convide-os a virem a uma das salas do templo do Senhor, e ofereça-lhes vinho para beber"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19827, 3, "Então busquei Jazanias, filho de Jeremias, filho de Habazinias, seus irmãos e todos os seus filhos e toda a comunidade dos recabitas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19828, 4, "Eu os levei ao templo do Senhor, à sala dos filhos de Hanã, filho de Jigdalias, homem de Deus. A sala ficava ao lado da sala dos líderes e debaixo da sala de Maaséias, filho de Salum, o porteiro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19829, 5, "Então, coloquei vasilhas cheias de vinho e alguns copos diante dos membros da comunidade dos recabitas e lhes pedi que bebessem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19830, 6, "Eles, porém, disseram: ""Não bebemos vinho porque o nosso antepassado Jonadabe, filho de Recabe, nos deu esta ordem: ‘Nem vocês nem os seus descendentes beberão vinho.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19831, 7, "Vocês não construirão casas nem semearão; não plantarão vinhas nem as possuirão; mas vocês sempre habitarão em tendas. Assim vocês viverão por muito tempo na terra na qual são nômades’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19832, 8, "Temos obedecido a tudo o que nos ordenou nosso antepassado Jonadabe, filho de Recabe. Nós, nossas mulheres, nossos filhos e nossas filhas jamais bebemos vinho em toda a nossa vida,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19833, 9, "não construímos casas para nossa moradia nem possuímos vinhas, campos ou plantações.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19834, 10, "Temos vivido em tendas e obedecido fielmente a tudo o que nosso antepassado Jonadabe nos ordenou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19835, 11, "Mas, quando Nabucodonosor, rei da Babilônia, invadiu esta terra, dissemos: ‘Venham, vamos para Jerusalém para escapar dos exércitos dos babilônios e dos sírios’. Assim, permanecemos em Jerusalém"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19836, 12, "O Senhor dirigiu a palavra a Jeremias, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19837, 13, """Assim diz o Senhor dos Exércitos, Deus de Israel: Vá dizer aos homens de Judá e aos habitantes de Jerusalém: ‘Será que vocês não vão aprender a lição e obedecer às minhas palavras? ’, pergunta o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19838, 14, "‘Jonadabe, filho de Recabe, ordenou a seus filhos que não bebessem vinho, e essa ordem tem sido obedecida até hoje. Eles não bebem vinho porque obedecem à ordem do seu antepassado. Mas eu tenho falado a vocês repetidas vezes, e, contudo, vocês não me obedecem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19839, 15, "Enviei a vocês, repetidas vezes, todos os meus servos, os profetas. Eles lhes diziam que cada um de vocês deveria converter-se da sua má conduta, corrigir as suas ações e deixar de seguir outros deuses para prestar-lhes culto. Assim, vocês habitariam na terra que dei a vocês e a seus antepassados. Mas vocês não me deram atenção nem me obedeceram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19840, 16, "Os descendentes de Jonadabe, filho de Recabe, cumprem a ordem que o seu antepassado lhes deu, mas este povo não me obedece. ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19841, 17, """Portanto, assim diz o Senhor dos Exércitos, Deus de Israel: ‘Trarei sobre Judá e sobre todos os habitantes de Jerusalém toda a desgraça da qual os adverti; porque falei a eles, mas não me ouviram, chamei-os, mas não me responderam’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19842, 18, "Jeremias disse à comunidade dos recabitas: ""Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Vocês têm obedecido àquele que ordenou o seu antepassado Jonadabe; têm cumprido todas as suas instruções e têm feito tudo o que ele ordenou’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19843, 19, "Por isso, assim diz o Senhor dos Exércitos, Deus de Israel: ‘Jamais faltará a Jonadabe, filho de Recabe, um descendente que me sirva’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19844, 1, "No quarto ano do reinado de Jeoaquim, filho de Josias, rei de Judá, o Senhor dirigiu esta palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19845, 2, """Pegue um rolo e escreva nele todas as palavras que lhe falei a respeito de Israel, de Judá e de todas as outras nações, desde que comecei a falar a você, durante o reinado de Josias, até hoje.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19846, 3, "Talvez, quando o povo de Judá souber de cada uma das desgraças que planejo trazer sobre eles, cada um se converta de sua má conduta e eu perdoe a iniqüidade e o pecado deles"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19847, 4, "Então Jeremias chamou Baruque, filho de Nerias, para que escrevesse no rolo, conforme Jeremias ditava, todas as palavras que o Senhor lhe havia falado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19848, 5, "Depois Jeremias disse a Baruque: ""Estou preso; não posso ir ao templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19849, 6, "Por isso, vá ao templo do Senhor no dia do jejum e leia ao povo as palavras do Senhor que eu ditei, as quais você escreveu. Você também as lerá a todo o povo de Judá que vem de suas cidades.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19850, 7, "Talvez a súplica deles chegue diante do Senhor, e cada um se converta de sua má conduta, pois é grande o furor anunciado pelo Senhor contra este povo"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19851, 8, "E Baruque, filho de Nerias, fez exatamente tudo aquilo que o profeta Jeremias lhe mandou fazer, e leu as palavras do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19852, 9, "No nono mês do quinto ano do reinado de Jeoaquim, filho de Josias, rei de Judá, foi proclamado um jejum perante o Senhor para todo o povo de Jerusalém e para todo o povo que vinha das cidades de Judá para Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19853, 10, "Baruque leu a todo o povo as palavras de Jeremias escritas no rolo. Ele as leu no templo do Senhor, da sala de Gemarias, filho do secretário Safã. A sala ficava no pátio superior, na porta Nova do templo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19854, 11, "Quando Micaías, filho de Gemarias, filho de Safã, ouviu todas as palavras do Senhor,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19855, 12, "desceu à sala do secretário, no palácio real, onde todos os líderes estavam sentados: o secretário Elisama, Delaías, filho de Semaías, Elnatã, filho de Acbor, Gemarias, filho de Safã, Zedequias, filho de Hananias, e todos os outros líderes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19856, 13, "Micaías relatou-lhes tudo o que tinha ouvido quando Baruque leu ao povo o que estava escrito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19857, 14, "Então todos os líderes mandaram por intermédio de Jeudi, filho de Netanias, neto de Selemias, bisneto de Cuchi, a seguinte mensagem a Baruque: ""Pegue o rolo que você leu ao povo e venha aqui"". Baruque, filho de Nerias, pegou o rolo e foi até eles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19858, 15, "Disseram-lhe: ""Sente-se, e leia-o para nós"". Então Baruque o leu para eles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19859, 16, "Quando ouviram todas aquelas palavras, entreolharam-se com medo e disseram a Baruque: ""É absolutamente necessário que relatemos ao rei todas essas palavras"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19860, 17, "Perguntaram a Baruque: ""Diga-nos, como você escreveu tudo isso? Foi Jeremias quem o ditou a você? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19861, 18, """Sim"", Baruque respondeu, ""ele ditou todas essas palavras, e eu as escrevi com tinta no rolo. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19862, 19, "Os líderes disseram a Baruque: ""Vá esconder-se com Jeremias; e que ninguém saiba onde vocês estão"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19863, 20, "Então deixaram o rolo na sala de Elisama, o secretário, e foram ao pátio do palácio real, e relataram tudo ao rei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19864, 21, "O rei mandou Jeudi pegar o rolo, e Jeudi o trouxe da sala de Elisama, o secretário, e o leu ao rei e a todos os líderes que estavam a seu serviço.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19865, 22, "Isso aconteceu no nono mês. O rei estava sentado em seu apartamento do inverno, perto de um braseiro aceso.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19866, 23, "Assim que Jeudi terminava de ler três ou quatro colunas, o rei as cortava com uma faca de escrivão e as atirava no braseiro, até que o rolo inteiro foi queimado no braseiro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19867, 24, "O rei e todos os seus conselheiros que ouviram todas aquelas palavras não ficaram alarmados nem rasgaram as suas roupas, lamentando-se.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19868, 25, "Embora Elnatã, Delaías e Gemarias tivessem insistido com o rei que não queimasse o rolo, ele não quis ouvi-los.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19869, 26, "Em vez disso, o rei ordenou a Jerameel, filho do rei, Seraías, filho de Azriel, e Selemias, filho de Abdeel, que prendessem o escriba Baruque e o profeta Jeremias. Mas o Senhor os tinha escondido.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19870, 27, "Depois que o rei queimou o rolo que continha as palavras que Jeremias ditou e Baruque escreveu, o Senhor dirigiu esta palavra a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19871, 28, """Pegue outro rolo e escreva nele todas as palavras que estavam no primeiro, que Jeoaquim, rei de Judá, queimou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19872, 29, "Também diga a Jeoaquim, rei de Judá: ‘Assim diz o Senhor: Você queimou aquele rolo e perguntou: ""Por que você escreveu nele que o rei da Babilônia virá e destruirá esta terra e dela eliminará tanto homens como animais? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19873, 30, "Pois assim diz o Senhor acerca de Jeoaquim, rei de Judá: Ele não terá nenhum descendente para sentar-se no trono de Davi; seu corpo será lançado fora e exposto ao calor de dia e à geada de noite.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19874, 31, "Eu castigarei a ele, aos seus filhos e aos seus conselheiros por causa dos seus pecados. Trarei sobre eles e sobre os habitantes de Jerusalém e sobre os homens de Judá toda a desgraça que pronunciei contra eles, porquanto não me deram atenção’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19875, 32, "Então Jeremias pegou outro rolo e o deu ao escriba Baruque, filho de Nerias, para que escrevesse nele, conforme Jeremias ditava, todas as palavras do livro que Jeoaquim, rei de Judá, tinha queimado, além de muitas outras palavras semelhantes que foram acrescentadas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19876, 1, "Zedequias, filho de Josias, rei de Judá, foi designado rei por Nabucodonosor, rei da Babilônia. Ele reinou em lugar de Joaquim, filho de Jeoaquim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19877, 2, "Nem ele, nem seus conselheiros, nem o povo da terra deram atenção às palavras que o Senhor tinha falado por meio do profeta Jeremias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19878, 3, "O rei Zedequias, porém, mandou Jucal, filho de Selemias, e o sacerdote Sofonias, filho de Maaséias, ao profeta Jeremias com esta mensagem: ""Ore ao Senhor, ao nosso Deus, em nosso favor"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19879, 4, "Naquela época Jeremias estava livre para circular entre o povo, pois ainda não tinha sido preso.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19880, 5, "Enquanto isso, o exército do faraó tinha saído do Egito. E quando os babilônios que cercavam Jerusalém ouviram isso, retiraram o cerco.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19881, 6, "O Senhor dirigiu esta palavra ao profeta Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19882, 7, """Assim diz o Senhor, o Deus de Israel: Digam ao rei de Judá, que os mandou para consultar-me: ‘O exército do faraó, que saiu do Egito para vir ajudá-los, retornará à sua própria terra, ao Egito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19883, 8, "Os babilônios voltarão e atacarão esta cidade; eles a conquistarão e a destruirão a fogo’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19884, 9, """Assim diz o Senhor: Não se enganem a si mesmos, dizendo: ‘Os babilônios certamente vão embora’. Porque eles não irão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19885, 10, "Ainda que vocês derrotassem todo o exército babilônio que está atacando vocês, e só lhe restasse homens feridos em suas tendas, eles se levantariam e incendiariam esta cidade"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19886, 11, "Depois que o exército babilônio se retirou de Jerusalém por causa do exército do faraó,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19887, 12, "Jeremias saiu da cidade para ir ao território de Benjamim a fim de tomar posse da propriedade que tinha entre o povo daquele lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19888, 13, "Mas, quando chegou à porta de Benjamim, o capitão da guarda, cujo nome era Jerias, filho de Selemias, filho de Hananias, o prendeu e disse: ""Você está desertando para o lado dos babilônios! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19889, 14, """Isso não é verdade! "", disse Jeremias. ""Não estou passando para o lado dos babilônios. "" Mas Jerias não quis ouvi-lo; e, prendendo Jeremias, o levou aos líderes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19890, 15, "Eles ficaram furiosos com Jeremias, espancaram-no e o prenderam na casa do secretário Jônatas, que tinham transformado numa prisão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19891, 16, "Jeremias foi posto numa cela subterrânea da prisão, onde ficou por muito tempo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19892, 17, "Então o rei mandou buscá-lo, e Jeremias foi trazido ao palácio. E, secretamente, o rei lhe perguntou: ""Há alguma palavra da parte do Senhor? "" ""Há"", respondeu Jeremias, ""você será entregue nas mãos do rei da Babilônia. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19893, 18, "Então Jeremias disse ao rei Zedequias: ""Que crime cometi contra ti ou contra os teus conselheiros ou contra este povo para que me mandasses para a prisão?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19894, 19, "Onde estão os seus profetas que lhes profetizaram: ‘O rei da Babilônia não atacará a vocês nem a esta terra’?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19895, 20, "Mas, agora, ó rei, meu senhor, escuta-me, por favor. Permita-me apresentar-te a minha súplica: Não me mandes de volta à casa de Jônatas, o secretário, para que eu não morra ali"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19896, 21, "Então o rei Zedequias deu ordens para que Jeremias fosse colocado no pátio da guarda e que diariamente recebesse pão da rua dos padeiros, enquanto houvesse pão na cidade. Assim Jeremias permaneceu no pátio da guarda.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19897, 1, "E ocorreu que Sefatias, filho de Matã, Gedalias, filho de Pasur, Jucal, filho de Selemias, e Pasur, filho de Malquias, ouviram o que Jeremias estava dizendo a todo o povo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19898, 2, """Assim diz o Senhor: ‘Aquele que permanecer nesta cidade morrerá pela espada, pela fome e pela peste; mas aquele que se render aos babilônios viverá. Escapará com vida e sobreviverá’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19899, 3, "E, assim diz o Senhor: ‘Esta cidade certamente será entregue ao exército do rei da Babilônia, que a conquistará’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19900, 4, "Então os líderes disseram ao rei: ""Este homem deve morrer. Ele está desencorajando os soldados que restaram nesta cidade, bem como todo o povo, com as coisas que ele está dizendo. Este homem não busca o bem deste povo, mas a sua ruína"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19901, 5, "O rei Zedequias respondeu: ""Ele está em suas mãos; o rei não pode opor-se a vocês"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19902, 6, "Assim, pegaram Jeremias e o jogaram na cisterna de Malquias, filho do rei, a qual ficava no pátio da guarda. Baixaram Jeremias por meio de cordas para dentro da cisterna. Não havia água na cisterna, mas somente lama; e Jeremias afundou na lama.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19903, 7, "Mas, Ebede-Meleque, o etíope, oficial do palácio real, ouviu que eles tinham jogado Jeremias na cisterna. Ora, o rei estava sentado junto à porta de Benjamim,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19904, 8, "Ebede-Meleque saiu do palácio e foi dizer-lhe:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19905, 9, """Ó rei, meu senhor, esses homens cometeram um mal em tudo o que fizeram ao profeta Jeremias. Eles o jogaram numa cisterna para que morra de fome, pois já não há mais pão na cidade"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19906, 10, "Então o rei ordenou a Ebede-Meleque, o etíope: ""Leve com você três homens sob as suas ordens e retire o profeta Jeremias da cisterna antes que ele morra"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19907, 11, "Então Ebede-Meleque levou consigo os homens que estavam sob as suas ordens e foi à sala que fica debaixo da tesouraria do palácio. Pegou alguns trapos e roupas velhas e desceu cordas até Jeremias na cisterna.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19908, 12, "O Ebede-Meleque, o etíope, disse a Jeremias: ""Ponha esses trapos e roupas velhas debaixo dos braços para servirem de almofada para as cordas"". E Jeremias assim fez.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19909, 13, "Assim, com as cordas o puxaram para cima e o tiraram da cisterna. E Jeremias permaneceu no pátio da guarda.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19910, 14, "Então o rei Zedequias mandou trazer o profeta Jeremias e o encontrou na terceira entrada do templo do Senhor. ""Quero pedir de você uma palavra"", disse o rei. ""Não me esconda nada. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19911, 15, "Jeremias disse a Zedequias: ""Se eu te der uma resposta, não me matarás? Mesmo que eu te desse conselho, tu não me escutarias"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19912, 16, "O rei Zedequias, porém, fez este juramento secreto a Jeremias: ""Juro pelo nome do Senhor, de quem recebemos a vida, eu não o matarei nem o entregarei nas mãos daqueles que desejam tirar-lhe a vida"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19913, 17, "Então Jeremias disse a Zedequias: ""Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Se te renderes imediatamente aos oficiais do rei da Babilônia, tua vida será poupada e esta cidade não será incendiada; tu e a tua família viverão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19914, 18, "Mas, se não te renderes imediatamente aos oficiais do rei da Babilônia, esta cidade será entregue nas mãos dos babilônios, e eles a incendiarão; nem mesmo tu escaparás das mãos deles’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19915, 19, "O rei Zedequias disse a Jeremias: ""Tenho medo dos judeus que estão apoiando os babilônios, pois os babilônios poderão entregar-me nas mãos deles, e eles me maltratarão"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19916, 20, """Eles não o entregarão"", Jeremias respondeu. ""Obedeça ao Senhor fazendo o que eu lhe digo, para que estejas bem e a tua vida seja poupada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19917, 21, "Mas se te recusares a render-se, foi isto que o Senhor me revelou:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19918, 22, "Todas as mulheres deixadas no palácio real de Judá serão levadas aos oficiais do rei da Babilônia. E elas lhe dirão: ‘Aqueles teus amigos de confiança te enganaram e prevaleceram sobre ti. Teus pés estão atolados na lama; teus amigos te abandonaram’. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19919, 23, """Todas as tuas mulheres e os teus filhos serão levados aos babilônios. Tu mesmo não escaparás das mãos deles, mas serás capturado pelo rei da Babilônia; e esta cidade será incendiada. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19920, 24, "Então Zedequias disse a Jeremias: ""Se alguém souber dessa conversa, você morrerá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19921, 25, "Se os líderes ouvirem que eu conversei com você e vierem dizer-lhe: ‘Conte-nos o que você disse ao rei e o que o rei disse a você; não esconda nada de nós, se não nós o mataremos’,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19922, 26, "diga: ‘Fui suplicar ao rei que não me mandasse de volta à casa de Jônatas, para ali morrer’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19923, 27, "Quando os líderes vieram interrogar Jeremias, ele lhes disse tudo o que o rei tinha ordenado que dissesse. E eles não lhe perguntaram mais nada, pois ninguém tinha ouvido a conversa com o rei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19924, 28, "E Jeremias permaneceu no pátio da guarda até o dia em que Jerusalém foi conquistada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19925, 1, "Foi assim que Jerusalém foi tomada: No nono ano do reinado de Zedequias, rei de Judá, no décimo mês, Nabucodonosor, rei da Babilônia, marchou contra Jerusalém com todo seu exército e a sitiou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19926, 2, "E no dia novo do quarto mês do décimo primeiro ano do reinado de Zedequias, o muro da cidade foi rompido.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19927, 3, "Então todos os oficiais do rei da Babilônia vieram e se assentaram junto à porta do Meio: Nergal-Sarezer de Sangar, Nebo-Sarsequim, um dos chefes dos oficiais, Nergal-Sarezer, um alto oficial, e todos os outros oficiais do rei da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19928, 4, "Quando Zedequias, rei de Judá, e todos os soldados os viram, fugiram e saíram da cidade, à noite, na direção do jardim real, pela porta entre os dois muros; e foram para a Arabá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19929, 5, "Mas o exército babilônio os perseguiu e alcançou Zedequias na planície de Jericó. Eles o capturaram e o levaram a Nabucodonosor, rei da Babilônia, em Ribla, na terra de Hamate, que o sentenciou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19930, 6, "Em Ribla, o rei da Babilônia mandou executar os filhos de Zedequias diante dos seus olhos, e também matou todos os nobres de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19931, 7, "Mandou furar os olhos de Zedequias e prendê-lo com correntes de bronze para levá-lo para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19932, 8, "Os babilônios incendiaram o palácio real e as casas do povo, e derrubaram os muros de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19933, 9, "Nebuzaradã, o comandante da guarda imperial, deportou para a Babilônia o povo que restou na cidade, juntamente com aqueles que tinham se rendido a ele, e o restante dos artesãos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19934, 10, "Somente alguns dos mais pobres do povo, que nada tinham, Nebuzaradã, o comandante da guarda imperial, deixou para trás em Judá. E, naquela ocasião, ele lhes deu vinhas e campos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19935, 11, "Mas Nabucodonosor, rei da Babilônia, deu ordens a respeito de Jeremias a Nebuzaradã, o comandante da guarda imperial:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19936, 12, """Vá buscá-lo e cuide bem dele; não o maltrate, mas faça o que ele pedir"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19937, 13, "Então Nebuzaradã, o comandante da guarda imperial, Nebusazbã, um dos chefes dos oficiais, Nergal-Sarezer, um alto oficial, e todos os outros oficiais do rei da Babilônia", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19938, 14, "mandaram tirar Jeremias do pátio da guarda e o entregaram a Gedalias, filho de Aicam, filho de Safã, para que o levasse à residência do governador. Assim, Jeremias permaneceu no meio do seu povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19939, 15, "Enquanto Jeremias esteve preso no pátio da guarda, o Senhor lhe dirigiu a palavra:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19940, 16, """Vá dizer a Ebede-Meleque, o etíope: ‘Assim diz o Senhor dos Exércitos, Deus de Israel: Estou para cumprir as minhas advertências contra esta cidade, com desgraça e não com prosperidade. Naquele dia, elas se cumprirão diante dos seus olhos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19941, 17, "Mas eu o resgatarei naquele dia’, declara o Senhor; ‘você não será entregue nas mãos daqueles a quem teme.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19942, 18, "Eu certamente o resgatarei; você não morrerá pela espada, mas escapará com vida, porque você confia em mim’ "", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19943, 1, "O Senhor dirigiu a palavra a Jeremias depois que o comandante da guarda imperial, Nebuzaradã, o libertou em Ramá. Ele tinha encontrado Jeremias acorrentado entre todos os cativos de Jerusalém e de Judá que estavam sendo levados para o exílio na Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19944, 2, "Quando o comandante da guarda encontrou Jeremias, disse-lhe: ""Foi o Senhor, o seu Deus, que determinou esta desgraça para este lugar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19945, 3, "Agora o Senhor a cumpriu e fez o que tinha prometido. Tudo isso aconteceu porque vocês pecaram contra o Senhor e não lhe obedeceram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19946, 4, "Mas hoje eu o liberto das correntes que prendem as suas mãos. Se você quiser, venha comigo para a Babilônia e eu cuidarei de você; se, porém, não quiser, pode ficar. Veja! Toda esta terra está diante de você; vá para onde melhor lhe parecer"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19947, 5, "Contudo, antes de Jeremias se virar para partir, Nebuzaradã acrescentou: ""Volte a Gedalias, filho de Aicam, neto de Safã, a quem o rei da Babilônia nomeou governador sobre as cidades de Judá, e viva com ele entre o povo, ou vá para qualquer outro lugar que desejar"". Então o comandante lhe deu provisões e um presente, e o deixou partir.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19948, 6, "Jeremias foi a Gedalias, filho de Aicam, em Mispá, e permaneceu com ele entre o povo que foi deixado na terra de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19949, 7, "Havia comandantes do exército, que ainda estavam em campo aberto com os seus soldados. Eles ouviram que o rei da Babilônia tinha nomeado Gedalias, filho de Aicam, governador de Judá e o havia encarregado dos homens, das mulheres, das crianças e dos mais pobres da terra, que não tinham sido deportados para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19950, 8, "Então foram até Gedalias, em Mispá: Ismael, filho de Netanias, Joanã e Jônatas, filhos de Careá, Seraías, filho de Tanumete, os filhos de Efai, de Netofate, e Jazanias, filho do maacatita, juntamente com os seus soldados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19951, 9, "Gedalias, filho de Aicam, neto de Safã, fez um juramento a eles e aos seus soldados: ""Não temam sujeitar-se aos babilônios. Estabeleçam-se na terra, sujeitem-se ao rei da Babilônia, e tudo lhes irá bem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19952, 10, "Eu mesmo permanecerei em Mispá para representá-los diante dos babilônios que vierem a nós. Mas, vocês, façam a colheita das uvas para o vinho, das frutas e das olivas para o azeite, ponham o produto em jarros, e vivam nas cidades que vocês ocuparam"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19953, 11, "Quando todos os judeus que estavam em Moabe, em Amom, em Edom e em todas as outras terras ouviram que o rei da Babilônia tinha deixado um remanescente em Judá, e que havia nomeado Gedalias, filho de Aicam, neto de Safã, governador sobre eles,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19954, 12, "voltaram, de todos os lugares para onde tinham sido espalhados, e vieram para a terra de Judá, e foram até Gedalias em Mispá. E fizeram uma grande colheita de uvas para o vinho, e também de frutas de verão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19955, 13, "Joanã, filho de Careá, e todos os comandantes do exército que ainda estavam em campo aberto, foram até Gedalias em Mispá", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19956, 14, "e lhe disseram: ""Você não sabe que Baalis, rei dos amonitas, enviou Ismael, filho de Netanias, para matá-lo? "" Mas Gedalias, filho de Aicam, não acreditou neles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19957, 15, "Então Joanã, filho de Careá, disse em particular a Gedalias, em Mispá: ""Irei agora e matarei Ismael, filho de Netanias, e ninguém ficará sabendo disso. Por que deveria ele fazer com que os judeus que se reuniram a você sejam espalhados e o remanescente de Judá seja destruído? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19958, 16, "Mas Gedalias, filho de Aicam, disse a Joanã, filho de Careá: ""Não faça uma coisa dessas. O que você está dizendo sobre Ismael não é verdade"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19959, 1, "No sétimo mês, Ismael, filho de Netanias, filho de Elisama, que era de sangue real e tinha sido um dos oficiais do rei, foi até Gedalias, filho de Aicam, em Mispá, levando consigo dez homens. Enquanto comiam juntos,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19960, 2, "Ismael, filho de Netanias, e os dez homens que estavam com ele se levantaram e feriram à espada Gedalias, filho de Aicam, neto de Safã, matando aquele que o rei da Babilônia tinha nomeado governador de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19961, 3, "Ismael também matou todos os judeus que estavam com Gedalias em Mispá, bem como os soldados babilônios que ali estavam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19962, 4, "No dia seguinte ao assassinato de Gedalias, antes que alguém o soubesse,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19963, 5, "oitenta homens que haviam rapado a barba, rasgado suas roupas e feito cortes no corpo, vieram de Siquém, de Siló e de Samaria, trazendo ofertas de cereal e incenso para oferecer no templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19964, 6, "Ismael, filho de Netanias, saiu de Mispá para encontrá-los, chorando enquanto caminhava. Quando os encontrou, disse: ""Venham até onde se encontra Gedalias, filho de Aicam"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19965, 7, "Quando entraram na cidade, Ismael, filho de Netanias, e os homens que estavam com ele os mataram e os atiraram numa cisterna.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19966, 8, "Mas dez deles disseram a Ismael: ""Não nos mate! Temos trigo e cevada, azeite e mel, escondidos num campo"". Então ele os deixou em paz e não os matou com os demais.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19967, 9, "A cisterna na qual ele jogou os corpos dos homens que havia matado, juntamente com o de Gedalias, tinha sido cavada pelo rei Asa para defender-se de Baasa, rei de Israel. Ismael, filho de Netanias, encheu-a com os mortos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19968, 10, "Ismael tomou como prisioneiros todo o restante do povo que estava em Mispá, inclusive as filhas do rei, sobre os quais Nebuzaradã, o comandante da guarda imperial, havia nomeado Gedalias, filho de Aicam, governador. Ismael, filho de Netanias, levou-os como prisioneiros e partiu para o território de Amom.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19969, 11, "Quando Joanã, filho de Careá, e todos os comandantes do exército, que com ele estavam, souberam do crime que Ismael, filho de Netanias, tinha cometido,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19970, 12, "convocaram todos os seus soldados para lutar contra ele. Eles o alcançaram perto do grande açude de Gibeom.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19971, 13, "Quando todo o povo, que Ismael tinha levado como prisioneiro, viu Joanã, filho de Careá, e os comandantes do exército que estavam com ele, alegrou-se.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19972, 14, "Todo o povo que Ismael tinha levado como prisioneiro de Mispá se voltou e passou para o lado de Joanã, filho de Careá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19973, 15, "Mas Ismael, filho de Netanias, e oito de seus homens escaparam de Joanã e fugiram para o território de Amom", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19974, 16, "Então, Joanã, filho de Careá, e todos os comandantes do exército que com ele estavam levaram todos os que tinham restado em Mispá, os quais ele tinha resgatado de Ismael, filho de Netanias, depois que este havia assassinado Gedalias, filho de Aicam: os soldados, as mulheres, as crianças e os oficiais do palácio real, que ele tinha trazido de Gibeom.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19975, 17, "E eles prosseguiram, parando em Gerute-Quimã, perto de Belém, a caminho do Egito,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19976, 18, "Queriam escapar dos babilônios. Estavam com medo porque Ismael, filho de Netanias, tinha matado Gedalias, filho de Aicam, a quem o rei da Babilônia nomeara governador de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19977, 1, "Então todos os líderes do exército, inclusive Joanã, filho de Careá, e Jezanias, filho de Hosaías, e todo o povo, desde o menor até o maior, aproximaram-se", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19978, 2, "do profeta Jeremias e lhe disseram: ""Por favor, ouça a nossa petição e ore ao Senhor, ao seu Deus, por nós e em favor de todo este remanescente; pois, como você vê, embora fôssemos muitos, agora só restam poucos de nós.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19979, 3, "Ore rogando ao Senhor, ao seu Deus, que nos diga para onde devemos ir e o que devemos fazer"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19980, 4, """Eu os atenderei"", respondeu o profeta Jeremias. ""Orarei ao Senhor, ao seu Deus, conforme vocês pediram. E tudo o que o Senhor responder eu lhes direi; nada esconderei de vocês. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19981, 5, "Então disseram a Jeremias: ""Que o Senhor seja uma testemunha verdadeira e fiel contra nós, caso não façamos tudo o que o Senhor, o seu Deus, nos ordenar por você.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19982, 6, "Quer seja favorável ou não, obedeceremos ao Senhor, ao nosso Deus, a quem o enviamos, para que tudo vá bem conosco, pois obedeceremos ao Senhor, ao nosso Deus"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19983, 7, "Dez dias depois o Senhor dirigiu a palavra a Jeremias,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19984, 8, "e ele convocou Joanã, filho de Careá, e todos os comandantes do exército que estavam com ele e todo o povo, desde o menor até o maior.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19985, 9, "Disse-lhes então: ""Assim diz o Senhor, o Deus de Israel, a quem vocês me enviaram para apresentar a petição de vocês:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19986, 10, "‘Se vocês permanecerem nesta terra, eu os edificarei e não os destruirei; eu os plantarei e não os arrancarei, pois muito me pesa a desgraça que eu trouxe sobre vocês.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19987, 11, "Não tenham medo do rei da Babilônia, a quem vocês agora temem. Não tenham medo dele, declara o Senhor, pois estou com vocês e os salvarei e os livrarei das mãos dele.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19988, 12, "Eu terei compaixão de vocês; e ele também terá compaixão de vocês e lhes permitirá retornar à terra de vocês’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19989, 13, """Contudo, se vocês disserem ‘Não permaneceremos nesta terra’, e assim desobedecerem ao Senhor, ao seu Deus,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19990, 14, "e se disserem: ‘Não, nós iremos para o Egito, onde não veremos a guerra nem ouviremos o som da trombeta, nem passaremos fome’,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19991, 15, "ouçam a palavra do Senhor, ó remanescente de Judá. Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Se vocês estão decididos a ir para o Egito e lá forem residir,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19992, 16, "a guerra que vocês temem os alcançará, e a fome que receiam os seguirá até o Egito, e lá vocês morrerão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19993, 17, "Todos os que estão decididos a partir e residir no Egito morrerão pela guerra, pela fome e pela peste; nem um só deles sobreviverá ou escapará da desgraça que trarei sobre eles’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19994, 18, "Assim diz o Senhor dos Exércitos, Deus de Israel: ‘Como o meu furor foi derramado sobre os habitantes de Jerusalém, também a minha ira será derramada sobre vocês, quando forem para o Egito. Vocês serão objeto de maldição e de pavor, de desprezo e de afronta. Vocês jamais tornarão a ver este lugar’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19995, 19, """Ó remanescente de Judá, o Senhor lhes disse: ‘Não vão para o Egito’. Estejam certos disto: Eu hoje os advirto", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19996, 20, "que vocês cometeram um erro fatal quando me enviaram ao Senhor, ao seu Deus, pedindo: ‘Ore ao Senhor, ao nosso Deus, em nosso favor. Diga-nos tudo o que ele lhe falar, e nós o faremos’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19997, 21, "Eu lhes disse, hoje mesmo, o que o Senhor, o seu Deus, me mandou dizer a vocês, mas vocês não lhe estão obedecendo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19998, 22, "Agora, porém, estejam certos de que vocês morrerão pela guerra, pela fome e pela peste, no lugar que vocês desejam residir"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (19999, 1, "Quando Jeremias acabou de dizer ao povo tudo o que o Senhor, o seu Deus, lhe mandara dizer,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20000, 2, "Azarias, filho de Hosaías, e Joanã, filho de Careá, e todos os homens arrogantes disseram a Jeremias: ""Você está mentindo! O Senhor não lhe mandou dizer que não fôssemos residir no Egito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20001, 3, "Mas é Baruque, filho de Nerias, que o está instigando contra nós para que sejamos entregues nas mãos dos babilônios, a fim de que nos matem ou nos levem para o exílio na Babilônia"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20002, 4, "Assim Joanã, filho de Careá, e todos os comandantes do exército e todo o povo desobedeceram à ordem do Senhor de que permanecessem na terra de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20003, 5, "Mas Joanã, filho de Careá, e todos os comandantes do exército, levaram todo o remanescente de Judá que tinha voltado de todas as nações para onde haviam sido espalhados a fim de viver na terra de Judá:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20004, 6, "todos os homens, mulheres e crianças, as filhas do rei, todos os que Nebuzaradã, o comandante da guarda imperial, deixara com Gedalias, filho de Aicam, neto de Safã; além do profeta Jeremias e de Baruque, filho de Nerias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20005, 7, "Eles foram para o Egito, desobedecendo ao Senhor, e foram até Tafnes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20006, 8, "Em Tafnes, o Senhor dirigiu a palavra a Jeremias, dizendo:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20007, 9, """Pegue algumas pedras grandes e, à vista dos homens de Judá, enterre-as no barro do pavimento na entrada do palácio do faraó, em Tafnes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20008, 10, "Então diga-lhes: ‘Assim diz o Senhor dos Exércitos, Deus de Israel: Mandarei chamar meu servo Nabucodonosor, rei da Babilônia, e ele colocará o seu trono sobre essas pedras que enterrei, e estenderá a sua tenda real sobre elas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20009, 11, "Ele virá e atacará o Egito, trará a morte aos destinados a morte, cativeiro aos destinados ao cativeiro, e espada aos destinados a morrer pela espada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20010, 12, "Ele incendiará os templos dos deuses do Egito; queimará seus templos e levará embora cativos os seus deuses. Como um pastor tira os piolhos do seu manto, assim ele despiolhará o seu Egito, e sairá em paz.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20011, 13, "Ele despedaçará as colunas no templo do sol, no Egito, e incendiará os templos dos deuses do Egito’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20012, 1, "Esta é a palavra do Senhor, que foi dirigida a Jeremias, para todos os judeus que estavam no Egito e viviam em Migdol, Tafnes, Mênfis, e na região de Patros:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20013, 2, """Assim diz o Senhor dos Exércitos, Deus de Israel: Vocês viram toda a desgraça que eu trouxe sobre Jerusalém e sobre todas as cidades de Judá. Hoje elas estão em ruínas e desabitadas", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20014, 3, "por causa do mal que fizeram. Seus moradores provocaram a minha ira queimando incenso e prestando culto a outros deuses, que nem eles nem vocês nem seus antepassados jamais conheceram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20015, 4, "Dia após dia, eu lhes enviei meus servos, os profetas, que disseram: ‘Não façam essa abominação detestável! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20016, 5, "Mas eles não me ouviram nem me deram atenção; não se converteram de sua impiedade nem cessaram de queimar incenso a outros deuses.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20017, 6, "Por isso, o meu furor foi derramado e queimou as cidades de Judá e as ruas de Jerusalém, tornando-as na ruína desolada que são no dia de hoje.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20018, 7, """Assim diz o Senhor, o Deus dos Exércitos, o Deus de Israel: Por que trazer uma desgraça tão grande sobre si mesmos, eliminando de Judá homens e mulheres, crianças e recém-nascidos, sem deixar remanescente algum?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20019, 8, "Por que vocês provocam a minha ira com o que fazem, queimando incenso a outros deuses no Egito, onde vocês vieram residir? Vocês se destruirão a si mesmos e se tornarão objeto de desprezo e afronta entre todas as nações da terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20020, 9, "Acaso vocês se esqueceram da impiedade cometida por seus antepassados, pelos reis de Judá e as mulheres deles, e da impiedade cometida por vocês e suas mulheres na terra de Judá e nas ruas de Jerusalém?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20021, 10, "Até hoje eles não se humilharam nem mostraram reverência, e não têm seguido a minha lei e os decretos que coloquei diante de vocês e dos seus antepassados"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20022, 11, """Portanto, assim diz o Senhor dos Exércitos, Deus de Israel: Estou decidido a trazer desgraça sobre vocês e a destruir todo o Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20023, 12, "Tomarei o remanescente de Judá, que decidiu partir e residir no Egito, e todos morrerão no Egito. Cairão pela espada ou pela fome; desde o menor até o maior, morrerão pela espada ou pela fome. Eles se tornarão objeto de maldição e de pavor, de desprezo e de afronta.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20024, 13, "Castigarei aqueles que vivem no Egito com a guerra, a fome e a peste, como castiguei Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20025, 14, "Ninguém dentre o remanescente de Judá que foi morar no Egito escapará ou sobreviverá para voltar à terra de Judá, para a qual anseiam voltar e nela anseiam viver; nenhum voltará, exceto uns poucos fugitivos"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20026, 15, "Então, todos os homens que sabiam que as suas mulheres queimavam incenso a outros deuses, e todas as mulheres que estavam presentes, em grande número, e todo o povo que morava no Egito, e na região de Patros, disseram a Jeremias:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20027, 16, """Nós não daremos atenção à mensagem que você nos apresenta em nome do Senhor!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20028, 17, "É certo que faremos tudo o que dissemos que faríamos: Queimaremos incenso à Rainha dos Céus e derramaremos ofertas de bebidas para ela, tal como fazíamos, nós e nossos antepassados, nossos reis e nossos líderes, nas cidades de Judá e nas ruas de Jerusalém. Naquela época tínhamos fartura de comida, éramos prósperos e em nada sofríamos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20029, 18, "Mas, desde que paramos de queimar incenso à Rainha dos Céus e de derramar ofertas de bebidas a ela, nada temos tido e temos perecido pela espada e pela fome"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20030, 19, "E as mulheres acrescentaram: ""Quando queimávamos incenso à Rainha dos Céus e derramávamos ofertas de bebidas para ela, será que era sem o consentimento de nossos maridos que fazíamos bolos na forma da imagem dela e derramávamos ofertas de bebidas para ela? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20031, 20, "Então Jeremias disse a todo o povo, tanto aos homens como às mulheres que estavam respondendo a ele:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20032, 21, """E o Senhor? Não se lembra ele do incenso queimado nas cidades de Judá e nas ruas de Jerusalém por vocês e por seus antepassados, seus reis e seus líderes e pelo povo da terra? Será que ele não pensa nisso?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20033, 22, "Quando o Senhor não pôde mais suportar as impiedades e as práticas repugnantes de vocês, a terra de vocês ficou devastada e desolada, e tornou-se objeto de maldição e desabitada, como se vê no dia de hoje.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20034, 23, "Foi porque vocês queimaram incenso e pecaram contra o Senhor, e não obedeceram à sua palavra nem seguiram a sua lei, os seus decretos e os seus testemunhos, que esta desgraça caiu sobre vocês, como se vê no dia de hoje"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20035, 24, "Disse então Jeremias a todo o povo, inclusive às mulheres: ""Ouçam a palavra do Senhor, todos vocês, judeus que estão no Egito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20036, 25, "Assim diz o Senhor dos Exércitos, Deus de Israel: Vocês e suas mulheres cumpriram o que prometeram quando disseram: ‘Certamente cumpriremos os votos que fizemos de queimar incenso e derramar ofertas de bebidas à Rainha dos Céus’. ""Prossigam! Façam o que prometeram! Cumpram os seus votos!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20037, 26, "Mas ouçam a palavra do Senhor, todos vocês, judeus que vivem no Egito: ‘Eu juro pelo meu grande nome’, diz o Senhor, ‘que em todo o Egito ninguém de Judá voltará a invocar o meu nome ou a jurar pela vida do Soberano Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20038, 27, "Vigiarei sobre eles para trazer-lhes a desgraça e não o bem; os judeus do Egito perecerão pela espada e pela fome até que sejam todos destruídos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20039, 28, "Serão poucos os que escaparão da espada e voltarão do Egito para a terra de Judá. Então, todo o remanescente de Judá que veio residir no Egito saberá qual é a palavra que se realiza, a minha ou a deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20040, 29, """ ‘Este será o sinal para vocês de que os castigarei neste lugar’, declara o Senhor, ‘e então vocês ficarão sabendo que as minhas ameaças de trazer-lhes desgraça certamente se realizarão’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20041, 30, "Assim diz o Senhor: ‘Entregarei o faraó Hofra, rei do Egito, nas mãos dos seus inimigos que desejam tirar-lhe a vida, assim como entreguei Zedequias, rei de Judá, nas mãos de Nabucodonosor, rei da Babilônia, o inimigo que desejava tirar a vida dele’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20042, 1, "No quarto ano do reinado de Jeoaquim, filho de Josias, rei de Judá, depois que Baruque, filho de Nerias, escreveu num rolo as palavras ditadas por Jeremias, este lhe disse:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20043, 2, """Assim diz o Senhor, o Deus de Israel, a você, Baruque:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20044, 3, "Você disse, ‘Ai de mim! O Senhor acrescentou tristeza ao meu sofrimento. Estou exausto de tanto gemer, e não encontro descanso’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20045, 4, "Mas o Senhor manda-me dizer-lhe: ‘Assim diz o Senhor: Destruirei o que edifiquei e arrancarei o que plantei em toda esta terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20046, 5, "E então? Você deveria buscar coisas especiais para você? Não as busque; pois trarei desgraça sobre toda a humanidade’, diz o Senhor, ‘mas eu o deixarei escapar com vida onde quer que você vá’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20047, 1, "Esta é a mensagem do Senhor que veio ao profeta Jeremias acerca das nações:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20048, 2, "Acerca do Egito: Esta é a mensagem contra o exército do rei do Egito, o faraó Neco, que foi derrotado em Carquemis, junto ao rio Eufrates, por Nabucodonosor, rei da Babilônia, no quarto ano do reinado de Jeoaquim, filho de Josias, rei de Judá:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20049, 3, """Preparem seus escudos, os grandes e os pequenos, e marchem para a batalha!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20050, 4, "Selem os cavalos e montem! Tomem posição e coloquem o capacete! Passem óleo na ponta de suas lanças e vistam a armadura!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20051, 5, "Mas o que vejo? Eles estão apavorados, estão se retirando, seus guerreiros estão derrotados. Fogem às pressas, sem olhar para trás; há terror por todos os lados"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20052, 6, """O ágil não consegue fugir, nem o forte escapar. No norte, junto ao rio Eufrates, eles tropeçam e caem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20053, 7, """Quem é aquele que se levanta como o Nilo, como rios de águas agitadas?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20054, 8, "O Egito se levanta como o Nilo, como rios de águas agitadas. Ele diz: ‘Eu me levantarei e cobrirei a terra; destruirei as cidades e os seus habitantes’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20055, 9, "Ao ataque, cavalos! Avancem, carros de guerra! Marchem em frente, guerreiros! Homens da Etiópia e da Líbia, que levam escudos; homens da Lídia, que empunham o arco!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20056, 10, "Mas aquele dia pertence ao Soberano, ao Senhor dos Exércitos. Será um dia de vingança, para vingar-se dos seus adversários. A espada devorará até saciar-se, até satisfazer sua sede de sangue. Porque o Soberano, o Senhor dos Exércitos, fará um banquete na terra do norte, junto ao rio Eufrates.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20057, 11, """Suba a Gileade em busca de bálsamo, ó virgem, filha do Egito! Você multiplica remédios em vão; não há cura para você.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20058, 12, "As nações ouviram da tua humilhação; os seus gritos encheram a terra, quando um guerreiro tropeçou noutro guerreiro e ambos caíram. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20059, 13, "Esta é a mensagem que o Senhor falou ao profeta Jeremias acerca da vinda de Nabucodonosor, rei da Babilônia para atacar o Egito:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20060, 14, """Anunciem isto no Egito e proclamem-no em Migdol; proclamem-no também em Mênfis e em Tafnes: ‘Assumam posição! Preparem-se! Porque a espada devora aqueles que estão ao seu redor’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20061, 15, "Por que o deus Ápis fugiu? O seu touro não resistiu, porque o Senhor o derrubou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20062, 16, "Tropeçam e caem, caem uns sobre os outros. Eles dizem: ‘Levantem-se. Vamos voltar para nosso próprio povo e para nossa terra natal, para longe da espada do opressor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20063, 17, "O faraó, rei do Egito, é barulho e nada mais! Ele perdeu a sua oportunidade’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20064, 18, """Juro pela minha vida"", declara o Rei, cujo nome é o Senhor dos Exércitos, ""ele virá como o Tabor entre os montes, como o Carmelo junto ao mar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20065, 19, "Arrumem a bagagem para o exílio, vocês que vivem no Egito, pois Mênfis será arrasada, ficará desolada e desabitada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20066, 20, """O Egito é uma linda novilha, mas do norte a ataca uma mutuca.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20067, 21, "Os mercenários em suas fileiras são como bezerros gordos. Eles também darão meia volta e juntos fugirão; não defenderão suas posições, pois o dia da derrota deles está chegando, a hora de serem castigados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20068, 22, "O Egito silvará como uma serpente em fuga à medida que o inimigo avança com grande força. Virão sobre ele com machados, como os homens que derrubam árvores.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20069, 23, "Eles derrubarão sua floresta"", declara o Senhor, ""por mais densa que seja. São mais que os gafanhotos; são incontáveis!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20070, 24, "A cidade do Egito será envergonhada, será entregue nas mãos do povo do norte"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20071, 25, "O Senhor dos Exércitos, o Deus de Israel, diz: ""Castigarei Amom, deus de Tebas, o faraó, o Egito, seus deuses e seus reis, e também os que confiam no faraó.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20072, 26, "Eu os entregarei nas mãos daqueles que desejam tirar-lhes a vida; nas mãos de Nabucodonosor, rei da Babilônia, e de seus oficiais. Mais tarde, porém, o Egito será habitado como em épocas passadas"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20073, 27, """Quanto a você, não tema, meu servo Jacó! Não fique assustado, ó Israel! Eu o salvarei de um lugar distante, e os seus descendentes da terra do seu exílio. Jacó voltará e ficará em paz e em segurança; ninguém o inquietará.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20074, 28, "Não tema, meu servo Jacó! Eu estou com você"", declara o Senhor. ""Destruirei completamente todas as nações entre as quais eu o dispersei; mas a você, não destruirei completamente. Eu o disciplinarei, como você merece; não serei severo demais. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20075, 1, "Esta é a palavra do Senhor que veio ao profeta Jeremias acerca dos filisteus, antes do ataque do faraó a Gaza:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20076, 2, "Assim diz o Senhor: ""Vejam como as águas estão subindo do norte; elas se tornam uma torrente transbordante. Inundarão esta terra e tudo o que nela existe; as cidades e os seus habitantes. O povo clamará, gritarão todos os habitantes desta terra,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20077, 3, "ao estrondo dos cascos dos seus cavalos galopando, ao barulho dos seus carros de guerra, e ao estampido de suas rodas. Os pais não se voltarão para ajudar seus filhos; pois suas mãos estão fracas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20078, 4, "Pois chegou o dia de destruir todos os filisteus e de eliminar todos os sobreviventes que poderiam ajudar Tiro e Sidom. O Senhor destruirá os filisteus, o remanescente da ilha de Caftor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20079, 5, "Os habitantes de Gaza rasparam a cabeça; Ascalom está calada. Ó remanescentes da planície, até quando você fará incisões no próprio corpo? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20080, 6, """ ‘Ah, espada do Senhor, quando você descansará? Volte à sua bainha, acalme-se e repouse. ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20081, 7, "Mas como poderá ela descansar quando o Senhor lhe deu ordens, quando ele lhe ordenou que ataque Ascalom e o litoral? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20082, 1, "Acerca de Moabe assim diz o Senhor dos Exércitos, Deus de Israel: ""Ai de Nebo, pois ficou em ruínas. Quiriataim foi derrotada e capturada; a fortaleza foi derrotada e destroçada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20083, 2, "Moabe não é mais louvada; em Hesbom tramam a sua ruína: ‘Venham! Vamos dar fim àquela nação’. Você também ficará calada, ó Madmém; a espada a perseguirá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20084, 3, "Ouçam os gritos de Horonaim: ‘Devastação! Grande destruição!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20085, 4, "Moabe está destruída! ’ É o grito que se ouve até em Zoar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20086, 5, "Eles sobem pelo caminho para Luíte, chorando amargamente enquanto seguem; na estrada que desce a Horonaim ouvem-se gritos angustiados por causa da destruição.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20087, 6, "Fujam! Corram para salvar suas vidas; tornem-se como um arbusto no deserto.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20088, 7, "Uma vez que vocês confiam em seus feitos e em suas riquezas, vocês também serão capturados, e Camos irá para o exílio, junto com seus sacerdotes e líderes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20089, 8, "O destruidor virá contra todas as cidades, e nenhuma escapará. O vale se tornará ruínas, e o planalto será destruído, como o Senhor falou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20090, 9, "Ponham sal sobre Moabe, pois ela será deixada em ruínas; suas cidades ficarão devastadas, sem nenhum habitante.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20091, 10, """Maldito o que faz com negligência o trabalho do Senhor! Maldito aquele que impede a sua espada de derramar sangue!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20092, 11, """Moabe tem estado tranqüila desde a sua juventude, como o vinho deixado com os seus resíduos; não foi mudada de vasilha em vasilha. Nunca foi para o exílio; por isso, o seu sabor permanece o mesmo e o seu cheiro não mudou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20093, 12, "Portanto, certamente vêm os dias"", declara o Senhor, ""quando enviarei decantadores que o decantarão; esvaziarão as suas jarras e as despedaçarão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20094, 13, "Então Moabe se decepcionará com Camos, assim como Israel se decepcionou com Betel, em quem confiava.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20095, 14, """Como vocês podem dizer: ‘Somos guerreiros, somos homens de guerra’?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20096, 15, "Moabe foi destruída e suas cidades serão invadidas; o melhor dos seus jovens desceu para a matança"", declara o Rei, cujo nome é o Senhor dos Exércitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20097, 16, """A derrota de Moabe está próxima; a sua desgraça vem rapidamente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20098, 17, "Lamentem por ela, todos os seus vizinhos, todos os que conhecem a sua fama. Digam: ‘Como está quebrado o cajado poderoso, o cetro glorioso! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20099, 18, """Desçam de sua glória e sentem-se sobre o chão ressequido, ó moradores da cidade de Dibom, pois o destruidor de Moabe veio para atacá-los e destruir as suas fortalezas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20100, 19, "Fiquem junto à estrada e vigiem, vocês que vivem em Aroer. Perguntem ao homem que foge e à mulher que escapa, perguntem a eles: ‘O que aconteceu? ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20101, 20, "Moabe ficou envergonhada, pois está destroçada. Gritem e clamem! Anunciem junto ao Arnom que Moabe foi destruída.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20102, 21, "O julgamento chegou ao planalto: a Holom, Jaza e Mefaate,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20103, 22, "a Dibom, Nebo e Bete-Diblataim,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20104, 23, "a Quiriataim, Bete-Gamul e Bete-Meom,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20105, 24, "a Queriote e Bozra, a todas as cidades de Moabe, distantes e próximas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20106, 25, "O poder de Moabe foi eliminado; seu braço está quebrado"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20107, 26, """Embriaguem-na, pois ela desafiou o Senhor. Moabe se revolverá no seu vômito e será objeto de ridículo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20108, 27, "Não foi Israel objeto de ridículo para você? Foi ele encontrado em companhia de ladrões para que você sacuda a cabeça sempre que fala dele?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20109, 28, "Abandonem as cidades! Habitem entre as rochas, vocês que moram em Moabe! Sejam como uma pomba que faz o seu ninho nas bordas de um precipício.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20110, 29, """Temos ouvido do orgulho de Moabe: da sua extrema arrogância, do seu orgulho e soberba, e do seu espírito de superioridade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20111, 30, "Conheço bem a sua arrogância"", declara o Senhor. ""A sua tagarelice sem fundamento e as suas ações que nada alcançam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20112, 31, "Por isso, lamentarei por Moabe, gritarei por causa de toda a terra de Moabe, prantearei pelos habitantes de Quir-Heres.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20113, 32, "Chorarei por vocês mais do que choro por Jazar, ó videiras de Sibma. Os seus ramos se estendiam até o mar, e chegavam até Jazar. O destruidor caiu sobre as suas frutas e sobre as suas uvas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20114, 33, "A alegria e a satisfação se foram das terras férteis de Moabe. Interrompi a produção de vinho nos lagares. Ninguém mais pisa as uvas com gritos de alegria; embora haja gritos, não são de alegria.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20115, 34, """O grito de Hesbom é ouvido em Eleale e Jaaz, desde Zoar até Horonaim e Eglate-Selisia, pois até as águas do Ninrim secaram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20116, 35, "Em Moabe darei fim àqueles que fazem ofertas nos altares idólatras e queimam incenso a seus deuses"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20117, 36, """Por isso o meu coração lamenta-se por Moabe, como uma flauta; lamenta-se como uma flauta pelos habitantes de Quir-Heres. A riqueza que acumularam se foi.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20118, 37, "Toda cabeça foi rapada e toda barba foi cortada; toda mão sofreu incisões e toda cintura foi coberta com veste de lamento.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20119, 38, "Em todos os terraços de Moabe e nas praças não há nada senão pranto, pois despedacei Moabe como a um jarro que ninguém deseja"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20120, 39, """Como ela foi destruída! Como lamentam! Como Moabe dá as costas, envergonhada! Moabe tornou-se objeto de ridículo e de pavor para todos os seus vizinhos. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20121, 40, "Assim diz o Senhor: ""Vejam! Uma águia planando estende as asas sobre Moabe.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20122, 41, "Queriote será capturada, e as fortalezas serão tomadas. Naquele dia, a coragem dos guerreiros de Moabe será como a de uma mulher em trabalho de parto.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20123, 42, "Moabe será destruída como nação pois ela desafiou o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20124, 43, "Terror e cova e laço esperam por você, ó povo de Moabe"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20125, 44, """Quem fugir do terror cairá numa cova, e quem sair da cova será apanhado num laço. Trarei sobre Moabe a hora do seu castigo"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20126, 45, """Na sombra de Hesbom os fugitivos se encontram desamparados, pois um fogo saiu de Hesbom, uma labareda do meio de Seom; e queima as testas dos homens de Moabe e os crânios de homens turbulentos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20127, 46, "Ai de você, ó Moabe! O povo de Camos está destruído; Seus filhos são levados para o exílio, e suas filhas para o cativeiro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20128, 47, """Contudo, restaurarei a sorte de Moabe em dias vindouros"", declara o Senhor. Aqui termina a sentença sobre Moabe.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20129, 1, "Acerca dos amonitas, assim diz o Senhor: ""Por acaso Israel não tem filhos? Será que não tem herdeiros? Por que será então que Moloque se apossou de Gade? Por que seu povo vive nas cidades de Gade?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20130, 2, "Portanto, certamente vêm os dias"", declara o Senhor, ""em que farei soar o grito de guerra contra Rabá dos amonitas; ela virá a ser uma pilha de ruínas, e os seus povoados ao redor serão incendiados. Então Israel expulsará aqueles que o expulsaram"", diz o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20131, 3, """Lamente, ó Hesbom, pois Ai está destruída! Gritem, ó moradores de Rabá! Ponham veste de lamento e chorem! Corram desorientados, pois Moloque irá para o exílio com os seus sacerdotes e os seus oficiais.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20132, 4, "Por que você se orgulha de seus vales? Por que se orgulha de seus vales tão frutíferos? Ó filha infiel! Você confia em suas riquezas e diz: ‘Quem me atacará? ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20133, 5, "Farei com que você tenha pavor de tudo o que está a sua volta"", diz o Senhor, o Senhor dos Exércitos. ""Vocês serão dispersos, cada um numa direção, e ninguém conseguirá reunir os fugitivos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20134, 6, """Contudo, depois disso, restaurarei a sorte dos amonitas"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20135, 7, "Acerca de Edom, assim diz o Senhor dos Exércitos: ""Será que já não há mais sabedoria em Temã? Será que o conselho desapareceu dos prudentes? A sabedoria deles deteriorou-se?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20136, 8, "Voltem-se e fujam, escondam-se em cavernas profundas, vocês que moram em Dedã, pois trarei a ruína sobre Esaú na hora em que eu o castigar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20137, 9, "Se os que colhem uvas viessem até você, não deixariam eles apenas umas poucas uvas? Se os ladrões viessem durante a noite, não roubariam apenas o quanto desejassem?", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20138, 10, "Mas eu despi Esaú e descobri os seus esconderijos, para que ele não mais se esconda. Os seus filhos, parentes e vizinhos foram destruídos. Ninguém restou para dizer:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20139, 11, "‘Deixe os seus órfãos; eu protegerei a vida deles. As suas viúvas também podem confiar em mim’ "".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20140, 12, "Assim diz o Senhor: ""Se aqueles para quem o cálice não era reservado tiveram que bebê-lo, por que você deveria ficar impune? Você não ficará sem castigo, mas irá bebê-lo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20141, 13, "Eu juro por mim mesmo"", declara o Senhor, ""que Bozra ficará em ruínas e desolada; ela se tornará objeto de afronta e de maldição, e todas as suas cidades serão ruínas para sempre"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20142, 14, "Ouvi uma mensagem da parte do Senhor: Um mensageiro foi mandado às nações para dizer: ""Reúnam-se para atacar Edom! Preparem-se para a batalha! """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20143, 15, """Agora eu faço de você uma nação pequena entre as demais, desprezada pelos homens.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20144, 16, "O pavor que você inspira e o orgulho de seu coração o enganaram, a você, que vive nas fendas das rochas, que ocupa os altos das colinas. Ainda que você, como a águia, faça o seu ninho nas alturas, de lá eu o derrubarei"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20145, 17, """Edom se tornará objeto de terror; todos os que por ali passarem ficarão chocados e zombarão por causa de todas as suas feridas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20146, 18, "Como a destruição de Sodoma e Gomorra, e as cidades vizinhas"", diz o Senhor, ""ninguém mais habitará ali, nenhum homem residirá nela.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20147, 19, """Assim como um leão que sobe da mata do Jordão em direção aos pastos verdejantes, subitamente eu caçarei Edom para fora de sua terra. Quem é o escolhido que designarei para isso? Quem é como eu que possa me desafiar? E que pastor pode me resistir? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20148, 20, "Por isso, ouçam o que o Senhor planejou contra Edom, o que preparou contra os habitantes de Temã: Os menores do rebanho serão arrastados, e as pastagens ficarão devastadas por causa deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20149, 21, "Ao som de sua queda a terra tremerá; o grito deles ressoará até o mar Vermelho.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20150, 22, "Vejam! Uma águia, subindo e planando, estende as asas sobre Bozra. Naquele dia, a coragem dos guerreiros de Edom será como a de uma mulher dando à luz.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20151, 23, "Acerca de Damasco: ""Hamate e Arpade estão atônitas, pois ouviram más notícias. Estão desencorajadas, perturbadas como o mar agitado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20152, 24, "Damasco tornou-se frágil, ela se virou para fugir, e o pânico tomou conta dela; angústia e dor dela se apoderaram, dor como a de uma mulher em trabalho de parto.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20153, 25, "Como está abandonada a cidade famosa, a cidade da alegria!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20154, 26, "Por isso, os seus jovens cairão nas ruas e todos os seus guerreiros se calarão naquele dia"", declara o Senhor dos exércitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20155, 27, """Porei fogo nas muralhas de Damasco, que consumirá as fortalezas de Ben-Hadade. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20156, 28, "Acerca de Quedar e os reinos de Hazor, que Nabucodonosor, rei da Babilônia, derrotou, assim diz o Senhor: ""Preparem-se, ataquem Quedar e destruam o povo do oriente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20157, 29, "Tomem suas tendas e seus rebanhos, suas cortinas com todos os seus utensílios e camelos. Gritam contra eles: ‘Há terror por todos os lados! ’", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20158, 30, """Fujam rapidamente! Escondam-se em cavernas profundas, vocês habitantes de Hazor"", diz o Senhor. ""Nabucodonosor, rei da Babilônia, fez planos e projetos contra vocês.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20159, 31, """Preparem-se e ataquem uma nação que vive tranqüila e confiante"", declara o Senhor, ""uma nação que não tem portas nem trancas, e que vive sozinha.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20160, 32, "Seus camelos se tornarão despojo e suas grandes manadas, espólio. Espalharei ao vento aqueles que raspam a cabeça, e de todos os lados trarei a ruína deles"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20161, 33, """Hazor se tornará uma habitação de chacais, uma ruína para sempre. Ninguém mais habitará ali, nenhum homem residirá nela. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20162, 34, "Esta é a palavra do Senhor que veio ao profeta Jeremias acerca de Elão, no início do reinado de Zedequias, rei de Judá:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20163, 35, "Assim diz o Senhor dos Exércitos: ""Vejam, quebrarei o arco de Elão, a base de seu poder.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20164, 36, "Farei com que os quatro ventos, que vêm dos quatro cantos do céu, soprem contra Elão. E eu os dispersarei aos quatro ventos, e não haverá nenhuma nação para onde os exilados de Elão não forem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20165, 37, "Farei com que Elão trema diante dos seus inimigos, diante daqueles que desejam tirar-lhes a vida. Trarei a desgraça sobre eles, a minha ira ardente"", declara o Senhor. ""Farei com que a espada os persiga até que eu os tenha eliminado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20166, 38, "Porei meu trono em Elão e destruirei seu rei e líderes"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20167, 39, """Contudo restaurarei a sorte de Elão em dias vindouros"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20168, 1, "Esta é a palavra que o Senhor falou pelo profeta Jeremias acerca da Babilônia e da terra dos babilônios:", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20169, 2, """Anunciem e proclamem entre as nações, ergam um sinal e proclamem; não escondam nada. Digam: ‘A Babilônia foi conquistada; Bel foi humilhado, Marduque apavorado. As imagens da Babilônia estão humilhadas e seus ídolos apavorados’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20170, 3, "Uma nação vinda do norte a atacará, arrasará a sua terra e não deixará nela nenhum habitante; tanto homens como animais fugirão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20171, 4, """Naqueles dias e naquela época"", declara o Senhor, ""o povo de Israel e o povo de Judá virão juntos, chorando e buscando o Senhor seu Deus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20172, 5, "Perguntarão pelo caminho para Sião e voltarão o rosto na direção dela. Virão e se apegarão ao Senhor numa aliança permanente que não será esquecida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20173, 6, """Meu povo tem sido ovelhas perdidas; seus pastores as desencaminharam e as fizeram perambular pelos montes. Elas vaguearam por montanhas e colinas e se esqueceram de seu próprio curral.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20174, 7, "Todos que as encontram as devoram. Os seus adversários disseram: ‘Não somos culpados, pois elas pecaram contra o Senhor, sua verdadeira pastagem, o Senhor, a esperança de seus antepassados’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20175, 8, """Fujam da Babilônia; saiam da terra dos babilônios e sejam como os bodes que lideram o rebanho.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20176, 9, "Vejam! Eu mobilizarei e trarei contra a Babilônia uma coalizão de grandes nações do norte. Elas tomarão posição de combate contra ela e a conquistarão. Suas flechas serão como guerreiros bem treinados, que não voltam de mãos vazias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20177, 10, "Assim a Babilônia será saqueada; todos os que a saquearem se fartarão"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20178, 11, """Ainda que você esteja alegre e exultante, você que saqueia a minha herança; ainda que você seja brincalhão como uma novilha solta no pasto, e relincha como os garanhões,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20179, 12, "sua mãe se envergonhará profundamente; aquela que lhes deu à luz ficará constrangida. Ela se tornará a menor das nações, um deserto, uma terra seca e árida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20180, 13, "Por causa da ira do Senhor ela não será habitada, mas estará completamente desolada. Todos os que passarem pela Babilônia ficarão chocados e zombarão por causa de todas as suas feridas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20181, 14, """Tomem posição de combate em volta da Babilônia, todos vocês que empunham o arco. Atirem nela! Não poupem flechas, pois ela pecou contra o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20182, 15, "Soem contra ela um grito de guerra de todos os lados! Ela se rende, suas torres caem e suas muralhas são derrubadas. Esta é a vingança do Senhor; vinguem-se dela! Façam a ela o que ela fez aos outros!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20183, 16, "Eliminem da Babilônia o semeador, e o ceifeiro com a sua foice na colheita. Por causa da espada do opressor que cada um volte para o seu próprio povo, e cada um fuja para a sua própria terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20184, 17, """Israel é um rebanho disperso, afugentado por leões. O primeiro a devorá-lo foi o rei da Assíria; e o último a esmagar os seus ossos foi Nabucodonosor, rei da Babilônia. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20185, 18, "Portanto, assim diz o Senhor dos Exércitos, o Deus de Israel: ""Castigarei o rei da Babilônia e a sua terra assim como castiguei o rei da Assíria.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20186, 19, "Mas trarei Israel de volta a sua própria pastagem e ele pastará no Carmelo e em Basã; e saciará o seu apetite nos montes de Efraim e em Gileade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20187, 20, "Naqueles dias, naquela época"", declara o Senhor, ""se procurará pela iniqüidade de israel, mas nada será achado, pelos pecados de Judá, mas nenhum será encontrado, pois perdoarei o remanescente que eu poupar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20188, 21, """Ataquem a terra de Merataim e aqueles que moram em Pecode. Persigam-nos, matem-nos e destruam-nos totalmente"", declara o Senhor. ""Façam tudo que lhes ordenei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20189, 22, "Há ruído de batalha na terra; grande destruição!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20190, 23, "Quão quebrado e destroçado está o martelo de toda a terra! Quão arrasada está a Babilônia entre as nações!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20191, 24, "Preparei uma armadilha para você, ó Babilônia, e você foi apanhada antes de percebê-lo; você foi achada e capturada porque se opôs ao Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20192, 25, "O Senhor abriu o seu arsenal e trouxe para fora as armas da sua ira, pois o Soberano Senhor dos Exércitos tem trabalho para fazer na terra dos babilônios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20193, 26, "Venham contra ela dos confins da terra. Arrombem os seus celeiros; empilhem-na como feixes de cereal. Destruam-na totalmente e não lhe deixem nenhum remanescente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20194, 27, "Matem todos os seus jovens guerreiros! Que eles desçam para o matadouro! Ai deles! Pois chegou o seu dia, a hora de serem castigados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20195, 28, "Escutem os fugitivos e refugiados vindos da Babilônia, declarando em Sião como, o Senhor, o nosso Deus se vingou, como se vingou de seu templo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20196, 29, """Convoquem flecheiros contra a Babilônia, todos aqueles que empunham o arco. Acampem-se todos ao redor dela; não deixem ninguém escapar. Retribuam a ela conforme os seus feitos; façam com ela tudo o que ela fez. Porque ela desafiou o Senhor, o Santo de Israel.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20197, 30, "Por isso, os seus jovens cairão nas ruas e todos os seus guerreiros se calarão naquele dia"", declara o Senhor dos Exércitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20198, 31, """Vejam, estou contra você, ó arrogante"", declara o Soberano Senhor dos Exércitos, ""pois chegou o seu dia, a sua hora de ser castigada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20199, 32, "A arrogância tropeçará e cairá, e ninguém a ajudará a se levantar. Incendiarei as suas cidades, e o fogo consumirá tudo ao seu redor. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20200, 33, "Assim diz o Senhor dos Exércitos: ""O povo de Israel está sendo oprimido, e também o povo de Judá. Todos os seus captores os prendem à força, recusando deixá-los ir.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20201, 34, "Contudo, o Redentor deles é forte; o Senhor dos Exércitos é o seu nome. Ele mesmo defenderá a causa deles, e trará descanso à terra, mas inquietação aos que vivem na Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20202, 35, """Uma espada contra os babilônios! "", declara o Senhor; ""contra os que vivem na Babilônia e contra seus líderes e sábios!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20203, 36, "Uma espada contra os seus falsos profetas! Eles se tornarão tolos. Uma espada contra os seus guerreiros! Eles ficarão apavorados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20204, 37, "Uma espada contra os seus cavalos, os seus carros de guerra e contra todos os estrangeiros em suas fileiras! Eles serão como mulheres. Uma espada contra os seus tesouros! Eles serão saqueados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20205, 38, "Uma espada contra as suas águas! Elas secarão. Porque é uma terra de imagens esculpidas, e eles enlouquecem por causa de seus ídolos horríveis.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20206, 39, """Por isso, criaturas do deserto e hienas nela morarão, e as corujas nela habitarão. Ela jamais voltará a ser habitada nem haverá quem nela viva no futuro.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20207, 40, "Como Deus destruiu Sodoma e Gomorra e as cidades vizinhas"", diz o Senhor, ""ninguém mais habitará ali, nenhum homem residirá nela.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20208, 41, """Vejam! Vem vindo um povo do norte; uma grande nação e muitos reis se mobilizam desde os confins da terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20209, 42, "Eles empunham o arcos e a lança; são cruéis e sem misericórdia, e o seu barulho é como o bramido do mar. Vêm montados em seus cavalos, em formação de batalha, para atacá-la, ó cidade de Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20210, 43, "Quando o rei da Babilônia ouviu relatos sobre eles, as suas mãos amoleceram. A angústia tomou conta dele, dores como as de uma mulher dando à luz.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20211, 44, "Assim como um leão que sobe da mata do Jordão em direção aos pastos verdejantes, subitamente eu caçarei a Babilônia para fora de sua terra. Quem é o escolhido que designarei para isso? Quem é como eu que possa me desafiar? E que pastor pode me resistir? """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20212, 45, "Por isso, ouçam o que o Senhor planejou contra a Babilônia, o que ele preparou contra a terra dos babilônios: os menores do rebanho serão arrastados, e as pastagens ficarão devastadas por causa deles.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20213, 46, "Ao som da tomada da Babilônia a terra tremerá; o grito deles ressoará entre as nações.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20214, 1, "Assim diz o Senhor: ""Vejam! Levantarei um vento destruidor contra a Babilônia, contra o povo de Lebe-Camai.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20215, 2, "Enviarei estrangeiros para a Babilônia a fim de peneirá-la como trigo e devastar a sua terra. No dia de sua desgraça virão contra ela de todos os lados.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20216, 3, "Que o arqueiro não arme o seu arco nem vista a sua armadura. Não poupem os seus jovens guerreiros, destruam completamente o seu exército.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20217, 4, "Eles cairão mortos na Babilônia, mortalmente feridos em suas ruas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20218, 5, "Israel e Judá não foram abandonadas como viúvas pelo seu Deus, o Senhor dos Exércitos, embora a terra dos babilônios esteja cheia de culpa diante do Santo de Israel.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20219, 6, """Fujam da Babilônia! Cada um por si! Não sejam destruídos por causa da iniqüidade dela. É hora da vingança do Senhor; ele pagará a ela o que ela merece.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20220, 7, "A Babilônia era um cálice de ouro nas mãos do Senhor; ela embriagou a terra toda. As nações beberam o seu vinho; por isso agora, enlouqueceram.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20221, 8, "A Babilônia caiu de repente e ficou arruinada. Lamentem por ela! Consigam bálsamo para a ferida dela; talvez ela possa ser curada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20222, 9, """Gostaríamos de ter curado Babilônia, mas ela não pode ser curada; deixem-na e vamos cada um para a sua própria terra, pois o julgamento dela chega ao céu, eleva-se tão alto quanto as nuvens.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20223, 10, """O Senhor defendeu o nosso nome; venham, contemos em Sião o que o Senhor, o nosso Deus, tem feito.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20224, 11, """Afiem as flechas, peguem os escudos! O Senhor incitou o espírito dos reis dos medos, porque seu propósito é destruir a Babilônia. O Senhor se vingará, se vingará de seu templo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20225, 12, "Ergam o sinal para atacar as muralhas da Babilônia! Reforcem a guarda! Posicionem as sentinelas! Preparem uma emboscada! O Senhor executará o seu plano, o que ameaçou fazer contra os habitantes da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20226, 13, "Você que vive junto a muitas águas e está rico de tesouros, chegou o seu fim, a hora de você ser eliminado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20227, 14, "O Senhor dos Exércitos jurou por si mesmo: com certeza a encherei de homens, como com um enxame de gafanhotos, e eles gritarão triunfante sobre você.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20228, 15, """Mas foi Deus quem fez a terra com o seu poder; firmou o mundo com a sua sabedoria e estendeu os céus com o seu entendimento.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20229, 16, "Ao som do seu trovão, as águas no céu rugem; ele faz com que as nuvens se levantem desde os confins da terra. Ele faz relâmpagos para a chuva e faz sair o vento de seus depósitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20230, 17, """São todos eles estúpidos e ignorantes; cada ourives é envergonhado pela imagem que esculpiu. Suas imagens esculpidas são uma fraude, elas não têm fôlego de vida.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20231, 18, "Elas são inúteis, são objetos de zombaria. Quando vier o julgamento delas, perecerão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20232, 19, "Aquele que é a Porção de Jacó não é como esses, pois ele é quem forma todas as coisas, e Israel é a tribo de sua propriedade; o Senhor dos Exércitos é o seu nome.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20233, 20, """Você é o meu martelo, a minha arma de guerra. Com você eu despedaço nações, com você eu destruo reinos,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20234, 21, "com você despedaço cavalo e cavaleiro, com você despedaço carro de guerra e cocheiro,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20235, 22, "com você despedaço homem e mulher, com você despedaço velho e jovem, com você despedaço rapaz e moça,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20236, 23, "com você despedaço pastor e rebanho, com você despedaço lavrador e bois, com você despedaço governadores e oficiais.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20237, 24, """Retribuirei à Babilônia e a todos os que vivem na Babilônia por toda a maldade que fizeram em Sião diante dos olhos de vocês"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20238, 25, """Estou contra você, ó montanha destruidora, você que destrói a terra inteira"", declara o Senhor. ""Estenderei minha mão contra você, eu a farei rolar dos penhascos, e farei de você uma montanha calcinada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20239, 26, "Nenhuma pedra sua será cortada para servir de pedra angular, nem para um alicerce, pois você estará arruinada para sempre"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20240, 27, """Ergam um estandarte na terra! Toquem a trombeta entre as nações! Preparem as nações para o combate contra ela; convoquem contra ela estes reinos: Ararate, Mini e Asquenaz. Nomeiem um comandante contra ela; lancem os cavalos ao ataque como um enxame de gafanhotos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20241, 28, "Preparem as nações para o combate contra ela: os reis dos medos, seus governadores e todos os seus oficiais, e todos os países que governam.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20242, 29, "A terra treme e se contorce de dor, pois permanecem de pé os planos do Senhor contra a Babilônia: desolar a terra da Babilônia para que fique desabitada.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20243, 30, "Os guerreiros da Babilônia pararam de lutar; permanecem em suas fortalezas. A força deles acabou; tornaram-se como mulheres. As habitações dela estão incendiadas; as trancas de suas portas estão quebradas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20244, 31, "Um emissário vai após outro, e um mensageiro sai após outro mensageiro para anunciar ao rei da Babilônia que sua cidade inteira foi capturada,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20245, 32, "os pontos de cruzar o rio foram tomados, a vegetação dos pântanos foi incendiada, e os soldados ficaram aterrorizados. """, 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20246, 33, "Assim diz o Senhor dos Exércitos, Deus de Israel: ""A cidade de Babilônia é como uma eira a época da colheita logo chegará para ela.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20247, 34, """Nabucodonosor, rei da Babilônia, devorou-nos, lançou-nos em confusão, fez de nós um jarro vazio. Tal como uma serpente ele nos engoliu e encheu seu estômago com nossas finas comidas e então nos vomitou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20248, 35, "Que a violência cometida à nossa carne esteja sobre a Babilônia"", dizem os habitantes de Sião. ""Que o nosso sangue esteja sobre aqueles que moram na Babilônia"", diz Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20249, 36, "Por isso, assim diz o Senhor: ""Vejam, defenderei a causa de vocês e os vingarei; secarei o seu mar e esgotarei as suas fontes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20250, 37, "A Babilônia se tornará um amontoado de ruínas, uma habitação de chacais, objeto de pavor e de zombaria, um lugar onde ninguém vive.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20251, 38, "O seu povo todo ruge como leõezinhos, rosnam como filhotes de leão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20252, 39, "Mas, enquanto estiverem excitados, prepararei um banquete para eles e os deixarei bêbados, para que fiquem bem alegres e, então, durmam e jamais acordem"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20253, 40, """Eu os levarei como cordeiros para o matadouro, como carneiros e bodes.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20254, 41, """Como Sesaque será capturada, o orgulho de toda a terra tomado! Que horror a Babilônia será entre as nações!", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20255, 42, "O mar se levantará sobre a Babilônia; suas ondas agitadas a cobrirão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20256, 43, "Suas cidades serão arrasadas, uma terra seca e deserta, uma terra onde ninguém mora, pela qual nenhum homem passa.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20257, 44, "Castigarei Bel na Babilônia e o farei vomitar o que engoliu. As nações não mais acorrerão a ele. E a muralha da Babilônia cairá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20258, 45, """Saia dela, meu povo! Cada um salve a sua própria vida, da ardente ira do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20259, 46, "Não desanimem nem tenham medo quando ouvirem rumores na terra; um rumor chega este ano, outro no próximo, rumor de violência na terra e de governante contra governante.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20260, 47, "Portanto, certamente vêm os dias quando castigarei as imagens esculpidas da Babilônia; toda a sua terra será envergonhada, e todos os seus mortos jazerão caídos dentro dela.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20261, 48, "Então o céu e a terra e tudo o que existe neles gritará de alegria por causa da Babilônia, pois do norte destruidores a atacarão"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20262, 49, """A Babilônia cairá por causa dos mortos de Israel, assim como os mortos de toda a terra caíram por causa da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20263, 50, "Vocês que escaparam da espada, saiam! Não permaneçam! Lembrem-se do Senhor numa terra distante, e pensem em Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20264, 51, """Estamos envergonhados pois fomos insultados e a vergonha cobre o nosso rosto, porque estrangeiros penetraram nos lugares santos do templo do Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20265, 52, """Portanto, centamente vêm os dias"", declara o Senhor, ""quando castigarei as suas imagens esculpidas, e por toda a sua terra os feridos gemerão.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20266, 53, "Mesmo que a Babilônia chegue ao céu e fortifique no alto a sua fortaleza, enviarei destruidores contra ela"", declara o Senhor.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20267, 54, """Vem da Babilônia o som de um grito, o som de grande destruição vem da terra dos babilônios.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20268, 55, "O Senhor destruirá a Babilônia; ele silenciará o seu grande ruído. Ondas de inimigos avançarão como grandes águas; o rugir de suas vozes ressoará.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20269, 56, "Um destruidor virá contra a Babilônia; seus guerreiros serão capturados, e seus arcos serão quebrados. Pois o Senhor é um Deus de retribuição; ele retribuirá plenamente.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20270, 57, "Embebedarei os seus líderes e os seus sábios; os seus governadores, os seus oficiais e os seus guerreiros. Eles dormirão para sempre e jamais acordarão"", declara o Rei, cujo nome é Senhor dos Exércitos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20271, 58, "Assim diz o Senhor dos Exércitos: ""A larga muralha da Babilônia será desmantelada e suas altas portas serão incendiadas. Os povos se exaurem por nada, o trabalho das nações não passa de combustível para as chamas"".", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20272, 59, "Esta é a mensagem que Jeremias deu ao responsável pelo acampamento Seraías, filho de Nerias, filho de Maaséias, quando ele foi à Babilônia com o rei Zedequias de Judá no quarto ano do seu reinado.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20273, 60, "Jeremias escreveu num rolo todas as desgraças que sobreviriam à Babilônia, tudo que fora registrado acerca da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20274, 61, "Ele disse a Seraías: ""Quando você chegar à Babilônia, tenha o cuidado de ler todas estas palavras em voz alta.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20275, 62, "Então diga: ‘Ó Senhor, disseste que destruirás este lugar, para que nem homem nem animal viva nele, pois ficará em ruínas para sempre’.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20276, 63, "Quando você terminar de ler este rolo, amarre nele uma pedra e atire-o no Eufrates.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20277, 64, "Então diga: ‘Assim Babilônia afundará para não mais se erguer, por causa da desgraça que trarei sobre ela. E seu povo cairá’ "". Aqui terminam as palavras de Jeremias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20278, 1, "Zedequias tinha vinte e um anos quando se tornou rei, e reinou onze anos em Jerusalém. O nome de sua mãe era Hamutal, filha de Jeremias, de Libna.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20279, 2, "Ele fez o que o Senhor reprova, assim como fez Jeoaquim.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20280, 3, "A ira do Senhor tem sido provocada em Jerusalém e em Judá de tal forma que tenho que tirá-los da minha frente. Zedequias se rebelou contra o rei da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20281, 4, "Então, no nono ano do reinado de Zedequias, no décimo mês, Nabucodonosor, rei da Babilônia, marchou contra Jerusalém com todo o seu exército. Acamparam fora da cidade e construíram torres de assalto ao redor dela.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20282, 5, "A cidade ficou sob cerco até o décimo primeiro ano do rei Zedequias.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20283, 6, "Ao chegar o nono dia do quarto mês a fome era tão severa que não havia comida para o povo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20284, 7, "Então o muro da cidade foi rompido. O rei e todos os soldados fugiram e saíram da cidade, à noite, na direção do jardim real, pela porta entre os dois muros, embora os babilônios estivessem cercando a cidade. Foram para a Arabá,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20285, 8, "mas os babilônios perseguiram o rei Zedequias e o alcançaram na planície de Jericó. Todos os seus soldados se separaram dele e se dispersaram,", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20286, 9, "e ele foi capturado. Ele foi levado ao rei da Babilônia em Ribla, na terra de Hamate, que o sentenciou.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20287, 10, "Em Ribla, o rei da Babilônia mandou executar os filhos de Zedequias diante de seus olhos, e também matou todos os nobres de Judá.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20288, 11, "Então mandou furar os olhos de Zedequias e prendê-lo com correntes de bronze e o levou para a Babilônia, onde o manteve na prisão até o dia de sua morte.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20289, 12, "No décimo dia do quinto mês, no décimo nono ano de Nabucodonosor, rei da Babilônia, Nebuzaradã, comandante da guarda imperial, que servia o rei da Babilônia, veio a Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20290, 13, "Ele incendiou o templo do Senhor, o palácio real e todas as casas de Jerusalém. Todo o edifício importante foi incendiado por ele.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20291, 14, "Todo o exército babilônio, sob o comandante da guarda imperial, derrubou todos os muros em torno de Jerusalém.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20292, 15, "Nebuzaradã, o comandante da guarda imperial, deportou para a Babilônia alguns dos mais pobres e o povo que restou na cidade, juntamente com o restante dos artesãos e aqueles que tinham se rendido ao rei da Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20293, 16, "Mas Nebuzaradã deixou para trás o restante dos mais pobres da terra para trabalhar nas vinhas e campos.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20294, 17, "Os babilônios despedaçaram as colunas de bronze, os estrados móveis e o mar de bronze que ficavam no templo do Senhor e levaram todo o bronze para a Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20295, 18, "Também levaram embora as panelas, pás, tesouras de pavio, bacias de aspersão, tigelas e todos os utensílios de bronze usados no serviço do templo.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20296, 19, "O comandante da guarda imperial levou embora as pias, os incensários, as bacias de aspersão, as panelas, os candeeiros, as tigelas e as bacias usadas para as ofertas derramadas, tudo que era feito de ouro puro ou de prata.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20297, 20, "O bronze tirado das duas colunas, o mar e os doze touros de bronze debaixo dele, e os estrados móveis, que o rei Salomão fizera para o templo do Senhor, eram mais do que se podia pesar.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20298, 21, "Cada uma das colunas tinha oito metros e dez centímetros de altura e cinco metros e quarenta centímetros de circunferência; cada uma tinha quadro dedos de espessura e era oca.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20299, 22, "O capitel de bronze no alto de uma coluna tinha dois metros e vinte e cinco centímetros de altura e era ornamentado com um trabalho entrelaçado e romãs de bronze em volta, tudo de bronze. A outra coluna, com suas romãs, era igual.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20300, 23, "Havia noventa e seis romãs nos lados; o número total de romãs acima do trabalho entrelaçado ao redor era de cem.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20301, 24, "O comandante da guarda tomou como prisioneiros o sumo sacerdote Seraías, o sacerdote adjunto Sofonias e os três guardas das portas.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20302, 25, "Dos que ainda estavam na cidade, tomou o oficial encarregado dos homens de combate e sete conselheiros reais. Também tomou o secretário que era o oficial maior encarregado do alistamento do povo da terra e sessenta de seus homens que foram encontrados na cidade.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20303, 26, "O comandante Nebuzaradã tomou todos eles e os levou ao rei da Babilônia em Ribla.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20304, 27, "Ali em Ribla, na terra de Hamate, o rei fez com que fossem executados. Assim Judá foi para o cativeiro, longe de sua terra.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20305, 28, "Este é o número dos que Nebuzaradã levou para o exílio: no sétimo ano, 3. 023 judeus;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20306, 29, "no décimo oitavo ano de Nabucodonosor, 832 de Jerusalém;", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20307, 30, "em seu vigésimo terceiro ano, 745 judeus levados ao exílio pelo comandante da guarda imperial Nebuzaradã. Foram ao todo 4. 600 judeus.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20308, 31, "No ano trinta e sete do exílio do rei Joaquim de Judá, no ano em que Evil-Merodaque tornou-se rei de Babilônia, ele libertou o rei Joaquim de Judá da prisão no dia vinte e cinco do décimo segundo mês.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20309, 32, "Ele falou bondosamente com ele e deu-lhe um assento de honra mais elevado do que os dos outros reis que estavam com ele na Babilônia.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20310, 33, "Desse modo Joaquim tirou as roupas da prisão e pelo resto da vida comeu à mesa do rei.", 23);
INSERT INTO verses (id, number, text, chapter_id) VALUES (20311, 34, "Dia após dia o rei da Babilônia deu a Joaquim uma pensão diária enquanto ele viveu, até o dia de sua morte.", 23);