INSERT INTO verses (id, number, text, chapter_id) VALUES (12871, 1, "Na terra de Uz vivia um homem chamado Jó. Era homem íntegro e justo; temia a Deus e evitava o mal.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12872, 2, "Tinha ele sete filhos e três filhas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12873, 3, "e possuía sete mil ovelhas, três mil camelos, quinhentas parelhas de boi e quinhentos jumentos, e tinha muita gente a seu serviço. Era o homem mais rico do oriente.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12874, 4, "Seus filhos costumavam dar banquetes em casa, um de cada vez, e convidavam suas três irmãs para comerem e beberem com eles.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12875, 5, "Terminado um período de banquetes, Jó mandava chamá-los e fazia com que se purificassem. De madrugada ele oferecia um holocausto em favor de cada um deles, pois pensava: ""Talvez os meus filhos tenham lá no íntimo pecado e amaldiçoado a Deus"". Essa era a prática constante de Jó.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12876, 6, "Certo dia os anjos vieram apresentar-se ao Senhor, e Satanás também veio com eles.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12877, 7, "O Senhor disse a Satanás: ""De onde você veio? "" Satanás respondeu ao Senhor: ""De perambular pela terra e andar por ela"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12878, 8, "Disse então o Senhor a Satanás: ""Reparou em meu servo Jó? Não há ninguém na terra como ele, irrepreensível, íntegro, homem que teme a Deus e evita o mal"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12879, 9, """Será que Jó não tem razões para temer a Deus? "", respondeu Satanás.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12880, 10, """Acaso não puseste uma cerca em volta dele, da família dele e de tudo o que ele possui? Tu mesmo tens abençoado tudo o que ele faz, de modo que todos os seus rebanhos estão espalhados por toda a terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12881, 11, "Mas estende a tua mão e fere tudo o que ele tem, e com certeza ele te amaldiçoará na tua face. """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12882, 12, "O Senhor disse a Satanás: ""Pois bem, tudo o que ele possui está nas suas mãos; apenas não encoste um dedo nele"". Então Satanás saiu da presença do Senhor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12883, 13, "Certo dia, quando os filhos e as filhas de Jó estavam num banquete, comendo e bebendo vinho na casa do irmão mais velho,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12884, 14, "um mensageiro veio dizer a Jó: ""Os bois estavam arando, e os jumentos estavam pastando por perto,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12885, 15, "e os sabeus atacaram e os levaram embora. Mataram à espada os empregados, e eu fui o único que escapou para lhe contar! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12886, 16, "Enquanto ele ainda estava falando, chegou outro mensageiro e disse: ""Fogo de Deus caiu do céu e queimou totalmente as ovelhas e os empregados, e eu fui o único que escapou para lhe contar! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12887, 17, "Enquanto ele ainda estava falando, chegou outro mensageiro e disse: ""Vieram caldeus em três bandos, atacaram os camelos e os levaram embora. Mataram à espada os empregados, e eu fui o único que escapou para lhe contar! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12888, 18, "Enquanto ele ainda estava falando, chegou ainda outro mensageiro e disse: ""Seus filhos e suas filhas estavam num banquete, comendo e bebendo vinho na casa do irmão mais velho,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12889, 19, "quando, de repente, um vento muito forte veio do deserto e atingiu os quatro cantos da casa, que desabou. Eles morreram, e eu fui o único que escapou para lhe contar! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12890, 20, "Ao ouvir isso, Jó levantou-se, rasgou o manto e rapou a cabeça. Então prostrou-se no chão em adoração,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12891, 21, "e disse: ""Saí nu do ventre da minha mãe, e nu partirei. O Senhor o deu, o Senhor o levou; louvado seja o nome do Senhor "".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12892, 22, "Em tudo isso Jó não pecou nem de nada culpou a Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12893, 1, "Num outro dia os anjos vieram apresentar-se ao Senhor, e Satanás também veio com eles para apresentar-se.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12894, 2, "E o Senhor disse a Satanás, ""De onde você vem? "" Satanás respondeu ao Senhor: ""De perambular pela terra e andar por ela"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12895, 3, "Disse então o Senhor a Satanás: ""Reparou em meu servo Jó? Não há ninguém na terra como ele, irrepreensível, íntegro, homem que teme a Deus e evita o mal. Ele se mantém íntegro, apesar de você me haver instigado contra ele para arruiná-lo sem motivo"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12896, 4, """Pele por pele! "", respondeu Satanás. ""Um homem dará tudo o que tem por sua vida.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12897, 5, "Estende a tua mão e fere a sua carne e os seus ossos, e com certeza ele te amaldiçoará na tua face. """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12898, 6, "O Senhor disse a Satanás: ""Pois bem, ele está nas suas mãos; apenas poupe a vida dele"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12899, 7, "Saiu, pois, Satanás da presença do Senhor e afligiu Jó com feridas terríveis, da sola dos pés ao alto da cabeça.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12900, 8, "Então Jó apanhou um caco de louça com o qual se raspava, sentado entre as cinzas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12901, 9, "Então sua mulher lhe disse: ""Você ainda mantém a sua integridade? Amaldiçoe a Deus, e morra! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12902, 10, "Ele respondeu: ""Você fala como uma insensata. Aceitaremos o bem dado por Deus, e não o mal? "" Em tudo isso Jó não pecou com os lábios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12903, 11, "Quando três amigos de Jó, Elifaz, de Temã, Bildade, de Suá, e Zofar, de Naamate, souberam de todos os males que o haviam atingido, saíram, cada um da sua região, e combinaram encontrar-se para mostrar solidariedade a Jó e consolá-lo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12904, 12, "Quando o viram à distância, mal puderam reconhecê-lo e começaram a chorar em alta voz. Cada um deles rasgou o manto e colocou terra sobre a cabeça.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12905, 13, "Depois se assentaram no chão com ele, durante sete dias e sete noites. Ninguém lhe disse uma palavra, pois viam como era grande o seu sofrimento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12906, 1, "Depois disso Jó abriu a boca e amaldiçoou o dia do seu nascimento,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12907, 2, "dizendo:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12908, 3, """Pereça o dia do meu nascimento e a noite em que se disse: ‘Nasceu um menino! ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12909, 4, "Transforme-se aquele dia em trevas, e Deus, lá do alto, não se importe com ele; não resplandeça a luz sobre ele.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12910, 5, "Chamem-no de volta as trevas e a mais densa escuridão; coloque-se uma nuvem sobre ele e o negrume aterrorize a sua luz.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12911, 6, "Apoderem-se daquela noite densas trevas! Não seja ela incluída entre os dias do ano, nem faça parte de nenhum dos meses.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12912, 7, "Seja aquela noite estéril, e nela não se ouçam brados de alegria.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12913, 8, "Amaldiçoem aquele dia os que amaldiçoam os dias e são capazes de atiçar o Leviatã.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12914, 9, "Fiquem escuras as suas estrelas matutinas, e espere ele em vão pela luz do sol e não veja os primeiros raios da alvorada,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12915, 10, "pois não fechou as portas do ventre materno para evitar que eu contemplasse males.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12916, 11, """Por que não morri ao nascer, e não pereci quando saí do ventre?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12917, 12, "Por que houve joelhos para me receberem e seios para me amamentarem?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12918, 13, "Agora eu bem poderia estar deitado em paz e achar repouso", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12919, 14, "junto aos reis e conselheiros da terra, que construíram para si lugares que agora jazem em ruínas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12920, 15, "com governantes que possuíam ouro, que enchiam suas casas de prata.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12921, 16, "Por que não me sepultaram como criança abortada, como um bebê que nunca viu a luz do dia?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12922, 17, "Ali os ímpios já não se agitam, e ali os cansados permanecem em repouso;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12923, 18, "os prisioneiros também desfrutam sossego, já não ouvem mais os gritos do feitor de escravos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12924, 19, "Os simples e os poderosos ali estão, e o escravo está livre de seu senhor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12925, 20, """Por que se dá luz aos infelizes, e vida aos de alma amargurada,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12926, 21, "aos que anseiam pela morte, e esta não vem, e a procuram mais do que a um tesouro oculto,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12927, 22, "aos que se enchem de alegria e exultam quando vão para a sepultura?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12928, 23, "Por que se dá vida àquele cujo caminho é oculto, e a quem Deus fechou as saídas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12929, 24, "Pois me vêm suspiros em vez de comida; meus gemidos transbordam como água.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12930, 25, "O que eu temia veio sobre mim; o que eu receava me aconteceu.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12931, 26, "Não tenho paz, nem tranqüilidade, nem descanso; somente inquietação"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12932, 1, "Então respondeu Elifaz, de Temã:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12933, 2, """Se alguém se aventurar a dizer-lhe uma palavra, você ficará impaciente? Mas quem pode refrear as palavras?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12934, 3, "Pense bem! Você ensinou a tantos; fortaleceu mãos fracas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12935, 4, "Suas palavras davam firmeza aos que tropeçavam; você fortaleceu joelhos vacilantes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12936, 5, "Mas agora que se vê em dificuldade, você se desanima; quando você é atingido, fica prostrado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12937, 6, "Sua vida piedosa não lhe inspira confiança, e o seu procedimento irrepreensível não lhe dá esperança?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12938, 7, """Reflita agora: Qual foi o inocente que chegou a perecer? Onde foi que os íntegros sofreram destruição?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12939, 8, "Pelo que tenho observado, quem cultiva o mal e semeia maldade, isso também colherá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12940, 9, "Pelo sopro de Deus são destruídos; pelo vento de sua ira eles perecem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12941, 10, "Os leões podem rugir e rosnar, mas até os dentes dos leões fortes se quebram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12942, 11, "O leão morre por falta de presa, e os filhotes da leoa se dispersam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12943, 12, """Disseram-me uma palavra em segredo, da qual os meus ouvidos captaram um murmúrio.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12944, 13, "Em meio a sonhos perturbadores da noite, quando cai sono profundo sobre os homens,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12945, 14, "temor e tremor se apoderaram de mim e fizeram estremecer todos os meus ossos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12946, 15, "Um espírito roçou o meu rosto, e os pêlos do meu corpo se arrepiaram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12947, 16, "Ele parou, mas não pude identificá-lo. Um vulto se pôs diante dos meus olhos, e ouvi uma voz suave, que dizia:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12948, 17, "‘Poderá algum mortal ser mais justo que Deus? Poderá algum homem ser mais puro que o seu Criador?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12949, 18, "Se Deus não confia em seus servos, se vê erro em seus anjos e os acusa,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12950, 19, "quanto mais nos que moram em casas de barro, cujos alicerces estão no pó! São mais facilmente esmagados que uma traça!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12951, 20, "Entre o alvorecer e o crepúsculo são despedaçados; perecem para sempre, sem sequer serem notados.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12952, 21, "Não é certo que as cordas de suas tendas são arrancadas, e eles morrem sem sabedoria? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12953, 1, """Clame, se quiser, mas quem o ouvirá? Para qual dos seres celestes você se voltará?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12954, 2, "O ressentimento mata o insensato, e a inveja destrói o tolo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12955, 3, "Eu mesmo já vi um insensato lançar raízes, mas de repente a sua casa foi amaldiçoada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12956, 4, "Seus filhos longe estão de desfrutar segurança, maltratados nos tribunais, não há quem os defenda.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12957, 5, "Os famintos devoram a sua colheita, tirando-a até do meio dos espinhos, e os sedentos sugam a sua riqueza.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12958, 6, "Pois o sofrimento não brota do pó, e as dificuldades não nascem do chão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12959, 7, "No entanto o homem nasce para as dificuldades tão certamente como as fagulhas voam para cima.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12960, 8, """Mas, se fosse comigo, eu apelaria para Deus; apresentaria a ele a minha causa.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12961, 9, "Ele realiza maravilhas insondáveis, milagres que não se pode contar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12962, 10, "Derrama chuva sobre a terra, e envia água sobre os campos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12963, 11, "Os humildes, ele os exalta, e traz os que pranteiam a um lugar de segurança.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12964, 12, "Ele frustra os planos dos astutos, para que fracassem as mãos deles.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12965, 13, "Apanha os sábios na astúcia deles, e as maquinações dos astutos são malogradas por sua precipitação.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12966, 14, "As trevas vêm sobre eles em pleno dia; ao meio-dia eles tateiam como se fosse noite.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12967, 15, "Ele salva o oprimido da espada que trazem na boca; salva-o das garras dos poderosos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12968, 16, "Por isso os pobres têm esperança, e a injustiça cala a boca.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12969, 17, """Como é feliz o homem a quem Deus corrige; portanto, não despreze a disciplina do Todo-poderoso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12970, 18, "Pois ele fere, mas dela vem tratar; ele machuca, mas suas mãos também curam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12971, 19, "De seis desgraças ele o livrará; em sete delas você nada sofrerá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12972, 20, "Na fome ele o livrará da morte, e na guerra o livrará do golpe da espada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12973, 21, "Você será protegido do açoite da língua, e não precisará ter medo quando a destruição chegar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12974, 22, "Você rirá da destruição e da fome, e não precisará temer as feras da terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12975, 23, "Pois fará aliança com as pedras do campo, e os animais selvagens estarão em paz com você.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12976, 24, "Você saberá que a sua tenda é segura; contará os seus bens da tua morada e de nada achará falta.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12977, 25, "Você saberá que os seus filhos serão muitos, e que os seus descendentes serão como a relva da terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12978, 26, "Você irá para a sepultura em pleno vigor, como um feixe recolhido no devido tempo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12979, 27, """Foi isso que verificamos ser verdade. Portanto, ouça e aplique isso à sua vida"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12980, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12981, 2, """Se tão-somente pudessem pesar a minha aflição e pôr na balança a minha desgraça!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12982, 3, "Veriam que o seu peso é maior que o da areia dos mares. Por isso as minhas palavras são tão impetuosas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12983, 4, "As flechas do Todo-poderoso estão cravadas em mim, e o meu espírito suga delas o veneno; os terrores de Deus estão posicionados contra mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12984, 5, "Zurra o jumento selvagem, se tiver capim? Muge o boi, se tiver forragem?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12985, 6, "Come-se sem sal uma comida insípida? E a clara do ovo, tem algum sabor?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12986, 7, "Recuso-me a tocar nisso; esse tipo de comida causa-me repugnância.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12987, 8, """Se tão-somente fosse atendido o meu pedido, se Deus me concedesse o meu desejo,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12988, 9, "se Deus se dispusesse a esmagar-me, a soltar a mão protetora e eliminar-me!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12989, 10, "Pois eu ainda teria o consolo, minha alegria em meio à dor implacável, de não ter negado as palavras do Santo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12990, 11, """Que esperança posso ter, se já não tenho forças? Como posso ter paciência, se não tenho futuro?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12991, 12, "Acaso tenho a força da pedra? Acaso a minha carne é de bronze?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12992, 13, "Haverá poder que me ajude, agora que os meus recursos se foram?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12993, 14, """Um homem desesperado deve receber a compaixão de seus amigos, muito embora ele tenha abandonado o temor do Todo-poderoso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12994, 15, "Mas os meus irmãos enganaram-me como riachos temporários, como os riachos que transbordam", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12995, 16, "quando o degelo os torna turvos e a neve que se derrete os faz encher,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12996, 17, "mas que param de fluir no tempo da seca, e no calor desaparecem dos seus leitos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12997, 18, "As caravanas se desviam de suas rotas; sobem para lugares desertos e perecem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12998, 19, "Procuram água as caravanas de Temá, olham esperançosos os mercadores de Sabá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12999, 20, "Ficam tristes, porque estavam confiantes; lá chegaram tão-somente para sofrer decepção.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13000, 21, "Pois agora vocês de nada me valeram; contemplam minha temível situação, e se enchem de medo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13001, 22, "Alguma vez lhes pedi que me dessem alguma coisa? Ou que da sua riqueza pagassem resgate por mim?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13002, 23, "Ou que me livrassem das mãos do inimigo? Ou que me libertassem das garras de quem me oprime?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13003, 24, """Ensinem-me, e eu me calarei; mostrem-me onde errei.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13004, 25, "Como doem as palavras verdadeiras! Mas o que provam os argumentos de vocês?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13005, 26, "Vocês pretendem corrigir o que digo e tratar como vento as palavras de um homem desesperado?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13006, 27, "Vocês seriam capazes de pôr em sorteio o órfão e de vender um amigo por uma bagatela!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13007, 28, """Mas agora, tenham a bondade de olhar para mim. Será que eu mentiria na frente de vocês?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13008, 29, "Reconsiderem a questão, não sejam injutos; tornem a analisá-la, pois a minha integridade está em jogo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13009, 30, "Há alguma iniqüidade em meus lábios? Será que a minha boca não consegue discernir a maldade?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13010, 1, """Não é pesado o labor do homem na terra? Seus dias não são como os de um assalariado?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13011, 2, "Como o escravo que anseia pelas sombras do entardecer, ou como o assalariado que espera ansioso pelo pagamento,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13012, 3, "assim me deram meses de ilusão, e noites de desgraça me foram destinadas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13013, 4, "Quando me deito, fico pensando: ‘Quanto vai demorar para eu me levantar? ’ A noite se arrasta, e eu fico me virando na cama até o amanhecer.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13014, 5, "Meu corpo está coberto de vermes e cascas de ferida, minha pele está rachada e vertendo pus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13015, 6, """Meus dias correm mais depressa que a lançadeira do tecelão, e chegam ao fim sem nenhuma esperança.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13016, 7, "Lembra-te, ó Deus, de que a minha vida não passa de um sopro; meus olhos jamais tornarão a ver a felicidade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13017, 8, "Os que agora me vêem, nunca mais me verão; puseste o teu olhar em mim, e já não existo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13018, 9, "Assim como a nuvem esvai-se e desaparece, assim quem desce à sepultura não volta.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13019, 10, "Nunca mais voltará ao seu lar; a sua habitação não mais o conhecerá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13020, 11, """Por isso não me calo; na aflição do meu espírito me desabafarei, na amargura da minha alma farei as minhas queixas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13021, 12, "Sou eu o mar, ou o monstro das profundezas, para que me ponhas sob guarda?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13022, 13, "Quando penso que a minha cama me consolará e que o meu leito aliviará a minha queixa,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13023, 14, "mesmo aí me assustas com sonhos e me aterrorizas com visões.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13024, 15, "Prefiro ser estrangulado e morrer do que sofrer assim;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13025, 16, "sinto desprezo pela minha vida! Não vou viver para sempre; deixa-me, pois os meus dias não têm sentido.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13026, 17, """Que é o homem, para que lhe dês importância e atenção,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13027, 18, "para que o examines a cada manhã e o proves a cada instante?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13028, 19, "Nunca desviarás de mim o teu olhar? Nunca me deixarás a sós, nem por um instante?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13029, 20, "Se pequei, que mal te causei, ó tu que vigias os homens? Por que me tornaste teu alvo? Acaso tornei-me um fardo para ti?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13030, 21, "Por que não perdoas as minhas ofensas e não apagas os meus pecados? Pois logo me deitarei no pó; tu me procurarás, mas eu já não existirei"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13031, 1, "Então Bildade, de Suá, respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13032, 2, """Até quando você vai falar desse modo? Suas palavras são um grande vendaval!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13033, 3, "Acaso Deus torce a justiça? Será que o Todo-poderoso torce o que é direito?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13034, 4, "Quando os seus filhos pecaram contra ele, ele os castigou pelo mal que fizeram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13035, 5, "Mas, se você procurar a Deus e implorar junto ao Todo-poderoso,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13036, 6, "se você for íntegro e puro, ele se levantará agora mesmo em seu favor e o restabelecerá no lugar que por justiça cabe a você.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13037, 7, "O seu começo parecerá modesto, mas o seu futuro será de grande prosperidade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13038, 8, """Pergunte às gerações anteriores e veja o que os seus pais aprenderam,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13039, 9, "pois nós nascemos ontem e não sabemos nada. Nossos dias na terra não passam de uma sombra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13040, 10, "Acaso eles não o instruirão, não lhe falarão? Não proferirão palavras vindas do entendimento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13041, 11, "Poderá o papiro crescer senão no pântano? Sem água cresce o junco?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13042, 12, "Mal cresce e, antes de ser colhido, seca-se, mais depressa que qualquer grama.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13043, 13, "Esse é o destino de todo o que se esquece de Deus; assim perece a esperança dos ímpios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13044, 14, "Aquilo em que ele confia é frágil, aquilo em que se apóia é uma teia de aranha.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13045, 15, "Encosta-se em sua teia, mas ela cede; agarra-se a ela, mas ela não agüenta.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13046, 16, "Ele é como uma planta bem regada ao brilho do sol, espalhando seus brotos pelo jardim;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13047, 17, "entrelaça as raízes em torno de um monte de pedras e procura um lugar entre as rochas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13048, 18, "Mas, quando é arrancada do seu lugar, este o rejeita e diz: ‘Nunca o vi’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13049, 19, "Esse é o fim da sua vida, e do solo brotam outras plantas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13050, 20, """Pois o certo é que Deus não rejeita o íntegro, e não fortalece as mãos dos que fazem o mal.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13051, 21, "Mas, quanto a você, ele encherá de riso a sua boca e de brados de alegria os seus lábios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13052, 22, "Seus inimigos se vestirão de vergonha, e as tendas dos ímpios não mais existirão"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13053, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13054, 2, """Bem sei que isso é verdade. Mas como pode o mortal ser justo diante de Deus?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13055, 3, "Ainda que quisesse discutir com ele, não conseguiria argumentar nem uma vez em mil.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13056, 4, "Sua sabedoria é profunda, seu poder é imenso. Quem tentou resisti-lo e saiu ileso?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13057, 5, "Ele transporta montanhas sem que elas o saibam, e em sua ira as põe de cabeça para baixo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13058, 6, "Sacode a terra e a tira do lugar, e faz suas colunas tremerem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13059, 7, "Fala com o sol, e ele não brilha; ele veda e esconde a luz das estrelas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13060, 8, "Só ele estende os céus e anda sobre as ondas do mar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13061, 9, "Ele é o Criador da Ursa e do Órion, das Plêiades e das constelações do sul.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13062, 10, "Realiza maravilhas que não se podem perscrutar, milagres incontáveis.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13063, 11, "Quando passa por mim, não posso vê-lo; se passa junto de mim, não o percebo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13064, 12, "Se ele apanha algo, quem pode pará-lo? Quem pode dizer-lhe: ‘O que fazes? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13065, 13, "Deus não refreia a sua ira; até o séquito de Raabe encolheu-se diante dos seus pés.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13066, 14, """Como então poderei eu discutir com ele? Como achar palavras para com ele argumentar?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13067, 15, "Embora inocente, eu seria incapaz de responder-lhe; poderia apenas implorar misericórdia ao meu Juiz.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13068, 16, "Mesmo que eu o chamasse e ele me respondesse, não creio que me daria ouvidos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13069, 17, "Ele me esmagaria com uma tempestade e sem motivo multiplicaria minhas feridas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13070, 18, "Não me permitiria recuperar o fôlego, mas me engolfaria em agruras.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13071, 19, "Se é questão de força, ele é poderoso! E se é questão de justiça, quem o intimará?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13072, 20, "Mesmo sendo eu inocente, minha boca me condenaria; se eu fosse íntegro, ela me declararia culpado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13073, 21, """Conquanto eu seja íntegro, já não me importo comigo; desprezo a minha própria vida.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13074, 22, "É tudo a mesma coisa; por isso digo: Ele destrói tanto o íntegro como o ímpio.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13075, 23, "Quando um flagelo causa morte repentina, ele zomba do desespero dos inocentes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13076, 24, "Quando um país cai nas mãos dos ímpios, ele venda os olhos de seus juízes. Se não é ele, quem é então? """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13077, 25, """Meus dias correm mais velozes que um atleta; eles voam sem um vislumbre de alegria.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13078, 26, "Passam como barcos de papiro, como águias que mergulham sobre as presas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13079, 27, "Se eu disser: Vou esquecer a minha queixa, vou mudar o meu semblante e sorrir,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13080, 28, "ainda assim me apavoro com todos os meus sofrimentos, pois sei que não me considerarás inocente.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13081, 29, "Uma vez que já fui considerado culpado, por que deveria eu lutar em vão?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13082, 30, "Mesmo que eu me lavasse com sabão e limpasse as minhas mãos com soda de lavadeira,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13083, 31, "tu me atirarias num poço de lodo, para que até as minhas roupas me detestassem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13084, 32, """Ele não é homem como eu, para que eu lhe responda, e nos enfrentemos em juízo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13085, 33, "Se tão-somente houvesse alguém para servir de árbitro entre nós, para impor as mãos sobre nós dois,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13086, 34, "alguém que afastasse de mim a vara de Deus, para que o seu terror não mais me assustasse!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13087, 35, "Então eu falaria sem medo; mas não é esse o caso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13088, 1, """Minha vida só me dá desgosto; por isso darei vazão à minha queixa e de alma amargurada me expressarei.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13089, 2, "Direi a Deus: Não me condenes, revela-me que acusações tens contra mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13090, 3, "Tens prazer em oprimir-me, em rejeitar a obra de tuas mãos, enquanto sorris para o plano dos ímpios?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13091, 4, "Acaso tens olhos de carne? Enxergas como os mortais?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13092, 5, "Teus dias são como os de qualquer mortal? Os anos de tua vida são como os do homem?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13093, 6, "Pois investigas a minha iniqüidade e vasculhas o meu pecado,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13094, 7, "embora saibas que não sou culpado e que ninguém pode livrar-me das tuas mãos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13095, 8, """Foram as tuas mãos que me formaram e me fizeram. Irás agora voltar-te e destruir-me?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13096, 9, "Lembra-te de que me moldaste como o barro, e agora me farás voltar ao pó?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13097, 10, "Acaso não me despejaste como leite e não me coalhaste como queijo?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13098, 11, "Não me vestiste de pele e carne e não me juntaste com ossos e tendões?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13099, 12, "Deste-me vida e foste bondoso para comigo, e na tua providência cuidaste do meu espírito.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13100, 13, """Mas algo escondeste em teu coração, e bem sei que és tu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13101, 14, "Se eu pecasse, me estarias observando e não deixarias sem punição a minha ofensa.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13102, 15, "Se eu fosse culpado, ai de mim! Mesmo sendo inocente, não posso erguer a cabeça, pois estou dominado pela vergonha e mergulhado na minha aflição.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13103, 16, "Se mantenho a cabeça erguida, ficas à minha espreita como um leão, e de novo manifestas contra mim o teu poder tremendo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13104, 17, "Trazes novas testemunhas contra mim e contra mim aumentas a tua ira; teus exércitos atacam-me, em batalhões sucessivos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13105, 18, """Então, por que me fizeste sair do ventre? Eu preferia ter morrido antes que pudesse ser visto.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13106, 19, "Se tão-somente eu jamais tivesse existido, ou fosse levado direto do ventre para a sepultura!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13107, 20, "Já estariam no fim os meus poucos dias? Afasta-te de mim, para que eu tenha um instante de alegria,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13108, 21, "antes que eu vá para o lugar do qual não há retorno, para a terra de sombras e densas trevas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13109, 22, "para a terra tenebrosa como a noite, terra de trevas e de caos, onde até mesmo a luz é trevas"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13110, 1, "Então Zofar, de Naamate, respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13111, 2, """Ficarão sem resposta todas essas palavras? Irá se confirmar o que esse tagarela diz?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13112, 3, "Sua conversa tola calará os homens? Ninguém o repreenderá por sua zombaria?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13113, 4, "Você diz a Deus: ‘A doutrina que eu aceito é perfeita, e sou puro aos teus olhos’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13114, 5, "Ah, se Deus lhe falasse, se abrisse os lábios contra você", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13115, 6, "e lhe revelasse os segredos da sabedoria! Pois a verdadeira sabedoria é complexa. Fique sabendo que Deus esqueceu alguns dos seus pecados.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13116, 7, """Você consegue perscrutar os mistérios de Deus? Pode sondar os limites do Todo-poderoso?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13117, 8, "São mais altos que os céus! Que é que você poderá fazer? São mais profundos que as profundezas! O que você poderá saber?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13118, 9, "Seu comprimento é maior do que a terra e a sua largura é maior do que o mar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13119, 10, """Se ele ordena uma prisão e convoca o tribunal, quem poderá opor-se?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13120, 11, "Pois ele identifica os enganadores; e não reconhece a iniqüidade logo que a vê?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13121, 12, "Mas o tolo só será sábio quando a cria do jumento selvagem nascer homem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13122, 13, """Contudo, se você lhe consagrar o coração, e estender as mãos para ele;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13123, 14, "se afastar das suas mãos o pecado, e não permitir que a maldade habite em sua tenda,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13124, 15, "então você levantará o rosto sem envergonhar-se; serás firme e destemido.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13125, 16, "Você esquecerá as suas desgraças, lembrando-as apenas como águas passadas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13126, 17, "A vida será mais refulgente que o meio-dia, e as trevas serão como a manhã que brilha.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13127, 18, "Você estará confiante, graças a esperança que haverá; olhará ao redor, e repousará em segurança.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13128, 19, "Você se deitará, e ninguém lhe causará medo, e muitos procurarão o seu favor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13129, 20, "Mas os olhos dos ímpios fenecerão, e em vão procurarão refúgio; o suspiro da morte será a esperança que terão"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13130, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13131, 2, """Sem dúvida vocês são o povo, e a sabedoria morrerá com vocês!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13132, 3, "Mas eu tenho a mesma capacidade de pensar que vocês têm; não sou inferior a vocês. Quem não sabe dessas coisas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13133, 4, """Tornei-me objeto de riso para os meus amigos, eu que clamava a Deus e ele me respondia, eu, íntegro e irrepreensível, um mero objeto de riso!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13134, 5, "Quem está bem despreza a desgraça, o destino daqueles cujos pés escorregam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13135, 6, "As tendas dos saqueadores não sofrem perturbação, e aqueles que provocam a Deus estão seguros, aqueles que transportam o seu deus em suas mãos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13136, 7, """Pergunte, porém, aos animais, e eles o ensinarão, ou às aves do céu, e elas lhe contarão;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13137, 8, "fale com a terra, e ela o instruirá, deixe que os peixes do mar o informem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13138, 9, "Quem de todos eles ignora que a mão do Senhor fez isso?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13139, 10, "Em sua mão está a vida de cada criatura e o fôlego de toda a humanidade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13140, 11, "O ouvido não experimenta as palavras como a língua experimenta a comida?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13141, 12, "A sabedoria se acha entre os idosos? A vida longa traz entendimento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13142, 13, """Deus é que tem sabedoria e poder; a ele pertencem o conselho e o entendimento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13143, 14, "O que ele derruba não se pode reconstruir; aquele a quem ele aprisiona ninguém pode libertar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13144, 15, "Se ele retém as águas, predomina a seca; se as solta, devastam a terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13145, 16, "A ele pertencem a força e a sabedoria; tanto o enganado quanto o enganador a ele pertencem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13146, 17, "Ele despoja e demite os conselheiros, e faz os juízes de tolos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13147, 18, "Tira as algemas postas pelos reis, e amarra uma faixa em torno da cintura deles.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13148, 19, "Despoja e demite os sacerdotes, e arruína os homens de sólida posição.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13149, 20, "Cala os lábios dos conselheiros de confiança, e tira o discernimento dos anciãos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13150, 21, "Derrama desprezo sobre os nobres, e desarma os poderosos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13151, 22, "Revela coisas profundas das trevas, e traz à luz densas sombras.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13152, 23, "Dá grandeza às nações, e as destrói; faz crescer as nações, e as dispersa.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13153, 24, "Priva da razão os líderes da terra, e os envia a perambular num deserto sem caminhos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13154, 25, "Andam tateando nas trevas, sem nenhuma luz; ele os faz cambalear como bêbados.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13155, 1, """Meus olhos viram tudo isso, meus ouvidos o ouviram e entenderam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13156, 2, "O que vocês sabem, eu também sei; não sou inferior a vocês.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13157, 3, "Mas desejo falar ao Todo-poderoso e defender a minha causa diante de Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13158, 4, "Vocês, porém, me difamam com mentiras; todos vocês são médicos que de nada valem!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13159, 5, "Se tão-somente ficassem calados! Mostrariam sabedoria.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13160, 6, "Escutem agora o meu argumento; prestem atenção à réplica de meus lábios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13161, 7, "Vocês vão falar com maldade em nome de Deus? Vão falar enganosamente a favor dele?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13162, 8, "Vão revelar parcialidade por ele? Vão defender a causa a favor de Deus?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13163, 9, "Tudo iria bem, se ele os examinasse? Vocês conseguiriam enganá-lo, como podem enganar os homens?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13164, 10, "Com certeza ele os repreenderia, se no íntimo vocês fossem parciais.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13165, 11, "O esplendor dele não os aterrorizaria? O pavor dele não cairia sobre vocês?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13166, 12, "As máximas que vocês citam são provérbios de cinza; suas defesas não passam de barro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13167, 13, """Aquietem-se e deixem-me falar; e aconteça-me o que me acontecer.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13168, 14, "Por que me ponho em perigo e tomo a minha vida em minhas mãos?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13169, 15, "Embora ele me mate, ainda assim esperarei nele; certo é que defenderei os meus caminhos diante dele.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13170, 16, "Aliás, isso será a minha libertação, pois nenhum ímpio ousaria apresentar-se a ele!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13171, 17, "Escutem atentamente as minhas palavras; que os seus ouvidos acolham o que eu digo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13172, 18, "Agora que preparei a minha defesa, sei que serei justificado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13173, 19, "Haverá quem me acuse? Se houver, ficarei calado e morrerei.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13174, 20, """Concede-me só estas duas coisas, ó Deus, e não me esconderei de ti:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13175, 21, "Afasta de mim a tua mão, e não mais me assuste com os teus terrores.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13176, 22, "Chama-me, e eu responderei, ou deixa-me falar, e tu responderás.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13177, 23, "Quantos erros e pecados cometi? Mostra-me a minha falta e o meu pecado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13178, 24, "Por que escondes o teu rosto e consideras-me teu inimigo?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13179, 25, "Atormentarás uma folha levada pelo vento? Perseguirás a palha?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13180, 26, "Pois fazes constar contra mim coisas amargas e fazes-me herdar os pecados da minha juventude.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13181, 27, "Acorrentas os meus pés e vigias todos os meus caminhos, pondo limites aos meus passos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13182, 28, """Assim o homem se consome como coisa podre, como a roupa que a traça vai roendo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13183, 1, """O homem nascido de mulher vive pouco tempo e passa por muitas dificuldades.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13184, 2, "Brota como a flor e murcha. Vai-se como a sombra passageira; não dura muito.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13185, 3, "Fixas o olhar num homem desses? E o trarás à tua presença para julgamento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13186, 4, "Quem pode extrair algo puro da impureza? Ninguém!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13187, 5, "Os dias do homem estão determinados; tu decretaste o número de seus meses e estabeleceste limites que ele não pode ultrapassar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13188, 6, "Por isso desvia dele o teu olhar, e deixa-o, até que ele cumpra o seu tempo como trabalhador contratado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13189, 7, """Para a árvore pelo menos há esperança: se é cortada, torna a brotar, e os seus renovos vingam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13190, 8, "Suas raízes poderão envelhecer no solo e seu tronco morrer no chão;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13191, 9, "ainda assim, com o cheiro de água ela brotará e dará ramos como se fosse muda plantada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13192, 10, "Mas o homem morre, e morto permanece; dá o último suspiro, e deixa de existir.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13193, 11, "Assim como a água desaparece do mar e o leito do rio perde as águas e seca,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13194, 12, "assim o homem se deita e não se levanta; até quando os céus já não existirem, os homens não acordarão e não serão despertados do seu sono.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13195, 13, """Se tão-somente me escondesses na sepultura e me ocultasses até passar a tua ira! Se tão-somente me impusesses um prazo e depois te lembrasses de mim!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13196, 14, "Quando um homem morre, acaso tornará a viver? Durante todos os dias do meu árduo labor esperarei pela minha dispensa.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13197, 15, "Chamarás, e eu te responderei; terás anelo pela criatura que as tuas mãos fizeram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13198, 16, "Por certo contarás então os meus passos, mas não tomarás conhecimento do meu pecado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13199, 17, "Minhas faltas serão encerradas num saco; tu esconderás a minha iniqüidade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13200, 18, """Mas, assim como a montanha sofre erosão e desmorona, e a rocha muda de lugar;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13201, 19, "e assim como a água desgasta as pedras e as torrentes arrastam terra, assim destróis a esperança do homem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13202, 20, "Tu o subjulgas de uma vez por todas, e ele se vai; alteras a sua fisionomia, e o mandas embora.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13203, 21, "Se honram os seus filhos, ele não fica sabendo; se os humilham, ele não o vê.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13204, 22, "Só sente a dor do seu próprio corpo; só pranteia por si mesmo"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13205, 1, "Então Elifaz, de Temã, respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13206, 2, """Responderia o sábio com idéias vãs, ou encheria o estômago com o vento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13207, 3, "Será que argumentaria com palavras inúteis, com discursos sem valor?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13208, 4, "Mas você sufoca a piedade e diminui a devoção a Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13209, 5, "O seu pecado motiva a sua boca; você adota a linguagem dos astutos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13210, 6, "É a sua própria boca que o condena, e não a minha; os seus próprios lábios depõem contra você.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13211, 7, """Será que você foi o primeiro a nascer? Acaso foi gerado antes das colinas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13212, 8, "Você costuma ouvir o conselho secreto de Deus? Só a você pertence a sabedoria?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13213, 9, "Que é que você sabe, que nós não sabemos? Que compreensão têm você, que nós não temos?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13214, 10, "Temos do nosso lado homens de cabelos brancos, muito mais velhos que o seu pai.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13215, 11, "Não lhe bastam as consolações divinas, e as nossas palavras amáveis?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13216, 12, "Por que você se deixa levar pelo coração, e por que esse brilho nos seus olhos?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13217, 13, "Pois contra Deus é que você dirige a sua ira e despeja da sua boca essas palavras!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13218, 14, """Como o homem pode ser puro? Como pode ser justo quem nasce de mulher?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13219, 15, "Pois se nem nos seus Deus confia, e se nem os céus são puros aos seus olhos,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13220, 16, "quanto menos o homem, que é impuro e corrupto, e que bebe iniqüidade como água.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13221, 17, """Escute-me, e eu lhe explicarei; vou dizer-lhe o que vi,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13222, 18, "o que os sábios declaram, sem esconder o que receberam dos seus pais,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13223, 19, "a quem foi dada a terra, e a mais ninguém; nenhum estrangeiro passou entre eles:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13224, 20, "O ímpio sofre tormentos a vida toda, como também o homem cruel, nos poucos anos que lhe são reservados.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13225, 21, "Só ouve ruídos aterrorizantes; quando se sente em paz, ladrões o atacam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13226, 22, "Não tem esperança de escapar das trevas; sente-se destinado ao fio da espada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13227, 23, "Fica perambulando; é comida para os abutres; sabe muito bem que logo virão sobre ele as trevas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13228, 24, "A aflição e a angústia o apavoram e o dominam; como um rei pronto para bater,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13229, 25, "porque agitou os punhos contra Deus, e desafiou o Todo-poderoso,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13230, 26, "afrontando-o com arrogância com um escudo grosso e resistente.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13231, 27, """Apesar de ter o rosto coberto de gordura e a cintura estufada de carne,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13232, 28, "habitará em cidades prestes a arruinar-se, em casas inabitáveis, caindo aos pedaços.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13233, 29, "Nunca mais será rico; sua riqueza não durará, e os seus bens não se propagarão pela terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13234, 30, "Não poderá escapar das trevas; o fogo chamuscará os seus renovos, e o sopro da boca de Deus o arrebatará.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13235, 31, "Que ele não se iluda em confiar no que não tem valor, pois nada receberá como compensação.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13236, 32, "Terá completa paga antes do tempo, e os seus ramos não florescerão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13237, 33, "Ele será como a vinha despojada de suas uvas verdes, como a oliveira que perdeu a sua floração,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13238, 34, "pois o companheirismo dos ímpios nada lhe trará, e o fogo devorará as tendas dos que gostam de subornar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13239, 35, "Eles concebem maldade e dão à luz a iniqüidade; seu ventre gera engano"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13240, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13241, 2, """Já ouvi muitas palavras como essas. Pobres consoladores são vocês todos!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13242, 3, "Esses discursos inúteis nunca acabarão? O que o leva a continuar discutindo?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13243, 4, "Bem que eu poderia falar como vocês, se estivessem em meu lugar; eu poderia condená-los com belos discursos, e menear a cabeça contra vocês.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13244, 5, "Mas a minha boca procuraria encorajá-los; a consolação dos meus lábios lhes daria alívio.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13245, 6, """Contudo, se falo, a minha dor não se alivia; se me calo, ela não desaparece.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13246, 7, "Sem dúvida, ó Deus, tu me esgotaste as forças; deste fim a toda a minha família.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13247, 8, "Tu me deixaste deprimido, o que é uma testemunha disso; a minha magreza se levanta e depõe contra mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13248, 9, "Deus, em sua ira, ataca-me e faz-me em pedaços, e range os dentes contra mim; meus inimigos fitam-me com olhar penetrante.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13249, 10, "Os homens abrem sua boca contra mim, esmurram meu rosto com zombaria e se unem contra mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13250, 11, "Deus fez-me cair nas mãos dos ímpios, e atirou-me nas garras dos maus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13251, 12, "Eu estava tranqüilo, mas ele me arrebentou; agarrou-me pelo pescoço e esmagou-me. Fez de mim o seu alvo;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13252, 13, "seus flecheiros me cercam. Ele traspassou sem dó os meus rins e derramou na terra a minha bílis.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13253, 14, "Lança-se sobre mim uma e outra vez; ataca-me como um guerreiro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13254, 15, """Costurei veste de lamento sobre a minha pele e enterrei a minha testa no pó.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13255, 16, "Meu rosto está rubro de tanto eu chorar, e sombras densas circundam os meus olhos,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13256, 17, "apesar de que não há violência em minhas mãos e de que é pura a minha oração.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13257, 18, """Ó terra, não cubra o meu sangue! Não haja lugar de repouso para o meu clamor!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13258, 19, "Saibam que agora mesmo a minha testemunha está nos céus; nas alturas está o meu advogado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13259, 20, "O meu intercessor é meu amigo, quando diante de Deus correm lágrimas dos meus olhos;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13260, 21, "ele defende a causa do homem perante Deus, como quem defende a causa do amigo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13261, 22, """Pois mais alguns anos apenas, e farei a viagem sem retorno.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13262, 1, """Meu espírito está quebrantado, os meus dias se encurtam, a sepultura me espera.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13263, 2, "A verdade é que zombadores me rodeiam, e tenho que ficar olhando a hostilidade deles.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13264, 3, """Dá-me, ó Deus, a garantia que exiges. Quem, senão tu, me dará segurança?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13265, 4, "Fechaste as mentes deles para o entendimento, e com isso não os deixarás triunfar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13266, 5, "Se alguém denunciar os seus amigos por recompensa, os olhos dos filhos dele fraquejarão,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13267, 6, """mas de mim Deus fez um provérbio para todos, um homem em cujo rosto os outros cospem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13268, 7, "Meus olhos se turvaram de tristeza; o meu corpo não passa de uma sombra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13269, 8, "Os íntegros ficam atônitos em face disso, e os inocentes se levantam contra os ímpios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13270, 9, "Mas os justos se manterão firmes em seus caminhos, e os homens de mãos puras se tornarão cada vez mais fortes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13271, 10, """Venham, porém, vocês todos, e façam nova tentativa! Não acharei nenhum sábio entre vocês.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13272, 11, "Foram-se os meus dias, os meus planos fracassaram, como também os desejos do meu coração.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13273, 12, "Andam querendo tornar a noite em dia; ante a aproximação das trevas dizem: ‘Vem chegando a luz’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13274, 13, "Ora, se o único lar pelo qual espero é a sepultura; se estendo a minha cama nas trevas;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13275, 14, "se digo à corrupção mortal: Você é o meu pai, e se aos vermes digo: Vocês são minha mãe e minha irmã,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13276, 15, "onde está então minha esperança? Quem poderá ver alguma esperança para mim?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13277, 16, "Descerá ela às portas do Sheol? Desceremos juntos ao pó? """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13278, 1, "Então Bildade, de Suá, respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13279, 2, """Quando você vai parar de falar? Proceda com sensatez, e depois poderemos conversar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13280, 3, "Por que somos considerados como animais, e somos ignorantes aos seus olhos?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13281, 4, "Ah você, que se dilacera de ira! Deve-se abandonar a terra por sua causa? Ou devem as rochas mudar de lugar?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13282, 5, """A lâmpada do ímpio se apaga, e a chama do seu fogo se extingue.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13283, 6, "Na sua tenda a luz se escurece; a lâmpada de sua vida se apaga.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13284, 7, "O vigor dos seus passos se enfraquece, e os seus próprios planos lançam por terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13285, 8, "Por seus próprios pés você se prende na rede, e se perde na sua malha.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13286, 9, "A armadilha o pega pelo calcanhar; o laço o prende firme.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13287, 10, "O nó corredio está escondido na terra para pegá-lo, há uma armadilha em seu caminho.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13288, 11, "Terrores de todos os lados o assustam e o perseguem em todos os seus passos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13289, 12, "A calamidade tem fome para alcançá-la, e a desgraça está à espera de sua queda.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13290, 13, "Ela consome partes da sua pele; o primogênito da morte devora os membros do seu corpo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13291, 14, "Ele é arrancado da segurança de sua tenda, e o levam à força ao rei dos terrores.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13292, 15, "O fogo mora na tenda dele; espalham enxofre ardente sobre a sua habitação.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13293, 16, "Suas raízes secam-se embaixo, e seus ramos murcham em cima.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13294, 17, "Sua lembrança desaparece da terra, e nome não tem, em parte alguma.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13295, 18, "É lançado da luz para as trevas; é banido do mundo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13296, 19, "Não tem filhos nem descendentes entre o seu povo, nem lhe restou sobrevivente algum nos lugares onde antes vivia.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13297, 20, "Os homens do ocidente assustam-se com a sua ruína, e os do oriente enchem-se de pavor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13298, 21, "É assim a habitação do perverso; essa é a situação de quem não conhece a Deus"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13299, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13300, 2, """Até quando vocês continuarão a atormentar-me, e a esmagar-me com palavras?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13301, 3, "Vocês já me repreenderam dez vezes; não se envergonham de agredir-me!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13302, 4, "Se é verdade que me desviei, meu erro só interessa a mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13303, 5, "Se de fato vocês se exaltam acima de mim e usam contra mim a minha humilhação,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13304, 6, "saibam que foi Deus que me tratou mal e me envolveu em sua rede.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13305, 7, """Se grito: É injustiça! Não obtenho resposta; clamo por socorro, todavia não há justiça.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13306, 8, "Ele bloqueou o meu caminho, e não consigo passar; cobriu de trevas as minhas veredas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13307, 9, "Despiu-me da minha honra e tirou a coroa de minha cabeça.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13308, 10, "Ele me arrasa por todos os lados, enquanto eu não me vou; desarraiga a minha esperança como se arranca uma planta.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13309, 11, "Sua ira acendeu-se contra mim; ele me vê como inimigo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13310, 12, "Suas tropas avançam poderosamente; cercam-me e acampam ao redor da minha tenda.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13311, 13, """Ele afastou de mim os meus irmãos; até os meus conhecidos estão longe de mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13312, 14, "Os meus parentes me abandonaram e os meus amigos esqueceram-se de mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13313, 15, "Os meus hóspedes e as minhas servas consideram-me estrangeiro; vêem-me como um estranho.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13314, 16, "Chamo o meu servo, mas ele não me responde, ainda que eu lhe implore pessoalmente.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13315, 17, "Minha mulher acha repugnante o meu hálito; meus próprios irmãos têm nojo de mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13316, 18, "Até os meninos zombam de mim, e dão risada quando apareço.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13317, 19, "Todos os meus amigos chegados me detestam; aqueles a quem amo voltaram-se contra mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13318, 20, "Não passo de pele e ossos; só escapei com a pele dos meus dentes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13319, 21, """Misericórdia, meus amigos! Misericórdia! Pois a mão de Deus me feriu.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13320, 22, "Por que vocês me perseguem como Deus o faz? Nunca vão saciar-se da minha carne?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13321, 23, """Quem dera as minhas palavras fossem registradas! Quem dera fossem escritas num livro,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13322, 24, "fossem talhadas a ferro no chumbo, ou gravadas para sempre na rocha!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13323, 25, "Eu sei que o meu Redentor vive, e que no fim se levantará sobre a terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13324, 26, "E depois que o meu corpo estiver destruído e sem carne, verei a Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13325, 27, "Eu o verei, com os meus próprios olhos; eu mesmo, e não outro! Como anseia no meu peito o coração!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13326, 28, """Se vocês disserem: ‘Vejamos como vamos persegui-lo, pois a raiz do problema está nele’,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13327, 29, "melhor será que temam a espada, porquanto por meio dela a ira lhes trará castigo, e então vocês saberão que há julgamento"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13328, 1, "Então Zofar, de Naamate, respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13329, 2, """Agitam-se os meus pensamentos e levam-me a responder porque estou profundamente perturbado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13330, 3, "Ouvi uma repreensão que me desonra, e o meu entendimento faz-me contestar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13331, 4, """Certamente você sabe que sempre foi assim, desde a antigüidade; desde que o homem foi posto na terra,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13332, 5, "o riso dos maus é passageiro, e a alegria dos ímpios dura apenas um instante.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13333, 6, "Mesmo que o seu orgulho chegue aos céus e a sua cabeça toque as nuvens,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13334, 7, "ele perecerá para sempre, como o seu próprio excremento; os que o tinham visto perguntarão: ‘Onde ele foi parar? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13335, 8, "Ele voa e vai-se como um sonho, para nunca mais ser encontrado, banido como uma visão noturna.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13336, 9, "O olho que o viu não o verá mais, nem o seu lugar o tornará a ver.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13337, 10, "Seus filhos têm que indenizar os pobres; ele próprio, com suas mãos, terá que refazer sua riqueza.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13338, 11, "O vigor juvenil que enche os seus ossos jazerá com ele no pó.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13339, 12, """Mesmo que o mal seja doce em sua boca e ele o esconda sob a língua,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13340, 13, "mesmo que o retenha na boca para saboreá-lo,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13341, 14, "ainda assim a sua comida azedará no estômago; e será como veneno de cobra em seu interior.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13342, 15, "Ele vomitará as riquezas que engoliu; Deus fará seu estômago lançá-las fora.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13343, 16, "Sugará veneno de cobra; as presas de uma víbora o matarão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13344, 17, "Não terá gosto na contemplação dos regatos, e dos rios que vertem mel e nata.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13345, 18, "Terá que devolver aquilo pelo que lutou, sem aproveitá-lo, e não desfrutará dos lucros do seu comércio.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13346, 19, "Sim, pois ele tem oprimido os pobres e os tem deixado desamparados; apoderou-se de casas que não construiu.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13347, 20, """Certo é que a sua cobiça não lhe trará descanso, e o seu tesouro não o salvará.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13348, 21, "Nada lhe restou para devorar; sua prosperidade não durará muito.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13349, 22, "Em meio à sua fartura, a aflição o dominará; a força total da desgraça o atingirá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13350, 23, "Quando ele estiver de estômago cheio, Deus dará vazão às tremendas chamas de sua ira, e sobre ele despejará o seu furor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13351, 24, "Se escapar da arma de ferro, o bronze da sua flecha o atravessará.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13352, 25, "Ele o arrancará das suas costas, a ponta reluzente saindo do seu fígado. Grande pavor virá sobre ele;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13353, 26, "densas trevas estarão à espera dos seus tesouros. Um fogo não assoprado o consumirá e devorará o que sobrar em sua tenda.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13354, 27, "Os céus porão à mostra a sua culpa; a terra se levantará contra ele.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13355, 28, "Uma inundação arrastará a sua casa, águas avassaladoras, no dia da ira de Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13356, 29, "Esse é o destino que Deus dá aos ímpios, é a herança designada por Deus para eles"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13357, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13358, 2, """Escutem com atenção as minhas palavras; seja esse o consolo que vocês haverão de dar-me.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13359, 3, "Suportem-me enquanto eu estiver falando; depois que eu falar poderão zombar de mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13360, 4, """Acaso é dos homens que me queixo? Por que não deveria eu estar impaciente?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13361, 5, "Olhem para mim, e ficarão atônitos; tapem a boca com a mão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13362, 6, "Quando penso nisso, fico aterrorizado; todo o meu corpo se põe a tremer.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13363, 7, "Por que vivem os ímpios? Por que chegam à velhice e aumentam seu poder?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13364, 8, "Eles vêem os seus filhos estabelecidos ao seu redor, e os seus descendentes diante dos seus olhos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13365, 9, "Seus lares estão seguros e livres de medo; a vara de Deus não os vem ferir.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13366, 10, "Seus touros nunca deixam de procriar; suas vacas dão crias e não abortam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13367, 11, "Eles soltam os seus filhos como um rebanho; seus pequeninos põem-se a dançar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13368, 12, "Cantam, acompanhando a música do tamborim e da harpa; alegram-se ao som da flauta.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13369, 13, "Passam a vida na prosperidade e descem à sepultura em paz.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13370, 14, "Contudo, dizem eles a Deus: ‘Deixa-nos! Não queremos conhecer os teus caminhos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13371, 15, "Quem é o Todo-poderoso, para que o sirvamos? Que vantagem nos dá orar a ele? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13372, 16, "Mas não depende deles a prosperidade de que desfrutam; por isso fico longe do conselho dos ímpios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13373, 17, """Pois, quantas vezes a lâmpada dos ímpios se apaga? Quantas vezes a desgraça cai sobre eles, o destino que em sua ira Deus lhes dá?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13374, 18, "Quantas vezes o vento os leva como palha, e o furacão os arrebata como cisco?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13375, 19, "Dizem que Deus reserva o castigo de um homem para os seus filhos. Que ele mesmo o receba, para que aprenda a lição!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13376, 20, "Que os seus próprios olhos vejam a sua ruína; que ele mesmo beba da ira do Todo-poderoso!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13377, 21, "Pois, que lhe importará a família que deixa atrás de si quando chegar ao fim os meses que lhe foram destinados?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13378, 22, """Haverá alguém que o ensine a conhecer a Deus, uma vez que ele julga até os de mais alta posição?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13379, 23, "Um homem morre em pleno vigor, quando se sentia bem e seguro,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13380, 24, "tendo o corpo bem nutrido e os ossos cheios de tutano.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13381, 25, "Já outro morre tendo a alma amargurada, sem nada ter desfrutado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13382, 26, "Um e outro jazem no pó, ambos cobertos de vermes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13383, 27, """Sei muito bem o que vocês estão pensando, as suas conspirações contra mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13384, 28, "‘Onde está agora a casa do grande homem? ’, vocês perguntam. ‘Onde a tenda dos ímpios? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13385, 29, "Vocês nunca fizeram perguntas aos que viajam? Não deram atenção ao que contam?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13386, 30, "Que o mau é poupado da calamidade, e que do dia da ira recebe livramento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13387, 31, "Quem o acusa lançando em rosto a sua conduta? Quem lhe retribui pelo mal que fez?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13388, 32, "Pois o levam para o túmulo, e sobre a sua sepultura se mantém vigilância.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13389, 33, "Para ele é macio o terreno do vale; todos o seguem, e uma multidão incontável o precede.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13390, 34, """Por isso, como podem vocês consolar-me com esses absurdos? O que sobra das suas respostas é pura falsidade! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13391, 1, "Então, de Temã, Elifaz respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13392, 2, """Pode alguém ser útil a Deus? Mesmo um sábio, pode ser-lhe de algum proveito?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13393, 3, "Que prazer você daria ao Todo-poderoso se você fosse justo? Que é que ele ganharia se os seus caminhos fossem irrepreensíveis?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13394, 4, """É por sua piedade que ele o repreende e lhe faz acusações?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13395, 5, "Não é grande a sua maldade? Não são infindos os seus pecados?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13396, 6, "Sem motivo você exigia penhores dos seus irmãos; você despojava das roupas os que quase nenhuma tinham.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13397, 7, "Você não deu água ao sedento e reteve a comida do faminto,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13398, 8, "sendo você poderoso, e dono de terras, delas vivendo, e honrado diante de todos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13399, 9, "Você mandou embora de mãos vazias as viúvas e quebrou a força dos órfãos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13400, 10, "Por isso está cercado de armadilhas e o perigo repentino o apavora.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13401, 11, "Também por isso você se vê envolto em escuridão que o cega, e o cobrem as águas, em tremenda inundação.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13402, 12, """Não está Deus nas alturas dos céus? E em que altura estão as estrelas mais distantes!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13403, 13, "Contudo você diz: ‘Que é que Deus sabe? Poderá julgar através de tão grande escuridão?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13404, 14, "Nuvens espessas o cobrem, e ele não pode nos ver, quando percorre a abóbada dos céus’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13405, 15, "Você vai continuar no velho caminho que os perversos palmilharam?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13406, 16, "Estes foram levados antes da hora; seus alicerces foram arrastados por uma enchente.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13407, 17, "Eles disseram a Deus: ‘Deixa-nos! Que é que o Todo-poderoso poderá fazer conosco? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13408, 18, "Contudo, foi ele que encheu de bens as casas deles; por isso fico longe do conselho dos ímpios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13409, 19, """Os justos vêem a ruína deles, e se regozijam; os inocentes zombam deles, dizendo:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13410, 20, "‘Certo é que os nossos inimigos foram destruídos, e o fogo devorou a sua riqueza’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13411, 21, """Sujeite-se a Deus, fique em paz com ele, e a prosperidade virá a você.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13412, 22, "Aceite a instrução que vem da sua boca e ponha no coração as suas palavras.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13413, 23, "Se você voltar-se para o Todo-poderoso, voltará ao seu lugar: Se afastar da sua tenda a injustiça,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13414, 24, "lançar ao pó as suas pepitas, o seu ouro puro de Ofir às rochas dos vales,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13415, 25, "o Todo-poderoso será o seu ouro, será para você prata seleta.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13416, 26, "É certo que você achará prazer no Todo-poderoso e erguerá o rosto para Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13417, 27, "A ele orará, e ele o ouvirá, e você cumprirá os seus votos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13418, 28, "O que você decidir se fará, e a luz brilhará em seus caminhos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13419, 29, "Quando os homens forem humilhados e você disser: ‘Levanta-os! ’, ele salvará o abatido.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13420, 30, "Livrará até o que não é inocente, que será liberto graças à pureza que há nas suas mãos"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13421, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13422, 2, """Até agora me queixo com amargura; a mão dele é pesada, a despeito de meu gemido.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13423, 3, "Se tão-somente eu soubesse onde encontrá-lo e ir à sua habitação!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13424, 4, "Eu lhe apresentaria a minha causa e encheria a minha boca de argumentos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13425, 5, "Estudaria o que ele me respondesse e analisaria o que me dissesse.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13426, 6, "Será que ele se oporia a mim com grande poder? Não, ele não me faria acusações.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13427, 7, "O homem íntegro poderia apresentar-lhe sua causa; eu seria liberto para sempre de quem me julga.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13428, 8, """Mas, se vou para o oriente, lá ele não está; se vou para o ocidente, não o encontro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13429, 9, "Quando ele está em ação no norte, não o enxergo; quando vai para o sul, nem sombra dele eu vejo!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13430, 10, "Mas ele conhece o caminho por onde ando; se me puser à prova, aparecerei como o ouro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13431, 11, "Meus pés seguiram de perto as suas pegadas; mantive-me no seu caminho, sem desviar-me.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13432, 12, "Não me afastei dos mandamentos dos seus lábios; dei mais valor às palavras de sua boca, do que ao meu pão de cada dia.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13433, 13, """Mas ele é ele! Quem poderá fazer-lhe oposição? Ele faz o que quer.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13434, 14, "Executa o seu decreto contra mim, e tem muitos outros semelhantes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13435, 15, "Por isso fico apavorado diante dele; pensar nisso me enche de medo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13436, 16, "Deus fez desmaiar o meu coração; o Todo-poderoso causou-me pavor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13437, 17, "Contudo não fui silenciado pelas trevas, pelas densas trevas que cobrem o meu rosto.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13438, 1, """Por que o Todo-poderoso não marca as datas para julgamento? Por que aqueles que o conhecem não chegam a vê-las?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13439, 2, "Há os que mudam os marcos dos limites e apascentam rebanhos que eles roubaram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13440, 3, "Levam o jumento que pertence ao órfão e tomam o boi da viúva como penhor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13441, 4, "Forçam os necessitados a saírem do caminho e os pobres da terra a esconder-se.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13442, 5, "Como jumentos selvagens no deserto, os pobres vão em busca de comida; da terra deserta a obtêm para os seus filhos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13443, 6, "Juntam forragem nos campos e respigam nas vinhas dos ímpios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13444, 7, "Pela falta de roupas, passam a noite nus; não têm com que cobrir-se no frio.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13445, 8, "Encharcados pelas chuvas das montanhas, abraçam-se às rochas por falta de abrigo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13446, 9, "A criança órfã é arrancada do seio de sua mãe; o recém-nascido do pobre é tomado para pagar uma dívida.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13447, 10, "Por falta de roupas, andam nus; carregam os feixes, mas continuam famintos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13448, 11, "Espremem azeitonas dentro dos seus muros; pisam uvas nos lagares, mas assim mesmo sofrem sede.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13449, 12, "Sobem da cidade os gemidos dos que estão para morrer, e as almas dos feridos clamam por socorro. Mas Deus não vê mal nisso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13450, 13, """Há os que se revoltam contra a luz, não conhecem os caminhos dela e não permanecem em suas veredas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13451, 14, "De manhã o assassino se levanta e mata os pobres e os necessitados; de noite age como ladrão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13452, 15, "Os olhos do adúltero ficam à espera do crepúsculo; ‘Nenhum olho me verá’, pensa ele; e mantém oculto o rosto.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13453, 16, "No escuro os homens invadem casas, mas de dia se enclausuram; não querem saber da luz.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13454, 17, "Para eles a manhã é tremenda escuridão; eles são amigos dos pavores das trevas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13455, 18, """São, porém, como espuma sobre as águas; sua parte da terra foi amaldiçoada, e por isso ninguém vai às vinhas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13456, 19, "Assim como o calor e a seca depressa consomem a neve derretida, assim a sepultura consome os que pecaram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13457, 20, "Sua mãe os esquece, os vermes se banqueteiam neles. Ninguém se lembra dos maus; quebram-se como árvores.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13458, 21, "Devoram a estéril e sem filhos e não mostram bondade para com a viúva.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13459, 22, "Mas Deus, por seu poder, os arranca; embora firmemente estabelecidos, a vida deles não tem segurança.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13460, 23, "Ele poderá deixá-los descansar, sentindo-se seguros, mas os vigia atento nos caminhos que seguem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13461, 24, "Por um breve instante são exaltados, e depois se vão; colhidos como todos os demais; ceifados como espigas de cereal.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13462, 25, """Se não é assim, quem poderá provar que minto e reduzir a nada as minhas palavras? """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13463, 1, "Então Bildade, de Suá, respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13464, 2, """O domínio e o temor pertencem a Deus; ele impõe ordem nas alturas, que a ele pertencem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13465, 3, "Seria possível contar os seus exércitos? E a sua luz, sobre quem não se levanta?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13466, 4, "Como pode então o homem ser justo diante de Deus? Como pode ser puro quem nasce de mulher?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13467, 5, "Se nem a lua é brilhante e as estrelas são puras aos olhos dele,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13468, 6, "muito menos o será o homem, que não passa de larva, o filho do homem, que não passa de verme! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13469, 1, "Então Jó respondeu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13470, 2, "Grande foi a ajuda que você deu ao desvalido! Que socorro você prestou ao braço frágil!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13471, 3, "Belo conselho você ofereceu a quem não é sábio, e que grande sabedoria você revelou!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13472, 4, "Quem o ajudou a proferir essas palavras, e por meio de que espírito você falou?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13473, 5, """Os mortos estão em grande angústia sob as águas e os que nelas vivem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13474, 6, "Nu está o Sheol diante de Deus, e nada encobre a Destruição.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13475, 7, "Ele estende os céus do norte sobre o espaço vazio; suspende a terra sobre o nada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13476, 8, "Envolve as águas em suas nuvens, e estas não se rompem sob o peso delas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13477, 9, "Ele cobre a face da lua cheia estendendo sobre ela as suas nuvens.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13478, 10, "Traça o horizonte sobre a superfície das águas para servir de limite entre a luz e as trevas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13479, 11, "As colunas dos céus estremecem e ficam perplexas diante da sua repreensão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13480, 12, "Com seu poder agitou violentamente o mar; com sua sabedoria despedaçou Raabe.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13481, 13, "Com seu sopro os céus ficaram límpidos; sua mão feriu a serpente arisca.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13482, 14, "E isso tudo é apenas a borda das suas obras! Um suave sussurro é o que ouvimos dele. Mas quem poderá compreender o trovão do seu poder? """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13483, 1, "E Jó prosseguiu em seu discurso:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13484, 2, """Pelo Deus vivo, que me negou justiça, pelo Todo-poderoso, que deu amargura à minha alma,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13485, 3, "enquanto eu tiver vida em mim, o sopro de Deus em minhas narinas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13486, 4, "meus lábios não falarão maldade, e minha língua não proferirá nada que seja falso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13487, 5, "Nunca darei razão a vocês! Minha integridade não negarei jamais, até à morte.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13488, 6, "Manterei minha retidão, e nunca a deixarei; enquanto eu viver, a minha consciência não me repreenderá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13489, 7, """Sejam os meus inimigos como os ímpios, e os meus adversários como os injustos!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13490, 8, "Pois, qual é a esperança do ímpio, quando é eliminado, quando Deus lhe tira a vida?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13491, 9, "Ouvirá Deus o seu clamor, quando vier sobre ele a aflição?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13492, 10, "Terá ele prazer no Todo-poderoso? Chamará a Deus a cada instante?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13493, 11, """Eu os ensinarei sobre o poder de Deus; não esconderei de vocês os caminhos do Todo-poderoso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13494, 12, "Pois a verdade é que todos vocês já viram isso. Por que então essa conversa sem sentido?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13495, 13, """Este é o destino que Deus determinou para o ímpio, a herança que o mau recebe do Todo-poderoso:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13496, 14, "Por mais filhos que tenha, o destino deles é a espada; sua prole jamais terá comida suficiente.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13497, 15, "A epidemia sepultará aqueles que lhe sobreviverem, e as suas viúvas não chorarão por eles.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13498, 16, "Ainda que ele acumule prata como pó e roupas como barro, amontoe;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13499, 17, "o que ele armazenar ficará para os justos, e os inocentes dividirão sua prata.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13500, 18, "A casa que ele constrói é como casulo de traça, como cabana feita pela sentinela.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13501, 19, "Rico ele se deita, mas nunca mais será! Quando abre os olhos, tudo se foi.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13502, 20, "Pavores vêm sobre ele como uma enchente; de noite a tempestade o leva de roldão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13503, 21, "O vento oriental o leva, e ele desaparece; arranca-o do seu lugar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13504, 22, "Atira-se contra ele sem piedade, enquanto ele foge às pressas do seu poder.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13505, 23, "Bate palmas contra ele e com assobios o expele do seu lugar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13506, 1, """Existem minas de prata e locais onde se refina ouro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13507, 2, "O ferro é extraído da terra, e do minério se funde o cobre.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13508, 3, "O homem dá fim à escuridão; e vasculha os recônditos mais remotos em busca de minério, nas mais escuras trevas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13509, 4, "Longe das moradias ele cava um poço, em local esquecido pelos pés dos homens; longe de todos, ele se pendura e balança.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13510, 5, "A terra, da qual vem o alimento, é revolvida embaixo como que pelo fogo;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13511, 6, "das suas rochas saem safiras, e seu pó contém pepitas de ouro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13512, 7, "Nenhuma ave de rapina conhece aquele caminho oculto, e os olhos de nenhum falcão o viram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13513, 8, "Os animais altivos não põem os pés nele, e nenhum leão ronda por ali.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13514, 9, "As mãos dos homens atacam a pederneira e transtornam as raízes das montanhas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13515, 10, "Fazem túneis através da rocha, e os seus olhos enxergam todos os tesouros dali.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13516, 11, "Eles vasculham as nascentes dos rios e trazem à luz coisas ocultas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13517, 12, """Onde, porém, se poderá achar a sabedoria? Onde habita o entendimento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13518, 13, "O homem não percebe o valor da sabedoria; ela não se encontra na terra dos viventes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13519, 14, "O abismo diz: ‘Em mim não está’; o mar diz: ‘Não está comigo’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13520, 15, "Não pode ser comprada, mesmo com o ouro mais puro, nem se pode pesar o seu preço em prata.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13521, 16, "Não pode ser comprada nem com o ouro puro de Ofir, nem com o precioso ônix ou com safiras.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13522, 17, "O ouro e o cristal não se comparam com ela, e é impossível tê-la em troca de jóias de ouro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13523, 18, "O coral e o jaspe nem merecem menção; o preço da sabedoria ultrapassa o dos rubis.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13524, 19, "O topázio da Etiópia não se compara com ela; não se compra a sabedoria nem com ouro puro!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13525, 20, """De onde vem, então, a sabedoria? Onde habita o entendimento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13526, 21, "Escondida está dos olhos de toda criatura viva, até das aves dos céus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13527, 22, "A Destruição e a Morte dizem: ‘Aos nossos ouvidos só chegou um leve rumor dela’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13528, 23, "Deus conhece o caminho; só ele sabe onde ela habita,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13529, 24, "pois ele enxerga os confins da terra e vê tudo o que há debaixo dos céus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13530, 25, "Quando ele determinou a força do vento e estabeleceu a medida exata para as águas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13531, 26, "quando fez um decreto para a chuva e o caminho para a tempestade trovejante,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13532, 27, "ele olhou para a sabedoria e a avaliou; confirmou-a e a pôs à prova.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13533, 28, "Disse então ao homem: ‘No temor do Senhor está a sabedoria, e evitar o mal é ter entendimento’ "".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13534, 1, "Jó prosseguiu sua fala:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13535, 2, """Como tenho saudade dos meses que se passaram, dos dias em que Deus cuidava de mim,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13536, 3, "quando a sua lâmpada brilhava sobre a minha cabeça e por sua luz eu caminhava em meio às trevas!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13537, 4, "Como tenho saudade dos dias do meu vigor, quando a amizade de Deus abençoava a minha casa,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13538, 5, "quando o Todo-poderoso ainda estava comigo e meus filhos estavam ao meu redor,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13539, 6, "quando as minhas veredas se embebiam em nata e a rocha me despejava torrentes de azeite.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13540, 7, """Quando eu ia à porta da cidade e tomava assento na praça pública;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13541, 8, "quando, ao me verem, os jovens saíam do caminho, e os idosos ficavam de pé;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13542, 9, "os líderes se abstinham de falar e com a mão cobriam a boca.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13543, 10, "As vozes dos nobres silenciavam, e suas línguas colavam-se ao céu da boca.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13544, 11, "Todos os que me ouviam falavam bem de mim, e quem me via me elogiava,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13545, 12, "pois eu socorria o pobre que clamava por ajuda, e o órfão que não tinha quem o ajudasse.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13546, 13, "O que estava à beira da morte me abençoava, e eu fazia regozijar-se o coração da viúva.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13547, 14, "A retidão era a minha roupa; a justiça era o meu manto e o meu turbante.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13548, 15, "Eu era os olhos do cego e os pés do aleijado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13549, 16, "Eu era o pai dos necessitados, e me interessava pela defesa de desconhecidos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13550, 17, "Eu quebrava as presas dos ímpios e dos seus dentes arrancava as suas vítimas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13551, 18, """Eu pensava: ‘Morrerei em casa, e os meus dias serão numerosos como os grãos de areia.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13552, 19, "Minhas raízes chegarão até as águas, e o orvalho passará a noite nos meus ramos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13553, 20, "Minha glória se renovará em mim, e novo será o meu arco em minha mão’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13554, 21, """Os homens me escutavam em ansiosa expectativa, aguardando em silêncio o meu conselho.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13555, 22, "Depois que eu falava, eles nada diziam; minhas palavras caíam suavemente em seus ouvidos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13556, 23, "Esperavam por mim como quem espera por uma chuvarada, e bebiam minhas palavras como quem bebe a chuva da primavera.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13557, 24, "Quando eu lhes sorria, mal acreditavam; a luz do meu rosto lhes era preciosa.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13558, 25, "Era eu que escolhia o caminho para eles, e me assentava como seu líder; instalava-me como um rei no meio das suas tropas; eu era como um consolador dos que choram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13559, 1, """Mas agora eles zombam de mim, homens mais jovens que eu, homens cujos pais eu teria rejeitado, não lhes permitindo sequer estar com os cães de guarda do rebanho.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13560, 2, "De que me serviria a força de suas mãos, já que desapareceu o seu vigor?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13561, 3, "Desfigurados de tanta necessidade e fome, perambulavam pela terra ressequida, em sombrios e devastados desertos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13562, 4, "Nos campos de mato rasteiro colhiam ervas, e a raiz da giesta era a sua comida.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13563, 5, "Da companhia dos amigos foram expulsos aos gritos, como se fossem ladrões.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13564, 6, "Foram forçados a morar nos leitos secos dos rios, entre as rochas e nos buracos da terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13565, 7, "Rugiam entre os arbustos e se encolhiam sob a vegetação.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13566, 8, "Prole desprezível e sem nome, foram expulsos da terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13567, 9, """E agora os filhos deles zombam de mim com suas canções; tornei-me um provérbio entre eles.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13568, 10, "Eles me detestam e se mantêm à distância; não hesitam em cuspir em meu rosto.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13569, 11, "Agora que Deus afrouxou a corda do meu arco e me afligiu, eles ficam sem freios na minha presença.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13570, 12, "À direita os embrutecidos me atacam; preparam armadilhas para os meus pés, e constroem rampas de cerco contra mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13571, 13, "Destroem o meu caminho; conseguem destruir-me, sem a ajuda de ninguém.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13572, 14, "Avançam como através de uma grande brecha; arrojam-se entre as ruínas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13573, 15, "Pavores apoderam-se de mim; a minha dignidade é levada como pelo vento, a minha segurança se desfaz como nuvem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13574, 16, """E agora esvai-se a minha vida; estou preso a dias de sofrimento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13575, 17, "A noite penetra os meus ossos; minhas dores me corroem sem cessar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13576, 18, "Em seu grande poder Deus é como a minha roupa; ele me envolve como a gola da minha veste.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13577, 19, "Lança-me na lama, e sou reduzido a pó e cinza.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13578, 20, """Clamo a ti, ó Deus, mas não respondes; fico de pé, mas apenas olhas para mim.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13579, 21, "Contra mim te voltas com dureza e me atacas com a força de tua mão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13580, 22, "Tu me apanhas e me levas contra o vento, e me jogas de um lado a outro na tempestade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13581, 23, "Sei que me farás descer até a morte, ao lugar destinado a todos os viventes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13582, 24, """A verdade é que ninguém dá a mão ao homem arruinado, quando este, em sua aflição, grita por socorro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13583, 25, "Não é certo que chorei por causa dos que passavam dificuldade? E que a minha alma entristeceu-se por causa dos pobres?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13584, 26, "Mesmo assim, quando eu esperava o bem, veio o mal; quando eu procurava luz, vieram trevas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13585, 27, "Nunca pára a agitação dentro de mim; dias de sofrimento me confrontam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13586, 28, "Perambulo escurecido, mas não pelo sol; levanto-me na assembléia e clamo por ajuda.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13587, 29, "Tornei-me irmão dos chacais, companheiro das corujas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13588, 30, "Minha pele escurece e cai; meu corpo queima de febre.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13589, 31, "Minha harpa está afinada para cantos fúnebres, e minha flauta para o som de pranto.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13590, 1, """Fiz acordo com os meus olhos de não olhar com cobiça para as moças.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13591, 2, "Pois qual é a porção que o homem recebe de Deus, lá de cima? Qual a sua herança do Todo-poderoso, que habita nas alturas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13592, 3, "Não é ruína para os ímpios, desgraça para os que fazem o mal?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13593, 4, "Não vê ele os meus caminhos, e não considera cada um de meus passos?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13594, 5, """Se me conduzi com falsidade, ou se meus pés se apressaram a enganar,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13595, 6, "Deus me pese em balança justa, e saberá que não tenho culpa;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13596, 7, "se meus passos desviaram-se do caminho, se o meu coração foi conduzido por meus olhos, ou se minhas mãos foram contaminadas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13597, 8, "que outros comam o que semeei, e que as minhas plantações sejam arrancadas pelas raízes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13598, 9, """Se o meu coração foi seduzido por mulher, ou se fiquei à espreita junto à porta do meu próximo,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13599, 10, "que a minha esposa moa cereal de outro homem, e que outros durmam com ela.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13600, 11, "Pois fazê-lo seria vergonhoso, crime merecedor de julgamento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13601, 12, "Isso é um fogo que consome até a Destruição; teria extirpado a minha colheita.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13602, 13, """Se neguei justiça aos meus servos e servas, quando reclamaram contra mim,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13603, 14, "que farei quando Deus me confrontar? Que responderei quando chamado a prestar contas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13604, 15, "Aquele que me fez no ventre materno não fez também a eles? Não foi ele quem formou a mim e a eles No interior de nossas mães?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13605, 16, """Se não atendi aos desejos do pobre, ou se fatiguei os olhos da viúva,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13606, 17, "se comi meu pão sozinho, sem compartilhá-lo com o órfão,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13607, 18, "sendo que desde a minha juventude o criei como se fosse seu pai, e desde o nascimento guiei a viúva;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13608, 19, "se vi alguém morrendo por falta de roupa, ou um necessitado sem cobertor,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13609, 20, "e o seu coração não me abençoou porque o aqueci com a lã de minhas ovelhas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13610, 21, "se levantei a mão contra o órfão, ciente da minha influência no tribunal,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13611, 22, "que o meu braço descaia do ombro, e se quebre nas juntas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13612, 23, "Pois eu tinha medo que Deus me destruísse, e, temendo o seu esplendor, não podia fazer tais coisas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13613, 24, """Se pus no ouro a minha confiança e disse ao ouro puro: Você é a minha garantia,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13614, 25, "se me regozijei por ter grande riqueza, pela fortuna que as minhas mãos obtiveram,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13615, 26, "se contemplei o sol em seu fulgor e a lua a mover-se esplêndida,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13616, 27, "e em segredo o meu coração foi seduzido e a minha mão lhes ofereceu beijos de veneração,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13617, 28, "esses também seriam pecados merecedores de condenação, pois eu teria sido infiel a Deus, que está nas alturas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13618, 29, """Se a desgraça do meu inimigo me alegrou, ou se os problemas que teve me deram prazer;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13619, 30, "eu, que nunca deixei minha boca pecar, lançando maldição sobre ele;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13620, 31, "se os que moram em minha casa nunca tivessem dito: ‘Quem não recebeu de Jó um pedaço de carne? ’,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13621, 32, "sendo que nenhum estrangeiro teve que passar a noite na rua, pois a minha porta sempre esteve aberta para o viajante;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13622, 33, "se escondi o meu pecado, como outros fazem, acobertando no coração a minha culpa,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13623, 34, "com tanto medo da multidão e do desprezo dos familiares que me calei e não saí de casa...", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13624, 35, "( ""Ah, se alguém me ouvisse! Agora assino a minha defesa. Que o Todo-poderoso me responda; que o meu acusador faça a acusação por escrito.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13625, 36, "Eu bem que a levaria nos ombros e a usaria como coroa.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13626, 37, "Eu lhe falaria sobre todos os meus passos; como um príncipe eu me aproximaria dele. )", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13627, 38, """Se a minha terra se queixar de mim e todos os seus sulcos chorarem,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13628, 39, "se consumi os seus produtos sem nada pagar, ou se causei desânimo aos seus ocupantes,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13629, 40, "que me venham espinhos em lugar de trigo e ervas daninhas em lugar de cevada"". Aqui terminam as palavras de Jó.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13630, 1, "Então esses três homens pararam de responder a Jó, pois este se julgava justo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13631, 2, "Mas Eliú, filho de Baraquel, de Buz, da família de Rão, indignou-se muito contra Jó, porque este se justificava a si mesmo diante de Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13632, 3, "Também se indignou contra os três amigos, pois não encontraram meios de refutar a Jó, e mesmo assim o tinham condenado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13633, 4, "Eliú tinha ficado esperando para falar a Jó, porque eles eram mais velhos que ele.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13634, 5, "Mas, quando viu que os três não tinham mais nada a dizer, indignou-se.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13635, 6, "Então Eliú, filho de Baraquel, de Buz, falou: ""Eu sou jovem, vocês têm idade. Por isso tive receio e não ousei dizer-lhes o que sei.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13636, 7, "Os que têm idade é que devem falar, pensava eu, os anos avançados é que devem ensinar sabedoria.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13637, 8, "Mas é o espírito dentro do homem que lhe dá entendimento, o sopro do Todo-poderoso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13638, 9, "Não são só os mais velhos, os sábios, não são só os de idade que entendem o que é certo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13639, 10, """Por isso digo: Escutem-me; também vou dizer o que sei.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13640, 11, "Enquanto vocês estavam falando, esperei; fiquei ouvindo os seus arrazoados; enquanto vocês estavam procurando palavras,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13641, 12, "dei-lhes total atenção. Mas não é que nenhum de vocês demonstrou que Jó está errado? Nenhum de vocês respondeu aos seus argumentos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13642, 13, "Não digam: ‘Encontramos a sabedoria; que Deus o refute, não o homem’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13643, 14, "Só que não foi contra mim que Jó dirigiu as suas palavras, e não vou responder a ele com os argumentos de vocês.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13644, 15, """Vejam eles estão consternados e não têm mais o que dizer; as palavras lhes fugiram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13645, 16, "Devo aguardar, agora que estão calados e sem resposta?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13646, 17, "Também vou dar a minha opinião, também vou dizer o que sei,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13647, 18, "pois não me faltam palavras, e dentro de mim o espírito me impulsiona.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13648, 19, "Por dentro estou como vinho arrolhado, como vasilhas de couro novas prestes a romper.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13649, 20, "Tenho que falar. Isso me aliviará. Tenho que abrir os lábios e responder.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13650, 21, "Não serei parcial com ninguém, e a ninguém bajularei,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13651, 22, "porque não sou bom em bajular; se fosse, o meu Criador em breve me levaria.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13652, 1, """Mas agora, Jó, escute as minhas palavras; preste atenção a tudo o que vou dizer.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13653, 2, "Estou prestes a abrir a boca; minhas palavras estão na ponta da língua.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13654, 3, "Minhas palavras procedem de um coração íntegro; meus lábios falam com sinceridade o que eu sei.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13655, 4, "O Espírito de Deus me fez; o sopro do Todo-poderoso me dá vida.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13656, 5, "Responda-me, então, se puder; prepare-se para enfrentar-me.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13657, 6, "Sou igual a você diante de Deus; eu também fui feito do barro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13658, 7, "Por isso não lhe devo inspirar temor, e a minha mão não há de ser pesada sobre você.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13659, 8, """Mas você disse ao meu alcance, eu ouvi bem as palavras:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13660, 9, "‘Estou limpo e sem pecado; estou puro e sem culpa.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13661, 10, "Contudo, Deus procurou em mim motivos para inimizade; ele me considera seu inimigo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13662, 11, "Ele acorrenta os meus pés; vigia de perto todos os meus caminhos. ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13663, 12, """Mas eu lhe digo que você não está certo, porquanto Deus é maior do que o homem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13664, 13, "Por que você se queixa a ele de que não responde às palavras dos homens?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13665, 14, "Pois a verdade é que Deus fala, ora de um modo, ora de outro, mesmo que o homem não o perceba.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13666, 15, "Em sonho ou em visão durante a noite, quando o sono profundo cai sobre os homens e eles dormem em suas camas,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13667, 16, "ele pode falar aos ouvidos deles e aterrorizá-los com advertências", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13668, 17, "para previnir o homem das suas más ações e livrá-lo do orgulho,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13669, 18, "para preservar da cova a sua alma, e a sua vida da espada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13670, 19, "Ou o homem pode ser castigado no leito de dor, com os seus ossos em constante agonia,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13671, 20, "levando-o a achar a comida repulsiva e a detestar na alma sua refeição preferida.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13672, 21, "Já não se vê sua carne, e seus ossos, que não se viam, agora aparecem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13673, 22, "Sua alma aproxima-se da cova, e sua vida, dos mensageiros da morte.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13674, 23, """Havendo, porém, um anjo ao seu lado, como mediador dentre mil, que diga ao homem o que é certo a seu respeito,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13675, 24, "para ser-lhe favorável e dizer: ‘Poupa-o de descer à cova; encontrei resgate para ele’,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13676, 25, "então sua carne se renova voltando a ser como de criança; ele se rejuvenece.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13677, 26, "Ele ora a Deus e recebe o seu favor; vê o rosto de Deus e dá gritos de alegria, e Deus lhe restitui a condição de justo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13678, 27, "Depois ele vem aos homens e diz: ‘Pequei e torci o que era certo, mas ele não me deu o que eu merecia.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13679, 28, "Ele resgatou a minha alma, impedindo-a de descer à cova, e viverei para desfrutar a luz’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13680, 29, """Deus faz dessas coisas ao homem, duas ou três vezes,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13681, 30, "para recuperar sua alma da cova, a fim de que refulja sobre ele a luz da vida.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13682, 31, """Preste atenção, Jó, e escute-me; fique em silêncio, e falarei.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13683, 32, "Se você tem algo para dizer, responda-me; fale logo, pois quero que você seja absolvido.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13684, 33, "Se não tem nada a dizer, ouça-me, fique em silêncio, e eu lhe ensinarei a sabedoria"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13685, 1, "Disse então Eliú:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13686, 2, """Ouçam as minhas palavras, vocês que são sábios; escutem-me, vocês que têm conhecimento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13687, 3, "Pois o ouvido prova as palavras como a língua prova o alimento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13688, 4, "Tratemos de discernir juntos o que é certo e de aprender o que é bom.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13689, 5, """Jó afirma: ‘Sou inocente, mas Deus me nega justiça.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13690, 6, "Apesar de eu estar certo, sou considerado mentiroso; apesar de estar sem culpa, sua flecha me causa ferida incurável’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13691, 7, "Que homem existe como Jó, que bebe zombaria como água?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13692, 8, "Ele é companheiro dos que fazem o mal, e anda com os ímpios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13693, 9, "Pois diz: ‘Não dá lucro agradar a Deus’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13694, 10, """Por isso escutem-me, vocês que têm conhecimento. Longe de Deus esteja o fazer o mal, e do Todo-poderoso o praticar a iniqüidade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13695, 11, "Ele retribui ao homem conforme o que este fez, e lhe dá o que a sua conduta merece.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13696, 12, "Não se pode nem pensar que Deus faça o mal, que o Todo-poderoso perverta a justiça.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13697, 13, "Quem o nomeou para governar a terra? Quem o encarregou de cuidar do mundo inteiro?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13698, 14, "Se fosse intenção dele, e de fato retirasse o seu espírito e o seu sopro,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13699, 15, "a humanidade pereceria toda de uma vez, e o homem voltaria ao pó.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13700, 16, """Portanto, se você tem entendimento, ouça-me, escute o que lhe digo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13701, 17, "Acaso quem odeia a justiça poderá governar? Será que você condenará aquele que é justo e poderoso?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13702, 18, "Não é ele que diz aos reis: ‘Vocês nada valem’, e aos nobres: ‘Vocês são ímpios’?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13703, 19, "Não é verdade que ele não mostra parcialidade a favor dos príncipes, e não favorece o rico em detrimento do pobre, uma vez que todos são obra de suas mãos?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13704, 20, "Morrem num momento, em plena noite; cambaleiam e passam. Os poderosos são retirados sem a intervenção de mãos humanas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13705, 21, """Pois Deus vê o caminho dos homens; ele enxerga cada um dos seus passos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13706, 22, "Não há sombra densa o bastante, onde os que fazem o mal possam esconder-se.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13707, 23, "Deus não precisa de maior tempo para examinar os homens, e levá-los à sua presença para julgamento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13708, 24, "Sem depender de investigações, ele destrói os poderosos e coloca outros em seu lugar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13709, 25, "Visto que ele repara nos atos que eles praticam, derruba-os, e eles são esmagados.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13710, 26, "Pela impiedade deles, ele os castiga onde todos podem vê-los.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13711, 27, "Isso porque deixaram de segui-lo e não deram atenção aos caminhos por ele traçados.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13712, 28, "Fizeram chegar a ele o grito do pobre, e ele ouviu o clamor do necessitado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13713, 29, "Mas, se ele permanecer calado, quem poderá condená-lo? Se esconder o rosto, quem poderá vê-lo? No entanto, ele domina igualmente sobre homens e nações,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13714, 30, "para evitar que o ímpio governe e prepare armadilhas para o povo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13715, 31, """Suponhamos que um homem diga a Deus: ‘Sou culpado, mas não vou mais pecar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13716, 32, "Mostra-me o que não estou vendo; se agi mal, não tornarei a fazê-lo’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13717, 33, "Quanto a você, deveria Deus recompensá-lo quando você nega a sua culpa? É você que tem que decidir, não eu; conte-me, pois, o que você sabe.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13718, 34, """Os homens de bom senso, os sábios que me ouvem, me declaram:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13719, 35, "‘Jó não sabe o que diz; não há discernimento em suas palavras’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13720, 36, "Ah, se Jó sofresse a mais dura prova, por sua resposta de ímpio!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13721, 37, "Ao seu pecado ele acrescenta a revolta; com desprezo bate palmas entre nós e multiplica suas palavras contra Deus"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13722, 1, "Eliú prosseguiu:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13723, 2, """Você acha que isso é justo? Pois você diz: ‘Serei absolvido por Deus’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13724, 3, "Contudo, você lhe pergunta: ‘Que vantagem tenho eu, e o que ganho, se não pecar? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13725, 4, """Desejo responder-lhe, a você e aos seus amigos que estão com você.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13726, 5, "Olhe para os céus e veja; mire as nuvens, tão elevadas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13727, 6, "Se você pecar, em que isso o afetará? Se os seus pecados forem muitos, que é que isso lhe fará?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13728, 7, "Se você for justo, o que lhe dará? Ou o que ele receberá de sua mão?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13729, 8, "A sua impiedade só afeta aos homens, seus semelhantes, e a sua justiça, aos filhos dos homens.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13730, 9, """Os homens se lamentam sob fardos de opressão; imploram que os libertem do braço dos poderosos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13731, 10, "Mas não há quem pergunte: ‘Onde está Deus, o meu Criador, que de noite faz surgirem cânticos,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13732, 11, "que nos ensina mais que aos animais da terra e nos faz mais sábios que as aves dos céus? ’", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13733, 12, "Quando clamam, ele não responde por causa da arrogância dos ímpios.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13734, 13, "Aliás, Deus não escuta a vã súplica que fazem; o Todo-poderoso não lhe dá atenção.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13735, 14, "Pois muito menos escutará quando você disser que não o vê, que a sua causa está diante dele e que você tem que esperar por ele.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13736, 15, "Mais que isso, que a sua ira jamais castiga e que ele não dá a mínima atenção à iniqüidade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13737, 16, "Assim é que Jó abre a sua boca para dizer palavras vãs; em sua ignorância ele multiplica palavras"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13738, 1, "Disse mais Eliú:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13739, 2, """Peço-lhe que seja um pouco mais paciente comigo, e lhe mostrarei que se pode dizer mais verdades em defesa de Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13740, 3, "Vem de longe o meu conhecimento; atribuirei justiça ao meu Criador.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13741, 4, "Não tenha dúvida de que as minhas palavras não são falsas; quem está com você é a perfeição no conhecimento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13742, 5, """Deus é poderoso, mas não despreza os homens; é poderoso e firme em seu propósito.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13743, 6, "Não poupa a vida dos ímpios, mas garante os direitos dos aflitos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13744, 7, "Não tira os seus olhos do justo; ele o coloca nos tronos com os reis e o exalta para sempre.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13745, 8, "Mas, se os homens forem acorrentados, presos firmemente com as cordas da aflição,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13746, 9, "e lhes dirá o que fizeram, que pecaram com arrogância.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13747, 10, "Ele os fará ouvir a correção e lhes ordenará que se arrependam do mal que praticaram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13748, 11, "Se lhe obedecerem e o servirem, serão prósperos até o fim dos seus dias e terão contentamento nos anos que lhes restam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13749, 12, "Mas, se não obedecerem, perecerão à espada e morrerão na ignorância.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13750, 13, """Os que têm coração ímpio guardam ressentimento; mesmo quando ele os agrilhoa eles não clamam por socorro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13751, 14, "Morrem em plena juventude entre os prostitutos dos santuários.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13752, 15, "Mas aos que sofrem ele os livra em meio ao sofrimento; em sua aflição ele lhes fala.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13753, 16, """Ele está atraindo você para longe das mandíbulas da aflição, para um lugar amplo e livre, para o conforto da mesa farta e seleta que você terá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13754, 17, "Mas agora, farto sobre você é o julgamento que cabe aos ímpios; o julgamento e a justiça o pegaram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13755, 18, "Cuidado! Que ninguém o seduza com riquezas; não se deixe desviar por suborno, por maior que este seja.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13756, 19, "Acaso a sua riqueza, ou mesmo todos os seus grandes esforços, dariam a você apoio e alívio da aflição?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13757, 20, "Não anseie pela noite, quando o povo é tirado dos seus lares.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13758, 21, "Cuidado! Não se volte para a iniqüidade, que você parece preferir à aflição.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13759, 22, """Deus é exaltado em seu poder. Quem é mestre como ele?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13760, 23, "Quem lhe prescreveu os seus caminhos, ou lhe disse: ‘Agiste mal’?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13761, 24, "Lembre-se de exaltar as suas obras, às quais os homens dedicam cânticos de louvor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13762, 25, "Toda a humanidade as vê; de lugares distantes os homens as contemplam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13763, 26, "Como Deus é grande! Ultrapassa o nosso entendimento! Não há como calcular os anos da sua existência.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13764, 27, """Ele atrai as gotas de água, que se dissolvem e descem como chuva para os regatos;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13765, 28, "as nuvens as despejam em aguaceiros sobre a humanidade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13766, 29, "Quem pode entender como ele estende as suas nuvens, como ele troveja desde o seu pavilhão?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13767, 30, "Observe como ele espalha os seus relâmpagos ao redor, iluminando até as profundezas do mar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13768, 31, "É assim que ele governa as nações e lhes fornece grande fartura.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13769, 32, "Ele enche as mãos de relâmpagos e lhes determina o alvo que deverão atingir.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13770, 33, "Seu trovão anuncia a tempestade que está a caminho; até o gado a pressente.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13771, 1, """Diante disso o meu coração bate aceleradamente e salta do seu lugar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13772, 2, "Ouça! Escute o estrondo da sua voz, o trovejar da sua boca.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13773, 3, "Ele solta os seus relâmpagos por baixo de toda a extensão do céu e os manda para os confins da terra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13774, 4, "Depois vem o som do seu grande estrondo: ele troveja com sua majestosa voz. Quando a sua voz ressoa, nada o faz recuar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13775, 5, "A voz de Deus troveja maravilhosamente; ele faz coisas grandiosas, acima do nosso entendimento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13776, 6, "Ele diz à neve: ‘Caia sobre a terra’, e à chuva: ‘Seja um forte aguaceiro’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13777, 7, "Ele paralisa o trabalho de cada homem, a fim de que todos os que ele criou conheçam a sua obra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13778, 8, "Os animais vão para os seus esconderijos; e ficam nas suas tocas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13779, 9, "A tempestade sai da sua câmara, e dos ventos vem o frio.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13780, 10, "O sopro de Deus produz gelo, e as vastas águas se congelam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13781, 11, "Também carrega de umidade as nuvens, e entre elas espalha os seus relâmpagos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13782, 12, "Ele as faz girar, circulando sobre a superfície de toda a terra, para fazerem tudo o que ele lhes ordenar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13783, 13, "Ele traz as nuvens, ora para castigar os homens, ora para regar a sua terra e mostrar o seu amor.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13784, 14, """Escute isto, Jó; pare e reflita nas maravilhas de Deus.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13785, 15, "Acaso você sabe como Deus comanda as nuvens e faz brilhar os seus relâmpagos?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13786, 16, "Você sabe como ficam suspensas as nuvens, essas maravilhas daquele que tem perfeito conhecimento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13787, 17, "Você, que em sua roupa desfalece de calor quando a terra fica amortecida sob o vento sul,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13788, 18, "pode ajudá-lo a estender os céus, duros como espelho de bronze?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13789, 19, """Diga-nos o que devemos dizer a ele; não podemos elaborar a nossa defesa por causa das nossas trevas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13790, 20, "Deve-se dizer-lhe o que lhe quero falar? Quem pediria para ser devorado?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13791, 21, "Ninguém pode olhar para o fulgor do sol nos céus, depois que o vento os clareia.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13792, 22, "Do norte vem luz dourada; Deus vem em temível majestade.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13793, 23, "Fora de nosso alcance está o Todo-poderoso, exaltado em poder; mas, em sua justiça e retidão, não oprime ninguém.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13794, 24, "Por isso os homens o temem; não dá ele atenção a todos os sábios de coração? """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13795, 1, "Então o Senhor respondeu a Jó do meio da tempestade. Disse ele:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13796, 2, """Quem é esse que obscurece o meu conselho com palavras sem conhecimento?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13797, 3, "Prepare-se como simples homem; vou fazer-lhe perguntas, e você me responderá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13798, 4, """Onde você estava quando lancei os alicerces da terra? Responda-me, se é que você sabe tanto.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13799, 5, "Quem marcou os limites das suas dimensões? Vai ver que você sabe! E quem estendeu sobre ela a linha de medir?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13800, 6, "E as suas bases, sobre o que foram postas? E quem colocou sua pedra de esquina,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13801, 7, "enquanto as estrelas matutinas juntas cantavam e todos os anjos se regozijavam?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13802, 8, """Quem represou o mar pondo-lhe portas, quando ele irrompeu do ventre materno,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13803, 9, "quando o vesti de nuvens e em densas trevas o envolvi,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13804, 10, "quando fixei os seus limites e lhe coloquei portas e barreiras,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13805, 11, "quando eu lhe disse: Até aqui você pode vir, além deste ponto não, aqui faço parar suas ondas orgulhosas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13806, 12, """Você já deu ordens à manhã ou mostrou à alvorada o seu lugar,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13807, 13, "para que ela apanhasse a terra pelas pontas e sacudisse dela os ímpios?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13808, 14, "A terra toma forma como o barro sob o sinete; e tudo nela se vê como uma veste.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13809, 15, "Aos ímpios é negada a sua luz, e quebra-se o seu braço levantado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13810, 16, """Você já foi até às nascentes do mar, ou já passeou pelas obscuras profundezas do abismo?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13811, 17, "As portas da morte lhe foram mostradas? Você viu as portas das densas trevas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13812, 18, "Você faz idéia de quão imensas são as áreas da terra? Fale-me, se é que você sabe.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13813, 19, """Como se vai ao lugar onde mora a luz? E onde está a residência das trevas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13814, 20, "Poderá você conduzi-las ao lugar que lhes pertence? Conhece o caminho da habitação delas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13815, 21, "Vai ver que conhece, pois você já tinha nascido! Você já viveu tantos anos!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13816, 22, """Acaso você entrou nos reservatórios de neve, já viu os depósitos de saraiva,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13817, 23, "que eu guardo para os períodos de tribulação, para os dias de guerra e de combate?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13818, 24, "Qual o caminho por onde se repartem os relâmpagos? Onde é que os ventos orientais são distribuídos sobre a terra?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13819, 25, "Quem é que abre um canal para a chuva torrencial, e um caminho para a tempestade trovejante,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13820, 26, "para fazer chover na terra em que não vive nenhum homem, no deserto onde não há ninguém,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13821, 27, "para matar a sede do deserto árido e nele fazer brotar vegetação?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13822, 28, "Acaso a chuva tem pai? Quem é o pai das gotas de orvalho?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13823, 29, "De que ventre materno vem o gelo? E quem dá à luz a geada que cai dos céus,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13824, 30, "quando as águas se tornam duras como pedra e a superfície do abismo se congela?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13825, 31, """Você pode amarrar as lindas Plêiades? Pode afrouxar as cordas do Órion?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13826, 32, "Pode fazer surgir no tempo certo as constelações ou fazer sair a Ursa com os seus filhotes?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13827, 33, "Você conhece as leis dos céus? Voce pode determinar o domínio de Deus sobre a terra?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13828, 34, """Você é capaz de levantar a voz até às nuvens e cobrir-se com uma inundação?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13829, 35, "É você que envia os relâmpagos, e eles lhe dizem: ‘Aqui estamos’?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13830, 36, "Quem foi que deu sabedoria ao coração e entendimento à mente?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13831, 37, "Quem é que tem sabedoria para avaliar as nuvens? Quem é capaz de despejar os cântaros de água dos céus,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13832, 38, "quando o pó se endurece e os torrões de terra grudam uns nos outros?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13833, 39, """É você que caça a presa para a leoa e satisfaz a fome dos leões,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13834, 40, "quando se agacham em suas tocas ou ficam à espreita no matagal?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13835, 41, "Quem dá alimento aos corvos quando os seus filhotes clamam a Deus e vagueiam por falta de comida?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13836, 1, """Você sabe quando as cabras monteses dão à luz? Você está atento quando a corça tem o seu filhote?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13837, 2, "Acaso você conta os meses até elas darem à luz? Sabe em que época elas têm as suas crias?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13838, 3, "Elas se agacham, dão à luz os seus filhotes, e suas dores se vão.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13839, 4, "Seus filhotes crescem nos campos e ficam fortes; partem, e não voltam mais.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13840, 5, """Quem pôs em liberdade o jumento selvagem? Quem soltou suas cordas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13841, 6, "Eu lhe dei o deserto como lar, os leito seco de lagos salgados como sua morada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13842, 7, "Ele se ri da agitação da cidade; não ouve os gritos do tropeiro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13843, 8, "Vagueia pelas colinas em busca de pasto e vai em busca daquilo que é verde.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13844, 9, """Será que o boi selvagem consentirá em servir você? e em passar a noite ao lado dos cochos do seu curral?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13845, 10, "Poderá você prendê-lo com arreio na vala? Irá atrás de você arando os vales?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13846, 11, "Você vai confiar nele, por causa da sua grande força? Vai deixar a cargo dele o trabalho pesado que você tem que fazer?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13847, 12, "Poderá você estar certo de que ele recolherá o seu trigo e o ajuntará na sua eira?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13848, 13, """A avestruz bate as asas alegremente. Que se dirá então das asas e da plumagem da cegonha?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13849, 14, "Ela abandona os ovos no chão e deixa que a areia os aqueça,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13850, 15, "esquecida de que um pé poderá esmagá-los, que algum animal selvagem poderá pisoteá-los.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13851, 16, "Ela trata mal os seus filhotes, como se não fossem dela, e não se importa se o seu trabalho é inútil.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13852, 17, "Isso porque Deus não lhe deu sabedoria nem parcela alguma de bom senso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13853, 18, "Contudo, quando estende as penas para correr, ela ri do cavalo e daquele que o cavalga.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13854, 19, """É você que dá força ao cavalo ou veste o seu pescoço com sua crina tremulante?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13855, 20, "Você o faz saltar como gafanhoto, espalhando terror com o seu orgulhoso resfolegar?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13856, 21, "Ele escarva com fúria, mostra com prazer a sua força, e sai para enfrentar as armas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13857, 22, "Ele ri do medo, e nada teme; não recua diante da espada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13858, 23, "A aljava balança ao seu lado, com a lança e o dardo flamejantes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13859, 24, "Num furor frenético ele devora o chão; não consegue esperar pelo toque da trombeta.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13860, 25, "Ao toque da trombeta, ele relincha: ‘Eia! ’ De longe sente cheiro de combate, o brado de comando e o grito de guerra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13861, 26, """É graças a inteligência que você tem que o falcão alça vôo e estende as asas rumo sul?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13862, 27, "É porque você manda, que a águia se eleva, e no alto constrói o seu ninho?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13863, 28, "Um penhasco é sua morada, e ali passa a noite; uma escarpa rochosa é a sua fortaleza.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13864, 29, "De lá sai ela em busca de alimento; de longe os seus olhos o vêem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13865, 30, "Seus filhotes bebem sangue, e, onde há mortos, ali ela está"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13866, 1, "O Senhor disse a Jó:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13867, 2, """Aquele que contende com o Todo-poderoso poderá repreendê-lo? Que responda a Deus aquele que o acusa! """, 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13868, 3, "Então Jó respondeu ao Senhor:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13869, 4, """Sou indigno; como posso responder-te? Ponho a mão sobre a minha boca.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13870, 5, "Falei uma vez, mas não tenho resposta; sim, duas vezes, mas não direi mais nada"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13871, 6, "Depois, o Senhor falou a Jó do meio da tempestade:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13872, 7, """Prepare-se como simples homem que é; eu lhe farei perguntas, e você me responderá.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13873, 8, """Você vai pôr em dúvida a minha justiça? Vai condenar-me para justificar-se?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13874, 9, "Seu braço é como o de Deus, e sua voz pode trovejar como a dele?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13875, 10, "Adorne-se, então, de esplendor e glória, e vista-se de majestade e honra.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13876, 11, "Derrame a fúria da sua ira, olhe para todo orgulhoso e lance-o por terra,", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13877, 12, "olhe para todo orgulhoso e humilhe-o, esmague os ímpios onde estiverem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13878, 13, "Enterre-os todos juntos no pó; encubra os rostos deles no túmulo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13879, 14, "Então admitirei que a sua mão direita pode salvar você.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13880, 15, """Veja o Beemote que criei quando criei você e que come de capim como o boi.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13881, 16, "Que força ele tem em seus lombos! Que poder nos músculos do seu ventre!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13882, 17, "A cauda dele balança como o cedro; os nervos de suas coxas são firmemente entrelaçados.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13883, 18, "Seus ossos são canos de bronze, seus membros são varas de ferro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13884, 19, "Ele ocupa o primeiro lugar entre as obras de Deus. No entanto, o seu Criador pode chegar a ele com sua espada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13885, 20, "Os montes lhe oferecem os seus produtos, e todos os animais selvagens brincam por perto.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13886, 21, "Sob os lotos se deita, oculto entre os juncos do brejo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13887, 22, "Os lotos o escondem à sua sombra; os salgueiros junto ao regato o cercam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13888, 23, "Quando o rio se enfurece, ele não se abala; mesmo que o Jordão encrespe as ondas contra a sua boca, ele se mantém calmo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13889, 24, "Poderá alguém capturá-lo pelos olhos, ou prendê-lo em armadilha e enganchá-lo pelo nariz?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13890, 1, """Você consegue pescar com anzol o leviatã ou prender sua língua com uma corda?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13891, 2, "Consegue fazer passar um cordão pelo seu nariz ou atravessar seu queixo com um gancho?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13892, 3, "Pensa que ele vai lhe implorar misericórdia e lhe vai falar palavras amáveis?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13893, 4, "Acha que ele vai fazer acordo com você, para que você o tenha como escravo pelo resto da vida?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13894, 5, "Acaso você consegue fazer dele um bichinho de estimação, como se ele fosse um passarinho, ou pôr-lhe uma coleira para as suas filhas?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13895, 6, "Poderão os negociantes vendê-lo? Ou reparti-lo entre os comerciantes?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13896, 7, "Você consegue encher de arpões o seu couro, e de lanças de pesca a sua cabeça?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13897, 8, "Se puser a mão nele, a luta ficará em sua memória, e nunca mais você tornará a fazê-lo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13898, 9, "Esperar vencê-lo é ilusão; só vê-lo já é assustador.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13899, 10, "Ninguém é suficientemente corajoso para despertá-lo. Quem então será capaz de resistir a mim?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13900, 11, "Quem primeiro me deu alguma coisa, que eu lhe deva pagar? Tudo o que há debaixo dos céus me pertence.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13901, 12, """Não deixarei de falar de seus membros, de sua força e de seu porte gracioso.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13902, 13, "Quem consegue arrancar sua capa externa? Quem se aproximaria dele com uma rédea?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13903, 14, "Quem ousa abrir as portas de sua boca, cercada com seus dentes temíveis?", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13904, 15, "Suas costas possuem fileiras de escudos firmemente unidos;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13905, 16, "cada um está tão junto do outro que nem o ar passa entre eles;", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13906, 17, "estão tão interligados, que é impossível separá-los.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13907, 18, "Seu forte sopro atira lampejos de luz; seus olhos são como os raios da alvorada.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13908, 19, "Tições saem da sua boca; fagulhas de fogo estalam.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13909, 20, "Das suas narinas sai fumaça como de panela fervente sobre fogueira de juncos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13910, 21, "Seu sopro faz o carvão pegar fogo, e da sua boca saltam chamas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13911, 22, "Tanta força reside em seu pescoço que o terror vai adiante dele.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13912, 23, "As dobras da sua carne são fortemente unidas; são tão firmes que não se movem.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13913, 24, "Seu peito é duro como pedra, rijo como a pedra inferior do moinho.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13914, 25, "Quando ele se ergue, os poderosos se apavoram; fogem com medo dos seus golpes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13915, 26, "A espada que o atinge não lhe faz nada, nem a lança nem a flecha nem o dardo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13916, 27, "Ferro ele trata como palha, e bronze como madeira podre.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13917, 28, "As flechas não o afugentam, as pedras das fundas são como cisco para ele.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13918, 29, "O bastão lhe parece fiapo de palha; o brandir da grande lança o faz rir.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13919, 30, "Seu ventre é como caco denteado, e deixa rastro na lama como o trilho de debulhar.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13920, 31, "Ele faz as profundezas se agitarem como caldeirão fervente, e revolve o mar como pote de ungüento.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13921, 32, "Deixa atrás de si um rastro cintilante; como se fossem os cabelos brancos do abismo.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13922, 33, "Nada na terra se equipara a ele; criatura destemida!", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13923, 34, "Com desdém olha todos os altivos; reina soberano sobre todos os orgulhosos"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13924, 1, "Então Jó respondeu ao Senhor:", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13925, 2, """Sei que podes fazer todas as coisas; nenhum dos teus planos pode ser frustrado.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13926, 3, "Tu perguntaste: ‘Quem é esse que obscurece o meu conselho sem conhecimento? ’ Certo é que falei de coisas que eu não entendia, coisas tão maravilhosas que eu não poderia saber.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13927, 4, """Tu disseste: ‘Agora escute, e eu falarei; vou fazer-lhe perguntas, e você me responderá’.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13928, 5, "Meus ouvidos já tinham ouvido a teu respeito, mas agora os meus olhos te viram.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13929, 6, "Por isso menosprezo a mim mesmo e me arrependo no pó e na cinza"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13930, 7, "Depois que o Senhor disse essas palavras a Jó, disse também ao Elifaz, de Temã: ""Estou indignado com você e com os seus dois amigos, pois vocês não falaram o que é certo a meu respeito, como fez meu servo Jó.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13931, 8, "Vão agora até meu servo Jó, levem sete novilhos e sete carneiros, e com eles apresentem holocaustos em favor de vocês mesmos. Meu servo Jó orará por vocês; eu aceitarei a oração dele e não farei com vocês o que vocês merecem pela loucura que cometeram. Vocês não falaram o que é certo a meu respeito, como fez meu servo Jó"".", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13932, 9, "Então Elifaz, de Temã, Bildade, de Suá, e Zofar, de Naamate, fizeram o que o Senhor lhes ordenara; e o Senhor aceitou a oração de Jó.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13933, 10, "Depois que Jó orou por seus amigos, o Senhor o tornou novamente próspero e lhe deu em dobro tudo o que tinha antes.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13934, 11, "Todos os seus irmãos e irmãs, e todos os que o haviam conhecido anteriormente vieram comer com ele em sua casa. Eles o consolaram e o confortaram por todas as tribulações que o Senhor tinha trazido sobre ele, e cada um lhe deu uma peça de prata e um anel de ouro.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13935, 12, "O Senhor abençoou o final da vida de Jó mais do que o início. Ele teve catorze mil ovelhas, seis mil camelos, mil juntas de boi e mil jumentos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13936, 13, "Também teve ainda sete filhos e três filhas.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13937, 14, "À primeira filha deu o nome de Jemima, à segunda o de Quézia e à terceira o de Quéren-Hapuque.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13938, 15, "Em parte alguma daquela terra havia mulheres tão bonitas como as filhas de Jó, e seu pai lhes deu herança junto com os seus irmãos.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13939, 16, "Depois disso Jó viveu cento e quarenta anos; viu seus filhos e os descendentes deles até a quarta geração.", 17);
INSERT INTO verses (id, number, text, chapter_id) VALUES (13940, 17, "E então morreu, em idade muito avançada.", 17);