INSERT INTO verses (id, number, text, chapter_id) VALUES (24895, 1, "Muitos já se dedicaram a elaborar um relato dos fatos que se cumpriram entre nós,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24896, 2, "conforme nos foram transmitidos por aqueles que desde o início foram testemunhas oculares e servos da palavra.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24897, 3, "Eu mesmo investiguei tudo cuidadosamente, desde o começo, e decidi escrever-te um relato ordenado, ó excelentíssimo Teófilo,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24898, 4, "para que tenhas a certeza das coisas que te foram ensinadas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24899, 5, "No tempo de Herodes, rei da Judéia, havia um sacerdote chamado Zacarias, que pertencia ao grupo sacerdotal de Abias; Isabel, sua mulher, também era descendente de Arão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24900, 6, "Ambos eram justos aos olhos de Deus, obedecendo de modo irrepreensível a todos os mandamentos e preceitos do Senhor.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24901, 7, "Mas eles não tinham filhos, porque Isabel era estéril; e ambos eram de idade avançada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24902, 8, "Certa vez, estando de serviço o seu grupo, Zacarias estava servindo como sacerdote diante de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24903, 9, "Ele foi escolhido por sorteio, de acordo com o costume do sacerdócio, para entrar no santuário do Senhor e oferecer incenso.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24904, 10, "Chegando a hora de oferecer incenso, o povo todo estava orando do lado de fora.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24905, 11, "Então um anjo do Senhor apareceu a Zacarias, à direita do altar do incenso.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24906, 12, "Quando Zacarias o viu, perturbou-se e foi dominado pelo medo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24907, 13, "Mas o anjo lhe disse: ""Não tenha medo, Zacarias; sua oração foi ouvida. Isabel, sua mulher, lhe dará um filho, e você lhe dará o nome de João.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24908, 14, "Ele será motivo de prazer e de alegria para você, e muitos se alegrarão por causa do nascimento dele,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24909, 15, "pois será grande aos olhos do Senhor. Ele nunca tomará vinho nem bebida fermentada, e será cheio do Espírito Santo desde antes do seu nascimento.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24910, 16, "Fará retornar muitos dentre o povo de Israel ao Senhor, o seu Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24911, 17, "E irá adiante do Senhor, no espírito e no poder de Elias, para fazer voltar o coração dos pais a seus filhos e os desobedientes à sabedoria dos justos, para deixar um povo preparado para o Senhor"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24912, 18, "Zacarias perguntou ao anjo: ""Como posso ter certeza disso? Sou velho, e minha mulher é de idade avançada"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24913, 19, "O anjo respondeu: ""Sou Gabriel, o que está sempre na presença de Deus. Fui enviado para lhe transmitir estas boas novas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24914, 20, "Agora você ficará mudo. Não poderá falar até o dia em que isso acontecer, porque não acreditou em minhas palavras, que se cumprirão no tempo oportuno"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24915, 21, "Enquanto isso, o povo esperava por Zacarias, estranhando sua demora no santuário.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24916, 22, "Quando saiu, não conseguia falar nada; o povo percebeu então que ele tivera uma visão no santuário. Zacarias fazia sinais para eles, mas permanecia mudo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24917, 23, "Quando se completou seu período de serviço, ele voltou para casa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24918, 24, "Depois disso, Isabel, sua mulher, engravidou e durante cinco meses não saiu de casa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24919, 25, "E ela dizia: ""Isto é obra do Senhor fez! Agora ele olhou para mim com favor, para desfazer a minha humilhação perante o povo. """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24920, 26, "No sexto mês Deus enviou o anjo Gabriel a Nazaré, cidade da Galiléia,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24921, 27, "a uma virgem prometida em casamento a certo homem chamado José, descendente de Davi. O nome da virgem era Maria.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24922, 28, "O anjo, aproximando-se dela, disse: ""Alegre-se, agraciada! O Senhor está com você! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24923, 29, "Maria ficou perturbada com essas palavras, pensando no que poderia significar esta saudação.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24924, 30, "Mas o anjo lhe disse: ""Não tenha medo, Maria; você foi agraciada por Deus!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24925, 31, "Você ficará grávida e dará à luz um filho, e lhe porá o nome de Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24926, 32, "Ele será grande e será chamado Filho do Altíssimo. O Senhor Deus lhe dará o trono de seu pai Davi,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24927, 33, "e ele reinará para sempre sobre o povo de Jacó; seu Reino jamais terá fim"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24928, 34, "Perguntou Maria ao anjo: ""Como acontecerá isso, se sou virgem? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24929, 35, "O anjo respondeu: ""O Espírito Santo virá sobre você, e o poder do Altíssimo a cobrirá com a sua sombra. Assim, aquele que há de nascer será chamado santo, Filho de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24930, 36, "Também Isabel, sua parenta, terá um filho na velhice; aquela que diziam ser estéril já está em seu sexto mês de gestação.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24931, 37, "Pois nada é impossível para Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24932, 38, "Respondeu Maria: ""Sou serva do Senhor; que aconteça comigo conforme a tua palavra"". Então o anjo a deixou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24933, 39, "Naqueles dias, Maria preparou-se e foi depressa para a uma cidade da região montanhosa da Judéia,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24934, 40, "onde entrou na casa de Zacarias e saudou Isabel.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24935, 41, "Quando Isabel ouviu a saudação de Maria, o bebê agitou-se em seu ventre, e Isabel ficou cheia do Espírito Santo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24936, 42, "Em alta voz exclamou: ""Bendita é você entre as mulheres, e bendito é o filho que você dará à luz!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24937, 43, "Mas por que sou tão agraciada, a ponto de me visitar a mãe do meu Senhor?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24938, 44, "Logo que a sua saudação chegou aos meus ouvidos, o bebê que está em meu ventre agitou-se de alegria.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24939, 45, "Feliz é aquela que creu que se cumprirá aquilo que o Senhor lhe disse! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24940, 46, "Então disse Maria: ""Minha alma engrandece ao Senhor", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24941, 47, "e o meu espírito se alegra em Deus, meu Salvador,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24942, 48, "pois atentou para a humildade da sua serva. De agora em diante, todas as gerações me chamarão bem-aventurada,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24943, 49, "pois o Poderoso fez grandes coisas em meu favor; santo é o seu nome.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24944, 50, "A sua misericórdia estende-se aos que o temem, de geração em geração.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24945, 51, "Ele realizou poderosos feitos com seu braço; dispersou os que são soberbos no mais íntimo do coração.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24946, 52, "Derrubou governantes dos seus tronos, mas exaltou os humildes.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24947, 53, "Encheu de coisas boas os famintos, mas despediu de mãos vazias os ricos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24948, 54, "Ajudou a seu servo Israel, lembrando-se da sua misericórdia", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24949, 55, "para com Abraão e seus descendentes para sempre, como dissera aos nossos antepassados"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24950, 56, "Maria ficou com Isabel cerca de três meses e depois voltou para casa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24951, 57, "Ao se completar o tempo de Isabel dar à luz, ela teve um filho.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24952, 58, "Seus vizinhos e parentes ouviram falar da grande misericórdia que o Senhor lhe havia demonstrado e se alegraram com ela.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24953, 59, "No oitavo dia foram circuncidar o menino e queriam dar-lhe o nome do pai, Zacarias;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24954, 60, "mas sua mãe tomou a palavra e disse: ""Não! Ele será chamado João"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24955, 61, "Disseram-lhe: ""Você não há nenhum parente com esse nome"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24956, 62, "Então fizeram sinais ao pai do menino, para saber como queria que a criança se chamasse.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24957, 63, "Ele pediu uma tabuinha e, para admiração de todos, escreveu: ""O nome dele é João"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24958, 64, "Imediatamente sua boca se abriu, sua língua se soltou e ele começou a falar, louvando a Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24959, 65, "Todos os vizinhos ficaram cheios de temor, e por toda a região montanhosa da Judéia se falava sobre essas coisas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24960, 66, "Todos os que ouviam falar disso se perguntavam: ""O que vai ser este menino? "" Pois a mão do Senhor estava com ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24961, 67, "Seu pai, Zacarias, foi cheio do Espírito Santo e profetizou:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24962, 68, """Louvado seja o Senhor, o Deus de Israel, porque visitou e redimiu o seu povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24963, 69, "Ele promoveu poderosa salvação para nós, na linhagem do seu servo Davi,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24964, 70, "( como falara pelos seus santos profetas, na antigüidade ),", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24965, 71, "salvando-nos dos nossos inimigos e da mão de todos os que nos odeiam,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24966, 72, "para mostrar sua misericórdia aos nossos antepassados e lembrar sua santa aliança,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24967, 73, "o juramento que fez ao nosso pai Abraão:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24968, 74, "resgatar-nos da mão dos nossos inimigos para servi-lo sem medo,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24969, 75, "em santidade e justiça, diante dele todos os nossos dias.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24970, 76, "E você, menino, será chamado profeta do Altíssimo, pois irá adiante do Senhor, para lhe preparar o caminho,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24971, 77, "para dar ao seu povo o conhecimento da salvação, mediante o perdão dos seus pecados,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24972, 78, "por causa das ternas misericórdias de nosso Deus, pelas quais do alto nos visitará o sol nascente", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24973, 79, "para brilhar sobre aqueles que estão vivendo nas trevas e na sombra da morte, e guiar nossos pés no caminho da paz"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24974, 80, "E o menino crescia e se fortalecia no espírito; e viveu no deserto, até aparecer publicamente a Israel.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24975, 1, "Naqueles dias César Augusto publicou um decreto ordenando o recenseamento de todo o império romano.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24976, 2, "Este foi o primeiro recenseamento feito quando Quirino era governador da Síria.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24977, 3, "E todos iam para a sua cidade natal, a fim de alistar-se.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24978, 4, "Assim, José também foi da cidade de Nazaré da Galiléia para a Judéia, para Belém, cidade de Davi, porque pertencia à casa e à linhagem de Davi.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24979, 5, "Ele foi a fim de alistar-se, com Maria, que lhe estava prometida em casamento e esperava um filho.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24980, 6, "Enquanto estavam lá, chegou o tempo de nascer o bebê,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24981, 7, "e ela deu à luz o seu primogênito. Envolveu-o em panos e o colocou numa manjedoura, porque não havia lugar para eles na hospedaria.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24982, 8, "Havia pastores que estavam nos campos próximos e durante a noite tomavam conta dos seus rebanhos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24983, 9, "E aconteceu que um anjo do Senhor apareceu-lhes e a glória do Senhor resplandeceu ao redor deles; e ficaram aterrorizados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24984, 10, "Mas o anjo lhes disse: ""Não tenham medo. Estou lhes trazendo boas novas de grande alegria, que são para todo o povo:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24985, 11, "Hoje, na cidade de Davi, lhes nasceu o Salvador que é Cristo, o Senhor.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24986, 12, "Isto lhes servirá de sinal: encontrarão o bebê envolto em panos e deitado numa manjedoura"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24987, 13, "De repente, uma grande multidão do exército celestial apareceu com o anjo, louvando a Deus e dizendo:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24988, 14, """Glória a Deus nas alturas, e paz na terra aos homens aos quais ele concede o seu favor"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24989, 15, "Quando os anjos os deixaram e foram para o céu, os pastores disseram uns aos outros: ""Vamos a Belém, e vejamos isso que aconteceu, e que o Senhor nos deu a conhecer"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24990, 16, "Então correram para lá e encontraram Maria e José, e o bebê deitado na manjedoura.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24991, 17, "Depois de o verem, contaram a todos o que lhes fora dito a respeito daquele menino,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24992, 18, "e todos os que ouviram o que os pastores diziam ficaram admirados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24993, 19, "Maria, porém, guardava todas essas coisas e sobre elas refletia em seu coração.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24994, 20, "Os pastores voltaram glorificando e louvando a Deus por tudo o que tinham visto e ouvido, como lhes fora dito.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24995, 21, "Completando-se os oito dias para a circuncisão do menino, foi-lhe posto o nome de Jesus, o qual lhe tinha sido dado pelo anjo antes de ele nascer.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24996, 22, "Completando-se o tempo da purificação deles, de acordo com a Lei de Moisés, José e Maria o levaram a Jerusalém para apresentá-lo ao Senhor", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24997, 23, "( como está escrito na Lei do Senhor: ""Todo primogênito do sexo masculino será consagrado ao Senhor"" )", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24998, 24, "e para oferecer um sacrifício, de acordo com o que diz a Lei do Senhor: ""duas rolinhas ou dois pombinhos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24999, 25, "Havia em Jerusalém um homem chamado Simeão, que era justo e piedoso, e que esperava a consolação de Israel; e o Espírito Santo estava sobre ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25000, 26, "Fora-lhe revelado pelo Espírito Santo que ele não morreria antes de ver o Cristo do Senhor.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25001, 27, "Movido pelo Espírito, ele foi ao templo. Quando os pais trouxeram o menino Jesus para lhe fazer conforme requeria o costume da lei,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25002, 28, "Simeão o tomou nos braços e louvou a Deus, dizendo:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25003, 29, """Ó Soberano, como prometeste, agora podes despedir em paz o teu servo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25004, 30, "Pois os meus olhos já viram a tua salvação,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25005, 31, "que preparaste à vista de todos os povos:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25006, 32, "luz para revelação aos gentios e para a glória de Israel, teu povo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25007, 33, "O pai e a mãe do menino estavam admirados com o que fora dito a respeito dele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25008, 34, "E Simeão os abençoou e disse a Maria, mãe de Jesus: ""Este menino está destinado a causar a queda e o soerguimento de muitos em Israel, e a ser um sinal de contradição,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25009, 35, "de modo que o pensamento de muitos corações será revelado. Quanto a você, uma espada atravessará a sua alma"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25010, 36, "Estava ali a profetisa Ana, filha de Fanuel, da tribo de Aser. Era muito idosa; havia vivido com seu marido sete anos depois de se casar", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25011, 37, "e então permanecera viúva até a idade de oitenta e quatro anos. Nunca deixava o templo: adorava a Deus jejuando e orando dia e noite.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25012, 38, "Tendo chegado ali naquele exato momento, deu graças a Deus e falava a respeito do menino a todos os que esperavam a redenção de Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25013, 39, "Depois de terem feito tudo o que era exigido pela Lei do Senhor, voltaram para a sua própria cidade, Nazaré, na Galiléia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25014, 40, "O menino crescia e se fortalecia, enchendo-se de sabedoria; e a graça de Deus estava sobre ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25015, 41, "Todos os anos seus pais iam a Jerusalém para a festa da Páscoa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25016, 42, "Quando ele completou doze anos de idade, eles subiram à festa, conforme o costume.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25017, 43, "Terminada a festa, voltando seus pais para casa, o menino Jesus ficou em Jerusalém, sem que eles percebessem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25018, 44, "Pensando que ele estava entre os companheiros de viagem, caminharam o dia todo. Então começaram a procurá-lo entre os seus parentes e conhecidos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25019, 45, "Não o encontrando, voltaram a Jerusalém para procurá-lo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25020, 46, "Depois de três dias o encontraram no templo, sentado entre os mestres, ouvindo-os e fazendo-lhes perguntas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25021, 47, "Todos os que o ouviam ficavam maravilhados com o seu entendimento e com as suas respostas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25022, 48, "Quando seus pais o viram, ficaram perplexos. Sua mãe lhe disse: ""Filho, por que você nos fez isto? Seu pai e eu estávamos aflitos, à sua procura"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25023, 49, "Ele perguntou: ""Por que vocês estavam me procurando? Não sabiam que eu devia estar na casa de meu Pai? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25024, 50, "Mas eles não compreenderam o que lhes dizia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25025, 51, "Então foi com eles para Nazaré, e era-lhes obediente. Sua mãe, porém, guardava todas essas coisas em seu coração.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25026, 52, "Jesus ia crescendo em sabedoria, estatura e graça diante de Deus e dos homens.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25027, 1, "No décimo quinto ano do reinado de Tibério César, quando Pôncio Pilatos era governador da Judéia; Herodes, tetrarca da Galiléia; seu irmão Filipe, tetrarca da Ituréia e Traconites; e Lisânias, tetrarca de Abilene;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25028, 2, "Anás e Caifás exerciam o sumo sacerdócio. Foi nesse ano que veio a palavra do Senhor a João, filho de Zacarias, no deserto.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25029, 3, "Ele percorreu toda a região próxima ao Jordão, pregando um batismo de arrependimento para o perdão dos pecados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25030, 4, "Como está escrito no livro das palavras de Isaías, o profeta: ""Voz do que clama no deserto: ‘Preparem o caminho para o Senhor, façam veredas retas para ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25031, 5, "Todo vale será aterrado e todas as montanhas e colinas, niveladas. As estradas tortuosas serão endireitadas e os caminhos acidentados, aplanados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25032, 6, "E toda a humanidade verá a salvação de Deus’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25033, 7, "João dizia às multidões que saíam para serem batizadas por ele: ""Raça de víboras! Quem lhes deu a idéia de fugir da ira que se aproxima?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25034, 8, "Dêem frutos que mostrem o arrependimento. E não comecem a dizer a si mesmos: ‘Abraão é nosso pai’. Pois eu lhes digo que destas pedras Deus pode fazer surgir filhos a Abraão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25035, 9, "O machado já está posto à raiz das árvores, e toda árvore que não der bom fruto será cortada e lançada ao fogo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25036, 10, """O que devemos fazer então? "", perguntavam as multidões.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25037, 11, "João respondia: ""Quem tem duas túnicas reparta-as com quem não tem nenhuma; e quem tem comida faça o mesmo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25038, 12, "Alguns publicanos também vieram para serem batizados. Eles perguntaram: ""Mestre, o que devemos fazer? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25039, 13, "Ele respondeu: ""Não cobrem nada além do que lhes foi estipulado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25040, 14, "Então alguns soldados lhe perguntaram: ""E nós, o que devemos fazer? "" Ele respondeu: ""Não pratiquem extorsão nem acusem ninguém falsamente; contentem-se com o seu salário"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25041, 15, "O povo estava em grande expectativa, questionando em seus corações se acaso João não seria o Cristo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25042, 16, "João respondeu a todos: ""Eu os batizo com água. Mas virá alguém mais poderoso do que eu, tanto que não sou digno nem de curvar-me e desamarrar as correias das suas sandálias. Ele os batizará com o Espírito Santo e com fogo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25043, 17, "Ele traz a pá em sua mão, a fim de limpar sua eira e juntar o trigo em seu celeiro; mas queimará a palha com fogo que nunca se apaga"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25044, 18, "E com muitas outras palavras João exortava o povo e lhe pregava as boas novas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25045, 19, "Todavia, quando João repreendeu Herodes, o tetrarca, por causa de Herodias, mulher do próprio irmão de Herodes, e por todas as outras coisas más que ele tinha feito,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25046, 20, "Herodes acrescentou a todas elas a de colocar João na prisão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25047, 21, "Quando todo o povo estava sendo batizado, também Jesus o foi. E, enquanto ele estava orando, o céu se abriu", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25048, 22, "e o Espírito Santo desceu sobre ele em forma corpórea, como pomba. Então veio do céu uma voz: ""Tu és o meu Filho amado; em ti me agrado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25049, 23, "Jesus tinha cerca de trinta anos de idade quando começou seu ministério. Ele era, como se pensava, filho de José, filho de Eli,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25050, 24, "filho de Matate, filho de Levi, filho de Melqui, filho de Janai, filho de José,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25051, 25, "filho de Matatias, filho de Amós, filho de Naum, filho de Esli, filho de Nagai,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25052, 26, "filho de Maate, filho de Matatias, filho de Semei, filho de Joseque, filho de Jodá,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25053, 27, "filho de Joanã, filho de Ressa, filho de Zorobabel, filho de Salatiel, filho de Neri,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25054, 28, "filho de Melqui, filho de Adi, filho de Cosã, filho de Elmadã, filho de Er,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25055, 29, "filho de Josué, filho de Eliézer, filho de Jorim, filho de Matate, filho de Levi,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25056, 30, "filho de Simeão, filho de Judá, filho de José, filho de Jonã, filho de Eliaquim,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25057, 31, "filho de Meleá, filho de Mená, filho de Matatá, filho de Natã, filho de Davi,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25058, 32, "filho de Jessé, filho de Obede, filho de Boaz, filho de Salmom, filho de Naassom,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25059, 33, "filho de Aminadabe, filho de Ram, filho de Esrom, filho de Perez, filho de Judá,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25060, 34, "filho de Jacó, filho de Isaque, filho de Abraão, filho de Terá, filho de Naor,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25061, 35, "filho de Serugue, filho de Ragaú, filho de Faleque, filho de Éber, filho de Salá,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25062, 36, "filho de Cainã, filho de Arfaxade, filho de Sem, filho de Noé, filho de Lameque,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25063, 37, "filho de Matusalém, filho de Enoque, filho de Jarede, filho de Maalaleel, filho de Cainã,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25064, 38, "filho de Enos, filho de Sete, filho de Adão, filho de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25065, 1, "Jesus, cheio do Espírito Santo, voltou do Jordão e foi levado pelo Espírito ao deserto,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25066, 2, "onde, durante quarenta dias, foi tentado pelo diabo. Não comeu nada durante esses dias e, ao fim deles, teve fome.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25067, 3, "O diabo lhe disse: ""Se você é o Filho de Deus, mande a esta pedra que se transforme em pão"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25068, 4, "Jesus respondeu: ""Está escrito: ‘Nem só de pão viverá o homem’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25069, 5, "O diabo o levou a um lugar alto e mostrou-lhe num relance todos os reinos do mundo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25070, 6, "E lhe disse: ""Eu lhe darei toda a autoridade sobre eles e todo o seu esplendor, porque me foram dados e posso dá-los a quem eu quiser.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25071, 7, "Então, se você me adorar, tudo será seu"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25072, 8, "Jesus respondeu: ""Está escrito: ‘Adore o Senhor, o seu Deus e só a ele preste culto’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25073, 9, "O diabo o levou a Jerusalém, colocou-o na parte mais alta do templo e lhe disse: ""Se você é o Filho de Deus, jogue-se daqui para baixo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25074, 10, "Pois está escrito: ‘Ele dará ordens a seus anjos a seu respeito, para lhe guardarem;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25075, 11, "com as mãos eles os segurarão, para que você não tropece em alguma pedra’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25076, 12, "Jesus respondeu: ""Dito está: ‘Não ponha à prova o Senhor, o seu Deus’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25077, 13, "Tendo terminado todas essas tentações, o diabo o deixou até ocasião oportuna.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25078, 14, "Jesus voltou para a Galiléia no poder do Espírito, e por toda aquela região se espalhou a sua fama.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25079, 15, "Ensinava nas sinagogas, e todos o elogiavam.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25080, 16, "Ele foi a Nazaré, onde havia sido criado, e no dia de sábado entrou na sinagoga, como era seu costume. E levantou-se para ler.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25081, 17, "Foi-lhe entregue o livro do profeta Isaías. Abriu-o e encontrou o lugar onde está escrito:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25082, 18, """O Espírito do Senhor está sobre mim, porque ele me ungiu para pregar boas novas aos pobres. Ele me enviou para proclamar liberdade aos presos e recuperação da vista aos cegos, para libertar os oprimidos", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25083, 19, "e proclamar o ano da graça do Senhor"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25084, 20, "Então ele fechou o livro, devolveu-o ao assistente e assentou-se. Na sinagoga todos tinham os olhos fitos nele;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25085, 21, "e ele começou a dizer-lhes: ""Hoje se cumpriu a Escritura que vocês acabaram de ouvir"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25086, 22, "Todos falavam bem dele, e estavam admirados com as palavras de graça que saíam de seus lábios. Mas perguntavam: ""Não é este o filho de José? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25087, 23, "Jesus lhes disse: ""É claro que vocês me citarão este provérbio: ‘Médico, cura-te a ti mesmo! ’ Faze aqui em tua terra o que ouvimos que fizeste em Cafarnaum"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25088, 24, "Continuou ele: ""Digo-lhes a verdade: Nenhum profeta é aceito em sua terra.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25089, 25, "Asseguro-lhes que havia muitas viúvas em Israel no tempo de Elias, quando o céu foi fechado por três anos e meio, e houve uma grande fome em toda a terra.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25090, 26, "Contudo, Elias não foi enviado a nenhuma delas, senão a uma viúva de Sarepta, na região de Sidom.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25091, 27, "Também havia muitos leprosos em Israel no tempo de Eliseu, o profeta; todavia, nenhum deles foi purificado: somente Naamã, o sírio"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25092, 28, "Todos os que estavam na sinagoga ficaram furiosos quando ouviram isso.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25093, 29, "Levantaram-se, expulsaram-no da cidade e o levaram até ao topo da colina sobre a qual fora construída a cidade, a fim de atirá-lo precipício abaixo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25094, 30, "Mas Jesus passou por entre eles e retirou-se.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25095, 31, "Então ele desceu a Cafarnaum, cidade da Galiléia, e, no sábado, começou a ensinar o povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25096, 32, "Todos ficavam maravilhados com o seu ensino, porque falava com autoridade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25097, 33, "Na sinagoga havia um homem possesso de um demônio, de um espírito imundo. Ele gritou com toda a força:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25098, 34, """Ah! que queres conosco, Jesus de Nazaré? Vieste para nos destruir? Sei quem tu és: o Santo de Deus! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25099, 35, "Jesus o repreendeu, e disse: ""Cale-se e saia dele! "" Então o demônio jogou o homem no chão diante de todos, e saiu dele sem o ferir.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25100, 36, "Todos ficaram admirados, e diziam uns aos outros: ""Que palavra é esta? Até aos espíritos imundos ele dá ordens com autoridade e poder, e eles saem! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25101, 37, "E a sua fama se espalhava por toda a região circunvizinha.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25102, 38, "Jesus saiu da sinagoga e foi à casa de Simão. A sogra de Simão estava com febre alta, e pediram a Jesus que fizesse algo por ela.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25103, 39, "Estando ele em pé junto dela, inclinou-se e repreendeu a febre, que a deixou. Ela se levantou imediatamente e passou a servi-los.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25104, 40, "Ao pôr-do-sol, o povo trouxe a Jesus todos os que tinham vários tipos de doenças; e ele os curou, impondo as mãos sobre cada um deles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25105, 41, "Além disso, de muitas pessoas saíam demônios gritando: ""Tu és o Filho de Deus! "" Ele, porém, os repreendia e não permitia que falassem, porque sabiam que ele era o Cristo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25106, 42, "Ao romper do dia, Jesus foi para um lugar solitário. As multidões o procuravam, e, quando chegaram até onde ele estava, insistiam que não as deixasse.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25107, 43, "Mas ele disse: ""É necessário que eu pregue as boas novas do Reino de Deus noutras cidades também, porque para isso fui enviado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25108, 44, "E continuava pregando nas sinagogas da Judéia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25109, 1, "Certo dia Jesus estava perto do lago de Genesaré, e uma multidão o comprimia de todos os lados para ouvir a palavra de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25110, 2, "Viu à beira do lago dois barcos, deixados ali pelos pescadores, que estavam lavando as suas redes.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25111, 3, "Entrou num dos barcos, o que pertencia a Simão, e pediu-lhe que o afastasse um pouco da praia. Então sentou-se, e do barco ensinava o povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25112, 4, "Tendo acabado de falar, disse a Simão: ""Vá para onde as águas são mais fundas"", e a todos: ""Lancem as redes para a pesca"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25113, 5, "Simão respondeu: ""Mestre, esforçamo-nos a noite inteira e não pegamos nada. Mas, porque és tu quem está dizendo isto, vou lançar as redes"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25114, 6, "Quando o fizeram, pegaram tal quantidade de peixe que as redes começaram a rasgar-se.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25115, 7, "Então fizeram sinais a seus companheiros no outro barco, para que viessem ajudá-lo; e eles vieram e encheram ambos os barcos, a ponto de quase começarem a afundar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25116, 8, "Quando Simão Pedro viu isso, prostrou-se aos pés de Jesus e disse: ""Afasta-te de mim, Senhor, porque sou um homem pecador! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25117, 9, "Pois ele e todos os seus companheiros estavam perplexos com a pesca que haviam feito,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25118, 10, "como também Tiago e João, os filhos de Zebedeu, sócios de Simão. Então Jesus disse a Simão: ""Não tenha medo; de agora em diante você será pescador de homens"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25119, 11, "Eles então arrastaram seus barcos para a praia, deixaram tudo e o seguiram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25120, 12, "Estando Jesus numa das cidades, passou um homem coberto de lepra. Quando viu a Jesus, prostrou-se com o rosto em terra e rogou-lhe: ""Se quiseres, podes purificar-me"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25121, 13, "Jesus estendeu a mão e tocou nele, dizendo: ""Quero. Seja purificado! "" E imediatamente a lepra o deixou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25122, 14, "Então Jesus lhe ordenou: ""Não conte isso a ninguém; mas vá mostrar-se ao sacerdote e ofereça pela sua purificação os sacrifícios que Moisés ordenou, para que sirva de testemunho"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25123, 15, "Todavia, as notícias a respeito dele se espalhavam ainda mais, de forma que multidões vinham para ouvi-lo e para serem curadas de suas doenças.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25124, 16, "Mas Jesus retirava-se para lugares solitários, e orava.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25125, 17, "Certo dia, quando ele ensinava, estavam sentados ali fariseus e mestres da lei, procedentes de todos os povoados da Galiléia, da Judéia e de Jerusalém. E o poder do Senhor estava com ele para curar os doentes.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25126, 18, "Vieram alguns homens trazendo um paralítico numa maca e tentaram fazê-lo entrar na casa, para colocá-lo diante de Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25127, 19, "Não conseguindo fazer isso, por causa da multidão, subiram ao terraço e o baixaram em sua maca, através de uma abertura, até o meio da multidão, bem em frente de Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25128, 20, "Vendo a fé que eles tinham, Jesus disse: ""Homem, os seus pecados estão perdoados"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25129, 21, "Os fariseus e os mestres da lei começaram a pensar: ""Quem é esse que blasfema? Quem pode perdoar pecados, a não ser somente Deus? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25130, 22, "Jesus, sabendo o que eles estavam pensando, perguntou: ""Por que vocês estão pensando assim?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25131, 23, "Que é mais fácil dizer: ‘Os seus pecados estão perdoados’, ou: ‘Levante-se e ande’?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25132, 24, "Mas, para que vocês saibam que o Filho do homem tem na terra autoridade para perdoar pecados"" — disse ao paralítico — ""eu lhe digo: levante-se, pegue a sua maca e vá para casa"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25133, 25, "Imediatamente ele se levantou na frente deles, pegou a maca em que estivera deitado e foi para casa louvando a Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25134, 26, "Todos ficaram atônitos e glorificavam a Deus, e, cheios de temor, diziam: ""Hoje vimos coisas extraordinárias! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25135, 27, "Depois disso, Jesus saiu e viu um publicano chamado Levi, sentado na coletoria, e disse-lhe: ""Siga-me"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25136, 28, "Levi levantou-se, deixou tudo e o seguiu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25137, 29, "Então Levi ofereceu um grande banquete a Jesus em sua casa. Havia muita gente comendo com eles: publicanos e outras pessoas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25138, 30, "Mas os fariseus e aqueles mestres da lei que eram da mesma facção queixaram-se aos discípulos de Jesus: ""Por que vocês comem e bebem com publicanos e ‘pecadores’? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25139, 31, "Jesus lhes respondeu: ""Não são os que têm saúde que precisam de médico, mas sim os doentes.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25140, 32, "Eu não vim chamar justos, mas pecadores ao arrependimento"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25141, 33, "E eles lhe disseram: ""Os discípulos de João jejuam e oram freqüentemente, bem como os discípulos dos fariseus; mas os teus vivem comendo e bebendo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25142, 34, "Jesus respondeu: ""Podem vocês fazer os convidados do noivo jejuar enquanto o noivo está com eles?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25143, 35, "Mas virão dias quando o noivo lhes será tirado; naqueles dias jejuarão"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25144, 36, "Então lhes contou esta parábola: ""Ninguém tira remendo de roupa nova e o costura em roupa velha; se o fizer, estragará a roupa nova, além do que o remendo da nova não se ajustará à velha.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25145, 37, "E ninguém põe vinho novo em vasilhas de couro velhas; se o fizer, o vinho novo rebentará as vasilhas, se derramará, e as vasilhas se estragarão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25146, 38, "Pelo contrário, vinho novo deve ser posto em vasilhas de couro novas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25147, 39, "E ninguém, depois de beber o vinho velho, prefere o novo, pois diz: ‘O vinho velho é melhor! ’ """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25148, 1, "Certo sábado, enquanto Jesus passava pelas lavouras de cereal, seus discípulos começaram a colher e a debulhar espigas com as mãos, comendo os grãos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25149, 2, "Alguns fariseus perguntaram: ""Por que vocês estão fazendo o que não é permitido no sábado? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25150, 3, "Jesus lhes respondeu: ""Vocês nunca leram o que fez Davi, quando ele e seus companheiros estavam com fome?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25151, 4, "Ele entrou na casa de Deus e, tomando os pães da Presença, comeu o que apenas aos sacerdotes era permitido comer, e os deu também aos seus companheiros"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25152, 5, "E então lhes disse: ""O Filho do homem é Senhor do sábado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25153, 6, "Noutro sábado, ele entrou na sinagoga e começou a ensinar; estava ali um homem cuja mão direita era atrofiada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25154, 7, "Os fariseus e os mestres da lei estavam procurando um motivo para acusar Jesus; por isso o observavam atentamente, para ver se ele iria curá-lo no sábado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25155, 8, "Mas Jesus sabia o que eles estavam pensando e disse ao homem da mão atrofiada: ""Levante-se e venha para o meio"". Ele se levantou e foi.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25156, 9, "Jesus lhes disse: ""Eu lhes pergunto: o que é permitido fazer no sábado: o bem ou o mal, salvar a vida ou destruí-la? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25157, 10, "Então, olhou para todos os que estavam à sua volta e disse ao homem: ""Estenda a mão"". Ele a estendeu, e ela foi restaurada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25158, 11, "Mas eles ficaram furiosos e começaram a discutir entre si o que poderiam fazer contra Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25159, 12, "Num daqueles dias, Jesus saiu para o monte a fim de orar, e passou a noite orando a Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25160, 13, "Ao amanhecer, chamou seus discípulos e escolheu doze deles, a quem também designou como apóstolos:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25161, 14, "Simão, a quem deu o nome de Pedro; seu irmão André; Tiago; João; Filipe; Bartolomeu;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25162, 15, "Mateus; Tomé; Tiago, filho de Alfeu; Simão, chamado zelote;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25163, 16, "Judas, filho de Tiago; e Judas Iscariotes, que veio a ser o traidor.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25164, 17, "Jesus desceu com eles e parou num lugar plano. Estava ali muitos dos seus discípulos e imensa multidão procedente de toda a Judéia, de Jerusalém e do litoral de Tiro e de Sidom;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25165, 18, "que vieram para ouvi-lo e serem curadas de suas doenças. Os que eram perturbados por espíritos imundos ficaram curados,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25166, 19, "e todos procuravam tocar nele, porque dele saía poder que curava a todos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25167, 20, "Olhando para os seus discípulos, ele disse: ""Bem-aventurados vocês os pobres, pois a vocês pertence o Reino de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25168, 21, "Bem-aventurados vocês, que agora têm fome, pois serão satisfeitos. Bem-aventurados vocês, que agora choram, pois haverão de rir.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25169, 22, "Bem-aventurados serão vocês, quando os odiarem, expulsarem e insultarem, e eliminarem o nome de vocês, como sendo mau, por causa do Filho do homem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25170, 23, """Regozijem-se nesse dia e saltem de alegria, porque grande é a recompensa de vocês no céu. Pois assim os antepassados deles trataram os profetas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25171, 24, """Mas ai de vocês, os ricos, pois já receberam sua consolação.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25172, 25, "Ai de vocês, que agora têm fartura, porque passarão fome. Ai de vocês, que agora riem, pois haverão de se lamentar e chorar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25173, 26, "Ai de vocês, quando todos falarem bem de vocês, pois assim os antepassados deles trataram os falsos profetas"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25174, 27, """Mas eu digo a vocês que estão me ouvindo: Amem os seus inimigos, façam o bem aos que os odeiam,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25175, 28, "abençoem os que os amaldiçoam, orem por aqueles que os maltratam.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25176, 29, "Se alguém lhe bater numa face, ofereça-lhe também a outra. Se alguém lhe tirar a capa, não o impeça de tirar-lhe a túnica.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25177, 30, "Dê a todo o que lhe pedir, e se alguém tirar o que pertence a você, não lhe exija que o devolva.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25178, 31, "Como vocês querem que os outros lhes façam, façam também vocês a eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25179, 32, """Que mérito vocês terão, se amarem aos que os amam? Até os ‘pecadores’ amam aos que os amam.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25180, 33, "E que mérito terão, se fizerem o bem àqueles que são bons para com vocês? Até os ‘pecadores’ agem assim.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25181, 34, "E que mérito terão, se emprestarem a pessoas de quem esperam devolução? Até os ‘pecadores’ emprestam a ‘pecadores’, esperando receber devolução integral.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25182, 35, "Amem, porém, os seus inimigos, façam-lhes o bem e emprestem a eles, sem esperar receber nada de volta. Então, a recompensa que terão será grande e vocês serão filhos do Altíssimo, porque ele é bondoso para com os ingratos e maus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25183, 36, "Sejam misericordiosos, assim como o Pai de vocês é misericordioso"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25184, 37, """Não julguem, e vocês não serão julgados. Não condenem, e não serão condenados. Perdoem, e serão perdoados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25185, 38, "Dêem, e lhes será dado: uma boa medida, calcada, sacudida e transbordante será dada a vocês. Pois a medida que usarem, também será usada para medir vocês"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25186, 39, "Jesus fez também a seguinte comparação: ""Pode um cego guiar outro cego? Não cairão os dois no buraco?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25187, 40, "O discípulo não está acima do seu mestre, mas todo aquele que for bem preparado será como o seu mestre.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25188, 41, """Por que você repara no cisco que está no olho do seu irmão e não se dá conta da viga que está em seu próprio olho?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25189, 42, "Como você pode dizer ao seu irmão: ‘Irmão, deixe-me tirar o cisco do seu olho’, se você mesmo não consegue ver a viga que está em seu próprio olho? Hipócrita, tire primeiro a viga do seu olho, e então você verá claramente para tirar o cisco do olho do seu irmão"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25190, 43, """Nenhuma árvore boa dá fruto ruim, nenhuma árvore ruim dá fruto bom.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25191, 44, "Toda árvore é reconhecida por seus frutos. Ninguém colhe figos de espinheiros, nem uvas de ervas daninhas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25192, 45, "O homem bom tira coisas boas do bom tesouro que está em seu coração, e o homem mau tira coisas más do mal que está em seu coração, porque a sua boca fala do que está cheio o coração"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25193, 46, """Por que vocês me chamam ‘Senhor, Senhor’ e não fazem o que eu digo?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25194, 47, "Eu lhes mostrarei a que se compara aquele que vem a mim, ouve as minhas palavras e as pratica.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25195, 48, "É como um homem que, ao construir uma casa, cavou fundo e colocou os alicerces na rocha. Quando veio a inundação, a torrente deu contra aquela casa, mas não a conseguiu abalar, porque estava bem construída.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25196, 49, "Mas aquele que ouve as minhas palavras e não as pratica, é como um homem que construiu uma casa sobre o chão, sem alicerces. No momento em que a torrente deu contra aquela casa, ela caiu, e a sua destruição foi completa"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25197, 1, "Tendo terminado de dizer tudo isso ao povo, Jesus entrou em Cafarnaum.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25198, 2, "Ali estava doente, quase à morte, o servo de um centurião, a quem seu senhor estimava muito.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25199, 3, "Ele ouviu falar de Jesus e enviou-lhe alguns líderes religiosos dos judeus, pedindo-lhe que fosse curar o seu servo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25200, 4, "Chegando-se a Jesus, suplicaram-lhe com insistência: ""Este homem merece que lhe faças isso,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25201, 5, "porque ama a nossa nação e construiu a nossa sinagoga"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25202, 6, "Jesus foi com eles. Já estava perto da casa quando o centurião mandou amigos dizerem a Jesus: ""Senhor, não te incomodes, pois não mereço receber-te debaixo do meu teto.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25203, 7, "Por isso, nem me considerei digno de ir ao teu encontro. Mas dize uma palavra, e o meu servo será curado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25204, 8, "Pois eu também sou homem sujeito a autoridade, e com soldados sob o meu comando. Digo a um: ‘Vá’, e ele vai; e a outro: ‘Venha’, e ele vem. Digo a meu servo: ‘Faça isto’, e ele faz"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25205, 9, "Ao ouvir isso, Jesus admirou-se dele e, voltando-se para a multidão que o seguia, disse: ""Eu lhes digo que nem em Israel encontrei tamanha fé"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25206, 10, "Então os homens que haviam sido enviados voltaram para casa e encontraram o servo restabelecido.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25207, 11, "Logo depois, Jesus foi a uma cidade chamada Naim, e com ele iam os seus discípulos e uma grande multidão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25208, 12, "Ao se aproximar da porta da cidade, estava saindo o enterro do filho único de uma viúva; e uma grande multidão da cidade estava com ela.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25209, 13, "Ao vê-la, o Senhor se compadeceu dela e disse: ""Não chore"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25210, 14, "Depois, aproximou-se e tocou no caixão, e os que o carregavam pararam. Jesus disse: ""Jovem, eu lhe digo, levante-se! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25211, 15, "Ele se levantou, sentou-se e começou a conversar, e Jesus o entregou à sua mãe.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25212, 16, "Todos ficaram cheios de temor e louvavam a Deus. ""Um grande profeta se levantou entre nós"", diziam eles. ""Deus interveio em favor do seu povo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25213, 17, "Essas notícias sobre Jesus espalharam-se por toda a Judéia e regiões circunvizinhas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25214, 18, "Os discípulos de João contaram-lhe todas essas coisas. Chamando dois deles,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25215, 19, "enviou-os ao Senhor para perguntarem: ""És tu aquele que haveria de vir ou devemos esperar algum outro? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25216, 20, "Dirigindo-se a Jesus, aqueles homens disseram: ""João Batista nos enviou para te perguntarmos: ‘És tu aquele que haveria de vir ou devemos esperar algum outro? ’ """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25217, 21, "Naquele momento Jesus curou muitos que tinham males, doenças graves e espíritos malignos, e concedeu visão a muitos que eram cegos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25218, 22, "Então ele respondeu aos mensageiros: ""Voltem e anunciem a João o que vocês viram e ouviram: os cegos vêem, os aleijados andam, os leprosos são purificados, os surdos ouvem, os mortos são ressuscitados e as boas novas são pregadas aos pobres;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25219, 23, "e feliz é aquele que não se escandaliza por minha causa"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25220, 24, "Depois que os mensageiros de João foram embora, Jesus começou a falar à multidão a respeito de João: ""O que vocês foram ver no deserto? Um caniço agitado pelo vento?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25221, 25, "Ou, o que foram ver? Um homem vestido de roupas finas? Ora, os que vestem roupas esplêndidas e se entregam ao luxo estão nos palácios.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25222, 26, "Afinal, o que foram ver? Um profeta? Sim, eu lhes digo, e mais que profeta.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25223, 27, "Este é aquele a respeito de quem está escrito: ‘Enviarei o meu mensageiro à tua frente; ele preparará o teu caminho diante de ti’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25224, 28, "Eu lhes digo que entre os que nasceram de mulher não há ninguém maior do que João; todavia, o menor no Reino de Deus é maior do que ele"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25225, 29, "Todo o povo, até os publicanos, ouvindo as palavras de Jesus, reconheceram que o caminho de Deus era justo, sendo batizados por João.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25226, 30, "Mas os fariseus e os peritos na lei rejeitaram o propósito de Deus para eles, não sendo batizados por João.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25227, 31, """A que posso, pois, comparar os homens desta geração? "", prosseguiu Jesus. ""Com que se parecem?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25228, 32, "São como crianças que ficam sentadas na praça e gritam umas às outras: ‘Nós lhes tocamos flauta, mas vocês não dançaram; cantamos um lamento, mas vocês não choraram’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25229, 33, "Pois veio João Batista, que jejua e não bebe vinho, e vocês dizem: ‘Ele tem demônio’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25230, 34, "Veio o Filho do homem, comendo e bebendo, e vocês dizem: ‘Aí está um comilão e beberrão, amigo de publicanos e ""pecadores"" ’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25231, 35, "Mas a sabedoria é comprovada por todos os seus discípulos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25232, 36, "Convidado por um dos fariseus para jantar, Jesus foi à casa dele e reclinou-se à mesa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25233, 37, "Ao saber que Jesus estava comendo na casa do fariseu, certa mulher daquela cidade, uma ‘pecadora’, trouxe um frasco de alabastro com perfume,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25234, 38, "e se colocou atrás de Jesus, a seus pés. Chorando, começou a molhar-lhe os pés com as suas lágrimas. Depois os enxugou com seus cabelos, beijou-os e os ungiu com o perfume.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25235, 39, "Ao ver isso, o fariseu que o havia convidado disse a si mesmo: ""Se este homem fosse profeta, saberia quem nele está tocando e que tipo de mulher ela é: uma ‘pecadora’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25236, 40, "Respondeu-lhe Jesus: ""Simão, tenho algo a lhe dizer"". ""Dize, Mestre"", disse ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25237, 41, """Dois homens deviam a certo credor. Um lhe devia quinhentos denários e o outro, cinqüenta.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25238, 42, "Nenhum dos dois tinha com que lhe pagar, por isso perdoou a dívida a ambos. Qual deles o amará mais? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25239, 43, "Simão respondeu: ""Suponho que aquele a quem foi perdoada a dívida maior"". ""Você julgou bem"", disse Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25240, 44, "Em seguida, virou-se para a mulher e disse a Simão: ""Vê esta mulher? Entrei em sua casa, mas você não me deu água para lavar os pés; ela, porém, molhou os meus pés com as suas lágrimas e os enxugou com os seus cabelos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25241, 45, "Você não me saudou com um beijo, mas esta mulher, desde que entrei aqui, não parou de beijar os meus pés.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25242, 46, "Você não ungiu a minha cabeça com óleo, mas ela derramou perfume nos meus pés.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25243, 47, "Portanto, eu lhe digo, os muitos pecados dela lhe foram perdoados, pelo que ela amou muito. Mas aquele a quem pouco foi perdoado, pouco ama"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25244, 48, "Então Jesus disse a ela: ""Seus pecados estão perdoados"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25245, 49, "Os outros convidados começaram a perguntar: ""Quem é este que até perdoa pecados? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25246, 50, "Jesus disse à mulher: ""Sua fé a salvou; vá em paz"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25247, 1, "Depois disso Jesus ia passando pelas cidades e povoados proclamando as boas novas do Reino de Deus. Os Doze estavam com ele,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25248, 2, "e também algumas mulheres que haviam sido curadas de espíritos malignos e doenças: Maria, chamada Madalena, de quem haviam saído sete demônios;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25249, 3, "Joana, mulher de Cuza, administrador da casa de Herodes; Susana e muitas outras. Essas mulheres ajudavam a sustentá-los com os seus bens.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25250, 4, "Reunindo-se uma grande multidão e vindo a Jesus gente de várias cidades, ele contou esta parábola:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25251, 5, """O semeador saiu a semear. Enquanto lançava a semente, parte dela caiu à beira do caminho; foi pisada, e as aves do céu a comeram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25252, 6, "Parte dela caiu sobre pedras e, quando germinou, as plantas secaram, porque não havia umidade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25253, 7, "Outra parte caiu entre espinhos, que cresceram com ela e sufocaram as plantas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25254, 8, "Outra ainda caiu em boa terra. Cresceu e deu boa colheita, a cem por um"". Tendo dito isso, exclamou: ""Aquele que tem ouvidos para ouvir, ouça! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25255, 9, "Seus discípulos perguntaram-lhe o que significava aquela parábola.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25256, 10, "Ele disse: ""A vocês foi dado o conhecimento dos mistérios do Reino de Deus, mas aos outros falo por parábolas, para que ‘vendo, não vejam; e ouvindo, não entendam’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25257, 11, """Este é o significado da parábola: A semente é a palavra de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25258, 12, "As que caíram à beira do caminho são os que ouvem, e então vem o diabo e tira a palavra dos seus corações, para que não creiam e não sejam salvos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25259, 13, "As que caíram sobre as pedras são os que recebem a palavra com alegria quando a ouvem, mas não têm raiz. Crêem durante algum tempo, mas desistem na hora da provação.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25260, 14, "As que caíram entre espinhos são os que ouvem, mas, ao seguirem seu caminho, são sufocados pelas preocupações, pelas riquezas e pelos prazeres desta vida, e não amadurecem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25261, 15, "Mas as que caíram em boa terra são os que, com coração bom e generoso, ouvem a palavra, a retêm e dão fruto, com perseverança"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25262, 16, """Ninguém acende uma candeia e a esconde num jarro ou a coloca debaixo de uma cama. Pelo contrário, coloca-a num lugar apropriado, de modo que os que entram possam ver a luz.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25263, 17, "Porque não há nada oculto que não venha a ser revelado, e nada escondido que não venha a ser conhecido e trazido à luz.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25264, 18, "Portanto, considerem atentamente como vocês estão ouvindo. A quem tiver, mais lhe será dado; de quem não tiver, até o que pensa que tem lhe será tirado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25265, 19, "A mãe e os irmãos de Jesus foram vê-lo, mas não conseguiam aproximar-se dele, por causa da multidão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25266, 20, "Alguém lhe disse: ""Tua mãe e teus irmãos estão lá fora e querem ver-te"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25267, 21, "Ele lhe respondeu: ""Minha mãe e meus irmãos são aqueles que ouvem a palavra de Deus e a praticam"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25268, 22, "Certo dia Jesus disse aos seus discípulos: ""Vamos para o outro lado do lago"". Eles entraram num barco e partiram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25269, 23, "Enquanto navegavam, ele adormeceu. Abateu-se sobre o lago um forte vendaval, de modo que o barco estava sendo inundado, e eles corriam grande perigo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25270, 24, "Os discípulos foram acordá-lo, clamando: ""Mestre, Mestre, vamos morrer! "" Ele se levantou e repreendeu o vento e a violência das águas; tudo se acalmou e ficou tranqüilo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25271, 25, """Onde está a sua fé? "", perguntou ele aos seus discípulos. Amedrontados e admirados, eles perguntaram uns aos outros: ""Quem é este que até aos ventos e às águas dá ordens, e eles lhe obedecem? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25272, 26, "Navegaram para a região dos gerasenos que fica do outro lado do lago, frente à Galiléia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25273, 27, "Quando Jesus pisou em terra, foi ao encontro dele um endemoninhado daquela cidade. Fazia muito tempo que aquele homem não usava roupas, nem vivia em casa alguma, mas nos sepulcros.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25274, 28, "Quando viu Jesus, gritou, prostrou-se aos seus pés e disse em alta voz: ""Que queres comigo, Jesus, Filho do Deus Altíssimo? Rogo-te que não me atormentes! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25275, 29, "Pois Jesus havia ordenado que o espírito imundo saísse daquele homem. Muitas vezes ele tinha se apoderado dele. Mesmo com os pés e as mãos acorrentados e entregue aos cuidados de guardas, quebrava as correntes, e era levado pelo demônio a lugares solitários.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25276, 30, "Jesus lhe perguntou: ""Qual é o seu nome? "" ""Legião"", respondeu ele; porque muitos demônios haviam entrado nele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25277, 31, "E imploravam-lhe que não os mandasse para o abismo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25278, 32, "Uma grande manada de porcos estava pastando naquela colina. Os demônios imploraram a Jesus que lhes permitisse entrar neles, e Jesus lhes deu permissão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25279, 33, "Saindo do homem, os demônios entraram nos porcos, e toda a manada atirou-se precipício abaixo em direção ao lago e se afogou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25280, 34, "Vendo o que acontecera, os que cuidavam dos porcos fugiram e contaram esses fatos, na cidade e nos campos,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25281, 35, "e o povo foi ver o que havia acontecido. Quando se aproximaram de Jesus, viram que o homem de quem haviam saído os demônios estava assentado aos pés de Jesus, vestido e em perfeito juízo, e ficaram com medo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25282, 36, "Os que o tinham visto contaram ao povo como o endemoninhado fora curado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25283, 37, "Então, todo o povo da região dos gerasenos suplicou a Jesus que se retirasse, porque estavam dominados pelo medo. Ele entrou no barco e regressou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25284, 38, "O homem de quem haviam saído os demônios suplicava-lhe que o deixasse ir com ele; mas Jesus o mandou embora, dizendo:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25285, 39, """Volte para casa e conte o quanto Deus lhe fez"". Assim, o homem se foi e anunciou a toda a cidade o quanto Jesus tinha feito por ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25286, 40, "Quando Jesus voltou, uma multidão o recebeu, pois todos o esperavam.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25287, 41, "Então um homem chamado Jairo, dirigente da sinagoga, veio e prostrou-se aos pés de Jesus, implorando-lhe que fosse à sua casa", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25288, 42, "porque sua única filha, de cerca de doze anos, estava à morte. Estando Jesus a caminho, a multidão o comprimia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25289, 43, "E estava ali certa mulher que havia doze anos vinha sofrendo de uma hemorragia e gastara tudo o que tinha com os médicos; mas ninguém pudera curá-la.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25290, 44, "Ela chegou por trás dele, tocou na borda de seu manto, e imediatamente cessou sua hemorragia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25291, 45, """Quem tocou em mim? "", perguntou Jesus. Como todos negassem, Pedro disse: ""Mestre, a multidão se aglomera e te comprime"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25292, 46, "Mas Jesus disse: ""Alguém tocou em mim; eu sei que de mim saiu poder"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25293, 47, "Então a mulher, vendo que não conseguiria passar despercebida, veio tremendo e prostrou-se aos seus pés. Na presença de todo o povo contou por que tinha tocado nele e como fora instantaneamente curada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25294, 48, "Então ele lhe disse: ""Filha, a sua fé a curou! Vá em paz"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25295, 49, "Enquanto Jesus ainda estava falando, chegou alguém da casa de Jairo, o dirigente da sinagoga, e disse: ""Sua filha morreu. Não incomode mais o Mestre"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25296, 50, "Ouvindo isso, Jesus disse a Jairo: ""Não tenha medo; tão-somente creia, e ela será curada"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25297, 51, "Quando chegou à casa de Jairo, não deixou ninguém entrar com ele, exceto Pedro, João, Tiago e o pai e a mãe da criança.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25298, 52, "Enquanto isso, todo o povo estava se lamentando e chorando por ela. ""Não chorem"", disse Jesus. ""Ela não está morta, mas dorme"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25299, 53, "Todos começaram a rir dele, pois sabiam que ela estava morta.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25300, 54, "Mas ele a tomou pela mão e disse: ""Menina, levante-se! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25301, 55, "O espírito dela voltou, e ela se levantou imediatamente. Então Jesus lhes ordenou que lhe dessem de comer.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25302, 56, "Os pais dela ficaram maravilhados, mas ele lhes ordenou que não contassem a ninguém o que tinha acontecido.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25303, 1, "Reunindo os Doze, Jesus deu-lhes poder e autoridade para expulsar todos os demônios e curar doenças,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25304, 2, "e os enviou a pregar o Reino de Deus e a curar os enfermos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25305, 3, "E disse-lhes: ""Não levem nada pelo caminho: nem bordão, nem saco de viagem, nem pão, nem dinheiro, nem túnica extra.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25306, 4, "Na casa em que vocês entrarem, fiquem ali até partirem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25307, 5, "Se não os receberem, sacudam a poeira dos seus pés quando saírem daquela cidade, como testemunho contra eles"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25308, 6, "Assim, eles saíram e foram pelos povoados, pregando o evangelho e fazendo curas por toda parte.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25309, 7, "Herodes, o tetrarca, ouviu falar de tudo o que estava acontecendo e ficou perplexo, porque algumas pessoas estavam dizendo que João tinha ressuscitado dos mortos;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25310, 8, "outros, que Elias tinha aparecido; e ainda outros, que um dos profetas do passado tinha voltado à vida.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25311, 9, "Mas Herodes disse: ""João, eu decapitei! Quem, pois, é este de quem ouço essas coisas? "" E procurava vê-lo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25312, 10, "Ao voltarem, os apóstolos relataram a Jesus o que tinham feito. Então ele os tomou consigo, e retiraram-se para uma cidade chamada Betsaida;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25313, 11, "mas as multidões ficaram sabendo, e o seguiram. Ele as acolheu, e falava-lhes acerca do Reino de Deus, e curava os que precisavam de cura.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25314, 12, "Ao fim da tarde os Doze aproximaram-se dele e disseram: ""Manda embora a multidão para que eles possam ir aos campos vizinhos e aos povoados, e encontrem comida e pousada, porque aqui estamos em lugar deserto"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25315, 13, "Ele, porém, respondeu: ""Dêem-lhes vocês algo para comer"". Eles disseram: ""Temos apenas cinco pães e dois peixes — a menos que compremos alimento para toda esta multidão"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25316, 14, "( E estavam ali cerca de cinco mil homens ). Mas ele disse aos seus discípulos: ""Façam-nos sentar-se em grupos de cinqüenta"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25317, 15, "Os discípulos assim fizeram, e todos se assentaram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25318, 16, "Tomando os cinco pães e os dois peixes, e olhando para o céu, deu graças e os partiu. Em seguida, entregou-os aos discípulos para que os servissem ao povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25319, 17, "Todos comeram e ficaram satisfeitos, e os discípulos recolheram doze cestos cheios de pedaços que sobraram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25320, 18, "Certa vez Jesus estava orando em particular, e com ele estavam os seus discípulos; então lhes perguntou: ""Quem as multidões dizem que eu sou? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25321, 19, "Eles responderam: ""Alguns dizem que és João Batista; outros, Elias; e, ainda outros, que és um dos profetas do passado que ressuscitou"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25322, 20, """E vocês, o que dizem? "", perguntou. ""Quem vocês dizem que eu sou? "" Pedro respondeu: ""O Cristo de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25323, 21, "Jesus os advertiu severamente que não contassem isso a ninguém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25324, 22, "E disse: ""É necessário que o Filho do homem sofra muitas coisas e seja rejeitado pelos líderes religiosos, pelos chefes dos sacerdotes e pelos mestres da lei, seja morto e ressuscite no terceiro dia"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25325, 23, "Jesus dizia a todos: ""Se alguém quiser acompanhar-me, negue-se a si mesmo, tome diariamente a sua cruz e siga-me.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25326, 24, "Pois quem quiser salvar a sua vida a perderá; mas quem perder a vida por minha causa, este a salvará.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25327, 25, "Pois que adianta ao homem ganhar o mundo inteiro, e perder-se ou destruir a si mesmo?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25328, 26, "Se alguém se envergonhar de mim e das minhas palavras, o Filho do homem se envergonhará dele, quando vier em sua glória e na glória do Pai e dos santos anjos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25329, 27, "Garanto-lhes que alguns que aqui se acham de modo nenhum experimentarão a morte antes de verem o Reino de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25330, 28, "Aproximadamente oito dias depois de dizer essas coisas, Jesus tomou consigo a Pedro, João e Tiago e subiu a um monte para orar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25331, 29, "Enquanto orava, a aparência de seu rosto se transformou, e suas roupas ficaram alvas e resplandecentes como o brilho de um relâmpago.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25332, 30, "Surgiram dois homens que começaram a conversar com Jesus. Eram Moisés e Elias.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25333, 31, "Apareceram em glorioso esplendor, e falavam sobre a partida de Jesus, que estava para se cumprir em Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25334, 32, "Pedro e os seus companheiros estavam dominados pelo sono; acordando subitamente, viram a glória de Jesus e os dois homens que estavam com ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25335, 33, "Quando estes estavam se retirando, Pedro disse a Jesus: ""Mestre, é bom estarmos aqui. Façamos três tendas: uma para ti, uma para Moisés e uma para Elias"". ( Ele não sabia o que estava dizendo. )", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25336, 34, "Enquanto ele estava falando, uma nuvem apareceu e os envolveu, e eles ficaram com medo ao entrarem na nuvem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25337, 35, "Dela saiu uma voz que dizia: ""Este é o meu Filho, o Escolhido; ouçam a ele! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25338, 36, "Tendo-se ouvido a voz, Jesus ficou só. Os discípulos guardaram isto somente para si; naqueles dias, não contaram a ninguém nada do que tinham visto.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25339, 37, "No dia seguinte, quando desceram do monte, uma grande multidão veio ao encontro dele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25340, 38, "Um homem da multidão bradou: ""Mestre, rogo-te que dês atenção ao meu filho, pois é o único que tenho.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25341, 39, "Um espírito o domina; de repente ele grita; lança-o em convulsões e o faz espumar; quase nunca o abandona, e o está destruindo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25342, 40, "Roguei aos teus discípulos que o expulsassem, mas eles não conseguiram"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25343, 41, "Respondeu Jesus: ""Ó geração incrédula e perversa, até quando estarei com vocês e terei que suportá-los? Traga-me aqui o seu filho"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25344, 42, "Quando o menino vinha vindo, o demônio o lançou por terra, em convulsão. Mas Jesus repreendeu o espírito imundo, curou o menino e o entregou de volta a seu pai.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25345, 43, "E todos ficaram atônitos ante a grandeza de Deus. Estando todos maravilhados com tudo o que Jesus fazia, ele disse aos seus discípulos:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25346, 44, """Ouçam atentamente o que vou lhes dizer: o Filho do homem será traído e entregue nas mãos dos homens"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25347, 45, "Mas eles não entendiam o que isso significava; era-lhes encoberto, para que não o entendessem. E tinham receio de perguntar-lhe a respeito dessa palavra.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25348, 46, "Começou uma discussão entre os discípulos, acerca de qual deles seria o maior.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25349, 47, "Jesus, conhecendo os seus pensamentos, tomou uma criança e a colocou em pé, a seu lado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25350, 48, "Então lhes disse: ""Quem recebe esta criança em meu nome, está me recebendo; e quem me recebe, está recebendo aquele que me enviou. Pois aquele que entre vocês for o menor, este será o maior"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25351, 49, "Disse João: ""Mestre, vimos um homem expulsando demônios em teu nome e procuramos impedi-lo, porque ele não era um dos nossos. """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25352, 50, """Não o impeçam"", disse Jesus, ""pois quem não é contra vocês, é a favor de vocês"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25353, 51, "Aproximando-se o tempo em que seria elevado ao céu, Jesus partiu resolutamente em direção a Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25354, 52, "E enviou mensageiros à sua frente. Indo estes, entraram num povoado samaritano para lhe fazer os preparativos;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25355, 53, "mas o povo dali não o recebeu porque se notava em seu semblante que ele ia para Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25356, 54, "Ao verem isso, os discípulos Tiago e João perguntaram: ""Senhor, queres que façamos cair fogo do céu para destruí-los? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25357, 55, "Mas Jesus, voltando-se, os repreendeu, dizendo: ""Vocês não sabem de que espécie de espírito são, pois o Filho do homem não veio para destruir a vida dos homens, mas para salvá-los"";", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25358, 56, "e foram para outro povoado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25359, 57, "Quando andavam pelo caminho, um homem lhe disse: ""Eu te seguirei por onde quer que fores"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25360, 58, "Jesus respondeu: ""As raposas têm suas tocas e as aves do céu têm seus ninhos, mas o Filho do homem não tem onde repousar a cabeça"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25361, 59, "A outro disse: ""Siga-me"". Mas o homem respondeu: ""Senhor, deixa-me ir primeiro sepultar meu pai"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25362, 60, "Jesus lhe disse: ""Deixe que os mortos sepultem os seus próprios mortos; você, porém, vá e proclame o Reino de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25363, 61, "Ainda outro disse: ""Vou seguir-te, Senhor, mas deixa-me primeiro voltar e me despedir da minha família"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25364, 62, "Jesus respondeu: ""Ninguém que põe a mão no arado e olha para trás é apto para o Reino de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25365, 1, "Depois disso o Senhor designou outros setenta e dois e os enviou dois a dois, adiante dele, a todas as cidades e lugares para onde ele estava prestes a ir.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25366, 2, "E lhes disse: ""A colheita é grande, mas os trabalhadores são poucos. Portanto, peçam ao Senhor da colheita que mande trabalhadores para a sua colheita.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25367, 3, "Vão! Eu os estou enviando como cordeiros entre lobos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25368, 4, "Não levem bolsa nem saco de viagem nem sandálias; e não saúdem ninguém pelo caminho.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25369, 5, """Quando entrarem numa casa, digam primeiro: ‘Paz a esta casa’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25370, 6, "Se houver ali um homem de paz, a paz de vocês repousará sobre ele; se não, ela voltará para vocês.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25371, 7, "Fiquem naquela casa, e comam e bebam o que lhes derem, pois o trabalhador merece o seu salário. Não fiquem mudando de casa em casa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25372, 8, """Quando entrarem numa cidade e forem bem recebidos, comam o que for posto diante de vocês.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25373, 9, "Curem os doentes que ali houver e digam-lhes: ‘O Reino de Deus está próximo de vocês’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25374, 10, "Mas quando entrarem numa cidade e não forem bem recebidos, saiam por suas ruas e digam:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25375, 11, "‘Até o pó da sua cidade, que se apegou aos nossos pés, sacudimos contra vocês. Fiquem certos disto: O Reino de Deus está próximo’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25376, 12, "Eu lhes digo: Naquele dia haverá mais tolerância para Sodoma do que para aquela cidade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25377, 13, """Ai de você, Corazim! Ai de você, Betsaida! Porque se os milagres que foram realizados entre vocês o fossem em Tiro e Sidom, há muito tempo elas se teriam arrependido, vestindo roupas de saco e cobrindo-se de cinzas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25378, 14, "Mas no juízo haverá menor rigor para Tiro e Sidom do que para vocês.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25379, 15, "E você, Cafarnaum: será elevada até o céu? Não; você descerá até ao Hades!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25380, 16, """Aquele que lhes dá ouvidos, está me dando ouvidos; aquele que os rejeita, está me rejeitando; mas aquele que me rejeita, está rejeitando aquele que me enviou"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25381, 17, "Os setenta e dois voltaram alegres e disseram: ""Senhor, até os demônios se submetem a nós, em teu nome"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25382, 18, "Ele respondeu: ""Eu vi Satanás caindo do céu como relâmpago.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25383, 19, "Eu lhes dei autoridade para pisarem sobre cobras e escorpiões, e sobre todo o poder do inimigo; nada lhes fará dano.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25384, 20, "Contudo, alegrem-se, não porque os espíritos se submetem a vocês, mas porque seus nomes estão escritos nos céus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25385, 21, "Naquela hora Jesus, exultando no Espírito Santo, disse: ""Eu te louvo, Pai, Senhor do céu e da terra, porque escondeste estas coisas dos sábios e cultos e as revelaste aos pequeninos. Sim, Pai, pois assim foi do teu agrado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25386, 22, """Todas as coisas me foram entregues por meu Pai. Ninguém sabe quem é o Filho, a não ser o Pai; e ninguém sabe quem é o Pai, a não ser o Filho e aqueles a quem o Filho o quiser revelar"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25387, 23, "Então ele se voltou para os seus discípulos e lhes disse em particular: ""Felizes são os olhos que vêem o que vocês vêem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25388, 24, "Pois eu lhes digo que muitos profetas e reis desejaram ver o que vocês estão vendo, mas não viram; e ouvir o que vocês estão ouvindo, mas não ouviram"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25389, 25, "Certa ocasião, um perito na lei levantou-se para pôr Jesus à prova e lhe perguntou: ""Mestre, o que preciso fazer para herdar a vida eterna? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25390, 26, """O que está escrito na Lei? "", respondeu Jesus. ""Como você a lê? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25391, 27, "Ele respondeu: "" ‘Ame o Senhor, o seu Deus de todo o seu coração, de toda a sua alma, de todas as suas forças e de todo o seu entendimento’ e ‘Ame o seu próximo como a si mesmo’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25392, 28, "Disse Jesus: ""Você respondeu corretamente. Faça isso, e viverá"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25393, 29, "Mas ele, querendo justificar-se, perguntou a Jesus: ""E quem é o meu próximo? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25394, 30, "Em resposta, disse Jesus: ""Um homem descia de Jerusalém para Jericó, quando caiu nas mãos de assaltantes. Estes lhe tiraram as roupas, espancaram-no e se foram, deixando-o quase morto.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25395, 31, "Aconteceu estar descendo pela mesma estrada um sacerdote. Quando viu o homem, passou pelo outro lado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25396, 32, "E assim também um levita; quando chegou ao lugar e o viu, passou pelo outro lado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25397, 33, "Mas um samaritano, estando de viagem, chegou onde se encontrava o homem e, quando o viu, teve piedade dele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25398, 34, "Aproximou-se, enfaixou-lhe as feridas, derramando nelas vinho e óleo. Depois colocou-o sobre o seu próprio animal, levou-o para uma hospedaria e cuidou dele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25399, 35, "No dia seguinte, deu dois denários ao hospedeiro e disse-lhe: ‘Cuide dele. Quando voltar lhe pagarei todas as despesas que você tiver’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25400, 36, """Qual destes três você acha que foi o próximo do homem que caiu nas mãos dos assaltantes? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25401, 37, """Aquele que teve misericórdia dele"", respondeu o perito na lei. Jesus lhe disse: ""Vá e faça o mesmo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25402, 38, "Caminhando Jesus e os seus discípulos, chegaram a um povoado, onde certa mulher chamada Marta o recebeu em sua casa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25403, 39, "Maria, sua irmã, ficou sentada aos pés do Senhor, ouvindo-lhe a palavra.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25404, 40, "Marta, porém, estava ocupada com muito serviço. E, aproximando-se dele, perguntou: ""Senhor, não te importas que minha irmã tenha me deixado sozinha com o serviço? Dize-lhe que me ajude! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25405, 41, "Respondeu o Senhor: ""Marta! Marta! Você está preocupada e inquieta com muitas coisas;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25406, 42, "todavia apenas uma é necessária. Maria escolheu a boa parte, e esta não lhe será tirada"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25407, 1, "Certo dia Jesus estava orando em determinado lugar. Tendo terminado, um dos seus discípulos lhe disse: ""Senhor, ensina-nos a orar, como João ensinou aos discípulos dele"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25408, 2, "Ele lhes disse: ""Quando vocês orarem, digam: ‘Pai! Santificado seja o teu nome. Venha o teu Reino.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25409, 3, "Dá-nos cada dia o nosso pão cotidiano.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25410, 4, "Perdoa-nos os nossos pecados, pois também perdoamos a todos os que nos devem. E não nos deixes cair em tentação’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25411, 5, "Então lhes disse: ""Suponham que um de vocês tenha um amigo e que recorra a ele à meia-noite e diga: ‘Amigo, empreste-me três pães,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25412, 6, "porque um amigo meu chegou de viagem, e não tenho nada para lhe oferecer’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25413, 7, """E o que estiver dentro responda: ‘Não me incomode. A porta já está fechada, e meus filhos estão deitados comigo. Não posso me levantar e lhe dar o que me pede’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25414, 8, "Eu lhes digo: embora ele não se levante para dar-lhe o pão por ser seu amigo, por causa da importunação se levantará e lhe dará tudo o que precisar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25415, 9, """Por isso lhes digo: Peçam, e lhes será dado; busquem, e encontrarão; batam, e a porta lhes será aberta.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25416, 10, "Pois todo o que pede, recebe; o que busca, encontra; e àquele que bate, a porta será aberta.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25417, 11, """Qual pai, entre vocês, se o filho lhe pedir um peixe, em lugar disso lhe dará uma cobra?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25418, 12, "Ou se pedir um ovo, lhe dará um escorpião?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25419, 13, "Se vocês, apesar de serem maus, sabem dar boas coisas aos seus filhos, quanto mais o Pai que está no céu dará o Espírito Santo a quem o pedir! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25420, 14, "Jesus estava expulsando um demônio que era mudo. Quando o demônio saiu, o mudo falou, e a multidão ficou admirada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25421, 15, "Mas alguns deles disseram: ""É por Belzebu, o príncipe dos demônios, que ele expulsa demônios"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25422, 16, "Outros o punham à prova, pedindo-lhe um sinal do céu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25423, 17, "Jesus, conhecendo os seus pensamentos, disse-lhes: ""Todo reino dividido contra si mesmo será arruinado, e uma casa dividida contra si mesma cairá.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25424, 18, "Se Satanás está dividido contra si mesmo, como o seu reino pode subsistir? Digo isso porque vocês estão dizendo que expulso demônios por Belzebu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25425, 19, "Se eu expulso demônios por Belzebu, por quem os expulsam os filhos de vocês? Por isso, eles mesmos estarão como juízes sobre vocês.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25426, 20, "Mas se é pelo dedo de Deus que eu expulso demônios, então chegou a vocês o Reino de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25427, 21, """Quando um homem forte, bem armado, guarda sua casa, seus bens estão seguros.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25428, 22, "Mas quando alguém mais forte o ataca e vence, tira-lhe a armadura em que confiava e divide os despojos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25429, 23, """Aquele que não está comigo é contra mim, e aquele que comigo não ajunta, espalha.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25430, 24, """Quando um espírito imundo sai de um homem, passa por lugares áridos procurando descanso, e não o encontrando, diz: ‘Voltarei para a casa de onde saí’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25431, 25, "Quando chega, encontra a casa varrida e em ordem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25432, 26, "Então vai e traz outros sete espíritos piores do que ele, e entrando passam a viver ali. E o estado final daquele homem torna-se pior do que o primeiro"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25433, 27, "Quando Jesus dizia estas coisas, uma mulher da multidão exclamou: ""Feliz é a mulher que te deu à luz e te amamentou"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25434, 28, "Ele respondeu: ""Antes, felizes são aqueles que ouvem a palavra de Deus e lhe obedecem"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25435, 29, "Aumentando a multidão, Jesus começou a dizer: ""Esta é uma geração perversa. Ela pede um sinal miraculoso, mas nenhum sinal lhe será dado, exceto o sinal de Jonas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25436, 30, "Pois assim como Jonas foi um sinal para os ninivitas, o Filho do homem também o será para esta geração.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25437, 31, "A rainha do Sul se levantará no juízo com os homens desta geração e os condenará, pois ela veio dos confins da terra para ouvir a sabedoria de Salomão, e agora está aqui o que é maior do que Salomão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25438, 32, "Os homens de Nínive se levantarão no juízo com esta geração e a condenarão; pois eles se arrependeram com a pregação de Jonas, e agora está aqui o que é maior do que Jonas"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25439, 33, """Ninguém acende uma candeia e a coloca em lugar onde fique escondida ou debaixo de uma vasilha. Pelo contrário, coloca-a no lugar apropriado, para que os que entram possam ver a luz.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25440, 34, "Os olhos são a candeia do corpo. Quando os seus olhos forem bons, igualmente todo o seu corpo estará cheio de luz. Mas quando forem maus, igualmente o seu corpo estará cheio de trevas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25441, 35, "Portanto, cuidado para que a luz que está em seu interior não sejam trevas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25442, 36, "Logo, se todo o seu corpo estiver cheio de luz, e nenhuma parte dele estiver em trevas, estará completamente iluminado, como quando a luz de uma candeia brilha sobre você"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25443, 37, "Tendo terminado de falar, um fariseu o convidou para comer com ele. Então Jesus foi, e reclinou-se à mesa;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25444, 38, "mas o fariseu, notando que Jesus não se lavara cerimonialmente antes da refeição, ficou surpreso.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25445, 39, "Então o Senhor lhe disse: ""Vocês, fariseus, limpam o exterior do copo e do prato, mas interiormente estão cheios de ganância e da maldade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25446, 40, "Insensatos! Quem fez o exterior não fez também o interior?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25447, 41, "Mas dêem o que está dentro do prato como esmola, e verão que tudo lhes ficará limpo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25448, 42, """Ai de vocês, fariseus, porque dão a Deus o dízimo da hortelã, da arruda e de toda a sorte de hortaliças, mas desprezam a justiça e o amor de Deus! Vocês deviam praticar estas coisas, sem deixar de fazer aquelas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25449, 43, """Ai de vocês, fariseus, porque amam os lugares de honra nas sinagogas e as saudações em público!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25450, 44, """Ai de vocês, porque são como túmulos que não se vêem, por sobre os quais os homens andam sem o saber! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25451, 45, "Um dos peritos na lei lhe respondeu: ""Mestre, quando dizes essas coisas, insultas também a nós"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25452, 46, """Quanto a vocês, peritos na lei"", disse Jesus, ""ai de vocês também! porque sobrecarregam os homens com fardos que dificilmente eles podem carregar, e vocês mesmos não levantam nem um dedo para ajudá-los.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25453, 47, """Ai de vocês, porque edificam os túmulos dos profetas, sendo que foram os seus próprios antepassados que os mataram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25454, 48, "Assim vocês dão testemunho de que aprovam o que os seus antepassados fizeram. Eles mataram os profetas, e vocês lhes edificam os túmulos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25455, 49, "Por isso, Deus disse em sua sabedoria: ‘Eu lhes mandarei profetas e apóstolos, dos quais eles matarão alguns, e a outros perseguirão’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25456, 50, "Pelo que, esta geração será considerada responsável pelo sangue de todos os profetas, derramado desde o princípio do mundo:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25457, 51, "desde o sangue de Abel até o sangue de Zacarias, que foi morto entre o altar e o santuário. Sim, eu lhes digo, esta geração será considerada responsável por tudo isso.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25458, 52, """Ai de vocês, peritos na lei, porque se apoderaram da chave do conhecimento. Vocês mesmos não entraram e impediram os que estavam prestes a entrar! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25459, 53, "Quando Jesus saiu dali, os fariseus e os mestres da lei começaram a opor-se fortemente a ele e a interrogá-lo com muitas perguntas,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25460, 54, "esperando apanhá-lo em algo que dissesse.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25461, 1, "Nesse meio tempo, tendo-se juntado uma multidão de milhares de pessoas, a ponto de se atropelarem umas às outras, Jesus começou a falar primeiramente aos seus discípulos, dizendo: ""Tenham cuidado com o fermento dos fariseus, que é a hipocrisia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25462, 2, "Não há nada escondido que não venha a ser descoberto, ou oculto que não venha a ser conhecido.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25463, 3, "O que vocês disseram nas trevas será ouvido à luz do dia, e o que vocês sussurraram aos ouvidos dentro de casa, será proclamado dos telhados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25464, 4, """Eu lhes digo, meus amigos: não tenham medo dos que matam o corpo e depois nada mais podem fazer.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25465, 5, "Mas eu lhes mostrarei a quem vocês devem temer: temam aquele que, depois de matar o corpo, tem poder para lançar no inferno. Sim, eu lhes digo, esse vocês devem temer.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25466, 6, "Não se vendem cinco pardais por duas moedinhas? Contudo, nenhum deles é esquecido por Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25467, 7, "Até os cabelos da cabeça de vocês estão todos contados. Não tenham medo; vocês valem mais do que muitos pardais!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25468, 8, """Eu lhes digo: quem me confessar diante dos homens, também o Filho do homem o confessará diante dos anjos de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25469, 9, "Mas aquele que me negar diante dos homens será negado diante dos anjos de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25470, 10, "Todo aquele que disser uma palavra contra o Filho do homem será perdoado, mas quem blasfemar contra o Espírito Santo não será perdoado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25471, 11, """Quando vocês forem levados às sinagogas e diante dos governantes e das autoridades, não se preocupem com a forma pela qual se defenderão, ou com o que dirão,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25472, 12, "pois naquela hora o Espírito Santo lhes ensinará o que devem dizer"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25473, 13, "Alguém da multidão lhe disse: ""Mestre, dize a meu irmão que divida a herança comigo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25474, 14, "Respondeu Jesus: ""Homem, quem me designou juiz ou árbitro entre vocês? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25475, 15, "Então lhes disse: ""Cuidado! Fiquem de sobreaviso contra todo tipo de ganância; a vida de um homem não consiste na quantidade dos seus bens"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25476, 16, "Então lhes contou esta parábola: ""A terra de certo homem rico produziu muito bem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25477, 17, "Ele pensou consigo mesmo: ‘O que vou fazer? Não tenho onde armazenar minha colheita’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25478, 18, """Então disse: ‘Já sei o que vou fazer. Vou derrubar os meus celeiros e construir outros maiores, e ali guardarei toda a minha safra e todos os meus bens.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25479, 19, "E direi a mim mesmo: Você tem grande quantidade de bens, armazenados para muitos anos. Descanse, coma, beba e alegre-se’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25480, 20, """Contudo, Deus lhe disse: ‘Insensato! Esta mesma noite a sua vida lhe será exigida. Então, quem ficará com o que você preparou? ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25481, 21, """Assim acontece com quem guarda para si riquezas, mas não é rico para com Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25482, 22, "Dirigindo-se aos seus discípulos, Jesus acrescentou: ""Portanto eu lhes digo: não se preocupem com suas próprias vidas, quanto ao que comer; nem com seus próprios corpos, quanto ao que vestir.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25483, 23, "A vida é mais importante do que a comida, e o corpo, mais do que as roupas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25484, 24, "Observem os corvos: não semeiam nem colhem, não têm armazéns nem celeiros; contudo, Deus os alimenta. E vocês têm muito mais valor do que as aves!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25485, 25, "Quem de vocês, por mais que se preocupe, pode acrescentar uma hora que seja à sua vida?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25486, 26, "Visto que vocês não podem sequer fazer uma coisa tão pequena, por que se preocupar com o restante?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25487, 27, """Observem como crescem os lírios. Eles não trabalham nem tecem. Contudo, eu lhes digo que nem Salomão, em todo o seu esplendor, vestiu-se como um deles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25488, 28, "Se Deus veste assim a erva do campo, que hoje existe e amanhã é lançada ao fogo, quanto mais vestirá vocês, homens de pequena fé!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25489, 29, "Não busquem ansiosamente o que hão de comer ou beber; não se preocupem com isso.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25490, 30, "Pois o mundo pagão é que corre atrás dessas coisas; mas o Pai sabe que vocês precisam delas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25491, 31, "Busquem, pois, o Reino de Deus, e essas coisas lhes serão acrescentadas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25492, 32, """Não tenham medo, pequeno rebanho, pois foi do agrado do Pai dar-lhes o Reino.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25493, 33, "Vendam o que têm e dêem esmolas. Façam para vocês bolsas que não se gastem com o tempo, um tesouro nos céus que não se acabe, onde ladrão algum chega perto e nenhuma traça destrói.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25494, 34, "Pois onde estiver o seu tesouro, ali também estará o seu coração"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25495, 35, """Estejam prontos para servir, e conservem acesas as suas candeias,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25496, 36, "como aqueles que esperam seu senhor voltar de um banquete de casamento; para que, quando ele chegar e bater, possam abrir-lhe a porta imediatamente.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25497, 37, "Felizes os servos cujo senhor os encontrar vigiando, quando voltar. Eu lhes afirmo que ele se vestirá para servir, fará que se reclinem à mesa, e virá servi-los.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25498, 38, "Mesmo que ele chegue de noite ou de madrugada, felizes os servos que o senhor encontrar preparados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25499, 39, "Entendam, porém, isto: se o dono da casa soubesse a que hora viria o ladrão, não permitiria que a sua casa fosse arrombada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25500, 40, "Estejam também vocês preparados, porque o Filho do homem virá numa hora em que não o esperam"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25501, 41, "Pedro perguntou: ""Senhor, estás contando esta parábola para nós ou para todos? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25502, 42, "O Senhor respondeu: ""Quem é, pois, o administrador fiel e sensato, a quem seu senhor encarrega dos seus servos, para lhes dar sua porção de alimento no tempo devido?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25503, 43, "Feliz o servo a quem o seu senhor encontrar fazendo assim quando voltar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25504, 44, "Garanto-lhes que ele o encarregará de todos os seus bens.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25505, 45, "Mas suponham que esse servo diga a si mesmo: ‘Meu senhor se demora a voltar’, e então comece a bater nos servos e nas servas, a comer, a beber e a embriagar-se.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25506, 46, "O senhor daquele servo virá num dia em que ele não o espera e numa hora que não sabe, e o punirá severamente e lhe dará um lugar com os infiéis.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25507, 47, """Aquele servo que conhece a vontade de seu senhor e não prepara o que ele deseja, nem o realiza, receberá muitos açoites.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25508, 48, "Mas aquele que não a conhece e pratica coisas merecedoras de castigo, receberá poucos açoites. A quem muito foi dado, muito será exigido; e a quem muito foi confiado, muito mais será pedido"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25509, 49, """Vim trazer fogo à terra, e como gostaria que já estivesse aceso!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25510, 50, "Mas tenho que passar por um batismo, e como estou angustiado até que ele se realize!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25511, 51, "Vocês pensam que vim trazer paz à terra? Não, eu lhes digo. Pelo contrário, vim trazer divisão!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25512, 52, "De agora em diante haverá cinco numa família divididos uns contra os outros: três contra dois e dois contra três.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25513, 53, "Estarão divididos pai contra filho e filho contra pai, mãe contra filha e filha contra mãe, sogra contra nora e nora contra sogra"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25514, 54, "Dizia ele à multidão: ""Quando vocês vêem uma nuvem se levantando no ocidente, logo dizem: ‘Vai chover’, e assim acontece.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25515, 55, "E quando sopra o vento sul, vocês dizem: ‘Vai fazer calor’, e assim ocorre.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25516, 56, "Hipócritas! Vocês sabem interpretar o aspecto da terra e do céu. Como não sabem interpretar o tempo presente?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25517, 57, """Por que vocês não julgam por si mesmos o que é justo?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25518, 58, "Quando algum de vocês estiver indo com seu adversário para o magistrado, faça tudo para se reconciliar com ele no caminho; para que ele não o arraste ao juiz, o juiz o entregue ao oficial de justiça, e o oficial de justiça o jogue na prisão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25519, 59, "Eu lhe digo que você não sairá de lá enquanto não pagar o último centavo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25520, 1, "Naquela ocasião, alguns dos que estavam presentes contaram a Jesus que Pilatos misturara o sangue de alguns galileus com os sacrifícios deles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25521, 2, "Jesus respondeu: ""Vocês pensam que esses galileus eram mais pecadores que todos os outros, por terem sofrido dessa maneira?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25522, 3, "Eu lhes digo que não! Mas se não se arrependerem, todos vocês também perecerão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25523, 4, "Ou vocês pensam que aqueles dezoito que morreram, quando caiu sobre eles a torre de Siloé, eram mais culpados do que todos os outros habitantes de Jerusalém?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25524, 5, "Eu lhes digo que não! Mas se não se arrependerem, todos vocês também perecerão"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25525, 6, "Então contou esta parábola: ""Um homem tinha uma figueira plantada em sua vinha. Foi procurar fruto nela, e não achou nenhum.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25526, 7, "Por isso disse ao que cuidava da vinha: ‘Já faz três anos que venho procurar fruto nesta figueira e não acho. Corte-a! Por que deixá-la inutilizar a terra? ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25527, 8, """Respondeu o homem: ‘Senhor, deixe-a por mais um ano, e eu cavarei ao redor dela e a adubarei.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25528, 9, "Se der fruto no ano que vem, muito bem! Se não, corte-a’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25529, 10, "Certo sábado Jesus estava ensinando numa das sinagogas,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25530, 11, "e ali estava uma mulher que tinha um espírito que a mantinha doente havia dezoito anos. Ela andava encurvada e de forma alguma podia endireitar-se.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25531, 12, "Ao vê-la, Jesus chamou-a à frente e lhe disse: ""Mulher, você está livre da sua doença"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25532, 13, "Então lhe impôs as mãos; e imediatamente ela se endireitou, e louvava a Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25533, 14, "Indignado porque Jesus havia curado no sábado, o dirigente da sinagoga disse ao povo: ""Há seis dias em que se deve trabalhar. Venham para ser curados nesses dias, e não no sábado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25534, 15, "O Senhor lhe respondeu: ""Hipócritas! Cada um de vocês não desamarra no sábado o seu boi ou jumento do estábulo e o leva dali para dar-lhe água?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25535, 16, "Então, esta mulher, uma filha de Abraão a quem Satanás mantinha presa por dezoito longos anos, não deveria no dia de sábado ser libertada daquilo que a prendia? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25536, 17, "Tendo dito isso, todos os seus oponentes ficaram envergonhados, mas o povo se alegrava com todas as maravilhas que ele estava fazendo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25537, 18, "Então Jesus perguntou: ""Com que se parece o Reino de Deus? Com que o compararei?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25538, 19, "É como um grão de mostarda que um homem semeou em sua horta. Ele cresceu e se tornou uma árvore, e as aves do céu se fizaram ninhos em seus ramos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25539, 20, "Mais uma vez ele perguntou: ""A que compararei o Reino de Deus?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25540, 21, "É como o fermento que uma mulher misturou com uma grande quantidade de farinha, e toda a massa ficou fermentada"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25541, 22, "Depois Jesus foi pelas cidades e povoados e ensinava, prosseguindo em direção a Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25542, 23, "Alguém lhe perguntou: ""Senhor, serão poucos os salvos? "" Ele lhes disse:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25543, 24, """Esforcem-se para entrar pela porta estreita, porque eu lhes digo que muitos tentarão entrar e não conseguirão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25544, 25, "Quando o dono da casa se levantar e fechar a porta, vocês ficarão do lado de fora, batendo e pedindo: ‘Senhor, abre-nos a porta’. ""Ele, porém, responderá: ‘Não os conheço, nem sei de onde são vocês’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25545, 26, """Então vocês dirão: ‘Comemos e bebemos contigo, e ensinaste em nossas ruas’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25546, 27, """Mas ele responderá: ‘Não os conheço, nem sei de onde são vocês. Afastem-se de mim, todos vocês, que praticam o mal! ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25547, 28, """Ali haverá choro e ranger de dentes, quando vocês virem Abraão, Isaque e Jacó e todos os profetas no Reino de Deus, mas vocês excluídos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25548, 29, "Pessoas virão do oriente e do ocidente, do norte e do sul, e ocuparão os seus lugares à mesa no Reino de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25549, 30, "De fato, há últimos que serão primeiros, e primeiros que serão últimos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25550, 31, "Naquela mesma hora alguns fariseus aproximaram-se de Jesus e lhe disseram: ""Saia e vá embora daqui, pois Herodes quer matá-lo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25551, 32, "Ele respondeu: ""Vão dizer àquela raposa: ‘Expulsarei demônios e curarei o povo hoje e amanhã, e no terceiro dia estarei pronto’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25552, 33, "Mas, preciso prosseguir hoje, amanhã e depois de amanhã, pois certamente nenhum profeta deve morrer fora de Jerusalém!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25553, 34, """Jerusalém, Jerusalém, você, que mata os profetas e apedrejas os que lhe são enviados! Quantas vezes eu quis reunir os seus filhos, como a galinha reúne os seus pintinhos debaixo das suas asas, mas vocês não quiseram!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25554, 35, "Eis que a casa de vocês ficará deserta. Eu lhes digo que vocês não me verão mais até que digam: ‘Bendito o que vem em nome do Senhor’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25555, 1, "Certo sábado, entrando Jesus para comer na casa de um fariseu importante, observavam-no atentamente.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25556, 2, "À frente dele estava um homem doente, com o corpo inchado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25557, 3, "Jesus perguntou aos fariseus e peritos na lei: ""É permitido ou não curar no sábado? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25558, 4, "Mas eles ficaram em silêncio. Assim, tomando o homem pela mão, Jesus o curou e o mandou embora.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25559, 5, "Então ele lhes perguntou: ""Se um de vocês tiver um filho ou um boi, e este cair num poço no dia de sábado, não irá tirá-lo imediatamente? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25560, 6, "E eles nada puderam responder.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25561, 7, "Quando notou como os convidados escolhiam os lugares de honra à mesa, Jesus lhes contou esta parábola:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25562, 8, """Quando alguém o convidar para um banquete de casamento, não ocupe o lugar de honra, pois pode ser que tenha sido convidado alguém de maior honra do que você.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25563, 9, "Se for assim, aquele que convidou os dois virá e lhe dirá: ‘Dê o lugar a este’. Então, humilhado, você precisará ocupar o lugar menos importante.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25564, 10, "Mas quando você for convidado, ocupe o lugar menos importante, de forma que, quando vier aquele que o convidou, diga-lhe: ‘Amigo, passe para um lugar mais importante’. Então você será honrado na presença de todos os convidados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25565, 11, "Pois todo o que se exalta será humilhado, e o que se humilha será exaltado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25566, 12, "Então Jesus disse ao que o tinha convidado: ""Quando você der um banquete ou jantar, não convide seus amigos, irmãos ou parentes, nem seus vizinhos ricos; se o fizer, eles poderão também, por sua vez, convidá-lo, e assim você será recompensado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25567, 13, "Mas, quando der um banquete, convide os pobres, os aleijados, os mancos, e os cegos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25568, 14, "Feliz será você, porque estes não têm como retribuir. A sua recompensa virá na ressurreição dos justos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25569, 15, "Ao ouvir isso, um dos que estavam à mesa com Jesus, disse-lhe: ""Feliz será aquele que comer no banquete do Reino de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25570, 16, "Jesus respondeu: ""Certo homem estava preparando um grande banquete e convidou muitas pessoas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25571, 17, "Na hora de começar, enviou seu servo para dizer aos que haviam sido convidados: ‘Venham, pois tudo já está pronto’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25572, 18, """Mas eles começaram, um por um, a apresentar desculpas. O primeiro disse: ‘Acabei de comprar uma propriedade, e preciso ir vê-la. Por favor, desculpe-me’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25573, 19, """Outro disse: ‘Acabei de comprar cinco juntas de bois e estou indo experimentá-las. Por favor, desculpe-me’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25574, 20, """Ainda outro disse: ‘Acabo de me casar, por isso não posso ir’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25575, 21, """O servo voltou e relatou isso ao seu senhor. Então o dono da casa irou-se e ordenou ao seu servo: ‘Vá rapidamente para as ruas e becos da cidade e traga os pobres, os aleijados, os cegos e os mancos’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25576, 22, """Disse o servo: ‘O que o senhor ordenou foi feito, e ainda há lugar’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25577, 23, """Então o senhor disse ao servo: ‘Vá pelos caminhos e valados e obrigue-os a entrar, para que a minha casa fique cheia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25578, 24, "Eu lhes digo: nenhum daqueles que foram convidados provará do meu banquete’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25579, 25, "Uma grande multidão ia acompanhando Jesus; este, voltando-se para ela, disse:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25580, 26, """Se alguém vem a mim e ama o seu pai, sua mãe, sua mulher, seus filhos, seus irmãos e irmãs, e até sua própria vida mais do que a mim, não pode ser meu discípulo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25581, 27, "E aquele que não carrega sua cruz e não me segue não pode ser meu discípulo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25582, 28, """Qual de vocês, se quiser construir uma torre, primeiro não se assenta e calcula o preço, para ver se tem dinheiro suficiente para completá-la?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25583, 29, "Pois, se lançar o alicerce e não for capaz de terminá-la, todos os que a virem rirão dele,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25584, 30, "dizendo: ‘Este homem começou a construir e não foi capaz de terminar’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25585, 31, """Ou, qual é o rei que, pretendendo sair à guerra contra outro rei, primeiro não se assenta e pensa se com dez mil homens é capaz de enfrentar aquele que vem contra ele com vinte mil?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25586, 32, "Se não for capaz, enviará uma delegação, enquanto o outro ainda está longe, e pedirá um acordo de paz.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25587, 33, "Da mesma forma, qualquer de vocês que não renunciar a tudo o que possui não pode ser meu discípulo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25588, 34, """O sal é bom, mas se ele perder o sabor, como restaurá-lo?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25589, 35, "Não serve nem para o solo nem para adubo; é jogado fora. ""Aquele que tem ouvidos para ouvir, ouça"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25590, 1, "Todos os publicanos e ""pecadores"" estavam se reunindo para ouvi-lo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25591, 2, "Mas os fariseus e os mestres da lei o criticavam: ""Este homem recebe pecadores e come com eles"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25592, 3, "Então Jesus lhes contou esta parábola:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25593, 4, """Qual de vocês que, possuindo cem ovelhas, e perdendo uma, não deixa as noventa e nove no campo e vai atrás da ovelha perdida, até encontrá-la?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25594, 5, "E quando a encontra, coloca-a alegremente sobre os ombros", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25595, 6, "e vai para casa. Ao chegar, reúne seus amigos e vizinhos e diz: ‘Alegrem-se comigo, pois encontrei minha ovelha perdida’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25596, 7, "Eu lhes digo que, da mesma forma, haverá mais alegria no céu por um pecador que se arrepende do que por noventa e nove justos que não precisam arrepender-se"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25597, 8, """Ou, qual é a mulher que, possuindo dez dracmas e, perdendo uma delas, não acende uma candeia, varre a casa e procura atentamente, até encontrá-la?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25598, 9, "E quando a encontra, reúne suas amigas e vizinhas e diz: ‘Alegrem-se comigo, pois encontrei minha moeda perdida’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25599, 10, "Eu lhes digo que, da mesma forma, há alegria na presença dos anjos de Deus por um pecador que se arrepende"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25600, 11, "Jesus continuou: ""Um homem tinha dois filhos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25601, 12, "O mais novo disse ao seu pai: ‘Pai, quero a minha parte da herança’. Assim, ele repartiu sua propriedade entre eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25602, 13, """Não muito tempo depois, o filho mais novo reuniu tudo o que tinha, e foi para uma região distante; e lá desperdiçou os seus bens vivendo irresponsavelmente.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25603, 14, "Depois de ter gasto tudo, houve uma grande fome em toda aquela região, e ele começou a passar necessidade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25604, 15, "Por isso foi empregar-se com um dos cidadãos daquela região, que o mandou para o seu campo a fim de cuidar de porcos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25605, 16, "Ele desejava encher o estômago com as vagens de alfarrobeira que os porcos comiam, mas ninguém lhe dava nada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25606, 17, """Caindo em si, ele disse: ‘Quantos empregados de meu pai têm comida de sobra, e eu aqui, morrendo de fome!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25607, 18, "Eu me porei a caminho e voltarei para meu pai, e lhe direi: Pai, pequei contra o céu e contra ti.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25608, 19, "Não sou mais digno de ser chamado teu filho; trata-me como um dos teus empregados’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25609, 20, "A seguir, levantou-se e foi para seu pai. ""Estando ainda longe, seu pai o viu e, cheio de compaixão, correu para seu filho, e o abraçou e beijou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25610, 21, """O filho lhe disse: ‘Pai, pequei contra o céu e contra ti. Não sou mais digno de ser chamado teu filho’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25611, 22, """Mas o pai disse aos seus servos: ‘Depressa! Tragam a melhor roupa e vistam nele. Coloquem um anel em seu dedo e calçados em seus pés.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25612, 23, "Tragam o novilho gordo e matem-no. Vamos fazer uma festa e comemorar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25613, 24, "Pois este meu filho estava morto e voltou à vida; estava perdido e foi achado’. E começaram a festejar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25614, 25, """Enquanto isso, o filho mais velho estava no campo. Quando se aproximou da casa, ouviu a música e a dança.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25615, 26, "Então chamou um dos servos e perguntou-lhe o que estava acontecendo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25616, 27, "Este lhe respondeu: ‘Seu irmão voltou, e seu pai matou o novilho gordo, porque o recebeu de volta são e salvo’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25617, 28, """O filho mais velho encheu-se de ira, e não quis entrar. Então seu pai saiu e insistiu com ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25618, 29, "Mas ele respondeu ao seu pai: ‘Olha! todos esses anos tenho trabalhado como um escravo ao teu serviço e nunca desobedeci às tuas ordens. Mas tu nunca me deste nem um cabrito para eu festejar com os meus amigos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25619, 30, "Mas quando volta para casa esse seu filho, que esbanjou os teus bens com as prostitutas, matas o novilho gordo para ele! ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25620, 31, """Disse o pai: ‘Meu filho, você está sempre comigo, e tudo o que tenho é seu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25621, 32, "Mas nós tínhamos que comemorar e alegrar-nos, porque este seu irmão estava morto e voltou à vida, estava perdido e foi achado’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25622, 1, "Jesus disse aos seus discípulos: ""O administrador de um homem rico foi acusado de estar desperdiçando os seus bens.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25623, 2, "Então ele o chamou e lhe perguntou: ‘Que é isso que estou ouvindo a seu respeito? Preste contas da sua administração, porque você não pode continuar sendo o administrador’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25624, 3, """O administrador disse a si mesmo: ‘Meu senhor está me despedindo. Que farei? Para cavar não tenho força, e tenho vergonha de mendigar...", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25625, 4, "Já sei o que vou fazer para que, quando perder o meu emprego aqui, as pessoas me recebam em suas casas’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25626, 5, """Então chamou cada um dos devedores do seu senhor. Perguntou ao primeiro: ‘Quanto você deve ao meu senhor? ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25627, 6, "‘Cem potes de azeite’, respondeu ele. ""O administrador lhe disse: ‘Tome a sua conta, sente-se depressa e escreva cinqüenta’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25628, 7, """A seguir ele perguntou ao segundo: ‘E você, quanto deve? ’ ‘Cem tonéis de trigo’, respondeu ele. ""Ele lhe disse: ‘Tome a sua conta e escreva oitenta’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25629, 8, """O senhor elogiou o administrador desonesto, porque agiu astutamente. Pois os filhos deste mundo são mais astutos no trato entre si do que os filhos da luz.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25630, 9, "Por isso, eu lhes digo: usem a riqueza deste mundo ímpio para ganhar amigos, de forma que, quando ela acabar, estes os recebam nas moradas eternas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25631, 10, """Quem é fiel no pouco, também é fiel no muito, e quem é desonesto no pouco, também é desonesto no muito.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25632, 11, "Assim, se vocês não forem dignos de confiança em lidar com as riquezas deste mundo ímpio, quem lhes confiará as verdadeiras riquezas?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25633, 12, "E se vocês não forem dignos de confiança em relação ao que é dos outros, quem lhes dará o que é de vocês?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25634, 13, """Nenhum servo pode servir a dois senhores; pois odiará a um e amará ao outro, ou se dedicará a um e desprezará ao outro. Vocês não podem servir a Deus e ao Dinheiro"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25635, 14, "Os fariseus, que amavam o dinheiro, ouviam tudo isso e zombavam de Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25636, 15, "Ele lhes disse: ""Vocês são os que se justificam a si mesmos aos olhos dos homens, mas Deus conhece os corações de vocês. Aquilo que tem muito valor entre os homens é detestável aos olhos de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25637, 16, """A Lei e os Profetas profetizaram até João. Desse tempo em diante estão sendo pregadas as boas novas do Reino de Deus, e todos tentam forçar sua entrada nele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25638, 17, "É mais fácil o céu e a terra desaparecerem do que cair da Lei o menor traço.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25639, 18, """Quem se divorciar de sua mulher e se casar com outra mulher estará cometendo adultério, e o homem que se casar com uma mulher divorciada do seu marido estará cometendo adultério"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25640, 19, """Havia um homem rico que se vestia de púrpura e de linho fino e vivia no luxo todos os dias.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25641, 20, "Diante do seu portão fora deixado um mendigo chamado Lázaro, coberto de chagas;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25642, 21, "este ansiava comer o que caía da mesa do rico. Em vez disso, os cães vinham lamber as suas feridas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25643, 22, """Chegou o dia em que o mendigo morreu, e os anjos o levaram para junto de Abraão. O rico também morreu e foi sepultado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25644, 23, "No Hades, onde estava sendo atormentado, ele olhou para cima e viu Abraão de longe, com Lázaro ao seu lado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25645, 24, "Então, chamou-o: ‘Pai Abraão, tem misericórdia de mim e manda que Lázaro molhe a ponta do dedo na água e refresque a minha língua, porque estou sofrendo muito neste fogo’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25646, 25, """Mas Abraão respondeu: ‘Filho, lembre-se de que durante a sua vida você recebeu coisas boas, enquanto que Lázaro recebeu coisas más. Agora, porém, ele está sendo consolado aqui e você está em sofrimento.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25647, 26, "E além disso, entre vocês e nós há um grande abismo, de forma que os que desejam passar do nosso lado para o seu, ou do seu lado para o nosso, não conseguem’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25648, 27, """Ele respondeu: ‘Então eu lhe suplico, pai: manda Lázaro ir à casa de meu pai,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25649, 28, "pois tenho cinco irmãos. Deixa que ele os avise, a fim de que eles não venham também para este lugar de tormento’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25650, 29, """Abraão respondeu: ‘Eles têm Moisés e os Profetas; que os ouçam’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25651, 30, """ ‘Não, pai Abraão’, disse ele, ‘mas se alguém dentre os mortos fosse até eles, eles se arrependeriam’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25652, 31, """Abraão respondeu: ‘Se não ouvem a Moisés e aos Profetas, tampouco se deixarão convencer, ainda que ressuscite alguém dentre os mortos’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25653, 1, "Jesus disse aos seus discípulos: ""É inevitável que aconteçam coisas que levem o povo a tropeçar, mas ai da pessoa por meio de quem elas acontecem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25654, 2, "Seria melhor que ela fosse lançada no mar com uma pedra de moinho amarrada no pescoço, do que levar um desses pequeninos a pecar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25655, 3, "Tomem cuidado. ""Se o seu irmão pecar, repreenda-o e, se ele se arrepender, perdoe-lhe.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25656, 4, "Se pecar contra você sete vezes no dia, e sete vezes voltar a você e disser: ‘Estou arrependido’, perdoe-lhe"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25657, 5, "Os apóstolos disseram ao Senhor: ""Aumenta a nossa fé! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25658, 6, "Ele respondeu: ""Se vocês tiverem fé do tamanho de uma semente de mostarda, poderão dizer a esta amoreira: ‘Arranque-se e plante-se no mar’, e ela lhes obedecerá.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25659, 7, """Qual de vocês que, tendo um servo que esteja arando ou cuidando das ovelhas, lhe dirá, quando ele chegar do campo: ‘Venha agora e sente-se para comer’?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25660, 8, "Pelo contrário, não dirá: ‘Prepare o meu jantar, apronte-se e sirva-me enquanto como e bebo; depois disso você pode comer e beber’?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25661, 9, "Será que ele agradecerá ao servo por ter feito o que lhe foi ordenado?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25662, 10, "Assim também vocês, quando tiverem feito tudo o que lhes for ordenado, devem dizer: ‘Somos servos inúteis; apenas cumprimos o nosso dever’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25663, 11, "A caminho de Jerusalém, Jesus passou pela divisa entre Samaria e Galiléia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25664, 12, "Ao entrar num povoado, dez leprosos dirigiram-se a ele. Ficaram a certa distância", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25665, 13, "e gritaram em alta voz: ""Jesus, Mestre, tem piedade de nós! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25666, 14, "Ao vê-los, ele disse: ""Vão mostrar-se aos sacerdotes"". Enquanto eles iam, foram purificados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25667, 15, "Um deles, quando viu que estava curado, voltou, louvando a Deus em alta voz.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25668, 16, "Prostrou-se aos pés de Jesus e lhe agradeceu. Este era samaritano.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25669, 17, "Jesus perguntou: ""Não foram purificados todos os dez? Onde estão os outros nove?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25670, 18, "Não se achou nenhum que voltasse e desse louvor a Deus, a não ser este estrangeiro? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25671, 19, "Então ele lhe disse: ""Levante-se e vá; a sua fé o salvou"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25672, 20, "Certa vez, tendo sido interrogado pelos fariseus sobre quando viria o Reino de Deus, Jesus respondeu: ""O Reino de Deus não vem de modo visível,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25673, 21, "nem se dirá: ‘Aqui está ele’, ou ‘Lá está’; porque o Reino de Deus está entre vocês"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25674, 22, "Depois disse aos seus discípulos: ""Chegará o tempo em que vocês desejarão ver um dos dias do Filho do homem, mas não verão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25675, 23, "Dirão a vocês: ‘Lá está ele! ’ ou ‘Aqui está! ’ Não se apressem em segui-los.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25676, 24, "Pois o Filho do homem no seu dia será como o relâmpago cujo brilho vai de uma extremidade à outra do céu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25677, 25, "Mas antes é necessário que ele sofra muito e seja rejeitado por esta geração.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25678, 26, """Assim como foi nos dias de Noé, também será nos dias do Filho do homem.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25679, 27, "O povo vivia comendo, bebendo, casando-se e sendo dado em casamento, até o dia em que Noé entrou na arca. Então veio o dilúvio e os destruiu a todos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25680, 28, """Aconteceu a mesma coisa nos dias de Ló. O povo estava comendo e bebendo, comprando e vendendo, plantando e construindo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25681, 29, "Mas no dia em que Ló saiu de Sodoma, choveu fogo e enxofre do céu e os destruiu a todos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25682, 30, """Acontecerá exatamente assim no dia em que o Filho do homem for revelado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25683, 31, "Naquele dia, quem estiver no telhado de sua casa, não deve desça para apanhar os seus bens dentro de casa. Semelhantemente, quem estiver no campo, não deve voltar atrás por coisa alguma.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25684, 32, "Lembrem-se da mulher de Ló!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25685, 33, "Quem tentar conservar a sua vida a perderá, e quem perder a sua vida a preservará.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25686, 34, "Eu lhes digo: naquela noite duas pessoas estarão numa cama; uma será tirada e a outra deixada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25687, 35, "Duas mulheres estarão moendo trigo juntas; uma será tirada e a outra deixada.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25688, 36, "Duas pessoas estarão no campo; uma será tirada e a outra deixada"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25689, 37, """Onde, Senhor? "", perguntaram eles. Ele respondeu: ""Onde houver um cadáver, ali se ajuntarão os abutres"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25690, 1, "Então Jesus contou aos seus discípulos uma parábola, para mostrar-lhes que eles deviam orar sempre e nunca desanimar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25691, 2, "Ele disse: ""Em certa cidade havia um juiz que não temia a Deus nem se importava com os homens.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25692, 3, "E havia naquela cidade uma viúva que se dirigia continuamente a ele, suplicando-lhe: ‘Faze-me justiça contra o meu adversário’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25693, 4, """Por algum tempo ele se recusou. Mas finalmente disse a si mesmo: ‘Embora eu não tema a Deus e nem me importe com os homens,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25694, 5, "esta viúva está me aborrecendo; vou fazer-lhe justiça para que ela não venha me importunar’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25695, 6, "E o Senhor continuou: ""Ouçam o que diz o juiz injusto.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25696, 7, "Acaso Deus não fará justiça aos seus escolhidos, que clamam a ele dia e noite? Continuará fazendo-os esperar?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25697, 8, "Eu lhes digo: ele lhes fará justiça, e depressa. Contudo, quando o Filho do homem vier, encontrará fé na terra? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25698, 9, "A alguns que confiavam em sua própria justiça e desprezavam os outros, Jesus contou esta parábola:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25699, 10, """Dois homens subiram ao templo para orar; um era fariseu e o outro, publicano.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25700, 11, "O fariseu, em pé, orava no íntimo: ‘Deus, eu te agradeço porque não sou como os outros homens: ladrões, corruptos, adúlteros; nem mesmo como este publicano.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25701, 12, "Jejuo duas vezes por semana e dou o dízimo de tudo quanto ganho’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25702, 13, """Mas o publicano ficou à distância. Ele nem ousava olhar para o céu, mas batendo no peito, dizia: ‘Deus, tem misericórdia de mim, que sou pecador’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25703, 14, """Eu lhes digo que este homem, e não o outro, foi para casa justificado diante de Deus. Pois quem se exalta será humilhado, e quem se humilha será exaltado"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25704, 15, "O povo também estava trazendo criancinhas para que Jesus tocasse nelas. Ao verem isto, os discípulos repreendiam os que as tinham trazido.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25705, 16, "Mas Jesus chamou a si as crianças e disse: ""Deixem vir a mim as crianças e não as impeçam; pois o Reino de Deus pertence aos que são semelhantes a elas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25706, 17, "Digo-lhes a verdade: Quem não receber o Reino de Deus como uma criança, nunca entrará nele"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25707, 18, "Certo homem importante lhe perguntou: ""Bom Mestre, que farei para herdar a vida eterna? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25708, 19, """Por que você me chama bom? "", respondeu Jesus. ""Não há ninguém que seja bom, a não ser somente Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25709, 20, "Você conhece os mandamentos: ‘Não adulterarás, não matarás, não furtarás, não darás falso testemunho, honra teu pai e tua mãe’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25710, 21, """A tudo isso tenho obedecido desde a adolescência"", disse ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25711, 22, "Ao ouvir isso, disse-lhe Jesus: ""Falta-lhe ainda uma coisa. Venda tudo o que você possui e dê o dinheiro aos pobres, e você terá um tesouro nos céus. Depois venha e siga-me"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25712, 23, "Ouvindo isso, ele ficou triste, porque era muito rico.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25713, 24, "Vendo-o entristecido, Jesus disse: ""Como é difícil aos ricos entrar no Reino de Deus!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25714, 25, "De fato, é mais fácil passar um camelo pelo fundo de uma agulha do que um rico entrar no Reino de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25715, 26, "Os que ouviram isso perguntaram: ""Então, quem pode ser salvo? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25716, 27, "Jesus respondeu: ""O que é impossível para os homens é possível para Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25717, 28, "Pedro lhe disse: ""Nós deixamos tudo o que tínhamos para seguir-te! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25718, 29, "Respondeu Jesus: ""Digo-lhes a verdade: Ninguém que tenha deixado casa, mulher, irmãos, pai ou filhos por causa do Reino de Deus", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25719, 30, "deixará de receber, na presente era, muitas vezes mais, e, na era futura, a vida eterna"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25720, 31, "Jesus chamou à parte os Doze e lhes disse: ""Estamos subindo para Jerusalém, e tudo o que está escrito pelos profetas acerca do Filho do homem se cumprirá.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25721, 32, "Ele será entregue aos gentios que zombarão dele, o insultarão, cuspirão nele, o açoitarão e o matarão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25722, 33, "No terceiro dia ele ressuscitará"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25723, 34, "Os discípulos não entenderam nada dessas coisas. O significado dessas palavras lhes estava oculto, e eles não sabiam do que ele estava falando.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25724, 35, "Ao aproximar-se Jesus de Jericó, um homem cego estava sentado à beira do caminho, pedindo esmola.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25725, 36, "Quando ouviu a multidão passando, ele perguntou o que estava acontecendo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25726, 37, "Disseram-lhe: ""Jesus de Nazaré está passando"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25727, 38, "Então ele se pôs a gritar: ""Jesus, filho de Davi, tem misericórdia de mim! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25728, 39, "Os que iam adiante o repreendiam para que ficasse quieto, mas ele gritava ainda mais: ""Filho de Davi, tem misericórdia de mim! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25729, 40, "Jesus parou e ordenou que o homem lhe fosse trazido. Quando ele chegou perto, Jesus perguntou-lhe:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25730, 41, """O que você quer que eu lhe faça? "" ""Senhor, eu quero ver"", respondeu ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25731, 42, "Jesus lhe disse: ""Recupere a visão! A sua fé o curou"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25732, 43, "Imediatamente ele recuperou a visão; e seguia a Jesus glorificando a Deus. Quando todo o povo viu isso, deu louvores a Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25733, 1, "Jesus entrou em Jericó, e atravessava a cidade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25734, 2, "Havia ali um homem rico chamado Zaqueu, chefe dos publicanos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25735, 3, "Ele queria ver quem era Jesus, mas, sendo de pequena estatura, não o conseguia, por causa da multidão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25736, 4, "Assim, correu adiante e subiu numa figueira brava para vê-lo, pois Jesus ia passar por ali.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25737, 5, "Quando Jesus chegou àquele lugar, olhou para cima e lhe disse: ""Zaqueu, desça depressa. Quero ficar em sua casa hoje"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25738, 6, "Então ele desceu rapidamente e o recebeu com alegria.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25739, 7, "Todo o povo viu isso e começou a se queixar: ""Ele se hospedou na casa de um ‘pecador’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25740, 8, "Mas Zaqueu levantou-se e disse ao Senhor: ""Olha, Senhor! Estou dando a metade dos meus bens aos pobres; e se de alguém extorqui alguma coisa, devolverei quatro vezes mais"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25741, 9, "Jesus lhe disse: ""Hoje houve salvação nesta casa! Porque este homem também é filho de Abraão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25742, 10, "Pois o Filho do homem veio buscar e salvar o que estava perdido"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25743, 11, "Estando eles a ouvi-lo, Jesus passou a contar-lhes uma parábola, porque estava perto de Jerusalém e o povo pensava que o Reino de Deus ia se manifestar de imediato.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25744, 12, "Ele disse: ""Um homem de nobre nascimento foi para uma terra distante para ser coroado rei e depois voltar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25745, 13, "Então, chamou dez dos seus servos e lhes deu dez minas. Disse ele: ‘Façam esse dinheiro render até à minha volta’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25746, 14, """Mas os seus súditos o odiavam e depois enviaram uma delegação para lhe dizer: ‘Não queremos que este homem seja nosso rei’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25747, 15, """Contudo, foi feito rei e voltou. Então mandou chamar os servos a quem dera o dinheiro, a fim de saber quanto tinham lucrado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25748, 16, """O primeiro veio e disse: ‘Senhor, a tua mina rendeu outras dez’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25749, 17, """ ‘Muito bem, meu bom servo! ’, respondeu o seu senhor. ‘Por ter sido confiável no pouco, governe sobre dez cidades’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25750, 18, """O segundo veio e disse: ‘Senhor, a tua mina rendeu cinco vezes mais’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25751, 19, """O seu senhor respondeu: ‘Também você, encarregue-se de cinco cidades’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25752, 20, """Então veio outro servo e disse: ‘Senhor, aqui está a tua mina; eu a conservei guardada num pedaço de pano.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25753, 21, "Tive medo, porque és um homem severo. Tiras o que não puseste e colhes o que não semeaste’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25754, 22, """O seu senhor respondeu: ‘Eu o julgarei pelas suas próprias palavras, servo mau! Você sabia que sou homem severo, que tiro o que não pus e colho o que não semeei.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25755, 23, "Então, por que não confiou o meu dinheiro ao banco? Assim, quando eu voltasse o receberia com os juros’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25756, 24, """E disse aos que estavam ali: ‘Tomem dele a sua mina e dêem-na ao que tem dez’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25757, 25, """ ‘Senhor’, disseram, ‘ele já tem dez! ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25758, 26, """Ele respondeu: ‘Eu lhes digo que a quem tem, mais será dado, mas a quem não tem, até o que tiver lhe será tirado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25759, 27, "E aqueles inimigos meus, que não queriam que eu reinasse sobre eles, tragam-nos aqui e matem-nos na minha frente! ’ """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25760, 28, "Depois de dizer isso, Jesus foi adiante, subindo para Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25761, 29, "Ao aproximar-se de Betfagé e de Betânia, no monte chamado das Oliveiras, enviou dois dos seus discípulos, dizendo-lhes:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25762, 30, """Vão ao povoado que está adiante e, ao entrarem, encontrarão um jumentinho amarrado, no qual ninguém jamais montou. Desamarrem-no e tragam-no aqui.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25763, 31, "Se alguém lhes perguntar: ‘Por que o estão desamarrando? ’ digam-lhe: ‘O Senhor precisa dele’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25764, 32, "Os que tinham sido enviados foram e encontraram o animal exatamente como ele lhes tinha dito.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25765, 33, "Quando estavam desamarrando o jumentinho, os seus donos lhes perguntaram: ""Por que vocês estão desamarrando o jumentinho? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25766, 34, "Eles responderam: ""O Senhor precisa dele"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25767, 35, "Levaram-no a Jesus, lançaram seus mantos sobre o jumentinho e fizeram que Jesus montasse nele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25768, 36, "Enquanto ele prosseguia, o povo estendia os seus mantos pelo caminho.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25769, 37, "Quando ele já estava perto da descida do monte das Oliveiras, toda a multidão dos discípulos começou a louvar a Deus alegremente, em alta voz, por todos os milagres que tinham visto. Exclamavam:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25770, 38, """Bendito é o rei que vem em nome do Senhor! "" ""Paz no céu e glória nas alturas! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25771, 39, "Alguns dos fariseus que estavam no meio da multidão disseram a Jesus: ""Mestre, repreende os teus discípulos! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25772, 40, """Eu lhes digo"", respondeu ele, ""se eles se calarem, as pedras clamarão"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25773, 41, "Quando se aproximou e viu a cidade, Jesus chorou sobre ela", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25774, 42, "e disse: ""Se você compreendesse neste dia, sim, você também, o que traz a paz! Mas agora isso está oculto aos seus olhos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25775, 43, "Virão dias em que os seus inimigos construirão trincheiras contra você, e a rodearão e a cercarão de todos os lados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25776, 44, "Também a lançarão por terra, você e os seus filhos. Não deixarão pedra sobre pedra, porque você não reconheceu o tempo em que Deus a visitaria"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25777, 45, "Então ele entrou no templo e começou a expulsar os que estavam vendendo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25778, 46, "Disse-lhes: ""Está escrito: ‘A minha casa será casa de oração’; mas vocês fizeram dela ‘um covil de ladrões’"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25779, 47, "Todos os dias ele ensinava no templo. Mas os chefes dos sacerdotes, os mestres da lei e os líderes do povo procuravam matá-lo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25780, 48, "Todavia, não conseguiam encontrar uma forma de fazê-lo, porque todo o povo estava fascinado pelas suas palavras.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25781, 1, "Certo dia, quando Jesus estava ensinando o povo no templo e pregando as boas novas, chegaram-se a ele os chefes dos sacerdotes, juntamente com os mestres da lei e os líderes religiosos,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25782, 2, "e lhe perguntaram: ""Com que autoridade estás fazendo estas coisas? Quem te deu esta autoridade? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25783, 3, "Ele respondeu: ""Eu também lhes farei uma pergunta: Digam-me:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25784, 4, "O batismo de João era do céu, ou dos homens? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25785, 5, "Eles discutiam entre si, dizendo: ""Se dissermos: ‘do céu’, ele perguntará: ‘Então por que vocês não creram nele? ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25786, 6, "Mas se dissermos: ‘dos homens’, todo o povo nos apedrejará, porque convencidos estão de que João era um profeta"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25787, 7, "Assim, responderam: ""Não sabemos de onde era"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25788, 8, "Disse então Jesus: ""Tampouco lhes direi com que autoridade estou fazendo estas coisas"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25789, 9, "Então Jesus passou a contar ao povo esta parábola: ""Certo homem plantou uma vinha, arrendou-a a alguns lavradores e ausentou-se por longo tempo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25790, 10, "Na época da colheita, ele enviou um servo aos lavradores, para que lhe entregassem parte do fruto da vinha. Mas os lavradores o espancaram e o mandaram embora de mãos vazias.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25791, 11, "Ele mandou outro servo, mas a esse também espancaram e o trataram de maneira humilhante, mandando-o embora de mãos vazias.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25792, 12, "Enviou ainda um terceiro, e eles o feriram e o expulsaram da vinha.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25793, 13, """Então o proprietário da vinha disse: ‘Que farei? Mandarei meu filho amado; quem sabe o respeitarão’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25794, 14, """Mas quando os lavradores o viram, combinaram entre si dizendo: ‘Este é o herdeiro. Vamos matá-lo, e a herança será nossa’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25795, 15, "Assim, lançaram-no fora da vinha e o mataram. ""O que lhes fará então o dono da vinha?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25796, 16, "Virá, matará aqueles lavradores e dará a vinha a outros"". Quando o povo ouviu isso, disse: ""Que isso nunca aconteça! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25797, 17, "Jesus olhou fixamente para eles e perguntou: ""Então, qual é o significado do que está escrito? ‘A pedra que os construtores rejeitaram tornou-se a pedra angular’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25798, 18, "Todo o que cair sobre esta pedra será despedaçado, e aquele sobre quem ela cair será reduzido a pó"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25799, 19, "Os mestres da lei e os chefes dos sacerdotes procuravam uma forma de prendê-lo imediatamente, pois perceberam que era contra eles que ele havia contado essa parábola. Todavia tinham medo do povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25800, 20, "Pondo-se a vigiá-lo, eles mandaram espiões, que se fingiam justos, para apanhar Jesus em alguma coisa que ele dissesse, de forma que o pudessem entregar ao poder e à autoridade do governador.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25801, 21, "Assim, os espiões lhe perguntaram: ""Mestre, sabemos que falas e ensinas o que é correto, e que não mostras parcialidade, mas ensinas o caminho de Deus conforme a verdade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25802, 22, "É certo pagar imposto a César ou não? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25803, 23, "Ele percebeu a astúcia deles e lhes disse:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25804, 24, """Mostrem-me um denário. De quem é a imagem e a inscrição que há nele? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25805, 25, """De César"", responderam eles. Ele lhes disse: ""Portanto, dêem a César o que é de César, e a Deus o que é de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25806, 26, "E não conseguiram apanhá-lo em nenhuma palavra diante do povo. E, admirados com a sua resposta, ficaram em silêncio.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25807, 27, "Alguns dos saduceus, que dizem que não há ressurreição, aproximaram-se de Jesus com a seguinte questão:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25808, 28, """Mestre"", disseram eles, ""Moisés nos deixou escrito que, se o irmão de um homem morrer e deixar mulher sem filhos, este deverá casar-se com a viúva e ter filhos para seu irmão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25809, 29, "Havia sete irmãos. O primeiro casou-se e morreu sem deixar filhos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25810, 30, "O segundo", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25811, 31, "e o terceiro e depois também os outros casaram-se com ela; e morreram os sete sucessivamente, sem deixar filhos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25812, 32, "Finalmente morreu também a mulher.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25813, 33, "Na ressurreição, de quem ela será esposa, visto que os sete foram casados com ela? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25814, 34, "Jesus respondeu: ""Os filhos desta era casam-se e são dados em casamento,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25815, 35, "mas os que forem considerados dignos de tomar parte na era que há de vir e na ressurreição dos mortos não se casarão nem serão dados em casamento,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25816, 36, "e não podem mais morrer, pois são como os anjos. São filhos de Deus, visto que são filhos da ressurreição.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25817, 37, "E que os mortos ressuscitam, já Moisés mostrou, no relato da sarça, quando ao Senhor ele chama ‘Deus de Abraão, Deus de Isaque e Deus de Jacó’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25818, 38, "Ele não é Deus de mortos, mas de vivos, pois para ele todos vivem"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25819, 39, "Alguns dos mestres da lei disseram: ""Respondeste bem, Mestre! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25820, 40, "E ninguém mais ousava fazer-lhe perguntas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25821, 41, "Então Jesus lhes perguntou: ""Como dizem que o Cristo é Filho de Davi?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25822, 42, """O próprio Davi afirma no Livro dos Salmos: ‘O Senhor disse ao meu Senhor: ""Senta-te à minha direita", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25823, 43, "até que eu ponha os teus inimigos como estrado para os teus pés"" ’.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25824, 44, "Portanto Davi o chama ‘Senhor’. Então, como é que ele pode ser seu filho? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25825, 45, "Estando todo o povo a ouvi-lo, Jesus disse aos seus discípulos:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25826, 46, """Cuidado com os mestres da lei. Eles fazem questão de andar com roupas especiais, e gostam muito de receber saudações nas praças e de ocupar os lugares mais importantes nas sinagogas e os lugares de honra nos banquetes.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25827, 47, "Eles devoram as casas das viúvas, e, para disfarçar, fazem longas orações. Esses homens serão punidos com maior rigor! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25828, 1, "Jesus olhou e viu os ricos colocando suas contribuições nas caixas de ofertas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25829, 2, "Viu também uma viúva pobre colocar duas pequeninas moedas de cobre.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25830, 3, "E disse: ""Afirmo-lhes que esta viúva pobre colocou mais do que todos os outros.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25831, 4, "Todos esses deram do que lhes sobrava; mas ela, da sua pobreza, deu tudo o que possuía para viver"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25832, 5, "Alguns dos seus discípulos estavam comentando como o templo era adornado com lindas pedras e dádivas dedicadas a Deus. Mas Jesus disse:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25833, 6, """Disso que vocês estão vendo, dias virão em que não ficará pedra sobre pedra; serão todas derrubadas"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25834, 7, """Mestre"", perguntaram eles, ""quando acontecerão essas coisas? E qual será o sinal de que elas estão prestes a acontecer? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25835, 8, "Ele respondeu: ""Cuidado para não serem enganados. Pois muitos virão em meu nome, dizendo: ‘Sou eu! ’ e ‘o tempo está próximo’. Não os sigam.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25836, 9, "Quando ouvirem falar de guerras e rebeliões, não tenham medo. É necessário que primeiro aconteçam essas coisas, mas o fim não virá imediatamente"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25837, 10, "Então lhes disse: ""Nação se levantará contra nação, e reino contra reino.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25838, 11, "Haverá grandes terremotos, fomes e pestes em vários lugares, e acontecimentos terríveis e grandes sinais provenientes do céu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25839, 12, """Mas antes de tudo isso, prenderão e perseguirão vocês. Então os entregarão às sinagogas e prisões, e vocês serão levados à presença de reis e governadores, tudo por causa do meu nome.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25840, 13, "Será para vocês uma oportunidade de dar testemunho.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25841, 14, "Mas convençam-se de uma vez de que não devem preocupar-se com o que dirão para se defender.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25842, 15, "Pois eu lhes darei palavras e sabedoria a que nenhum dos seus adversários será capaz de resistir ou contradizer.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25843, 16, "Vocês serão traídos até por pais, irmãos, parentes e amigos, e eles entregarão alguns de vocês à morte.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25844, 17, "Todos odiarão vocês por causa do meu nome.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25845, 18, "Contudo, nenhum fio de cabelo da cabeça de vocês se perderá.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25846, 19, "É perseverando que vocês obterão a vida.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25847, 20, """Quando virem Jerusalém rodeada de exércitos, vocês saberão que a sua devastação está próxima.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25848, 21, "Então os que estiverem na Judéia fujam para os montes, os que estiverem na cidade saiam, e os que estiverem no campo não entrem na cidade.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25849, 22, "Pois esses são os dias da vingança, em cumprimento de tudo o que foi escrito.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25850, 23, "Como serão terríveis aqueles dias para as grávidas e para as que estiverem amamentando! Haverá grande aflição na terra e ira contra este povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25851, 24, "Cairão pela espada e serão levados como prisioneiros para todas as nações. Jerusalém será pisada pelos gentios, até que os tempos deles se cumpram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25852, 25, """Haverá sinais no sol, na lua e nas estrelas. Na terra, as nações se verão em angústia e perplexidade com o bramido e a agitação do mar.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25853, 26, "Os homens desmaiarão de terror, apreensivos com o que estará sobrevindo ao mundo; e os poderes celestes serão abalados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25854, 27, "Então se verá o Filho do homem vindo numa nuvem com poder e grande glória.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25855, 28, "Quando começarem a acontecer estas coisas, levantem-se e ergam a cabeça, porque estará próxima a redenção de vocês"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25856, 29, "Ele lhes contou esta parábola: ""Observem a figueira e todas as árvores.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25857, 30, "Quando elas brotam, vocês mesmos percebem e sabem que o verão está próximo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25858, 31, "Assim também, quando virem estas coisas acontecendo, saibam que o Reino de Deus está próximo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25859, 32, """Eu lhes asseguro que não passará esta geração até que todas essas coisas aconteçam.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25860, 33, "O céu e a terra passarão, mas as minhas palavras jamais passarão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25861, 34, """Tenham cuidado, para que os seus corações não fiquem carregados de libertinagem, bebedeira e ansiedades da vida, e aquele dia venha sobre vocês inesperadamente.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25862, 35, "Porque ele virá sobre todos os que vivem na face de toda a terra.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25863, 36, "Estejam sempre atentos e orem para que vocês possam escapar de tudo o que está para acontecer, e estar de pé diante do Filho do homem"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25864, 37, "Jesus passava o dia ensinando no templo; e, ao entardecer, saía para passar a noite no monte chamado das Oliveiras.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25865, 38, "Todo o povo ia de manhã cedo ouvi-lo no templo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25866, 1, "Estava se aproximando a festa dos pães sem fermento, chamada Páscoa,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25867, 2, "e os chefes dos sacerdotes e os mestres da lei estavam procurando um meio de matar Jesus, mas tinham medo do povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25868, 3, "Então Satanás entrou em Judas, chamado Iscariotes, um dos Doze.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25869, 4, "Judas dirigiu-se aos chefes dos sacerdotes e aos oficiais da guarda do templo e tratou com eles como lhes poderia entregar Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25870, 5, "A proposta muito os alegrou, e lhe prometeram dinheiro.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25871, 6, "Ele consentiu e ficou esperando uma oportunidade para lhes entregar Jesus quando a multidão não estivesse presente.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25872, 7, "Finalmente, chegou o dia dos pães sem fermento, no qual devia ser sacrificado o cordeiro pascal.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25873, 8, "Jesus enviou Pedro e João, dizendo: ""Vão preparar a refeição da Páscoa"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25874, 9, """Onde queres que a preparemos? "", perguntaram eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25875, 10, "Ele respondeu: ""Ao entrarem na cidade, vocês encontrarão um homem carregando um pote de água. Sigam-no até a casa em que ele entrar", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25876, 11, "e digam ao dono da casa: ‘O Mestre pergunta: Onde é o salão de hóspedes no qual poderei comer a Páscoa com os meus discípulos? ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25877, 12, "Ele lhes mostrará uma ampla sala no andar superior, toda mobiliada. Façam ali os preparativos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25878, 13, "Eles saíram e encontraram tudo como Jesus lhes tinha dito. Então, prepararam a Páscoa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25879, 14, "Quando chegou a hora, Jesus e os seus apóstolos reclinaram-se à mesa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25880, 15, "E disse-lhes: ""Desejei ansiosamente comer esta Páscoa com vocês antes de sofrer.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25881, 16, "Pois eu lhes digo: Não comerei dela novamente até que se cumpra no Reino de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25882, 17, "Recebendo um cálice, ele deu graças e disse: ""Tomem isto e partilhem uns com os outros.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25883, 18, "Pois eu lhes digo que não beberei outra vez do fruto da videira até que venha o Reino de Deus"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25884, 19, "Tomando o pão, deu graças, partiu-o e o deu aos discípulos, dizendo: ""Isto é o meu corpo dado em favor de vocês; façam isto em memória de mim"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25885, 20, "Da mesma forma, depois da ceia, tomou o cálice, dizendo: ""Este cálice é a nova aliança no meu sangue, derramado em favor de vocês.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25886, 21, """Mas eis que a mão daquele que vai me trair está com a minha sobre a mesa.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25887, 22, "O Filho do homem vai, como foi determinado; mas ai daquele que o trai! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25888, 23, "Eles começaram a perguntar entre si qual deles iria fazer aquilo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25889, 24, "Surgiu também uma discussão entre eles, acerca de qual deles era considerado o maior.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25890, 25, "Jesus lhes disse: ""Os reis das nações dominam sobre elas; e os que exercem autoridade sobre elas são chamados benfeitores.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25891, 26, "Mas, vocês não serão assim. Pelo contrário, o maior entre vocês deverá ser como o mais jovem, e aquele que governa como o que serve.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25892, 27, "Pois quem é maior: o que está à mesa, ou o que serve? Não é o que está à mesa? Mas eu estou entre vocês como quem serve.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25893, 28, "Vocês são os que têm permanecido ao meu lado durante as minhas provações.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25894, 29, "E eu lhes designo um Reino, assim como meu Pai o designou a mim,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25895, 30, "para que vocês possam comer e beber à minha mesa no meu Reino e sentar-se em tronos, julgando as doze tribos de Israel.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25896, 31, """Simão, Simão, Satanás pediu vocês para peneirá-los como trigo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25897, 32, "Mas eu orei por você, para que a sua fé não desfaleça. E quando você se converter, fortaleça os seus irmãos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25898, 33, "Mas ele respondeu: ""Estou pronto para ir contigo para a prisão e para a morte"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25899, 34, "Respondeu Jesus: ""Eu lhe digo, Pedro, que antes que o galo cante hoje, três vezes você negará que me conhece"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25900, 35, "Então Jesus lhes perguntou: ""Quando eu os enviei sem bolsa, saco de viagem ou sandálias, faltou-lhes alguma coisa? "" ""Nada"", responderam eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25901, 36, "Ele lhes disse: ""Mas agora, se vocês têm bolsa, levem-na, e também o saco de viagem; e se não têm espada, vendam a sua capa e comprem uma.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25902, 37, "Está escrito: ‘E ele foi contado com os transgressores’; e eu lhes digo que isto precisa cumprir-se em mim. Sim, o que está escrito a meu respeito está para se cumprir"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25903, 38, "Os discípulos disseram: ""Vê, Senhor, aqui estão duas espadas"". ""É o suficiente! "", respondeu ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25904, 39, "Como de costume, Jesus foii para o monte das Oliveiras, e os seus discípulos o seguiram.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25905, 40, "Chegando ao lugar, ele lhes disse: ""Orem para que vocês não caiam em tentação"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25906, 41, "Ele se afastou deles a uma pequena distância, ajoelhou-se e começou a orar:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25907, 42, """Pai, se queres, afasta de mim este cálice; contudo, não seja feita a minha vontade, mas a tua"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25908, 43, "Apareceu-lhe então um anjo do céu que o fortalecia.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25909, 44, "Estando angustiado, ele orou ainda mais intensamente; e o seu suor era como gotas de sangue que caíam no chão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25910, 45, "Quando se levantou da oração e voltou aos discípulos, encontrou-os dormindo, dominados pela tristeza.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25911, 46, """Por que estão dormindo? "", perguntou-lhes. ""Levantem-se e orem para que vocês não caiam em tentação! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25912, 47, "Enquanto ele ainda falava, apareceu uma multidão conduzida por Judas, um dos Doze. Este se aproximou de Jesus para saudá-lo com um beijo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25913, 48, "Mas Jesus lhe perguntou: ""Judas, com um beijo você está traindo o Filho do homem? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25914, 49, "Ao verem o que ia acontecer, os que estavam com Jesus lhe disseram: ""Senhor, atacaremos com espadas? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25915, 50, "E um deles feriu o servo do sumo sacerdote, decepando-lhe a orelha direita.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25916, 51, "Jesus, porém, respondeu: ""Basta! "" E tocando na orelha do homem, ele o curou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25917, 52, "Então Jesus disse aos chefes dos sacerdotes, aos oficiais da guarda do templo e aos líderes religiosos que tinham vindo procurá-lo: ""Estou eu chefiando alguma rebelião, para que vocês tenham vindo com espadas e varas?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25918, 53, "Todos os dias eu estava com vocês no templo e vocês não levantaram a mão contra mim. Mas esta é a hora de vocês — quando as trevas reinam"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25919, 54, "Então, prendendo-o, levaram-no para a casa do sumo sacerdote. Pedro os seguia à distância.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25920, 55, "Mas, quando acenderam um fogo no meio do pátio e se sentaram ao redor dele, Pedro sentou-se com eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25921, 56, "Uma criada o viu sentado ali à luz do fogo. Olhou fixamente para ele e disse: ""Este homem estava com ele"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25922, 57, "Mas ele negou: ""Mulher, não o conheço"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25923, 58, "Pouco depois, um homem o viu e disse: ""Você também é um deles"". ""Homem, não sou! "", respondeu Pedro.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25924, 59, "Cerca de uma hora mais tarde, outro afirmou: ""Certamente este homem estava com ele, pois é galileu"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25925, 60, "Pedro respondeu: ""Homem, não sei do que você está falando! "" Falava ele ainda, quando o galo cantou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25926, 61, "O Senhor voltou-se e olhou diretamente para Pedro. Então Pedro se lembrou da palavra que o Senhor lhe tinha dito: ""Antes que o galo cante hoje, você me negará três vezes"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25927, 62, "Saindo dali, chorou amargamente.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25928, 63, "Os homens que estavam detendo Jesus começaram a zombar dele e a bater nele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25929, 64, "Cobriam seus olhos e perguntavam: ""Profetize! Quem foi que lhe bateu? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25930, 65, "E lhe dirigiam muitas outras palavras de insulto.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25931, 66, "Ao romper do dia, reuniu-se o Sinédrio, tanto os chefes dos sacerdotes quanto os mestres da lei, e Jesus foi levado perante eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25932, 67, """Se você é o Cristo, diga-nos"", disseram eles. Jesus respondeu: ""Se eu vos disser, não crereis em mim", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25933, 68, "e, se eu vos perguntar, não me respondereis.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25934, 69, "Mas de agora em diante o Filho do homem estará assentado à direita do Deus Todo-poderoso"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25935, 70, "Perguntaram-lhe todos: ""Então, você é o Filho de Deus? "" ""Vós estais dizendo que eu sou"", respondeu ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25936, 71, "Eles disseram: ""Por que precisamos de mais testemunhas? Acabamos de ouvir dos próprios lábios dele"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25937, 1, "Então toda a assembléia levantou-se e o levou a Pilatos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25938, 2, "E começaram a acusá-lo, dizendo: ""Encontramos este homem subvertendo a nossa nação. Ele proíbe o pagamento de imposto a César e se declara ele próprio o Cristo, um rei"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25939, 3, "Pilatos perguntou a Jesus: ""Você é o rei dos judeus? "" ""Tu o dizes"", respondeu Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25940, 4, "Então Pilatos disse aos chefes dos sacerdotes e à multidão: ""Não encontro motivo para acusar este homem"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25941, 5, "Mas eles insistiam: ""Ele está subvertendo o povo em toda a Judéia com os seus ensinamentos. Começou na Galiléia e chegou até aqui"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25942, 6, "Ouvindo isso, Pilatos perguntou se Jesus era galileu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25943, 7, "Quando ficou sabendo que ele era da jurisdição de Herodes, enviou-o a Herodes, que também estava em Jerusalém naqueles dias.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25944, 8, "Quando Herodes viu Jesus, ficou muito alegre, porque havia muito tempo queria vê-lo. Pelo que ouvira falar dele, esperava vê-lo realizar algum milagre.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25945, 9, "Interrogou-o com muitas perguntas, mas Jesus não lhe deu resposta.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25946, 10, "Os chefes dos sacerdotes e os mestres da lei estavam ali, acusando-o com veemência.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25947, 11, "Então Herodes e os seus soldados ridicularizaram-no e zombaram dele. Vestindo-o com um manto esplêndido, mandaram-no de volta a Pilatos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25948, 12, "Herodes e Pilatos, que até ali eram inimigos, naquele dia tornaram-se amigos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25949, 13, "Pilatos reuniu os chefes dos sacerdotes, as autoridades e o povo,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25950, 14, "dizendo-lhes: ""Vocês me trouxeram este homem como alguém que estava incitando o povo à rebelião. Eu o examinei na presença de vocês e não achei nenhuma base para as acusações que fazem contra ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25951, 15, "Nem Herodes, pois ele o mandou de volta para nós. Como podem ver, ele nada fez que mereça a morte.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25952, 16, "Portanto, eu o castigarei e depois o soltarei.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25953, 17, "Ele era obrigado a soltar-lhes um preso durante a festa"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25954, 18, "A uma só voz eles gritaram: ""Acaba com ele! Solta-nos Barrabás! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25955, 19, "( Barrabás havia sido lançado na prisão por causa de uma insurreição na cidade e por assassinato. )", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25956, 20, "Desejando soltar a Jesus, Pilatos dirigiu-se a eles novamente.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25957, 21, "Mas eles continuaram gritando: ""Crucifica-o! Crucifica-o! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25958, 22, "Pela terceira vez ele lhes falou: ""Por quê? Que crime este homem cometeu? Não encontrei nele nada digno de morte. Vou mandar castigá-lo e depois o soltarei"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25959, 23, "Eles, porém, pediam insistentemente, com fortes gritos, que ele fosse crucificado; e a gritaria prevaleceu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25960, 24, "Então Pilatos decidiu fazer a vontade deles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25961, 25, "Libertou o homem que havia sido lançado na prisão por insurreição e assassinato, aquele que eles haviam pedido, e entregou Jesus à vontade deles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25962, 26, "Enquanto o levavam, agarraram Simão de Cirene, que estava chegando do campo, e lhe colocaram a cruz às costas, fazendo-o carregá-la atrás de Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25963, 27, "Um grande número de pessoas o seguia, inclusive mulheres que lamentavam e choravam por ele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25964, 28, "Jesus voltou-se e disse-lhes: ""Filhas de Jerusalém, não chorem por mim; chorem por vocês mesmas e por seus filhos!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25965, 29, "Pois chegará a hora em que vocês dirão: ‘Felizes as estéreis, os ventres que nunca geraram e os seios que nunca amamentaram! ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25966, 30, """Então dirão às montanhas: ‘Caiam sobre nós! ’ e às colinas: ‘Cubram-nos! ’", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25967, 31, "Pois, se fazem isto com a árvore verde, o que acontecerá quando ela estiver seca? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25968, 32, "Dois outros homens, ambos criminosos, também foram levados com ele, para serem executados.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25969, 33, "Quando chegaram ao lugar chamado Caveira, ali o crucificaram com os criminosos, um à sua direita e o outro à sua esquerda.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25970, 34, "Jesus disse: ""Pai, perdoa-lhes, pois não sabem o que estão fazendo"". Então eles dividiram as roupas dele, tirando sortes.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25971, 35, "O povo ficou observando, e as autoridades o ridicularizavam. ""Salvou os outros"", diziam; ""salve-se a si mesmo, se é o Cristo de Deus, o Escolhido"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25972, 36, "Os soldados, aproximando-se, também zombavam dele. Oferecendo-lhe vinagre,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25973, 37, "diziam: ""Se você é o rei dos judeus, salve-se a si mesmo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25974, 38, "Havia uma inscrição acima dele, que dizia: ESTE É O REI DOS JUDEUS.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25975, 39, "Um dos criminosos que ali estavam dependurados lançava-lhe insultos: ""Você não é o Cristo? Salve-se a si mesmo e a nós! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25976, 40, "Mas o outro criminoso o repreendeu, dizendo: ""Você não teme a Deus, nem estando sob a mesma sentença?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25977, 41, "Nós estamos sendo punidos com justiça, porque estamos recebendo o que os nossos atos merecem. Mas este homem não cometeu nenhum mal"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25978, 42, "Então ele disse: ""Jesus, lembra-te de mim quando entrares no teu Reino"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25979, 43, "Jesus lhe respondeu: ""Eu lhe garanto: Hoje você estará comigo no paraíso"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25980, 44, "Já era quase meio dia, e trevas cobriram toda a terra até às três horas da tarde;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25981, 45, "o sol deixara de brilhar. E o véu do santuário rasgou-se ao meio.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25982, 46, "Jesus bradou em alta voz: ""Pai, nas tuas mãos entrego o meu espírito"". Tendo dito isso, expirou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25983, 47, "O centurião, vendo o que havia acontecido, louvou a Deus, dizendo: ""Certamente este homem era justo"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25984, 48, "E todo o povo que se havia juntado para presenciar o que estava acontecendo, ao ver isso, começou a bater no peito e a afastar-se.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25985, 49, "Mas todos os que o conheciam, inclusive as mulheres que o haviam seguido desde a Galiléia, ficaram de longe, observando essas coisas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25986, 50, "Havia um homem chamado José, membro do Conselho, homem bom e justo,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25987, 51, "que não tinha consentido na decisão e no procedimento dos outros. Ele era da cidade de Arimatéia, na Judéia, e esperava o Reino de Deus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25988, 52, "Dirigindo-se a Pilatos, pediu o corpo de Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25989, 53, "Então, desceu-o, envolveu-o num lençol de linho e o colocou num sepulcro cavado na rocha, no qual ninguém ainda fora colocado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25990, 54, "Era o Dia da Preparação, e estava para começar o sábado.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25991, 55, "As mulheres que haviam acompanhado Jesus desde a Galiléia, seguiram José e viram o sepulcro, e como o corpo de Jesus fora colocado nele.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25992, 56, "Então, foram para casa e prepararam perfumes e especiarias aromáticas. E descansaram no sábado, em obediência ao mandamento.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25993, 1, "No primeiro dia da semana, de manhã bem cedo, as mulheres tomaram as especiarias aromáticas que haviam preparado e foram ao sepulcro.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25994, 2, "Encontraram removida a pedra do sepulcro,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25995, 3, "mas, quando entraram, não encontraram o corpo do Senhor Jesus.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25996, 4, "Ficaram perplexas, sem saber o que fazer. De repente dois homens com roupas que brilhavam como a luz do sol colocaram-se ao lado delas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25997, 5, "Amedrontadas, as mulheres baixaram o rosto para o chão, e os homens lhes disseram: ""Por que vocês estão procurando entre os mortos aquele que vive?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25998, 6, "Ele não está aqui! Ressuscitou! Lembrem-se do que ele lhes disse, quando ainda estava com vocês na Galiléia:", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (25999, 7, "‘É necessário que o Filho do homem seja entregue nas mãos de homens pecadores, seja crucificado e ressuscite no terceiro dia’ "".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26000, 8, "Então se lembraram das suas palavras.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26001, 9, "Quando voltaram do sepulcro, elas contaram todas estas coisas aos Onze e a todos os outros.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26002, 10, "As que contaram estas coisas aos apóstolos foram Maria Madalena, Joana e Maria, mãe de Tiago, e as outras que estavam com elas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26003, 11, "Mas eles não acreditaram nas mulheres; as palavras delas lhes pareciam loucura.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26004, 12, "Pedro, todavia, levantou-se e correu ao sepulcro. Abaixando-se, viu as faixas de linho e mais nada; afastou-se, e voltou admirado com o que acontecera.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26005, 13, "Naquele mesmo dia, dois deles estavam indo para um povoado chamado Emaús, a onze quilômetros de Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26006, 14, "No caminho, conversavam a respeito de tudo o que havia acontecido.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26007, 15, "Enquanto conversavam e discutiam, o próprio Jesus se aproximou e começou a caminhar com eles;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26008, 16, "mas os olhos deles foram impedidos de reconhecê-lo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26009, 17, "Ele lhes perguntou: ""Sobre o que vocês estão discutindo enquanto caminham? "" Eles pararam, com os rostos entristecidos.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26010, 18, "Um deles, chamado Cleopas, perguntou-lhe: ""Você é o único visitante em Jerusalém que não sabe das coisas que ali aconteceram nestes dias? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26011, 19, """Que coisas? "", perguntou ele. ""O que aconteceu com Jesus de Nazaré"", responderam eles. ""Ele era um profeta, poderoso em palavras e em obras diante de Deus e de todo o povo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26012, 20, "Os chefes dos sacerdotes e as nossas autoridades o entregaram para ser condenado à morte, e o crucificaram;", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26013, 21, "e nós esperávamos que era ele que ia trazer a redenção a Israel. E hoje é o terceiro dia desde que tudo isso aconteceu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26014, 22, "Algumas das mulheres entre nós nos deram um susto hoje. Foram de manhã bem cedo ao sepulcro", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26015, 23, "e não acharam o corpo dele. Voltaram e nos contaram que tinham tido uma visão de anjos, que disseram que ele está vivo.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26016, 24, "Alguns dos nossos companheiros foram ao sepulcro e encontraram tudo exatamente como as mulheres tinham dito, mas não o viram"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26017, 25, "Ele lhes disse: ""Como vocês custam a entender e como demoram a crer em tudo o que os profetas falaram!", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26018, 26, "Não devia o Cristo sofrer estas coisas, para entrar na sua glória? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26019, 27, "E começando por Moisés e todos os profetas, explicou-lhes o que constava a respeito dele em todas as Escrituras.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26020, 28, "Ao se aproximarem do povoado para o qual estavam indo, Jesus fez como quem ia mais adiante.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26021, 29, "Mas eles insistiram muito com ele: ""Fique conosco, pois a noite já vem; o dia já está quase findando"". Então, ele entrou para ficar com eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26022, 30, "Quando estava à mesa com eles, tomou o pão, deu graças, partiu-o e o deu a eles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26023, 31, "Então os olhos deles foram abertos e o reconheceram, e ele desapareceu da vista deles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26024, 32, "Perguntaram-se um ao outro: ""Não estavam ardendo os nossos corações dentro de nós, enquanto ele nos falava no caminho e nos expunha as Escrituras? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26025, 33, "Levantaram-se e voltaram imediatamente para Jesuralém. Ali encontraram os Onze e os que estavam com eles reunidos,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26026, 34, "que diziam: ""É verdade! O Senhor ressuscitou e apareceu a Simão! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26027, 35, "Então os dois contaram o que tinha acontecido no caminho, e como Jesus fora reconhecido por eles quando partia o pão.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26028, 36, "Enquanto falavam sobre isso, o próprio Jesus apresentou-se entre eles e lhes disse: ""Paz seja com vocês! """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26029, 37, "Eles ficaram assustados e com medo, pensando que estavam vendo um espírito.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26030, 38, "Ele lhes disse: ""Por que vocês estão perturbados e por que se levantam dúvidas em seus corações?", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26031, 39, "Vejam as minhas mãos e os meus pés. Sou eu mesmo! Toquem-me e vejam; um espírito não tem carne nem ossos, como vocês estão vendo que eu tenho"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26032, 40, "Tendo dito isso, mostrou-lhes as mãos e os pés.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26033, 41, "E por não crerem ainda, tão cheios estavam de alegria e de espanto, ele lhes perguntou: ""Vocês têm aqui algo para comer? """, 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26034, 42, "Deram-lhe um pedaço de peixe assado,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26035, 43, "e ele o comeu na presença deles.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26036, 44, "E disse-lhes: ""Foi isso que eu lhes falei enquanto ainda estava com vocês: Era necessário que se cumprisse tudo o que a meu respeito estava escrito na Lei de Moisés, nos Profetas e nos Salmos"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26037, 45, "Então lhes abriu o entendimento, para que pudessem compreender as Escrituras.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26038, 46, "E lhes disse: ""Está escrito que o Cristo haveria de sofrer e ressuscitar dos mortos no terceiro dia,", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26039, 47, "e que em seu nome seria pregado o arrependimento para perdão de pecados a todas as nações, começando por Jerusalém.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26040, 48, "Vocês são testemunhas destas coisas.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26041, 49, "Eu lhes envio a promessa de meu Pai; mas fiquem na cidade até serem revestidos do poder do alto"".", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26042, 50, "Tendo-os levado até as proximidades de Betânia, Jesus levantou as mãos e os abençoou.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26043, 51, "Estando ainda a abençoá-los, ele os deixou e foi elevado ao céu.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26044, 52, "Então eles o adoraram e voltaram para Jerusalém com grande alegria.", 41);
INSERT INTO verses (id, number, text, chapter_id) VALUES (26045, 53, "E permaneciam constantemente no templo, louvando a Deus.", 41);