INSERT INTO verses (id, number, text, chapter_id) VALUES (2746, 1, "Da Tenda do Encontro o Senhor chamou Moisés e lhe ordenou:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2747, 2, """Diga o seguinte aos israelitas: Quando alguém trouxer um animal como oferta ao Senhor, que seja do gado ou do rebanho de ovelhas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2748, 3, """Se o holocausto for de gado, oferecerá um macho sem defeito. Ele o apresentará à entrada da Tenda do Encontro para que seja aceito pelo Senhor,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2749, 4, "e porá a mão sobre a cabeça do animal do holocausto para que seja aceito como propiciação em seu lugar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2750, 5, "Então o novilho será morto perante o Senhor, e os sacerdotes, descendentes de Arão, trarão o sangue e o derramarão nos lados do altar, que está na entrada da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2751, 6, "Depois se tirará a pele do animal, que será cortado em pedaços.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2752, 7, "Então os descendentes do sacerdote Arão acenderão o fogo do altar e arrumarão a lenha sobre o fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2753, 8, "Em seguida arrumarão os pedaços, inclusive a cabeça e a gordura, sobre a lenha que está no fogo do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2754, 9, "As vísceras e as pernas serão lavadas com água. E o sacerdote queimará tudo isso no altar. É um holocausto, oferta preparada no fogo, de aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2755, 10, """Se a oferta for um holocausto do rebanho, quer de cordeiros quer de cabritos, oferecerá um macho sem defeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2756, 11, "O animal será morto no lado norte do altar, perante o Senhor; os sacerdotes, descendentes de Arão, derramarão o sangue nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2757, 12, "Então o animal será cortado em pedaços. O sacerdote arrumará os pedaços, inclusive a cabeça e a gordura, sobre a lenha que está no fogo do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2758, 13, "As vísceras e as pernas serão lavadas com água. O sacerdote trará tudo isso como oferta e o queimará no altar. É um holocausto, oferta preparada no fogo, de aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2759, 14, """Se a sua oferta ao Senhor for um holocausto de aves, traga uma rolinha ou um pombinho.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2760, 15, "O sacerdote trará a ave ao altar, destroncará o pescoço dela e a queimará, e deixará escorrer o sangue da ave na parede do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2761, 16, "Ele retirará o papo com o seu conteúdo e o jogará ao lado leste do altar, onde ficam as cinzas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2762, 17, "Rasgará a ave pelas asas, sem dividi-la totalmente, e então o sacerdote a queimará sobre a lenha acesa no altar. É um holocausto, oferta preparada no fogo, de aroma agradável ao Senhor"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2763, 1, """Quando alguém trouxer uma oferta de cereal ao Senhor, terá que ser da melhor farinha. Sobre ela derramará óleo, colocará incenso", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2764, 2, "e a levará aos descendentes de Arão, os sacerdotes. Um deles apanhará um punhado da melhor farinha com óleo, juntamente com todo o incenso, e o queimará no altar como porção memorial. É oferta preparada no fogo, de aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2765, 3, "O que restar da oferta de cereal pertence a Arão e a seus descendentes; é parte santíssima das ofertas dedicadas ao Senhor preparadas no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2766, 4, """Se um de vocês trouxer uma oferta de cereal assada no forno, seja da melhor farinha: bolos feitos sem fermento, amassados com óleo, ou pães finos sem fermento e untados com óleo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2767, 5, "Se a sua oferta de cereal for preparada numa assadeira, seja da melhor farinha, amassada com óleo e sem fermento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2768, 6, "Divida-as em pedaços e derrame óleo sobre ela; é uma oferta de cereal.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2769, 7, "Se a sua oferta de cereal for cozida numa panela, seja da melhor farinha com óleo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2770, 8, "Traga ao Senhor a oferta de cereal feita desses ingredientes, apresente-a ao sacerdote, que a levará ao altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2771, 9, "Ele apanhará a porção memorial da oferta de cereal e a queimará no altar; é oferta preparada no fogo, de aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2772, 10, "O restante da oferta de cereal pertence a Arão e a seus descendentes; é parte santíssima das ofertas dedicadas ao Senhor preparadas no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2773, 11, """Nenhuma oferta de cereal que vocês trouxerem ao Senhor será feita com fermento, pois vocês não queimarão fermento nem mel como oferta preparada no fogo ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2774, 12, "Podem trazê-los como oferta dos primeiros frutos ao Senhor, mas não poderão oferecê-los no altar como aroma agradável.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2775, 13, "Tempere com sal todas as suas ofertas de cereal. Não exclua de suas ofertas de cereal o sal da aliança do seu Deus; acrescente sal a todas as suas ofertas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2776, 14, """Se você trouxer ao Senhor uma oferta de cereal dos primeiros frutos, ofereça grãos esmagados de cereal novo, tostados no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2777, 15, "Sobre ela derrame óleo e coloque incenso; é oferta de cereal.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2778, 16, "O sacerdote queimará a porção memorial do cereal esmagado e do óleo, juntamente com todo o incenso, como uma oferta ao Senhor, preparada no fogo. """, 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2779, 1, """Quando a oferta de alguém for sacrifício de comunhão, assim se fará: se oferecer um animal do gado, seja macho ou fêmea, apresentará ao Senhor um animal sem defeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2780, 2, "Porá a mão sobre a cabeça do animal, que será morto na entrada da Tenda do Encontro. Os descendentes de Arão, os sacerdotes, derramarão o sangue nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2781, 3, "Desse sacrifício de comunhão, oferta preparada no fogo, ele trará ao Senhor toda a gordura que cobre as vísceras e está ligada a elas,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2782, 4, "os dois rins com a gordura que os cobre e que está perto dos lombos, e o lóbulo do fígado, que ele removerá junto com os rins.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2783, 5, "Os descendentes de Arão queimarão tudo isso em cima do holocausto que está sobre a lenha acesa no altar como oferta preparada no fogo, de aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2784, 6, """Se oferecer um animal do rebanho como sacrifício de comunhão ao Senhor, traga um macho ou fêmea sem defeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2785, 7, "Se oferecer um cordeiro, apresente-o ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2786, 8, "Porá a mão sobre a cabeça do animal, que será morto diante da Tenda do Encontro. Então os descendentes de Arão derramarão o sangue nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2787, 9, "Desse sacrifício de comunhão, oferta preparada no fogo, ele trará ao Senhor a gordura, tanto a da cauda gorda cortada rente à espinha, como toda a gordura que cobre as vísceras e está ligada a elas,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2788, 10, "os dois rins com a gordura que os cobre e que está perto dos lombos, e o lóbulo do fígado, que ele removerá junto com os rins.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2789, 11, "O sacerdote os queimará no altar como alimento oferecido ao Senhor, preparado no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2790, 12, """Se a sua oferta for um cabrito, ele o apresentará ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2791, 13, "Porá a mão sobre a cabeça do animal, que será morto diante da Tenda do Encontro. Então os descendentes de Arão derramarão o sangue nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2792, 14, "Desse animal, que é uma oferta preparada no fogo, trará ao Senhor a gordura que cobre as vísceras e está ligada a elas,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2793, 15, "os dois rins com a gordura que os cobre e que está perto dos lombos, e o lóbulo do fígado, que ele removerá junto com os rins.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2794, 16, "O sacerdote os queimará no altar como alimento, como oferta feita com fogo, de aroma agradável. Toda a gordura será do Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2795, 17, """Este é um decreto perpétuo para as suas gerações, onde quer que vivam: Não comam gordura alguma, nem sangue algum"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2796, 1, "O Senhor ordenou a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2797, 2, """Diga aos israelitas: Quando alguém pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, assim se fará:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2798, 3, """Se for o sacerdote ungido que pecar, trazendo culpa sobre o povo, trará ao Senhor um novilho sem defeito como oferta pelo pecado que cometeu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2799, 4, "Apresentará ao Senhor o novilho na entrada da Tenda do Encontro. Porá a mão sobre a cabeça do novilho que será morto perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2800, 5, "Então o sacerdote ungido pegará um pouco do sangue do novilho e o levará à Tenda do Encontro;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2801, 6, "molhará o dedo no sangue e o aspergirá sete vezes perante o Senhor, diante do véu do santuário.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2802, 7, "O sacerdote porá um pouco do sangue nas pontas do altar do incenso aromático que está perante o Senhor na Tenda do Encontro. Derramará todo o restante do sangue do novilho na base do altar do holocausto, na entrada da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2803, 8, "Então retirará toda a gordura do novilho da oferta pelo pecado: a gordura que cobre as vísceras e está ligada a elas,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2804, 9, "os dois rins com a gordura que os cobre e que está perto dos lombos, e o lóbulo do fígado, que ele removerá junto com os rins,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2805, 10, "como se retira a gordura do boi sacrificado como oferta de comunhão. Então o sacerdote os queimará no altar dos holocaustos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2806, 11, "Mas o couro do novilho e toda a sua carne, bem como a cabeça e as pernas, as vísceras e os excrementos,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2807, 12, "isto é, tudo o que restar do novilho, ele os levará para fora do acampamento, a um local cerimonialmente puro, onde se lançam as cinzas. Ali os queimará sobre a lenha de uma fogueira, sobre o monte de cinzas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2808, 13, """Se for toda a comunidade de Israel que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor, ainda que não tenha consciência disso, a comunidade será culpada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2809, 14, "Quando tiver consciência do pecado que cometeu, a comunidade trará um novilho como oferta pelo pecado e o apresentará diante da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2810, 15, "As autoridades da comunidade porão as mãos sobre a cabeça do novilho perante o Senhor. E o novilho será morto perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2811, 16, "Então o sacerdote ungido levará um pouco do sangue do novilho para a Tenda do Encontro;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2812, 17, "molhará o dedo no sangue e o aspergirá sete vezes perante o Senhor, diante do véu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2813, 18, "Porá o sangue nas pontas do altar que está perante o Senhor na Tenda do Encontro e derramará todo o restante do sangue na base do altar dos holocaustos, na entrada da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2814, 19, "Então retirará toda a gordura do animal e a queimará no altar,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2815, 20, "e fará com este novilho como fez com o novilho da oferta pelo pecado. Assim o sacerdote fará propiciação por eles, e eles serão perdoados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2816, 21, "Depois levará o novilho para fora do acampamento e o queimará como queimou o primeiro. É oferta pelo pecado da comunidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2817, 22, """Quando for um líder que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor seu Deus, será culpado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2818, 23, "Quando o conscientizarem do seu pecado, trará como oferta um bode sem defeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2819, 24, "Porá a mão sobre a cabeça do bode, que será morto no local onde o holocausto é sacrificado, perante o Senhor. Esta é a oferta pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2820, 25, "Então o sacerdote pegará com o dedo um pouco do sangue da oferta pelo pecado, o porá nas pontas do altar dos holocaustos e derramará o restante do sangue na base do altar do holocausto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2821, 26, "Queimará toda a gordura no altar, como queimou a gordura do sacrifício de comunhão. Assim o sacerdote fará propiciação pelo pecado do líder, e ele será perdoado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2822, 27, """Se for alguém da comunidade que pecar sem intenção, fazendo o que é proibido em qualquer dos mandamentos do Senhor seu Deus, será culpado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2823, 28, "Quando o conscientizarem do seu pecado, trará como oferta pelo pecado que cometeu uma cabra sem defeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2824, 29, "Porá a mão sobre a cabeça do animal da oferta pelo pecado, que será morto no lugar dos holocaustos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2825, 30, "Então o sacerdote pegará com o dedo um pouco do sangue, o porá nas pontas do altar dos holocaustos e derramará o restante do sangue na base do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2826, 31, "Então retirará toda a gordura, como se retira a gordura do sacrifício de comunhão; o sacerdote a queimará no altar como aroma agradável ao Senhor. Assim o sacerdote fará propiciação por esse homem, e ele será perdoado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2827, 32, """Se trouxer uma ovelha como oferta pelo pecado, terá que ser sem defeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2828, 33, "Porá a mão sobre a cabeça do animal, que será morto como oferta pelo pecado no lugar onde é sacrificado o holocausto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2829, 34, "Então o sacerdote pegará com o dedo um pouco do sangue da oferta pelo pecado e o porá nas pontas do altar dos holocaustos, e derramará o restante do sangue na base do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2830, 35, "Retirará toda a gordura, como se retira a gordura do cordeiro do sacrifício de comunhão; o sacerdote a queimará no altar, em cima das ofertas dedicadas ao Senhor, preparadas no fogo. Assim o sacerdote fará em favor dele propiciação pelo pecado que cometeu, e ele será perdoado"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2831, 1, """Se alguém pecar porque, tendo sido testemunha de algo que viu ou soube, não o declarou, sofrerá as conseqüências da sua iniqüidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2832, 2, """Se alguém tocar qualquer coisa impura, seja um cadáver de animal selvagem ou de animal do rebanho ou de uma das pequenas criaturas que povoam a terra, ainda que não tenha consciência disso, ele se tornará impuro e será culpado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2833, 3, """Se alguém tocar impureza humana, qualquer coisa que o torne impuro, sem ter consciência disso, quando o souber será culpado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2834, 4, """Se alguém impensadamente jurar fazer algo bom ou mau, em qualquer assunto que alguém possa jurar descuidadamente, ainda que não tenha consciência disso, quando o souber será culpado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2835, 5, """Quando alguém for culpado de qualquer dessas coisas, confessará em que pecou", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2836, 6, "e, pelo pecado que cometeu, trará ao Senhor uma ovelha ou uma cabra do rebanho como oferta de reparação; e em favor dele o sacerdote fará propiciação pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2837, 7, """Se não tiver recursos para oferecer uma ovelha, trará pela culpa do seu pecado duas rolinhas ou dois pombinhos ao Senhor: um como oferta pelo pecado e o outro como holocausto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2838, 8, "Ele os trará ao sacerdote, que oferecerá primeiro o que for oferecido como sacrifício pelo pecado. E destroncará o pescoço da ave, sem arrancar-lhe a cabeça totalmente,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2839, 9, "e aspergirá no lado do altar o sangue da oferta pelo pecado e deixará escorrer o restante do sangue na base do altar. É oferta pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2840, 10, "O sacerdote então oferecerá a outra ave como holocausto, de acordo com a forma prescrita, e fará propiciação em favor dele pelo pecado que cometeu, e ele será perdoado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2841, 11, """Se, contudo, não tiver recursos para oferecer duas rolinhas ou dois pombinhos, trará como oferta pelo pecado um jarro da melhor farinha como oferta pelo pecado. Mas sobre ela não derramará óleo nem colocará incenso, porquanto é oferta pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2842, 12, "Ele a trará ao sacerdote, que apanhará um punhado dela como porção memorial e queimará essa porção no altar, em cima das ofertas dedicadas ao Senhor, preparadas no fogo. É oferta pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2843, 13, "Assim o sacerdote fará propiciação em favor dele por qualquer desses pecados que tiver cometido, e ele será perdoado. O restante da oferta pertence ao sacerdote, como no caso da oferta de cereal"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2844, 14, "O Senhor disse a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2845, 15, """Quando alguém cometer uma ofensa, pecando sem intenção em qualquer coisa consagrada ao Senhor, trará ao Senhor um carneiro do rebanho, sem defeito, avaliado em prata com base no peso padrão do santuário, como oferta pela culpa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2846, 16, "Fará restituição pelo que deixou de fazer em relação às coisas consagradas, acrescentará um quinto do valor e o entregará ao sacerdote. Este fará propiciação por ele com o carneiro da oferta pela culpa, e ele será perdoado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2847, 17, """Se alguém pecar, fazendo o que é proibido em qualquer dos mandamentos do Senhor, ainda que não o saiba, será culpado e sofrerá as conseqüências da sua iniqüidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2848, 18, "Do rebanho ele trará ao sacerdote um carneiro, sem defeito e devidamente avaliado, como oferta pela culpa. Assim o sacerdote fará propiciação em favor dele pelo erro que cometeu sem intenção, e ele será perdoado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2849, 19, "É oferta pela culpa, pois com certeza tornou-se culpado perante o Senhor"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2850, 1, "E disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2851, 2, """Se alguém pecar, cometendo uma ofensa contra o Senhor, enganando o seu próximo no que diz respeito a algo que lhe foi confiado ou deixado como penhor ou roubado, ou se lhe extorquir algo,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2852, 3, "ou se achar algum bem perdido e mentir a respeito disso, ou se jurar falsamente a respeito de qualquer coisa, cometendo pecado;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2853, 4, "quando assim pecar, tornando-se por isso culpado, terá que devolver o que roubou ou tomou mediante extorsão, ou o que lhe foi confiado, ou os bens perdidos que achou,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2854, 5, "ou qualquer coisa sobre a qual tenha jurado falsamente. Fará restituição plena, acrescentará a isso um quinto do valor e dará tudo ao proprietário no dia em que apresentar a sua oferta pela culpa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2855, 6, "E por sua culpa trará ao sacerdote uma oferta dedicada ao Senhor: um carneiro do rebanho, sem defeito e devidamente avaliado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2856, 7, "Dessa forma o sacerdote fará propiciação por ele perante o Senhor, e ele será perdoado de qualquer dessas coisas que fez e que o tornou culpado"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2857, 8, "O Senhor disse a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2858, 9, """Dê este mandamento a Arão e a seus filhos: Esta é a regulamentação acerca do holocausto: ele terá que ficar queimando até de manhã sobre as brasas do altar, onde o fogo terá que ser mantido aceso.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2859, 10, "O sacerdote vestirá suas roupas de linho e os calções de linho por baixo, retirará as cinzas do holocausto que o fogo consumiu no altar e as colocará ao lado do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2860, 11, "Depois trocará de roupa, e levará as cinzas para fora do acampamento, a um lugar cerimonialmente puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2861, 12, "Mantenha-se aceso o fogo no altar; não deve ser apagado. Toda manhã o sacerdote acrescentará lenha, arrumará o holocausto sobre o fogo e queimará sobre ele a gordura das ofertas de comunhão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2862, 13, "Mantenha-se o fogo continuamente aceso no altar; não deve ser apagado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2863, 14, """Esta é a regulamentação da oferta de cereal: os filhos de Arão a apresentarão ao Senhor, em frente do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2864, 15, "O sacerdote apanhará um punhado da melhor farinha com óleo, juntamente com todo o incenso que está sobre a oferta de cereal, e queimará no altar a porção memorial como aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2865, 16, "Arão e seus filhos comerão o restante da oferta, mas deverão comê-lo sem fermento e em lugar sagrado, no pátio da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2866, 17, "Essa oferta não será assada com fermento; eu a dei a eles como porção das ofertas feitas a mim com fogo. É santíssima, como a oferta pelo pecado e como a oferta pela culpa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2867, 18, "Somente os homens descendentes de Arão poderão comer da porção das ofertas dedicadas ao Senhor, preparadas no fogo. É um decreto perpétuo para as suas gerações. Tudo o que nelas tocar se tornará santo"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2868, 19, "O Senhor disse também a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2869, 20, """Esta é a oferta que Arão e os seus descendentes terão que trazer ao Senhor no dia em que ele for ungido: um jarro da melhor farinha, como na oferta regular de cereal, metade pela manhã e metade à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2870, 21, "Prepare-a com óleo numa assadeira; traga-a bem misturada e apresente a oferta de cereal partida em pedaços, como aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2871, 22, "Todo sacerdote ungido, descendentes de Arão, também preparará essa oferta. É a porção do Senhor por decreto perpétuo e será totalmente queimada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2872, 23, "Toda oferta de cereal do sacerdote será totalmente queimada; não será comida"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2873, 24, "O Senhor disse a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2874, 25, """Diga a Arão e aos seus filhos: Esta é a regulamentação da oferta pelo pecado: O animal da oferta pelo pecado será morto perante o Senhor no local onde é sacrificado o holocausto; é uma oferta santíssima.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2875, 26, "O sacerdote que oferecer o animal, o comerá em lugar sagrado, no pátio da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2876, 27, "Tudo o que tocar na carne se tornará santo, e se o sangue respingar na roupa, será lavada em lugar sagrado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2877, 28, "A vasilha de barro em que a carne for cozida deverá ser quebrada; mas, se for cozida numa vasilha de bronze, a vasilha deverá ser esfregada e enxaguada com água.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2878, 29, "Somente os homens da família dos sacerdotes poderão comê-la; é uma oferta santíssima.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2879, 30, "Mas toda oferta pelo pecado, cujo sangue for trazido para a Tenda do Encontro para propiciação no Lugar Santo, não será comida; terá que ser queimada"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2880, 1, """Esta é a regulamentação da oferta pela culpa, que é oferta santíssima:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2881, 2, "O animal da oferta pela culpa será morto no local onde são sacrificados os holocaustos, e seu sangue será derramado nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2882, 3, "Toda a sua gordura será oferecida: a parte gorda da cauda e a gordura que cobre as vísceras,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2883, 4, "os dois rins com a gordura que os cobre e que está perto dos lombos, e o lóbulo do fígado, que será removido juntamente com os rins.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2884, 5, "O sacerdote os queimará no altar como oferta dedicada ao Senhor, preparada no fogo. É oferta pela culpa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2885, 6, "Somente os homens da família dos sacerdotes poderão comê-la, mas deve ser comida em lugar sagrado; é oferta santíssima.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2886, 7, """A mesma regulamentação aplica-se tanto à oferta pelo pecado quanto à oferta pela culpa: a carne pertence ao sacerdote que faz propiciação pela culpa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2887, 8, "O sacerdote que oferecer um holocausto por alguém ficará com o couro do animal.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2888, 9, "Toda oferta de cereal, assada num forno ou cozida numa panela ou numa assadeira, pertence ao sacerdote que a oferecer,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2889, 10, "e toda oferta de cereal, amassada com óleo ou não, pertence igualmente aos descendentes de Arão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2890, 11, """Esta é a regulamentação da oferta de comunhão que pode ser apresentada ao Senhor:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2891, 12, """Se alguém a fizer por gratidão, então, junto com sua oferta de gratidão, terá que oferecer bolos sem fermento e amassados com óleo, pães finos sem fermento e untados com óleo, e bolos da melhor farinha bem amassados e misturados com óleo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2892, 13, "Juntamente com sua oferta de comunhão por gratidão, apresentará uma oferta que inclua bolos com fermento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2893, 14, "De cada oferta trará uma contribuição ao Senhor que será dada ao sacerdote que asperge o sangue das ofertas de comunhão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2894, 15, "A carne da sua oferta de comunhão por gratidão será comida no dia em que for oferecida; nada poderá sobrar até o amanhecer.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2895, 16, """Se, contudo, sua oferta for resultado de um voto ou for uma oferta voluntária, a carne do sacrifício será comida no dia em que for oferecida, e o que sobrar poderá ser comido no dia seguinte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2896, 17, "Mas a carne que sobrar do sacrifício até o terceiro dia será queimada no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2897, 18, "Se a carne da oferta de comunhão for comida ao terceiro dia, ela não será aceita. A oferta não será atribuída àquele que a ofereceu, pois a carne estará estragada; e quem dela comer sofrerá as conseqüências da sua iniqüidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2898, 19, """A carne que tocar em qualquer coisa impura não será comida; será queimada no fogo. A carne do sacrifício, porém, poderá ser comida por quem estiver puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2899, 20, "Mas se alguém que está impuro comer da carne da oferta de comunhão que pertence ao Senhor, será eliminado do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2900, 21, "Se alguém tocar em alguma coisa impura, seja impureza humana, seja de animal, seja qualquer outra coisa impura e proibida, e comer da carne da oferta de comunhão que pertence ao Senhor, será eliminado do meio do seu povo. """, 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2901, 22, "E disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2902, 23, """Diga aos israelitas: Não comam gordura alguma de boi, carneiro ou cabrito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2903, 24, "A gordura de um animal encontrado morto ou despedaçado por animais selvagens pode ser usada para qualquer outra finalidade, mas nunca poderá ser comida.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2904, 25, "Quem comer a gordura de um animal, dedicado ao Senhor numa oferta preparada no fogo, será eliminado do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2905, 26, "Onde quer que vocês vivam, não comam o sangue de nenhuma ave nem de animal.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2906, 27, "Quem comer sangue será eliminado do meio do seu povo"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2907, 28, "Disse mais o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2908, 29, """Diga aos israelitas: Todo aquele que trouxer sacrifício de comunhão ao Senhor terá que dedicar parte dele ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2909, 30, "Com suas próprias mãos trará ao Senhor as ofertas preparadas no fogo; trará a gordura juntamente com o peito, e o moverá perante o Senhor como gesto ritual de apresentação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2910, 31, "O sacerdote queimará a gordura no altar, mas o peito pertence a Arão e a seus descendentes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2911, 32, "Vocês deverão dar a coxa direita das ofertas de comunhão ao sacerdote como contribuição.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2912, 33, "O descendente de Arão que oferecer o sangue e a gordura da oferta de comunhão receberá a coxa direita como porção.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2913, 34, "Das ofertas de comunhão dos israelitas, tomei o peito que é movido ritualmente e a coxa que é ofertada, e os dei ao sacerdote Arão e a seus descendentes por decreto perpétuo para os israelitas"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2914, 35, "Essa é a parte das ofertas dedicadas ao Senhor, preparadas no fogo, designada a Arão e a seus filhos no dia em que foram apresentados para servirem ao Senhor como sacerdotes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2915, 36, "Foi isso que o Senhor ordenou dar a eles, no dia em que foram ungidos dentre os israelitas. É um decreto perpétuo para as suas gerações.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2916, 37, "Essa é a regulamentação acerca do holocausto, da oferta de cereal, da oferta pelo pecado, da oferta pela culpa, da oferta de ordenação e da oferta de comunhão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2917, 38, "O Senhor entregou-a a Moisés no monte Sinai, no dia em que ordenou aos israelitas que trouxessem suas ofertas ao Senhor, no deserto do Sinai.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2918, 1, "O Senhor disse a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2919, 2, """Traga Arão e seus filhos, suas vestes, o óleo da unção, o novilho para a oferta pelo pecado, os dois carneiros e o cesto de pães sem fermento;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2920, 3, "e reúna toda a comunidade à entrada da Tenda do Encontro"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2921, 4, "Moisés fez como o Senhor lhe ordenou, e a comunidade reuniu-se à entrada da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2922, 5, "Então Moisés disse à comunidade: ""Foi isto que o Senhor mandou fazer"";", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2923, 6, "e levou Arão e seus filhos à frente e mandou-os banhar-se com água;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2924, 7, "pôs a túnica em Arão, colocou-lhe o cinto e o manto, e pôs sobre ele o colete sacerdotal; depois a ele prendeu o manto sacerdotal com o cinturão;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2925, 8, "colocou também o peitoral, e nele pôs o Urim e o Tumim;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2926, 9, "e colocou o turbante na cabeça de Arão com a lâmina de ouro, isto é, a coroa sagrada, na frente do turbante, conforme o Senhor tinha ordenado a Moisés.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2927, 10, "Depois Moisés pegou o óleo da unção e ungiu o tabernáculo e tudo o que nele havia, e assim os consagrou.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2928, 11, "Aspergiu sete vezes o óleo sobre o altar, ungindo o altar e todos os seus utensílios e a bacia com o seu suporte, para consagrá-los.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2929, 12, "Derramou o óleo da unção sobre a cabeça de Arão para ungi-lo e consagrá-lo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2930, 13, "Trouxe então os filhos de Arão à frente, vestiu-os com suas túnicas e cintos, e colocou-lhes gorros, conforme o Senhor lhe havia ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2931, 14, "Em seguida trouxe o novilho para a oferta pelo pecado, e Arão e seus filhos puseram-lhe as mãos sobre a cabeça.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2932, 15, "Moisés sacrificou o novilho, e com o dedo pôs um pouco do sangue em todas as pontas do altar para purificá-lo. Derramou o restante do sangue na base do altar e assim o consagrou para fazer propiciação por ele.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2933, 16, "Moisés pegou também toda a gordura que cobre as vísceras, o lóbulo do fígado e os dois rins com a gordura que os cobre, e os queimou no altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2934, 17, "Mas o novilho com o seu couro, a sua carne e o seu excremento, ele os queimou fora do acampamento, conforme o Senhor lhe havia ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2935, 18, "Mandou trazer então o carneiro para o holocausto, e Arão e seus filhos puseram as mãos sobre a cabeça do carneiro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2936, 19, "A seguir Moisés sacrificou o carneiro e derramou o sangue nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2937, 20, "Depois, cortou o carneiro em pedaços; queimou a cabeça, os pedaços e a gordura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2938, 21, "Lavou as vísceras e as pernas, e queimou o carneiro inteiro sobre o altar, como holocausto, oferta de aroma agradável ao Senhor, preparado no fogo, conforme o Senhor lhe havia ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2939, 22, "Depois mandou trazer o outro carneiro, o carneiro para a oferta de ordenação, e Arão e seus filhos colocaram as mãos sobre a cabeça do carneiro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2940, 23, "Moisés sacrificou o carneiro e pôs um pouco do sangue na ponta da orelha direita de Arão, no polegar da sua mão direita e no polegar do seu pé direito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2941, 24, "Moisés também mandou que os filhos de Arão se aproximassem, e sobre cada um pôs um pouco do sangue na ponta da orelha direita, no polegar da mão direita e no polegar do pé direito; e derramou o restante do sangue nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2942, 25, "Apanhou a gordura, a cauda gorda, toda a gordura que cobre as vísceras, o lóbulo do fígado, os dois rins e a gordura que os cobre e a coxa direita.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2943, 26, "Então, do cesto de pães sem fermento, que estava perante o Senhor, apanhou um pão comum, outro feito com óleo e um pão fino, colocando-os sobre as porções de gordura e sobre a coxa direita.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2944, 27, "Pôs tudo nas mãos de Arão e de seus filhos e os moveu perante o Senhor como gesto ritual de apresentação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2945, 28, "Depois Moisés pegou de volta das mãos deles e queimou tudo no altar, em cima do holocausto, como uma oferta de ordenação, preparada no fogo, de aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2946, 29, "Moisés também pegou o peito e a sua própria porção do carneiro da ordenação, e o moveu perante o Senhor como gesto ritual de apresentação, como o Senhor lhe havia ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2947, 30, "Então pegou um pouco do óleo da unção e um pouco do sangue que estava no altar e os aspergiu sobre Arão e suas vestes, bem como sobre seus filhos e suas vestes. Assim consagrou Arão e suas vestes, e seus filhos e suas vestes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2948, 31, "Moisés então disse a Arão e a seus filhos: ""Cozinhem a carne na entrada da Tenda do Encontro, onde a deverão comer com o pão do cesto das ofertas de ordenação, conforme me foi ordenado: ‘Arão e seus filhos deverão comê-la’.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2949, 32, "Depois queimem o restante da carne e do pão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2950, 33, "Não saiam da entrada da Tenda do Encontro por sete dias, até que se completem os dias da ordenação de vocês, pois essa cerimônia de ordenação durará sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2951, 34, "O que se fez hoje foi ordenado pelo Senhor para fazer propiciação por vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2952, 35, "Vocês terão que permanecer dia e noite à entrada da Tenda do Encontro por sete dias e obedecer às exigências do Senhor, para que não morram; pois isso me foi ordenado"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2953, 36, "Arão e seus filhos fizeram tudo o que o Senhor tinha ordenado por meio de Moisés.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2954, 1, "Oito dias depois Moisés convocou Arão e seus filhos e as autoridades de Israel.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2955, 2, "E disse a Arão: ""Traga um bezerro para a oferta pelo pecado e um carneiro para o holocausto, ambos sem defeito, e apresente-os ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2956, 3, "Depois diga aos israelitas: Tragam um bode para oferta pelo pecado, um bezerro e um cordeiro, ambos de um ano de idade e sem defeito, para holocausto;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2957, 4, "e um boi e um carneiro para oferta de comunhão, para os sacrificar perante o Senhor, juntamente com a oferta de cereal amassada com óleo; pois hoje o Senhor aparecerá a vocês"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2958, 5, "Levaram então tudo o que Moisés determinou para a frente da Tenda do Encontro, e a comunidade inteira aproximou-se e ficou de pé perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2959, 6, "Disse-lhes Moisés: ""Foi isso que o Senhor ordenou que façam, para que a glória do Senhor apareça a vocês"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2960, 7, "Disse Moisés a Arão: ""Venha até o altar e ofereça o seu sacrifício pelo pecado e o seu holocausto, e faça propiciação por você mesmo e pelo povo; ofereça o sacrifício pelo povo e faça propiciação por ele, conforme o Senhor ordenou"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2961, 8, "Arão foi até o altar e ofereceu o bezerro como sacrifício pelo pecado por si mesmo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2962, 9, "Seus filhos levaram-lhe o sangue, e ele molhou o dedo no sangue e o pôs nas pontas do altar; então derramou o restante do sangue na base do altar,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2963, 10, "onde queimou a gordura, os rins e o lóbulo do fígado da oferta pelo pecado, conforme o Senhor tinha ordenado a Moisés;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2964, 11, "a carne e o couro, porém, queimou fora do acampamento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2965, 12, "Depois sacrificou o holocausto. Seus filhos lhe entregaram o sangue, e ele o derramou nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2966, 13, "Entregaram-lhe em seguida o holocausto pedaço por pedaço, inclusive a cabeça, e ele os queimou no altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2967, 14, "Lavou as vísceras e as pernas e as queimou em cima do holocausto sobre o altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2968, 15, "Depois Arão apresentou a oferta pelo povo. Pegou o bode para a oferta pelo pecado do povo e o ofereceu como sacrifício pelo pecado, como fizera com o primeiro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2969, 16, "Apresentou o holocausto e ofereceu-o conforme fora prescrito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2970, 17, "Também apresentou a oferta de cereal, pegou um punhado dela e a queimou no altar, além do holocausto da manhã.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2971, 18, "Matou o boi e o carneiro como sacrifício de comunhão pelo povo. Seus filhos levaram-lhe o sangue, e ele o derramou nos lados do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2972, 19, "Mas as porções de gordura do boi e do carneiro, a cauda gorda, a gordura que cobre as vísceras, os rins e o lóbulo do fígado,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2973, 20, "puseram em cima do peito; e Arão as queimou no altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2974, 21, "Em seguida, Arão moveu o peito e a coxa direita do animal perante o Senhor como gesto ritual de apresentação, conforme Moisés tinha ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2975, 22, "Depois Arão ergueu as mãos em direção ao povo e o abençoou. E, tendo oferecido o sacrifício pelo pecado, o holocausto e o sacrifício de comunhão, desceu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2976, 23, "Assim Moisés e Arão entraram na Tenda do Encontro. Quando saíram, abençoaram o povo; e a glória do Senhor apareceu a todo o povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2977, 24, "Saiu fogo da presença do Senhor e consumiu o holocausto e as porções de gordura sobre o altar. E, quando todo o povo viu isso, gritou de alegria e prostrou-se rosto em terra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2978, 1, "Nadabe e Abiú, filhos de Arão, pegaram cada um o seu incensário, nos quais acenderam fogo, acrescentaram incenso, e trouxeram fogo profano perante o Senhor, sem que tivessem sido autorizados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2979, 2, "Então saiu fogo da presença do Senhor e os consumiu. Morreram perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2980, 3, "Moisés então disse a Arão: ""Foi isto que o Senhor disse: ‘Aos que de mim se aproximam santo me mostrarei; à vista de todo o povo glorificado serei’ "". Arão, porém, ficou em silêncio.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2981, 4, "Então Moisés chamou Misael e Elzafã, filhos de Uziel, tio de Arão, e lhes disse: ""Venham cá; tirem os seus primos da frente do santuário e levem-nos para fora do acampamento"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2982, 5, "Eles foram e os puxaram pelas túnicas, para fora do acampamento, conforme Moisés tinha ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2983, 6, "Então Moisés disse a Arão e a seus filhos Eleazar e Itamar: ""Não andem descabelados, nem rasguem as roupas em sinal de luto, senão vocês morrerão e a ira do Senhor cairá sobre toda a comunidade. Mas os seus parentes, e toda a nação de Israel, poderão chorar por aqueles que o Senhor destruiu pelo fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2984, 7, "Não saiam da entrada da Tenda do Encontro, senão vocês morrerão, porquanto o óleo da unção do Senhor está sobre vocês"". E eles fizeram conforme Moisés tinha ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2985, 8, "Depois o Senhor disse a Arão:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2986, 9, """Você e seus filhos não devem beber vinho nem outra bebida fermentada antes de entrar na Tenda do Encontro, senão vocês morrerão. É um decreto perpétuo para as suas gerações.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2987, 10, "Vocês têm que fazer separação entre o santo e o profano, entre o puro e o impuro,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2988, 11, "e ensinar aos israelitas todos os decretos que o Senhor lhes deu por meio de Moisés"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2989, 12, "Então Moisés disse a Arão e aos seus filhos que ficaram vivos, Eleazar e Itamar: ""Peguem a oferta de cereal que sobrou das ofertas dedicadas ao Senhor, preparadas no fogo, e comam-na sem fermento junto ao altar, pois é santíssima.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2990, 13, "Comam-na em lugar sagrado, porquanto é a porção que lhes cabe por decreto, a você e a seus filhos, das ofertas dedicadas ao Senhor, preparadas no fogo; pois assim me foi ordenado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2991, 14, "O peito ritualmente movido e a coxa ofertada, você, seus filhos e suas filhas poderão comer num lugar cerimonialmente puro; foi dado a você e a seus filhos como porção das ofertas de comunhão dos israelitas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2992, 15, "A coxa ofertada e o peito ritualmente movido devem ser trazidos junto com as porções de gordura das ofertas preparadas no fogo, para serem movidos perante o Senhor como gesto ritual de apresentação. Esta será a porção por decreto perpétuo para você e seus descendentes, conforme o Senhor tinha ordenado"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2993, 16, "Quando Moisés procurou por toda parte o bode da oferta pelo pecado e soube que já fora queimado, irou-se contra Eleazar e Itamar, os filhos de Arão que ficaram vivos, e perguntou:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2994, 17, """Por que vocês não comeram a carne da oferta pelo pecado no Lugar Santo? É santíssima; foi-lhes dada para retirar a culpa da comunidade e fazer propiciação por ela perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2995, 18, "Como o sangue do animal não foi levado para dentro do Lugar Santo, vocês deviam tê-lo comido no Lugar Santo, conforme ordenei"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2996, 19, "Arão respondeu a Moisés: ""Hoje eles ofereceram o seu sacrifício pelo pecado e o seu holocausto perante o Senhor; mesmo assim coisas como essas aconteceram comigo. Será que teria agradado ao Senhor se eu tivesse comido a oferta pelo pecado hoje? """, 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2997, 20, "Quando Moisés ouviu isso, ficou satisfeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2998, 1, "Disse o Senhor a Moisés e a Arão:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (2999, 2, """Digam aos israelitas: De todos os animais que vivem na terra, estes são os que vocês poderão comer:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3000, 3, "Qualquer animal que tem casco fendido e dividido em duas unhas, e que rumina.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3001, 4, """Vocês não poderão comer aqueles que só ruminam nem os que só têm o casco fendido. O camelo, embora rumine, não tem casco fendido; considerem-no impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3002, 5, "O coelho, embora rumine, não tem casco fendido; é impuro para vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3003, 6, "A lebre, embora rumine, não tem casco fendido; considerem-na impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3004, 7, "E o porco, embora tenha casco fendido e dividido em duas unhas, não rumina; considerem-no impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3005, 8, "Vocês não comerão a carne desses animais nem tocarão em seus cadáveres; considerem-nos impuros.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3006, 9, """De todas as criaturas que vivem nas águas do mar e dos rios, vocês poderão comer todas as que possuem barbatanas e escamas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3007, 10, "Mas todas as criaturas que vivem nos mares ou nos rios, que não possuem barbatanas e escamas, quer dentre todas as pequenas criaturas que povoam as águas quer dentre todos os outros animais das águas, serão proibidas para vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3008, 11, "Por isso, não poderão comer sua carne e considerarão impuros os seus cadáveres.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3009, 12, "Tudo o que vive na água e não possui barbatanas e escamas será proibido para vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3010, 13, """Estas são as aves que vocês considerarão impuras, das quais não poderão comer porque são proibidas: a águia, o urubu, a águia-marinha,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3011, 14, "o milhafre, o falcão,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3012, 15, "qualquer espécie de corvo,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3013, 16, "a coruja-de-chifre, a coruja-de-orelha-pequena, a coruja-orelhuda, qualquer espécie de gavião,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3014, 17, "o mocho, a coruja-pescadora e o corujão,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3015, 18, "a coruja-branca, a coruja-do-deserto, o abutre,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3016, 19, "a cegonha, qualquer tipo de garça, a poupa e o morcego.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3017, 20, """Todas as pequenas criaturas que enxameiam, que têm asas, mas que se movem pelo chão serão proibidas para vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3018, 21, "Dentre estas, porém, vocês poderão comer aquelas que têm pernas articuladas para saltar no chão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3019, 22, "Dessas vocês poderão comer os diversos tipos de gafanhotos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3020, 23, "Mas considerarão impuras todas as outras criaturas que enxameiam, que têm asas e que se movem pelo chão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3021, 24, """Por meio deles vocês ficarão impuros; todo aquele que tocar em seus cadáveres estará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3022, 25, "Todo o que carregar o cadáver de algum deles lavará as suas roupas e estará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3023, 26, """Todo animal de casco não dividido em duas unhas ou que não rumina é impuro para vocês; quem tocar qualquer um deles ficará impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3024, 27, "Todos os animais de quatro pés, que andam sobre a planta dos pés são impuros para vocês; todo o que tocar os seus cadáveres ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3025, 28, "Quem carregar o cadáver de algum deles lavará suas roupas, e estará impuro até à tarde. São impuros para vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3026, 29, """Dos animais que se movem rente ao chão, estes vocês considerarão impuros: a doninha, o rato, qualquer espécie de lagarto grande,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3027, 30, "a lagartixa, o lagarto-pintado, o lagarto, o lagarto da areia e o camaleão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3028, 31, "De todos os que se movem rente ao chão, esses vocês considerarão impuros. Quem neles tocar depois de mortos estará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3029, 32, "E tudo sobre o que um deles cair depois de morto, qualquer que seja o seu uso, ficará impuro, seja objeto feito de madeira, de pano, de couro ou de pano de saco. Deverá ser posto em água e estará impuro até a tarde, e então ficará puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3030, 33, "Se um deles cair dentro de uma vasilha de barro, tudo o que nela houver ficará impuro, e vocês quebrarão a vasilha.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3031, 34, "Qualquer alimento sobre o qual cair essa água, ficará impuro, e qualquer bebida que estiver dentro da vasilha, ficará impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3032, 35, "Tudo aquilo sobre o que o cadáver de um desses animais cair ficará impuro; se for um forno ou um fogão de barro vocês o quebrarão. Estão impuros, e vocês os considerarão como tais.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3033, 36, "Mas, se cair numa fonte ou numa cisterna onde se recolhe água, ela permanece pura; mas quem tocar no cadáver ficará impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3034, 37, "Se um cadáver cair sobre alguma semente a ser plantada, ela permanece pura;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3035, 38, "mas se foi derramada água sobre a semente, vocês a considerarão impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3036, 39, """Quando morrer um animal que vocês têm permissão para comer, quem tocar no seu cadáver ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3037, 40, "Quem comer da carne do animal morto terá que lavar as suas roupas e ficará impuro até à tarde. Quem carregar o cadáver do animal terá que lavar as suas roupas, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3038, 41, """Todo animal que se move rente ao chão lhes será proibido e não poderá ser comido.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3039, 42, "Vocês não poderão comer animal algum que se move rente ao chão, quer se arraste sobre o ventre quer ande de quatro ou com o auxílio de muitos pés; eles lhes são proibidos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3040, 43, "Não se contaminem com qualquer desses animais. Não se tornem impuros com eles nem deixem que eles os tornem impuros.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3041, 44, "Pois eu sou o Senhor Deus de vocês; consagrem-se e sejam santos, porque eu sou santo. Não se tornem impuros com qualquer animal que se move rente ao chão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3042, 45, "Eu sou o Senhor que os tirou da terra do Egito para ser o seu Deus; por isso, sejam santos, porque eu sou santo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3043, 46, """Essa é a regulamentação acerca dos animais, das aves, de todos os seres vivos que se movem na água e de todo animal que se move rente ao chão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3044, 47, "Vocês farão separação entre o impuro e o puro, entre animais que podem e os que não podem ser comidos"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3045, 1, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3046, 2, """Diga aos israelitas: Quando uma mulher engravidar e der à luz um menino, estará impura por sete dias, assim como está impura durante o seu período menstrual.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3047, 3, "No oitavo dia o menino terá que ser circuncidado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3048, 4, "Então a mulher aguardará trinta e três dias para ser purificada do seu sangramento. Não poderá tocar em nenhuma coisa sagrada e não poderá ir ao santuário, até que se completem os dias da sua purificação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3049, 5, "Se der à luz uma menina, estará impura por duas semanas, como durante o seu período menstrual. Nesse caso aguardará sessenta e seis dias para ser purificada do seu sangramento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3050, 6, """Quando se completarem os dias da sua purificação pelo nascimento de um menino ou de uma menina, ela trará ao sacerdote, à entrada da Tenda do Encontro, um cordeiro de um ano para o holocausto e um pombinho ou uma rolinha como oferta pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3051, 7, "Ele os oferecerá ao Senhor para fazer propiciação por ela, que ficará pura do fluxo do seu sangramento. Essa é a regulamentação para a mulher que der à luz um menino ou uma menina.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3052, 8, "Se ela não tiver recursos para oferecer um cordeiro, poderá trazer duas rolinhas ou dois pombinhos, um para o holocausto e o outro para a oferta pelo pecado. Assim o sacerdote fará propiciação por ela, e ela ficará pura"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3053, 1, "Disse o Senhor a Moisés e a Arão:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3054, 2, """Quando alguém tiver um inchaço, uma erupção ou uma mancha brilhante na pele que possa ser sinal de lepra, será levado ao sacerdote Arão ou a um dos seus filhos que seja sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3055, 3, "Este examinará a parte afetada da pele, e, se naquela parte o pêlo tiver se tornado branco e o lugar parecer mais profundo do que a pele, é sinal de lepra. Depois de examiná-lo, o sacerdote o declarará impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3056, 4, "Se a mancha na pele for branca, mas não parecer mais profunda do que a pele e sobre ela o pêlo não tiver se tornado branco, o sacerdote o porá em isolamento por sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3057, 5, "No sétimo dia o sacerdote o examinará, e, se verificar que a parte afetada não se alterou nem se espalhou pela pele, o manterá em isolamento por mais sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3058, 6, "Ao sétimo dia o sacerdote o examinará de novo, e, se a parte afetada diminuiu e não se espalhou pela pele, o sacerdote o declarará puro; é apenas uma erupção. Então ele lavará as suas roupas, e estará puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3059, 7, "Mas, se depois que se apresentou ao sacerdote para ser declarado puro a erupção se espalhar pela pele, ele terá que se apresentar novamente ao sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3060, 8, "O sacerdote o examinará, e, se a erupção espalhou-se pela pele, ele o declarará impuro; trata-se de lepra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3061, 9, """Quando alguém apresentar sinal de lepra, será levado ao sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3062, 10, "Este o examinará, e, se houver inchaço branco na pele, o qual tornou branco o pêlo, e se houver carne viva no inchaço,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3063, 11, "é lepra crônica na pele, e o sacerdote o declarará impuro. Não o porá em isolamento, porquanto já está impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3064, 12, """Se a doença se alastrar por toda a pele e cobrir toda a pele da pessoa infectada, da cabeça aos pés, até onde é possível ao sacerdote verificar,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3065, 13, "este o examinará e, se observar que a lepra cobriu todo o corpo, ele a declarará pura. Visto que tudo ficou branco, ela está pura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3066, 14, "Mas quando nela aparecer carne viva, ficará impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3067, 15, "Quando o sacerdote vir a carne viva, ele a declarará impura. A carne viva é impura; trata-se de lepra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3068, 16, "Se a carne viva retroceder e a pele se tornar branca, a pessoa voltará ao sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3069, 17, "Este a examinará, e, se a parte afetada se tornou branca, o sacerdote declarará pura a pessoa infectada, que então estará pura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3070, 18, """Quando alguém tiver uma ferida purulenta em sua pele e ela sarar,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3071, 19, "e no lugar da ferida aparecer um inchaço branco ou uma mancha avermelhada, ele se apresentará ao sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3072, 20, "Este examinará o local, e, se parecer mais profundo do que a pele e o pêlo ali tiver se tornado branco, o sacerdote o declarará impuro. É sinal de lepra que se alastrou onde estava a ferida.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3073, 21, "Mas se, quando o sacerdote o examinar não houver nenhum pêlo branco e o lugar não estiver mais profundo do que a pele e tiver diminuído, então o sacerdote o porá em isolamento por sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3074, 22, "Se de fato estiver se espalhando pela pele, o sacerdote o declarará impuro; é sinal de lepra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3075, 23, "Mas, se a mancha não tiver se alterado nem se espalhado, é apenas a cicatriz da ferida, e o sacerdote o declarará puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3076, 24, """Quando alguém tiver uma queimadura na pele, e uma mancha avermelhada ou branca aparecer na carne viva da queimadura,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3077, 25, "o sacerdote examinará a mancha, e, se o pêlo sobre ela tiver se tornado branco e ela parecer mais profunda do que a pele, é lepra que surgiu na queimadura. O sacerdote o declarará impuro; é sinal de lepra na pele.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3078, 26, "Mas, se o sacerdote examinar a mancha e nela não houver pêlo branco e esta não estiver mais profunda do que a pele e tiver diminuído, então o sacerdote o porá em isolamento por sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3079, 27, "No sétimo dia o sacerdote o examinará, e, se a mancha estiver se espalhado pela pele, o sacerdote o declarará impuro; é sinal de lepra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3080, 28, "Se, todavia, a mancha não tiver se alterado nem se espalhado pela pele, mas tiver diminuído, é um inchaço da queimadura, e o sacerdote o declarará puro; é apenas a cicatriz da queimadura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3081, 29, """Quando um homem ou uma mulher tiver uma ferida na cabeça ou no queixo,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3082, 30, "o sacerdote examinará a ferida, e, se ela parecer mais profunda do que a pele e o pêlo nela for amarelado e fino, o sacerdote declarará impura aquela pessoa; é sarna, isto é, lepra da cabeça ou do queixo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3083, 31, "Mas, se quando o sacerdote examinar o sinal de sarna, este não parecer mais profundo do que a pele e não houver pêlo escuro nela, então o sacerdote porá a pessoa infectada em isolamento por sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3084, 32, "No sétimo dia o sacerdote examinará a parte afetada, e, se a sarna não tiver se espalhado e não houver pêlo amarelado nela e não parecer mais profunda do que a pele,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3085, 33, "a pessoa rapará os pêlos, exceto na parte afetada, e o sacerdote a porá em isolamento por mais sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3086, 34, "No sétimo dia o sacerdote examinará a sarna, e, se não tiver se espalhado mais e não parecer mais profunda do que a pele, o sacerdote declarará pura a pessoa. Esta lavará suas roupas e estará pura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3087, 35, "Mas, se a sarna se espalhar pela pele depois que a pessoa for declarada pura,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3088, 36, "o sacerdote a examinará, e, se a sarna tiver se espalhado pela pele, o sacerdote não precisará procurar pêlo amarelado; a pessoa está impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3089, 37, "Se, entretanto, verificar que não houve alteração e cresceu pêlo escuro, a sarna está curada. Ela está pura, e o sacerdote a declarará pura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3090, 38, """Quando um homem ou uma mulher tiver manchas brancas na pele,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3091, 39, "o sacerdote examinará as manchas; se forem brancas e sem brilho, é um eczema que se alastrou; essa pessoa está pura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3092, 40, """Quando os cabelos de um homem caírem, ele está calvo, todavia puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3093, 41, "Se lhe caírem os cabelos da frente da cabeça, ele está meio-calvo, porém puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3094, 42, "Mas, se tiver uma ferida avermelhada na parte calva da frente ou de trás da cabeça, é lepra que se alastra pela calva da frente ou de trás da cabeça.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3095, 43, "O sacerdote o examinará, e, se a ferida inchada na parte da frente ou de trás da calva for avermelhada como a lepra de pele,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3096, 44, "o homem está leproso e impuro. O sacerdote terá que declará-lo impuro devido à ferida na cabeça.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3097, 45, """Quem ficar leproso, apresentando quaisquer desses sintomas, usará roupas rasgadas, andará descabelado, cobrirá a parte inferior do rosto e gritará: ‘Impuro! Impuro! ’", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3098, 46, "Enquanto tiver a doença, estará impuro. Viverá separado, fora do acampamento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3099, 47, """Quando aparecer mancha de mofo em alguma roupa, seja roupa de lã, seja de linho,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3100, 48, "ou em qualquer peça tecida ou entrelaçada de linho ou de lã, ou em algum pedaço ou objeto de couro,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3101, 49, "se a mancha na roupa, ou no pedaço de couro, ou na peça tecida ou entrelaçada, ou em qualquer objeto de couro, for esverdeada ou avermelhada, é mancha de mofo que deverá ser mostrada ao sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3102, 50, "O sacerdote examinará a mancha e isolará o objeto afetado por sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3103, 51, "No sétimo dia examinará a mancha, e, se ela tiver se espalhado pela roupa, ou pela peça tecida ou entrelaçada, ou pelo pedaço de couro, qualquer que seja o seu uso, é mofo corrosivo; o objeto está impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3104, 52, "Ele queimará a roupa, ou a peça tecida ou entrelaçada, ou qualquer objeto de couro que tiver a mancha, pois é mofo corrosivo; o objeto será queimado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3105, 53, """Mas se, quando o sacerdote o examinar, a mancha não tiver se espalhado pela roupa, ou pela peça tecida ou entrelaçada, ou pelo objeto de couro,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3106, 54, "ordenará que o objeto afetado seja lavado. Então ele o isolará por mais sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3107, 55, "Depois de lavado o objeto afetado, o sacerdote o examinará, e, se a mancha não tiver alterado sua cor, ainda que não tenha se espalhado, o objeto estará impuro. Queime-o com fogo, quer o mofo corrosivo tenha afetado um lado, quer o outro do objeto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3108, 56, "Se quando o sacerdote o examinar, a mancha tiver diminuído depois de lavado o objeto, ele cortará a parte afetada da roupa, ou do pedaço de couro, ou da peça tecida ou entrelaçada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3109, 57, "Mas, se a mancha ainda aparecer na roupa, ou na peça tecida ou entrelaçada, ou no objeto de couro, é mofo que se alastra, e tudo o que tiver o mofo será queimado com fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3110, 58, "Mas se, depois de lavada, a mancha desaparecer da roupa, ou da peça tecida ou entrelaçada, ou do objeto de couro, será lavado de novo, e então estará puro"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3111, 59, "Essa é a regulamentação acerca da mancha de mofo nas roupas de lã ou de linho, nas peças tecidas ou entrelaçadas, ou nos objetos de couro, para declará-los puros ou impuros.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3112, 1, "Disse também o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3113, 2, """Esta é a regulamentação acerca da purificação de um leproso: ele será levado ao sacerdote,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3114, 3, "que sairá do acampamento e o examinará. Se a pessoa foi curada da lepra,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3115, 4, "o sacerdote ordenará que duas aves puras, vivas, um pedaço de madeira de cedro, um pano vermelho e um ramo de hissopo sejam trazidos em favor daquele que será purificado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3116, 5, "Então o sacerdote ordenará que uma das aves seja morta numa vasilha de barro com água da fonte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3117, 6, "Então pegará a ave viva e a molhará, juntamente com o pedaço de madeira de cedro, com o pano vermelho e com o ramo de hissopo, no sangue da ave morta em água corrente.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3118, 7, "Sete vezes ele aspergirá aquele que está sendo purificado da lepra e o declarará puro. Depois soltará a ave viva em campo aberto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3119, 8, """Aquele que estiver sendo purificado lavará as suas roupas, rapará todos os seus pêlos e se banhará com água; e assim estará puro. Depois disso poderá entrar no acampamento, mas ficará fora da sua tenda por sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3120, 9, "No sétimo dia rapará todos os seus pêlos: o cabelo, a barba, as sobrancelhas e o restante dos pêlos. Lavará suas roupas e banhará o corpo com água; então ficará puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3121, 10, """No oitavo dia pegará dois cordeiros sem defeito e uma cordeira de um ano sem defeito, juntamente com três jarros da melhor farinha amassada com óleo, como oferta de cereal, e uma caneca de óleo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3122, 11, "O sacerdote que faz a purificação apresentará ao Senhor, à entrada da Tenda do Encontro, tanto aquele que estiver para ser purificado como as suas ofertas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3123, 12, """Então o sacerdote pegará um dos cordeiros e o sacrificará como oferta pela culpa, juntamente com a caneca de óleo; ele os moverá perante o Senhor como gesto ritual de apresentação e", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3124, 13, "matará o cordeiro no Lugar Santo, onde são sacrificados a oferta pelo pecado e o holocausto. Como se dá com a oferta pelo pecado, também a oferta pela culpa pertence ao sacerdote; é santíssima.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3125, 14, "O sacerdote porá um pouco do sangue da oferta pela culpa na ponta da orelha direita daquele que será purificado, no polegar da sua mão direita e no polegar do seu pé direito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3126, 15, "Então o sacerdote pegará um pouco de óleo da caneca e o derramará na palma da sua própria mão esquerda,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3127, 16, "molhará o dedo direito no óleo que está na palma da mão esquerda, e com o dedo o aspergirá sete vezes perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3128, 17, "O sacerdote ainda porá um pouco do óleo restante na palma da sua mão, na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito, em cima do sangue da oferta pela culpa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3129, 18, "O óleo que restar na palma da sua mão, o sacerdote derramará sobre a cabeça daquele que está sendo purificado e fará propiciação por ele perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3130, 19, """Então o sacerdote sacrificará a oferta pelo pecado e fará propiciação em favor daquele que está sendo purificado da sua impureza. Depois disso, o sacerdote matará o animal do holocausto", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3131, 20, "e o oferecerá sobre o altar, juntamente com a oferta de cereal; e assim fará propiciação pelo ofertante, o qual estará puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3132, 21, """Se, todavia, for alguém pobre, sem recursos para isso, pegará um cordeiro como oferta pela culpa para ser movido para fazer propiciação por ele, juntamente com um jarro da melhor farinha, amassada com óleo, como oferta de cereal; uma caneca de óleo", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3133, 22, "e duas rolinhas ou dois pombinhos, os quais ele tenha condições de ofertar, um como oferta pelo pecado e o outro como holocausto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3134, 23, """No oitavo dia ele os trará ao sacerdote, para a sua purificação, à entrada da Tenda do Encontro, perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3135, 24, "O sacerdote pegará o cordeiro da oferta pela culpa, juntamente com uma caneca de óleo, e os moverá perante o Senhor como gesto ritual de apresentação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3136, 25, "Matará o cordeiro da oferta pela culpa e pegará um pouco do sangue e o porá na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3137, 26, "O sacerdote derramará um pouco do óleo na palma da sua mão esquerda,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3138, 27, "e com o dedo indicador direito aspergirá um pouco do óleo da palma da sua mão esquerda sete vezes perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3139, 28, "Ele porá o óleo da palma da sua mão nos mesmos lugares em que pôs o sangue da oferta pela culpa: na ponta da orelha direita daquele que está sendo purificado, no polegar da sua mão direita e no polegar do seu pé direito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3140, 29, "O que restar do óleo na palma da sua mão, o sacerdote derramará sobre a cabeça daquele que está sendo purificado, para fazer propiciação por ele perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3141, 30, "Depois sacrificará uma das rolinhas ou um dos pombinhos, os quais a pessoa tem condições de ofertar,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3142, 31, "um como oferta pelo pecado e o outro como holocausto, juntamente com a oferta de cereal. Assim o sacerdote fará propiciação perante o Senhor em favor daquele que está sendo purificado"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3143, 32, "Essa é a regulamentação para todo aquele que tem lepra e não tem recursos para fazer a oferta da sua purificação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3144, 33, "O Senhor disse a Moisés e a Arão:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3145, 34, """Quando vocês entrarem na terra de Canaã, que lhes dou como propriedade, e eu puser mancha de mofo numa casa, na terra que lhes pertence,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3146, 35, "o dono da casa irá ao sacerdote e dirá: ‘Parece-me que há mancha de mofo em minha casa’.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3147, 36, "Antes de examinar o mofo, o sacerdote ordenará que desocupem a casa para que nada que houver na casa se torne impuro. Depois disso, o sacerdote irá examinar a casa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3148, 37, "Examinará as manchas nas paredes, e, se elas forem esverdeadas ou avermelhadas e parecerem mais profundas do que a superfície da parede,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3149, 38, "o sacerdote sairá da casa e a deixará fechada por sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3150, 39, "No sétimo dia voltará para examinar a casa. Se as manchas se houverem espalhado pelas paredes da casa,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3151, 40, "ordenará que as pedras contaminadas pelas manchas sejam retiradas e jogadas num local impuro, fora da cidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3152, 41, "Fará que a casa seja raspada por dentro e que o reboco raspado seja jogado num local impuro, fora da cidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3153, 42, "Depois colocarão outras pedras no lugar das primeiras, e rebocarão a casa com barro novo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3154, 43, """Se as manchas tornarem a alastrar-se na casa depois de retiradas as pedras e de raspada e rebocada a casa,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3155, 44, "o sacerdote irá examiná-la, e, se as manchas se espalharam pela casa, é mofo corrosivo; a casa está impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3156, 45, "Ela terá que ser demolida: as pedras, as madeiras e todo o reboco da casa; tudo será levado para um local impuro, fora da cidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3157, 46, """Quem entrar na casa enquanto estiver fechada estará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3158, 47, "Aquele que dormir ou comer na casa terá que lavar as suas roupas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3159, 48, """Mas, se o sacerdote for examiná-la e as manchas não se houverem espalhado depois de rebocada a casa, declarará pura a casa, pois as manchas de mofo desapareceram.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3160, 49, "Para purificar a casa, ele pegará duas aves, um pedaço de madeira de cedro, um pano vermelho e hissopo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3161, 50, "Depois matará uma das aves numa vasilha de barro com água da fonte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3162, 51, "Então pegará o pedaço de madeira de cedro, o hissopo, o pano vermelho e a ave viva, e os molhará no sangue da ave morta e na água da fonte, e aspergirá a casa sete vezes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3163, 52, "Ele purificará a casa com o sangue da ave, com a água da fonte, com a ave viva, com o pedaço de madeira de cedro, com o hissopo e com o pano vermelho.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3164, 53, "Depois soltará a ave viva em campo aberto, fora da cidade. Assim fará propiciação pela casa, que ficará pura"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3165, 54, "Essa é a regulamentação acerca de qualquer tipo de lepra, de sarna,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3166, 55, "de mofo nas roupas ou numa casa", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3167, 56, "e de inchaço, erupção ou mancha brilhante,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3168, 57, "para se determinar quando uma coisa é pura ou impura. Essa é a regulamentação acerca de qualquer tipo de lepra e de mofo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3169, 1, "O Senhor disse a Moisés e a Arão:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3170, 2, """Digam o seguinte aos israelitas: Quando um homem tiver um fluxo que sai do corpo, o fluxo é impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3171, 3, "Ele ficará impuro por causa do seu fluxo, quer continue, quer fique retido.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3172, 4, """A cama em que um homem com fluxo se deitar ficará impura, e qualquer coisa em que se sentar ficará impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3173, 5, "Quem tocar na cama dele, lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3174, 6, "Todo aquele que se sentar sobre qualquer coisa na qual esse homem se sentou, lavará suas roupas e se banhará com água, e estará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3175, 7, """Quem tocar no homem que tiver um fluxo lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3176, 8, """Se o homem cuspir em alguém que está puro, este lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3177, 9, "Tudo aquilo em que o homem se sentar quando montar um animal estará impuro,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3178, 10, "e todo aquele que tocar em qualquer coisa que tenha estado debaixo dele ficará impuro até à tarde; quem pegar essas coisas lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3179, 11, """Qualquer pessoa em quem o homem com fluxo tocar sem lavar as mãos, lavará as suas roupas e se banhará com água, e ficará impura até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3180, 12, """A vasilha de barro na qual ele tocar será quebrada; se tocar numa vasilha de madeira, ela será lavada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3181, 13, """Quando um homem sarar de seu fluxo, contará sete dias para a sua purificação; lavará as suas roupas e se banhará em água corrente, e ficará puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3182, 14, "No oitavo dia pegará duas rolinhas ou dois pombinhos e irá perante o Senhor, à entrada da Tenda do Encontro, e os dará ao sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3183, 15, "O sacerdote os sacrificará, um como oferta pelo pecado e o outro como holocausto, e assim fará propiciação perante o Senhor em favor do homem por causa do fluxo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3184, 16, """Quando de um homem sair o sêmen, banhará todo o seu corpo com água, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3185, 17, "Qualquer peça de roupa ou de couro em que houver sêmen será lavada com água, e ficará impura até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3186, 18, """Quando um homem se deitar com uma mulher e lhe sair o sêmen, ambos terão que se banhar com água, e estarão impuros até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3187, 19, """Quando uma mulher tiver fluxo de sangue que sai do corpo, a impureza da sua menstruação durará sete dias, e quem nela tocar ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3188, 20, """Tudo sobre o que ela se deitar durante a sua menstruação ficará impuro, e tudo sobre o que ela se sentar ficará impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3189, 21, "Todo aquele que tocar em sua cama lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3190, 22, "Quem tocar em alguma coisa sobre a qual ela se sentar lavará as suas roupas e se banhará com água, e estará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3191, 23, "Quer seja a cama, quer seja qualquer coisa sobre a qual ela esteve sentada, quando alguém nisso tocar estará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3192, 24, """Se um homem se deitar com ela e a menstruação dela nele tocar, estará impuro por sete dias; qualquer cama sobre a qual ele se deitar estará impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3193, 25, """Quando uma mulher tiver um fluxo de sangue por muitos dias fora da sua menstruação normal, ou um fluxo que continue além desse período, ela ficará impura enquanto durar o corrimento, como nos dias da sua menstruação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3194, 26, "Qualquer cama em que ela se deitar enquanto continuar o seu fluxo estará impura, como acontece com a sua cama durante a sua menstruação, e tudo sobre o que ela se sentar estará impuro, como durante a sua menstruação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3195, 27, "Quem tocar em alguma dessas coisas ficará impuro; lavará as suas roupas e se banhará com água, e ficará impuro até à tarde.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3196, 28, """Quando sarar do seu fluxo, contará sete dias, e depois disso estará pura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3197, 29, "No oitavo dia pegará duas rolinhas ou dois pombinhos e os levará ao sacerdote, à entrada da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3198, 30, "O sacerdote sacrificará um como oferta pelo pecado e o outro como holocausto, e assim fará propiciação em favor dela, perante o Senhor, devido à impureza do seu fluxo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3199, 31, """Mantenham os israelitas separados das coisas que os tornam impuros, para que não morram por contaminar com sua impureza o meu tabernáculo, que está entre eles"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3200, 32, "Essa é a regulamentação acerca do homem que tem fluxo e daquele de quem sai o sêmen, tornando-se impuro,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3201, 33, "da mulher em sua menstruação, do homem ou da mulher que têm fluxo e do homem que se deita com uma mulher que está impura.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3202, 1, "O Senhor falou com Moisés depois que morreram os dois filhos de Arão, por haverem se aproximado do Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3203, 2, "O Senhor disse a Moisés: ""Diga a seu irmão Arão que não entre a toda hora no Lugar Santíssimo, atrás do véu, diante da tampa da arca, para que não morra; pois aparecerei na nuvem, acima da tampa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3204, 3, """Arão deverá entrar no Lugar Santo com um novilho como oferta pelo pecado e com um carneiro como holocausto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3205, 4, "Ele vestirá a túnica sagrada de linho, com calções também de linho por baixo; porá o cinto de linho na cintura e também o turbante de linho. Essas vestes são sagradas; por isso ele se banhará com água antes de vesti-las.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3206, 5, "Receberá da comunidade de Israel dois bodes como oferta pelo pecado e um carneiro como holocausto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3207, 6, """Arão sacrificará o novilho como oferta pelo seu próprio pecado para fazer propiciação por si mesmo e por sua família.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3208, 7, "Depois pegará os dois bodes e os apresentará ao Senhor, à entrada da Tenda do Encontro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3209, 8, "E tirará sortes quanto aos dois bodes: uma para o Senhor e a outra para Azazel.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3210, 9, "Arão trará o bode cuja sorte caiu para o Senhor e o sacrificará como oferta pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3211, 10, "Mas o bode sobre o qual caiu a sorte para Azazel será apresentado vivo ao Senhor para se fazer propiciação e será enviado para Azazel no deserto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3212, 11, """Arão trará o novilho como oferta por seu próprio pecado para fazer propiciação por si mesmo e por sua família, e ele o oferecerá como sacrifício pelo seu próprio pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3213, 12, "Pegará o incensário cheio de brasas do altar que está perante o Senhor e dois punhados de incenso aromático em pó, e os levará para trás do véu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3214, 13, "Porá o incenso no fogo perante o Senhor, e a fumaça do incenso cobrirá a tampa que está acima das tábuas da aliança, a fim de que não morra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3215, 14, "Pegará um pouco do sangue do novilho e com o dedo o aspergirá sobre a parte da frente da tampa; depois, com o dedo aspergirá o sangue sete vezes, diante da tampa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3216, 15, """Então sacrificará o bode da oferta pelo pecado, em favor do povo, e trará o sangue para trás do véu; fará com o sangue o que fez com o sangue do novilho; ele o aspergirá sobre a tampa e na frente dela.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3217, 16, "Assim fará propiciação pelo Lugar Santíssimo por causa das impurezas e das rebeliões dos israelitas, quaisquer que tenham sido os seus pecados. Fará o mesmo em favor da Tenda do Encontro, que está entre eles no meio das suas impurezas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3218, 17, "Ninguém estará na Tenda do Encontro quando ele entrar para fazer propiciação no Lugar Santíssimo, até a saída de Arão, depois que fizer propiciação por si mesmo, por sua família e por toda a assembléia de Israel.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3219, 18, """Depois irá ao altar que está perante o Senhor e pelo altar fará propiciação. Pegará um pouco do sangue do novilho e do sangue do bode e o porá em todas as pontas do altar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3220, 19, "Com o dedo aspergirá o sangue sete vezes sobre o altar para purificá-lo e santificá-lo das impurezas dos israelitas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3221, 20, """Quando Arão terminar de fazer propiciação pelo Lugar Santíssimo, pela Tenda do Encontro e pelo altar, trará para a frente o bode vivo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3222, 21, "Então colocará as duas mãos sobre a cabeça do bode vivo e confessará todas as iniqüidades e rebeliões dos israelitas, todos os seus pecados, e os porá sobre a cabeça do bode. Em seguida enviará o bode para o deserto aos cuidados de um homem designado para isso.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3223, 22, "O bode levará consigo todas as iniqüidades deles para um lugar solitário. E o homem soltará o bode no deserto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3224, 23, """Depois Arão entrará na Tenda do Encontro, tirará as vestes de linho que usou para entrar no Lugar Santíssimo e as deixará ali.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3225, 24, "Ele se banhará com água num lugar sagrado e vestirá as suas próprias roupas. Então sairá e sacrificará o holocausto por si mesmo e o holocausto pelo povo, para fazer propiciação por si mesmo e pelo povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3226, 25, "Também queimará sobre o altar a gordura da oferta pelo pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3227, 26, """Aquele que soltar o bode para Azazel lavará as suas roupas e se banhará com água, e depois poderá entrar no acampamento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3228, 27, "O novilho e o bode da oferta pelo pecado, cujo sangue foi trazido ao Lugar Santíssimo para fazer propiciação, serão levados para fora do acampamento; o couro, a carne e o excremento deles serão queimados com fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3229, 28, "Aquele que os queimar lavará as suas roupas e se banhará com água; depois poderá entrar no acampamento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3230, 29, """Este é um decreto perpétuo para vocês: No décimo dia do sétimo mês vocês se humilharão a si mesmos e não poderão realizar trabalho algum, nem o natural da terra, nem o estrangeiro residente.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3231, 30, "Porquanto nesse dia se fará propiciação por vocês, para purificá-los. Então, perante o Senhor, vocês estarão puros de todos os seus pecados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3232, 31, "Este lhes será um sábado de descanso, quando vocês se humilharão; é um decreto perpétuo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3233, 32, "O sacerdote que for ungido e ordenado para suceder seu pai como sumo sacerdote fará a propiciação. Porá as vestes sagradas de linho", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3234, 33, "e fará propiciação pelo Lugar Santíssimo, pela Tenda do Encontro, pelo altar, por todos os sacerdotes e por todo o povo da assembléia.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3235, 34, """Este é um decreto perpétuo para vocês: A propiciação será feita uma vez por ano, por todos os pecados dos israelitas"". E tudo foi feito conforme o Senhor tinha ordenado a Moisés.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3236, 1, "O Senhor disse a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3237, 2, """Diga o seguinte a Arão e seus filhos e a todos os israelitas: Foi isto o que o Senhor ordenou:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3238, 3, "Qualquer israelita que sacrificar um boi, um cordeiro ou um cabrito dentro ou fora do acampamento,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3239, 4, "e não o trouxer à entrada da Tenda do Encontro para apresentá-lo como oferta ao Senhor, diante do tabernáculo do Senhor, será considerado culpado de sangue; derramou sangue e será eliminado do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3240, 5, "Os sacrifícios, que os israelitas agora fazem em campo aberto, passarão a trazer ao Senhor, entregando-os ao sacerdote, para oferecê-los ao Senhor, à entrada da Tenda do Encontro, e os sacrificarão como ofertas de comunhão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3241, 6, "O sacerdote aspergirá o sangue no altar do Senhor, à entrada da Tenda do Encontro, e queimará a gordura como aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3242, 7, "Não oferecerão mais sacrifícios aos ídolos em forma de bode, aos quais prestam culto imoral. Este é um decreto perpétuo para eles e para as suas gerações.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3243, 8, """Diga-lhes: Todo israelita ou estrangeiro residente que oferecer holocausto ou sacrifício,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3244, 9, "e não o trouxer à entrada da Tenda do Encontro para oferecê-lo ao Senhor, será eliminado do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3245, 10, """Todo israelita ou estrangeiro residente que comer sangue de qualquer animal eu me voltarei contra esse que comeu sangue, e o eliminarei do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3246, 11, "Pois a vida da carne está no sangue, e eu o dei a vocês para fazerem propiciação por si mesmos no altar; é o sangue que faz propiciação pela vida.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3247, 12, "Por isso digo aos israelitas: nenhum de vocês poderá comer sangue, nem também o estrangeiro residente.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3248, 13, """Qualquer israelita ou estrangeiro residente que caçar um animal ou ave que se pode comer, derramará o sangue e o cobrirá com terra,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3249, 14, "porque a vida de toda carne é o seu sangue. Por isso eu disse aos israelitas: vocês não poderão comer o sangue de nenhum animal, porque a vida de toda carne é o seu sangue; todo aquele que o comer será eliminado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3250, 15, """Todo aquele que, natural da terra ou estrangeiro, comer um animal encontrado morto ou despedaçado por animais selvagens, lavará suas roupas e se banhará com água, e ficará impuro até à tarde; então estará puro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3251, 16, "Mas, se não lavar as suas roupas nem se banhar, sofrerá as conseqüências da sua iniqüidade"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3252, 1, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3253, 2, """Diga o seguinte aos israelitas: Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3254, 3, "Não procedam como se procede no Egito, onde vocês moraram, nem como se procede na terra de Canaã, para onde os estou levando. Não sigam as suas práticas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3255, 4, "Pratiquem as minhas ordenanças, obedeçam aos meus decretos e sigam-nos. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3256, 5, "Obedeçam aos meus decretos e ordenanças, pois o homem que os praticar viverá por eles. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3257, 6, """Ninguém poderá se aproximar de uma parenta próxima para se envolver sexualmente com ela. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3258, 7, """Não desonre o seu pai, envolvendo-se sexualmente com a sua mãe. Ela é sua mãe; não se envolva sexualmente com ela.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3259, 8, """Não se envolva sexualmente com a mulher do seu pai; isso desonraria seu pai.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3260, 9, """Não se envolva sexualmente com a sua irmã, filha do seu pai ou da sua mãe, tenha ela nascido na mesma casa ou em outro lugar.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3261, 10, """Não se envolva sexualmente com a filha do seu filho ou com a filha da sua filha; isso desonraria você.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3262, 11, """Não se envolva sexualmente com a filha da mulher do seu pai, gerada por seu pai; ela é sua irmã.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3263, 12, """Não se envolva sexualmente com a irmã do seu pai; ela é parenta próxima do seu pai.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3264, 13, """Não se envolva sexualmente com a irmã da sua mãe; ela é parenta próxima da sua mãe.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3265, 14, """Não desonre o irmão do seu pai aproximando-se da sua mulher para com ela se envolver sexualmente; ela é sua tia.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3266, 15, """Não se envolva sexualmente com a sua nora. Ela é mulher do seu filho; não se envolva sexualmente com ela.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3267, 16, """Não se envolva sexualmente com a mulher do seu irmão; isso desonraria seu irmão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3268, 17, """Não se envolva sexualmente com uma mulher e sua filha. Não se envolva sexualmente com a filha do seu filho ou com a filha da sua filha; são parentes próximos. É perversidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3269, 18, """Não tome por mulher a irmã da sua mulher, tornando-a rival, envolvendo-se sexualmente com ela, estando a sua mulher ainda viva.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3270, 19, """Não se aproxime de uma mulher para se envolver sexualmente com ela quando ela estiver na impureza da sua menstruação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3271, 20, """Não se deite com a mulher do seu próximo, contaminando-se com ela.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3272, 21, """Não entregue os seus filhos para serem sacrificados a Moloque. Não profanem o nome do seu Deus. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3273, 22, """Não se deite com um homem como quem se deita com uma mulher; é repugnante.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3274, 23, """Não tenha relações sexuais com um animal, contaminando-se com ele. Mulher nenhuma se porá diante de um animal para ajuntar-se com ele; é depravação.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3275, 24, """Não se contaminem com nenhuma dessas coisas, porque assim se contaminaram as nações que vou expulsar da presença de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3276, 25, "Até a terra ficou contaminada; e eu castiguei a sua iniqüidade, e a terra vomitou os seus habitantes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3277, 26, "Mas vocês obedecerão aos meus decretos e às minhas leis. Nem o natural da terra nem o estrangeiro residente entre vocês farão nenhuma dessas abominações,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3278, 27, "pois todas estas abominações foram praticadas pelos que habitaram essa terra antes de vocês; por isso a terra ficou contaminada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3279, 28, "E, se vocês contaminarem a terra, ela os vomitará, como vomitou os povos que ali estavam antes de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3280, 29, """Todo aquele que fizer alguma destas abominações, aqueles que assim procederem serão eliminados do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3281, 30, "Obedeçam aos meus preceitos, e não pratiquem os costumes repugnantes praticados antes de vocês, nem se contaminem com eles. Eu sou o Senhor, o Deus de vocês"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3282, 1, "Disse ainda o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3283, 2, """Diga o seguinte a toda comunidade de Israel: Sejam santos porque eu, o Senhor, o Deus de vocês, sou santo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3284, 3, """Respeite cada um de vocês a sua mãe e o seu pai, e guarde os meus sábados. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3285, 4, """Não se voltem para os ídolos, nem façam para si deuses de metal. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3286, 5, """Quando vocês oferecerem um sacrifício de comunhão ao Senhor, ofereçam-no de modo que seja aceito em favor de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3287, 6, "Terá que ser comido no dia em que o oferecerem, ou no dia seguinte; o que sobrar até ao terceiro dia será queimado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3288, 7, "Se alguma coisa for comida no terceiro dia, estará estragada e não será aceita.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3289, 8, "Quem comer sofrerá as conseqüências da sua iniqüidade, porque profanou o que é santo ao Senhor; será eliminado do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3290, 9, """Quando fizerem a colheita da sua terra, não colham até às extremidades da sua lavoura, nem ajuntem as espigas caídas de sua colheita.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3291, 10, "Não passem duas vezes pela sua vinha, nem apanhem as uvas que tiverem caído. Deixem-nas para o necessitado e para o estrangeiro. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3292, 11, """Não furtem. ""Não mintam. ""Não enganem uns aos outros.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3293, 12, """Não jurem falsamente pelo meu nome, profanando assim o nome do seu Deus. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3294, 13, """Não oprimam nem roubem o seu próximo. ""Não retenham até a manhã do dia seguinte o pagamento de um diarista.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3295, 14, """Não amaldiçoem o surdo nem ponham pedra de tropeço à frente do cego, mas tema o seu Deus. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3296, 15, """Não cometam injustiça num julgamento; não favoreçam os pobres, nem procurem agradar os grandes, mas julguem o seu próximo com justiça.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3297, 16, """Não espalhem calúnias entre o seu povo. ""Não se levantem contra a vida do seu próximo. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3298, 17, """Não guardem ódio contra o seu irmão no coração; antes repreendam com franqueza o seu próximo para que, por causa dele, não sofram as conseqüências de um pecado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3299, 18, """Não procurem vingança, nem guardem rancor contra alguém do seu povo, mas ame cada um o seu próximo como a si mesmo. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3300, 19, """Obedeçam às minhas leis. ""Não cruzem diferentes espécies de animais. ""Não plantem duas espécies de sementes na sua lavoura. ""Não usem roupas feitas com dois tipos de tecido.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3301, 20, """Se um homem deitar-se com uma escrava prometida a outro homem, mas que não tenha sido resgatada nem recebido sua liberdade, aplique-se a devida punição. Contudo não serão mortos, porquanto ela não havia sido libertada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3302, 21, "O homem, porém, trará ao Senhor, à entrada da Tenda do Encontro, um carneiro como oferta pela culpa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3303, 22, "Com o carneiro da oferta pela culpa o sacerdote fará propiciação por ele perante o Senhor, pelo pecado que cometeu; assim o pecado que ele cometeu será perdoado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3304, 23, """Quando vocês entrarem na terra e plantarem qualquer tipo de árvore frutífera, considerem proibidas as suas frutas. Durante três anos vocês as considerarão proibidas; não poderão comê-las.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3305, 24, "No quarto ano todas as suas frutas serão santas; será uma oferta de louvor ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3306, 25, "No quinto ano, porém, vocês poderão comer as suas frutas. Assim a sua colheita aumentará. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3307, 26, """Não comam nada com sangue. ""Não pratiquem adivinhação nem feitiçaria.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3308, 27, """Não cortem o cabelo dos lados da cabeça, nem aparem as pontas da barba.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3309, 28, """Não façam cortes em seus corpos por causa dos mortos, nem tatuagem em si mesmos. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3310, 29, """Ninguém desonre a sua filha tornando-a uma prostituta, se não a terra se entregará à prostituição e se encherá de perversidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3311, 30, """Guardem os meus sábados e reverenciem o meu santuário. Eu sou o Senhor"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3312, 31, """Não recorram aos médiuns, nem busquem os espíritas, pois vocês serão contaminados por eles. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3313, 32, """Levantem-se na presença dos idosos, honrem os anciãos, tema o seu Deus. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3314, 33, """Quando um estrangeiro viver na terra de vocês, não o maltratem.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3315, 34, "O estrangeiro residente que viver com vocês será tratado como o natural da terra. Amem-no como a si mesmos, pois vocês foram estrangeiros no Egito. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3316, 35, """Não usem medidas desonestas quando medirem comprimento, peso ou quantidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3317, 36, "Usem balanças de pesos honestos, tanto para cereais quanto para líquidos. Eu sou o Senhor, o Deus de vocês, que os tirei da terra do Egito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3318, 37, """Obedeçam a todos os meus decretos e a todas as minhas leis e pratiquem-nos. Eu sou o Senhor"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3319, 1, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3320, 2, """Diga aos israelitas: Qualquer israelita ou estrangeiro residente em Israel que entregar um dos seus filhos a Moloque, terá que ser executado. O povo da terra o apedrejará.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3321, 3, "Voltarei o meu rosto contra ele e o eliminarei do meio do seu povo; pois deu os seus filhos a Moloque, contaminando assim o meu santuário e profanando o meu santo nome.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3322, 4, "Se o povo deliberadamente fechar os olhos quando alguém entregar um dos seus filhos a Moloque, e deixar de executá-lo,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3323, 5, "voltarei o meu rosto contra aquele homem e contra o seu clã e eliminarei do meio do seu povo tanto ele quanto todos os que o seguem, prostituindo-se com Moloque.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3324, 6, """Voltarei o meu rosto contra aquele que procurar médiuns e espíritas para segui-los, prostituindo-se com eles. Eu o eliminarei do meio do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3325, 7, """Consagrem-se, porém, e sejam santos, porque eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3326, 8, "Obedeçam aos meus decretos e pratiquem-nos. Eu sou o Senhor que os santifica.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3327, 9, """Se alguém amaldiçoar seu pai ou sua mãe, terá que ser executado. Por ter amaldiçoado o seu pai ou a sua mãe, merece a morte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3328, 10, """Se um homem cometer adultério com a mulher de outro homem, com a mulher do seu próximo, tanto o adúltero quanto a adúltera terão que ser executados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3329, 11, """Se um homem se deitar com a mulher do seu pai, desonrou seu pai. Tanto o homem quanto a mulher terão que ser executados, pois merecem a morte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3330, 12, """Se um homem se deitar com a sua nora, ambos terão que ser executados. O que fizeram é depravação; merecem a morte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3331, 13, """Se um homem se deitar com outro homem como quem se deita com uma mulher, ambos praticaram um ato repugnante. Terão que ser executados, pois merecem a morte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3332, 14, """Se um homem tomar uma mulher e a mãe dela, comete perversidade. Tanto ele quanto elas serão queimados com fogo, para que não haja perversidade entre vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3333, 15, """Se um homem tiver relações sexuais com um animal, terá que ser executado, e vocês matarão também o animal.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3334, 16, """Se uma mulher se aproximar de algum animal para ajuntar-se com ele, vocês matarão a mulher e o animal. Ambos terão que ser executados, pois merecem a morte.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3335, 17, """Se um homem tomar por mulher sua irmã, filha de seu pai ou de sua mãe, e se envolver sexualmente com ela, pratica um ato vergonhoso. Serão eliminados à vista de todo o povo. Desonrou sua irmã e sofrerá as conseqüências da sua iniqüidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3336, 18, """Se um homem se deitar com uma mulher durante a menstruação e com ela se envolver sexualmente, ambos serão eliminados do meio do seu povo, pois expuseram o sangramento dela.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3337, 19, """Não se envolva sexualmente com a irmã de sua mãe, nem com a irmã de seu pai; pois quem se envolver sexualmente com uma parenta próxima sofrerá as conseqüências da sua iniqüidade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3338, 20, """Se um homem se deitar com a mulher do seu tio, desonrou seu tio. Eles sofrerão as conseqüências do seu pecado; morrerão sem filhos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3339, 21, """Se um homem tomar por mulher a mulher do seu irmão, comete impureza; desonrou seu irmão. Ficarão sem filhos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3340, 22, """Obedeçam a todos os meus decretos e leis e pratiquem-nos, para que a terra para onde os estou levando para habitarem não os vomite.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3341, 23, "Não sigam os costumes dos povos que vou expulsar de diante de vocês. Por terem feito todas essas coisas, causam-me repugnância.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3342, 24, "Mas a vocês prometi que herdarão a terra deles; eu a darei a vocês como herança, terra que mana leite e mel. Eu sou o Senhor, o Deus de vocês, que os separou dentre os povos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3343, 25, """Portanto, façam separação entre animais puros e impuros e entre aves puras e impuras. Não se contaminem com animal, ou ave, ou com qualquer criatura que se move rente ao chão, os quais separei de vocês por serem eles impuros.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3344, 26, "Vocês serão santos para mim, porque eu, o Senhor, sou santo, e os separei dentre os povos para serem meus.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3345, 27, """Os homens ou mulheres que, entre vocês, forem médiuns ou espíritas, terão que ser executados. Serão apedrejados, pois merecem a morte"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3346, 1, "Disse ainda o Senhor a Moisés: ""Diga o seguinte aos sacerdotes, os filhos de Arão: Um sacerdote não poderá tornar-se impuro por causa de alguém do seu povo que venha a morrer,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3347, 2, "a não ser por um parente próximo, como mãe ou pai, filho ou filha, irmão,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3348, 3, "ou irmã virgem dependente dele por ainda não ter marido; por causa dela, poderá tornar-se impuro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3349, 4, "Não poderá tornar-se impuro e contaminar-se por causa de parentes por casamento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3350, 5, """Os sacerdotes não raparão a cabeça, nem apararão as pontas da barba, nem farão cortes no corpo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3351, 6, "Serão santos ao seu Deus, e não profanarão o nome do seu Deus. Pelo fato de apresentarem ao Senhor as ofertas preparadas no fogo, ofertas de alimento do seu Deus, serão santos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3352, 7, """Não poderão tomar por mulher uma prostituta, uma moça que tenha perdido a virgindade, ou uma mulher divorciada do seu marido, porque o sacerdote é santo ao seu Deus.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3353, 8, "Considerem-no santo, porque ele oferece o alimento do seu Deus. Considerem-no santo, porque eu, o Senhor, que os santifico, sou santo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3354, 9, """Se a filha de um sacerdote se corromper, tornando-se prostituta, desonra seu pai; será queimada com fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3355, 10, """O sumo sacerdote, aquele entre seus irmãos sobre cuja cabeça tiver sido derramado o óleo da unção, e que tiver sido consagrado para usar as vestes sacerdotais, não andará descabelado, nem rasgará as roupas em sinal de luto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3356, 11, "Não entrará onde houver um cadáver. Não se tornará impuro, nem mesmo por causa do seu pai ou da sua mãe;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3357, 12, "e não deixará o santuário do seu Deus, nem o profanará, porquanto foi consagrado pelo óleo da unção do seu Deus. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3358, 13, """A mulher que ele tomar terá que ser virgem.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3359, 14, "Não poderá ser viúva, nem divorciada, nem moça que perdeu a virgindade, nem prostituta, mas terá que ser uma virgem do seu próprio povo,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3360, 15, "assim ele não profanará a sua descendência entre o seu povo. Eu sou o Senhor, que o santifico"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3361, 16, "Disse ainda o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3362, 17, """Diga a Arão: Pelas suas gerações, nenhum dos seus descendentes que tenha algum defeito poderá aproximar-se para trazer ao seu Deus ofertas de alimento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3363, 18, "Nenhum homem que tenha algum defeito poderá aproximar-se: ninguém que seja cego ou aleijado, que tenha o rosto defeituoso ou o corpo deformado;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3364, 19, "ninguém que tenha o pé ou a mão defeituosos,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3365, 20, "ou que seja corcunda ou anão, ou que tenha qualquer defeito na vista, ou que esteja com feridas purulentas ou com fluxo, ou que tenha testículos defeituosos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3366, 21, "Nenhum descendente do sacerdote Arão que tenha qualquer defeito poderá aproximar-se para apresentar ao Senhor ofertas preparadas no fogo. Tem defeito; não poderá aproximar-se para trazê-las ao seu Deus.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3367, 22, "Poderá comer o alimento santíssimo de seu Deus, e também o alimento santo;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3368, 23, "contudo, por causa do seu defeito, não se aproximará do véu nem do altar, para que não profane o meu santuário. Eu sou o Senhor, que os santifico"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3369, 24, "Foi isso que Moisés falou a Arão e a seus filhos e a todos os israelitas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3370, 1, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3371, 2, """Diga a Arão e a seus filhos que tratem com respeito as ofertas sagradas que os israelitas me consagrarem, para que não profanem o meu santo nome. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3372, 3, """Avise-lhes que se, em suas futuras gerações, algum dos seus descendentes estiver impuro quando se aproximar das ofertas sagradas que os israelitas consagrarem ao Senhor, será eliminado da minha presença. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3373, 4, """Nenhum descendente de Arão que tenha lepra ou fluxo no corpo poderá comer das ofertas sagradas até que esteja purificado. Também estará impuro se tocar em algo contaminado por um cadáver, ou se lhe sair o sêmen,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3374, 5, "ou se tocar em alguma criatura, ou em alguém que o torne impuro, seja qual for a impureza.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3375, 6, "Aquele que neles tocar ficará impuro até à tarde. Não poderá comer das ofertas sagradas, a menos que se tenha banhado com água.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3376, 7, "Depois do pôr do sol estará puro, e então poderá comer as ofertas sagradas, pois são o seu alimento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3377, 8, "Também não poderá comer animal encontrado morto ou despedaçado por animais selvagens, pois se tornaria impuro por causa deles. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3378, 9, """Os sacerdotes obedecerão aos meus preceitos, para que não sofram as conseqüências do seu pecado nem sejam executados por tê-los profanado. Eu sou o Senhor, que os santifico.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3379, 10, """Somente o sacerdote e a sua família poderão comer da oferta sagrada; não poderá comê-la o seu hóspede, nem o seu empregado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3380, 11, "Mas, se um sacerdote comprar um escravo, ou se um escravo nascer em sua casa, esse escravo poderá comer do seu alimento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3381, 12, "Se a filha de um sacerdote se casar com alguém que não seja sacerdote, não poderá comer das ofertas sagradas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3382, 13, "Mas, se a filha de um sacerdote ficar viúva ou se divorciar, e não tiver filhos, e voltar a viver na casa do pai como na sua juventude, poderá comer do alimento do pai, mas dele não poderá comer ninguém que não seja da família do sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3383, 14, """Se alguém, sem intenção, comer uma oferta sagrada, fará restituição da oferta ao sacerdote e lhe acrescentará um quinto do seu valor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3384, 15, """Os sacerdotes não profanarão as ofertas sagradas que os israelitas apresentam ao Senhor,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3385, 16, "permitindo-lhes comê-las e trazendo assim sobre eles culpa que exige reparação. Eu sou o Senhor que os santifico"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3386, 17, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3387, 18, """Diga o seguinte a Arão e a seus filhos e a todos os israelitas: Se algum de vocês, seja israelita, seja estrangeiro residente em Israel, apresentar uma oferta como holocausto ao Senhor, quer para cumprir voto, quer como oferta voluntária,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3388, 19, "apresentará um macho sem defeito do rebanho, boi, carneiro ou bode, a fim de que seja aceita em seu favor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3389, 20, "Não tragam nenhum animal defeituoso, porque não será aceito em favor de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3390, 21, "Quando alguém trouxer um animal do gado ou do rebanho de ovelhas como oferta de comunhão para o Senhor, em cumprimento de voto, ou como oferta voluntária, para ser aceitável o animal terá que ser sem defeito e sem mácula.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3391, 22, "Não ofereçam ao Senhor animal cego, aleijado, mutilado, ulceroso, cheio de feridas purulentas ou com fluxo. Não coloquem nenhum desses animais sobre o altar como oferta ao Senhor, preparada no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3392, 23, "Todavia, poderão apresentar como oferta voluntária um boi ou um carneiro ou um cabrito deformados ou atrofiados, mas no caso do cumprimento de voto não serão aceitos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3393, 24, "Não poderão oferecer ao Senhor um animal cujos testículos estejam machucados, esmagados, despedaçados ou cortados. Não façam isso em sua própria terra,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3394, 25, "nem aceitem animais como esses das mãos de um estrangeiro para oferecê-los como alimento do seu Deus. Não serão aceitos em favor de vocês, pois são deformados e apresentam defeitos"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3395, 26, "Disse ainda o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3396, 27, """Quando nascer um bezerro, um cordeiro ou um cabrito, ficará sete dias com sua mãe. Do oitavo dia em diante será aceito como oferta ao Senhor preparada no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3397, 28, "Não matem uma vaca ou uma ovelha ou uma cabra e sua cria no mesmo dia.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3398, 29, """Quando vocês oferecerem um sacrifício de gratidão ao Senhor, ofereçam-no de maneira que seja aceito em favor de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3399, 30, "Será comido naquele mesmo dia; não deixem nada até a manhã seguinte. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3400, 31, """Obedeçam aos meus mandamentos e os coloquem em prática. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3401, 32, "Não profanem o meu santo nome. Eu serei reconhecido como santo pelos israelitas. Eu sou o Senhor que os santifico", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3402, 33, "e que os tirou do Egito para ser o Deus de vocês. Eu sou o Senhor"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3403, 1, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3404, 2, """Diga o seguinte aos israelitas: Estas são as minhas festas, as festas fixas do Senhor, que vocês proclamarão como reuniões sagradas:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3405, 3, """Em seis dias realize os seus trabalhos, mas o sétimo dia é sábado, dia de descanso e de reunião sagrada. Não realizem trabalho algum; onde quer que morarem, será sábado dedicado ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3406, 4, """Estas são as festas fixas do Senhor, as reuniões sagradas que vocês proclamarão no tempo devido:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3407, 5, "A páscoa do Senhor começa no entardecer do décimo quarto dia do primeiro mês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3408, 6, "No décimo quinto dia daquele mês começa a festa do Senhor, a festa dos pães sem fermento; durante sete dias vocês comerão pães sem fermento.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3409, 7, "No primeiro dia façam uma reunião sagrada e não realizem trabalho algum.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3410, 8, "Durante sete dias apresentem ao Senhor ofertas preparadas no fogo. E no sétimo dia façam uma reunião sagrada e não realizem trabalho algum"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3411, 9, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3412, 10, """Diga o seguinte aos israelitas: Quando vocês entrarem na terra que lhes dou e fizerem colheita, tragam ao sacerdote um feixe do primeiro cereal que colherem.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3413, 11, "O sacerdote moverá ritualmente o feixe perante o Senhor para que seja aceito em favor de vocês; ele o moverá no dia seguinte ao sábado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3414, 12, "No dia em que moverem o feixe, vocês oferecerão em holocausto ao Senhor um cordeiro de um ano de idade e sem defeito.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3415, 13, "Apresentem também uma oferta de cereal de dois jarros da melhor farinha amassada com óleo, oferta ao Senhor preparada no fogo, de aroma agradável, e uma oferta derramada de um litro de vinho.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3416, 14, "Vocês não poderão comer pão algum, nem cereal tostado, nem cereal novo, até o dia em que trouxerem essa oferta ao Deus de vocês. Este é um decreto perpétuo para as suas gerações, onde quer que morarem.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3417, 15, """A partir do dia seguinte ao sábado, o dia em que vocês trarão o feixe da oferta ritualmente movida, contem sete semanas completas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3418, 16, "Contem cinqüenta dias, até um dia depois do sétimo sábado, e então apresentem uma oferta de cereal novo ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3419, 17, "Onde quer que morarem, tragam de casa dois pães feitos com dois jarros da melhor farinha, cozidos com fermento, como oferta movida dos primeiros frutos ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3420, 18, "Junto com os pães apresentem sete cordeiros, cada um com um ano de idade e sem defeito, um novilho e dois carneiros. Eles serão holocausto ao Senhor, juntamente com as suas ofertas de cereal e ofertas derramadas; é oferta preparada no fogo, de aroma agradável ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3421, 19, "Depois sacrifiquem um bode como oferta pelo pecado e dois cordeiros, cada um com um ano de idade, como oferta de comunhão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3422, 20, "O sacerdote moverá os dois cordeiros perante o Senhor como gesto ritual de apresentação, juntamente com o pão dos primeiros frutos. São uma oferta sagrada ao Senhor que pertencem ao sacerdote.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3423, 21, "Naquele mesmo dia vocês proclamarão uma reunião sagrada e não realizarão trabalho algum. Este é um decreto perpétuo para as suas gerações, onde quer que vocês morarem.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3424, 22, """Quando fizerem a colheita da sua terra, não colham até às extremidades da sua lavoura, nem ajuntem as espigas caídas da sua colheita. Deixem-nas para o necessitado e para o estrangeiro. Eu sou o Senhor, o Deus de vocês"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3425, 23, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3426, 24, """Diga também aos israelitas: No dia primeiro do sétimo mês vocês terão um dia de descanso, uma reunião sagrada, comemorada com toques de trombeta.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3427, 25, "Não realizem trabalho algum, mas apresentem ao Senhor uma oferta preparada no fogo"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3428, 26, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3429, 27, """O décimo dia deste sétimo mês é o Dia da Expiação. Façam uma reunião sagrada e humilhem-se, e apresentem ao Senhor uma oferta preparada no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3430, 28, "Não realizem trabalho algum nesse dia, porque é o Dia da Expiação, quando se faz propiciação por vocês perante o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3431, 29, "Quem não se humilhar nesse dia será eliminado do seu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3432, 30, "Eu destruirei do meio do seu povo todo aquele que realizar algum trabalho nesse dia.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3433, 31, "Vocês não realizarão trabalho algum. Este é um decreto perpétuo para as suas gerações, onde quer que vocês morarem.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3434, 32, "É um sábado de descanso para vocês, e vocês se humilharão. Desde o entardecer do nono dia do mês até ao entardecer do dia seguinte vocês guardarão esse sábado"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3435, 33, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3436, 34, """Diga ainda aos israelitas: No décimo quinto dia deste sétimo mês começa a Festa das Cabanas do Senhor, que dura sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3437, 35, "No primeiro dia haverá reunião sagrada; não realizem trabalho algum.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3438, 36, "Durante sete dias apresentem ao Senhor ofertas preparadas no fogo, e no oitavo dia façam outra reunião sagrada, e também apresentem ao Senhor uma oferta preparada no fogo. É reunião solene; não realizem trabalho algum.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3439, 37, "( Estas são as festas fixas do Senhor, que vocês proclamarão como reuniões sagradas para trazerem ao Senhor ofertas preparadas no fogo, holocaustos e ofertas de cereal, sacrifícios e ofertas derramadas exigidas para cada dia.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3440, 38, "Isso fora as do sábado do Senhor e fora as dádivas e votos de vocês, e todas as ofertas voluntárias que vocês derem ao Senhor. )", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3441, 39, """Assim, começando no décimo quinto dia do sétimo mês, depois de terem colhido o que a terra produziu, comemorem a festa do Senhor durante sete dias; o primeiro dia e também o oitavo serão dias de descanso.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3442, 40, "No primeiro dia vocês apanharão os melhores frutos das árvores, folhagem de tamareira, galhos frondosos e salgueiros, e se alegrarão perante o Senhor, o Deus de vocês, durante sete dias.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3443, 41, "Comemorem essa festa do Senhor durante sete dias todos os anos. Este é um decreto perpétuo para as suas gerações; comemorem-na no sétimo mês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3444, 42, "Morem em tendas durante sete dias; todos os israelitas de nascimento morarão em tendas,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3445, 43, "para que os descendentes de vocês saibam que eu fiz os israelitas morarem em tendas quando os tirei da terra do Egito. Eu sou o Senhor, o Deus de vocês"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3446, 44, "Assim anunciou Moisés aos israelitas as festas fixas do Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3447, 1, "Disse o Senhor a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3448, 2, """Ordene aos israelitas que lhe tragam azeite puro de oliva batida para as lâmpadas, para que fiquem sempre acesas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3449, 3, "Na Tenda do Encontro, do lado de fora do véu que esconde as tábuas da aliança, Arão manterá as lâmpadas continuamente acesas diante do Senhor, desde o entardecer até a manhã seguinte. Este é um decreto perpétuo para as suas gerações.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3450, 4, "Mantenha sempre em ordem as lâmpadas no candelabro de ouro puro perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3451, 5, """Apanhe da melhor farinha e asse doze pães, usando dois jarros para cada pão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3452, 6, "Coloque-os em duas fileiras, com seis pães em cada uma, sobre a mesa de ouro puro perante o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3453, 7, "Junto a cada fileira coloque um pouco de incenso puro como porção memorial para representar o pão e ser uma oferta ao Senhor preparada no fogo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3454, 8, "Esses pães serão colocados regularmente perante o Senhor, cada sábado, em nome dos israelitas, como aliança perpétua.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3455, 9, "Pertencem a Arão e a seus descendentes, que os comerão num lugar sagrado, porque é parte santíssima de sua porção regular das ofertas dedicadas ao Senhor, preparadas no fogo. É decreto perpétuo"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3456, 10, "Aconteceu que o filho de uma israelita e de um egípcio saiu e foi para o meio dos israelitas. No acampamento houve uma briga entre ele e um israelita.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3457, 11, "O filho da israelita blasfemou o Nome com uma maldição; então o levaram a Moisés. O nome de sua mãe era Selomite, filha de Dibri, da tribo de Dã.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3458, 12, "Deixaram-no preso até que a vontade do Senhor lhes fosse declarada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3459, 13, "Então o Senhor disse a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3460, 14, """Leve o que blasfemou para fora do acampamento. Todos aqueles que o ouviram colocarão as mãos sobre a cabeça dele, e a comunidade toda o apedrejará.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3461, 15, "Diga aos israelitas: Se alguém amaldiçoar seu Deus, será responsável pelo seu pecado;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3462, 16, "quem blasfemar o nome do Senhor terá que ser executado. A comunidade toda o apedrejará. Seja estrangeiro, seja natural da terra, se blasfemar o Nome, terá que ser morto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3463, 17, """Se alguém ferir uma pessoa a ponto de matá-la, terá que ser executado.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3464, 18, "Quem matar um animal fará restituição: vida por vida.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3465, 19, "Se alguém ferir seu próximo, deixando-o defeituoso, assim como fez lhe será feito:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3466, 20, "fratura por fratura, olho por olho, dente por dente. Assim como feriu o outro, deixando-o defeituoso, assim também será ferido.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3467, 21, "Quem matar um animal fará restituição, mas quem matar um homem será morto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3468, 22, "Vocês terão a mesma lei para o estrangeiro e para o natural. Eu sou o Senhor, o Deus de vocês"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3469, 23, "Depois que Moisés falou aos israelitas, levaram o que blasfemou para fora do acampamento e o apedrejaram. Os israelitas fizeram conforme o Senhor tinha ordenado a Moisés.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3470, 1, "Então disse o Senhor a Moisés no monte Sinai:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3471, 2, """Diga o seguinte aos israelitas: Quando vocês entrarem na terra que lhes dou, a própria terra guardará um sábado para o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3472, 3, "Durante seis anos semeiem as suas lavouras, aparem as suas vinhas e façam a colheita de suas plantações.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3473, 4, "Mas no sétimo ano a terra terá um sábado de descanso, um sábado dedicado ao Senhor. Não semeiem as suas lavouras, nem aparem as suas vinhas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3474, 5, "Não colham o que crescer por si, nem colham as uvas das suas vinhas que não serão podadas. A terra terá um ano de descanso.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3475, 6, "Vocês se sustentarão do que a terra produzir no ano de descanso, você, o seu escravo, a sua escrava, o trabalhador contratado e o residente temporário que vive entre vocês,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3476, 7, "bem como os seus rebanhos e os animais selvagens de sua terra. Tudo o que a terra produzir poderá ser comido.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3477, 8, """Contem sete semanas de anos, sete vezes sete anos; essas sete semanas de anos totalizam quarenta e nove anos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3478, 9, "Então façam soar a trombeta no décimo dia do sétimo mês; no Dia da Expiação façam soar a trombeta por toda a terra de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3479, 10, "Consagrem o qüinquagésimo ano e proclamem libertação por toda a terra a todos os seus moradores. Este lhes será um ano de jubileu, quando cada um de vocês voltará para a propriedade da sua família e para o seu próprio clã.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3480, 11, "O quinquagésimo ano lhes será jubileu; não semeiem e não ceifem o que cresce por si mesmo nem colham das vinhas não podadas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3481, 12, "É jubileu, e lhes será santo; comam apenas o que a terra produzir.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3482, 13, """Nesse ano do jubileu cada um de vocês voltará para a sua propriedade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3483, 14, """Se vocês venderem alguma propriedade ao seu próximo ou se comprarem alguma propriedade dele, não explore o seu irmão.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3484, 15, "O que comprarem do seu próximo será avaliado com base no número de anos desde o Jubileu. E fará a venda com base no número de anos que restam de colheitas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3485, 16, "Quando os anos forem muitos, vocês deverão aumentar o preço, mas quando forem poucos, deverão diminuir o preço, pois o que ele está lhes vendendo é o número de colheitas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3486, 17, "Não explorem um ao outro, mas temam ao Deus de vocês. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3487, 18, """Pratiquem os meus decretos e obedeçam às minhas ordenanças, e vocês viverão com segurança na terra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3488, 19, "Então a terra dará o seu fruto, e vocês comerão até fartar-se e ali viverão em segurança.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3489, 20, "Vocês poderão perguntar: ‘Que iremos comer no sétimo ano, se não plantarmos nem fizermos a colheita? ’", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3490, 21, "Saibam que eu lhes enviarei a minha bênção no sexto ano, e a terra produzirá o suficiente para três anos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3491, 22, "Quando vocês estiverem plantando no oitavo ano, comerão ainda da colheita anterior e dela continuarão a comer até a colheita do nono ano.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3492, 23, """A terra não poderá ser vendida definitivamente, porque ela é minha, e vocês são apenas estrangeiros e imigrantes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3493, 24, "Em toda terra em que tiverem propriedade, concedam o direito de resgate da terra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3494, 25, """Se alguém do seu povo empobrecer e vender parte da sua propriedade, seu parente mais próximo virá e resgatará aquilo que o seu compatriota vendeu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3495, 26, "Se, contudo, um homem não tiver quem lhe resgate a terra, mas ele mesmo prosperar e adquirir recursos para resgatá-la,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3496, 27, "calculará os anos desde que a vendeu e devolverá a diferença àquele a quem a vendeu; então poderá voltar para a sua propriedade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3497, 28, "Mas, se não adquirir recursos para devolver-lhe o valor, a propriedade que vendeu permanecerá em posse do comprador até o ano do jubileu. Será devolvida no Jubileu, e ele então poderá voltar para a sua propriedade.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3498, 29, """Se um homem vender uma casa numa cidade murada, terá o direito de resgate até que se complete um ano após a venda. Nesse período poderá resgatá-la.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3499, 30, "Se não for resgatada antes de se completar um ano, a casa da cidade murada pertencerá definitivamente ao comprador e aos seus descendentes; não será devolvida no Jubileu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3500, 31, "Mas as casas dos povoados sem muros ao redor serão consideradas campo aberto. Poderão ser resgatadas e serão devolvidas no Jubileu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3501, 32, """No caso das cidades dos levitas, eles sempre terão direito de resgatar suas casas nas cidades que lhes pertencem.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3502, 33, "Assim, a propriedade dos levitas, isto é, uma casa vendida em qualquer cidade deles, é resgatável e deverá ser devolvida no Jubileu, porque as casas das cidades dos levitas são propriedade deles entre os israelitas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3503, 34, "Mas as pastagens pertencentes às suas cidades não serão vendidas; são propriedade permanente deles.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3504, 35, """Se alguém do seu povo empobrecer e não puder sustentar-se, ajudem-no como se faz ao estrangeiro e ao residente temporário, para que possa continuar a viver entre vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3505, 36, "Não cobrem dele juro algum, mas temam o seu Deus, para que o seu próximo continue a viver entre vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3506, 37, "Vocês não poderão exigir dele juros nem emprestar-lhe mantimento visando lucro.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3507, 38, "Eu sou o Senhor, o Deus de vocês, que os tirou da terra do Egito para dar-lhes a terra de Canaã e para ser o seu Deus.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3508, 39, """Se alguém do seu povo empobrecer e se vender a algum de vocês, não o façam trabalhar como escravo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3509, 40, "Ele deverá ser tratado como trabalhador contratado ou como residente temporário; trabalhará para quem o comprou até o ano do jubileu.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3510, 41, "Então ele e os seus filhos estarão livres, e ele poderá voltar para o seu próprio clã e para a propriedade dos seus antepassados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3511, 42, "Pois os israelitas são meus servos, a quem tirei da terra do Egito; não poderão ser vendidos como escravos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3512, 43, "Não dominem impiedosamente sobre eles, mas temam o seu Deus.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3513, 44, """Os seus escravos e as suas escravas deverão vir dos povos que vivem ao redor de vocês; deles vocês poderão comprar escravos e escravas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3514, 45, "Também poderão comprá-los entre os filhos dos residentes temporários que vivem entre vocês e entre os que pertencem aos clãs deles, ainda que nascidos na terra de vocês; eles se tornarão propriedade de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3515, 46, "Vocês poderão deixá-los como herança para os seus filhos e poderão fazê-los escravos para sempre, mas sobre os seus irmãos israelitas vocês não poderão dominar impiedosamente.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3516, 47, """Se um estrangeiro ou um residente temporário entre vocês enriquecer e alguém do seu povo empobrecer e se vender a esse estrangeiro ou a alguém que pertence ao clã desse estrangeiro,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3517, 48, "manterá o direito de resgate mesmo depois de vender a si mesmo. Um dos seus parentes poderá resgatá-lo:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3518, 49, "ou tio, ou primo, ou qualquer parente próximo poderá resgatá-lo. Se, todavia, prosperar, poderá resgatar a si mesmo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3519, 50, "Ele e o seu comprador contarão o tempo desde o ano em que vendeu a si mesmo até o ano do jubileu. O preço do resgate se baseará no salário de um empregado contratado por aquele número de anos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3520, 51, "Se restarem muitos anos, pagará o seu resgate proporcionalmente ao preço de compra.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3521, 52, "Se restarem apenas poucos anos até o ano do jubileu, fará o cálculo, e pagará o seu resgate proporcionalmente aos anos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3522, 53, "Ele deverá ser tratado como um empregado contratado anualmente; não permitam que o seu senhor domine impiedosamente sobre ele.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3523, 54, """Se não for resgatado por nenhuma dessas maneiras, ele e os seus filhos estarão livres no ano do jubileu,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3524, 55, "porque os israelitas são meus servos, os quais tirei da terra do Egito. Eu sou o Senhor, o Deus de vocês"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3525, 1, """Não façam ídolos, nem imagens, nem colunas sagradas para vocês, e não coloquem nenhuma pedra esculpida em sua terra para curvar-se diante dela. Eu sou o Senhor, o Deus de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3526, 2, """Guardem os meus sábados e reverenciem o meu santuário. Eu sou o Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3527, 3, """Se vocês seguirem os meus decretos e obedecerem aos meus mandamentos, e os colocarem em prática,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3528, 4, "eu lhes mandarei chuva na estação certa, e a terra dará a sua colheita e as árvores do campo darão o seu fruto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3529, 5, "A debulha prosseguirá até a colheita das uvas, e a colheita das uvas prosseguirá até a época da plantação, e vocês comerão até ficarem satisfeitos e viverão em segurança na terra de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3530, 6, """Estabelecerei paz na terra, e vocês se deitarão, e ninguém os amedrontará. Farei desaparecer da terra os animais selvagens, e a espada não passará pela terra de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3531, 7, "Vocês perseguirão os seus inimigos, e estes cairão à espada diante de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3532, 8, "Cinco de vocês perseguirão cem, cem de vocês perseguirão dez mil, e os seus inimigos cairão à espada diante de vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3533, 9, """Eu me voltarei para vocês e os farei prolíferos; e os multiplicarei e guardarei a minha aliança com vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3534, 10, "Vocês ainda estarão comendo da colheita armazenada no ano anterior, quando terão que se livrar dela para dar espaço para a nova colheita.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3535, 11, "Estabelecerei a minha habitação entre vocês e não os rejeitarei.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3536, 12, "Andarei entre vocês e serei o seu Deus, e vocês serão o meu povo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3537, 13, "Eu sou o Senhor, o Deus de vocês, que os tirou da terra do Egito para que não mais fossem escravos deles; quebrei as traves do jugo que os prendia e os fiz andar de cabeça erguida. """, 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3538, 14, """Mas, se vocês não me ouvirem e não colocarem em prática todos esses mandamentos,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3539, 15, "e desprezarem os meus decretos, rejeitarem as minhas ordenanças, deixarem de colocar em prática todos os meus mandamentos e forem infiéis à minha aliança,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3540, 16, "então assim os tratarei: eu lhes trarei pavor repentino, doenças e febre que tirarão a visão e lhes definharão a vida. Vocês semearão inutilmente, porque os seus inimigos comerão as suas sementes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3541, 17, "O meu rosto estará contra vocês, e vocês serão derrotados pelos inimigos; os seus adversários os dominarão, e vocês fugirão mesmo quando ninguém os estiver perseguindo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3542, 18, """Se depois disso tudo vocês não me ouvirem, eu os castigarei sete vezes mais pelos seus pecados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3543, 19, "Eu lhes quebrarei o orgulho rebelde e farei que o céu sobre vocês fique como ferro e a terra de vocês fique como bronze.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3544, 20, "A força de vocês será gasta em vão, porque a terra não lhes dará colheita, nem as árvores da terra lhes darão fruto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3545, 21, """Se continuarem se opondo a mim e recusarem ouvir-me, eu os castigarei sete vezes mais, conforme os seus pecados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3546, 22, "Mandarei contra vocês animais selvagens que matarão os seus filhos, acabarei com os seus rebanhos e reduzirei vocês a tão poucos que os seus caminhos ficarão desertos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3547, 23, """Se apesar disso vocês não aceitarem a minha disciplina, mas continuarem a opor-se a mim,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3548, 24, "eu mesmo me oporei a vocês e os castigarei sete vezes mais por causa dos seus pecados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3549, 25, "E trarei a espada contra vocês para vingar a aliança. Quando se refugiarem em suas cidades, eu lhes mandarei uma praga, e vocês serão entregues em mãos inimigas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3550, 26, "Quando eu lhes cortar o suprimento de pão, dez mulheres assarão o pão num único forno e repartirão o pão a peso. Vocês comerão, mas não ficarão satisfeitos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3551, 27, """Se apesar disso tudo vocês ainda não me ouvirem, mas continuarem a opor-se a mim,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3552, 28, "então com furor me oporei a vocês, e eu mesmo os castigarei sete vezes mais por causa dos seus pecados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3553, 29, "Vocês comerão a carne dos seus filhos e das suas filhas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3554, 30, "Destruirei os seus altares idólatras, despedaçarei os seus altares de incenso e empilharei os seus cadáveres sobre os seus ídolos mortos, e rejeitarei vocês.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3555, 31, "Deixarei as cidades de vocês em ruínas e arrasarei os seus santuários, e não terei prazer no aroma das suas ofertas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3556, 32, "Desolarei a terra a ponto de ficarem perplexos os seus inimigos que vierem ocupá-la.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3557, 33, "Espalharei vocês entre as nações e desembainharei a espada contra vocês. Sua terra ficará desolada, e as suas cidades, em ruínas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3558, 34, "Então a terra desfrutará os seus anos sabáticos enquanto estiver desolada e enquanto vocês estiverem na terra dos seus inimigos; e a terra descansará e desfrutará os seus sábados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3559, 35, "Enquanto estiver desolada, a terra terá o descanso sabático que não teve quando vocês a habitaram.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3560, 36, """Quanto aos que sobreviverem, eu lhes encherei o coração de tanto medo na terra do inimigo, que o som de uma folha levada pelo vento os porá em fuga. Correrão como quem foge da espada, e cairão, sem que ninguém os persiga.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3561, 37, "Tropeçarão uns nos outros, como que fugindo da espada, sem que ninguém os esteja perseguindo. Assim vocês não poderão subsistir diante dos inimigos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3562, 38, "Vocês perecerão entre as nações, e a terra dos seus inimigos os devorará.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3563, 39, "Os que sobreviverem apodrecerão na terra do inimigo por causa dos seus pecados, e também por causa dos pecados dos seus antepassados.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3564, 40, """Mas, se confessarem os seus pecados e os pecados dos seus antepassados, sua infidelidade e oposição a mim,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3565, 41, "que me levaram a opor-me a eles e a enviá-los para a terra dos seus inimigos; se o seu coração obstinado se humilhar, e eles aceitarem o castigo do seu pecado,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3566, 42, "eu me lembrarei da minha aliança com Jacó e da minha aliança com Isaque e da minha aliança com Abraão, e também me lembrarei da terra,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3567, 43, "que por eles será abandonada e desfrutará os seus sábados enquanto permanecer desolada. Receberão o castigo pelos seus pecados porque desprezaram as minhas ordenanças e rejeitaram os meus decretos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3568, 44, "Apesar disso, quando estiverem na terra do inimigo, não os desprezarei, nem os rejeitarei, para destruí-los totalmente, quebrando a minha aliança com eles, pois eu sou o Senhor, o Deus deles.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3569, 45, "Mas por amor deles eu me lembrarei da aliança com os seus antepassados que tirei da terra do Egito à vista das nações, para ser o Deus deles. Eu sou o Senhor. """, 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3570, 46, "São esses os decretos, as ordenanças e as leis que o Senhor estabeleceu no monte Sinai entre ele próprio e os israelitas, por intermédio de Moisés.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3571, 1, "Disse também o SENHOR a Moisés:", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3572, 2, """Diga o seguinte aos israelitas: Se alguém fizer um voto especial, dedicando pessoas ao Senhor, faça-o conforme o devido valor;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3573, 3, "atribua aos homens entre vinte e sessenta anos o valor de seiscentos gramas de prata, com base no peso padrão do santuário;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3574, 4, "e, se for mulher, atribua-lhe o valor de trezentos e sessenta gramas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3575, 5, "Se for alguém que tenha entre cinco e vinte anos, atribua aos homens o valor de duzentos e quarenta gramas e às mulheres o valor de cento e vinte gramas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3576, 6, "Se for alguém que tenha entre um mês e cinco anos de idade, atribua aos meninos o valor de sessenta gramas de prata e às meninas o valor de trinta e seis gramas de prata.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3577, 7, "Se for alguém que tenha de sessenta anos para cima, atribua aos homens o valor de cento e oitenta gramas e às mulheres o valor de cento e vinte gramas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3578, 8, "Se quem fizer o voto for pobre demais para pagar o valor especificado, apresentará a pessoa ao sacerdote, que estabelecerá o valor de acordo com as possibilidades do homem que fez o voto.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3579, 9, """Se o que ele prometeu mediante voto for um animal aceitável como oferta ao Senhor, um animal assim dado ao Senhor torna-se santo.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3580, 10, "Ele não poderá trocá-lo nem substituir um animal ruim por um bom, nem um animal bom por um ruim; caso troque um animal por outro, tanto o substituto quanto o substituído se tornarão santos.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3581, 11, "Se o que ele prometeu mediante voto for um animal impuro, não aceitável como oferta ao Senhor, o animal será apresentado ao sacerdote,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3582, 12, "que o avaliará por suas qualidades. A avaliação do sacerdote determinará o valor do animal.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3583, 13, "Se o dono desejar resgatar o animal, terá que acrescentar um quinto ao seu valor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3584, 14, """Se um homem consagrar a sua casa ao Senhor, o sacerdote avaliará a casa por suas qualidades. A avaliação do sacerdote determinará o valor da casa.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3585, 15, "Se o homem que consagrar a sua casa quiser resgatá-la, terá que acrescentar um quinto ao seu valor, e a casa voltará a ser sua.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3586, 16, """Se um homem consagrar ao Senhor parte das terras da sua família, sua avaliação será de acordo com a semeadura: seiscentos gramas de prata para cada barril de semente de cevada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3587, 17, "Se consagrar a sua terra durante o ano do jubileu, o valor será integral.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3588, 18, "Mas, se a consagrar depois do Jubileu, o sacerdote calculará o valor de acordo com o número de anos que faltarem para o próximo ano do jubileu, e o valor será reduzido.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3589, 19, "Se o homem que consagrar a sua terra desejar resgatá-la, terá que acrescentar um quinto ao seu valor, e a terra voltará a ser sua.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3590, 20, "Mas se não a resgatar, ou se a tiver vendido, não poderá mais ser resgatada;", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3591, 21, "quando a terra for liberada no Jubileu, será santa, consagrada ao Senhor, e se tornará propriedade dos sacerdotes.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3592, 22, """Se um homem consagrar ao Senhor terras que tenha comprado, terras que não fazem parte da propriedade da sua família,", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3593, 23, "o sacerdote determinará o valor de acordo com o tempo que falta para o ano do jubileu; o homem pagará o valor no mesmo dia, consagrando-o ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3594, 24, "No ano do jubileu as terras serão devolvidas àquele de quem ele as comprou.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3595, 25, "Todos os valores serão calculados com base no peso padrão do santuário, que são doze gramas.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3596, 26, """Ninguém poderá consagrar a primeira cria de um animal, pois já pertence ao Senhor; seja cria de vaca, seja de cabra, seja de ovelha, pertence ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3597, 27, "Mas se for a cria de um animal impuro, poderá resgatá-la pelo valor estabelecido, acrescentando um quinto a esse valor. Se não for resgatada, será vendida pelo valor estabelecido.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3598, 28, """Todavia, nada que um homem possua e consagre ao Senhor, seja homem, seja animal, sejam terras de sua propriedade, poderá ser vendido ou resgatado; todas as coisas assim consagradas são santíssimas ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3599, 29, """Nenhuma pessoa consagrada para a destruição poderá ser resgatada; terá que ser executada.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3600, 30, """Todos os dízimos da terra, seja dos cereais, seja das frutas das árvores, pertencem ao Senhor; são consagrados ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3601, 31, "Se um homem desejar resgatar parte do seu dízimo, terá que acrescentar um quinto ao seu valor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3602, 32, "O dízimo dos seus rebanhos, um de cada dez animais que passem debaixo da vara do pastor, será consagrado ao Senhor.", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3603, 33, "O dono não poderá retirar os bons dentre os ruins, nem fazer qualquer troca. Se fizer alguma troca, tanto o animal quanto o substituto se tornarão consagrados e não poderão ser resgatados"".", 2);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3604, 34, "São esses os mandamentos que o Senhor ordenou a Moisés no monte Sinai para os israelitas.", 2);