INSERT INTO verses (id, number, text, chapter_id) VALUES (24217, 1, "Princípio do evangelho de Jesus Cristo, o Filho de Deus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24218, 2, "Conforme está escrito no profeta Isaías: ""Enviarei à tua frente o meu mensageiro; ele preparará o teu caminho""—", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24219, 3, """voz do que clama no deserto: ‘Preparem o caminho para o Senhor, façam veredas retas para ele’ "".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24220, 4, "Assim surgiu João, batizando no deserto e pregando um batismo de arrependimento para o perdão dos pecados.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24221, 5, "A ele vinha toda a região da Judéia e todo o povo de Jerusalém. Confessando os seus pecados, eram batizados por ele no rio Jordão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24222, 6, "João vestia roupas feitas de pêlos de camelo, usava um cinto de couro e comia gafanhotos e mel silvestre.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24223, 7, "E esta era a sua mensagem: ""Depois de mim vem alguém mais poderoso do que eu, tanto que não sou digno nem de curvar-me e desamarrar as correias das suas sandálias.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24224, 8, "Eu os batizo com água, mas ele os batizará com o Espírito Santo"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24225, 9, "Naquela ocasião Jesus veio de Nazaré da Galiléia e foi batizado por João no Jordão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24226, 10, "Assim que saiu da água, Jesus viu os céus se abrindo, e o Espírito descendo como pomba sobre ele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24227, 11, "Então veio dos céus uma voz: ""Tu és o meu Filho amado; em ti me agrado"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24228, 12, "Logo após, o Espírito o impeliu para o deserto.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24229, 13, "Ali esteve quarenta dias, sendo tentado por Satanás. Estava com os animais selvagens, e os anjos o serviam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24230, 14, "Depois que João foi preso, Jesus foi para a Galiléia, proclamando as boas novas de Deus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24231, 15, """O tempo é chegado"", dizia ele. ""O Reino de Deus está próximo. Arrependam-se e creiam nas boas novas! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24232, 16, "Andando à beira do mar da Galiléia, Jesus viu Simão e seu irmão André lançando redes ao mar, pois eram pescadores.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24233, 17, "E disse Jesus: ""Sigam-me, e eu os farei pescadores de homens"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24234, 18, "No mesmo instante eles deixaram as suas redes e o seguiram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24235, 19, "Indo um pouco mais adiante, viu num barco Tiago, filho de Zebedeu, e João, seu irmão, preparando as suas redes.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24236, 20, "Logo os chamou, e eles o seguiram, deixando Zebedeu, seu pai, com os empregados no barco.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24237, 21, "Eles foram para Cafarnaum e, assim que chegou o sábado, Jesus entrou na sinagoga e começou a ensinar.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24238, 22, "Todos ficavam maravilhados com o seu ensino, porque lhes ensinava como alguém que tem autoridade e não como os mestres da lei.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24239, 23, "Justamente naquela hora, na sinagoga, um homem possesso de um espírito imundo gritou:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24240, 24, """O que queres conosco, Jesus de Nazaré? Vieste para nos destruir? Sei quem tu és: o Santo de Deus! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24241, 25, """Cale-se e saia dele! "", repreendeu-o Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24242, 26, "O espírito imundo sacudiu o homem violentamente e saiu dele gritando.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24243, 27, "Todos ficaram tão admirados que perguntavam uns aos outros: ""O que é isto? Um novo ensino — e com autoridade! Até aos espíritos imundos ele dá ordens, e eles lhe obedecem! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24244, 28, "As notícias a seu respeito se espalharam rapidamente por toda a região da Galiléia.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24245, 29, "Logo que saíram da sinagoga, foram com Tiago e João à casa de Simão e André.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24246, 30, "A sogra de Simão estava de cama, com febre, e falaram a respeito dela a Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24247, 31, "Então ele se aproximou dela, tomou-a pela mão e ajudou-a a levantar-se. A febre a deixou, e ela começou a servi-los.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24248, 32, "Ao anoitecer, depois do pôr-do-sol, o povo levou a Jesus todos os doentes e os endemoninhados.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24249, 33, "Toda a cidade se reuniu à porta da casa,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24250, 34, "e Jesus curou muitos que sofriam de várias doenças. Também expulsou muitos demônios; não permitia, porém, que estes falassem, porque sabiam quem ele era.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24251, 35, "De madrugada, quando ainda estava escuro, Jesus levantou-se, saiu de casa e foi para um lugar deserto, onde ficou orando.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24252, 36, "Simão e seus companheiros foram procurá-lo", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24253, 37, "e, ao encontrá-lo, disseram: ""Todos estão te procurando! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24254, 38, "Jesus respondeu: ""Vamos para outro lugar, para os povoados vizinhos, para que também lá eu pregue. Foi para isso que eu vim"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24255, 39, "Então ele percorreu toda a Galiléia, pregando nas sinagogas e expulsando os demônios.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24256, 40, "Um leproso aproximou-se dele e suplicou-lhe de joelhos: ""Se quiseres, podes purificar-me! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24257, 41, "Cheio de compaixão, Jesus estendeu a mão, tocou nele e disse: ""Quero. Seja purificado! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24258, 42, "Imediatamente a lepra o deixou, e ele foi purificado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24259, 43, "Em seguida Jesus o despediu, com uma severa advertência:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24260, 44, """Olhe, não conte isso a ninguém. Mas vá mostrar-se ao sacerdote e ofereça pela sua purificação os sacrifícios que Moisés ordenou, para que sirva de testemunho"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24261, 45, "Ele, porém, saiu e começou a tornar público o fato, espalhando a notícia. Por isso Jesus não podia mais entrar publicamente em nenhuma cidade, mas ficava fora, em lugares solitários. Todavia, assim mesmo vinha a ele gente de todas as partes.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24262, 1, "Poucos dias depois, tendo Jesus entrado novamente em Cafarnaum, o povo ouviu falar que ele estava em casa.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24263, 2, "Então muita gente se reuniu ali, de forma que não havia lugar nem junto à porta; e ele lhes pregava a palavra.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24264, 3, "Vieram alguns homens, trazendo-lhe um paralítico, carregado por quatro deles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24265, 4, "Não podendo levá-lo até Jesus, por causa da multidão, removeram parte da cobertura do lugar onde Jesus estava e, através de uma abertura no teto, baixaram a maca em que estava deitado o paralítico.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24266, 5, "Vendo a fé que eles tinham, Jesus disse ao paralítico: ""Filho, os seus pecados estão perdoados"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24267, 6, "Estavam sentados ali alguns mestres da lei, raciocinando em seu íntimo:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24268, 7, """Por que esse homem fala assim? Está blasfemando! Quem pode perdoar pecados, a não ser somente Deus? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24269, 8, "Jesus percebeu logo em seu espírito que era isso que eles estavam pensando e lhes disse: ""Por que vocês estão remoendo essas coisas em seus corações?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24270, 9, "Que é mais fácil dizer ao paralítico: ‘Os seus pecados estão perdoados’, ou: ‘Levante-se, pegue a sua maca e ande’?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24271, 10, "Mas, para que vocês saibam que o Filho do homem tem na terra autoridade para perdoar pecados — disse ao paralítico —", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24272, 11, "eu lhe digo: Levante-se, pegue a sua maca e vá para casa"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24273, 12, "Ele se levantou, pegou a maca e saiu à vista de todos. Estes ficaram atônitos e glorificaram a Deus, dizendo: ""Nunca vimos nada igual! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24274, 13, "Jesus saiu outra vez para beira-mar. Uma grande multidão aproximou-se, e ele começou a ensiná-los.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24275, 14, "Passando por ali, viu Levi, filho de Alfeu, sentado na coletoria, e disse-lhe: ""Siga-me"". Levi levantou-se e o seguiu.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24276, 15, "Durante uma refeição na casa de Levi, muitos publicanos e ""pecadores"" estavam comendo com Jesus e seus discípulos, pois havia muitos que o seguiam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24277, 16, "Quando os mestres da lei que eram fariseus o viram comendo com ""pecadores"" e publicanos, perguntaram aos discípulos de Jesus: ""Por que ele come com publicanos e ‘pecadores’? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24278, 17, "Ouvindo isso, Jesus lhes disse: ""Não são os que têm saúde que precisam de médico, mas sim os doentes. Eu não vim para chamar justos, mas pecadores"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24279, 18, "Os discípulos de João e os fariseus estavam jejuando. Algumas pessoas vieram a Jesus e lhe perguntaram: ""Por que os discípulos de João e os dos fariseus jejuam, mas os teus não? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24280, 19, "Jesus respondeu: ""Como podem os convidados do noivo jejuar enquanto este está com eles? Não podem, enquanto o têm consigo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24281, 20, "Mas virão dias quando o noivo lhes será tirado; e nesse tempo jejuarão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24282, 21, """Ninguém põe remendo de pano novo em roupa velha, pois o remendo forçará a roupa, tornando pior o rasgo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24283, 22, "E ninguém põe vinho novo em vasilhas de couro velhas; se o fizer, o vinho rebentará as vasilhas, e tanto o vinho quanto as vasilhas se estragarão. Pelo contrário, põe-se vinho novo em vasilhas de couro novas"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24284, 23, "Certo sábado Jesus estava passando pelas lavouras de cereal. Enquanto caminhavam, seus discípulos começaram a colher espigas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24285, 24, "Os fariseus lhe perguntaram: ""Olha, por que eles estão fazendo o que não é permitido no sábado? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24286, 25, "Ele respondeu: ""Vocês nunca leram o que fez Davi quando ele e seus companheiros estavam necessitados e com fome?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24287, 26, "Nos dias de Abiatar, o sumo sacerdote, ele entrou na casa de Deus e comeu os pães da Presença, que apenas aos sacerdotes era permitido comer, e os deu também aos seus companheiros"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24288, 27, "E então lhes disse: ""O sábado foi feito por causa do homem, e não o homem por causa do sábado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24289, 28, "Assim, pois, o Filho do homem é Senhor até mesmo do sábado"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24290, 1, "Noutra ocasião ele entrou na sinagoga, e estava ali um homem com uma das mãos atrofiada.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24291, 2, "Alguns deles estavam procurando um motivo para acusar Jesus; por isso o observavam atentamente, para ver se ele iria curá-lo no sábado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24292, 3, "Jesus disse ao homem da mão atrofiada: ""Levante-se e venha para o meio"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24293, 4, "Depois Jesus lhes perguntou: ""O que é permitido fazer no sábado: o bem ou o mal, salvar a vida ou matar? "" Mas eles permaneceram em silêncio.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24294, 5, "Irado, olhou para os que estavam à sua volta e, profundamente entristecido por causa dos seus corações endurecidos, disse ao homem: ""Estenda a mão"". Ele a estendeu, e ela foi restaurada.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24295, 6, "Então os fariseus saíram e começaram a conspirar com os herodianos contra Jesus, sobre como poderiam matá-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24296, 7, "Jesus retirou-se com os seus discípulos para o mar, e uma grande multidão vinda da Galiléia o seguia.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24297, 8, "Quando ouviram a respeito de tudo o que ele estava fazendo, muitas pessoas procedentes da Judéia, de Jerusalém, da Iduméia e das regiões do outro lado do Jordão e dos arredores de Tiro e de Sidom foram atrás dele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24298, 9, "Por causa da multidão, ele disse aos discípulos que lhe preparassem um pequeno barco, para evitar que o comprimissem.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24299, 10, "Pois ele havia curado a muitos, de modo que os que sofriam de doenças ficavam se empurrando para conseguir tocar nele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24300, 11, "Sempre que os espíritos imundos o viam, prostravam-se diante dele e gritavam: ""Tu és o Filho de Deus"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24301, 12, "Mas ele lhes dava ordens severas para que não dissessem quem ele era.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24302, 13, "Jesus subiu a um monte e chamou a si aqueles que ele quis, os quais vieram para junto dele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24303, 14, "Escolheu doze, designando-os como apóstolos, para que estivessem com ele, os enviasse a pregar", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24304, 15, "e tivessem autoridade para expulsar demônios.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24305, 16, "Estes são os doze que ele escolheu: Simão, a quem deu o nome de Pedro;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24306, 17, "Tiago, filho de Zebedeu, e João, seu irmão, aos quais deu o nome de Boanerges, que significa filhos do trovão;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24307, 18, "André; Filipe; Bartolomeu; Mateus; Tomé; Tiago, filho de Alfeu; Tadeu; Simão, o zelote,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24308, 19, "e Judas Iscariotes, que o traiu.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24309, 20, "Então Jesus entrou numa casa, e novamente reuniu-se ali uma multidão, de modo que ele e os seus discípulos não conseguiam nem comer.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24310, 21, "Quando seus familiares ouviram falar disso, saíram para apoderar-se dele, pois diziam: ""Ele está fora de si"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24311, 22, "E os mestres da lei que haviam descido de Jerusalém diziam: ""Ele está com Belzebu! Pelo príncipe dos demônios é que ele expulsa demônios"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24312, 23, "Então Jesus os chamou e lhes falou por parábolas: ""Como pode Satanás expulsar Satanás?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24313, 24, "Se um reino estiver dividido contra si mesmo, não poderá subsistir.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24314, 25, "Se uma casa estiver dividida contra si mesma, também não poderá subsistir.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24315, 26, "E se Satanás se opuser a si mesmo e estiver dividido, não poderá subsistir; chegou o seu fim.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24316, 27, "De fato, ninguém pode entrar na casa do homem forte e levar dali os seus bens, sem que antes o amarre. Só então poderá roubar a casa dele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24317, 28, "Eu lhes asseguro que todos os pecados e blasfêmias dos homens lhes serão perdoados,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24318, 29, "mas quem blasfemar contra o Espírito Santo nunca terá perdão: é culpado de pecado eterno"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24319, 30, "Jesus falou isso porque eles estavam dizendo: ""Ele está com um espírito imundo"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24320, 31, "Então chegaram a mãe e os irmãos de Jesus. Ficando do lado de fora, mandaram alguém chamá-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24321, 32, "Havia muita gente assentada ao seu redor; e lhe disseram: ""Tua mãe e teus irmãos estão lá fora e te procuram"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24322, 33, """Quem é minha mãe, e quem são meus irmãos? "", perguntou ele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24323, 34, "Então olhou para os que estavam assentados ao seu redor e disse: ""Aqui estão minha mãe e meus irmãos!", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24324, 35, "Quem faz a vontade de Deus, este é meu irmão, minha irmã e minha mãe"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24325, 1, "Novamente Jesus começou a ensinar à beira-mar. Reuniu-se ao seu redor uma multidão tão grande que ele teve que entrar num barco e assentar-se nele. O barco estava no mar, enquanto todo o povo ficava na beira da praia.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24326, 2, "Ele lhes ensinava muitas coisas por parábolas, dizendo em seu ensino:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24327, 3, """Ouçam! O semeador saiu a semear.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24328, 4, "Enquanto lançava a semente, parte dela caiu à beira do caminho, e as aves vieram e a comeram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24329, 5, "Parte dela caiu em terreno pedregoso, onde não havia muita terra; e logo brotou, porque a terra não era profunda.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24330, 6, "Mas quando saiu o sol, as plantas se queimaram e secaram, porque não tinham raiz.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24331, 7, "Outra parte caiu entre espinhos, que cresceram e sufocaram as plantas, de forma que ela não deu fruto.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24332, 8, "Outra ainda caiu em boa terra, germinou, cresceu e deu boa colheita, a trinta, sessenta e até cem por um"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24333, 9, "A seguir Jesus acrescentou: ""Aquele que tem ouvidos para ouvir, ouça! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24334, 10, "Quando ele ficou sozinho, os Doze e os outros que estavam ao seu redor lhe fizeram perguntas acerca das parábolas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24335, 11, "Ele lhes disse: ""A vocês foi dado o mistério do Reino de Deus, mas aos que estão fora tudo é dito por parábolas,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24336, 12, "a fim de que, ‘ainda que vejam, não percebam, ainda que ouçam, não entendam; de outro modo, poderiam converter-se e ser perdoados! ’""", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24337, 13, "Então Jesus lhes perguntou: ""Vocês não entendem esta parábola? Como, então, compreenderão todas as outras parábolas?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24338, 14, "O semeador semeia a palavra.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24339, 15, "Algumas pessoas são como a semente à beira do caminho, onde a palavra é semeada. Logo que a ouvem, Satanás vem e retira a palavra nelas semeada.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24340, 16, "Outras, como a semente lançada em terreno pedregoso, ouvem a palavra e logo a recebem com alegria.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24341, 17, "Todavia, visto que não têm raiz em si mesmas, permanecem por pouco tempo. Quando surge alguma tribulação ou perseguição por causa da palavra, logo a abandonam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24342, 18, "Outras ainda, como a semente lançada entre espinhos, ouvem a palavra;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24343, 19, "mas quando chegam as preocupações desta vida, o engano das riquezas e os anseios por outras coisas, sufocam a palavra, tornando-a infrutífera.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24344, 20, "Outras pessoas são como a semente lançada em boa terra: ouvem a palavra, aceitam-na e dão uma colheita de trinta, sessenta e até cem por um"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24345, 21, "Ele lhes disse: ""Quem traz uma candeia para ser colocada debaixo de uma vasilha ou de uma cama? Acaso não a coloca num lugar apropriado?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24346, 22, "Porque não há nada oculto, senão para ser revelado, e nada escondido senão para ser trazido à luz.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24347, 23, "Se alguém tem ouvidos para ouvir, ouça!", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24348, 24, """Considerem atentamente o que vocês estão ouvindo"", continuou ele. ""Com a medida com que medirem, vocês serão medidos; e ainda mais lhes acrescentarão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24349, 25, "A quem tiver, mais lhe será dado; de quem não tiver, até o que tem lhe será tirado"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24350, 26, "Ele prosseguiu dizendo: ""O Reino de Deus é semelhante a um homem que lança a semente sobre a terra.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24351, 27, "Noite e dia, quer ele durma quer se levante, a semente germina e cresce, embora ele não saiba como.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24352, 28, "A terra por si própria produz o grão: primeiro o talo, depois a espiga e, então, o grão cheio na espiga.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24353, 29, "Logo que o grão fica maduro, o homem lhe passa a foice, porque chegou a colheita"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24354, 30, "Novamente ele disse: ""Com que compararemos o Reino de Deus? Que parábola usaremos para descrevê-lo?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24355, 31, "É como um grão de mostarda, que, quando plantada, é a menor semente de todas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24356, 32, "No entanto, plantada, ela cresce e se torna a maior de todas as hortaliças, com ramos tão grandes que as aves do céu podem abrigar-se à sua sombra"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24357, 33, "Com muitas parábolas semelhantes Jesus lhes anunciava a palavra, tanto quanto podiam receber.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24358, 34, "Não lhes dizia nada sem usar alguma parábola. Quando, porém, estava a sós com os seus discípulos, explicava-lhes tudo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24359, 35, "Naquele dia, ao anoitecer, disse ele aos seus discípulos: ""Vamos atravessar para o outro lado"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24360, 36, "Deixando a multidão, eles o levaram no barco, assim como estava. Outros barcos também o acompanhavam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24361, 37, "Levantou-se um forte vendaval, e as ondas se lançavam sobre o barco, de forma que este foi se enchendo de água.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24362, 38, "Jesus estava na popa, dormindo com a cabeça sobre um travesseiro. Os discípulos o acordaram e clamaram: ""Mestre, não te importas que morramos? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24363, 39, "Ele se levantou, repreendeu o vento e disse ao mar: ""Aquiete-se! Acalme-se! "" O vento se aquietou, e fez-se completa bonança.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24364, 40, "Então perguntou aos seus discípulos: ""Por que vocês estão com tanto medo? Ainda não têm fé? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24365, 41, "Eles estavam apavorados e perguntavam uns aos outros: ""Quem é este que até o vento e o mar lhe obedecem? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24366, 1, "Eles atravessaram o mar e foram para a região dos gerasenos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24367, 2, "Quando Jesus desembarcou, um homem com um espírito imundo veio dos sepulcros ao seu encontro.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24368, 3, "Esse homem vivia nos sepulcros, e ninguém conseguia prendê-lo, nem mesmo com correntes;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24369, 4, "pois muitas vezes lhe haviam sido acorrentados pés e mãos, mas ele arrebentara as correntes e quebrara os ferros de seus pés. Ninguém era suficientemente forte para dominá-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24370, 5, "Noite e dia ele andava gritando e cortando-se com pedras entre os sepulcros e nas colinas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24371, 6, "Quando ele viu Jesus de longe, correu e prostrou-se diante dele,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24372, 7, "e gritou em alta voz: ""Que queres comigo, Jesus, Filho do Deus Altíssimo? Rogo-te por Deus que não me atormentes! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24373, 8, "Pois Jesus lhe tinha dito: ""Saia deste homem, espírito imundo! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24374, 9, "Então Jesus lhe perguntou: ""Qual é o seu nome? "" ""Meu nome é Legião"", respondeu ele, ""porque somos muitos"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24375, 10, "E implorava a Jesus, com insistência, que não os mandasse sair daquela região.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24376, 11, "Uma grande manada de porcos estava pastando numa colina próxima.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24377, 12, "Os demônios imploraram a Jesus: ""Manda-nos para os porcos, para que entremos neles"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24378, 13, "Ele lhes deu permissão, e os espíritos imundos saíram e entraram nos porcos. A manada de cerca de dois mil porcos atirou-se precipício abaixo, em direção ao mar, e nele se afogou.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24379, 14, "Os que cuidavam dos porcos fugiram e contaram esses fatos na cidade e nos campos, e o povo foi ver o que havia acontecido.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24380, 15, "Quando se aproximaram de Jesus, viram ali o homem que fora possesso da legião de demônios, assentado, vestido e em perfeito juízo; e ficaram com medo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24381, 16, "Os que o tinham visto contaram ao povo o que acontecera ao endemoninhado, e falaram também sobre os porcos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24382, 17, "Então o povo começou a suplicar a Jesus que saísse do território deles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24383, 18, "Quando Jesus estava entrando no barco, o homem que estivera endemoninhado suplicava-lhe que o deixasse ir com ele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24384, 19, "Jesus não o permitiu, mas disse: ""Vá para casa, para a sua família e anuncie-lhes quanto o Senhor fez por você e como teve misericórdia de você"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24385, 20, "Então, aquele homem se foi e começou a anunciar em Decápolis quanto Jesus tinha feito por ele. Todos ficavam admirados.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24386, 21, "Tendo Jesus voltado de barco para a outra margem, uma grande multidão se reuniu ao seu redor, enquanto ele estava à beira do mar.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24387, 22, "Então chegou ali um dos dirigentes da sinagoga, chamado Jairo. Vendo Jesus, prostrou-se aos seus pés", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24388, 23, "e lhe implorou insistentemente: ""Minha filhinha está morrendo! Vem, por favor, e impõe as mãos sobre ela, para que seja curada e viva"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24389, 24, "Jesus foi com ele. Uma grande multidão o seguia e o comprimia.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24390, 25, "E estava ali certa mulher que havia doze anos vinha sofrendo de uma hemorragia.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24391, 26, "Ela padecera muito sob o cuidado de vários médicos e gastara tudo o que tinha, mas, em vez de melhorar, piorava.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24392, 27, "Quando ouviu falar de Jesus, chegou-se por trás dele, no meio da multidão, e tocou em seu manto,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24393, 28, "porque pensava: ""Se eu tão-somente tocar em seu manto, ficarei curada"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24394, 29, "Imediatamente cessou sua hemorragia e ela sentiu em seu corpo que estava livre do seu sofrimento.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24395, 30, "No mesmo instante, Jesus percebeu que dele havia saído poder, virou-se para a multidão e perguntou: ""Quem tocou em meu manto? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24396, 31, "Responderam os seus discípulos: ""Vês a multidão aglomerada ao teu redor e ainda perguntas: ‘Quem tocou em mim? ’ """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24397, 32, "Mas Jesus continuou olhando ao seu redor para ver quem tinha feito aquilo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24398, 33, "Então a mulher, sabendo o que lhe tinha acontecido, aproximou-se, prostrou-se aos seus pés e, tremendo de medo, contou-lhe toda a verdade.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24399, 34, "Então ele lhe disse: ""Filha, a sua fé a curou! Vá em paz e fique livre do seu sofrimento"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24400, 35, "Enquanto Jesus ainda estava falando, chegaram algumas pessoas da casa de Jairo, o dirigente da sinagoga. ""Sua filha morreu"", disseram eles. ""Não precisa mais incomodar o mestre! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24401, 36, "Não fazendo caso do que eles disseram, Jesus disse ao dirigente da sinagoga: ""Não tenha medo; tão-somente creia"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24402, 37, "E não deixou ninguém segui-lo, senão Pedro, Tiago e João, irmão de Tiago.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24403, 38, "Quando chegaram à casa do dirigente da sinagoga, Jesus viu um alvoroço, com gente chorando e se lamentando em alta voz.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24404, 39, "Então entrou e lhes disse: ""Por que todo este alvoroço e lamento? A criança não está morta, mas dorme"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24405, 40, "Mas todos começaram a rir de Jesus. Ele, porém, ordenou que eles saíssem, tomou consigo o pai e a mãe da criança e os discípulos que estavam com ele, e entrou onde se encontrava a criança.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24406, 41, "Tomou-a pela mão e lhe disse: ""Talita cumi! "", que significa: ""Menina, eu lhe ordeno, levante-se! "".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24407, 42, "Imediatamente a menina, que tinha doze anos de idade, levantou-se e começou a andar. Isso os deixou atônitos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24408, 43, "Ele deu ordens expressas para que não dissessem nada a ninguém e mandou que dessem a ela alguma coisa para comer.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24409, 1, "Jesus saiu dali e foi para a sua cidade, acompanhado dos seus discípulos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24410, 2, "Quando chegou o sábado, começou a ensinar na sinagoga, e muitos dos que o ouviam ficavam admirados. ""De onde lhe vêm estas coisas? "", perguntavam eles. ""Que sabedoria é esta que lhe foi dada? E estes milagres que ele faz?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24411, 3, "Não é este o carpinteiro, filho de Maria e irmão de Tiago, José, Judas e Simão? Não estão aqui conosco as suas irmãs? "" E ficavam escandalizados por causa dele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24412, 4, "Jesus lhes disse: ""Só em sua própria terra, entre seus parentes e em sua própria casa, é que um profeta não tem honra"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24413, 5, "E não pôde fazer ali nenhum milagre, exceto impor as mãos sobre alguns doentes e curá-los.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24414, 6, "E ficou admirado com a incredulidade deles. Então Jesus passou a percorrer os povoados, ensinando.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24415, 7, "Chamando os Doze para junto de si, enviou-os de dois em dois e deu-lhes autoridade sobre os espíritos imundos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24416, 8, "Estas foram as suas instruções: ""Não levem nada pelo caminho, a não ser um bordão. Não levem pão, nem saco de viagem, nem dinheiro em seus cintos;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24417, 9, "calcem sandálias, mas não levem túnica extra;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24418, 10, "sempre que entrarem numa casa, fiquem ali até partirem;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24419, 11, "e, se algum povoado não os receber nem os ouvir, sacudam a poeira dos seus pés quando saírem de lá, como testemunho contra eles"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24420, 12, "Eles saíram e pregaram ao povo que se arrependesse.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24421, 13, "Expulsavam muitos demônios, ungiam muitos doentes com óleo e os curavam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24422, 14, "O rei Herodes ouviu falar dessas coisas, pois o nome de Jesus havia se tornado bem conhecido. Algumas pessoas estavam dizendo: ""João Batista ressuscitou dos mortos! Por isso estão operando nele poderes miraculosos"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24423, 15, "Outros diziam: ""Ele é Elias"". E ainda outros afirmavam: ""Ele é um profeta, como um dos antigos profetas"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24424, 16, "Mas quando Herodes ouviu essas coisas, disse: ""João, o homem a quem decapitei, ressuscitou dos mortos! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24425, 17, "Pois o próprio Herodes tinha dado ordens para que prendessem João, o amarrassem e o colocassem na prisão, por causa de Herodias, mulher de Filipe, seu irmão, com a qual se casara.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24426, 18, "Porquanto João dizia a Herodes: ""Não te é permitido viver com a mulher do teu irmão"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24427, 19, "Assim, Herodias o odiava e queria matá-lo. Mas não podia fazê-lo,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24428, 20, "porque Herodes temia a João e o protegia, sabendo que ele era um homem justo e santo; e quando o ouvia, ficava perplexo. Mesmo assim gostava de ouvi-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24429, 21, "Finalmente chegou uma ocasião oportuna. No seu aniversário, Herodes ofereceu um banquete aos seus líderes mais importantes, aos comandantes militares e às principais personalidades da Galiléia.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24430, 22, "Quando a filha de Herodias entrou e dançou, agradou a Herodes e aos convidados. O rei disse à jovem: ""Peça-me qualquer coisa que você quiser, e eu lhe darei"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24431, 23, "E prometeu-lhe sob juramento: ""Seja o que for que me pedir, eu lhe darei, até a metade do meu reino"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24432, 24, "Ela saiu e disse à sua mãe: ""Que pedirei? "" ""A cabeça de João Batista"", respondeu ela.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24433, 25, "Imediatamente a jovem apressou-se em apresentar-se ao rei com o pedido: ""Desejo que me dês agora mesmo a cabeça de João Batista num prato"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24434, 26, "O rei ficou muito aflito, mas por causa do seu juramento e dos convidados, não quis negar o pedido à jovem.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24435, 27, "Assim enviou imediatamente um carrasco com ordens para trazer a cabeça de João. O homem foi, decapitou João na prisão", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24436, 28, "e trouxe sua cabeça num prato. Ele a entregou à jovem, e esta a deu à sua mãe.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24437, 29, "Tendo ouvido isso, os discípulos de João vieram, levaram o seu corpo e o colocaram num túmulo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24438, 30, "Os apóstolos reuniram-se a Jesus e lhe relataram tudo o que tinham feito e ensinado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24439, 31, "Havia muita gente indo e vindo, a ponto de eles não terem tempo para comer. Jesus lhes disse: ""Venham comigo para um lugar deserto e descansem um pouco"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24440, 32, "Assim, eles se afastaram num barco para um lugar deserto.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24441, 33, "Mas muitos dos que os viram retirar-se, tendo-os reconhecido, correram a pé de todas as cidades e chegaram lá antes deles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24442, 34, "Quando Jesus saiu do barco e viu uma grande multidão, teve compaixão deles, porque eram como ovelhas sem pastor. Então começou a ensinar-lhes muitas coisas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24443, 35, "Já era tarde e, por isso, os seus discípulos aproximaram-se dele e disseram: ""Este é um lugar deserto, e já é tarde.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24444, 36, "Manda embora o povo para que possa ir aos campos e povoados vizinhos comprar algo para comer"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24445, 37, "Ele, porém, respondeu: ""Dêem-lhes vocês algo para comer"". Eles lhe disseram: ""Isto exigiria duzentos denários! Devemos gastar tanto dinheiro em pão e dar-lhes de comer? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24446, 38, "Perguntou ele: ""Quantos pães vocês têm? Verifiquem"". Quando ficaram sabendo, disseram: ""Cinco pães e dois peixes"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24447, 39, "Então Jesus ordenou que fizessem todo o povo assentar-se em grupos na grama verde.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24448, 40, "Assim, eles se assentaram em grupos de cem e de cinqüenta.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24449, 41, "Tomando os cinco pães e os dois peixes e, olhando para o céu, deu graças e partiu os pães. Em seguida, entregou-os aos seus discípulos para que os servissem ao povo. E também dividiu os dois peixes entre todos eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24450, 42, "Todos comeram e ficaram satisfeitos,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24451, 43, "e os discípulos recolheram doze cestos cheios de pedaços de pão e de peixe.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24452, 44, "Os que comeram foram cinco mil homens.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24453, 45, "Logo em seguida, Jesus insistiu com os discípulos para que entrassem no barco e fossem adiante dele para Betsaida, enquanto ele despedia a multidão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24454, 46, "Tendo-a despedido, subiu a um monte para orar.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24455, 47, "Ao anoitecer, o barco estava no meio do mar, e Jesus se achava sozinho em terra.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24456, 48, "Ele viu os discípulos remando com dificuldade, porque o vento soprava contra eles. Alta madrugada, Jesus dirigiu-se a eles, andando sobre o mar; e estava já a ponto de passar por eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24457, 49, "Quando o viram andando sobre o mar, pensaram que fosse um fantasma. Então gritaram,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24458, 50, "pois todos o tinham visto e ficam aterrorizados. Mas Jesus imediatamente lhes disse: ""Coragem! Sou eu! Não tenham medo! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24459, 51, "Então subiu no barco para junto deles, e o vento se acalmou; e eles ficaram atônitos,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24460, 52, "pois não tinham entendido o milagre dos pães. Seus corações estavam endurecidos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24461, 53, "Depois de atravessarem o mar, chegaram a Genesaré e ali amarraram o barco.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24462, 54, "Logo que desembarcaram, o povo reconheceu Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24463, 55, "Eles percorriam toda aquela região e levavam os doentes em macas, para onde ouviam que ele estava.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24464, 56, "E aonde quer que ele fosse, povoados, cidades ou campos, levavam os doentes para as praças. Suplicavam-lhe que pudessem pelo menos tocar na borda do seu manto; e todos os que nele tocavam eram curados.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24465, 1, "Os fariseus e alguns dos mestres da lei, vindos de Jerusalém, reuniram-se a Jesus e", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24466, 2, "viram alguns dos seus discípulos comerem com as mãos ""impuras"", isto é, por lavar.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24467, 3, "( Os fariseus e todos os judeus não comem sem lavar as mãos cerimonialmente, apegando-se, assim, à tradição dos líderes religiosos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24468, 4, "Quando chegam da rua, não comem sem antes se lavarem. E observam muitas outras tradições, tais como o lavar de copos, jarros e vasilhas de metal. )", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24469, 5, "Então os fariseus e os mestres da lei perguntaram a Jesus: ""Por que os seus discípulos não vivem de acordo com a tradição dos líderes religiosos, em vez de comerem o alimento com as mãos ‘impuras’? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24470, 6, "Ele respondeu: ""Bem profetizou Isaías acerca de vocês, hipócritas; como está escrito: ‘Este povo me honra com os lábios, mas o seu coração está longe de mim.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24471, 7, "Em vão me adoram; seus ensinamentos não passam de regras ensinadas por homens’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24472, 8, "Vocês negligenciam os mandamentos de Deus e se apegam às tradições dos homens"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24473, 9, "E disse-lhes: ""Vocês estão sempre encontrando uma boa maneira para pôr de lado os mandamentos de Deus, a fim de obedecer às suas tradições!", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24474, 10, "Pois Moisés disse: ‘Honra teu pai e tua mãe’, e ‘quem amaldiçoar seu pai ou sua mãe terá que ser executado’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24475, 11, "Mas vocês afirmam que se alguém disser a seu pai ou a sua mãe: ‘Qualquer ajuda que vocês poderiam receber de mim é Corbã’, isto é, uma oferta dedicada a Deus,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24476, 12, "vocês o desobrigam de qualquer dever para com seu pai ou sua mãe.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24477, 13, "Assim vocês anulam a palavra de Deus, por meio da tradição que vocês mesmos transmitiram. E fazem muitas coisas como essa"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24478, 14, "Jesus chamou novamente a multidão para junto de si e disse: ""Ouçam-me todos e entendam isto:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24479, 15, "não há nada fora do homem que, nele entrando, possa torná-lo ‘impuro’. Pelo contrário, o que sai do homem é que o torna ‘impuro’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24480, 16, "Se alguém tem ouvidos para ouvir, ouça! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24481, 17, "Depois de deixar a multidão e entrar em casa, os discípulos lhe pediram explicação da parábola.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24482, 18, """Será que vocês também não conseguem entender? "", perguntou-lhes Jesus. ""Não percebem que nada que entre no homem pode torná-lo ‘impuro’?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24483, 19, "Porque não entra em seu coração, mas em seu estômago, sendo depois eliminado"". Ao dizer isto, Jesus declarou ""puros"" todos os alimentos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24484, 20, "E continuou: ""O que sai do homem é que o torna ‘impuro’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24485, 21, "Pois do interior do coração dos homens vêm os maus pensamentos, as imoralidades sexuais, os roubos, os homicídios, os adultérios,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24486, 22, "as cobiças, as maldades, o engano, a devassidão, a inveja, a calúnia, a arrogância e a insensatez.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24487, 23, "Todos esses males vêm de dentro e tornam o homem ‘impuro’ "".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24488, 24, "Jesus saiu daquele lugar e foi para os arredores de Tiro e de Sidom. Entrou numa casa e não queria que ninguém o soubesse; contudo, não conseguiu manter em segredo a sua presença.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24489, 25, "De fato, logo que ouviu falar dele, certa mulher, cuja filha estava com um espírito imundo, veio e lançou-se aos seus pés.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24490, 26, "A mulher era grega, siro-fenícia de origem, e rogava a Jesus que expulsasse de sua filha o demônio.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24491, 27, "Ele lhe disse: ""Deixe que primeiro os filhos comam até se fartar; pois não é correto tirar o pão dos filhos e lançá-lo aos cachorrinhos"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24492, 28, "Ela respondeu: ""Sim, Senhor, mas até os cachorrinhos, debaixo da mesa, comem das migalhas das crianças"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24493, 29, "Então ele lhe disse: ""Por causa desta resposta, você pode ir; o demônio já saiu da sua filha"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24494, 30, "Ela foi para casa e encontrou sua filha deitada na cama, e o demônio já a tinha deixado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24495, 31, "A seguir Jesus saiu dos arredores de Tiro e atravessou Sidom, até o mar da Galiléia e a região de Decápolis.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24496, 32, "Ali algumas pessoas lhe trouxeram um homem que era surdo e mal podia falar, suplicando que lhe impusesse as mãos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24497, 33, "Depois de levá-lo à parte, longe da multidão, Jesus colocou os dedos nos ouvidos dele. Em seguida, cuspiu e tocou na língua do homem.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24498, 34, "Então voltou os olhos para os céus e, com um profundo suspiro, disse-lhe: ""Efatá! "", que significa: Abra-se.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24499, 35, "Com isso, os ouvidos do homem se abriram, sua língua ficou livre e ele começou a falar corretamente.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24500, 36, "Jesus ordenou-lhes que não o contassem a ninguém. Contudo, quanto mais ele os proibia, mais eles falavam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24501, 37, "O povo ficava simplesmente maravilhado e dizia: ""Ele faz tudo muito bem. Faz até o surdo ouvir e o mudo falar"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24502, 1, "Naqueles dias, outra vez reuniu-se uma grande multidão. Visto que não tinham nada para comer, Jesus chamou os seus discípulos e disse-lhes:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24503, 2, """Tenho compaixão desta multidão; já faz três dias que eles estão comigo e nada têm para comer.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24504, 3, "Se eu os mandar para casa com fome, vão desfalecer no caminho, porque alguns deles vieram de longe"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24505, 4, "Os seus discípulos responderam: ""Onde, neste lugar deserto, poderia alguém conseguir pão suficiente para alimentá-los? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24506, 5, """Quantos pães vocês têm? "", perguntou Jesus. ""Sete"", responderam eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24507, 6, "Ele ordenou à multidão que se assentasse no chão. Depois de tomar os sete pães e dar graças, partiu-os e os entregou aos seus discípulos, para que os servissem à multidão; e eles o fizeram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24508, 7, "Tinham também alguns peixes pequenos; ele deu graças igualmente por eles e disse aos discípulos que os distribuíssem.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24509, 8, "O povo comeu até se fartar. E ajuntaram sete cestos cheios de pedaços que sobraram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24510, 9, "Cerca de quatro mil homens estavam presentes. E, tendo-os despedido,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24511, 10, "entrou no barco com seus discípulos e foi para a região de Dalmanuta.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24512, 11, "Os fariseus vieram e começaram a interrogar Jesus. Para pô-lo à prova, pediram-lhe um sinal do céu.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24513, 12, "Ele suspirou profundamente e disse: ""Por que esta geração pede um sinal miraculoso? Eu lhes afirmo que nenhum sinal lhe será dado"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24514, 13, "Então se afastou deles, voltou para o barco e atravessou para o outro lado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24515, 14, "Os discípulos haviam se esquecido de levar pão, a não ser um pão que tinham consigo no barco.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24516, 15, "Advertiu-os Jesus: ""Estejam atentos e tenham cuidado com o fermento dos fariseus e com o fermento de Herodes"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24517, 16, "E eles discutiam entre si, dizendo: ""É porque não temos pão"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24518, 17, "Percebendo a discussão, Jesus lhes perguntou: ""Por que vocês estão discutindo sobre não terem pão? Ainda não compreendem nem percebem? Seus corações estão endurecidos?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24519, 18, "Vocês têm olhos, mas não vêem? Têm ouvidos, mas não ouvem? Não se lembram?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24520, 19, "Quando eu parti os cinco pães para os cinco mil, quantos cestos cheios de pedaços vocês recolheram? "" ""Doze"", responderam eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24521, 20, """E quando eu parti os sete pães para os quatro mil, quantos cestos cheios de pedaços vocês recolheram? "" ""Sete"", responderam eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24522, 21, "Ele lhes disse: ""Vocês ainda não entendem? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24523, 22, "Eles foram para Betsaida, e algumas pessoas trouxeram um cego a Jesus, suplicando-lhe que tocasse nele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24524, 23, "Ele tomou o cego pela mão e o levou para fora do povoado. Depois de cuspir nos olhos do homem e impor-lhe as mãos, Jesus perguntou: ""Você está vendo alguma coisa? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24525, 24, "Ele levantou os olhos e disse: ""Vejo pessoas; elas parecem árvores andando"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24526, 25, "Mais uma vez, Jesus colocou as mãos sobre os olhos do homem. Então seus olhos foram abertos, e sua vista lhe foi restaurada, e ele via tudo claramente.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24527, 26, "Jesus mandou-o para casa, dizendo: ""Não entre no povoado! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24528, 27, "Jesus e os seus discípulos dirigiram-se para os povoados nas proximidades de Cesaréia de Filipe. No caminho, ele lhes perguntou: ""Quem o povo diz que eu sou? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24529, 28, "Eles responderam: ""Alguns dizem que és João Batista; outros, Elias; e, ainda outros, um dos profetas"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24530, 29, """E vocês? "", perguntou ele. ""Quem vocês dizem que eu sou? "" Pedro respondeu: ""Tu és o Cristo"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24531, 30, "Jesus os advertiu que não falassem a ninguém a seu respeito.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24532, 31, "Então ele começou a ensinar-lhes que era necessário que o Filho do homem sofresse muitas coisas e fosse rejeitado pelos líderes religiosos, pelos chefes dos sacerdotes e pelos mestres da lei, fosse morto e três dias depois ressuscitasse.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24533, 32, "Ele falou claramente a esse respeito. Então Pedro, chamando-o à parte, começou a repreendê-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24534, 33, "Jesus, porém, voltou-se, olhou para os seus discípulos e repreendeu Pedro, dizendo: ""Para trás de mim, Satanás! Você não pensa nas coisas de Deus, mas nas dos homens"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24535, 34, "Então ele chamou a multidão e os discípulos e disse: ""Se alguém quiser acompanhar-me, negue-se a si mesmo, tome a sua cruz e siga-me.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24536, 35, "Pois quem quiser salvar a sua vida, a perderá, mas quem perder a vida por minha causa e pelo evangelho, a salvará.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24537, 36, "Pois, que adianta ao homem ganhar o mundo inteiro e perder a sua alma?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24538, 37, "Ou, o que o homem poderia dar em troca de sua alma?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24539, 38, "Se alguém se envergonhar de mim e das minhas palavras nesta geração adúltera e pecadora, o Filho do homem se envergonhará dele quando vier na glória de seu Pai com os santos anjos"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24540, 1, "E lhes disse: ""Garanto-lhes que alguns dos que aqui estão de modo nenhum experimentarão a morte, antes de verem o Reino de Deus vindo com poder"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24541, 2, "Seis dias depois, Jesus tomou consigo Pedro, Tiago e João e os levou a um alto monte, onde ficaram a sós. Ali ele foi transfigurado diante deles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24542, 3, "Suas roupas se tornaram brancas, de um branco resplandecente, como nenhum lavandeiro no mundo seria capaz de branqueá-las.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24543, 4, "E apareceram diante deles Elias e Moisés, os quais conversavam com Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24544, 5, "Então Pedro disse a Jesus: ""Mestre, é bom estarmos aqui. Façamos três tendas: uma para ti, uma para Moisés e uma para Elias"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24545, 6, "Ele não sabia o que dizer, pois estavam apavorados.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24546, 7, "A seguir apareceu uma nuvem e os envolveu, e dela saiu uma voz, que disse: ""Este é o meu Filho amado. Ouçam-no! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24547, 8, "Repentinamente, quando olharam ao redor, não viram mais ninguém, a não ser Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24548, 9, "Enquanto desciam do monte, Jesus lhes ordenou que não contassem a ninguém o que tinham visto, até que o Filho do homem tivesse ressuscitado dos mortos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24549, 10, "Eles guardaram o assunto apenas entre si, discutindo o que significaria ""ressuscitar dos mortos"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24550, 11, "E lhe perguntaram: ""Por que os mestres da lei dizem que é necessário que Elias venha primeiro? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24551, 12, "Jesus respondeu: ""De fato, Elias vem primeiro e restaura todas as coisas. Então, por que está escrito que é necessário que o Filho do homem sofra muito e seja rejeitado com desprezo?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24552, 13, "Mas eu lhes digo: Elias já veio, e fizeram com ele tudo o que quiseram, como está escrito a seu respeito"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24553, 14, "Quando chegaram onde estavam os outros discípulos, viram uma grande multidão ao redor deles e os mestres da lei discutindo com eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24554, 15, "Logo que todo o povo viu Jesus, ficou muito surpreso e correu para saudá-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24555, 16, "Perguntou Jesus: ""O que vocês estão discutindo? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24556, 17, "Um homem, no meio da multidão, respondeu: ""Mestre, eu te trouxe o meu filho, que está com um espírito que o impede de falar.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24557, 18, "Onde quer que o apanhe, joga-o no chão. Ele espuma pela boca, range os dentes e fica rígido. Pedi aos teus discípulos que expulsassem o espírito, mas eles não conseguiram"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24558, 19, "Respondeu Jesus: ""Ó geração incrédula, até quando estarei com vocês? Até quando terei que suportá-los? Tragam-me o menino"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24559, 20, "Então, eles o trouxeram. Quando o espírito viu Jesus, imediatamente causou uma convulsão no menino. Este caiu no chão e começou a rolar, espumando pela boca.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24560, 21, "Jesus perguntou ao pai do menino: ""Há quanto tempo ele está assim? "" ""Desde a infância"", respondeu ele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24561, 22, """Muitas vezes o tem lançado no fogo e na água para matá-lo. Mas, se podes fazer alguma coisa, tem compaixão de nós e ajuda-nos. """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24562, 23, """Se podes? "", disse Jesus. ""Tudo é possível àquele que crê. """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24563, 24, "Imediatamente o pai do menino exclamou: ""Creio, ajuda-me a vencer a minha incredulidade! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24564, 25, "Quando Jesus viu que uma multidão estava se ajuntando, repreendeu o espírito imundo, dizendo: ""Espírito mudo e surdo, eu ordeno que o deixe e nunca mais entre nele"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24565, 26, "O espírito gritou, agitou-o violentamente e saiu. O menino ficou como morto, a ponto de muitos dizerem: ""Ele morreu"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24566, 27, "Mas Jesus tomou-o pela mão e o levantou, e ele ficou em pé.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24567, 28, "Depois de Jesus ter entrado em casa, seus discípulos lhe perguntaram em particular: ""Por que não conseguimos expulsá-lo? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24568, 29, "Ele respondeu: ""Essa espécie só sai pela oração e pelo jejum"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24569, 30, "Eles saíram daquele lugar e atravessaram a Galiléia. Jesus não queria que ninguém soubesse onde eles estavam,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24570, 31, "porque estava ensinando os seus discípulos. E lhes dizia: ""O Filho do homem está para ser entregue nas mãos dos homens. Eles o matarão, e três dias depois ele ressuscitará"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24571, 32, "Mas eles não entendiam o que ele queria dizer e tinham receio de perguntar-lhe.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24572, 33, "E chegaram a Cafarnaum. Quando ele estava em casa, perguntou-lhes: ""O que vocês estavam discutindo no caminho? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24573, 34, "Mas eles guardaram silêncio, porque no caminho haviam discutido sobre quem era o maior.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24574, 35, "Assentando-se, Jesus chamou os Doze e disse: ""Se alguém quiser ser o primeiro, será o último, e servo de todos"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24575, 36, "E, tomando uma criança, colocou-a no meio deles. Pegando-a nos braços, disse-lhes:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24576, 37, """Quem recebe uma destas crianças em meu nome, está me recebendo; e quem me recebe, não está apenas me recebendo, mas também àquele que me enviou"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24577, 38, """Mestre"", disse João, ""vimos um homem expulsando demônios em teu nome e procuramos impedi-lo, porque ele não era um dos nossos. """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24578, 39, """Não o impeçam"", disse Jesus. ""Ninguém que faça um milagre em meu nome, pode falar mal de mim logo em seguida,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24579, 40, "pois quem não é contra nós está a nosso favor.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24580, 41, "Eu lhes digo a verdade: Quem lhes der um copo de água em meu nome, por vocês pertencerem a Cristo, de modo nenhum perderá a sua recompensa. """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24581, 42, """Se alguém fizer tropeçar um destes pequeninos que crêem em mim, seria melhor que fosse lançado no mar com uma grande pedra amarrada no pescoço.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24582, 43, "Se a sua mão o fizer tropeçar, corte-a. É melhor entrar na vida mutilado do que, tendo as duas mãos, ir para o inferno, onde o fogo nunca se apaga,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24583, 44, "onde o seu verme não morre, e o fogo não se apaga.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24584, 45, "E se o seu pé o fizer tropeçar, corte-o. É melhor entrar na vida aleijado do que, tendo os dois pés, ser lançado no inferno.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24585, 46, "onde o seu verme não morre, e o fogo não se apaga.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24586, 47, "E se o seu olho o fizer tropeçar, arranque-o. É melhor entrar no Reino de Deus com um só olho do que, tendo os dois olhos, ser lançado no inferno,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24587, 48, "onde ‘o seu verme não morre, e o fogo não se apaga’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24588, 49, "Cada um será salgado com fogo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24589, 50, """O sal é bom, mas se deixar de ser salgado, como restaurar o seu sabor? Tenham sal em vocês mesmos e vivam em paz uns com os outros"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24590, 1, "Então Jesus saiu dali e foi para a região da Judéia e para o outro lado do Jordão. Novamente uma multidão veio a ele e, segundo o seu costume, ele a ensinava.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24591, 2, "Alguns fariseus aproximaram-se dele para pô-lo à prova, perguntando: ""É permitido ao homem divorciar-se de sua mulher? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24592, 3, """O que Moisés lhes ordenou? "", perguntou ele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24593, 4, "Eles disseram: ""Moisés permitiu que o homem desse uma certidão de divórcio e a mandasse embora"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24594, 5, "Respondeu Jesus: ""Moisés escreveu essa lei por causa da dureza de coração de vocês.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24595, 6, "Mas no princípio da criação Deus ‘os fez homem e mulher’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24596, 7, "‘Por esta razão, o homem deixará pai e mãe e se unirá à sua mulher,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24597, 8, "e os dois se tornarão uma só carne’. Assim, eles já não são dois, mas sim uma só carne.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24598, 9, "Portanto, o que Deus uniu, ninguém o separe"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24599, 10, "Quando estava em casa novamente, os discípulos interrogaram Jesus sobre o mesmo assunto.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24600, 11, "Ele respondeu: ""Todo aquele que se divorciar de sua mulher e se casar com outra mulher, estará cometendo adultério contra ela.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24601, 12, "E se ela se divorciar de seu marido e se casar com outro homem, estará cometendo adultério"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24602, 13, "Alguns traziam crianças a Jesus para que ele tocasse nelas, mas os discípulos os repreendiam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24603, 14, "Quando Jesus viu isso, ficou indignado e lhes disse: ""Deixem vir a mim as crianças, não as impeçam; pois o Reino de Deus pertence aos que são semelhantes a elas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24604, 15, "Digo-lhes a verdade: Quem não receber o Reino de Deus como uma criança, nunca entrará nele"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24605, 16, "Em seguida, tomou as crianças nos braços, impôs-lhes as mãos e as abençoou.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24606, 17, "Quando Jesus ia saindo, um homem correu em sua direção, pôs-se de joelhos diante dele e lhe perguntou: ""Bom mestre, que farei para herdar a vida eterna? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24607, 18, "Respondeu-lhe Jesus: ""Por que você me chama bom? Ninguém é bom, a não ser um, que é Deus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24608, 19, "Você conhece os mandamentos: ‘não matarás, não adulterarás, não furtarás, não darás falso testemunho, não enganarás ninguém, honra teu pai e tua mãe’"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24609, 20, "E ele declarou: ""Mestre, a tudo isso tenho obedecido desde a minha adolescência"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24610, 21, "Jesus olhou para ele e o amou. ""Falta-lhe uma coisa"", disse ele. ""Vá, venda tudo o que você possui e dê o dinheiro aos pobres, e você terá um tesouro no céu. Depois, venha e siga-me"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24611, 22, "Diante disso ele ficou abatido e afastou-se triste, porque tinha muitas riquezas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24612, 23, "Jesus olhou ao redor e disse aos seus discípulos: ""Como é difícil aos ricos entrar no Reino de Deus! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24613, 24, "Os discípulos ficaram admirados com essas palavras. Mas Jesus repetiu: ""Filhos, como é difícil entrar no Reino de Deus!", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24614, 25, "É mais fácil passar um camelo pelo fundo de uma agulha do que um rico entrar no Reino de Deus"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24615, 26, "Os discípulos ficaram perplexos, e perguntavam uns aos outros: ""Neste caso, quem pode ser salvo? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24616, 27, "Jesus olhou para eles e respondeu: ""Para o homem é impossível, mas para Deus não; todas as coisas são possíveis para Deus"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24617, 28, "Então Pedro começou a dizer-lhe: ""Nós deixamos tudo para seguir-te"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24618, 29, "Respondeu Jesus: ""Digo-lhes a verdade: Ninguém que tenha deixado casa, irmãos, irmãs, mãe, pai, filhos, ou campos, por causa de mim e do evangelho,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24619, 30, "deixará de receber cem vezes mais já no tempo presente casas, irmãos, irmãs, mães, filhos e campos, e com eles perseguição; e, na era futura, a vida eterna.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24620, 31, "Contudo, muitos primeiros serão últimos, e os últimos serão primeiros"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24621, 32, "Eles estavam subindo para Jerusalém, e Jesus ia à frente. Os discípulos estavam admirados, enquanto os que o seguiam estavam com medo. Novamente ele chamou à parte os Doze e lhes disse o que haveria de lhe acontecer:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24622, 33, """Estamos subindo para Jerusalém e o Filho do homem será entregue aos chefes dos sacerdotes e aos mestres da lei. Eles o condenarão à morte e o entregarão aos gentios,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24623, 34, "que zombarão dele, cuspirão nele, o açoitarão e o matarão. Três dias depois ele ressuscitará"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24624, 35, "Nisso Tiago e João, filhos de Zebedeu, aproximaram-se dele e disseram: ""Mestre, queremos que nos faças o que vamos te pedir"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24625, 36, """O que vocês querem que eu lhes faça? "", perguntou ele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24626, 37, "Eles responderam: ""Permite que, na tua glória, nos assentemos um à tua direita e o outro à tua esquerda"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24627, 38, "Disse-lhes Jesus: ""Vocês não sabem o que estão pedindo. Podem vocês beber o cálice que eu estou bebendo ou ser batizados com o batismo com que estou sendo batizado? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24628, 39, """Podemos"", responderam eles. Jesus lhes disse: ""Vocês beberão o cálice que estou bebendo e serão batizados com o batismo com que estou sendo batizado;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24629, 40, "mas o assentar-se à minha direita ou à minha esquerda não cabe a mim conceder. Esses lugares pertencem àqueles para quem foram preparados"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24630, 41, "Quando os outros dez ouviram essas coisas, ficaram indignados com Tiago e João.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24631, 42, "Jesus os chamou e disse: ""Vocês sabem que aqueles que são considerados governantes das nações as dominam, e as pessoas importantes exercem poder sobre elas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24632, 43, "Não será assim entre vocês. Pelo contrário, quem quiser tornar-se importante entre vocês deverá ser servo;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24633, 44, "e quem quiser ser o primeiro deverá ser escravo de todos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24634, 45, "Pois nem mesmo o Filho do homem veio para ser servido, mas para servir e dar a sua vida em resgate por muitos"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24635, 46, "Então chegaram a Jericó. Quando Jesus e seus discípulos, juntamente com uma grande multidão, estavam saindo da cidade, o filho de Timeu, Bartimeu, que era cego, estava sentado à beira do caminho pedindo esmolas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24636, 47, "Quando ouviu que era Jesus de Nazaré, começou a gritar: ""Jesus, Filho de Davi, tem misericórdia de mim! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24637, 48, "Muitos o repreendiam para que ficasse quieto, mas ele gritava ainda mais: ""Filho de Davi, tem misericórdia de mim! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24638, 49, "Jesus parou e disse: ""Chamem-no"". E chamaram o cego: ""Ânimo! Levante-se! Ele o está chamando"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24639, 50, "Lançando sua capa para o lado, de um salto, pôs-se de pé e dirigiu-se a Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24640, 51, """O que você quer que eu lhe faça? "", perguntou-lhe Jesus. O cego respondeu: ""Mestre, eu quero ver! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24641, 52, """Vá"", disse Jesus, ""a sua fé o curou"". Imediatamente ele recuperou a visão e seguia a Jesus pelo caminho.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24642, 1, "Quando se aproximaram de Jerusalém e chegaram a Betfagé e Betânia, perto do monte das Oliveiras, Jesus enviou dois de seus discípulos,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24643, 2, "dizendo-lhes: ""Vão ao povoado que está adiante de vocês; logo que entrarem, encontrarão um jumentinho amarrado, no qual ninguém jamais montou. Desamarrem-no e tragam-no aqui.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24644, 3, "Se alguém lhes perguntar: ‘Por que vocês estão fazendo isso? ’ digam-lhe: ‘O Senhor precisa dele e logo o devolverá’ "".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24645, 4, "Eles foram e encontraram um jumentinho, na rua, amarrado a um portão. Enquanto o desamarravam,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24646, 5, "alguns dos que ali estavam lhes perguntaram: ""O que vocês estão fazendo, desamarrando esse jumentinho? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24647, 6, "Os discípulos responderam como Jesus lhes tinha dito, e eles os deixaram ir.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24648, 7, "Trouxeram o jumentinho a Jesus, puseram sobre ele os seus mantos; e Jesus montou.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24649, 8, "Muitos estenderam seus mantos pelo caminho, outros espalharam ramos que haviam cortado nos campos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24650, 9, "Os que iam adiante dele e os que o seguiam gritavam: ""Hosana! "" ""Bendito é o que vem em nome do Senhor! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24651, 10, """Bendito é o Reino vindouro de nosso pai Davi! "" ""Hosana nas alturas! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24652, 11, "Jesus entrou em Jerusalém e dirigiu-se ao templo. Observou tudo à sua volta e, como já era tarde, foi para Betânia com os Doze.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24653, 12, "No dia seguinte, quando estavam saindo de Betânia, Jesus teve fome.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24654, 13, "Vendo à distância uma figueira com folhas, foi ver se encontraria nela algum fruto. Aproximando-se dela, nada encontrou, a não ser folhas, porque não era tempo de figos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24655, 14, "Então lhe disse: ""Ninguém mais coma de seu fruto"". E os seus discípulos ouviram-no dizer isso.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24656, 15, "Chegando a Jerusalém, Jesus entrou no templo e ali começou a expulsar os que estavam comprando e vendendo. Derrubou as mesas dos cambistas e as cadeiras dos que vendiam pombas", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24657, 16, "e não permitia que ninguém carregasse mercadorias pelo templo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24658, 17, "E os ensinava, dizendo: ""Não está escrito: ‘A minha casa será chamada casa de oração para todos os povos’? Mas vocês fizeram dela um covil de ladrões"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24659, 18, "Os chefes dos sacerdotes e os mestres da lei ouviram essas palavras e começaram a procurar uma forma de matá-lo, pois o temiam, visto que toda a multidão estava maravilhada com o seu ensino.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24660, 19, "Ao cair da tarde, eles saíram da cidade.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24661, 20, "De manhã, ao passarem, viram a figueira seca desde as raízes.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24662, 21, "Lembrando-se Pedro, disse a Jesus: ""Mestre! Vê! A figueira que amaldiçoaste secou! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24663, 22, "Respondeu Jesus: ""Tenham fé em Deus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24664, 23, "Eu lhes asseguro que se alguém disser a este monte: ‘Levante-se e atire-se no mar’, e não duvidar em seu coração, mas crer que acontecerá o que diz, assim lhe será feito.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24665, 24, "Portanto, eu lhes digo: tudo o que vocês pedirem em oração, creiam que já o receberam, e assim lhes sucederá.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24666, 25, "E quando estiverem orando, se tiverem alguma coisa contra alguém, perdoem-no, para que também o Pai celestial lhes perdoe os seus pecados"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24667, 26, "Mas se vocês não perdoarem, também o seu Pai que está no céu não perdoará os seus pecados.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24668, 27, "Chegaram novamente a Jerusalém e, quando Jesus estava passando pelo templo, aproximaram-se dele os chefes dos sacerdotes, os mestres da lei e os líderes religiosos e perguntaram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24669, 28, """Com que autoridade estás fazendo estas coisas? Quem te deu autoridade para fazê-las? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24670, 29, "Respondeu Jesus: ""Eu lhes farei uma pergunta. Respondam-me, e eu lhes direi com que autoridade estou fazendo estas coisas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24671, 30, "O batismo de João era do céu ou dos homens? Digam-me! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24672, 31, "Eles discutiam entre si, dizendo: ""Se dissermos: ‘do céu’, ele perguntará: ‘Então por que vocês não creram nele? ’", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24673, 32, "Mas se dissermos: ‘dos homens’... "" Eles temiam o povo, pois todos realmente consideravam João um profeta.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24674, 33, "Eles responderam a Jesus: ""Não sabemos"". Disse então Jesus: ""Tampouco lhes direi com que autoridade estou fazendo estas coisas"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24675, 1, "Então Jesus começou a lhes falar por parábolas: ""Certo homem plantou uma vinha, colocou uma cerca ao redor dela, cavou um tanque para prensar as uvas e construiu uma torre. Depois arrendou a vinha a alguns lavradores e foi fazer uma viagem.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24676, 2, "Na época da colheita, enviou um servo aos lavradores, para receber deles parte do fruto da vinha.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24677, 3, "Mas eles o agarraram e espancaram, e o mandaram embora de mãos vazias.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24678, 4, "Então enviou-lhes outro servo; e lhe bateram na cabeça e o humilharam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24679, 5, "E enviou ainda outro, o qual mataram. Enviou muitos outros; em alguns bateram, a outros mataram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24680, 6, """Faltava-lhe ainda um para enviar: seu filho amado. Por fim o enviou, dizendo: ‘A meu filho respeitarão’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24681, 7, """Mas os lavradores disseram uns aos outros: ‘Este é o herdeiro. Venham, vamos matá-lo, e a herança será nossa’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24682, 8, "Assim eles o agarraram, e o mataram, e o lançaram para fora da vinha.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24683, 9, """O que fará então o dono da vinha? Virá e matará aqueles lavradores e dará a vinha a outros.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24684, 10, "Vocês nunca leram esta passagem das Escrituras? ‘A pedra que os construtores rejeitaram tornou-se a pedra angular;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24685, 11, "isso vem do Senhor, e é algo maravilhoso para nós’ "".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24686, 12, "Então começaram a procurar um meio de prendê-lo, pois perceberam que era contra eles que ele havia contado aquela parábola. Mas tinham medo da multidão; por isso o deixaram e foram embora.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24687, 13, "Mais tarde enviaram a Jesus alguns dos fariseus e herodianos para o apanharem em alguma coisa que ele dissesse.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24688, 14, "Estes se aproximaram dele e disseram: ""Mestre, sabemos que és íntegro e que não te deixas influenciar por ninguém, porque não te prendes à aparência dos homens, mas ensinas o caminho de Deus conforme a verdade. É certo pagar imposto a César ou não?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24689, 15, "Devemos pagar ou não? "" Mas Jesus, percebendo a hipocrisia deles, perguntou: ""Por que vocês estão me pondo à prova? Tragam-me um denário para que eu o veja"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24690, 16, "Eles lhe trouxeram a moeda, e ele lhes perguntou: ""De quem é esta imagem e esta inscrição? "" ""De César"", responderam eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24691, 17, "Então Jesus lhes disse: ""Dêem a César o que é de César e a Deus o que é de Deus"". E ficaram admirados com ele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24692, 18, "Depois os saduceus, que dizem que não há ressurreição, aproximaram-se dele com a seguinte questão:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24693, 19, """Mestre, Moisés nos deixou escrito que, se o irmão de um homem morrer e deixar mulher sem filhos, este deverá casar-se com a viúva e ter filhos para seu irmão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24694, 20, "Havia sete irmãos. O primeiro casou-se e morreu sem deixar filhos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24695, 21, "O segundo casou-se com a viúva, mas também morreu sem deixar filhos. O mesmo aconteceu com o terceiro.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24696, 22, "Nenhum dos sete deixou filhos. Finalmente, morreu também a mulher.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24697, 23, "Na ressurreição, de quem ela será esposa, visto que os sete foram casados com ela? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24698, 24, "Jesus respondeu: ""Vocês estão enganados! Pois não conhecem as Escrituras nem o poder de Deus!", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24699, 25, "Quando os mortos ressuscitam, não se casam nem são dados em casamento, mas são como os anjos nos céus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24700, 26, "Quanto à ressurreição dos mortos, vocês não leram no livro de Moisés, no relato da sarça, como Deus lhe disse: ‘Eu sou o Deus de Abraão, o Deus de Isaque e o Deus de Jacó’?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24701, 27, "Ele não é Deus de mortos, mas de vivos. Vocês estão muito enganados! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24702, 28, "Um dos mestres da lei aproximou-se e os ouviu discutindo. Notando que Jesus lhes dera uma boa resposta, perguntou-lhe: ""De todos os mandamentos, qual é o mais importante? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24703, 29, "Respondeu Jesus: ""O mais importante é este: ‘Ouve, ó Israel, o Senhor, o nosso Deus, o Senhor é o único Senhor.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24704, 30, "Ame o Senhor, o seu Deus de todo o seu coração, de toda a sua alma, de todo o seu entendimento e de todas as suas forças’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24705, 31, "O segundo é este: ‘Ame o seu próximo como a si mesmo’. Não existe mandamento maior do que estes"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24706, 32, """Muito bem, mestre"", disse o homem. ""Estás certo ao dizeres que Deus é único e que não existe outro além dele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24707, 33, "Amá-lo de todo o coração, de todo o entendimento e de todas as forças, e amar ao próximo como a si mesmo é mais importante do que todos os sacrifícios e ofertas"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24708, 34, "Vendo que ele tinha respondido sabiamente, Jesus lhe disse: ""Você não está longe do Reino de Deus"". Daí por diante ninguém mais ousava lhe fazer perguntas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24709, 35, "Ensinando no templo, Jesus perguntou: ""Como os mestres da lei dizem que o Cristo é filho de Davi?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24710, 36, "O próprio Davi, falando pelo Espírito Santo, disse: ‘O Senhor disse ao meu Senhor: Senta-te à minha direita até que eu ponha os teus inimigos debaixo de teus pés’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24711, 37, "O próprio Davi o chama ‘Senhor’. Como pode, então, ser ele seu filho? "" E a grande multidão o ouvia com prazer.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24712, 38, "Ao ensinar, Jesus dizia: ""Cuidado com os mestres da lei. Eles fazem questão de andar com roupas especiais, de receber saudações nas praças", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24713, 39, "e de ocupar os lugares mais importantes nas sinagogas e os lugares de honra nos banquetes.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24714, 40, "Eles devoram as casas das viúvas, e, para disfarçar, fazem longas orações. Esses receberão condenação mais severa! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24715, 41, "Jesus sentou-se em frente do lugar onde eram colocadas as contribuições, e observava a multidão colocando o dinheiro nas caixas de ofertas. Muitos ricos lançavam ali grandes quantias.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24716, 42, "Então, uma viúva pobre chegou-se e colocou duas pequeninas moedas de cobre, de muito pouco valor.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24717, 43, "Chamando a si os seus discípulos, Jesus declarou: ""Afirmo-lhes que esta viúva pobre colocou na caixa de ofertas mais do que todos os outros.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24718, 44, "Todos deram do que lhes sobrava; mas ela, da sua pobreza, deu tudo o que possuía para viver"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24719, 1, "Quando ele estava saindo do templo, um de seus discípulos lhe disse: ""Olha, Mestre! Que pedras enormes! Que construções magníficas! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24720, 2, """Você está vendo todas estas grandes construções? "", perguntou Jesus. ""Aqui não ficará pedra sobre pedra; serão todas derrubadas"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24721, 3, "Tendo Jesus se assentado no monte das Oliveiras, de frente para o templo, Pedro, Tiago, João e André lhe perguntaram em particular:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24722, 4, """Dize-nos, quando acontecerão essas coisas? E qual será o sinal de que tudo isso está prestes a cumprir-se? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24723, 5, "Jesus lhes disse: ""Cuidado, que ninguém os engane.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24724, 6, "Muitos virão em meu nome, dizendo: ‘Sou eu! ’ e enganarão a muitos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24725, 7, "Quando ouvirem falar de guerras e rumores de guerras, não tenham medo. É necessário que tais coisas aconteçam, mas ainda não é o fim.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24726, 8, "Nação se levantará contra nação, e reino contra reino. Haverá terremotos em vários lugares e também fomes. Essas coisas são o início das dores.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24727, 9, """Fiquem atentos, pois vocês serão entregues aos tribunais e serão açoitados nas sinagogas. Por minha causa vocês serão levados à presença de governadores e reis, como testemunho a eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24728, 10, "E é necessário que antes o evangelho seja pregado a todas as nações.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24729, 11, "Sempre que forem presos e levados a julgamento, não fiquem preocupados com o que vão dizer. Digam tão-somente o que lhes for dado naquela hora, pois não serão vocês que estarão falando, mas o Espírito Santo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24730, 12, """O irmão trairá seu próprio irmão, entregando-o à morte, e o mesmo fará o pai a seu filho. Filhos se rebelarão contra seus pais e os matarão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24731, 13, "Todos odiarão vocês por minha causa; mas aquele que perseverar até o fim será salvo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24732, 14, """Quando vocês virem ‘o sacrilégio terrível’ no lugar onde não deve estar — quem lê, entenda — então, os que estiverem na Judéia fujam para os montes.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24733, 15, "Quem estiver no telhado de sua casa não desça nem entre em casa para tirar dela coisa alguma.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24734, 16, "Quem estiver no campo não volte para pegar seu manto.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24735, 17, "Como serão terríveis aqueles dias para as grávidas e para as que estiverem amamentando!", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24736, 18, "Orem para que essas coisas não aconteçam no inverno.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24737, 19, "Porque aqueles serão dias de tribulação como nunca houve desde que Deus criou o mundo até agora, nem jamais haverá.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24738, 20, "Se o Senhor não tivesse abreviado tais dias, ninguém sobreviveria. Mas, por causa dos eleitos por ele escolhidos, ele os abreviou.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24739, 21, "Se, então, alguém lhes disser: ‘Vejam, aqui está o Cristo! ’ ou: ‘Vejam, ali está ele! ’, não acreditem.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24740, 22, "Pois aparecerão falsos cristos e falsos profetas que realizarão sinais e maravilhas para, se possível, enganar os eleitos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24741, 23, "Por isso, fiquem atentos: avisei-os de tudo antecipadamente.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24742, 24, """Mas naqueles dias, após aquela tribulação, ‘o sol escurecerá e a lua não dará a sua luz;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24743, 25, "as estrelas cairão do céu e os poderes celestes serão abalados’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24744, 26, """Então se verá o Filho do homem vindo nas nuvens com grande poder e glória.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24745, 27, "E ele enviará os seus anjos e reunirá os seus eleitos dos quatro ventos, dos confins da terra até os confins do céu.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24746, 28, """Aprendam a lição da figueira: quando seus ramos se renovam e suas folhas começam a brotar, vocês sabem que o verão está próximo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24747, 29, "Assim também, quando virem estas coisas acontecendo, saibam que ele está próximo, às portas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24748, 30, "Eu lhes asseguro que não passará esta geração até que todas essas coisas aconteçam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24749, 31, "O céu e a terra passarão, mas as minhas palavras jamais passarão"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24750, 32, """Quanto ao dia e à hora ninguém sabe, nem os anjos no céu, nem o Filho, senão somente o Pai.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24751, 33, "Fiquem atentos! Vigiem! Vocês não sabem quando virá esse tempo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24752, 34, "É como um homem que sai de viagem. Ele deixa sua casa, encarrega de tarefas cada um dos seus servos e ordena ao porteiro que vigie.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24753, 35, "Portanto, vigiem, porque vocês não sabem quando o dono da casa voltará: se à tarde, à meia-noite, ao cantar do galo ou ao amanhecer.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24754, 36, "Se ele vier de repente, que não os encontre dormindo!", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24755, 37, "O que lhes digo, digo a todos: Vigiem! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24756, 1, "Faltavam apenas dois dias para a Páscoa e para a festa dos pães sem fermento. Os chefes dos sacerdotes e os mestres da lei estavam procurando um meio de flagrar Jesus em algum erro e matá-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24757, 2, "Mas diziam: ""Não durante a festa, para que não haja tumulto entre o povo"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24758, 3, "Estando Jesus em Betânia, reclinado à mesa na casa de um homem conhecido como Simão, o leproso, aproximou-se dele certa mulher com um frasco de alabastro contendo um perfume muito caro, feito de nardo puro. Ela quebrou o frasco e derramou o perfume sobre a cabeça de Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24759, 4, "Alguns dos presentes começaram a dizer uns aos outros, indignados: ""Por que este desperdício de perfume?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24760, 5, "Ele poderia ser vendido por trezentos denários, e o dinheiro dado aos pobres"". E a repreendiam severamente.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24761, 6, """Deixem-na em paz"", disse Jesus. ""Por que a estão perturbando? Ela praticou uma boa ação para comigo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24762, 7, "Pois os pobres vocês sempre terão consigo, e poderão ajudá-los sempre que o desejarem. Mas a mim vocês nem sempre terão.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24763, 8, "Ela fez o que pôde. Derramou o perfume em meu corpo antecipadamente, preparando-o para o sepultamento.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24764, 9, "Eu lhes asseguro que onde quer que o evangelho for anunciado, em todo o mundo, também o que ela fez será contado em sua memória"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24765, 10, "Então Judas Iscariotes, um dos Doze, dirigiu-se aos chefes dos sacerdotes a fim de lhes entregar Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24766, 11, "A proposta muito os alegrou, e lhe prometeram dinheiro. Assim, ele procurava uma oportunidade para entregá-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24767, 12, "No primeiro dia da festa dos pães sem fermento, quando se costumava sacrificar o cordeiro pascal, os discípulos de Jesus lhe perguntaram: ""Aonde queres que vamos e te preparemos a refeição da Páscoa? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24768, 13, "Então ele enviou dois de seus discípulos, dizendo-lhes: ""Entrem na cidade, e um homem carregando um pote de água virá ao encontro de vocês. Sigam-no", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24769, 14, "e digam ao dono da casa em que ele entrar: ‘O Mestre pergunta: Onde é o meu salão de hóspedes, no qual poderei comer a Páscoa com meus discípulos? ’", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24770, 15, "Ele lhes mostrará uma ampla sala no andar superior, mobiliada e pronta. Façam ali os preparativos para nós"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24771, 16, "Os discípulos se retiraram, entraram na cidade e encontraram tudo como Jesus lhes tinha dito. E prepararam a Páscoa.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24772, 17, "Ao anoitecer, Jesus chegou com os Doze.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24773, 18, "Quando estavam comendo, reclinados à mesa, Jesus disse: ""Digo-lhes que certamente um de vocês me trairá, alguém que está comendo comigo"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24774, 19, "Eles ficaram tristes e, um por um, lhe disseram: ""Com certeza não sou eu! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24775, 20, "Afirmou Jesus: ""É um dos Doze, alguém que come comigo do mesmo prato.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24776, 21, "O Filho do homem vai, como está escrito a seu respeito. Mas ai daquele que trai o Filho do homem! Melhor lhe seria não haver nascido"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24777, 22, "Enquanto comiam, Jesus tomou o pão, deu graças, partiu-o, e o deu aos discípulos, dizendo: ""Tomem; isto é o meu corpo"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24778, 23, "Em seguida tomou o cálice, deu graças, ofereceu-o aos discípulos, e todos beberam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24779, 24, "E lhes disse: ""Isto é o meu sangue da aliança, que é derramado em favor de muitos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24780, 25, "Eu lhes afirmo que não beberei outra vez do fruto da videira, até aquele dia em que beberei o vinho novo no Reino de Deus"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24781, 26, "Depois de terem cantado um hino, saíram para o monte das Oliveiras.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24782, 27, "Disse-lhes Jesus: ""Vocês todos me abandonarão. Pois está escrito: ‘Ferirei o pastor, e as ovelhas serão dispersas’.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24783, 28, "Mas, depois de ressuscitar, irei adiante de vocês para a Galiléia"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24784, 29, "Pedro declarou: ""Ainda que todos te abandonem, eu não te abandonarei! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24785, 30, "Respondeu Jesus: ""Asseguro-lhe que ainda hoje, esta noite, antes que duas vezes cante o galo, três vezes você me negará"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24786, 31, "Mas Pedro insistia ainda mais: ""Mesmo que seja preciso que eu morra contigo, nunca te negarei"". E todos os outros disseram o mesmo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24787, 32, "Então foram para um lugar chamado Getsêmani, e Jesus disse aos seus discípulos: ""Sentem-se aqui enquanto vou orar"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24788, 33, "Levou consigo Pedro, Tiago e João, e começou a ficar aflito e angustiado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24789, 34, "E lhes disse: ""A minha alma está profundamente triste, numa tristeza mortal. Fiquem aqui e vigiem"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24790, 35, "Indo um pouco mais adiante, prostrou-se e orava para que, se possível, fosse afastada dele aquela hora.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24791, 36, "E dizia: ""Aba, Pai, tudo te é possível. Afasta de mim este cálice; contudo, não seja o que eu quero, mas sim o que tu queres"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24792, 37, "Então, voltou aos seus discípulos e os encontrou dormindo. ""Simão"", disse ele a Pedro, ""você está dormindo? Não pôde vigiar nem por uma hora?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24793, 38, "Vigiem e orem para que não caiam em tentação. O espírito está pronto, mas a carne é fraca. """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24794, 39, "Mais uma vez ele se afastou e orou, repetindo as mesmas palavras.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24795, 40, "Quando voltou, de novo os encontrou dormindo, porque seus olhos estavam pesados. Eles não sabiam o que lhe dizer.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24796, 41, "Voltando pela terceira vez, ele lhes disse: ""Vocês ainda dormem e descansam? Basta! Chegou a hora! Eis que o Filho do homem está sendo entregue nas mãos dos pecadores.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24797, 42, "Levantem-se e vamos! Aí vem aquele que me trai! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24798, 43, "Enquanto ele ainda falava, apareceu Judas, um dos Doze. Com ele estava uma multidão armada de espadas e varas, enviada pelos chefes dos sacerdotes, mestres da lei e líderes religiosos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24799, 44, "O traidor havia combinado um sinal com eles: ""Aquele a quem eu saudar com um beijo, é ele: prendam-no e levem-no em segurança"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24800, 45, "Dirigindo-se imediatamente a Jesus, Judas disse: ""Mestre! "", e o beijou.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24801, 46, "Os homens agarraram Jesus e o prenderam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24802, 47, "Então, um dos que estavam por perto puxou a espada e feriu o servo do sumo sacerdote, decepando-lhe a orelha.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24803, 48, "Disse Jesus: ""Estou eu chefiando alguma rebelião, para que vocês venham me prender com espadas e varas?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24804, 49, "Todos os dias eu estava com vocês, ensinando no templo, e vocês não me prenderam. Mas as Escrituras precisam ser cumpridas"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24805, 50, "Então todos o abandonaram e fugiram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24806, 51, "Um jovem, vestindo apenas um lençol de linho, estava seguindo a Jesus. Quando tentaram prendê-lo,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24807, 52, "ele fugiu nu, deixando o lençol para trás.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24808, 53, "Levaram Jesus ao sumo sacerdote; e então se reuniram todos os chefes dos sacerdotes, os líderes religiosos e os mestres da lei.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24809, 54, "Pedro o seguiu de longe até o pátio do sumo sacerdote. Sentando-se ali com os guardas, esquentava-se junto ao fogo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24810, 55, "Os chefes dos sacerdotes e todo o Sinédrio estavam procurando depoimentos contra Jesus, para que pudessem condená-lo à morte, mas não encontravam nenhum.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24811, 56, "Muitos testemunharam falsamente contra ele, mas as declarações deles não eram coerentes.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24812, 57, "Então se levantaram alguns e declararam falsamente contra ele:", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24813, 58, """Nós o ouvimos dizer: ‘Destruirei este templo feito por mãos humanas e em três dias construirei outro, não feito por mãos de homens’ "".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24814, 59, "Mas, nem mesmo assim, o depoimento deles era coerente.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24815, 60, "Depois o sumo sacerdote levantou-se diante deles e perguntou a Jesus: ""Você não vai responder à acusação que estes lhe fazem? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24816, 61, "Mas Jesus permaneceu em silêncio e nada respondeu. Outra vez o sumo sacerdote lhe perguntou: ""Você é o Cristo, o Filho do Deus Bendito? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24817, 62, """Sou"", disse Jesus. ""E vereis o Filho do homem assentado à direita do Poderoso vindo com as nuvens do céu"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24818, 63, "O sumo sacerdote, rasgando as próprias vestes, perguntou: ""Por que precisamos de mais testemunhas?", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24819, 64, "Vocês ouviram a blasfêmia. Que acham? "" Todos o julgaram digno de morte.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24820, 65, "Então alguns começaram a cuspir nele; vendaram-lhe os olhos e, dando-lhe murros, diziam: ""Profetize! "" E os guardas o levaram, dando-lhe tapas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24821, 66, "Estando Pedro em baixo, no pátio, uma das criadas do sumo sacerdote passou por ali.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24822, 67, "Vendo Pedro a aquecer-se, olhou bem para ele e disse: ""Você também estava com Jesus, o Nazareno"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24823, 68, "Contudo ele o negou, dizendo: ""Não o conheço, nem sei do que você está falando"". E saiu para o alpendre.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24824, 69, "Quando a criada o viu lá, disse novamente aos que estavam por perto: ""Esse aí é um deles"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24825, 70, "De novo ele negou. Pouco tempo depois, os que estavam sentados ali perto disseram a Pedro: ""Certamente você é um deles. Você é galileu! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24826, 71, "Ele começou a se amaldiçoar e a jurar: ""Não conheço o homem de quem vocês estão falando! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24827, 72, "E logo o galo cantou pela segunda vez. Então Pedro se lembrou da palavra que Jesus lhe tinha dito: ""Antes que duas vezes cante o galo, você me negará três vezes"". E se pôs a chorar.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24828, 1, "De manhã bem cedo, os chefes dos sacerdotes com os líderes religiosos, os mestres da lei e todo o Sinédrio chegaram a uma decisão. Amarrando Jesus, levaram-no e o entregaram a Pilatos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24829, 2, """Você é o rei dos judeus? "", perguntou Pilatos. ""Tu o dizes"", respondeu Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24830, 3, "Os chefes dos sacerdotes o acusavam de muitas coisas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24831, 4, "Então Pilatos lhe perguntou novamente: ""Você não vai responder? Veja de quantas coisas o estão acusando"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24832, 5, "Mas Jesus não respondeu nada, e Pilatos ficou impressionado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24833, 6, "Por ocasião da festa, era costume soltar um prisioneiro que o povo pedisse.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24834, 7, "Um homem chamado Barrabás estava na prisão com os rebeldes que haviam cometido assassinato durante uma rebelião.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24835, 8, "A multidão chegou e pediu a Pilatos que lhe fizesse o que costumava fazer.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24836, 9, """Vocês querem que eu lhes solte o rei dos judeus? "", perguntou Pilatos,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24837, 10, "sabendo que fora por inveja que os chefes dos sacerdotes lhe haviam entregado Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24838, 11, "Mas os chefes dos sacerdotes incitaram a multidão a pedir que Pilatos, ao contrário, soltasse Barrabás.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24839, 12, """Então, que farei com aquele a quem vocês chamam rei dos judeus? "", perguntou-lhes Pilatos.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24840, 13, """Crucifica-o"", gritaram eles.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24841, 14, """Por quê? Que crime ele cometeu? "", perguntou Pilatos. Mas eles gritavam ainda mais: ""Crucifica-o! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24842, 15, "Desejando agradar a multidão, Pilatos soltou-lhes Barrabás, mandou açoitar Jesus e o entregou para ser crucificado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24843, 16, "Os soldados levaram Jesus para dentro do palácio, isto é, ao Pretório e reuniram toda a tropa.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24844, 17, "Vestiram-no com um manto de púrpura, depois fizeram uma coroa de espinhos e a colocaram nele.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24845, 18, "E começaram a saudá-lo: ""Salve, rei dos judeus! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24846, 19, "Batiam-lhe na cabeça com uma vara e cuspiam nele. Ajoelhavam-se e lhe prestavam adoração.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24847, 20, "Depois de terem zombado dele, tiraram-lhe o manto de púrpura e vestiram-lhe suas próprias roupas. Então o levaram para fora, a fim de crucificá-lo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24848, 21, "Certo homem de Cirene, chamado Simão, pai de Alexandre e de Rufo, passava por ali, chegando do campo. Eles o forçaram a carregar a cruz.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24849, 22, "Levaram Jesus ao lugar chamado Gólgota, que quer dizer Lugar da Caveira.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24850, 23, "Então lhe deram vinho misturado com mirra, mas ele não o bebeu.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24851, 24, "E o crucificaram. Dividindo as roupas dele, tiraram sortes para saber com o que cada um iria ficar.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24852, 25, "Eram nove horas da manhã quando o crucificaram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24853, 26, "E assim estava escrito na acusação contra ele: O REI DOS JUDEUS.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24854, 27, "Com ele crucificaram dois ladrões, um à sua direita e outro à sua esquerda,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24855, 28, "e cumpriu-se a Escritura que diz: ""Ele foi contado entre os transgressores"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24856, 29, "Os que passavam lançavam-lhe insultos, balançando a cabeça e dizendo: ""Ora, você que destrói o templo e o reedifica em três dias,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24857, 30, "desça da cruz e salve-se a si mesmo! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24858, 31, "Da mesma forma, os chefes dos sacerdotes e os mestres da lei zombavam dele entre si, dizendo: ""Salvou os outros, mas não é capaz de salvar a si mesmo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24859, 32, "O Cristo, o Rei de Israel... Desça da cruz, para que o vejamos e creiamos! "" Os que foram crucificados com ele também o insultavam.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24860, 33, "E houve trevas sobre toda a terra, do meio dia às três horas da tarde.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24861, 34, "Por volta das três horas da tarde, Jesus bradou em alta voz: ""Eloí, Eloí, lamá sabactâni? "" que significa: ""Meu Deus! Meu Deus! Por que me abandonaste? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24862, 35, "Quando alguns dos que estavam presentes ouviram isso, disseram: ""Ouçam! Ele está chamando Elias"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24863, 36, "Um deles correu, embebeu uma esponja em vinagre, colocou-a na ponta de uma vara e deu-a a Jesus para beber. E disse: ""Deixem-no. Vejamos se Elias vem tirá-lo daí"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24864, 37, "Mas Jesus, com um alto brado, expirou.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24865, 38, "E o véu do santuário rasgou-se em duas partes, de alto a baixo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24866, 39, "Quando o centurião que estava em frente de Jesus ouviu o seu brado e viu como ele morreu, disse: ""Realmente este homem era o Filho de Deus! """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24867, 40, "Algumas mulheres estavam observando de longe. Entre elas estavam Maria Madalena, Salomé e Maria, mãe de Tiago, o mais jovem, e de José.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24868, 41, "Na Galiléia elas tinham seguido e servido a Jesus. Muitas outras mulheres que tinham subido com ele para Jerusalém também estavam ali.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24869, 42, "Era o Dia da Preparação, isto é, a véspera do sábado. Ao cair da tarde,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24870, 43, "José de Arimatéia, membro de destaque do Sinédrio, que também esperava o Reino de Deus, dirigiu-se corajosamente a Pilatos e pediu o corpo de Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24871, 44, "Pilatos ficou surpreso ao ouvir que ele já tinha morrido. Chamando o centurião, perguntou-lhe se Jesus já tinha morrido.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24872, 45, "Sendo informado pelo centurião, entregou o corpo a José.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24873, 46, "Então José comprou um lençol de linho, baixou o corpo da cruz, envolveu-o no lençol e o colocou num sepulcro cavado na rocha. Depois, fez rolar uma pedra sobre a entrada do sepulcro.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24874, 47, "Maria Madalena e Maria, mãe de José, viram onde ele fora colocado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24875, 1, "Quando terminou o sábado, Maria Madalena, Salomé e Maria, mãe de Tiago, compraram especiarias aromáticas para ungir o corpo de Jesus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24876, 2, "No primeiro dia da semana, bem cedo, ao nascer do sol, elas se dirigiram ao sepulcro,", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24877, 3, "perguntando umas às outras: ""Quem removerá para nós a pedra da entrada do sepulcro? """, 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24878, 4, "Mas, quando foram verificar, viram que a pedra, que era muito grande, havia sido removida.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24879, 5, "Entrando no sepulcro, viram um jovem vestido de roupas brancas assentado à direita, e ficaram amedrontadas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24880, 6, """Não tenham medo"", disse ele. ""Vocês estão procurando Jesus, o Nazareno, que foi crucificado. Ele ressuscitou! Não está aqui. Vejam o lugar onde o haviam posto.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24881, 7, "Vão e digam aos discípulos dele e a Pedro: ‘Ele está indo adiante de vocês para a Galiléia. Lá vocês o verão, como ele lhes disse’ "".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24882, 8, "Tremendo e assustadas, as mulheres saíram e fugiram do sepulcro. E não disseram nada a ninguém, porque estavam amedrontadas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24883, 9, "Quando Jesus ressuscitou, na madrugada do primeiro dia da semana, apareceu primeiramente a Maria Madalena, de quem havia expulsado sete demônios.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24884, 10, "Ela foi e contou aos que com ele tinham estado; eles estavam lamentando e chorando.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24885, 11, "Quando ouviram que Jesus estava vivo e fora visto por ela, não creram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24886, 12, "Depois Jesus apareceu noutra forma a dois deles, estando eles a caminho do campo.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24887, 13, "Eles voltaram e relataram isso aos outros; mas também nestes eles não creram.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24888, 14, "Mais tarde Jesus apareceu aos Onze enquanto eles comiam; censurou-lhes a incredulidade e a dureza de coração, porque não acreditaram nos que o tinham visto depois de ressurreto.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24889, 15, "E disse-lhes: ""Vão pelo mundo todo e preguem o evangelho a todas as pessoas.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24890, 16, "Quem crer e for batizado será salvo, mas quem não crer será condenado.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24891, 17, "Estes sinais acompanharão os que crerem: em meu nome expulsarão demônios; falarão novas línguas;", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24892, 18, "pegarão em serpentes; e, se beberem algum veneno mortal, não lhes fará mal nenhum; imporão as mãos sobre os doentes, e estes ficarão curados"".", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24893, 19, "Depois de lhes ter falado, o Senhor Jesus foi elevado ao céu e assentou-se à direita de Deus.", 40);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24894, 20, "Então, os discípulos saíram e pregaram por toda parte; e o Senhor cooperava com eles, confirmando-lhes a palavra com os sinais que a acompanhavam.", 40);