INSERT INTO verses (id, number, text, chapter_id) VALUES (23146, 1, "Registro da genealogia de Jesus Cristo, filho de Davi, filho de Abraão:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23147, 2, "Abraão gerou Isaque; Isaque gerou Jacó; Jacó gerou Judá e seus irmãos;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23148, 3, "Judá gerou Perez e Zerá, cuja mãe foi Tamar; Perez gerou Esrom; Esrom gerou Arão;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23149, 4, "Arão gerou Aminadabe; Aminadabe gerou Naassom; Naassom gerou Salmom;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23150, 5, "Salmom gerou Boaz, cuja mãe foi Raabe; Boaz gerou Obede, cuja mãe foi Rute; Obede gerou Jessé;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23151, 6, "e Jessé gerou o rei Davi. Davi gerou Salomão, cuja mãe tinha sido mulher de Urias;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23152, 7, "Salomão gerou Roboão; Roboão gerou Abias; Abias gerou Asa;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23153, 8, "Asa gerou Josafá; Josafá gerou Jorão; Jorão gerou Uzias;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23154, 9, "Uzias gerou Jotão; Jotão gerou Acaz; Acaz gerou Ezequias;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23155, 10, "Ezequias gerou Manassés; Manassés gerou Amom; Amom gerou Josias;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23156, 11, "e Josias gerou Jeconias e seus irmãos, no tempo do exílio na Babilônia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23157, 12, "Depois do exílio na Babilônia: Jeconias gerou Salatiel; Salatiel gerou Zorobabel;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23158, 13, "Zorobabel gerou Abiúde; Abiúde gerou Eliaquim; Eliaquim gerou Azor;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23159, 14, "Azor gerou Sadoque; Sadoque gerou Aquim; Aquim gerou Eliúde;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23160, 15, "Eliúde gerou Eleazar; Eleazar gerou Matã; Matã gerou Jacó;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23161, 16, "e Jacó gerou José, marido de Maria, da qual nasceu Jesus, que é chamado Cristo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23162, 17, "Assim, ao todo houve catorze gerações de Abraão a Davi, catorze de Davi até o exílio na Babilônia e catorze do exílio até o Cristo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23163, 18, "Foi assim o nascimento de Jesus Cristo: Maria, sua mãe, estava prometida em casamento a José, mas, antes que se unissem, achou-se grávida pelo Espírito Santo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23164, 19, "Por ser José, seu marido, um homem justo, e não querendo expô-la à desonra pública, pretendia anular o casamento secretamente.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23165, 20, "Mas, depois de ter pensado nisso, apareceu-lhe um anjo do Senhor em sonho e disse: ""José, filho de Davi, não tema receber Maria como sua esposa, pois o que nela foi gerado procede do Espírito Santo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23166, 21, "Ela dará à luz um filho, e você deverá dar-lhe o nome de Jesus, porque ele salvará o seu povo dos seus pecados"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23167, 22, "Tudo isso aconteceu para que se cumprisse o que o Senhor dissera pelo profeta:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23168, 23, """A virgem ficará grávida e dará à luz um filho, e lhe chamarão Emanuel"" que significa ""Deus conosco"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23169, 24, "Ao acordar, José fez o que o anjo do Senhor lhe tinha ordenado e recebeu Maria como sua esposa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23170, 25, "Mas não teve relações com ela enquanto ela não deu à luz um filho. E ele lhe pôs o nome de Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23171, 1, "Depois que Jesus nasceu em Belém da Judéia, nos dias do rei Herodes, magos vindos do Oriente chegaram a Jerusalém", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23172, 2, "e perguntaram: ""Onde está o recém-nascido rei dos judeus? Vimos a sua estrela no Oriente e viemos adorá-lo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23173, 3, "Quando o rei Herodes ouviu isso, ficou perturbado, e com ele toda a Jerusalém.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23174, 4, "Tendo reunido todos os chefes dos sacerdotes do povo e os mestres da lei, perguntou-lhes onde deveria nascer o Cristo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23175, 5, "E eles responderam: ""Em Belém da Judéia; pois assim escreveu o profeta:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23176, 6, "‘Mas tu, Belém, da terra de Judá, de forma alguma és a menor entre as principais cidades de Judá; pois de ti virá o líder que, como pastor, conduzirá Israel, o meu povo’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23177, 7, "Então Herodes chamou os magos secretamente e informou-se com eles a respeito do tempo exato em que a estrela tinha aparecido.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23178, 8, "Enviou-os a Belém e disse: ""Vão informar-se com exatidão sobre o menino. Logo que o encontrarem, avisem-me, para que eu também vá adorá-lo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23179, 9, "Depois de ouvirem o rei, eles seguiram o seu caminho, e a estrela que tinham visto no Oriente foi adiante deles, até que finalmente parou sobre o lugar onde estava o menino.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23180, 10, "Quando tornaram a ver a estrela, encheram-se de júbilo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23181, 11, "Ao entrarem na casa, viram o menino com Maria, sua mãe, e, prostrando-se, o adoraram. Então abriram os seus tesouros e lhe deram presentes: ouro, incenso e mirra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23182, 12, "E, tendo sido advertidos em sonho para não voltarem a Herodes, retornaram a sua terra por outro caminho.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23183, 13, "Depois que partiram, um anjo do Senhor apareceu a José em sonho e disse-lhe: ""Levante-se, tome o menino e sua mãe, e fuja para o Egito. Fique lá até que eu lhe diga, pois Herodes vai procurar o menino para matá-lo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23184, 14, "Então ele se levantou, tomou o menino e sua mãe durante a noite, e partiu para o Egito,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23185, 15, "onde ficou até a morte de Herodes. E assim se cumpriu o que o Senhor tinha dito pelo profeta: ""Do Egito chamei o meu filho"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23186, 16, "Quando Herodes percebeu que havia sido enganado pelos magos, ficou furioso e ordenou que matassem todos os meninos de dois anos para baixo, em Belém e nas proximidades, de acordo com a informação que havia obtido dos magos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23187, 17, "Então se cumpriu o que fora dito pelo profeta Jeremias:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23188, 18, """Ouviu-se uma voz em Ramá, choro e grande lamentação; é Raquel que chora por seus filhos e recusa ser consolada, porque já não existem"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23189, 19, "Depois que Herodes morreu, um anjo do Senhor apareceu em sonho a José, no Egito,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23190, 20, "e disse: ""Levante-se, tome o menino e sua mãe, e vá para a terra de Israel, pois estão mortos os que procuravam tirar a vida do menino"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23191, 21, "Ele se levantou, tomou o menino e sua mãe, e foi para a terra de Israel.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23192, 22, "Mas, ao ouvir que Arquelau estava reinando na Judéia em lugar de seu pai Herodes, teve medo de ir para lá. Tendo sido avisado em sonho, retirou-se para a região da Galiléia", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23193, 23, "e foi viver numa cidade chamada Nazaré. Assim cumpriu-se o que fora dito pelos profetas: Ele será chamado Nazareno.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23194, 1, "Naqueles dias surgiu João Batista, pregando no deserto da Judéia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23195, 2, "Ele dizia: ""Arrependam-se, porque o Reino dos céus está próximo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23196, 3, "Este é aquele que foi anunciado pelo profeta Isaías: ""Voz do que clama no deserto: ‘Preparem o caminho para o Senhor, façam veredas retas para ele’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23197, 4, "As roupas de João eram feitas de pêlos de camelo, e ele usava um cinto de couro na cintura. O seu alimento era gafanhotos e mel silvestre.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23198, 5, "A ele vinha gente de Jerusalém, de toda a Judéia e de toda a região ao redor do Jordão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23199, 6, "Confessando os seus pecados, eram batizados por ele no rio Jordão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23200, 7, "Quando viu que muitos fariseus e saduceus vinham para onde ele estava batizando, disse-lhes: ""Raça de víboras! Quem lhes deu a idéia de fugir da ira que se aproxima?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23201, 8, "Dêem fruto que mostre o arrependimento!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23202, 9, "Não pensem que vocês podem dizer a si mesmos: ‘Abraão é nosso pai’. Pois eu lhes digo que destas pedras Deus pode fazer surgir filhos a Abraão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23203, 10, "O machado já está posto à raiz das árvores, e toda árvore que não der bom fruto será cortada e lançada ao fogo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23204, 11, """Eu os batizo com água para arrependimento. Mas depois de mim vem alguém mais poderoso do que eu, tanto que não sou digno nem de levar as suas sandálias. Ele os batizará com o Espírito Santo e com fogo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23205, 12, "Ele traz a pá em sua mão e limpará sua eira, juntando seu trigo no celeiro, mas queimará a palha com fogo que nunca se apaga"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23206, 13, "Então Jesus veio da Galiléia ao Jordão para ser batizado por João.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23207, 14, "João, porém, tentou impedi-lo, dizendo: ""Eu preciso ser batizado por ti, e tu vens a mim? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23208, 15, "Respondeu Jesus: ""Deixe assim por enquanto; convém que assim façamos, para cumprir toda a justiça"". E João concordou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23209, 16, "Assim que Jesus foi batizado, saiu da água. Naquele momento os céus se abriram, e ele viu o Espírito de Deus descendo como pomba e pousando sobre ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23210, 17, "Então uma voz dos céus disse: ""Este é o meu Filho amado, em quem me agrado"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23211, 1, "Então Jesus foi levado pelo Espírito ao deserto, para ser tentado pelo diabo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23212, 2, "Depois de jejuar quarenta dias e quarenta noites, teve fome.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23213, 3, "O tentador aproximou-se dele e disse: ""Se você é o Filho de Deus, mande que estas pedras se transformem em pães"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23214, 4, "Jesus respondeu: ""Está escrito: ‘Nem só de pão viverá o homem, mas de toda palavra que procede da boca de Deus’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23215, 5, "Então o diabo o levou à cidade santa, colocou-o na parte mais alta do templo e lhe disse:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23216, 6, """Se você é o Filho de Deus, jogue-se daqui para baixo. Pois está escrito: ‘Ele dará ordens a seus anjos a seu respeito, e com as mãos eles o segurarão, para que você não tropece em alguma pedra’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23217, 7, "Jesus lhe respondeu: ""Também está escrito: ‘Não ponha à prova o Senhor, o seu Deus’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23218, 8, "Depois, o diabo o levou a um monte muito alto e mostrou-lhe todos os reinos do mundo e o seu esplendor.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23219, 9, "E lhe disse: ""Tudo isto lhe darei, se você se prostrar e me adorar"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23220, 10, "Jesus lhe disse: ""Retire-se, Satanás! Pois está escrito: ‘Adore o Senhor, o seu Deus e só a ele preste culto’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23221, 11, "Então o diabo o deixou, e anjos vieram e o serviram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23222, 12, "Quando Jesus ouviu que João tinha sido preso, voltou para a Galiléia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23223, 13, "Saindo de Nazaré, foi viver em Cafarnaum, que ficava junto ao mar, na região de Zebulom e Naftali,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23224, 14, "para cumprir o que fora dito pelo profeta Isaías:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23225, 15, """Terra de Zebulom e terra de Naftali, caminho do mar, além do Jordão, Galiléia dos gentios;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23226, 16, "o povo que vivia nas trevas viu uma grande luz; sobre os que viviam na terra da sombra da morte raiou uma luz"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23227, 17, "Daí em diante Jesus começou a pregar: ""Arrependam-se, pois o Reino dos céus está próximo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23228, 18, "Andando à beira do mar da Galiléia, Jesus viu dois irmãos: Simão, chamado Pedro, e seu irmão André. Eles estavam lançando redes ao mar, pois eram pescadores.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23229, 19, "E disse Jesus: ""Sigam-me, e eu os farei pescadores de homens"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23230, 20, "No mesmo instante eles deixaram as suas redes e o seguiram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23231, 21, "Indo adiante, viu outros dois irmãos: Tiago, filho de Zebedeu, e João, seu irmão. Eles estavam num barco com seu pai, Zebedeu, preparando as suas redes. Jesus os chamou,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23232, 22, "e eles, deixando imediatamente o barco e seu pai, o seguiram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23233, 23, "Jesus foi por toda a Galiléia, ensinando nas sinagogas deles, pregando as boas novas do Reino e curando todas as enfermidades e doenças entre o povo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23234, 24, "Notícias sobre ele se espalharam por toda a Síria, e o povo lhe trouxe todos os que estavam padecendo vários males e tormentos: endemoninhados, epiléticos e paralíticos; e ele os curou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23235, 25, "Grandes multidões o seguiam, vindas da Galiléia, Decápolis, Jerusalém, Judéia e da região do outro lado do Jordão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23236, 1, "Vendo as multidões, Jesus subiu ao monte e se assentou. Seus discípulos aproximaram-se dele,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23237, 2, "e ele começou a ensiná-los, dizendo:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23238, 3, """Bem-aventurados os pobres em espírito, pois deles é o Reino dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23239, 4, "Bem-aventurados os que choram, pois serão consolados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23240, 5, "Bem-aventurados os humildes, pois eles receberão a terra por herança.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23241, 6, "Bem-aventurados os que têm fome e sede de justiça, pois serão satisfeitos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23242, 7, "Bem-aventurados os misericordiosos, pois obterão misericórdia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23243, 8, "Bem-aventurados os puros de coração, pois verão a Deus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23244, 9, "Bem-aventurados os pacificadores, pois serão chamados filhos de Deus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23245, 10, "Bem-aventurados os perseguidos por causa da justiça, pois deles é o Reino dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23246, 11, """Bem-aventurados serão vocês quando, por minha causa os insultarem, perseguirem e levantarem todo tipo de calúnia contra vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23247, 12, "Alegrem-se e regozijem-se, porque grande é a recompensa de vocês nos céus, pois da mesma forma perseguiram os profetas que viveram antes de vocês"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23248, 13, """Vocês são o sal da terra. Mas se o sal perder o seu sabor, como restaurá-lo? Não servirá para nada, exceto para ser jogado fora e pisado pelos homens.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23249, 14, """Vocês são a luz do mundo. Não se pode esconder uma cidade construída sobre um monte.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23250, 15, "E, também, ninguém acende uma candeia e a coloca debaixo de uma vasilha. Pelo contrário, coloca-a no lugar apropriado, e assim ilumina a todos os que estão na casa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23251, 16, "Assim brilhe a luz de vocês diante dos homens, para que vejam as suas boas obras e glorifiquem ao Pai de vocês, que está nos céus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23252, 17, """Não pensem que vim abolir a Lei ou os Profetas; não vim abolir, mas cumprir.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23253, 18, "Digo-lhes a verdade: Enquanto existirem céus e terra, de forma alguma desaparecerá da Lei a menor letra ou o menor traço, até que tudo se cumpra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23254, 19, "Todo aquele que desobedecer a um desses mandamentos, ainda que dos menores, e ensinar os outros a fazerem o mesmo, será chamado menor no Reino dos céus; mas todo aquele que praticar e ensinar estes mandamentos será chamado grande no Reino dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23255, 20, "Pois eu lhes digo que se a justiça de vocês não for muito superior à dos fariseus e mestres da lei, de modo nenhum entrarão no Reino dos céus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23256, 21, """Vocês ouviram o que foi dito aos seus antepassados: ‘Não matarás’, e ‘quem matar estará sujeito a julgamento’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23257, 22, "Mas eu lhes digo que qualquer que se irar contra seu irmão estará sujeito a julgamento. Também, qualquer que disser a seu irmão: ‘Racá’, será levado ao tribunal. E qualquer que disser: ‘Louco! ’, corre o risco de ir para o fogo do inferno.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23258, 23, """Portanto, se você estiver apresentando sua oferta diante do altar e ali se lembrar de que seu irmão tem algo contra você,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23259, 24, "deixe sua oferta ali, diante do altar, e vá primeiro reconciliar-se com seu irmão; depois volte e apresente sua oferta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23260, 25, """Entre em acordo depressa com seu adversário que pretende levá-lo ao tribunal. Faça isso enquanto ainda estiver com ele a caminho, pois, caso contrário, ele poderá entregá-lo ao juiz, e o juiz ao guarda, e você poderá ser jogado na prisão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23261, 26, "Eu lhe garanto que você não sairá de lá enquanto não pagar o último centavo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23262, 27, """Vocês ouviram o que foi dito: ‘Não adulterarás’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23263, 28, "Mas eu lhes digo: qualquer que olhar para uma mulher para desejá-la, já cometeu adultério com ela no seu coração.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23264, 29, "Se o seu olho direito o fizer pecar, arranque-o e lance-o fora. É melhor perder uma parte do seu corpo do que ser todo ele lançado no inferno.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23265, 30, "E se a sua mão direita o fizer pecar, corte-a e lance-a fora. É melhor perder uma parte do seu corpo do que ir todo ele para o inferno"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23266, 31, """Foi dito: ‘Aquele que se divorciar de sua mulher deverá dar-lhe certidão de divórcio’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23267, 32, "Mas eu lhes digo que todo aquele que se divorciar de sua mulher, exceto por imoralidade sexual, faz que ela se torne adúltera, e quem se casar com a mulher divorciada estará cometendo adultério"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23268, 33, """Vocês também ouviram o que foi dito aos seus antepassados: ‘Não jure falsamente, mas cumpra os juramentos que você fez diante do Senhor’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23269, 34, "Mas eu lhes digo: Não jurem de forma alguma: nem pelo céu, porque é o trono de Deus;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23270, 35, "nem pela terra, porque é o estrado de seus pés; nem por Jerusalém, porque é a cidade do grande Rei.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23271, 36, "E não jure pela sua cabeça, pois você não pode tornar branco ou preto nem um fio de cabelo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23272, 37, "Seja o seu ‘sim’, ‘sim’, e o seu ‘não’, ‘não’; o que passar disso vem do Maligno"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23273, 38, """Vocês ouviram o que foi dito: ‘Olho por olho e dente por dente’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23274, 39, "Mas eu lhes digo: Não resistam ao perverso. Se alguém o ferir na face direita, ofereça-lhe também a outra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23275, 40, "E se alguém quiser processá-lo e tirar-lhe a túnica, deixe que leve também a capa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23276, 41, "Se alguém o forçar a caminhar com ele uma milha, vá com ele duas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23277, 42, "Dê a quem lhe pede, e não volte as costas àquele que deseja pedir-lhe algo emprestado"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23278, 43, """Vocês ouviram o que foi dito: ‘Ame o seu próximo e odeie o seu inimigo’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23279, 44, "Mas eu lhes digo: Amem os seus inimigos e orem por aqueles que os perseguem,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23280, 45, "para que vocês venham a ser filhos de seu Pai que está nos céus. Porque ele faz raiar o seu sol sobre maus e bons e derrama chuva sobre justos e injustos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23281, 46, "Se vocês amarem aqueles que os amam, que recompensa receberão? Até os publicanos fazem isso!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23282, 47, "E se vocês saudarem apenas os seus irmãos, o que estarão fazendo de mais? Até os pagãos fazem isso!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23283, 48, "Portanto, sejam perfeitos como perfeito é o Pai celestial de vocês"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23284, 1, """Tenham o cuidado de não praticar suas ‘obras de justiça’ diante dos outros para serem vistos por eles. Se fizerem isso, vocês não terão nenhuma recompensa do Pai celestial.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23285, 2, """Portanto, quando você der esmola, não anuncie isso com trombetas, como fazem os hipócritas nas sinagogas e nas ruas, a fim de serem honrados pelos outros. Eu lhes garanto que eles já receberam sua plena recompensa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23286, 3, "Mas quando você der esmola, que a sua mão esquerda não saiba o que está fazendo a direita,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23287, 4, "de forma que você preste a sua ajuda em segredo. E seu Pai, que vê o que é feito em segredo, o recompensará"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23288, 5, """E quando vocês orarem, não sejam como os hipócritas. Eles gostam de ficar orando em pé nas sinagogas e nas esquinas, a fim de serem vistos pelos outros. Eu lhes asseguro que eles já receberam sua plena recompensa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23289, 6, "Mas quando você orar, vá para seu quarto, feche a porta e ore a seu Pai, que está no secreto. Então seu Pai, que vê no secreto, o recompensará.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23290, 7, "E quando orarem, não fiquem sempre repetindo a mesma coisa, como fazem os pagãos. Eles pensam que por muito falarem serão ouvidos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23291, 8, "Não sejam iguais a eles, porque o seu Pai sabe do que vocês precisam, antes mesmo de o pedirem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23292, 9, "Vocês, orem assim: ‘Pai nosso, que estás nos céus! Santificado seja o teu nome.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23293, 10, "Venha o teu Reino; seja feita a tua vontade, assim na terra como no céu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23294, 11, "Dá-nos hoje o nosso pão de cada dia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23295, 12, "Perdoa as nossas dívidas, assim como perdoamos aos nossos devedores.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23296, 13, "E não nos deixes cair em tentação, mas livra-nos do mal, porque teu é o Reino, o poder e a glória para sempre. Amém’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23297, 14, "Pois se perdoarem as ofensas uns dos outros, o Pai celestial também lhes perdoará.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23298, 15, "Mas se não perdoarem uns aos outros, o Pai celestial não lhes perdoará as ofensas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23299, 16, """Quando jejuarem, não mostrem uma aparência triste como os hipócritas, pois eles mudam a aparência do rosto a fim de que os homens vejam que eles estão jejuando. Eu lhes digo verdadeiramente que eles já receberam sua plena recompensa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23300, 17, "Ao jejuar, ponha óleo sobre a cabeça e lave o rosto,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23301, 18, "para que não pareça aos outros que você está jejuando, mas apenas a seu Pai, que vê no secreto. E seu Pai, que vê no secreto, o recompensará"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23302, 19, """Não acumulem para vocês tesouros na terra, onde a traça e a ferrugem destroem, e onde os ladrões arrombam e furtam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23303, 20, "Mas acumulem para vocês tesouros no céu, onde a traça e a ferrugem não destroem, e onde os ladrões não arrombam nem furtam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23304, 21, "Pois onde estiver o seu tesouro, aí também estará o seu coração.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23305, 22, """Os olhos são a candeia do corpo. Se os seus olhos forem bons, todo o seu corpo será cheio de luz.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23306, 23, "Mas se os seus olhos forem maus, todo o seu corpo será cheio de trevas. Portanto, se a luz que está dentro de você são trevas, que tremendas trevas são!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23307, 24, """Ninguém pode servir a dois senhores; pois odiará a um e amará o outro, ou se dedicará a um e desprezará o outro. Vocês não podem servir a Deus e ao Dinheiro"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23308, 25, """Portanto eu lhes digo: não se preocupem com suas próprias vidas, quanto ao que comer ou beber; nem com seus próprios corpos, quanto ao que vestir. Não é a vida mais importante do que a comida, e o corpo mais importante do que a roupa?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23309, 26, "Observem as aves do céu: não semeiam nem colhem nem armazenam em celeiros; contudo, o Pai celestial as alimenta. Não têm vocês muito mais valor do que elas?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23310, 27, "Quem de vocês, por mais que se preocupe, pode acrescentar uma hora que seja à sua vida?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23311, 28, """Por que vocês se preocupam com roupas? Vejam como crescem os lírios do campo. Eles não trabalham nem tecem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23312, 29, "Contudo, eu lhes digo que nem Salomão, em todo o seu esplendor, vestiu-se como um deles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23313, 30, "Se Deus veste assim a erva do campo, que hoje existe e amanhã é lançada ao fogo, não vestirá muito mais a vocês, homens de pequena fé?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23314, 31, "Portanto, não se preocupem, dizendo: ‘Que vamos comer? ’ ou ‘que vamos beber? ’ ou ‘que vamos vestir? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23315, 32, "Pois os pagãos é que correm atrás dessas coisas; mas o Pai celestial sabe que vocês precisam delas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23316, 33, "Busquem, pois, em primeiro lugar o Reino de Deus e a sua justiça, e todas essas coisas lhes serão acrescentadas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23317, 34, "Portanto, não se preocupem com o amanhã, pois o amanhã se preocupará consigo mesmo. Basta a cada dia o seu próprio mal"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23318, 1, """Não julguem, para que vocês não sejam julgados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23319, 2, "Pois da mesma forma que julgarem, vocês serão julgados; e a medida que usarem, também será usada para medir vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23320, 3, """Por que você repara no cisco que está no olho do seu irmão, e não se dá conta da viga que está em seu próprio olho?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23321, 4, "Como você pode dizer ao seu irmão: ‘Deixe-me tirar o cisco do seu olho’, quando há uma viga no seu?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23322, 5, "Hipócrita, tire primeiro a viga do seu olho, e então você verá claramente para tirar o cisco do olho do seu irmão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23323, 6, """Não dêem o que é sagrado aos cães, nem atirem suas pérolas aos porcos; caso contrário, estes as pisarão e, aqueles, voltando-se contra vocês, os despedaçarão"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23324, 7, """Peçam, e lhes será dado; busquem, e encontrarão; batam, e a porta lhes será aberta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23325, 8, "Pois todo o que pede, recebe; o que busca, encontra; e àquele que bate, a porta será aberta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23326, 9, """Qual de vocês, se seu filho pedir pão, lhe dará uma pedra?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23327, 10, "Ou se pedir peixe, lhe dará uma cobra?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23328, 11, "Se vocês, apesar de serem maus, sabem dar boas coisas aos seus filhos, quanto mais o Pai de vocês, que está nos céus, dará coisas boas aos que lhe pedirem!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23329, 12, "Assim, em tudo, façam aos outros o que vocês querem que eles lhes façam; pois esta é a Lei e os Profetas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23330, 13, """Entrem pela porta estreita, pois larga é a porta e amplo o caminho que leva à perdição, e são muitos os que entram por ela.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23331, 14, "Como é estreita a porta, e apertado o caminho que leva à vida! São poucos os que a encontram"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23332, 15, """Cuidado com os falsos profetas. Eles vêm a vocês vestidos de peles de ovelhas, mas por dentro são lobos devoradores.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23333, 16, "Vocês os reconhecerão por seus frutos. Pode alguém colher uvas de um espinheiro ou figos de ervas daninhas?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23334, 17, "Semelhantemente, toda árvore boa dá frutos bons, mas a árvore ruim dá frutos ruins.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23335, 18, "A árvore boa não pode dar frutos ruins, nem a árvore ruim pode dar frutos bons.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23336, 19, "Toda árvore que não produz bons frutos é cortada e lançada ao fogo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23337, 20, "Assim, pelos seus frutos vocês os reconhecerão!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23338, 21, """Nem todo aquele que me diz: ‘Senhor, Senhor’, entrará no Reino dos céus, mas apenas aquele que faz a vontade de meu Pai que está nos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23339, 22, "Muitos me dirão naquele dia: ‘Senhor, Senhor, não profetizamos nós em teu nome? Em teu nome não expulsamos demônios e não realizamos muitos milagres? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23340, 23, "Então eu lhes direi claramente: ‘Nunca os conheci. Afastem-se de mim vocês, que praticam o mal! ’ """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23341, 24, """Portanto, quem ouve estas minhas palavras e as pratica é como um homem prudente que construiu a sua casa sobre a rocha.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23342, 25, "Caiu a chuva, transbordaram os rios, sopraram os ventos e deram contra aquela casa, e ela não caiu, porque tinha seus alicerces na rocha.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23343, 26, "Mas quem ouve estas minhas palavras e não as pratica é como um insensato que construiu a sua casa sobre a areia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23344, 27, "Caiu a chuva, transbordaram os rios, sopraram os ventos e deram contra aquela casa, e ela caiu. E foi grande a sua queda"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23345, 28, "Quando Jesus acabou de dizer essas coisas, as multidões estavam maravilhadas com o seu ensino,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23346, 29, "porque ele as ensinava como quem tem autoridade, e não como os mestres da lei.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23347, 1, "Quando ele desceu do monte, grandes multidões o seguiram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23348, 2, "Um leproso, aproximando-se, adorou-o de joelhos e disse: ""Senhor, se quiseres, podes purificar-me! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23349, 3, "Jesus estendeu a mão, tocou nele e disse: ""Quero. Seja purificado! "" Imediatamente ele foi purificado da lepra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23350, 4, "Em seguida Jesus lhe disse: ""Olhe, não conte isso a ninguém. Mas vá mostrar-se ao sacerdote e apresente a oferta que Moisés ordenou, para que sirva de testemunho"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23351, 5, "Entrando Jesus em Cafarnaum, dirigiu-se a ele um centurião, pedindo-lhe ajuda.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23352, 6, "E disse: ""Senhor, meu servo está em casa, paralítico, em terrível sofrimento"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23353, 7, "Jesus lhe disse: ""Eu irei curá-lo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23354, 8, "Respondeu o centurião: ""Senhor, não mereço receber-te debaixo do meu teto. Mas dize apenas uma palavra, e o meu servo será curado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23355, 9, "Pois eu também sou homem sujeito à autoridade, com soldados sob o meu comando. Digo a um: ‘Vá’, e ele vai; e a outro: ‘Venha’, e ele vem. Digo a meu servo: ‘Faça isto’, e ele faz"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23356, 10, "Ao ouvir isso, Jesus admirou-se e disse aos que o seguiam: ""Digo-lhes a verdade: Não encontrei em Israel ninguém com tamanha fé.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23357, 11, "Eu lhes digo que muitos virão do Oriente e do Ocidente, e se sentarão à mesa com Abraão, Isaque e Jacó no Reino dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23358, 12, "Mas os súditos do Reino serão lançados para fora, nas trevas, onde haverá choro e ranger de dentes"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23359, 13, "Então Jesus disse ao centurião: ""Vá! Como você creu, assim lhe acontecerá! "" Na mesma hora o seu servo foi curado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23360, 14, "Entrando Jesus na casa de Pedro, viu a sogra deste de cama, com febre.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23361, 15, "Tomando-a pela mão, a febre a deixou, e ela se levantou e começou a servi-lo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23362, 16, "Ao anoitecer foram trazidos a ele muitos endemoninhados, e ele expulsou os espíritos com uma palavra e curou todos os doentes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23363, 17, "E assim se cumpriu o que fora dito pelo profeta Isaías: ""Ele tomou sobre si as nossas enfermidades e sobre si levou as nossas doenças"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23364, 18, "Quando Jesus viu a multidão ao seu redor, deu ordens para que atravessassem para o outro lado do mar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23365, 19, "Então, um mestre da lei aproximou-se e disse: ""Mestre, eu te seguirei por onde quer que fores"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23366, 20, "Jesus respondeu: ""As raposas têm suas tocas e as aves do céu têm seus ninhos, mas o Filho do homem não tem onde repousar a cabeça"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23367, 21, "Outro discípulo lhe disse: ""Senhor, deixa-me ir primeiro sepultar meu pai"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23368, 22, "Mas Jesus lhe disse: ""Siga-me, e deixe que os mortos sepultem os seus próprios mortos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23369, 23, "Entrando ele no barco, seus discípulos o seguiram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23370, 24, "De repente, uma violenta tempestade abateu-se sobre o mar, de forma que as ondas inundavam o barco. Jesus, porém, dormia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23371, 25, "Os discípulos foram acordá-lo, clamando: ""Senhor, salva-nos! Vamos morrer! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23372, 26, "Ele perguntou: ""Por que vocês estão com tanto medo, homens de pequena fé? "" Então ele se levantou e repreendeu os ventos e o mar, e fez-se completa bonança.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23373, 27, "Os homens ficaram perplexos e perguntaram: ""Quem é este que até os ventos e o mar lhe obedecem? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23374, 28, "Quando ele chegou ao outro lado, à região dos gadarenos, foram ao seu encontro dois endemoninhados, que vinham dos sepulcros. Eles eram tão violentos que ninguém podia passar por aquele caminho.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23375, 29, "Então eles gritaram: ""Que queres conosco, Filho de Deus? Vieste aqui para nos atormentar antes do devido tempo? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23376, 30, "A certa distância deles estava pastando uma grande manada de porcos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23377, 31, "Os demônios imploravam a Jesus: ""Se nos expulsas, manda-nos entrar naquela manada de porcos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23378, 32, "Ele lhes disse: ""Vão! "" Eles saíram e entraram nos porcos, e toda a manada atirou-se precipício abaixo, em direção ao mar, e morreu afogada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23379, 33, "Os que cuidavam dos porcos fugiram, foram à cidade e contaram tudo, inclusive o que acontecera aos endemoninhados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23380, 34, "Toda a cidade saiu ao encontro de Jesus, e quando o viram, suplicaram-lhe que saísse do território deles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23381, 1, "Entrando Jesus num barco, atravessou o mar e foi para a sua própria cidade.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23382, 2, "Alguns homens trouxeram-lhe um paralítico, deitado numa cama. Vendo a fé que eles tinham, Jesus disse ao paralítico: ""Tenha bom ânimo, filho; os seus pecados estão perdoados"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23383, 3, "Diante disso, alguns mestres da lei disseram a si mesmos: ""Este homem está blasfemando! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23384, 4, "Conhecendo Jesus seus pensamentos, disse-lhes: ""Por que vocês pensam maldosamente em seus corações?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23385, 5, "Que é mais fácil dizer: ‘Os seus pecados estão perdoados’, ou: ‘Levante-se e ande’?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23386, 6, "Mas, para que vocês saibam que o Filho do homem tem na terra autoridade para perdoar pecados"" — disse ao paralítico: ""Levante-se, pegue a sua maca e vá para casa"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23387, 7, "Ele se levantou e foi.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23388, 8, "Vendo isso, a multidão ficou cheia de temor e glorificou a Deus, que dera tal autoridade aos homens.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23389, 9, "Passando por ali, Jesus viu um homem chamado Mateus, sentado na coletoria, e disse-lhe: ""Siga-me"". Mateus levantou-se e o seguiu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23390, 10, "Estando Jesus em casa, foram comer com ele e seus discípulos muitos publicanos e ""pecadores"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23391, 11, "Vendo isso, os fariseus perguntaram aos discípulos dele: ""Por que o mestre de vocês come com publicanos e ‘pecadores’? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23392, 12, "Ouvindo isso, Jesus disse: ""Não são os que têm saúde que precisam de médico, mas sim os doentes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23393, 13, "Vão aprender o que significa isto: ‘Desejo misericórdia, não sacrifícios’. Pois eu não vim chamar justos, mas pecadores"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23394, 14, "Então os discípulos de João vieram perguntar-lhe: ""Por que nós e os fariseus jejuamos, mas os teus discípulos não? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23395, 15, "Jesus respondeu: ""Como podem os convidados do noivo ficar de luto enquanto o noivo está com eles? Virão dias quando o noivo lhes será tirado; então jejuarão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23396, 16, """Ninguém põe remendo de pano novo em roupa velha, pois o remendo forçará a roupa, tornando pior o rasgo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23397, 17, "Nem se põe vinho novo em vasilhas de couro velhas; se o fizer, as vasilhas se rebentarão, o vinho se derramará e as vasilhas se estragarão. Pelo contrário, põe-se vinho novo em vasilhas de couro novas; e ambos se conservam"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23398, 18, "Falava ele ainda quando um dos dirigentes da sinagoga chegou, ajoelhou-se diante dele e disse: ""Minha filha acaba de morrer. Vem e impõe a tua mão sobre ela, e ela viverá"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23399, 19, "Jesus levantou-se e foi com ele, e também os seus discípulos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23400, 20, "Nisso uma mulher que havia doze anos vinha sofrendo de uma hemorragia, chegou por trás dele e tocou na borda do seu manto,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23401, 21, "pois dizia a si mesma: ""Se eu tão-somente tocar em seu manto, ficarei curada"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23402, 22, "Voltando-se, Jesus a viu e disse: ""Ânimo, filha, a sua fé a curou! "" E desde aquele instante a mulher ficou curada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23403, 23, "Quando ele chegou à casa do dirigente da sinagoga e viu os flautistas e a multidão agitada,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23404, 24, "disse: ""Saiam! A menina não está morta, mas dorme"". Todos começaram a rir dele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23405, 25, "Depois que a multidão se afastou, ele entrou e tomou a menina pela mão, e ela se levantou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23406, 26, "A notícia deste acontecimento espalhou-se por toda aquela região.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23407, 27, "Saindo Jesus dali, dois cegos o seguiram, clamando: ""Filho de Davi, tem misericórdia de nós! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23408, 28, "Entrando ele em casa, os cegos se aproximaram, e ele lhes perguntou: ""Vocês crêem que eu sou capaz de fazer isso? "" Eles responderam: ""Sim, Senhor! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23409, 29, "E ele, tocando nos olhos deles, disse: ""Que lhes seja feito segundo a fé que vocês têm! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23410, 30, "E a visão deles foi restaurada. Então Jesus os advertiu severamente: ""Cuidem para que ninguém saiba disso"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23411, 31, "Eles, porém, saíram e espalharam a notícia por toda aquela região.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23412, 32, "Enquanto eles se retiravam, foi levado a Jesus um homem endemoninhado que não podia falar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23413, 33, "Quando o demônio foi expulso, o mudo começou a falar. A multidão ficou admirada e disse: ""Nunca se viu nada parecido em Israel! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23414, 34, "Mas os fariseus diziam: ""É pelo príncipe dos demônios que ele expulsa demônios"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23415, 35, "Jesus ia passando por todas as cidades e povoados, ensinando nas sinagogas, pregando as boas novas do Reino e curando todas as enfermidades e doenças.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23416, 36, "Ao ver as multidões, teve compaixão delas, porque estavam aflitas e desamparadas, como ovelhas sem pastor.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23417, 37, "Então disse aos seus discípulos: ""A seara é grande, mas os trabalhadores são poucos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23418, 38, "Peçam, pois, ao Senhor da seara que envie trabalhadores para a sua seara"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23419, 1, "Chamando seus doze discípulos, deu-lhes autoridade para expulsar espíritos imundos e curar todas as doenças e enfermidades.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23420, 2, "Estes são os nomes dos doze apóstolos: primeiro, Simão, chamado Pedro, e André, seu irmão; Tiago, filho de Zebedeu, e João, seu irmão;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23421, 3, "Filipe e Bartolomeu; Tomé e Mateus, o publicano; Tiago, filho de Alfeu, e Tadeu;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23422, 4, "Simão, o zelote, e Judas Iscariotes, que o traiu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23423, 5, "Jesus enviou estes doze com as seguintes instruções: ""Não se dirijam aos gentios, nem entrem em cidade alguma dos samaritanos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23424, 6, "Antes, dirijam-se às ovelhas perdidas de Israel.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23425, 7, "Por onde forem, preguem esta mensagem: ‘O Reino dos céus está próximo’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23426, 8, "Curem os enfermos, ressuscitem os mortos, purifiquem os leprosos, expulsem os demônios. Vocês receberam de graça; dêem também de graça.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23427, 9, "Não levem nem ouro, nem prata, nem cobre em seus cintos;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23428, 10, "não levem nenhum saco de viagem, nem túnica extra, nem sandálias, nem bordão; pois o trabalhador é digno do seu sustento.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23429, 11, """Na cidade ou povoado em que entrarem, procurem alguém digno de recebê-los, e fiquem em sua casa até partirem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23430, 12, "Ao entrarem na casa, saúdem-na.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23431, 13, "Se a casa for digna, que a paz de vocês repouse sobre ela; se não for, que a paz retorne para vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23432, 14, "Se alguém não os receber nem ouvir suas palavras, sacudam a poeira dos pés, quando saírem daquela casa ou cidade.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23433, 15, "Eu lhes digo a verdade: No dia do juízo haverá menor rigor para Sodoma e Gomorra do que para aquela cidade.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23434, 16, "Eu os estou enviando como ovelhas entre lobos. Portanto, sejam prudentes como as serpentes e simples como as pombas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23435, 17, """Tenham cuidado, pois os homens os entregarão aos tribunais e os açoitarão nas sinagogas deles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23436, 18, "Por minha causa vocês serão levados à presença de governadores e reis como testemunhas a eles e aos gentios.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23437, 19, "Mas quando os prenderem, não se preocupem quanto ao que dizer, ou como dizer. Naquela hora lhes será dado o que dizer,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23438, 20, "pois não serão vocês que estarão falando, mas o Espírito do Pai de vocês falará por intermédio de vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23439, 21, """O irmão entregará à morte o seu irmão, e o pai o seu filho; filhos se rebelarão contra seus pais e os matarão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23440, 22, "Todos odiarão vocês por minha causa, mas aquele que perseverar até o fim será salvo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23441, 23, "Quando forem perseguidos num lugar, fujam para outro. Eu lhes garanto que vocês não terão percorrido todas as cidades de Israel antes que venha o Filho do homem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23442, 24, """O discípulo não está acima do seu mestre, nem o servo acima do seu senhor.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23443, 25, "Basta ao discípulo ser como o seu mestre, e ao servo, como o seu senhor. Se o dono da casa foi chamado Belzebu, quanto mais os membros da sua família!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23444, 26, """Portanto, não tenham medo deles. Não há nada escondido que não venha a ser revelado, nem oculto que não venha a se tornar conhecido.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23445, 27, "O que eu lhes digo na escuridão, falem à luz do dia; o que é sussurrado em seus ouvidos, proclamem dos telhados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23446, 28, "Não tenham medo dos que matam o corpo, mas não podem matar a alma. Antes, tenham medo daquele que pode destruir tanto a alma como o corpo no inferno.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23447, 29, "Não se vendem dois pardais por uma moedinha? Contudo, nenhum deles cai no chão sem o consentimento do Pai de vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23448, 30, "Até os cabelos da cabeça de vocês estão todos contados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23449, 31, "Portanto, não tenham medo; vocês valem mais do que muitos pardais!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23450, 32, """Quem, pois, me confessar diante dos homens, eu também o confessarei diante do meu Pai que está nos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23451, 33, "Mas aquele que me negar diante dos homens, eu também o negarei diante do meu Pai que está nos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23452, 34, """Não pensem que vim trazer paz à terra; não vim trazer paz, mas espada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23453, 35, "Pois vim para fazer que ‘o homem fique contra seu pai, a filha contra sua mãe, a nora contra sua sogra;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23454, 36, "os inimigos do homem serão os da sua própria família’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23455, 37, """Quem ama seu pai ou sua mãe mais do que a mim não é digno de mim; quem ama seu filho ou sua filha mais do que a mim não é digno de mim;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23456, 38, "e quem não toma a sua cruz e não me segue, não é digno de mim.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23457, 39, "Quem acha a sua vida a perderá, e quem perde a sua vida por minha causa a encontrará.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23458, 40, """Quem recebe vocês, recebe a mim; e quem me recebe, recebe aquele que me enviou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23459, 41, "Quem recebe um profeta, porque ele é profeta, receberá a recompensa de profeta, e quem recebe um justo, porque ele é justo, receberá a recompensa de justo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23460, 42, "E se alguém der mesmo que seja apenas um copo de água fria a um destes pequeninos, porque ele é meu discípulo, eu lhes asseguro que não perderá a sua recompensa"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23461, 1, "Depois que terminou de instruir seus doze discípulos, Jesus saiu para ensinar e pregar nas cidades da Galiléia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23462, 2, "João, ao ouvir na prisão o que Cristo estava fazendo, enviou seus discípulos para lhe perguntarem:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23463, 3, """És tu aquele que haveria de vir ou devemos esperar algum outro? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23464, 4, "Jesus respondeu: ""Voltem e anunciem a João o que vocês estão ouvindo e vendo:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23465, 5, "os cegos vêem, os mancos andam, os leprosos são purificados, os surdos ouvem, os mortos são ressuscitados, e as boas novas são pregadas aos pobres;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23466, 6, "e feliz é aquele que não se escandaliza por minha causa"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23467, 7, "Enquanto saíam os discípulos de João, Jesus começou a falar à multidão a respeito de João: ""O que vocês foram ver no deserto? Um caniço agitado pelo vento?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23468, 8, "Ou, o que foram ver? Um homem vestido de roupas finas? Ora, os que usam roupas finas estão nos palácios reais.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23469, 9, "Afinal, o que foram ver? Um profeta? Sim, eu lhes digo, e mais que profeta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23470, 10, "Este é aquele a respeito de quem está escrito: ‘Enviarei o meu mensageiro à tua frente; ele preparará o teu caminho diante de ti’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23471, 11, "Digo-lhes a verdade: Entre os nascidos de mulher não surgiu ninguém maior do que João Batista; todavia, o menor no Reino dos céus é maior do que ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23472, 12, "Desde os dias de João Batista até agora, o Reino dos céus é tomado à força, e os que usam de força se apoderam dele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23473, 13, "Pois todos os Profetas e a Lei profetizaram até João.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23474, 14, "E se vocês quiserem aceitar, este é o Elias que havia de vir.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23475, 15, "Aquele que tem ouvidos, ouça!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23476, 16, """A que posso comparar esta geração? São como crianças que ficam sentadas nas praças e gritam umas às outras:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23477, 17, "‘Nós lhes tocamos flauta, mas vocês não dançaram; cantamos um lamento, mas vocês não se entristeceram’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23478, 18, "Pois veio João, que jejua e não bebe vinho, e dizem: ‘Ele tem demônio’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23479, 19, "Veio o Filho do homem comendo e bebendo, e dizem: ‘Aí está um comilão e beberrão, amigo de publicanos e ""pecadores"" ’. Mas a sabedoria é comprovada pelas obras que a acompanham"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23480, 20, "Então Jesus começou a denunciar as cidades em que havia sido realizada a maioria dos seus milagres, porque não se arrependeram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23481, 21, """Ai de você, Corazim! Ai de você, Betsaida! Porque se os milagres que foram realizados entre vocês tivessem sido realizados em Tiro e Sidom, há muito tempo elas se teriam arrependido, vestindo roupas de saco e cobrindo-se de cinzas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23482, 22, "Mas eu lhes afirmo que no dia do juízo haverá menor rigor para Tiro e Sidom do que para vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23483, 23, "E você, Cafarnaum: será elevada até o céu? Não, você descerá até ao Hades! Se os milagres que em você foram realizados tivessem sido realizados em Sodoma, ela teria permanecido até hoje.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23484, 24, "Mas eu lhes afirmo que no dia do juízo haverá menor rigor para Sodoma do que para você"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23485, 25, "Naquela ocasião Jesus disse: ""Eu te louvo, Pai, Senhor dos céus e da terra, porque escondeste estas coisas dos sábios e cultos, e as revelaste aos pequeninos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23486, 26, "Sim, Pai, pois assim foi do teu agrado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23487, 27, """Todas as coisas me foram entregues por meu Pai. Ninguém conhece o Filho a não ser o Pai, e ninguém conhece o Pai a não ser o Filho e aqueles a quem o Filho o quiser revelar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23488, 28, """Venham a mim, todos os que estão cansados e sobrecarregados, e eu lhes darei descanso.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23489, 29, "Tomem sobre vocês o meu jugo e aprendam de mim, pois sou manso e humilde de coração, e vocês encontrarão descanso para as suas almas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23490, 30, "Pois o meu jugo é suave e o meu fardo é leve"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23491, 1, "Naquela ocasião Jesus passou pelas lavouras de cereal no sábado. Seus discípulos estavam com fome e começaram a colher espigas para comê-las.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23492, 2, "Os fariseus, vendo aquilo, lhe disseram: ""Olha, os teus discípulos estão fazendo o que não é permitido no sábado"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23493, 3, "Ele respondeu: ""Vocês não leram o que fez Davi quando ele e seus companheiros estavam com fome?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23494, 4, "Ele entrou na casa de Deus, e juntamente com os seus companheiros comeu os pães da Presença, o que não lhes era permitido fazer, mas apenas aos sacerdotes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23495, 5, "Ou vocês não leram na Lei que, no sábado, os sacerdotes no templo profanam esse dia e, contudo, ficam sem culpa?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23496, 6, "Eu lhes digo que aqui está o que é maior do que o templo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23497, 7, "Se vocês soubessem o que significam estas palavras: ‘Desejo misericórdia, não sacrifícios’, não teriam condenado inocentes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23498, 8, "Pois o Filho do homem é Senhor do sábado"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23499, 9, "Saindo daquele lugar, dirigiu-se à sinagoga deles,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23500, 10, "e estava ali um homem com uma das mãos atrofiada. Procurando um motivo para acusar Jesus, eles lhe perguntaram: ""É permitido curar no sábado? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23501, 11, "Ele lhes respondeu: ""Qual de vocês, se tiver uma ovelha e ela cair num buraco no sábado, não irá pegá-la e tirá-la de lá?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23502, 12, "Quanto mais vale um homem do que uma ovelha! Portanto, é permitido fazer o bem no sábado"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23503, 13, "Então ele disse ao homem: ""Estenda a mão"". Ele a estendeu, e ela foi restaurada, e ficou boa como a outra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23504, 14, "Então os fariseus saíram e começaram a conspirar sobre como poderiam matar Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23505, 15, "Sabendo disso, Jesus retirou-se daquele lugar. Muitos o seguiram, e ele curou a todos os doentes que havia entre eles,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23506, 16, "advertindo-os que não dissessem quem ele era.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23507, 17, "Isso aconteceu para se cumprir o que fora dito pelo do profeta Isaías:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23508, 18, """Eis o meu servo, a quem escolhi, o meu amado, em quem tenho prazer. Porei sobre ele o meu Espírito, e ele anunciará justiça às nações.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23509, 19, "Não discutirá nem gritará; ninguém ouvirá sua voz nas ruas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23510, 20, "Não quebrará o caniço rachado, não apagará o pavio fumegante, até que leve à vitória a justiça.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23511, 21, "Em seu nome as nações porão sua esperança"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23512, 22, "Então levaram-lhe um endemoninhado que era cego e mudo, e Jesus o curou, de modo que ele pôde falar e ver.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23513, 23, "Todo o povo ficou atônito e disse: ""Não será este o Filho de Davi? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23514, 24, "Mas quando os fariseus ouviram isso, disseram: ""É somente por Belzebu, o príncipe dos demônios, que ele expulsa demônios"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23515, 25, "Jesus, conhecendo os seus pensamentos, disse-lhes: ""Todo reino dividido contra si mesmo será arruinado, e toda cidade ou casa dividida contra si mesma não subsistirá.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23516, 26, "Se Satanás expulsa Satanás, está dividido contra si mesmo. Como, então, subsistirá seu reino?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23517, 27, "E se eu expulso demônios por Belzebu, por quem os expulsam os filhos de vocês? Por isso, eles mesmos serão juízes sobre vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23518, 28, "Mas se é pelo Espírito de Deus que eu expulso demônios, então chegou a vocês o Reino de Deus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23519, 29, """Ou como alguém pode entrar na casa do homem forte e levar dali seus bens, sem antes amarrá-lo? Só então poderá roubar a casa dele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23520, 30, """Aquele que não está comigo, está contra mim; e aquele que comigo não ajunta, espalha.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23521, 31, "Por esse motivo eu lhes digo: todo pecado e blasfêmia serão perdoados aos homens, mas a blasfêmia contra o Espírito não será perdoada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23522, 32, "Todo aquele que disser uma palavra contra o Filho do homem será perdoado, mas quem falar contra o Espírito Santo não será perdoado, nem nesta era nem na era que há de vir.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23523, 33, """Considerem: uma árvore boa dá bom fruto; uma árvore ruim, dá fruto ruim, pois uma árvore é conhecida por seu fruto.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23524, 34, "Raça de víboras, como podem vocês, que são maus, dizer coisas boas? Pois a boca fala do que está cheio o coração.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23525, 35, "O homem bom, do seu bom tesouro, tira coisas boas, e o homem mau, do seu mau tesouro, tira coisas más.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23526, 36, "Mas eu lhes digo que, no dia do juízo, os homens haverão de dar conta de toda palavra inútil que tiverem falado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23527, 37, "Pois por suas palavras você será absolvido, e por suas palavras será condenado"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23528, 38, "Então alguns dos fariseus e mestres da lei lhe disseram: ""Mestre, queremos ver um sinal miraculoso feito por ti"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23529, 39, "Ele respondeu: ""Uma geração perversa e adúltera pede um sinal miraculoso! Mas nenhum sinal lhe será dado, exceto o sinal do profeta Jonas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23530, 40, "Pois assim como Jonas esteve três dias e três noites no ventre de um grande peixe, assim o Filho do homem ficará três dias e três noites no coração da terra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23531, 41, "Os homens de Nínive se levantarão no juízo com esta geração e a condenarão; pois eles se arrependeram com a pregação de Jonas, e agora está aqui o que é maior do que Jonas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23532, 42, "A rainha do Sul se levantará no juízo com esta geração e a condenará, pois ela veio dos confins da terra para ouvir a sabedoria de Salomão, e agora está aqui o que é maior do que Salomão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23533, 43, """Quando um espírito imundo sai de um homem, passa por lugares áridos procurando descanso e não encontra,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23534, 44, "e diz: ‘Voltarei para a casa de onde saí’. Chegando, encontra a casa desocupada, varrida e em ordem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23535, 45, "Então vai e traz consigo outros sete espíritos piores do que ele, e entrando passam a viver ali. E o estado final daquele homem torna-se pior do que o primeiro. Assim acontecerá a esta geração perversa"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23536, 46, "Falava ainda Jesus à multidão quando sua mãe e seus irmãos chegaram do lado de fora, querendo falar com ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23537, 47, "Alguém lhe disse: ""Tua mãe e teus irmãos estão lá fora e querem falar contigo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23538, 48, """Quem é minha mãe, e quem são meus irmãos? "", perguntou ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23539, 49, "E, estendendo a mão para os discípulos, disse: ""Aqui estão minha mãe e meus irmãos!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23540, 50, "Pois quem faz a vontade de meu Pai que está nos céus, este é meu irmão, minha irmã e minha mãe"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23541, 1, "Naquele mesmo dia Jesus saiu de casa e assentou-se à beira-mar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23542, 2, "Reuniu-se ao seu redor uma multidão tão grande que ele teve que entrar num barco e assentar-se nele, enquanto todo o povo ficou na praia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23543, 3, "Então lhes falou muitas coisas por parábolas, dizendo: ""O semeador saiu a semear.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23544, 4, "Enquanto lançava a semente, parte dela caiu à beira do caminho, e as aves vieram e a comeram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23545, 5, "Parte dela caiu em terreno pedregoso, onde não havia muita terra; e logo brotou, porque a terra não era profunda.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23546, 6, "Mas quando saiu o sol, as plantas se queimaram e secaram, porque não tinham raiz.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23547, 7, "Outra parte caiu entre espinhos, que cresceram e sufocaram as plantas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23548, 8, "Outra ainda caiu em boa terra, deu boa colheita, a cem, sessenta e trinta por um.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23549, 9, "Aquele que tem ouvidos para ouvir, ouça! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23550, 10, "Os discípulos aproximaram-se dele e perguntaram: ""Por que falas ao povo por parábolas? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23551, 11, "Ele respondeu: ""A vocês foi dado o conhecimento dos mistérios do Reino dos céus, mas a eles não.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23552, 12, "A quem tem será dado, e este terá em grande quantidade. De quem não tem, até o que tem lhe será tirado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23553, 13, "Por essa razão eu lhes falo por parábolas: ‘Porque vendo, eles não vêem e, ouvindo, não ouvem nem entendem’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23554, 14, "Neles se cumpre a profecia de Isaías: ‘Ainda que estejam sempre ouvindo, vocês nunca entenderão; ainda que estejam sempre vendo, jamais perceberão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23555, 15, "Pois o coração deste povo se tornou insensível; de má vontade ouviram com os seus ouvidos, e fecharam os seus olhos. Se assim não fosse, poderiam ver com os olhos, ouvir com os ouvidos, entender com o coração e converter-se, e eu os curaria’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23556, 16, "Mas, felizes são os olhos de vocês, porque vêem; e os ouvidos de vocês, porque ouvem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23557, 17, "Pois eu lhes digo a verdade: Muitos profetas e justos desejaram ver o que vocês estão vendo, mas não viram, e ouvir o que vocês estão ouvindo, mas não ouviram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23558, 18, """Portanto, ouçam o que significa a parábola do semeador:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23559, 19, "Quando alguém ouve a mensagem do Reino e não a entende, o Maligno vem e lhe arranca o que foi semeado em seu coração. Este é o que foi semeado à beira do caminho.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23560, 20, "Quanto ao que foi semeado em terreno pedregoso, este é aquele que ouve a palavra e logo a recebe com alegria.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23561, 21, "Todavia, visto que não tem raiz em si mesmo, permanece por pouco tempo. Quando surge alguma tribulação ou perseguição por causa da palavra, logo a abandona.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23562, 22, "Quanto ao que foi semeado entre os espinhos, este é aquele que ouve a palavra, mas a preocupação desta vida e o engano das riquezas a sufocam, tornando-a infrutífera.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23563, 23, "E, finalmente, o que foi semeado em boa terra: este é aquele que ouve a palavra e a entende, e dá uma colheita de cem, sessenta e trinta por um"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23564, 24, "Jesus lhes contou outra parábola, dizendo: ""O Reino dos céus é como um homem que semeou boa semente em seu campo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23565, 25, "Mas enquanto todos dormiam, veio o seu inimigo e semeou o joio no meio do trigo e se foi.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23566, 26, "Quando o trigo brotou e formou espigas, o joio também apareceu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23567, 27, """Os servos do dono do campo dirigiram-se a ele e disseram: ‘O senhor não semeou boa semente em seu campo? Então, de onde veio o joio? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23568, 28, """ ‘Um inimigo fez isso’, respondeu ele. ""Os servos lhe perguntaram: ‘O senhor quer que vamos tirá-lo? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23569, 29, """Ele respondeu: ‘Não, porque, ao tirar o joio, vocês poderão arrancar com ele o trigo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23570, 30, "Deixem que cresçam juntos até à colheita. Então direi aos encarregados da colheita: Juntem primeiro o joio e amarrem-no em feixes para ser queimado; depois juntem o trigo e guardem-no no meu celeiro’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23571, 31, "E contou-lhes outra parábola: ""O Reino dos céus é como um grão de mostarda que um homem plantou em seu campo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23572, 32, "Embora seja a menor dentre todas as sementes, quando cresce torna-se a maior das hortaliças e se transforma numa árvore, de modo que as aves do céu vêm fazer os seus ninhos em seus ramos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23573, 33, "E contou-lhes ainda outra parábola: ""O Reino dos céus é como o fermento que uma mulher tomou e misturou com uma grande quantidade de farinha, e toda a massa ficou fermentada"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23574, 34, "Jesus falou todas estas coisas à multidão por parábolas. Nada lhes dizia sem usar alguma parábola,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23575, 35, "cumprindo-se, assim, o que fora dito pelo profeta: ""Abrirei minha boca em parábolas, Proclamarei coisas ocultas Desde a criação do mundo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23576, 36, "Então ele deixou a multidão e foi para casa. Seus discípulos aproximaram-se dele e disseram: ""Explica-nos a parábola do joio no campo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23577, 37, "Ele respondeu: ""Aquele que semeou a boa semente é o Filho do homem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23578, 38, "O campo é o mundo, e a boa semente são os filhos do Reino. O joio são os filhos do Maligno,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23579, 39, "e o inimigo que o semeia é o diabo. A colheita é o fim desta era, e os encarregados da colheita são anjos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23580, 40, """Assim como o joio é colhido e queimado no fogo, assim também acontecerá no fim desta era.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23581, 41, "O Filho do homem enviará os seus anjos, e eles tirarão do seu Reino tudo o que faz tropeçar e todos os que praticam o mal.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23582, 42, "Eles os lançarão na fornalha ardente, onde haverá choro e ranger de dentes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23583, 43, "Então os justos brilharão como o sol no Reino do seu Pai. Aquele que tem ouvidos, ouça"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23584, 44, """O Reino dos céus é como um tesouro escondido num campo. Certo homem, tendo-o encontrado, escondeu-o de novo e, então, cheio de alegria, foi, vendeu tudo o que tinha e comprou aquele campo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23585, 45, """O Reino dos céus também é como um negociante que procura pérolas preciosas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23586, 46, "Encontrando uma pérola de grande valor, foi, vendeu tudo o que tinha e a comprou"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23587, 47, """O Reino dos céus é ainda como uma rede que é lançada ao mar e apanha toda sorte de peixes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23588, 48, "Quando está cheia, os pescadores a puxam para a praia. Então se assentam e juntam os peixes bons em cestos, mas jogam fora os ruins.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23589, 49, "Assim acontecerá no fim desta era. Os anjos virão, separarão os perversos dos justos", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23590, 50, "e lançarão aqueles na fornalha ardente, onde haverá choro e ranger de dentes"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23591, 51, "Então perguntou Jesus: ""Vocês entenderam todas essas coisas? "" ""Sim"", responderam eles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23592, 52, "Ele lhes disse: ""Por isso, todo mestre da lei instruído quanto ao Reino dos céus é como o dono de uma casa que tira do seu tesouro coisas novas e coisas velhas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23593, 53, "Tendo terminado de contar estas parábolas, Jesus saiu dali.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23594, 54, "Chegando à sua cidade, começou a ensinar o povo na sinagoga. Todos ficaram admirados e perguntavam: ""De onde lhe vêm esta sabedoria e estes poderes miraculosos?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23595, 55, "Não é este o filho do carpinteiro? O nome de sua mãe não é Maria, e não são seus irmãos Tiago, José, Simão e Judas?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23596, 56, "Não estão conosco todas as suas irmãs? De onde, pois, ele obteve todas essas coisas? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23597, 57, "E ficavam escandalizados por causa dele. Mas Jesus lhes disse: ""Só em sua própria terra e em sua própria casa é que um profeta não tem honra"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23598, 58, "E não realizou muitos milagres ali, por causa da incredulidade deles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23599, 1, "Por aquele tempo Herodes, o tetrarca, ouviu os relatos a respeito de Jesus", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23600, 2, "e disse aos que o serviam: ""Este é João Batista; ele ressuscitou dos mortos! Por isso estão operando nele poderes miraculosos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23601, 3, "Pois Herodes havia prendido e amarrado João, colocando-o na prisão por causa de Herodias, mulher de Filipe, seu irmão,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23602, 4, "porquanto João lhe dizia: ""Não te é permitido viver com ela"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23603, 5, "Herodes queria matá-lo, mas tinha medo do povo, porque este o considerava profeta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23604, 6, "No aniversário de Herodes, a filha de Herodias dançou diante de todos, e agradou tanto a Herodes", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23605, 7, "que ele prometeu sob juramento dar-lhe o que ela pedisse.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23606, 8, "Influenciada por sua mãe, ela disse: ""Dá-me aqui, num prato, a cabeça de João Batista"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23607, 9, "O rei ficou aflito, mas, por causa dos juramento e dos convidados, ordenou que lhe fosse dado o que ela pedia", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23608, 10, "e mandou decapitar João na prisão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23609, 11, "Sua cabeça foi levada num prato e entregue à jovem, que a levou à sua mãe.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23610, 12, "Os discípulos de João vieram, levaram o seu corpo e o sepultaram. Depois foram contar isso a Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23611, 13, "Ouvindo o que havia ocorrido, Jesus retirou-se de barco em particular para um lugar deserto. As multidões, ao ouvirem falar disso, saíram das cidades e o seguiram a pé.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23612, 14, "Quando Jesus saiu do barco e viu tão grande multidão, teve compaixão deles e curou os seus doentes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23613, 15, "Ao cair da tarde, os discípulos aproximaram-se dele e disseram: ""Este é um lugar deserto, e já está ficando tarde. Manda embora a multidão para que possam ir aos povoados comprar comida"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23614, 16, "Respondeu Jesus: ""Eles não precisam ir. Dêem-lhes vocês algo para comer"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23615, 17, "Eles lhe disseram: ""Tudo o que temos aqui são cinco pães e dois peixes"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23616, 18, """Tragam-nos aqui para mim"", disse ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23617, 19, "E ordenou que a multidão se assentasse na grama. Tomando os cinco pães e os dois peixes e, olhando para o céu, deu graças e partiu os pães. Em seguida, deu-os aos discípulos, e estes à multidão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23618, 20, "Todos comeram e ficaram satisfeitos, e os discípulos recolheram doze cestos cheios de pedaços que sobraram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23619, 21, "Os que comeram foram cerca de cinco mil homens, sem contar mulheres e crianças.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23620, 22, "Logo em seguida, Jesus insistiu com os discípulos para que entrassem no barco e fossem adiante dele para o outro lado, enquanto ele despedia a multidão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23621, 23, "Tendo despedido a multidão, subiu sozinho a um monte para orar. Ao anoitecer, ele estava ali sozinho,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23622, 24, "mas o barco já estava a considerável distância da terra, fustigado pelas ondas, porque o vento soprava contra ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23623, 25, "Alta madrugada, Jesus dirigiu-se a eles, andando sobre o mar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23624, 26, "Quando o viram andando sobre o mar, ficaram aterrorizados e disseram: ""É um fantasma! "" E gritaram de medo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23625, 27, "Mas Jesus imediatamente lhes disse: ""Coragem! Sou eu. Não tenham medo! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23626, 28, """Senhor"", disse Pedro, ""se és tu, manda-me ir ao teu encontro por sobre as águas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23627, 29, """Venha"", respondeu ele. Então Pedro saiu do barco, andou sobre a água e foi na direção de Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23628, 30, "Mas, quando reparou no vento, ficou com medo e, começando a afundar, gritou: ""Senhor, salva-me! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23629, 31, "Imediatamente Jesus estendeu a mão e o segurou. E disse: ""Homem de pequena fé, porque você duvidou? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23630, 32, "Quando entraram no barco, o vento cessou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23631, 33, "Então os que estavam no barco o adoraram, dizendo: ""Verdadeiramente tu és o Filho de Deus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23632, 34, "Depois de atravessarem o mar, chegaram a Genesaré.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23633, 35, "Quando os homens daquele lugar reconheceram Jesus, espalharam a notícia em toda aquela região e lhe trouxeram os seus doentes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23634, 36, "Suplicavam-lhe que apenas pudessem tocar na borda do seu manto; e todos os que nele tocaram foram curados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23635, 1, "Então alguns fariseus e mestres da lei, vindos de Jerusalém, foram a Jesus e perguntaram:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23636, 2, """Por que os seus discípulos transgridem a tradição dos líderes religiosos? Pois não lavam as mãos antes de comer! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23637, 3, "Respondeu Jesus: ""E por que vocês transgridem o mandamento de Deus por causa da tradição de vocês?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23638, 4, "Pois Deus disse: ‘Honra teu pai e tua mãe’ e ‘quem amaldiçoar seu pai ou sua mãe terá que ser executado’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23639, 5, "Mas vocês afirmam que se alguém disser a seu pai ou a sua mãe: ‘Qualquer ajuda que vocês poderiam receber de mim é uma oferta dedicada a Deus’,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23640, 6, "ele não é obrigado a ‘honrar seu pai’ dessa forma. Assim vocês anulam a palavra de Deus por causa da tradição de vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23641, 7, "Hipócritas! Bem profetizou Isaías acerca de vocês, dizendo:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23642, 8, "‘Este povo me honra com os lábios, mas o seu coração está longe de mim.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23643, 9, "Em vão me adoram; seus ensinamentos não passam de regras ensinadas por homens’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23644, 10, "Jesus chamou para junto de si a multidão e disse: ""Ouçam e entendam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23645, 11, "O que entra pela boca não torna o homem ‘impuro’; mas o que sai de sua boca, isto o torna ‘impuro’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23646, 12, "Então os discípulos se aproximaram dele e perguntaram: ""Sabes que os fariseus ficaram ofendidos quando ouviram isso? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23647, 13, "Ele respondeu: ""Toda planta que meu Pai celestial não plantou será arrancada pelas raízes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23648, 14, "Deixem-nos; eles são guias cegos. Se um cego conduzir outro cego, ambos cairão num buraco"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23649, 15, "Então Pedro pediu-lhe: ""Explica-nos a parábola"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23650, 16, """Será que vocês ainda não conseguem entender? "", perguntou Jesus:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23651, 17, """Não percebem que o que entra pela boca vai para o estômago e mais tarde é expelido?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23652, 18, "Mas as coisas que saem da boca vêm do coração, e são essas que tornam o homem ‘impuro’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23653, 19, "Pois do coração saem os maus pensamentos, os homicídios, os adultérios, as imoralidades sexuais, os roubos, os falsos testemunhos e as calúnias.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23654, 20, "Essas coisas tornam o homem ‘impuro’; mas o comer sem lavar as mãos não o torna ‘impuro’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23655, 21, "Saindo daquele lugar, Jesus retirou-se para a região de Tiro e de Sidom.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23656, 22, "Uma mulher cananéia, natural dali, veio a ele, gritando: ""Senhor, Filho de Davi, tem misericórdia de mim! Minha filha está endemoninhada e está sofrendo muito"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23657, 23, "Mas Jesus não lhe respondeu palavra. Então seus discípulos se aproximaram dele e pediram: ""Manda-a embora, pois vem gritando atrás de nós"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23658, 24, "Ele respondeu: ""Eu fui enviado apenas às ovelhas perdidas de Israel"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23659, 25, "A mulher veio, adorou-o de joelhos e disse: ""Senhor, ajuda-me! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23660, 26, "Ele respondeu: ""Não é certo tirar o pão dos filhos e lançá-lo aos cachorrinhos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23661, 27, "Disse ela, porém: ""Sim, Senhor, mas até os cachorrinhos comem das migalhas que caem da mesa dos seus donos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23662, 28, "Jesus respondeu: ""Mulher, grande é a sua fé! Seja conforme você deseja"". E naquele mesmo instante a sua filha foi curada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23663, 29, "Jesus saiu dali e foi para a beira do mar da Galiléia. Depois subiu a um monte e se assentou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23664, 30, "Uma grande multidão dirigiu-se a ele, levando-lhe os mancos, os aleijados, os cegos, os mudos e muitos outros, e os colocaram aos seus pés; e ele os curou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23665, 31, "O povo ficou admirado quando viu os mudos falando, os aleijados curados, os mancos andando e os cegos vendo. E louvaram o Deus de Israel.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23666, 32, "Jesus chamou os seus discípulos e disse: ""Tenho compaixão desta multidão; já faz três dias que eles estão comigo e nada têm para comer. Não quero mandá-los embora com fome, porque podem desfalecer no caminho"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23667, 33, "Os seus discípulos responderam: ""Onde poderíamos encontrar, neste lugar deserto, pão suficiente para alimentar tanta gente? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23668, 34, """Quantos pães vocês têm? "", perguntou Jesus. ""Sete"", responderam eles, ""e alguns peixinhos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23669, 35, "Ele ordenou à multidão que se assentasse no chão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23670, 36, "Depois de tomar os sete pães e os peixes e dar graças, partiu-os e os entregou aos discípulos, e os discípulos à multidão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23671, 37, "Todos comeram até se fartar. E ajuntaram sete cestos cheios de pedaços que sobraram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23672, 38, "Os que comeram foram quatro mil homens, sem contar mulheres e crianças.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23673, 39, "E, havendo despedido a multidão, Jesus entrou no barco e foi para a região de Magadã.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23674, 1, "Os fariseus e os saduceus aproximaram-se de Jesus e o puseram à prova, pedindo-lhe que lhes mostrasse um sinal do céu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23675, 2, "Ele respondeu: ""Quando a tarde vem, vocês dizem: ‘Vai fazer bom tempo, porque o céu está vermelho’,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23676, 3, "e de manhã: ‘Hoje haverá tempestade, porque o céu está vermelho e nublado’. Vocês sabem interpretar o aspecto do céu, mas não sabem interpretar os sinais dos tempos!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23677, 4, "Uma geração perversa e adúltera pede um sinal miraculoso, mas nenhum sinal lhe será dado, a não ser o sinal de Jonas"". Então Jesus os deixou e retirou-se.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23678, 5, "Indo os discípulos para o outro lado do mar, esqueceram-se de levar pão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23679, 6, "Disse-lhes Jesus: ""Estejam atentos e tenham cuidado com o fermento dos fariseus e dos saduceus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23680, 7, "E eles discutiam entre si, dizendo: ""É porque não trouxemos pão"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23681, 8, "Percebendo a discussão, Jesus lhes perguntou: ""Homens de pequena fé, por que vocês estão discutindo entre si sobre não terem pão?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23682, 9, "Ainda não compreendem? Não se lembram dos cinco pães para os cinco mil e de quantos cestos vocês recolheram?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23683, 10, "Nem dos sete pães para os quatro mil e de quantos cestos recolheram?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23684, 11, "Como é que vocês não entendem que não era de pão que eu estava lhes falando? Mas tomem cuidado com o fermento dos fariseus e dos saduceus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23685, 12, "Então entenderam que não estava lhes dizendo que tomassem cuidado com o fermento de pão, mas com o ensino dos fariseus e dos saduceus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23686, 13, "Chegando Jesus à região de Cesaréia de Filipe, perguntou aos seus discípulos: ""Quem os homens dizem que o Filho do homem é? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23687, 14, "Eles responderam: ""Alguns dizem que é João Batista; outros, Elias; e, ainda outros, Jeremias ou um dos profetas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23688, 15, """E vocês? "", perguntou ele. ""Quem vocês dizem que eu sou? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23689, 16, "Simão Pedro respondeu: ""Tu és o Cristo, o Filho do Deus vivo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23690, 17, "Respondeu Jesus: ""Feliz é você, Simão, filho de Jonas! Porque isto não lhe foi revelado por carne ou sangue, mas por meu Pai que está nos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23691, 18, "E eu lhe digo que você é Pedro, e sobre esta pedra edificarei a minha igreja, e as portas do Hades não poderão vencê-la.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23692, 19, "Eu lhe darei as chaves do Reino dos céus; o que você ligar na terra terá sido ligado nos céus, e o que você desligar na terra terá sido desligado nos céus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23693, 20, "Então advertiu a seus discípulos que não contassem a ninguém que ele era o Cristo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23694, 21, "Desde aquele momento Jesus começou a explicar aos seus discípulos que era necessário que ele fosse para Jerusalém e sofresse muitas coisas nas mãos dos líderes religiosos, dos chefes dos sacerdotes e dos mestres da lei, e fosse morto e ressuscitasse no terceiro dia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23695, 22, "Então Pedro, chamando-o à parte, começou a repreendê-lo, dizendo: ""Nunca, Senhor! Isso nunca te acontecerá! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23696, 23, "Jesus virou-se e disse a Pedro: ""Para trás de mim, Satanás! Você é uma pedra de tropeço para mim, e não pensa nas coisas de Deus, mas nas dos homens"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23697, 24, "Então Jesus disse aos seus discípulos: ""Se alguém quiser acompanhar-me, negue-se a si mesmo, tome a sua cruz e siga-me.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23698, 25, "Pois quem quiser salvar a sua vida, a perderá, mas quem perder a vida por minha causa, a encontrará.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23699, 26, "Pois, que adiantará ao homem ganhar o mundo inteiro e perder a sua alma? Ou, o que o homem poderá dar em troca de sua alma?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23700, 27, "Pois o Filho do homem virá na glória de seu Pai, com os seus anjos, e então recompensará a cada um de acordo com o que tenha feito.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23701, 28, "Garanto-lhes que alguns dos que aqui se acham não experimentarão a morte antes de verem o Filho do homem vindo em seu Reino"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23702, 1, "Seis dias depois, Jesus tomou consigo Pedro, Tiago e João, irmão de Tiago, e os levou, em particular, a um alto monte.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23703, 2, "Ali ele foi transfigurado diante deles. Sua face brilhou como o sol, e suas roupas se tornaram brancas como a luz.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23704, 3, "Naquele mesmo momento apareceram diante deles Moisés e Elias, conversando com Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23705, 4, "Então Pedro disse a Jesus: ""Senhor, é bom estarmos aqui. Se quiseres, farei três tendas: uma para ti, uma para Moisés e outra para Elias"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23706, 5, "Enquanto ele ainda estava falando, uma nuvem resplandecente os envolveu, e dela saiu uma voz, que dizia: ""Este é o meu Filho amado em quem me agrado. Ouçam-no! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23707, 6, "Ouvindo isso, os discípulos prostraram-se com o rosto em terra e ficaram aterrorizados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23708, 7, "Mas Jesus se aproximou, tocou neles e disse: ""Levantem-se! Não tenham medo! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23709, 8, "E erguendo eles os olhos, não viram mais ninguém a não ser Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23710, 9, "Enquanto desciam do monte, Jesus lhes ordenou: ""Não contem a ninguém o que vocês viram, até que o Filho do homem tenha sido ressuscitado dos mortos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23711, 10, "Os discípulos lhe perguntaram: ""Então, por que os mestres da lei dizem que é necessário que Elias venha primeiro? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23712, 11, "Jesus respondeu: ""De fato, Elias vem e restaurará todas as coisas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23713, 12, "Mas eu lhes digo: Elias já veio, e eles não o reconheceram, mas fizeram com ele tudo o que quiseram. Da mesma forma o Filho do homem será maltratado por eles"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23714, 13, "Então os discípulos entenderam que era de João Batista que ele tinha falado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23715, 14, "Quando chegaram onde estava a multidão, um homem aproximou-se de Jesus, ajoelhou-se diante dele e disse:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23716, 15, """Senhor, tem misericórdia do meu filho. Ele tem ataques e está sofrendo muito. Muitas vezes cai no fogo ou na água.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23717, 16, "Eu o trouxe aos teus discípulos, mas eles não puderam curá-lo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23718, 17, "Respondeu Jesus: ""Ó geração incrédula e perversa, até quando estarei com vocês? Até quando terei que suportá-los? Tragam-me o menino"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23719, 18, "Jesus repreendeu o demônio; este saiu do menino e, desde aquele momento, ele ficou curado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23720, 19, "Então os discípulos aproximaram-se de Jesus em particular e perguntaram: ""Por que não conseguimos expulsá-lo? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23721, 20, "Ele respondeu: ""Por que a fé que vocês têm é pequena. Eu lhes asseguro que se vocês tiverem fé do tamanho de um grão de mostarda, poderão dizer a este monte: ‘Vá daqui para lá’, e ele irá. Nada lhes será impossível.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23722, 21, "Mas esta espécie só sai pela oração e pelo jejum"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23723, 22, "Reunindo-se eles na Galiléia, Jesus lhes disse: ""O Filho do homem será entregue nas mãos dos homens.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23724, 23, "Eles o matarão, e no terceiro dia ele ressuscitará"". E os discípulos ficaram cheios de tristeza.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23725, 24, "Quando Jesus e seus discípulos chegaram a Cafarnaum, os coletores do imposto de duas dracmas vieram a Pedro e perguntaram: ""O mestre de vocês não paga o imposto do templo? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23726, 25, """Sim, paga"", respondeu ele. Quando Pedro entrou na casa, Jesus foi o primeiro a falar, perguntando-lhe: ""O que você acha, Simão? De quem os reis da terra cobram tributos e impostos: de seus próprios filhos ou dos outros? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23727, 26, """Dos outros"", respondeu Pedro. Disse-lhe Jesus: ""Então os filhos estão isentos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23728, 27, "Mas, para não escandalizá-los, vá ao mar e jogue o anzol. Tire o primeiro peixe que você pegar, abra-lhe a boca, e você encontrará uma moeda de quatro dracmas. Pegue-a e entregue-a a eles, para pagar o meu imposto e o seu"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23729, 1, "Naquele momento os discípulos chegaram a Jesus e perguntaram: ""Quem é o maior no Reino dos céus? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23730, 2, "Chamando uma criança, colocou-a no meio deles,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23731, 3, "e disse: ""Eu lhes asseguro que, a não ser que vocês se convertam e se tornem como crianças, jamais entrarão no Reino dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23732, 4, "Portanto, quem se faz humilde como esta criança, este é o maior no Reino dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23733, 5, """Quem recebe uma destas crianças em meu nome, está me recebendo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23734, 6, "Mas se alguém fizer tropeçar um destes pequeninos que crêem em mim, melhor lhe seria amarrar uma pedra de moinho no pescoço e se afogar nas profundezas do mar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23735, 7, """Ai do mundo, por causa das coisas que fazem tropeçar! É inevitável que tais coisas aconteçam, mas ai daquele por meio de quem elas acontecem!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23736, 8, "Se a sua mão ou o seu pé o fizerem tropeçar, corte-os e jogue-os fora. É melhor entrar na vida mutilado ou aleijado do que, tendo as duas mãos ou os dois pés, ser lançado no fogo eterno.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23737, 9, "E se o seu olho o fizer tropeçar, arranque-o e jogue-o fora. É melhor entrar na vida com um só olho do que, tendo os dois olhos, ser lançado no fogo do inferno"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23738, 10, """Cuidado para não desprezarem um só destes pequeninos! Pois eu lhes digo que os anjos deles nos céus estão sempre vendo a face de meu Pai celeste.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23739, 11, "O Filho do homem veio para salvar o que se havia perdido.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23740, 12, """O que acham vocês? Se alguém possui cem ovelhas, e uma delas se perde, não deixará as noventa e nove nos montes, indo procurar a que se perdeu?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23741, 13, "E se conseguir encontrá-la, garanto-lhes que ele ficará mais contente com aquela ovelha do que com as noventa e nove que não se perderam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23742, 14, "Da mesma forma, o Pai de vocês, que está nos céus, não quer que nenhum destes pequeninos se perca"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23743, 15, """Se o seu irmão pecar contra você, vá e, a sós com ele, mostre-lhe o erro. Se ele o ouvir, você ganhou seu irmão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23744, 16, "Mas se ele não o ouvir, leve consigo mais um ou dois outros, de modo que ‘qualquer acusação seja confirmada pelo depoimento de duas ou três testemunhas’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23745, 17, "Se ele se recusar a ouvi-los, conte à igreja; e se ele se recusar a ouvir também a igreja, trate-o como pagão ou publicano.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23746, 18, """Digo-lhes a verdade: Tudo o que vocês ligarem na terra terá sido ligado no céu, e tudo o que vocês desligarem na terra terá sido desligado no céu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23747, 19, """Também lhes digo que se dois de vocês concordarem na terra em qualquer assunto sobre o qual pedirem, isso lhes será feito por meu Pai que está nos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23748, 20, "Pois onde se reunirem dois ou três em meu nome, ali eu estou no meio deles"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23749, 21, "Então Pedro aproximou-se de Jesus e perguntou: ""Senhor, quantas vezes deverei perdoar a meu irmão quando ele pecar contra mim? Até sete vezes? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23750, 22, "Jesus respondeu: ""Eu lhe digo: não até sete, mas até setenta vezes sete.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23751, 23, """Por isso, o Reino dos céus é como um rei que desejava acertar contas com seus servos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23752, 24, "Quando começou o acerto, foi trazido à sua presença um que lhe devia uma enorme quantidade de prata.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23753, 25, "Como não tinha condições de pagar, o senhor ordenou que ele, sua mulher, seus filhos e tudo o que ele possuía fossem vendidos para pagar a dívida.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23754, 26, """O servo prostrou-se diante dele e lhe implorou: ‘Tem paciência comigo, e eu te pagarei tudo’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23755, 27, "O senhor daquele servo teve compaixão dele, cancelou a dívida e o deixou ir.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23756, 28, """Mas quando aquele servo saiu, encontrou um de seus conservos, que lhe devia cem denários. Agarrou-o e começou a sufocá-lo, dizendo: ‘Pague-me o que me deve! ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23757, 29, """Então o seu conservo caiu de joelhos e implorou-lhe: ‘Tenha paciência comigo, e eu lhe pagarei’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23758, 30, """Mas ele não quis. Antes, saiu e mandou lançá-lo na prisão, até que pagasse a dívida.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23759, 31, "Quando os outros servos, companheiros dele, viram o que havia acontecido, ficaram muito tristes e foram contar ao seu senhor tudo o que havia acontecido.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23760, 32, """Então o senhor chamou o servo e disse: ‘Servo mau, cancelei toda a sua dívida porque você me implorou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23761, 33, "Você não devia ter tido misericórdia do seu conservo como eu tive de você? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23762, 34, "Irado, seu senhor entregou-o aos torturadores, até que pagasse tudo o que devia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23763, 35, """Assim também lhes fará meu Pai celestial, se cada um de vocês não perdoar de coração a seu irmão"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23764, 1, "Tendo acabado de dizer essas coisas, Jesus saiu da Galiléia e foi para a região da Judéia, no outro lado do Jordão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23765, 2, "Grandes multidões o seguiam, e ele as curou ali.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23766, 3, "Alguns fariseus aproximaram-se dele para pô-lo à prova. E perguntaram-lhe: ""É permitido ao homem divorciar-se de sua mulher por qualquer motivo? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23767, 4, "Ele respondeu: ""Vocês não leram que, no princípio, o Criador ‘os fez homem e mulher’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23768, 5, "e disse: ‘Por essa razão, o homem deixará pai e mãe e se unirá à sua mulher, e os dois se tornarão uma só carne’?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23769, 6, "Assim, eles já não são dois, mas sim uma só carne. Portanto, o que Deus uniu, ninguém o separe"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23770, 7, "Perguntaram eles: ""Então, por que Moisés mandou dar uma certidão de divórcio à mulher e mandá-la embora? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23771, 8, "Jesus respondeu: ""Moisés lhes permitiu divorciar-se de suas mulheres por causa da dureza de coração de vocês. Mas não foi assim desde o princípio.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23772, 9, "Eu lhes digo que todo aquele que se divorciar de sua mulher, exceto por imoralidade sexual, e se casar com outra mulher, estará cometendo adultério"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23773, 10, "Os discípulos lhe disseram: ""Se esta é a situação entre o homem e sua mulher, é melhor não casar"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23774, 11, "Jesus respondeu: ""Nem todos têm condições de aceitar esta palavra; somente aqueles a quem isso é dado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23775, 12, "Alguns são eunucos porque nasceram assim; outros foram feitos assim pelos homens; outros ainda se fizeram eunucos por causa do Reino dos céus. Quem puder aceitar isso, aceite"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23776, 13, "Depois trouxeram crianças a Jesus, para que lhes impusesse as mãos e orasse por elas. Mas os discípulos os repreendiam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23777, 14, "Então disse Jesus: ""Deixem vir a mim as crianças e não as impeçam; pois o Reino dos céus pertence aos que são semelhantes a elas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23778, 15, "Depois de lhes impor as mãos, partiu dali.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23779, 16, "Eis que alguém se aproximou de Jesus e lhe perguntou: ""Mestre, que farei de bom para ter a vida eterna? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23780, 17, "Respondeu-lhe Jesus: ""Por que você me pergunta sobre o que é bom? Há somente um que é bom. Se você quer entrar na vida, obedeça aos mandamentos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23781, 18, """Quais? "", perguntou ele. Jesus respondeu: "" ‘Não matarás, não adulterarás, não furtarás, não darás falso testemunho,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23782, 19, "honra teu pai e tua mãe’ e ‘amarás o teu próximo como a ti mesmo’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23783, 20, "Disse-lhe o jovem: ""A tudo isso tenho obedecido. O que me falta ainda? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23784, 21, "Jesus respondeu: ""Se você quer ser perfeito, vá, venda os seus bens e dê o dinheiro aos pobres, e você terá um tesouro no céu. Depois, venha e siga-me"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23785, 22, "Ouvindo isso, o jovem afastou-se triste, porque tinha muitas riquezas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23786, 23, "Então Jesus disse aos discípulos: ""Digo-lhes a verdade: Dificilmente um rico entrará no Reino dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23787, 24, "E lhes digo ainda: é mais fácil passar um camelo pelo fundo de uma agulha do que um rico entrar no Reino de Deus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23788, 25, "Ao ouvirem isso, os discípulos ficaram perplexos e perguntaram: ""Neste caso, quem pode ser salvo? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23789, 26, "Jesus olhou para eles e respondeu: ""Para o homem é impossível, mas para Deus todas as coisas são possíveis"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23790, 27, "Então Pedro lhe respondeu: ""Nós deixamos tudo para seguir-te! Que será de nós? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23791, 28, "Jesus lhes disse: ""Digo-lhes a verdade: Por ocasião da regeneração de todas as coisas, quando o Filho do homem se assentar em seu trono glorioso, vocês que me seguiram também se assentarão em doze tronos, para julgar as doze tribos de Israel.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23792, 29, "E todos os que tiverem deixado casas, irmãos, irmãs, pai, mãe, filhos ou campos, por minha causa, receberão cem vezes mais e herdarão a vida eterna.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23793, 30, "Contudo, muitos primeiros serão últimos, e muitos últimos serão primeiros"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23794, 1, """Pois o Reino dos céus é como um proprietário que saiu de manhã cedo para contratar trabalhadores para a sua vinha.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23795, 2, "Ele combinou pagar-lhes um denário pelo dia e mandou-os para a sua vinha.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23796, 3, """Por volta das noves hora da manhã, ele saiu e viu outros que estavam desocupados na praça,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23797, 4, "e lhes disse: ‘Vão também trabalhar na vinha, e eu lhes pagarei o que for justo’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23798, 5, "E eles foram. ""Saindo outra vez, por volta do meio dia e das três horas da tarde e nona, fez a mesma coisa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23799, 6, "Saindo por volta da cinco horas da tarde, encontrou ainda outros que estavam desocupados e lhes perguntou: ‘Por que vocês estiveram aqui desocupados o dia todo? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23800, 7, "‘Porque ninguém nos contratou’, responderam eles. ""Ele lhes disse: ‘Vão vocês também trabalhar na vinha’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23801, 8, """Ao cair da tarde, o dono da vinha disse a seu administrador: ‘Chame os trabalhadores e pague-lhes o salário, começando com os últimos contratados e terminando nos primeiros’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23802, 9, """Vieram os trabalhadores contratados por volta das cinco horas da tarde, e cada um recebeu um denário.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23803, 10, "Quando vieram os que tinham sido contratados primeiro, esperavam receber mais. Mas cada um deles também recebeu um denário.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23804, 11, "Quando o receberam, começaram a se queixar do proprietário da vinha,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23805, 12, "dizendo-lhe: ‘Estes homens contratados por último trabalharam apenas uma hora, e o senhor os igualou a nós, que suportamos o peso do trabalho e o calor do dia’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23806, 13, """Mas ele respondeu a um deles: ‘Amigo, não estou sendo injusto com você. Você não concordou em trabalhar por um denário?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23807, 14, "Receba o que é seu e vá. Eu quero dar ao que foi contratado por último o mesmo que lhe dei.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23808, 15, "Não tenho o direito de fazer o que quero com o meu dinheiro? Ou você está com inveja porque sou generoso? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23809, 16, """Assim, os últimos serão primeiros, e os primeiros serão últimos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23810, 17, "Enquanto estava subindo para Jerusalém, Jesus chamou em particular os doze discípulos e lhes disse:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23811, 18, """Estamos subindo para Jerusalém, e o Filho do homem será entregue aos chefes dos sacerdotes e aos mestres da lei. Eles o condenarão à morte", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23812, 19, "e o entregarão aos gentios para que zombem dele, o açoitem e o crucifiquem. No terceiro dia ele ressuscitará! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23813, 20, "Então, aproximou-se de Jesus a mãe dos filhos de Zebedeu com seus filhos e, prostrando-se, fez-lhe um pedido.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23814, 21, """O que você quer? "", perguntou ele. Ela respondeu: ""Declara que no teu Reino estes meus dois filhos se assentarão um à tua direita e o outro à tua esquerda"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23815, 22, "Disse-lhes Jesus: ""Vocês não sabem o que estão pedindo. Podem vocês beber o cálice que eu vou beber? "" ""Podemos"", responderam eles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23816, 23, "Jesus lhes disse: ""Certamente vocês beberão do meu cálice; mas o assentar-se à minha direita ou à minha esquerda não cabe a mim conceder. Esses lugares pertencem àqueles para quem foram preparados por meu Pai"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23817, 24, "Quando os outros dez ouviram isso, ficaram indignados com os dois irmãos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23818, 25, "Jesus os chamou e disse: ""Vocês sabem que os governantes das nações as dominam, e as pessoas importantes exercem poder sobre elas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23819, 26, "Não será assim entre vocês. Pelo contrário, quem quiser tornar-se importante entre vocês deverá ser servo,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23820, 27, "e quem quiser ser o primeiro deverá ser escravo;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23821, 28, "como o Filho do homem, que não veio para ser servido, mas para servir e dar a sua vida em resgate por muitos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23822, 29, "Ao saírem de Jericó, uma grande multidão seguiu a Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23823, 30, "Dois cegos estavam sentados à beira do caminho e, quando ouviram falar que Jesus estava passando, puseram-se a gritar: ""Senhor, Filho de Davi, tem misericórdia de nós! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23824, 31, "A multidão os repreendeu para que ficassem quietos, mas eles gritavam ainda mais: ""Senhor, Filho de Davi, tem misericórdia de nós! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23825, 32, "Jesus, parando, chamou-os e perguntou-lhes: ""O que vocês querem que eu lhes faça? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23826, 33, "Responderam eles: ""Senhor, queremos que se abram os nossos olhos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23827, 34, "Jesus teve compaixão deles e tocou nos olhos deles. Imediatamente eles recuperaram a visão e o seguiram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23828, 1, "Quando se aproximaram de Jerusalém e chegaram a Betfagé, ao monte das Oliveiras, Jesus enviou dois discípulos,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23829, 2, "dizendo-lhes: ""Vão ao povoado que está adiante de vocês; logo encontrarão uma jumenta amarrada, com um jumentinho ao lado. Desamarrem-nos e tragam-nos para mim.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23830, 3, "Se alguém lhes perguntar algo, digam-lhe que o Senhor precisa deles e logo os enviará de volta"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23831, 4, "Isso aconteceu para que se cumprisse o que fora dito pelo profeta:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23832, 5, """Digam à cidade de Sião: ‘Eis que o seu rei vem a você, humilde e montado num jumento, num jumentinho, cria de jumenta’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23833, 6, "Os discípulos foram e fizeram o que Jesus tinha ordenado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23834, 7, "Trouxeram a jumenta e o jumentinho, colocaram sobre eles os seus mantos, e sobre estes Jesus montou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23835, 8, "Uma grande multidão estendeu seus mantos pelo caminho, outros cortavam ramos de árvores e os espalhavam pelo caminho.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23836, 9, "A multidão que ia adiante dele e os que o seguiam gritavam: ""Hosana ao Filho de Davi! "" ""Bendito é o que vem em nome do Senhor! "" ""Hosana nas alturas! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23837, 10, "Quando Jesus entrou em Jerusalém, toda a cidade ficou agitada e perguntava: ""Quem é este? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23838, 11, "A multidão respondia: ""Este é Jesus, o profeta de Nazaré da Galiléia"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23839, 12, "Jesus entrou no templo e expulsou todos os que ali estavam comprando e vendendo. Derrubou as mesas dos cambistas e as cadeiras dos que vendiam pombas,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23840, 13, "e lhes disse: ""Está escrito: ‘A minha casa será chamada casa de oração’; mas vocês estão fazendo dela um ‘covil de ladrões’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23841, 14, "Os cegos e os mancos aproximaram-se dele no templo, e ele os curou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23842, 15, "Mas quando os chefes dos sacerdotes e os mestres da lei viram as coisas maravilhosas que Jesus fazia e as crianças gritando no templo: ""Hosana ao Filho de Davi"", ficaram indignados,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23843, 16, "e lhe perguntaram: ""Não estás ouvindo o que estas crianças estão dizendo? "" Respondeu Jesus: ""Sim, vocês nunca leram: ‘dos lábios das crianças e dos recém-nascidos suscitaste louvor’? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23844, 17, "E, deixando-os, saiu da cidade para Betânia, onde passou a noite.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23845, 18, "De manhã cedo, quando voltava para a cidade, Jesus teve fome.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23846, 19, "Vendo uma figueira à beira do caminho, aproximou-se dela, mas nada encontrou, a não ser folhas. Então lhe disse: ""Nunca mais dê frutos! "" Imediatamente a árvore secou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23847, 20, "Ao verem isso, os discípulos ficaram espantados e perguntaram: ""Como a figueira secou tão depressa? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23848, 21, "Jesus respondeu: ""Eu lhes asseguro que, se vocês tiverem fé e não duvidarem, poderão fazer não somente o que foi feito à figueira, mas também dizer a este monte: ‘Levante-se e atire-se no mar’, e assim será feito.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23849, 22, "E tudo o que pedirem em oração, se crerem, vocês receberão"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23850, 23, "Jesus entrou no templo e, enquanto ensinava, aproximaram-se dele os chefes dos sacerdotes e os líderes religiosos do povo e perguntaram: ""Com que autoridade estás fazendo estas coisas? E quem te deu tal autoridade? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23851, 24, "Respondeu Jesus: ""Eu também lhes farei uma pergunta. Se vocês me responderem, eu lhes direi com que autoridade estou fazendo estas coisas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23852, 25, "De onde era o batismo de João? Do céu ou dos homens? "" Eles discutiam entre si, dizendo: ""Se dissermos: ‘do céu’, ele perguntará: ‘Então por que vocês não creram nele? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23853, 26, "Mas se dissermos: ‘dos homens’ — temos medo do povo, pois todos consideram João um profeta"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23854, 27, "Eles responderam a Jesus: ""Não sabemos"". E ele lhes disse: ""Tampouco lhes direi com que autoridade estou fazendo estas coisas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23855, 28, """O que acham? Havia um homem que tinha dois filhos. Chegando ao primeiro, disse: ‘Filho, vá trabalhar hoje na vinha’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23856, 29, """E este respondeu: ‘Não quero! ’ Mas depois mudou de idéia e foi.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23857, 30, """O pai chegou ao outro filho e disse a mesma coisa. Ele respondeu: ‘Sim, senhor! ’ Mas não foi.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23858, 31, """Qual dos dois fez a vontade do pai? "" ""O primeiro"", responderam eles. Jesus lhes disse: ""Digo-lhes a verdade: Os publicanos e as prostitutas estão entrando antes de vocês no Reino de Deus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23859, 32, "Porque João veio para lhes mostrar o caminho da justiça, e vocês não creram nele, mas os publicanos e as prostitutas creram. E, mesmo depois de verem isso, vocês não se arrependeram nem creram nele"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23860, 33, """Ouçam outra parábola: Havia um proprietário de terras que plantou uma vinha. Colocou uma cerca ao redor dela, cavou um tanque para prensar as uvas e construiu uma torre. Depois arrendou a vinha a alguns lavradores e foi fazer uma viagem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23861, 34, "Aproximando-se a época da colheita, enviou seus servos aos lavradores, para receber os frutos que lhe pertenciam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23862, 35, """Os lavradores agarraram seus servos; a um espancaram, a outro mataram e apedrejaram o terceiro.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23863, 36, "Então enviou-lhes outros servos em maior número, e os lavradores os trataram da mesma forma.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23864, 37, "Por último, enviou-lhes seu filho, dizendo: ‘A meu filho respeitarão’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23865, 38, """Mas quando os lavradores viram o filho, disseram uns aos outros: ‘Este é o herdeiro. Venham, vamos matá-lo e tomar a sua herança’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23866, 39, "Assim eles o agarraram, lançaram-no para fora da vinha e o mataram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23867, 40, """Portanto, quando vier o dono da vinha, o que fará àqueles lavradores? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23868, 41, "Responderam eles: ""Matará de modo horrível esses perversos e arrendará a vinha a outros lavradores, que lhe dêem a sua parte no tempo da colheita"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23869, 42, "Jesus lhes disse: ""Vocês nunca leram nas Escrituras? ‘A pedra que os construtores rejeitaram tornou-se a pedra angular; isso vem do Senhor, e é algo maravilhoso para nós’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23870, 43, """Portanto eu lhes digo que o Reino de Deus será tirado de vocês e será dado a um povo que dê os frutos do Reino.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23871, 44, "Aquele que cair sobre esta pedra será despedaçado, e aquele sobre quem ela cair será reduzido a pó"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23872, 45, "Quando os chefes dos sacerdotes e os fariseus ouviram as parábolas de Jesus, compreenderam que ele falava a respeito deles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23873, 46, "E procuravam um meio de prendê-lo; mas tinham medo das multidões, pois elas o consideravam profeta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23874, 1, "Jesus lhes falou novamente por parábolas, dizendo:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23875, 2, """O Reino dos céus é como um rei que preparou um banquete de casamento para seu filho.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23876, 3, "Enviou seus servos aos que tinham sido convidados para o banquete, dizendo-lhes que viessem; mas eles não quiseram vir.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23877, 4, """De novo enviou outros servos e disse: ‘Digam aos que foram convidados que preparei meu banquete: meus bois e meus novilhos gordos foram abatidos, e tudo está preparado. Venham para o banquete de casamento! ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23878, 5, """Mas eles não lhes deram atenção e saíram, um para o seu campo, outro para os seus negócios.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23879, 6, "Os restantes, agarrando os servos, maltrataram-nos e os mataram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23880, 7, "O rei ficou irado e, enviando o seu exército, destruiu aqueles assassinos e queimou a cidade deles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23881, 8, """Então disse a seus servos: ‘O banquete de casamento está pronto, mas os meus convidados não eram dignos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23882, 9, "Vão às esquinas e convidem para o banquete todos os que vocês encontrarem’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23883, 10, "Então os servos saíram para as ruas e reuniram todas as pessoas que puderam encontrar, gente boa e gente má, e a sala do banquete de casamento ficou cheia de convidados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23884, 11, """Mas quando o rei entrou para ver os convidados, notou ali um homem que não estava usando veste nupcial.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23885, 12, "E lhe perguntou: ‘Amigo, como você entrou aqui sem veste nupcial? ’ O homem emudeceu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23886, 13, """Então o rei disse aos que serviam: ‘Amarrem-lhe as mãos e os pés, e lancem-no para fora, nas trevas; ali haverá choro e ranger de dentes’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23887, 14, """Pois muitos são chamados, mas poucos são escolhidos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23888, 15, "Então os fariseus saíram e começaram a planejar um meio de enredá-lo em suas próprias palavras.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23889, 16, "Enviaram-lhe seus discípulos juntamente com os herodianos que lhe disseram: ""Mestre, sabemos que és íntegro e que ensinas o caminho de Deus conforme a verdade. Tu não te deixas influenciar por ninguém, porque não te prendes à aparência dos homens.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23890, 17, "Dize-nos, pois: Qual é a tua opinião? É certo pagar imposto a César ou não? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23891, 18, "Mas Jesus, percebendo a má intenção deles, perguntou: ""Hipócritas! Por que vocês estão me pondo à prova?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23892, 19, "Mostrem-me a moeda usada para pagar o imposto"". Eles lhe mostraram um denário,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23893, 20, "e ele lhes perguntou: ""De quem é esta imagem e esta inscrição? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23894, 21, """De César"", responderam eles. E ele lhes disse: ""Então, dêem a César o que é de César e a Deus o que é de Deus"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23895, 22, "Ao ouvirem isso, eles ficaram admirados; e, deixando-o, retiraram-se.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23896, 23, "Naquele mesmo dia, os saduceus, que dizem que não há ressurreição, aproximaram-se dele com a seguinte questão:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23897, 24, """Mestre, Moisés disse que se um homem morrer sem deixar filhos, seu irmão deverá casar-se com a viúva e dar-lhe descendência.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23898, 25, "Entre nós havia sete irmãos. O primeiro casou-se e morreu. Como não teve filhos, deixou a mulher para seu irmão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23899, 26, "A mesma coisa aconteceu com o segundo, com o terceiro, até o sétimo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23900, 27, "Finalmente, depois de todos, morreu a mulher.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23901, 28, "Pois bem, na ressurreição, de qual dos sete ela será esposa, visto que todos foram casados com ela? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23902, 29, "Jesus respondeu: ""Vocês estão enganados porque não conhecem as Escrituras nem o poder de Deus!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23903, 30, "Na ressurreição, as pessoas não se casam nem são dadas em casamento; mas são como os anjos no céu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23904, 31, "E quanto à ressurreição dos mortos, vocês não leram o que Deus lhes disse:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23905, 32, "‘Eu sou o Deus de Abraão, o Deus de Isaque e o Deus de Jacó’? Ele não é Deus de mortos, mas de vivos! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23906, 33, "Ouvindo isso, a multidão ficou admirada com o seu ensino.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23907, 34, "Ao ouvirem dizer que Jesus havia deixado os saduceus sem resposta, os fariseus se reuniram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23908, 35, "Um deles, perito na lei, o pôs à prova com esta pergunta:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23909, 36, """Mestre, qual é o maior mandamento da Lei? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23910, 37, "Respondeu Jesus: "" ‘Ame o Senhor, o seu Deus de todo o seu coração, de toda a sua alma e de todo o seu entendimento’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23911, 38, "Este é o primeiro e maior mandamento.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23912, 39, "E o segundo é semelhante a ele: ‘Ame o seu próximo como a si mesmo’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23913, 40, "Destes dois mandamentos dependem toda a Lei e os Profetas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23914, 41, "Estando os fariseus reunidos, Jesus lhes perguntou:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23915, 42, """O que vocês pensam a respeito do Cristo? De quem ele é filho? "" ""É filho de Davi"", responderam eles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23916, 43, "Ele lhes disse: ""Então, como é que Davi, falando pelo Espírito, o chama ‘Senhor’? Pois ele afirma:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23917, 44, "‘O Senhor disse ao meu Senhor: ""Senta-te à minha direita, até que eu ponha os teus inimigos debaixo de teus pés"" ’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23918, 45, "Se, pois, Davi o chama ‘Senhor’, como pode ser ele seu filho? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23919, 46, "Ninguém conseguia responder-lhe uma palavra; e daquele dia em diante, ninguém jamais se atreveu a lhe fazer perguntas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23920, 1, "Então, Jesus disse à multidão e aos seus discípulos:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23921, 2, """Os mestres da lei e os fariseus se assentam na cadeira de Moisés.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23922, 3, "Obedeçam-lhes e façam tudo o que eles lhes dizem. Mas não façam o que eles fazem, pois não praticam o que pregam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23923, 4, "Eles atam fardos pesados e os colocam sobre os ombros dos homens, mas eles mesmos não estão dispostos a levantar um só dedo para movê-los.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23924, 5, """Tudo o que fazem é para serem vistos pelos homens. Eles fazem seus filactérios bem largos e as franjas de suas vestes bem longas;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23925, 6, "gostam do lugar de honra nos banquetes e dos assentos mais importantes nas sinagogas,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23926, 7, "de serem saudados nas praças e de serem chamados ‘rabis’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23927, 8, """Mas vocês não devem ser chamados ‘rabis’; um só é o mestre de vocês, e todos vocês são irmãos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23928, 9, "A ninguém na terra chamem ‘pai’, porque vocês só têm um Pai, aquele que está nos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23929, 10, "Tampouco vocês devem ser chamados ‘chefes’, porquanto vocês têm um só Chefe, o Cristo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23930, 11, "O maior entre vocês deverá ser servo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23931, 12, "Pois todo aquele que a si mesmo se exaltar será humilhado, e todo aquele que a si mesmo se humilhar será exaltado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23932, 13, """Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês fecham o Reino dos céus diante dos homens! Vocês mesmos não entram, nem deixam entrar aqueles que gostariam de fazê-lo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23933, 14, """Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês devoram as casas das viúvas e, para disfarçar, fazem longas orações. Por isso serão castigados mais severamente.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23934, 15, """Ai de vocês, mestres da lei e fariseus, hipócritas, porque percorrem terra e mar para fazer um convertido e, quando conseguem, vocês o tornam duas vezes mais filho do inferno do que vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23935, 16, """Ai de vocês, guias cegos!, pois dizem: ‘Se alguém jurar pelo santuário, isto nada significa; mas se alguém jurar pelo ouro do santuário, está obrigado por seu juramento’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23936, 17, "Cegos insensatos! Que é mais importante: o ouro ou o santuário que santifica o ouro?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23937, 18, "Vocês também dizem: ‘Se alguém jurar pelo altar, isto nada significa; mas se alguém jurar pela oferta que está sobre ele, está obrigado por seu juramento’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23938, 19, "Cegos! Que é mais importante: a oferta, ou o altar que santifica a oferta?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23939, 20, "Portanto, aquele que jurar pelo altar, jura por ele e por tudo o que está sobre ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23940, 21, "E o que jurar pelo santuário, jura por ele e por aquele que nele habita.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23941, 22, "E aquele que jurar pelo céu, jura pelo trono de Deus e por aquele que nele se assenta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23942, 23, """Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês dão o dízimo da hortelã, do endro e do cominho, mas têm negligenciado os preceitos mais importantes da lei: a justiça, a misericórdia e a fidelidade. Vocês devem praticar estas coisas, sem omitir aquelas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23943, 24, "Guias cegos! Vocês coam um mosquito e engolem um camelo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23944, 25, """Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês limpam o exterior do copo e do prato, mas por dentro eles estão cheios de ganância e cobiça.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23945, 26, "Fariseu cego! Limpe primeiro o interior do copo e do prato, para que o exterior também fique limpo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23946, 27, """Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês são como sepulcros caiados: bonitos por fora, mas por dentro estão cheios de ossos e de todo tipo de imundície.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23947, 28, "Assim são vocês: por fora parecem justos ao povo, mas por dentro estão cheios de hipocrisia e maldade.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23948, 29, """Ai de vocês, mestres da lei e fariseus, hipócritas! Vocês edificam os túmulos dos profetas e adornam os monumentos dos justos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23949, 30, "E dizem: ‘Se tivéssemos vivido no tempo dos nossos antepassados, não teríamos tomado parte com eles no derramamento do sangue dos profetas’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23950, 31, "Assim, vocês testemunham contra si mesmos que são descendentes dos que assassinaram os profetas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23951, 32, "Acabem, pois, de encher a medida do pecado dos seus antepassados!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23952, 33, """Serpentes! Raça de víboras! Como vocês escaparão da condenação ao inferno?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23953, 34, "Por isso, eu lhes estou enviando profetas, sábios e mestres. A uns vocês matarão e crucificarão; a outros açoitarão nas sinagogas de vocês e perseguirão de cidade em cidade.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23954, 35, "E, assim, sobre vocês recairá todo o sangue justo derramado na terra, desde o sangue do justo Abel, até o sangue de Zacarias, filho de Baraquias, a quem vocês assassinaram entre o santuário e o altar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23955, 36, "Eu lhes asseguro que tudo isso sobrevirá a esta geração.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23956, 37, """Jerusalém, Jerusalém, você, que mata os profetas e apedreja os que lhe são enviados! Quantas vezes eu quis reunir os seus filhos, como a galinha reúne os seus pintinhos debaixo das suas asas, mas vocês não quiseram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23957, 38, "Eis que a casa de vocês ficará deserta.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23958, 39, "Pois eu lhes digo que vocês não me verão desde agora, até que digam: ‘Bendito é o que vem em nome do Senhor’"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23959, 1, "Jesus saiu do templo e, enquanto caminhava, seus discípulos aproximaram-se dele para lhe mostrar as construções do templo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23960, 2, """Vocês estão vendo tudo isto? "", perguntou ele. ""Eu lhes garanto que não ficará aqui pedra sobre pedra; serão todas derrubadas"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23961, 3, "Tendo Jesus se assentado no monte das Oliveiras, os discípulos dirigiram-se a ele em particular e disseram: ""Dize-nos, quando acontecerão essas coisas? E qual será o sinal da tua vinda e do fim dos tempos? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23962, 4, "Jesus respondeu: ""Cuidado, que ninguém os engane.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23963, 5, "Pois muitos virão em meu nome, dizendo: ‘Eu sou o Cristo! ’ e enganarão a muitos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23964, 6, "Vocês ouvirão falar de guerras e rumores de guerras, mas não tenham medo. É necessário que tais coisas aconteçam, mas ainda não é o fim.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23965, 7, "Nação se levantará contra nação, e reino contra reino. Haverá fomes e terremotos em vários lugares.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23966, 8, "Tudo isso será o início das dores.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23967, 9, """Então eles os entregarão para serem perseguidos e condenados à morte, e vocês serão odiados por todas as nações por minha causa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23968, 10, "Naquele tempo muitos ficarão escandalizados, trairão e odiarão uns aos outros,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23969, 11, "e numerosos falsos profetas surgirão e enganarão a muitos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23970, 12, "Devido ao aumento da maldade, o amor de muitos esfriará,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23971, 13, "mas aquele que perseverar até o fim será salvo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23972, 14, "E este evangelho do Reino será pregado em todo o mundo como testemunho a todas as nações, e então virá o fim.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23973, 15, """Assim, quando vocês virem ‘o sacrilégio terrível’, do qual falou o profeta Daniel, no lugar santo — quem lê, entenda —", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23974, 16, "então, os que estiverem na Judéia fujam para os montes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23975, 17, "Quem estiver no telhado de sua casa não desça para tirar dela coisa alguma.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23976, 18, "Quem estiver no campo não volte para pegar seu manto.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23977, 19, "Como serão terríveis aqueles dias para as grávidas e para as que estiverem amamentando!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23978, 20, "Orem para que a fuga de vocês não aconteça no inverno nem no sábado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23979, 21, "Porque haverá então grande tribulação, como nunca houve desde o princípio do mundo até agora, nem jamais haverá.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23980, 22, "Se aqueles dias não fossem abreviados, ninguém sobreviveria; mas, por causa dos eleitos, aqueles dias serão abreviados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23981, 23, "Se, então, alguém lhes disser: ‘Vejam, aqui está o Cristo! ’ ou: ‘Ali está ele! ’, não acreditem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23982, 24, "Pois aparecerão falsos cristos e falsos profetas que realizarão grandes sinais e maravilhas para, se possível, enganar até os eleitos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23983, 25, "Vejam que eu os avisei antecipadamente.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23984, 26, """Assim, se alguém lhes disser: ‘Ele está lá, no deserto! ’, não saiam; ou: ‘Ali está ele, dentro da casa! ’, não acreditem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23985, 27, "Porque assim como o relâmpago sai do Oriente e se mostra no Ocidente, assim será a vinda do Filho do homem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23986, 28, "Onde houver um cadáver, aí se ajuntarão os abutres.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23987, 29, """Imediatamente após a tribulação daqueles dias ‘o sol escurecerá, e a lua não dará a sua luz; as estrelas cairão do céu, e os poderes celestes serão abalados’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23988, 30, """Então aparecerá no céu o sinal do Filho do homem, e todas as nações da terra se lamentarão e verão o Filho do homem vindo nas nuvens do céu com poder e grande glória.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23989, 31, "E ele enviará os seus anjos com grande som de trombeta, e estes reunirão os seus eleitos dos quatro ventos, de uma a outra extremidade dos céus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23990, 32, """Aprendam a lição da figueira: quando seus ramos se renovam e suas folhas começam a brotar, vocês sabem que o verão está próximo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23991, 33, "Assim também, quando virem todas estas coisas, saibam que ele está próximo, às portas.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23992, 34, "Eu lhes asseguro que não passará esta geração até que todas essas coisas aconteçam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23993, 35, "O céu e a terra passarão, mas as minhas palavras jamais passarão"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23994, 36, """Quanto ao dia e à hora ninguém sabe, nem os anjos dos céus, nem o Filho, senão somente o Pai.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23995, 37, "Como foi nos dias de Noé, assim também será na vinda do Filho do homem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23996, 38, "Pois nos dias anteriores ao dilúvio, o povo vivia comendo e bebendo, casando-se e dando-se em casamento, até o dia em que Noé entrou na arca;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23997, 39, "e eles nada perceberam, até que veio o dilúvio e os levou a todos. Assim acontecerá na vinda do Filho do homem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23998, 40, "Dois homens estarão no campo: um será levado e o outro deixado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (23999, 41, "Duas mulheres estarão trabalhando num moinho: uma será levada e a outra deixada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24000, 42, """Portanto, vigiem, porque vocês não sabem em que dia virá o seu Senhor.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24001, 43, "Mas entendam isto: se o dono da casa soubesse a que hora da noite o ladrão viria, ele ficaria de guarda e não deixaria que a sua casa fosse arrombada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24002, 44, "Assim, também vocês precisam estar preparados, porque o Filho do homem virá numa hora em que vocês menos esperam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24003, 45, """Quem é, pois, o servo fiel e sensato, a quem seu senhor encarrega dos de sua casa para lhes dar alimento no tempo devido?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24004, 46, "Feliz o servo a quem seu senhor encontrar fazendo assim quando voltar.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24005, 47, "Garanto-lhes que ele o encarregará de todos os seus bens.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24006, 48, "Mas suponham que esse servo seja mau e diga a si mesmo: ‘Meu senhor se demora’,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24007, 49, "e então comece a bater em seus conservos e a comer e a beber com os beberrões.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24008, 50, "O senhor daquele servo virá num dia em que ele não o espera e numa hora que não sabe.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24009, 51, "Ele o punirá severamente e lhe dará lugar com os hipócritas, onde haverá choro e ranger de dentes"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24010, 1, """O Reino dos céus, pois, será semelhante a dez virgens que pegaram suas candeias e saíram para encontrar-se com o noivo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24011, 2, "Cinco delas eram insensatas, e cinco eram prudentes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24012, 3, "As insensatas pegaram suas candeias, mas não levaram óleo consigo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24013, 4, "As prudentes, porém, levaram óleo em vasilhas juntamente com suas candeias.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24014, 5, "O noivo demorou a chegar, e todas ficaram com sono e adormeceram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24015, 6, """À meia-noite, ouviu-se um grito: ‘O noivo se aproxima! Saiam para encontrá-lo! ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24016, 7, """Então todas as virgens acordaram e prepararam suas candeias.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24017, 8, "As insensatas disseram às prudentes: ‘Dêem-nos um pouco do seu óleo, pois as nossas candeias estão se apagando’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24018, 9, """Elas responderam: ‘Não, pois pode ser que não haja o suficiente para nós e para vocês. Vão comprar óleo para vocês’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24019, 10, """E saindo elas para comprar o óleo, chegou o noivo. As virgens que estavam preparadas entraram com ele para o banquete nupcial. E a porta foi fechada.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24020, 11, """Mais tarde vieram também as outras e disseram: ‘Senhor! Senhor! Abra a porta para nós! ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24021, 12, """Mas ele respondeu: ‘A verdade é que não as conheço! ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24022, 13, """Portanto, vigiem, porque vocês não sabem o dia nem a hora! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24023, 14, """E também será como um homem que, ao sair de viagem, chamou seus servos e confiou-lhes os seus bens.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24024, 15, "A um deu cinco talentos, a outro dois, e a outro um; a cada um de acordo com a sua capacidade. Em seguida partiu de viagem.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24025, 16, "O que havia recebido cinco talentos saiu imediatamente, aplicou-os, e ganhou mais cinco.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24026, 17, "Também o que tinha dois talentos ganhou mais dois.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24027, 18, "Mas o que tinha recebido um talento saiu, cavou um buraco no chão e escondeu o dinheiro do seu senhor.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24028, 19, """Depois de muito tempo o senhor daqueles servos voltou e acertou contas com eles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24029, 20, "O que tinha recebido cinco talentos trouxe os outros cinco e disse: ‘O senhor me confiou cinco talentos; veja, eu ganhei mais cinco’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24030, 21, """O senhor respondeu: ‘Muito bem, servo bom e fiel! Você foi fiel no pouco; eu o porei sobre o muito. Venha e participe da alegria do seu senhor! ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24031, 22, """Veio também o que tinha recebido dois talentos e disse: ‘O senhor me confiou dois talentos; veja, eu ganhei mais dois’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24032, 23, """O senhor respondeu: ‘Muito bem, servo bom e fiel! Você foi fiel no pouco; eu o porei sobre o muito. Venha e participe da alegria do seu senhor! ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24033, 24, """Por fim veio o que tinha recebido um talento e disse: ‘Eu sabia que o senhor é um homem severo, que colhe onde não plantou e junta onde não semeou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24034, 25, "Por isso, tive medo, saí e escondi o seu talento no chão. Veja, aqui está o que lhe pertence’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24035, 26, """O senhor respondeu: ‘Servo mau e negligente! Você sabia que eu colho onde não plantei e junto onde não semeei?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24036, 27, "Então você devia ter confiado o meu dinheiro aos banqueiros, para que, quando eu voltasse, o recebesse de volta com juros.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24037, 28, """ ‘Tirem o talento dele e entreguem-no ao que tem dez.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24038, 29, "Pois a quem tem, mais será dado, e terá em grande quantidade. Mas a quem não tem, até o que tem lhe será tirado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24039, 30, "E lancem fora o servo inútil, nas trevas, onde haverá choro e ranger de dentes’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24040, 31, """Quando o Filho do homem vier em sua glória, com todos os anjos, assentar-se-á em seu trono na glória celestial.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24041, 32, "Todas as nações serão reunidas diante dele, e ele separará umas das outras como o pastor separa as ovelhas dos bodes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24042, 33, "E colocará as ovelhas à sua direita e os bodes à sua esquerda.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24043, 34, """Então o Rei dirá aos que estiverem à sua direita: ‘Venham, benditos de meu Pai! Recebam como herança o Reino que lhes foi preparado desde a criação do mundo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24044, 35, "Pois eu tive fome, e vocês me deram de comer; tive sede, e vocês me deram de beber; fui estrangeiro, e vocês me acolheram;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24045, 36, "necessitei de roupas, e vocês me vestiram; estive enfermo, e vocês cuidaram de mim; estive preso, e vocês me visitaram’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24046, 37, """Então os justos lhe responderão: ‘Senhor, quando te vimos com fome e te demos de comer, ou com sede e te demos de beber?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24047, 38, "Quando te vimos como estrangeiro e te acolhemos, ou necessitado de roupas e te vestimos?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24048, 39, "Quando te vimos enfermo ou preso e fomos te visitar? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24049, 40, """O Rei responderá: ‘Digo-lhes a verdade: o que vocês fizeram a algum dos meus menores irmãos, a mim o fizeram’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24050, 41, """Então ele dirá aos que estiverem à sua esquerda: ‘Malditos, apartem-se de mim para o fogo eterno, preparado para o diabo e os seus anjos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24051, 42, "Pois eu tive fome, e vocês não me deram de comer; tive sede, e nada me deram para beber;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24052, 43, "fui estrangeiro, e vocês não me acolheram; necessitei de roupas, e vocês não me vestiram; estive enfermo e preso, e vocês não me visitaram’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24053, 44, """Eles também responderão: ‘Senhor, quando te vimos com fome ou com sede ou estrangeiro ou necessitado de roupas ou enfermo ou preso, e não te ajudamos? ’", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24054, 45, """Ele responderá: ‘Digo-lhes a verdade: o que vocês deixaram de fazer a alguns destes mais pequeninos, também a mim deixaram de fazê-lo’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24055, 46, """E estes irão para o castigo eterno, mas os justos para a vida eterna"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24056, 1, "Tendo dito essas coisas, disse Jesus aos seus discípulos:", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24057, 2, """Como vocês sabem, estamos a dois dias da Páscoa, e o Filho do homem será entregue para ser crucificado"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24058, 3, "Naquela ocasião os chefes dos sacerdotes e os líderes religioso do povo se reuniram no palácio do sumo sacerdote, cujo nome era Caifás,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24059, 4, "e juntos planejaram prender Jesus à traição e matá-lo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24060, 5, "Mas diziam: ""Não durante a festa, para que não haja tumulto entre o povo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24061, 6, "Estando Jesus em Betânia, na casa de Simão, o leproso,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24062, 7, "aproximou-se dele uma mulher com um frasco de alabastro contendo um perfume muito caro. Ela o derramou sobre a cabeça de Jesus, quando ele se encontrava reclinado à mesa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24063, 8, "Os discípulos, ao verem isso, ficaram indignados e perguntaram: ""Por que este desperdício?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24064, 9, "Este perfume poderia ser vendido por alto preço, e o dinheiro dado aos pobres"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24065, 10, "Percebendo isso, Jesus lhes disse: ""Por que vocês estão perturbando essa mulher? Ela praticou uma boa ação para comigo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24066, 11, "Pois os pobres vocês sempre terão consigo, mas a mim vocês nem sempre terão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24067, 12, "Quando derramou este perfume sobre o meu corpo, ela o fez a fim de me preparar para o sepultamento.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24068, 13, "Eu lhes asseguro que onde quer que este evangelho for anunciado, em todo o mundo, também o que ela fez será contado, em sua memória"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24069, 14, "Então, um dos Doze, chamado Judas Iscariotes, dirigiu-se aos chefes dos sacerdotes", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24070, 15, "e lhes perguntou: ""O que me darão se eu o entregar a vocês? "" E eles lhe fixaram o preço: trinta moedas de prata.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24071, 16, "Desse momento em diante Judas passou a procurar uma oportunidade para entregá-lo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24072, 17, "No primeiro dia da festa dos pães sem fermento, os discípulos dirigiram-se a Jesus e lhe perguntaram: ""Onde queres que preparemos a refeição da Páscoa? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24073, 18, "Ele respondeu dizendo que entrassem na cidade, procurassem um certo homem e lhe dissessem: ""O Mestre diz: ‘O meu tempo está próximo. Vou celebrar a Páscoa com meus discípulos em sua casa"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24074, 19, "Os discípulos fizeram como Jesus os havia instruído e prepararam a Páscoa.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24075, 20, "Ao anoitecer, Jesus estava reclinado à mesa com os Doze.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24076, 21, "E, enquanto estavam comendo, ele disse: ""Digo-lhes que certamente um de vocês me trairá"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24077, 22, "Eles ficaram muito tristes e começaram a dizer-lhe, um após outro: ""Com certeza não sou eu, Senhor! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24078, 23, "Afirmou Jesus: ""Aquele que comeu comigo do mesmo prato há de me trair.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24079, 24, "O Filho do homem vai, como está escrito a seu respeito. Mas ai daquele que trai o Filho do homem! Melhor lhe seria não haver nascido"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24080, 25, "Então, Judas, que haveria de traí-lo, disse: ""Com certeza não sou eu, Mestre! "" Jesus afirmou: ""Sim, é você"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24081, 26, "Enquanto comiam, Jesus tomou o pão, deu graças, partiu-o, e o deu aos seus discípulos, dizendo: ""Tomem e comam; isto é o meu corpo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24082, 27, "Em seguida tomou o cálice, deu graças e o ofereceu aos discípulos, dizendo: ""Bebam dele todos vocês.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24083, 28, "Isto é o meu sangue da aliança, que é derramado em favor de muitos, para perdão de pecados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24084, 29, "Eu lhes digo que, de agora em diante, não beberei deste fruto da videira até aquele dia em que beberei o vinho novo com vocês no Reino de meu Pai"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24085, 30, "Depois de terem cantado um hino, saíram para o monte das Oliveiras.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24086, 31, "Então Jesus lhes disse: ""Ainda esta noite todos vocês me abandonarão. Pois está escrito: ‘Ferirei o pastor, e as ovelhas do rebanho serão dispersas’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24087, 32, "Mas, depois de ressuscitar, irei adiante de vocês para a Galiléia"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24088, 33, "Pedro respondeu: ""Ainda que todos te abandonem, eu nunca te abandonarei! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24089, 34, "Respondeu Jesus: ""Asseguro-lhe que ainda esta noite, antes que o galo cante, três vezes você me negará"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24090, 35, "Mas Pedro declarou: ""Mesmo que seja preciso que eu morra contigo, nunca te negarei"". E todos os outros discípulos disseram o mesmo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24091, 36, "Então Jesus foi com seus discípulos para um lugar chamado Getsêmani e disse-lhes: ""Sentem-se aqui enquanto vou ali orar"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24092, 37, "Levando consigo Pedro e os dois filhos de Zebedeu, começou a entristecer-se e a angustiar-se.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24093, 38, "Disse-lhes então: ""A minha alma está profundamente triste, numa tristeza mortal. Fiquem aqui e vigiem comigo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24094, 39, "Indo um pouco mais adiante, prostrou-se com o rosto em terra e orou: ""Meu Pai, se for possível, afasta de mim este cálice; contudo, não seja como eu quero, mas sim como tu queres"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24095, 40, "Então, voltou aos seus discípulos e os encontrou dormindo. ""Vocês não puderam vigiar comigo nem por uma hora? "", perguntou ele a Pedro.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24096, 41, """Vigiem e orem para que não caiam em tentação. O espírito está pronto, mas a carne é fraca"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24097, 42, "E retirou-se outra vez para orar: ""Meu Pai, se não for possível afastar de mim este cálice sem que eu o beba, faça-se a tua vontade"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24098, 43, "Quando voltou, de novo os encontrou dormindo, porque seus olhos estavam pesados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24099, 44, "Então os deixou novamente e orou pela terceira vez, dizendo as mesmas palavras.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24100, 45, "Depois voltou aos discípulos e lhes disse: ""Vocês ainda dormem e descansam? Chegou a hora! Eis que o Filho do homem está sendo entregue nas mãos de pecadores.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24101, 46, "Levantem-se e vamos! Aí vem aquele que me trai! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24102, 47, "Enquanto ele ainda falava, chegou Judas, um dos Doze. Com ele estava uma grande multidão armada de espadas e varas, enviada pelos chefes dos sacerdotes e líderes religiosos do povo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24103, 48, "O traidor havia combinado um sinal com eles, dizendo-lhes: ""Aquele a quem eu saudar com um beijo, é ele; prendam-no"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24104, 49, "Dirigindo-se imediatamente a Jesus, Judas disse: ""Salve, Mestre! "", e o beijou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24105, 50, "Jesus perguntou: ""Amigo, que é que o traz? "" Então os homens se aproximaram, agarraram Jesus e o prenderam.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24106, 51, "Um dos que estavam com Jesus, estendendo a mão, puxou a espada e feriu o servo do sumo sacerdote, decepando-lhe a orelha.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24107, 52, "Disse-lhe Jesus: ""Guarde a espada! Pois todos os que empunham a espada, pela espada morrerão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24108, 53, "Você acha que eu não posso pedir a meu Pai, e ele não colocaria imediatamente à minha disposição mais de doze legiões de anjos?", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24109, 54, "Como então se cumpririam as Escrituras que dizem que as coisas deveriam acontecer desta forma? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24110, 55, "Naquela hora Jesus disse à multidão: ""Estou eu chefiando alguma rebelião, para que vocês venham prender-me com espadas e varas? Todos os dias eu estava ensinando no templo, e vocês não me prenderam!", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24111, 56, "Mas tudo isso aconteceu para que se cumprissem as Escrituras dos profetas"". Então todos os discípulos o abandonaram e fugiram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24112, 57, "Os que prenderam Jesus o levaram a Caifás, o sumo sacerdote, em cuja casa se haviam reunido os mestres da lei e os líderes religiosos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24113, 58, "Mas Pedro o seguiu de longe até o pátio do sumo sacerdote, entrou e sentou-se com os guardas, para ver o que aconteceria.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24114, 59, "Os chefes dos sacerdotes e todo o Sinédrio estavam procurando um depoimento falso contra Jesus, para que pudessem condená-lo à morte.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24115, 60, "Mas nada encontraram, embora se apresentassem muitas falsas testemunhas. Finalmente se apresentaram duas", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24116, 61, "que declararam: ""Este homem disse: ‘Sou capaz de destruir o santuário de Deus e reconstruí-lo em três dias’ "".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24117, 62, "Então o sumo sacerdote levantou-se e disse a Jesus: ""Você não vai responder à acusação que estes lhe fazem? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24118, 63, "Mas Jesus permaneceu em silêncio. O sumo sacerdote lhe disse: ""Exijo que você jure pelo Deus vivo: se você é o Cristo, o Filho de Deus, diga-nos"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24119, 64, """Tu mesmo o disseste"", respondeu Jesus. ""Mas eu digo a todos vós: chegará o dia em que vereis o Filho do homem assentado à direita do Poderoso e vindo sobre as nuvens do céu"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24120, 65, "Foi quando o sumo sacerdote rasgou as próprias vestes e disse: ""Blasfemou! Por que precisamos de mais testemunhas? Vocês acabaram de ouvir a blasfêmia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24121, 66, "Que acham? "" ""É réu de morte! "", responderam eles.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24122, 67, "Então alguns lhe cuspiram no rosto e lhe deram murros. Outros lhe davam tapas", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24123, 68, "e diziam: ""Profetize-nos, Cristo. Quem foi que lhe bateu? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24124, 69, "Pedro estava sentado no pátio, e uma criada, aproximando-se dele, disse: ""Você também estava com Jesus, o galileu"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24125, 70, "Mas ele o negou diante de todos, dizendo: ""Não sei do que você está falando"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24126, 71, "Depois, saiu em direção à porta, onde outra criada o viu e disse aos que estavam ali: ""Este homem estava com Jesus, o Nazareno"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24127, 72, "E ele, jurando, o negou outra vez: ""Não conheço esse homem! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24128, 73, "Pouco tempo depois, os que estavam por ali chegaram a Pedro e disseram: ""Certamente você é um deles! O seu modo de falar o denuncia"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24129, 74, "Aí ele começou a se amaldiçoar e a jurar: ""Não conheço esse homem! "" Imediatamente um galo cantou.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24130, 75, "Então Pedro se lembrou da palavra que Jesus tinha dito: ""Antes que o galo cante, você me negará três vezes"". E, saindo dali, chorou amargamente.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24131, 1, "De manhã cedo, todos os chefes dos sacerdotes e líderes religiosos do povo tomaram a decisão de condenar Jesus à morte.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24132, 2, "E, amarrando-o, levaram-no e o entregaram a Pilatos, o governador.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24133, 3, "Quando Judas, que o havia traído, viu que Jesus fora condenado, foi tomado de remorso e devolveu aos chefes dos sacerdotes e aos líderes religiosos as trinta moedas de prata.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24134, 4, "E disse: ""Pequei, pois traí sangue inocente"". E eles retrucaram: ""Que nos importa? A responsabilidade é sua"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24135, 5, "Então Judas jogou o dinheiro dentro do templo, saindo, foi e enforcou-se.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24136, 6, "Os chefes dos sacerdotes ajuntaram as moedas e disseram: ""É contra a lei colocar este dinheiro no tesouro, visto que é preço de sangue"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24137, 7, "Então decidiram usar aquele dinheiro para comprar o campo do Oleiro, para cemitério de estrangeiros.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24138, 8, "Por isso ele se chama campo de Sangue até o dia de hoje.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24139, 9, "Então se cumpriu o que fora dito pelo profeta Jeremias: ""Tomaram as trinta moedas de prata, preço em que foi avaliado pelo povo de Israel,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24140, 10, "e as usaram para comprar o campo do Oleiro, como o Senhor me ordenou"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24141, 11, "Jesus foi posto diante do governador, e este lhe perguntou: ""Você é o rei dos judeus? "" Respondeu-lhe Jesus: ""Tu o dizes"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24142, 12, "Acusado pelos chefes dos sacerdotes e pelos líderes religiosos, ele nada respondeu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24143, 13, "Então Pilatos lhe perguntou: ""Você não ouve a acusação que eles estão fazendo contra você? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24144, 14, "Mas Jesus não lhe respondeu nenhuma palavra, de modo que o governador ficou muito impressionado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24145, 15, "Por ocasião da festa era costume do governador soltar um prisioneiro escolhido pela multidão.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24146, 16, "Eles tinham, naquela ocasião, um prisioneiro muito conhecido, chamado Barrabás.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24147, 17, "Pilatos perguntou à multidão que ali se havia reunido: ""Qual destes vocês querem que lhes solte: Barrabás ou Jesus, chamado Cristo? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24148, 18, "Porque sabia que o haviam entregado por inveja.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24149, 19, "Estando Pilatos sentado no tribunal, sua mulher lhe enviou esta mensagem: ""Não se envolva com este inocente, porque hoje, em sonho, sofri muito por causa dele"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24150, 20, "Mas os chefes dos sacerdotes e os líderes religiosos convenceram a multidão a que pedisse Barrabás e mandasse executar a Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24151, 21, "Então perguntou o governador: ""Qual dos dois vocês querem que eu lhes solte? "" Responderam eles: ""Barrabás! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24152, 22, "Perguntou Pilatos: ""Que farei então com Jesus, chamado Cristo? "" Todos responderam: ""Crucifica-o! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24153, 23, """Por quê? Que crime ele cometeu? "", perguntou Pilatos. Mas eles gritavam ainda mais: ""Crucifica-o! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24154, 24, "Quando Pilatos percebeu que não estava obtendo nenhum resultado, mas, pelo contrário, estava se iniciando um tumulto, mandou trazer água, lavou as mãos diante da multidão e disse: ""Estou inocente do sangue deste homem; a responsabilidade é de vocês"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24155, 25, "Todo o povo respondeu: ""Que o sangue dele caia sobre nós e sobre nossos filhos! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24156, 26, "Então Pilatos soltou-lhes Barrabás, mandou açoitar Jesus e o entregou para ser crucificado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24157, 27, "Então, os soldados do governador levaram Jesus ao Pretório e reuniram toda a tropa ao seu redor.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24158, 28, "Tiraram-lhe as vestes e puseram nele um manto vermelho;", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24159, 29, "fizeram uma coroa de espinhos e a colocaram em sua cabeça. Puseram uma vara em sua mão direita e, ajoelhando-se diante dele, zombavam: ""Salve, rei dos judeus! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24160, 30, "Cuspiram nele e, tirando-lhe a vara, batiam-lhe com ela na cabeça.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24161, 31, "Depois de terem zombado dele, tiraram-lhe o manto e vestiram-lhe suas próprias roupas. Então o levaram para crucificá-lo.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24162, 32, "Ao saírem, encontraram um homem de Cirene, chamado Simão, e o forçaram a carregar a cruz.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24163, 33, "Chegaram a um lugar chamado Gólgota, que quer dizer Lugar da Caveira,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24164, 34, "e lhe deram para beber vinho misturado com fel; mas, depois de prová-lo, recusou-se a beber.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24165, 35, "Depois de o crucificarem, dividiram as roupas dele, tirando sortes.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24166, 36, "E, sentando-se, vigiavam-no ali.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24167, 37, "Por cima de sua cabeça colocaram por escrito a acusação feita contra ele: ESTE É JESUS, O REI DOS JUDEUS.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24168, 38, "Dois ladrões foram crucificados com ele, um à sua direita e outro à sua esquerda.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24169, 39, "Os que passavam lançavam-lhe insultos, balançando a cabeça", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24170, 40, "e dizendo: ""Você que destrói o templo e o reedifica em três dias, salve-se! Desça da cruz, se é Filho de Deus! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24171, 41, "Da mesma forma, os chefes dos sacerdotes, os mestres da lei e os líderes religiosos zombavam dele,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24172, 42, "dizendo: ""Salvou os outros, mas não é capaz de salvar a si mesmo! E é o rei de Israel! Desça agora da cruz, e creremos nele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24173, 43, "Ele confiou em Deus. Que Deus o salve agora, se dele tem compaixão, pois disse: ‘Sou o Filho de Deus! ’ """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24174, 44, "Igualmente o insultavam os ladrões que haviam sido crucificados com ele.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24175, 45, "E houve trevas sobre toda a terra, do meio dia às três horas da tarde.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24176, 46, "Por volta das três horas da tarde, Jesus bradou em alta voz: ""Eloí, Eloí, lamá sabactâni? "" que significa: ""Meu Deus! Meu Deus! Por que me abandonaste? """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24177, 47, "Quando alguns dos que estavam ali ouviram isso, disseram: ""Ele está chamando Elias"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24178, 48, "Imediatamente, um deles correu em busca de uma esponja, embebeu-a em vinagre, colocou-a na ponta de uma vara e deu-a a Jesus para beber.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24179, 49, "Mas os outros disseram: ""Deixem-no. Vejamos se Elias vem salvá-lo"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24180, 50, "Depois de ter bradado novamente em alta voz, Jesus entregou o espírito.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24181, 51, "Naquele momento, o véu do santuário rasgou-se em duas partes, de alto a baixo. A terra tremeu, e as rochas se partiram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24182, 52, "Os sepulcros se abriram, e os corpos de muitos santos que tinham morrido foram ressuscitados.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24183, 53, "E, saindo dos sepulcros, depois da ressurreição de Jesus, entraram na cidade santa e apareceram a muitos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24184, 54, "Quando o centurião e os que com ele vigiavam Jesus viram o terremoto e tudo o que havia acontecido, ficaram aterrorizados e exclamaram: ""Verdadeiramente este era o Filho de Deus! """, 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24185, 55, "Muitas mulheres estavam ali, observando de longe. Elas haviam seguido Jesus desde a Galiléia, para o servir.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24186, 56, "Entre elas estavam Maria Madalena; Maria, mãe de Tiago e de José; e a mãe dos filhos de Zebedeu.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24187, 57, "Ao cair da tarde chegou um homem rico, de Arimatéia, chamado José, que se tornara discípulo de Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24188, 58, "Dirigindo-se a Pilatos, pediu o corpo de Jesus, e Pilatos ordenou que lhe fosse entregue.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24189, 59, "José tomou o corpo, envolveu-o num limpo lençol de linho", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24190, 60, "e o colocou num sepulcro novo, que ele havia mandado cavar na rocha. E, fazendo rolar uma grande pedra sobre a entrada do sepulcro, retirou-se.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24191, 61, "Maria Madalena e a outra Maria estavam assentadas ali, em frente do sepulcro.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24192, 62, "No outro dia, que era o seguinte ao da Preparação, os chefes dos sacerdotes e os fariseus dirigiram-se a Pilatos", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24193, 63, "e disseram: ""Senhor, lembramos que, enquanto ainda estava vivo, aquele impostor disse: ‘Depois de três dias ressuscitarei’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24194, 64, "Ordena, pois, que o sepulcro dele seja guardado até o terceiro dia, para que não venham seus discípulos e, roubando o corpo, digam ao povo que ele ressuscitou dentre os mortos. Este último engano será pior do que o primeiro"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24195, 65, """Levem um destacamento"", respondeu Pilatos. ""Podem ir, e mantenham o sepulcro em segurança como acharem melhor"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24196, 66, "Eles foram e armaram um esquema de segurança no sepulcro; e além de deixarem um destacamento montando guarda, lacraram a pedra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24197, 1, "Depois do sábado, tendo começado o primeiro dia da semana, Maria Madalena e a outra Maria foram ver o sepulcro.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24198, 2, "E eis que sobreveio um grande terremoto, pois um anjo do Senhor desceu do céu e, chegando ao sepulcro, rolou a pedra da entrada e assentou-se sobre ela.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24199, 3, "Sua aparência era como um relâmpago, e suas vestes eram brancas como a neve.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24200, 4, "Os guardas tremeram de medo e ficaram como mortos.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24201, 5, "O anjo disse às mulheres: ""Não tenham medo! Sei que vocês estão procurando Jesus, que foi crucificado.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24202, 6, "Ele não está aqui; ressuscitou, como tinha dito. Venham ver o lugar onde ele jazia.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24203, 7, "Vão depressa e digam aos discípulos dele: ‘Ele ressuscitou dentre os mortos e está indo adiante de vocês para a Galiléia. Lá vocês o verão’. Notem que eu já os avisei"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24204, 8, "As mulheres saíram depressa do sepulcro, amedrontadas e cheias de alegria, e foram correndo anunciá-lo aos discípulos de Jesus.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24205, 9, "De repente, Jesus as encontrou e disse: ""Salve! "" Elas se aproximaram dele, abraçaram-lhe os pés e o adoraram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24206, 10, "Então Jesus lhes disse: ""Não tenham medo. Vão dizer a meus irmãos que se dirijam para a Galiléia; lá eles me verão"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24207, 11, "Enquanto as mulheres estavam a caminho, alguns dos guardas dirigiram-se à cidade e contaram aos chefes dos sacerdotes tudo o que havia acontecido.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24208, 12, "Quando os chefes dos sacerdotes se reuniram com os líderes religiosos, elaboraram um plano. Deram aos soldados grande soma de dinheiro,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24209, 13, "dizendo-lhes: ""Vocês devem declarar o seguinte: ‘Os discípulos dele vieram durante a noite e furtaram o corpo, enquanto estávamos dormindo’.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24210, 14, "Se isso chegar aos ouvidos do governador, nós lhe daremos explicações e livraremos vocês de qualquer problema"".", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24211, 15, "Assim, os soldados receberam o dinheiro e fizeram como tinham sido instruídos. E esta versão se divulgou entre os judeus até o dia de hoje.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24212, 16, "Os onze discípulos foram para a Galiléia, para o monte que Jesus lhes indicara.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24213, 17, "Quando o viram o adoraram; mas alguns duvidaram.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24214, 18, "Então, Jesus aproximou-se deles e disse: ""Foi-me dada toda a autoridade no céu e na terra.", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24215, 19, "Portanto, vão e façam discípulos de todas as nações, batizando-os em nome do Pai e do Filho e do Espírito Santo,", 39);
INSERT INTO verses (id, number, text, chapter_id) VALUES (24216, 20, "ensinando-os a obedecer a tudo o que eu lhes ordenei. E eu estarei sempre com vocês, até o fim dos tempos"".", 39);