INSERT INTO verses (id, number, text, chapter_id) VALUES (22686, 1, "Advertência contra Nínive. Livro da visão de Naum, de Elcós.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22687, 2, "O SENHOR é Deus zeloso e vingador! O SENHOR é vingador! Seu furor é terrível! O SENHOR executa vingança contra os seus adversários e manifesta o seu furor contra os seus inimigos.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22688, 3, "O SENHOR é muito paciente, mas o seu poder é imenso; o SENHOR não deixará impune o culpado. O seu caminho está no vendaval e na tempestade, e as nuvens são a poeira de seus pés.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22689, 4, "Ele repreende o mar e o faz secar, faz que todos os rios se sequem. Basã e o Carmelo se desvanecem e as flores do Líbano murcham.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22690, 5, "Quando ele se aproxima os montes tremem e as colinas se derretem. A terra se agita na sua presença, o mundo e todos os que nele vivem.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22691, 6, "Quem pode resistir à sua indignação? Quem pode suportar o despertar de sua ira? O seu furor se derrama como fogo, e as rochas se despedaçam diante dele.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22692, 7, "O SENHOR é bom, um refúgio em tempos de angústia. Ele protege os que nele confiam,", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22693, 8, "mas com uma enchente devastadora dará fim a Nínive; expulsará os seus inimigos para a escuridão.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22694, 9, "O SENHOR acabará com tudo o que vocês planejarem contra ele; a tribulação não precisará vir uma segunda vez.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22695, 10, "Embora estejam entrelaçados como espinhos e encharcados de bebida como bêbados, serão consumidos como a palha mais seca.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22696, 11, "Foi de você, ó Nínive, que saiu aquele que trama perversidades, que planeja o mal contra o SENHOR.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22697, 12, "Assim diz o SENHOR: “Apesar de serem fortes e numerosos, serão ceifados e destruídos; mas, você, Judá, embora eu a tenha afligido, não a afligirei mais.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22698, 13, "Agora vou quebrar o jugo do seu pescoço e arrancar as suas algemas”.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22699, 14, "O SENHOR decreta o seguinte a seu respeito, ó rei de Nínive: “Você não terá descendentes que perpetuem o seu nome. Destruirei as imagens esculpidas e os ídolos de metal do templo dos seus deuses. Prepararei o seu túmulo, porque você é desprezível”.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22700, 15, "Vejam sobre os montes os pés do que anuncia boas notícias e proclama a paz! Comemore as suas festas, ó Judá, e cumpra os seus votos. Nunca mais o perverso a invadirá; ele será completamente destruído.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22701, 1, "O destruidor avança contra você, Nínive! Guarde a fortaleza! Vigie a estrada! Prepare a resistência! Reúna todas as suas forças!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22702, 2, "O SENHOR restaurará o esplendor de Jacó; restaurará o esplendor de Israel, embora os saqueadores tenham devastado e destruído as suas videiras.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22703, 3, "Os escudos e os uniformes dos soldados inimigos são vermelhos. Os seus carros de guerra reluzem quando se alinham para a batalha; agitam-se as lanças de pinho.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22704, 4, "Os carros de guerra percorrem loucamente as ruas e se cruzam velozmente pelos quarteirões. Parecem tochas de fogo e arremessam-se como relâmpagos.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22705, 5, "Convocam-se as suas tropas de elite, mas elas vêm tropeçando; correm para a muralha da cidade para formar a linha de proteção.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22706, 6, "As comportas dos canais são abertas, e o palácio desaba.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22707, 7, "Está decretado: a cidade irá para o exílio, será deportada. As jovens tomadas como escravas batem no peito; seu gemer é como o arrulhar das pombas.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22708, 8, "Nínive é como um açude antigo cujas águas estão vazando. “Parem, parem”, eles gritam, mas ninguém sequer olha para trás.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22709, 9, "Saqueiem a prata! Saqueiem o ouro! Sua riqueza não tem fim; está repleta de objetos de valor!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22710, 10, "Ah! Devastação! Destruição! Desolação! Os corações se derretem, os joelhos vacilam, todos os corpos tremem e o rosto de todos empalidece!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22711, 11, "Onde está agora a toca dos leões? Onde o lugar em que alimentavam seus filhotes, para onde iam o leão, a leoa e os leõezinhos, sem nada temer?", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22712, 12, "Onde está o leão que caçava o bastante para os seus filhotes e estrangulava animais para as suas leoas, e que enchia as suas covas de presas e suas tocas de vítimas?", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22713, 13, "“Estou contra você”, declara o SENHOR dos Exércitos, “queimarei no fogo os seus carros de guerra, e a espada matará os seus leões. Eliminarei da terra a sua caça, e a voz dos seus mensageiros jamais será ouvida”.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22714, 1, "Ai da cidade sanguinária, repleta de fraudes e cheia de roubos, sempre fazendo as suas vítimas!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22715, 2, "Ah, o estalo dos chicotes, o barulho das rodas, o galope dos cavalos e o sacudir dos carros de guerra!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22716, 3, "Cavaleiros atacando, espadas reluzentes e lanças cintilantes! Muitos mortos, montanhas de cadáveres, corpos sem conta, gente tropeçando por cima deles!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22717, 4, "Tudo por causa do desejo desenfreado de uma prostituta sedutora, mestra de feitiçarias, que escravizou nações com a sua prostituição e povos, com a sua feitiçaria.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22718, 5, "“Eu estou contra você”, declara o SENHOR dos Exércitos, “vou levantar o seu vestido até a altura do seu rosto. Mostrarei às nações a sua nudez e aos reinos, as suas vergonhas.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22719, 6, "Eu jogarei imundície sobre você, e a tratarei com desprezo; farei de você um exemplo.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22720, 7, "Todos os que a virem fugirão dizendo: ‘Nínive está arrasada! Quem a lamentará? ’ Onde encontrarei quem a console? ”", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22721, 8, "Acaso és melhor do que Tebas, situada junto ao Nilo, rodeada de águas? O rio era a sua defesa; as águas, o seu muro.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22722, 9, "A Etiópia e o Egito eram a sua força ilimitada; Fute e a Líbia estavam entre os seus aliados.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22723, 10, "Apesar disso, ela foi deportada, levada para o exílio. Em cada esquina as suas crianças foram massacradas. Tiraram sortes para decidir o destino dos seus nobres; todos os poderosos foram acorrentados.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22724, 11, "Você também ficará embriagada; irá esconder-se, tentando proteger-se do inimigo.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22725, 12, "Todas as suas fortalezas são como figueiras carregadas de figos maduros; basta sacudi-las, e os figos caem em bocas vorazes.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22726, 13, "Olhe bem para as suas tropas: não passam de mulheres! As suas portas estão escancaradas para os seus inimigos; o fogo devorou as suas trancas.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22727, 14, "Reserve água para o tempo do cerco! Reforce as suas fortalezas! Entre no barro, pise a argamassa, prepare a forma para os tijolos!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22728, 15, "Mesmo assim o fogo consumirá você; a espada a eliminará, e, como gafanhotos devastadores, a devorará! Multiplique-se como gafanhotos devastadores, multiplique-se como gafanhotos peregrinos!", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22729, 16, "Você multiplicou os seus comerciantes, tornando-os mais numerosos do que as estrelas do céu; mas como gafanhotos devastadores, devoram o país e depois voam para longe.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22730, 17, "Os seus guardas são como gafanhotos peregrinos, os seus oficiais, como enxames de gafanhotos que se ajuntam sobre os muros em dias frios; mas quando o sol aparece, eles voam, ninguém sabe para onde.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22731, 18, "Ó rei da Assíria, os seus pastores dormem; os seus nobres adormecem. O seu povo está espalhado pelos montes e não há ninguém para reuni-lo.", 33);
INSERT INTO verses (id, number, text, chapter_id) VALUES (22732, 19, "Não há cura para a sua chaga; a sua ferida é mortal. Quem ouve notícias a seu respeito bate palmas pela sua queda, pois, quem não sofreu a sua crueldade sem limites?", 33);