INSERT INTO verses (id, number, text, chapter_id) VALUES (12442, 21, "de Ater, por meio de Ezequias, 98", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12443, 22, "de Hasum 328", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12444, 23, "de Besai 324", 15);
