INSERT INTO verses (id, number, text, chapter_id) VALUES (12446, 25, "de Gibeom 95", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12447, 26, "das cidades de Belém e de Netofate 188", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12448, 27, "de Anatote 128", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12449, 28, "de Bete-Azmavete 42", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12450, 29, "de Quiriate-Jearim, Cefira e Beerote 743", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12451, 30, "de Ramá e Geba 621", 15);
