INSERT INTO verses (id, number, text, chapter_id) VALUES (12453, 32, "de Betel e Ai 123", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12454, 33, "do outro Nebo 52", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12455, 34, "do outro Elão 1. 254", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12456, 35, "de Harim 320", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12457, 36, "de Jericó 345", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12458, 37, "de Lode, Hadide e Ono 721", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12459, 38, "de Senaá 3. 930.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12460, 39, "Os sacerdotes: os descendentes de Jedaías, por meio da família de Jesua, 973", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12461, 40, "de Imer 1. 052", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12462, 41, "de Pasur 1. 247", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12463, 42, "de Harim 1. 017.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12464, 43, "Os levitas: os descendentes de Jesua, por meio de Cadmiel, pela linhagem de Hodeva 74.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12465, 44, "Os cantores: os descendentes de Asafe 148.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12466, 45, "Os porteiros do templo: os descendentes de Salum, Ater, Talmom, Acube, Hatita e Sobai 138.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12467, 46, "Os servidores do templo: os descendentes de Zia, Hasufa, Tabaote,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12468, 47, "Queros, Sia, Padom,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12469, 48, "Lebana, Hagaba, Salmai,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12470, 49, "Hanã, Gidel, Gaar,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12471, 50, "Reaías, Rezim, Necoda,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12472, 51, "Gazão, Uzá, Paséia,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12473, 52, "Besai, Meunim, Nefusim,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12474, 53, "Baquebuque, Hacufa, Harur,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12475, 54, "Baslite, Meída, Harsa,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12476, 55, "Barcos, Sísera, Tamá,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12477, 56, "Nesias e Hatifa.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12478, 57, "Os descendentes dos servos de Salomão: os descendentes de Sotai, Soferete, Perida,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12479, 58, "Jaala, Darcom, Gidel,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12480, 59, "Sefatias, Hatil, Poquerete-Hazebaim e Amom.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12481, 60, "Os servos do templo e os descendentes dos servos de Salomão 392.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12482, 61, "Os que chegaram das cidades de Tel-Melá, Tel-Harsa, Querube, Adom e Imer, mas não puderam provar que suas famílias eram descendentes de Israel:", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12483, 62, "Os descendentes de Delaías, Tobias e Necoda 642.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12484, 63, "E dentre os sacerdotes: os descendentes de Habaías, Coz e Barzilai, homem que se casou com uma filha de Barzilai, de Gileade, e que era chamado por aquele nome.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12485, 64, "Esses procuraram seus registros de família, mas não conseguiram achá-los e, dessa forma, foram considerados impuros para o sacerdócio.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12486, 65, "Por isso o governador determinou que eles não comessem das ofertas santíssimas enquanto não houvesse um sacerdote para consultar o Urim e o Tumim.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12487, 66, "O total de todos os registrados foi 42. 360 homens,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12488, 67, "além de seus 7. 337 servos e servas; havia entre eles 245 cantores e cantoras.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12489, 68, "Possuíam 736 cavalos, 245 mulas,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12490, 69, "435 camelos e 6. 720 jumentos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12491, 70, "Alguns dos chefes das famílias contribuíram para o trabalho. O governador deu à tesouraria oito quilos de ouro, 50 bacias e 530 vestes para os sacerdotes.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12492, 71, "Alguns dos chefes das famílias deram à tesouraria, para a realização do trabalho, cento e sessenta quilos de ouro e uma tonelada e trezentos e vinte quilos de prata.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12493, 72, "O total dado pelo restante do povo foi de cento e sessenta quilos de ouro, uma tonelada e duzentos quilos de prata e 67 vestes para os sacerdotes.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12494, 73, "Os sacerdotes, os levitas, os porteiros, os cantores e os servidores do templo, e também alguns do povo e os demais israelitas, estabeleceram-se em suas próprias cidades. Quando chegou o sétimo mês e os israelitas tinham se instalado em suas cidades,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12495, 1, "todo o povo juntou-se como se fosse um só homem na praça, em frente da porta das Águas. Pediram ao escriba Esdras que trouxesse o Livro da Lei de Moisés, que o Senhor dera a Israel.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12496, 2, "Assim, no dia primeiro do sétimo mês, o sacerdote Esdras trouxe a Lei diante da assembléia, que era constituída de homens e mulheres e de todos os que podiam entender.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12497, 3, "Ele a leu em voz alta desde o raiar da manhã até o meio-dia, de frente para a praça, em frente da porta das Águas, na presença dos homens, mulheres e de outros que podiam entender. E todo o povo ouvia com atenção a leitura do Livro da Lei.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12498, 4, "O escriba Esdras estava numa plataforma elevada, de madeira, construída para a ocasião. Ao seu lado, à direita, estavam Matitias, Sema, Anaías, Urias, Hilquias e Maaséias; e à esquerda estavam Pedaías, Misael, Malquias, Hasum, Hasbadana, Zacarias e Mesulão.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12499, 5, "Esdras abriu o livro diante de todo o povo, e este podia vê-lo, pois ele estava num lugar mais alto. E, quando abriu o livro, o povo todo se levantou.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12500, 6, "Esdras louvou o Senhor, o grande Deus, e todo o povo ergueu as mãos e respondeu: ""Amém! Amém! "" Então eles adoraram o Senhor, prostrados, rosto em terra.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12501, 7, "Os levitas Jesua, Bani, Serebias, Jamim, Acube, Sabetai, Hodias, Maaséias, Quelita, Azarias, Jozabade, Hanã e Pelaías, instruíram o povo na Lei, e todos permaneciam ali.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12502, 8, "Leram o Livro da Lei de Deus, interpretando-o e explicando-o, a fim de que o povo entendesse o que estava sendo lido.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12503, 9, "Então Neemias, o governador, Esdras, o sacerdote e escriba, e os levitas que estavam instruindo o povo disseram a todos: ""Este dia é consagrado ao Senhor Deus. Nada de tristeza e de choro! "" Pois todo o povo estava chorando enquanto ouvia as palavras da Lei.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12504, 10, "E Neemias acrescentou: ""Podem sair, e comam e bebam do melhor que tiverem, e repartam com os que nada têm preparado. Este dia é consagrado ao nosso Senhor. Não se entristeçam, porque a alegria do Senhor os fortalecerá"".", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12505, 11, "Os levitas tranqüilizaram todo o povo, dizendo: ""Acalmem-se porque este é um dia santo. Não fiquem tristes! """, 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12506, 12, "Então todo o povo saiu para comer, beber, repartir com os que nada tinham preparado e para comemorar com grande alegria, pois agora compreendiam as palavras que lhes foram explicadas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12507, 13, "No segundo dia do mês, os chefes de todas as famílias, os sacerdotes e os levitas reuniram-se com o escriba Esdras para estudar as palavras da Lei.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12508, 14, "Descobriram que estava escrito na Lei, que o Senhor tinha ordenado por meio de Moisés, que os israelitas deviam morar em tendas durante a festa do sétimo mês.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12509, 15, "Por isso anunciaram em todas as suas cidades e em Jerusalém: ""Saiam às montanhas e tragam ramos de oliveiras cultivadas, de oliveiras silvestres, de murtas, de tamareiras e de árvores frondosas, para fazerem tendas"", conforme está escrito.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12510, 16, "Então o povo saiu e trouxe os ramos, e eles mesmos construíram tendas nos seus terraços, nos seus pátios, nos pátios do templo de Deus e na praça junto à porta das Águas e na que fica junto à porta de Efraim.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12511, 17, "Todos os que tinham voltado do exílio construíram tendas e moraram nelas. Desde os dias de Josué, filho de Num, até aquele dia, os israelitas não tinham celebrado a festa dessa maneira. E a alegria deles foi muito grande.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12512, 18, "Dia após dia, desde o primeiro até o último dia da festa, Esdras leu o Livro da Lei de Deus. Eles celebraram a festa durante sete dias, e no oitavo dia, conforme o ritual, houve uma reunião solene.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12513, 1, "No dia vinte e quatro do mês, os israelitas se reuniram, jejuaram, vestiram pano de saco e puseram terra sobre a cabeça.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12514, 2, "Os que eram de ascendência israelita tinham se separado de todos os estrangeiros. Levantaram-se nos seus lugares, confessaram seus pecados e a maldade dos seus antepassados.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12515, 3, "Ficaram onde estavam e leram o Livro da Lei do Senhor, do seu Deus, durante três horas, e passaram outras três horas confessando e adorando o Senhor, o seu Deus.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12516, 4, "De pé, na plataforma, estavam os levitas Jesua, Bani, Cadmiel, Sebanias, Buni, Serebias, Bani e Quenani, que em alta voz clamavam ao Senhor, ao seu Deus.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12517, 5, "E os levitas Jesua, Cadmiel, Bani, Hasabnéias, Serebias, Hodias, Sebanias e Petaías conclamavam o povo, dizendo: ""Levantem-se e louvem o Senhor, o seu Deus, que vive para todo o sempre. ""Bendito seja o teu nome glorioso! A tua grandeza está acima de toda expressão de louvor.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12518, 6, "Só tu és o Senhor. Fizeste os céus, e os mais altos céus, e tudo que neles há, a terra e tudo o que nela existe, os mares e tudo o que neles existe. Tu deste vida a todos os seres, e os exércitos dos céus te adoram.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12519, 7, """Tu és o Senhor, o Deus que escolheu Abrão, trouxe-o de Ur dos caldeus e deu-lhe o nome de Abraão.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12520, 8, "Viste que o coração dele era fiel, e fizeste com ele uma aliança, prometendo dar aos seus descendentes a terra dos cananeus, dos hititas, dos amorreus, dos ferezeus, dos jebuseus e dos girgaseus. E cumpriste a tua promessa porque tu és justo.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12521, 9, """Viste o sofrimento dos nossos antepassados no Egito, e ouviste o clamor deles no mar Vermelho.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12522, 10, "Fizeste sinais e maravilhas contra o faraó e todos os seus oficiais e contra todo o povo da sua terra, pois sabias com quanta arrogância os egípcios os tratavam. Alcançaste renome, que permanece até hoje.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12523, 11, "Dividiste o mar diante deles, para que o atravessassem a seco, mas lançaste os seus perseguidores nas profundezas, como uma pedra em águas agitadas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12524, 12, "Tu os conduziste de dia com uma nuvem e de noite com uma coluna de fogo, para iluminar o caminho que tinham que percorrer.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12525, 13, """Tu desceste ao monte Sinai; dos céus lhes falaste. Deste-lhes ordenanças justas e leis verdadeiras, e decretos e mandamentos excelentes.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12526, 14, "Fizeste que conhecessem o teu sábado santo e lhes deste ordens, decretos e leis por meio de Moisés, teu servo.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12527, 15, "Na fome deste-lhes pão do céu, e na sede tiraste para eles água da rocha; mandaste-os entrar e tomar posse da terra que, sob juramento, tinhas prometido dar-lhes.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12528, 16, """Mas os nossos antepassados tornaram-se arrogantes e obstinados, e não obedeceram aos teus mandamentos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12529, 17, "Eles se recusaram a ouvir-te e esqueceram-se dos milagres que realizaste entre eles. Tornaram-se obstinados e, na sua rebeldia, escolheram um líder a fim de voltarem à sua escravidão. Mas tu és um Deus perdoador, um Deus bondoso e misericordioso, muito paciente e cheio de amor. Por isso não os abandonaste,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12530, 18, "mesmo quando fundiram para si um ídolo na forma de bezerro e disseram: ‘Este é o seu deus, que os tirou do Egito’, ou quando proferiram blasfêmias terríveis.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12531, 19, """Foi por tua grande compaixão que não os abandonaste no deserto. De dia a nuvem não deixava de guiá-los em seu caminho, nem de noite a coluna de fogo deixava de brilhar sobre o caminho que deviam percorrer.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12532, 20, "Deste o teu bom Espírito para instruí-los. Não retiveste o teu maná que os alimentava, e deste-lhes água para matar a sede.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12533, 21, "Durante quarenta anos tu os sustentaste no deserto; nada lhes faltou, as roupas deles não se gastaram nem os seus pés ficaram inchados.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12534, 22, """Deste-lhes reinos e nações, cuja terra repartiste entre eles. Eles conquistaram a terra de Seom, rei de Hesbom, e a terra de Ogue, rei de Basã.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12535, 23, "Tornaste os seus filhos tão numerosos como as estrelas do céu, e os trouxeste para entrar e possuir a terra que prometeste aos seus antepassados.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12536, 24, "Seus filhos entraram e tomaram posse da terra. Tu subjugaste diante deles os cananeus, que viviam na terra, e os entregaste nas suas mãos, com os seus reis e com os povos daquela terra, para que os tratassem como bem quisessem.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12537, 25, "Conquistaram cidades fortificadas e terra fértil; apossaram-se de casas cheias de bens, poços já escavados, vinhas, olivais e muitas árvores frutíferas. Comeram até fartar-se e foram bem alimentados; eles desfrutaram de tua grande bondade.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12538, 26, """Mas foram desobedientes e se rebelaram contra ti; deram as costas para a tua Lei. Mataram os teus profetas, que os tinham advertido que se voltassem para ti; e te fizeram ofensas detestáveis.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12539, 27, "Por isso tu os entregaste nas mãos de seus inimigos, que os oprimiram. Mas, quando foram oprimidos, clamaram a ti. Dos céus tu os ouviste, e na tua grande compaixão deste-lhes libertadores, que os livraram das mãos de seus inimigos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12540, 28, """Mas, tão logo voltavam a ter paz, de novo faziam o que tu reprovas. Então os abandonavas às mãos de seus inimigos para que dominassem sobre eles. E, quando novamente clamavam a ti, dos céus tu os ouvias e na tua compaixão os livravas vez após vez.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12541, 29, """Tu os advertiste que voltassem à tua Lei, mas eles se tornaram arrogantes e desobedeceram aos teus mandamentos. Pecaram contra as tuas ordenanças, pelas quais o homem vive se lhes obedece. Com teimosia te deram as costas, tornaram-se obstinados e recusaram-se a ouvir-te.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12542, 30, "E durante muitos anos foste paciente com eles. Mediante o teu Espírito os advertiste por meio de teus profetas. Contudo, não te deram atenção, de modo que os entregaste nas mãos dos povos vizinhos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12543, 31, "Graças, porém, à tua grande misericórdia, não os destruíste nem os abandonaste, pois és Deus bondoso e misericordioso.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12544, 32, """Agora, portanto, nosso Deus, ó Deus grande, poderoso e temível, fiel à tua aliança e misericordioso, não fiques indiferente a toda a aflição que veio sobre nós, sobre os nossos reis e os nossos líderes, sobre os nossos sacerdotes e profetas, sobre os nossos antepassados e sobre todo o teu povo, desde os dias dos reis da Assíria até hoje.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12545, 33, "Em tudo o que nos aconteceu foste justo; agiste com lealdade mesmo quando fomos infiéis.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12546, 34, "Nossos reis, nossos líderes, nossos sacerdotes e nossos antepassados não seguiram a tua Lei; não deram atenção aos teus mandamentos nem às advertências que lhes fizeste.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12547, 35, "Mesmo quando estavam no reino deles, desfrutando da tua grande bondade na terra espaçosa e fértil que lhes deste, eles não te serviram nem abandonaram os seus maus caminhos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12548, 36, """Vê, porém, que hoje somos escravos, escravos na terra que deste aos nossos antepassados para que usufruíssem dos seus frutos e das outras boas coisas que ela produz.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12549, 37, "Por causa de nossos pecados, a sua grande produção pertence aos reis que puseste sobre nós. Eles dominam sobre nós e sobre os nossos rebanhos como bem lhes parece. É grande a nossa angústia!", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12550, 38, """Em vista disso tudo, estamos fazendo um acordo, por escrito, e assinado por nossos líderes, nossos levitas e nossos sacerdotes"".", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12551, 1, "Esta é a relação dos que o assinaram: Neemias, o governador, filho de Hacalias, e Zedequias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12552, 2, "Seraías, Azarias, Jeremias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12553, 3, "Pasur, Amarias, Malquias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12554, 4, "Hatus, Sebanias, Maluque,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12555, 5, "Harim, Meremote, Obadias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12556, 6, "Daniel, Ginetom, Baruque,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12557, 7, "Mesulão, Abias, Miamim,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12558, 8, "Maazias, Bilgai e Semaías. Esses eram sacerdotes.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12559, 9, "Dos levitas: Jesua, filho de Azanias, Binui, dos filhos de Henadade, Cadmiel", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12560, 10, "e seus colegas: Sebanias, Hodias, Quelita, Pelaías, Hanã,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12561, 11, "Mica, Reobe, Hasabias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12562, 12, "Zacur, Serebias, Sebanias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12563, 13, "Hodias, Bani e Beninu.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12564, 14, "Dos líderes do povo: Parós, Paate-Moabe, Elão, Zatu, Bani,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12565, 15, "Buni, Azgade, Bebai,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12566, 16, "Adonias, Bigvai, Adim,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12567, 17, "Ater, Ezequias, Azur,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12568, 18, "Hodias, Hasum, Besai,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12569, 19, "Harife, Anatote, Nebai,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12570, 20, "Magpias, Mesulão, Hezir,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12571, 21, "Mesezabeel, Zadoque, Jadua,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12572, 22, "Pelatias, Hanã, Anaías,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12573, 23, "Oséias, Hananias, Hassube,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12574, 24, "Haloés, Pílea, Sobeque,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12575, 25, "Reum, Hasabna, Maaséias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12576, 26, "Aías, Hanã, Anã,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12577, 27, "Maluque, Harim e Baaná.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12578, 28, """O restante do povo: sacerdotes, levitas, porteiros, cantores, servidores do templo e todos os que se separaram dos povos vizinhos por amor à Lei de Deus, juntamente com suas mulheres e com todos os seus filhos e filhas que eram capazes de entender,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12579, 29, "agora se unem aos seus irmãos, os nobres, e se obrigam sob maldição e sob juramento a seguir a Lei de Deus dada por meio do servo de Deus Moisés e a obedecer fielmente a todos os mandamentos, ordenanças e decretos do Senhor, o nosso Senhor.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12580, 30, """Prometemos não dar nossas filhas em casamento aos povos vizinhos nem aceitar que as filhas deles se casem com os nossos filhos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12581, 31, """Quando os povos vizinhos trouxerem mercadorias ou cereal para venderem em dia de sábado ou de festa, não compraremos deles nesses dias. Cada sete anos abriremos mão de trabalhar a terra e cancelaremos todas as dívidas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12582, 32, """Assumimos a responsabilidade de, conforme o mandamento, dar anualmente quatro gramas para o serviço do templo de nosso Deus:", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12583, 33, "para os pães consagrados, para as ofertas regulares de cereal e para os holocaustos, para as ofertas dos sábados, das festas de lua nova e para as festas fixas, para as ofertas sagradas, para as ofertas pelo pecado para fazer propiciação por Israel, e para as necessidades do templo de nosso Deus.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12584, 34, """Também tiramos sorte entre as famílias dos sacerdotes, dos levitas e do povo, para escalar anualmente a que deverá trazer lenha ao templo de nosso Deus, no tempo determinado, para queimar sobre o altar do Senhor, o nosso Deus, conforme está escrito na Lei.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12585, 35, """Também assumimos a responsabilidade de trazer anualmente ao templo do Senhor os primeiros frutos de nossas colheitas e de toda árvore frutífera.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12586, 36, """Conforme também está escrito na Lei, traremos o primeiro de nossos filhos e a primeira cria de nossos rebanhos, tanto de ovelhas como de bois, para o templo de nosso Deus, para os sacerdotes que ali estiverem ministrando.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12587, 37, """Além do mais, traremos para os depósitos do templo de nosso Deus, para os sacerdotes, a nossa primeira massa de cereal moído, e as nossas primeiras ofertas de cereal, do fruto de todas as nossas árvores e de nosso vinho e azeite. E traremos o dízimo das nossas colheitas para os levitas, pois são eles que recolhem os dízimos em todas as cidades onde trabalhamos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12588, 38, "Um sacerdote descendente de Arão deverá acompanhar os levitas quando receberem os dízimos, e os levitas terão que trazer um décimo dos dízimos ao templo de nosso Deus, aos depósitos do templo.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12589, 39, "O povo de Israel, inclusive os levitas, deverão trazer ofertas de cereal, de vinho novo e de azeite aos depósitos onde se guardam os utensílios para o santuário. É ali que os sacerdotes ministram e que os porteiros e os cantores ficam. ""Não negligenciaremos o templo de nosso Deus"".", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12590, 1, "Os líderes do povo passaram a morar em Jerusalém, e o restante do povo fez um sorteio para que, de cada dez pessoas, uma viesse morar em Jerusalém, a santa cidade; as outras nove deveriam ficar em suas próprias cidades.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12591, 2, "O povo abençoou todos os homens que se apresentaram voluntariamente para morar em Jerusalém.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12592, 3, "Alguns israelitas, sacerdotes, levitas, servos do templo e descendentes dos servos de Salomão viviam nas cidades de Judá, cada um em sua propriedade. Estes são os líderes da província que passaram a morar em Jerusalém;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12593, 4, "além deles veio gente tanto de Judá quanto de Benjamim viver em Jerusalém. Dentre os descendentes de Judá: Ataías, filho de Uzias, neto de Zacarias, bisneto de Amarias; Amarias era filho de Sefatias e neto de Maalaleel, descendente de Perez.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12594, 5, "Maaséias, filho de Baruque, neto de Col-Hozé, bisneto de Hazaías; Hazaías era filho de Adaías, neto de Joiaribe e bisneto de Zacarias, descendente de Selá.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12595, 6, "Os descendentes de Perez que viviam em Jerusalém totalizavam 468 homens de destaque.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12596, 7, "Dentre os descendentes de Benjamim: Salu, filho de Mesulão, neto de Joede, bisneto de Pedaías; Pedaías era filho de Colaías, neto de Maaséias, bisneto de Itiel, tataraneto de Jesaías;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12597, 8, "os seguidores de Salu, Gabai e Salai totalizavam 928 homens.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12598, 9, "Joel, filho de Zicri, era o oficial superior entre eles, e Judá, filho de Senua, era responsável pelo segundo distrito da cidade.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12599, 10, "Dentre os sacerdotes: Jedaías; o filho de Joiaribe; Jaquim;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12600, 11, "Seraías, filho de Hilquias, neto de Mesulão, bisneto de Zadoque; Zadoque era filho de Meraiote, neto de Aitube, supervisor na casa de Deus,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12601, 12, "e seus colegas, que faziam o trabalho do templo totalizavam 822 homens. Adaías, filho de Jeroão, neto de Pelaías, bisneto de Anzi; Anzi que era filho de Zacarias, neto de Pasur, bisneto de Malquias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12602, 13, "e seus colegas, que eram chefes de famílias, totalizavam 242 homens. Amassai, filho de Azareel, neto de Azai, bisneto de Mesilemote, tataraneto de Imer,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12603, 14, "e os seus colegas, que eram homens de destaque, totalizavam 128. O oficial superior deles era Zabdiel, filho de Gedolim.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12604, 15, "Dentre os levitas: Semaías, filho de Hassube, neto de Azricão, bisneto de Hasabias, tataraneto de Buni;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12605, 16, "Sabetai e Jozabade, dois dos líderes dos levitas, encarregados do trabalho externo do templo de Deus;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12606, 17, "Matanias, filho de Mica, neto de Zabdi, bisneto de Asafe, o dirigente que conduzia as ações de graça e as orações; Bacbuquias, o segundo entre os seus colegas; e Abda, filho de Samua, neto de Galal, bisneto de Jedutum.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12607, 18, "Os levitas totalizavam 284 na cidade santa.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12608, 19, "Os porteiros: Acube, Talmom e os homens dos seus clãs; eram 172 homens que guardavam as portas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12609, 20, "Os demais israelitas, incluindo os sacerdotes e os levitas, estavam em todas as cidades de Judá, cada um na propriedade de sua herança.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12610, 21, "Os que prestavam serviço no templo moravam na colina de Ofel, e Zia e Gispa estavam encarregados deles.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12611, 22, "O oficial superior dos levitas em Jerusalém era Uzi, filho de Bani, neto de Hasabias, bisneto de Matanias, tataraneto de Mica. Uzi era um dos descendentes de Asafe, que eram responsáveis pela música do templo de Deus.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12612, 23, "Eles estavam sujeitos às prescrições do rei, que regulamentavam suas atividades diárias.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12613, 24, "Petaías, filho de Mesezabeel, descendente de Zerá, filho de Judá, representavam o rei nas questões de ordem civil.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12614, 25, "Alguns do povo de Judá foram morar em Quiriate-Arba e em seus povoados ao redor, em Dibom e seus povoados, em Jecabzeel e seus povoados,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12615, 26, "em Jesua, em Moladá, em Bete-Pelete,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12616, 27, "em Hazar-Sual, em Berseba e seus povoados,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12617, 28, "em Ziclague, em Meconá e seus povoados,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12618, 29, "em En-Rimom, em Zorá, em Jarmute,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12619, 30, "em Zanoa, em Adulão e em seus povoados, em Láquis e seus arredores, e em Azeca e seus povoados. Eles se estabeleceram desde Berseba até o vale de Hinom.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12620, 31, "Os descendentes dos benjamitas foram viver em Geba, Micmás, Aia, Betel e seus povoados,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12621, 32, "em Anatote, Nobe e Ananias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12622, 33, "Hazor, Ramá e Gitaim,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12623, 34, "Hadide, Zeboim e Nebalate,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12624, 35, "Lode e Ono, e no vale dos Artesãos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12625, 36, "Alguns grupos dos levitas de Judá se estabeleceram em Benjamim.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12626, 1, "Estes foram os sacerdotes e os levitas que voltaram com Zorobabel, filho de Sealtiel, e com Jesua: Seraías, Jeremias, Esdras,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12627, 2, "Amarias, Maluque, Hatus,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12628, 3, "Secanias, Reum, Meremote,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12629, 4, "Ido, Ginetom, Abias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12630, 5, "Miamim, Maadias, Bilga,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12631, 6, "Semaías, Joiaribe, Jedaías,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12632, 7, "Salu, Amoque, Hilquias e Jedaías. Esses foram os chefes dos sacerdotes e seus colegas nos dias de Jesua.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12633, 8, "Os levitas foram Jesua, Binui, Cadmiel, Serebias, Judá, e também Matanias, que, junto com seus colegas, estava encarregado dos cânticos de ações de graças.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12634, 9, "Bacbuquias e Uni, colegas deles, ficavam em frente deles para responder-lhes.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12635, 10, "Jesua foi o pai de Joiaquim, Joiaquim foi o pai de Eliasibe, Eliasibe foi o pai de Joiada,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12636, 11, "Joiada foi o pai de Jônatas, Jônatas foi o pai de Jadua.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12637, 12, "Nos dias de Joiaquim estes foram os líderes das famílias dos sacerdotes: da família de Seraías, Meraías; da família de Jeremias, Hananias;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12638, 13, "da família de Esdras, Mesulão; da família de Amarias, Joanã;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12639, 14, "da família de Maluqui, Jônatas; da família de Secanias, José;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12640, 15, "da família de Harim, Adna; da família de Meremote, Helcai;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12641, 16, "da família de Ido, Zacarias; da família de Ginetom, Mesulão;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12642, 17, "da família de Abias, Zicri; da família de Miniamim e de Maadias, Piltai;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12643, 18, "da família de Bilga, Samua; da família de Semaías, Jônatas;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12644, 19, "da família de Joiaribe, Matenai; da família de Jedaías, Uzi;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12645, 20, "da família de Salai, Calai; da família de Amoque, Héber;", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12646, 21, "da família de Hilquias, Hasabias; da família de Jedaías, Natanael.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12647, 22, "Os chefes das famílias dos levitas e dos sacerdotes, nos dias de Eliasibe, Joiada, Joanã e Jadua, foram registrados no reinado de Dario, o persa.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12648, 23, "Os chefes das famílias dos descendentes de Levi até à época de Joanã, filho de Eliasibe, foram registrados no livro das crônicas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12649, 24, "Os líderes dos levitas foram Hasabias, Serebias, Jesua, filho de Cadmiel, e seus colegas, que ficavam em frente deles quando entoavam louvores e ações de graças; um grupo respondia ao outro, conforme prescrito por Davi, homem de Deus.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12650, 25, "Matanias, Bacbuquias, Obadias, Mesulão, Talmom e Acube eram porteiros; vigiavam os depósitos junto às portas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12651, 26, "Eles serviram nos dias de Joiaquim, filho de Jesua, neto de Jozadaque, e nos dias do governador Neemias e de Esdras, sacerdote e escriba.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12652, 27, "Por ocasião da dedicação dos muros de Jerusalém, os levitas foram procurados e trazidos de onde moravam para Jerusalém para celebrarem a dedicação alegremente, com cânticos e ações de graças, ao som de címbalos, harpas e liras.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12653, 28, "Os cantores foram trazidos dos arredores de Jerusalém, dos povoados dos netofatitas,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12654, 29, "de Bete-Gilgal, e da região de Geba e Azmavete, pois os cantores haviam construído povoados para si ao redor de Jerusalém.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12655, 30, "Os sacerdotes e os levitas se purificaram cerimonialmente, e depois purificaram também o povo, as portas e os muros.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12656, 31, "Ordenei aos líderes de Judá que subissem ao alto do muro. Também designei dois grandes coros para darem graças. Um deles avançou em cima do muro, para a direita, até à porta do Esterco.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12657, 32, "Hosaías e metade dos líderes de Judá os seguiram,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12658, 33, "e mais Azarias, Esdras, Mesulão,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12659, 34, "Judá, Benjamim, Semaías, Jeremias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12660, 35, "bem como alguns sacerdotes com trombetas, além de Zacarias, filho de Jônatas, neto de Semaías, bisneto de Matanias, que era filho de Micaías, neto de Zacur, bisneto de Asafe,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12661, 36, "e seus colegas, Semaías, Azareel, Milalai, Gilalai, Maai, Natanael, Judá e Hanani, que tocavam os instrumentos musicais prescritos por Davi, homem de Deus. Esdras, o escriba, ia na frente deles.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12662, 37, "À porta da Fonte eles subiram diretamente os degraus da cidade de Davi, na subida para o muro, e passaram sobre a casa de Davi até à porta das Águas, a leste.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12663, 38, "O segundo coro avançou no sentido oposto. Eu os acompanhei, quando iam sobre o muro, levando comigo a metade do povo; passamos pela torre dos Fornos até à porta Larga,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12664, 39, "sobre a porta de Efraim, a porta Jesaná, a porta do Peixe, a torre de Hananeel e a torre dos Cem, indo até à porta das Ovelhas. Junto à porta da Guarda eles pararam.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12665, 40, "Então, os dois coros encarregados das ações de graças, assumiram os seus lugares no templo de Deus, o que também fiz eu acompanhado da metade dos oficiais,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12666, 41, "e também dos sacerdotes Eliaquim, Maaséias, Miniamim, Micaías, Elioenai, Zacarias e Hananias, com suas trombetas", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12667, 42, "e também de Maaséias, Semaías, Eleazar, Uzi, Joanã, Malquias, Elão e Ézer. Os coros cantaram sob a direção de Jezraías.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12668, 43, "E naquele dia contentes ofereceram grandes sacrifícios, pois Deus lhes enchera de grande alegria. As mulheres e as crianças também se alegravam, e os sons da alegria de Jerusalém podiam ser ouvidos de longe.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12669, 44, "Naquela ocasião foram designados alguns encarregados dos depósitos onde se recebiam as contribuições gerais, os primeiros frutos e os dízimos. Das lavouras que havia em torno das cidades eles deviam trazer para os depósitos as porções exigidas pela Lei para os sacerdotes e os levitas. E, de fato, o povo de Judá estava satisfeito com os sacerdotes e os levitas que ministravam no templo.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12670, 45, "Eles celebravam o culto ao seu Deus e o ritual de purificação, dos quais também participavam os cantores e os porteiros, de acordo com as ordens de Davi e do seu filho Salomão.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12671, 46, "Pois muito tempo antes, nos dias de Davi e de Asafe, havia dirigentes dos cantores e que dirigiam os cânticos de louvor e de graças a Deus.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12672, 47, "Sucedeu, pois, que nos dias de Zorobabel e de Neemias, todo o Israel contribuía com ofertas diárias para os cantores e para os porteiros, além de separar a parte dos outros levitas, e os levitas separavam a porção dos descendentes de Arão.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12673, 1, "Naquele dia o livro de Moisés foi lido em voz alta diante do povo, e ali achou-se escrito que nenhum amonita ou moabita jamais poderia ser admitido no povo de Deus,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12674, 2, "pois eles, em vez de dar água e comida aos israelitas, tinham contratado Balaão para invocar uma maldição sobre eles. O nosso Deus, porém, transformou a maldição em bênção.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12675, 3, "Quando o povo ouviu essa Lei, excluiu de Israel todos que eram de ascendência estrangeira.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12676, 4, "Antes disso o sacerdote Eliasibe tinha sido encarregado dos depósitos do templo de nosso Deus. Ele era parente próximo de Tobias,", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12677, 5, "e lhe havia cedido uma grande sala, anteriormente utilizada para guardar as ofertas de cereal, o incenso, os utensílios do templo, e também os dízimos do trigo, do vinho novo e do azeite prescritos para os levitas, para os cantores e para os porteiros, e as ofertas para os sacerdotes.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12678, 6, "Mas, enquanto tudo isso estava acontecendo, eu não estava em Jerusalém, pois no trigésimo segundo ano do reinado de Artaxerxes, rei da Babilônia, voltei ao rei. Algum tempo depois pedi sua permissão", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12679, 7, "e voltei para Jerusalém. Aqui soube do mal que Eliasibe fizera ao ceder uma sala para Tobias nos pátios do templo de Deus.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12680, 8, "Fiquei muito aborrecido e joguei todos os móveis de Tobias fora da sala.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12681, 9, "Mandei purificar as salas, e coloquei de volta nelas os utensílios do templo de Deus, com as ofertas de cereal e o incenso.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12682, 10, "Também fiquei sabendo que os levitas não tinham recebido a parte que lhes era devida, e que todos os levitas e cantores responsáveis pelo culto haviam voltado para suas próprias terras.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12683, 11, "Por isso repreendi os oficiais e lhes perguntei: ""Por que essa negligência com o templo de Deus? "" Então eu convoquei os levitas e cantores e os coloquei em seus postos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12684, 12, "E todo o povo de Judá trouxe os dízimos do trigo, do vinho novo e do azeite aos depósitos.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12685, 13, "Coloquei o sacerdote Selemias, o escriba Zadoque e um levita chamado Pedaías como encarregados dos depósitos e fiz de Hanã, filho de Zacur, neto de Matanias, assistente deles, porque esses homens eram de confiança, e ficaram responsáveis pela distribuição de suprimentos aos seus colegas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12686, 14, "Lembra-te de mim por isso, meu Deus, e não te esqueças do que fiz com tanta fidelidade pelo templo de meu Deus e pelo seu culto.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12687, 15, "Naqueles dias vi que em Judá alguns pisavam uvas nos tanques de prensá-las no sábado e ajuntavam trigo e o carregavam em jumentos, junto com vinho, uvas, figos e todo tipo de carga. Tudo isso era trazido para Jerusalém em pleno sábado. Então os adverti que não vendessem alimento nesse dia.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12688, 16, "Havia alguns da cidade de Tiro que moravam em Jerusalém e traziam peixes e toda espécie de mercadoria e as vendiam em Jerusalém, no sábado, para o povo de Judá.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12689, 17, "Diante disso, repreendi os nobres de Judá e disse-lhes: ""Que mal é esse que vocês estão fazendo, profanando o dia de sábado?", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12690, 18, "Por acaso os seus antepassados não fizeram o mesmo, levando o nosso Deus a trazer toda essa desgraça sobre nós e sobre esta cidade? Pois agora, profanando o sábado, vocês provocam mais ira contra Israel! """, 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12691, 19, "Quando as sombras da tarde cobriram as portas de Jerusalém na véspera do sábado, ordenei que fossem fechadas e só fossem abertas depois que o sábado tivesse terminado. Coloquei alguns de meus homens de confiança junto às portas, para que nenhum carregamento pudesse ser introduzido no dia de sábado.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12692, 20, "Uma ou duas vezes os comerciantes e vendedores de todo tipo de mercadoria passaram a noite do lado de fora de Jerusalém.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12693, 21, "Mas eu os adverti, dizendo: Por que vocês passam a noite junto ao muro? Se fizerem isso de novo, mandarei prendê-los. Depois disso não vieram mais no sábado.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12694, 22, "Então ordenei aos levitas que se purificassem e fossem vigiar as portas a fim de que o dia de sábado fosse respeitado como sagrado. Lembra-te de mim também por isso, ó meu Deus, e tenha misericórdia de mim conforme o teu grande amor.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12695, 23, "Além disso, naqueles dias vi alguns judeus que se haviam casado com mulheres de Asdode, de Amom e de Moabe.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12696, 24, "A metade dos seus filhos falavam a língua de Asdode ou a língua de um dos outros povos, e não sabiam falar a língua de Judá.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12697, 25, "Eu os repreendi e invoquei maldições sobre eles. Bati em alguns deles e arranquei os seus cabelos. Fiz com que jurassem em nome de Deus e disse-lhes: ""Não consintam mais em dar suas filhas em casamento aos filhos deles, nem haja casamento das filhas deles com seus filhos ou com vocês.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12698, 26, "Não foi por causa de casamentos como esses que Salomão, rei de Israel, pecou? Entre as muitas nações não havia rei algum como ele. Ele era amado de seu Deus, e Deus o fez rei sobre todo o Israel, mas até mesmo ele foi induzido ao pecado por mulheres estrangeiras.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12699, 27, "Como podemos tolerar o que ouvimos? Como podem vocês cometer essa terrível maldade e serem infiéis ao nosso Deus, casando-se com mulheres estrangeiras? """, 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12700, 28, "Um dos filhos de Joiada, filho do sumo sacerdote Eliasibe, era genro de Sambalate, o horonita. E eu o expulsei para longe de mim.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12701, 29, "Não te esqueças deles, ó meu Deus, pois profanaram o ofício sacerdotal e a aliança do sacerdócio e dos levitas.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12702, 30, "Dessa forma purifiquei os sacerdotes e os levitas de tudo o que era estrangeiro, e designei-lhes responsabilidades, cada um em seu próprio cargo.", 15);
INSERT INTO verses (id, number, text, chapter_id) VALUES (12703, 31, "Também estabeleci regras para as provisões de lenha, determinando as datas certas para serem trazidas, e para os primeiros frutos. Em tua bondade, lembra-te de mim, ó meu Deus.", 15);