INSERT INTO verses (id, number, text, chapter_id) VALUES (3605, 1, "O Senhor falou a Moisés na Tenda do Encontro, no deserto do Sinai, no primeiro dia do segundo mês do segundo ano, depois que os israelitas saíram do Egito. Ele disse:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3606, 2, """Façam um recenseamento de toda a comunidade de Israel, pelos seus clãs e famílias, alistando todos os homens, um a um, pelo nome.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3607, 3, "Você e Arão contarão todos os homens que possam servir no exército, de vinte anos para cima, organizados segundo as suas divisões.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3608, 4, "Um homem de cada tribo, o chefe dos grupos de famílias, deverá ajudá-los.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3609, 5, "Estes são os nomes dos homens que os ajudarão: de Rúben, Elizur, filho de Sedeur;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3610, 6, "de Simeão, Selumiel, filho de Zurisadai;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3611, 7, "de Judá, Naassom, filho de Aminadabe;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3612, 8, "de Issacar, Natanael, filho de Zuar;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3613, 9, "de Zebulom, Eliabe, filho de Helom;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3614, 10, "dos filhos de José: de Efraim, Elisama, filho de Amiúde; de Manassés, Gamaliel, filho de Pedazur;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3615, 11, "de Benjamim, Abidã, filho de Gideoni;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3616, 12, "de Dã, Aieser, filho de Amisadai;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3617, 13, "de Aser, Pagiel, filho de Ocrã;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3618, 14, "de Gade, Eliasafe, filho de Deuel;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3619, 15, "de Naftali, Aira, filho de Enã"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3620, 16, "Foram esses os escolhidos dentre a comunidade, líderes das tribos dos seus antepassados, chefes dos clãs de Israel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3621, 17, "Moisés e Arão reuniram os homens nomeados", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3622, 18, "e convocaram toda a comunidade no primeiro dia do segundo mês. Os homens de vinte anos para cima inscreveram-se conforme os seus clãs e as suas famílias, um a um, pelo nome,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3623, 19, "conforme o Senhor tinha ordenado a Moisés. E assim ele os contou no deserto do Sinai, na seguinte ordem:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3624, 20, "Dos descendentes de Rúben, o filho mais velho de Israel: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os registros de seus clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3625, 21, "O número dos da tribo de Rúben foi 46. 500.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3626, 22, "Dos descendentes de Simeão: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3627, 23, "O número dos da tribo de Simeão foi 59. 300.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3628, 24, "Dos descendentes de Gade: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3629, 25, "O número dos da tribo de Gade foi 45. 650.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3630, 26, "Dos descendentes de Judá: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3631, 27, "O número dos da tribo de Judá foi 74. 600.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3632, 28, "Dos descendentes de Issacar: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3633, 29, "O número dos da tribo de Issacar foi 54. 400.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3634, 30, "Dos descendentes de Zebulom: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3635, 31, "O número dos da tribo de Zebulom foi 57. 400.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3636, 32, "Dos filhos de José: Dos descendentes de Efraim: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3637, 33, "O número dos da tribo de Efraim foi 40. 500.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3638, 34, "Dos descendentes de Manassés: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3639, 35, "O número dos da tribo de Manassés foi 32. 200.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3640, 36, "Dos descendentes de Benjamim: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3641, 37, "O número dos da tribo de Benjamim foi 35. 400.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3642, 38, "Dos descendentes de Dã: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3643, 39, "O número dos da tribo de Dã foi 62. 700.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3644, 40, "Dos descendentes de Aser: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3645, 41, "O número dos da tribo de Aser foi 41. 500.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3646, 42, "Dos descendentes de Naftali: Todos os homens de vinte anos para cima que podiam servir no exército foram relacionados, cada um pelo seu nome, de acordo com os seus registros de clãs e famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3647, 43, "O número dos da tribo de Naftali foi 53. 400.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3648, 44, "Esses foram os homens contados por Moisés e por Arão e pelos doze líderes de Israel, cada um representando a sua família.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3649, 45, "Todos os israelitas de vinte anos para cima que podiam servir no exército foram contados de acordo com as suas famílias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3650, 46, "O total foi 603. 550 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3651, 47, "As famílias da tribo de Levi, porém, não foram contadas juntamente com as outras,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3652, 48, "pois o Senhor tinha dito a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3653, 49, """Não faça o recenseamento da tribo de Levi nem a relacione entre os demais israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3654, 50, "Em vez disso, designe os levitas como responsáveis pelo tabernáculo que guarda as tábuas da aliança, por todos os seus utensílios e por tudo o que pertence a ele. Eles transportarão o tabernáculo e todos os seus utensílios; cuidarão dele e acamparão ao seu redor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3655, 51, "Sempre que o tabernáculo tiver que ser removido, os levitas o desmontarão e, sempre que tiver que ser armado, os levitas o farão. Qualquer pessoa não autorizada que se aproximar do tabernáculo terá que ser executada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3656, 52, "Os israelitas armarão as suas tendas organizadas segundo as suas divisões, cada um em seu próprio acampamento e junto à sua bandeira.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3657, 53, "Os levitas, porém, armarão as suas tendas ao redor do tabernáculo que guarda as tábuas da aliança, para que a ira divina não caia sobre a comunidade de Israel. Os levitas terão a responsabilidade de cuidar do tabernáculo que guarda as tábuas da aliança"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3658, 54, "Os israelitas fizeram tudo exatamente como o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3659, 1, "O Senhor disse a Moisés e a Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3660, 2, """Os israelitas acamparão ao redor da Tenda do Encontro, a certa distância, cada homem junto à sua bandeira com os emblemas da sua família"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3661, 3, "A leste, os exércitos de Judá acamparão junto à sua bandeira. O líder de Judá será Naassom, filho de Aminadabe.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3662, 4, "Seu exército é de 74. 600 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3663, 5, "A tribo de Issacar acampará ao lado de Judá. O líder de Issacar será Natanael, filho de Zuar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3664, 6, "Seu exército é de 54. 400 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3665, 7, "A tribo de Zebulom virá em seguida. O líder de Zebulom será Eliabe, filho de Helom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3666, 8, "Seu exército é de 57. 400 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3667, 9, "O número total dos homens recenseados do acampamento de Judá, de acordo com os seus exércitos, foi 186. 400. Esses marcharão primeiro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3668, 10, "Ao sul estarão os exércitos do acampamento de Rúben, junto à sua bandeira. O líder de Rúben será Elizur, filho de Sedeur.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3669, 11, "Seu exército é de 46. 500 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3670, 12, "A tribo de Simeão acampará ao lado de Rúben. O líder de Simeão será Selumiel, filho de Zurisadai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3671, 13, "Seu exército é de 59. 300 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3672, 14, "A tribo de Gade virá em seguida. O líder de Gade será Eliasafe, filho de Deuel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3673, 15, "Seu exército é de 45. 650 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3674, 16, "O número total dos homens recenseados do acampamento de Rúben, de acordo com os seus exércitos, foi 151. 450. Esses marcharão em segundo lugar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3675, 17, "Em seguida os levitas marcharão levando a Tenda do Encontro no meio dos outros acampamentos, na mesma ordem em que acamparem, cada um em seu próprio lugar, junto à sua bandeira.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3676, 18, "A oeste estarão os exércitos do acampamento de Efraim, junto à sua bandeira. O líder de Efraim será Elisama, filho de Amiúde.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3677, 19, "Seu exército é de 40. 500 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3678, 20, "A tribo de Manassés acampará ao lado de Efraim. O líder de Manassés será Gamaliel, filho de Pedazur.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3679, 21, "Seu exército é de 32. 200 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3680, 22, "A tribo de Benjamim virá em seguida. O líder de Benjamim será Abidã, filho de Gideoni.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3681, 23, "Seu exército é de 35. 400 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3682, 24, "O número total dos homens recenseados do acampamento de Efraim, de acordo com os seus exércitos, foi 108. 100. Esses marcharão em terceiro lugar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3683, 25, "Ao norte estarão os exércitos do acampamento de Dã, junto à sua bandeira. O líder de Dã será Aieser, filho de Amisadai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3684, 26, "Seu exército é de 62. 700 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3685, 27, "A tribo de Aser acampará ao lado de Dã. O líder de Aser será Pagiel, filho de Ocrã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3686, 28, "Seu exército é de 41. 500 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3687, 29, "A tribo de Naftali virá em seguida. O líder de Naftali será Aira, filho de Enã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3688, 30, "Seu exército é de 53. 400 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3689, 31, "O número total dos homens recenseados do acampamento de Dã, de acordo com os seus exércitos, foi 157. 600. Estes marcharão por último, junto às suas bandeiras.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3690, 32, "Foram esses os israelitas contados de acordo com as suas famílias. O número total dos que foram contados nos acampamentos, de acordo com os seus exércitos, foi 603. 550.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3691, 33, "Os levitas, contudo, não foram contados com os outros israelitas, conforme o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3692, 34, "Assim os israelitas fizeram tudo o que o Senhor tinha ordenado a Moisés; eles acampavam junto às suas bandeiras e depois partiam, cada um com o seu clã e com a sua família.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3693, 1, "Esta é a história da descendência de Arão e de Moisés, quando o Senhor falou com Moisés no monte Sinai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3694, 2, "Os nomes dos filhos de Arão são Nadabe, o mais velho, Abiú, Eleazar e Itamar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3695, 3, "São esses os nomes dos filhos de Arão, que foram ungidos para o sacerdócio e que foram ordenados sacerdotes.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3696, 4, "Nadabe e Abiú, entretanto, caíram mortos perante o Senhor quando lhe trouxeram uma oferta com fogo profano, no deserto do Sinai. Como não tinham filhos, somente Eleazar e Itamar serviram como sacerdotes durante a vida de Arão, seu pai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3697, 5, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3698, 6, """Mande chamar a tribo de Levi e apresente-a ao sacerdote Arão para auxiliá-lo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3699, 7, "Eles cuidarão das obrigações próprias da Tenda do Encontro, fazendo o serviço do tabernáculo para Arão e para toda a comunidade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3700, 8, "Tomarão conta de todos os utensílios da Tenda do Encontro, cumprindo as obrigações dos israelitas no serviço do tabernáculo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3701, 9, "Dedique os levitas a Arão e a seus filhos; eles serão escolhidos entre os israelitas para serem inteiramente dedicados a Arão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3702, 10, "Encarregue Arão e os seus filhos de cuidar do sacerdócio; qualquer pessoa não autorizada que se aproximar do santuário terá que ser executada"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3703, 11, "Disse também o Senhor a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3704, 12, """Eu mesmo escolho os levitas dentre os israelitas em lugar do primeiro filho de cada mulher israelita. Os levitas são meus,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3705, 13, "pois todos os primogênitos são meus. Quando feri todos os primogênitos no Egito, separei para mim mesmo todo primogênito de Israel, tanto entre os homens como entre os rebanhos. Serão meus. Eu sou o Senhor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3706, 14, "E o Senhor disse ainda a Moisés no deserto do Sinai:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3707, 15, """Conte os levitas pelas suas famílias e clãs. Serão contados todos os do sexo masculino de um mês de idade para cima"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3708, 16, "Então Moisés os contou, conforme a ordem que recebera do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3709, 17, "São estes os nomes dos filhos de Levi: Gérson, Coate e Merari.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3710, 18, "São estes os nomes dos clãs gersonitas: Libni e Simei.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3711, 19, "São estes os nomes dos clãs coatitas: Anrão, Isar, Hebrom e Uziel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3712, 20, "E estes são os nomes dos clãs meraritas: Mali e Musi. Foram esses os líderes dos clãs levitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3713, 21, "A Gérson pertenciam os clãs dos libnitas e dos simeítas; eram esses os clãs gersonitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3714, 22, "O número de todos os que foram contados do sexo masculino, de um mês de idade para cima, foi 7. 500.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3715, 23, "Os clãs gersonitas tinham que acampar a oeste, atrás do tabernáculo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3716, 24, "O líder das famílias dos gersonitas era Eliasafe, filho de Lael.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3717, 25, "Na Tenda do Encontro os gersonitas tinham a responsabilidade de cuidar do tabernáculo, da tenda, da sua cobertura, da cortina da entrada da Tenda do Encontro,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3718, 26, "das cortinas externas do pátio, da cortina da entrada do pátio que rodeia o tabernáculo e o altar, das cordas, e de tudo o que estava relacionado com esse serviço.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3719, 27, "A Coate pertenciam os clãs dos anramitas, dos isaritas, dos hebronitas e dos uzielitas; eram esses os clãs coatitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3720, 28, "O número de todos os do sexo masculino, de um mês de idade para cima, foi 8. 600. Os coatitas tinham a responsabilidade de cuidar do santuário.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3721, 29, "Os clãs coatitas tinham que acampar no lado sul do tabernáculo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3722, 30, "O líder das famílias dos clãs coatitas era Elisafã, filho de Uziel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3723, 31, "Tinham a responsabilidade de cuidar da arca, da mesa, do candelabro, dos altares, dos utensílios do santuário com os quais ministravam, da cortina e de tudo o que estava relacionado com esse serviço.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3724, 32, "O principal líder dos levitas era Eleazar, filho do sacerdote Arão. Ele tinha a responsabilidade de supervisionar os encarregados de cuidar do santuário.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3725, 33, "A Merari pertenciam os clãs dos malitas e dos musitas; eram esses os clãs meraritas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3726, 34, "O número de todos os que foram contados do sexo masculino, de um mês de idade para cima, foi 6. 200.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3727, 35, "O líder das famílias dos clãs meraritas era Zuriel, filho de Abiail; eles tinham que acampar no lado norte do tabernáculo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3728, 36, "Os meraritas tinham a responsabilidade de cuidar das armações do tabernáculo, de seus travessões, das colunas, das bases, de todos os seus utensílios e de tudo o que estava relacionado com esse serviço,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3729, 37, "bem como das colunas do pátio ao redor, com suas bases, suas estacas e suas cordas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3730, 38, "E acamparam a leste do tabernáculo, em frente da Tenda do Encontro, Moisés, Arão e seus filhos. Tinham a responsabilidade de cuidar do santuário em favor dos israelitas. Qualquer pessoa não autorizada que se aproximasse do santuário teria que ser executado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3731, 39, "O número total de levitas contados por Moisés e Arão, conforme a ordem do Senhor, segundo os clãs deles, todos os do sexo masculino, de um mês de idade para cima, foi 22. 000.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3732, 40, "E o Senhor disse a Moisés: ""Conte todos os primeiros filhos dos israelitas, do sexo masculino, de um mês de idade para cima e faça uma relação de seus nomes.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3733, 41, "Dedique a mim os levitas em lugar de todos os primogênitos dos israelitas, e os rebanhos dos levitas, em lugar de todas as primeiras crias dos rebanhos dos israelitas. Eu sou o Senhor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3734, 42, "E Moisés contou todos os primeiros filhos dos israelitas, conforme o Senhor lhe havia ordenado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3735, 43, "O número total dos primeiros filhos do sexo masculino, de um mês de idade para cima, relacionados pelo nome, foi 22. 273.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3736, 44, "Disse também o Senhor a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3737, 45, """Dedique os levitas em lugar de todos os primogênitos dos israelitas, e os rebanhos dos levitas em lugar dos rebanhos dos israelitas. Os levitas serão meus. Eu sou o Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3738, 46, "Para o resgate dos primeiros 273 filhos dos israelitas que excedem o número de levitas,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3739, 47, "recolha sessenta gramas de prata, com base no peso padrão do santuário, que são doze gramas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3740, 48, "Entregue a Arão e aos seus filhos a prata para o resgate do número excedente de israelitas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3741, 49, "Assim Moisés recolheu a prata para o resgate daqueles que excederam o número dos levitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3742, 50, "Dos primeiros filhos dos israelitas ele recolheu prata no peso de quase dezesseis quilos e meio, com base no peso padrão do santuário.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3743, 51, "Moisés entregou a Arão e aos filhos dele a prata para o resgate, conforme a ordem que recebera do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3744, 1, "Disse ainda o Senhor a Moisés e a Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3745, 2, """Façam um recenseamento dos coatitas na tribo de Levi, pelos seus clãs e famílias;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3746, 3, "contem todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3747, 4, """O serviço dos coatitas na Tenda do Encontro será o cuidado das coisas santíssimas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3748, 5, "Quando o acampamento tiver que mudar, Arão e os seus filhos entrarão e descerão o véu protetor e com ele cobrirão a arca da aliança.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3749, 6, "Depois a cobrirão com couro, estenderão um pano inteiramente azul sobre ela e colocarão as varas no lugar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3750, 7, """Sobre a mesa da Presença eles estenderão um pano azul e colocarão os pratos, os recipientes para incenso, as tigelas e as bacias para as ofertas derramadas, e os pães da Presença, que devem estar sempre sobre ela.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3751, 8, "Sobre tudo isso estenderão um pano vermelho e o cobrirão com couro. Depois colocarão as varas no lugar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3752, 9, """Pegarão também um pano azul e cobrirão o candelabro usado para iluminação, as suas candeias, as suas tesouras de aparo, os seus apagadores e todos os seus jarros necessários para alimentá-la de óleo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3753, 10, "Então o embrulharão com todos os seus utensílios numa cobertura de couro e o colocarão num suporte para carregar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3754, 11, """Sobre o altar de ouro estenderão um pano azul e o cobrirão com couro. E colocarão as suas varas no lugar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3755, 12, """Apanharão todos os utensílios usados na ministração no santuário, depois os embrulharão num pano azul e os cobrirão com couro; a seguir os colocarão num suporte para carregar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3756, 13, """Tirarão a cinza do altar de bronze e estenderão sobre ele um pano roxo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3757, 14, "Colocarão sobre ele todos os utensílios usados na ministração no altar: os braseiros, os garfos de carne, as pás e as bacias da aspersão. Sobre ele estenderão uma cobertura de couro e colocarão as varas no lugar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3758, 15, """Quando Arão e os seus filhos terminarem de cobrir os utensílios sagrados e todos os artigos sagrados, e o acampamento estiver pronto para partir, os coatitas virão carregá-los. Mas não tocarão nas coisas sagradas; se o fizerem, morrerão. São esses os utensílios da Tenda do Encontro que os coatitas carregarão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3759, 16, """Eleazar, filho do sacerdote Arão, ficará encarregado do azeite para a iluminação, do incenso aromático, da oferta costumeira de cereal e do óleo da unção. Ficará encarregado de todo o tabernáculo e de tudo o que nele há, seus utensílios e seus artigos sagrados"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3760, 17, "O Senhor disse a Moisés e a Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3761, 18, """Não permitam que o ramo dos clãs coatitas seja eliminado dentre os levitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3762, 19, "Mas, para que continuem vivos e não morram quando se aproximarem das coisas santíssimas, Arão e os seus filhos entrarão no santuário e designarão a cada homem a sua tarefa e o que deverá carregar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3763, 20, "Os coatitas não entrarão para ver as coisas sagradas, nem por um breve momento, para que não morram"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3764, 21, "E o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3765, 22, """Faça também um recenseamento dos gersonitas, pelas suas famílias e clãs;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3766, 23, "conte todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3767, 24, """Este é o serviço dos clãs gersonitas, o que devem fazer e carregar:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3768, 25, "Eles levarão as cortinas internas do tabernáculo, a Tenda do Encontro, a sua cobertura, a cobertura exterior de couro, as cortinas da entrada da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3769, 26, "Farão tudo o que for necessário com aquelas coisas e com as cortinas externas do pátio que rodeia o tabernáculo e o altar, com a cortina da entrada, com as cordas e com todos os utensílios usados em seu serviço.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3770, 27, "Todo o serviço deles, tudo o que devem fazer e carregar estará sob a direção de Arão e de seus filhos. Designe como responsabilidade deles tudo o que tiverem que carregar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3771, 28, "Esse é o serviço dos clãs gersonitas na Tenda do Encontro. Suas atividades estarão sob a supervisão de Itamar, filho do sacerdote Arão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3772, 29, """Conte os meraritas conforme os seus clãs e famílias,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3773, 30, "todos os homens entre trinta e cinqüenta anos, aptos para servir, para que façam o serviço da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3774, 31, "Esta é a responsabilidade deles no serviço que realizam na Tenda do Encontro: carregar as armações do tabernáculo, seus travessões, suas colunas e suas bases,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3775, 32, "bem como as colunas do pátio, que rodeia a tenda, com suas bases, suas estacas e suas cordas; todos os seus utensílios e tudo o que está relacionado com o seu uso. Designe a cada um aquilo que deverá levar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3776, 33, "Este é o serviço dos clãs meraritas. Todo o serviço deles na Tenda do Encontro estará sob a supervisão de Itamar, filho do sacerdote Arão"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3777, 34, "Moisés, Arão e os líderes da comunidade contaram os coatitas, conforme seus clãs e famílias,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3778, 35, "todos os homens entre trinta e cinqüenta anos, aptos para servir, para que fizessem o serviço da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3779, 36, "Foram contados, conforme os seus clãs, 2. 750 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3780, 37, "Esse foi o total de recenseados dos clãs coatitas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor, anunciada por Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3781, 38, "Os gersonitas foram contados conforme os seus clãs e famílias,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3782, 39, "todos os homens entre trinta e cinqüenta anos, aptos para servir, para fazer o serviço da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3783, 40, "Foram contados conforme os seus clãs e famílias 2. 630.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3784, 41, "Esse foi o total de recenseados dos clãs gersonitas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3785, 42, "Os meraritas foram contados conforme os seus clãs e famílias,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3786, 43, "todos os homens entre trinta e cinqüenta anos, aptos para servir, para fazer o serviço da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3787, 44, "Foram contados conforme os seus clãs 3. 200.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3788, 45, "Esse foi o total de recenseados dos clãs meraritas que serviam na Tenda do Encontro. Moisés e Arão os contaram de acordo com a ordem do Senhor, anunciada por Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3789, 46, "Assim Moisés, Arão e os líderes de Israel contaram todos os levitas conforme os seus clãs e famílias,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3790, 47, "todos os homens entre trinta e cinqüenta anos de idade que vieram para servir e carregar a Tenda do Encontro", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3791, 48, "somavam 8. 580.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3792, 49, "Conforme a ordem do Senhor anunciada por Moisés, a cada um foi designado o seu trabalho e foi dito o que deveria carregar. Assim foram todos contados, conforme o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3793, 1, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3794, 2, """Ordene aos israelitas que mandem para fora do acampamento todo aquele que tiver lepra, ou que tiver um fluxo, ou que se tornar impuro por tocar um cadáver.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3795, 3, "Mande-os para fora do acampamento tanto homens como mulheres, para que não contaminem o seu próprio acampamento, onde habito entre eles"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3796, 4, "Os israelitas assim fizeram e os mandaram para fora do acampamento, como o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3797, 5, "E o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3798, 6, """Diga aos israelitas: Quando um homem ou uma mulher prejudicar outra pessoa e, portanto, ofender ao Senhor, será culpado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3799, 7, "Confessará o pecado que cometeu, fará restituição total, acrescentará um quinto a esse valor e entregará tudo isso a quem ele prejudicou.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3800, 8, "Mas, se o prejudicado não tiver nenhum parente próximo para receber a restituição, esta pertencerá ao Senhor e será entregue ao sacerdote, juntamente com o carneiro com o qual se faz propiciação pelo culpado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3801, 9, "Todas as contribuições, ou seja, todas as dádivas sagradas que os israelitas trouxerem ao sacerdote, pertencerão a ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3802, 10, "As dádivas sagradas de cada pessoa pertencem a ela, mas o que ela der ao sacerdote pertencerá ao sacerdote"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3803, 11, "Então o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3804, 12, """Diga o seguinte aos israelitas: Se a mulher de alguém se desviar e lhe for infiel,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3805, 13, "e outro homem deitar-se com ela, e isso estiver oculto de seu marido, e a impureza dela não for descoberta, por não haver testemunha contra ela nem tiver ela sido pega no ato;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3806, 14, "e se o marido dela tiver ciúmes e suspeitar de sua mulher, esteja ela pura ou impura,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3807, 15, "ele a levará ao sacerdote, com uma oferta de um jarro de farinha de cevada em favor dela. Não derramará azeite nem porá incenso sobre a farinha, porque é uma oferta de cereal pelo ciúme, para que se revele a verdade sobre o pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3808, 16, """O sacerdote trará a mulher e a colocará perante o Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3809, 17, "Então apanhará um pouco de água sagrada num jarro de barro e colocará na água um pouco do pó do chão do tabernáculo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3810, 18, "Depois de colocar a mulher perante o Senhor, o sacerdote soltará o cabelo dela e porá nas mãos dela a oferta memorial, a oferta pelo ciúme, enquanto ele mesmo terá em sua mão a água amarga que traz maldição.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3811, 19, "Então o sacerdote fará a mulher jurar e lhe dirá: Se nenhum outro homem se deitou com você e se você não foi infiel nem se tornou impura enquanto casada, que esta água amarga que traz maldição não lhe faça mal.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3812, 20, "Mas, se você foi infiel enquanto casada e se contaminou por ter se deitado com um homem que não é seu marido —", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3813, 21, "então o sacerdote fará a mulher pronunciar este juramento com maldição — que o Senhor faça de você objeto de maldição e de desprezo no meio do povo fazendo que a sua barriga inche e que você jamais tenha filhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3814, 22, "Que esta água que traz maldição entre em seu corpo, inche a sua barriga e a impeça de ter filhos. ""Então a mulher dirá: ‘Amém. Assim seja’.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3815, 23, """O sacerdote escreverá essas maldições num documento e depois as lavará na água amarga.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3816, 24, "Ele a fará beber a água amarga que traz maldição, e essa água entrará nela, causando-lhe amargo sofrimento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3817, 25, "O sacerdote apanhará das mãos dela a oferta de cereal pelo ciúme, a moverá ritualmente perante o Senhor e a trará ao altar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3818, 26, "Então apanhará um punhado da oferta de cereal como oferta memorial e a queimará sobre o altar; depois disso fará a mulher beber a água.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3819, 27, "Se ela houver se contaminado, sendo infiel ao seu marido, quando o sacerdote fizer que ela beba a água que traz maldição, essa água entrará nela e causará um amargo sofrimento; sua barriga inchará e ela, incapaz de ter filhos, se tornará objeto de maldição entre o seu povo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3820, 28, "Se, porém, a mulher não houver se contaminado, mas estiver pura, não sofrerá punição e será capaz de ter filhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3821, 29, """Esse é, pois, o ritual quanto ao ciúme, quando uma mulher for infiel e se contaminar enquanto casada,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3822, 30, "ou quando o ciúme se apoderar de um homem porque suspeita de sua mulher. O sacerdote a colocará perante o Senhor e a fará passar por todo esse ritual.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3823, 31, "Se a suspeita não se confirmar, o marido estará inocente, do contrário a mulher sofrerá as conseqüências da sua iniqüidade"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3824, 1, "O Senhor disse ainda a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3825, 2, """Diga o seguinte aos israelitas: Se um homem ou uma mulher fizer um voto especial, um voto de separação para o Senhor como nazireu,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3826, 3, "terá que se abster de vinho e de outras bebidas fermentadas e não poderá beber vinagre feito de vinho ou de outra bebida fermentada. Não poderá beber suco de uva nem comer uvas nem passas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3827, 4, "Enquanto for nazireu, não poderá comer nada que venha da videira, nem mesmo as sementes ou as cascas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3828, 5, """Durante todo o período de seu voto de separação, nenhuma lâmina será usada em sua cabeça. Até que termine o período de sua separação para o Senhor ele estará consagrado e deixará crescer o cabelo de sua cabeça.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3829, 6, "Durante todo o período de sua separação para o Senhor, não poderá aproximar-se de um cadáver.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3830, 7, "Mesmo que o seu próprio pai ou mãe ou irmã ou irmão morra, ele não poderá tornar-se impuro por causa deles, pois traz sobre a cabeça o símbolo de sua separação para Deus.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3831, 8, "Durante todo o período de sua separação, estará consagrado ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3832, 9, """Se alguém morrer repentinamente perto dele, contaminando assim o cabelo que consagrou, ele terá que rapar a cabeça sete dias depois, dia da sua purificação.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3833, 10, "No oitavo dia, trará duas rolinhas ou dois pombinhos ao sacerdote à entrada da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3834, 11, "O sacerdote oferecerá um como oferta pelo pecado e o outro como holocausto, para fazer propiciação por ele, pois pecou ao se aproximar de um cadáver. Naquele mesmo dia o nazireu reconsagrará a sua cabeça.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3835, 12, "Ele se dedicará ao Senhor pelo período de sua separação e trará um cordeiro de um ano de idade como oferta de reparação. Não se contarão os dias anteriores porque ficou contaminado durante a sua separação.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3836, 13, """Este é o ritual do nazireu quando terminar o período de sua separação: ele será trazido à entrada da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3837, 14, "Ali apresentará a sua oferta ao Senhor: um cordeiro de um ano e sem defeito como holocausto, uma cordeira de um ano e sem defeito como oferta pelo pecado, um carneiro sem defeito como oferta de comunhão", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3838, 15, "juntamente com a sua oferta de cereal, com a oferta derramada e com um cesto de pães sem fermento com bolos feitos da melhor farinha amassada com azeite e pães finos untados com azeite.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3839, 16, """O sacerdote os apresentará ao Senhor e oferecerá o sacrifício pelo pecado e o holocausto.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3840, 17, "Apresentará o cesto de pães sem fermento e oferecerá o cordeiro como sacrifício de comunhão ao Senhor, juntamente com a oferta de cereal e a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3841, 18, """Em seguida, à entrada da Tenda do Encontro, o nazireu rapará o cabelo que consagrou e o jogará no fogo que está embaixo do sacrifício da oferta de comunhão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3842, 19, """Depois que o nazireu rapar o cabelo da sua consagração, o sacerdote lhe colocará nas mãos um ombro cozido do carneiro, um bolo e um pão fino tirados do cesto, ambos sem fermento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3843, 20, "O sacerdote os moverá perante o Senhor como gesto ritual de apresentação; são santos e pertencem ao sacerdote, bem como o peito que foi movido e a coxa. Depois disso o nazireu poderá beber vinho.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3844, 21, """Esse é o ritual do voto de nazireu e da oferta dedicada ao Senhor de acordo com a sua separação, sem contar qualquer outra coisa que ele possa dedicar. Cumprirá o voto que tiver feito de acordo com o ritual do nazireu"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3845, 22, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3846, 23, """Diga a Arão e aos seus filhos: Assim vocês abençoarão os israelitas:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3847, 24, """O Senhor te abençoe e te guarde;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3848, 25, "o Senhor faça resplandecer o seu rosto sobre ti e te conceda graça;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3849, 26, "o Senhor volte para ti o seu rosto e te dê paz.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3850, 27, """Assim eles invocarão o meu nome sobre os israelitas, e eu os abençoarei"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3851, 1, "Quando Moisés acabou de armar o tabernáculo, ele o ungiu e o consagrou, juntamente com todos os seus utensílios. Também ungiu e consagrou o altar com todos os seus utensílios.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3852, 2, "Então os líderes de Israel, os chefes das famílias que eram os líderes das tribos encarregados do recenseamento, apresentaram ofertas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3853, 3, "Trouxeram as suas dádivas ao Senhor: seis carroças cobertas e doze bois, um boi de cada líder e uma carroça de cada dois líderes; e as apresentaram diante do tabernáculo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3854, 4, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3855, 5, """Aceite as ofertas deles para que sejam usadas no trabalho da Tenda do Encontro. Entregue-as aos levitas, conforme exigir o trabalho de cada homem"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3856, 6, "Então Moisés recebeu as carroças e os bois e os entregou aos levitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3857, 7, "Deu duas carroças e quatro bois aos gersonitas, conforme exigia o trabalho deles,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3858, 8, "e quatro carroças e oito bois aos meraritas, conforme exigia o trabalho deles. Estavam todos sob a supervisão de Itamar, filho do sacerdote Arão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3859, 9, "Mas aos coatitas Moisés não deu nada, pois eles deveriam carregar nos ombros os objetos sagrados pelos quais eram responsáveis.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3860, 10, "Quando o altar foi ungido, os líderes trouxeram as suas ofertas para a dedicação do altar, e as apresentaram diante dele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3861, 11, "Pois o Senhor tinha dito a Moisés: ""Cada dia um líder deverá trazer a sua oferta para a dedicação do altar"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3862, 12, "No primeiro dia, Naassom, filho de Aminadabe, da tribo de Judá, trouxe a sua oferta.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3863, 13, "A oferta dele foi um prato de prata de um quilo e quinhentos e sessenta gramas e uma bacia de prata para as aspersões de oitocentos e quarenta gramas, ambos pesados com base no peso padrão do santuário, cada um cheio da melhor farinha amassada com óleo como oferta de cereal;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3864, 14, "uma vasilha de ouro de cento e vinte gramas, cheia de incenso;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3865, 15, "um novilho, um carneiro e um cordeiro de um ano como holocausto;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3866, 16, "um bode como oferta pelo pecado;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3867, 17, "e dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano para serem oferecidos como sacrifício de comunhão. Essa foi a oferta de Naassom, filho de Aminadabe.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3868, 18, "No segundo dia, Natanael, filho de Zuar e líder de Issacar, trouxe a sua oferta.", 3);
