INSERT INTO verses (id, number, text, chapter_id) VALUES (3928, 78, "No décimo segundo dia, Aira, filho de Enã e líder de Naftali, trouxe a sua oferta.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3929, 79, "A oferta dele foi um prato de prata de um quilo e quinhentos e sessenta gramas e uma bacia de prata para as aspersões de oitocentos e quarenta gramas, ambos pesados com base no peso padrão do santuário, cada um cheio da melhor farinha amassada com óleo como oferta de cereal;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3930, 80, "uma vasilha de ouro de cento e vinte gramas, cheia de incenso;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3931, 81, "um novilho, um carneiro e um cordeiro de um ano como holocausto;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3932, 82, "um bode como oferta pelo pecado;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3933, 83, "e dois bois, cinco carneiros, cinco bodes e cinco cordeiros de um ano para serem oferecidos como sacrifício de comunhão. Essa foi a oferta de Aira, filho de Enã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3934, 84, "Essas foram as ofertas dos líderes israelitas para a dedicação do altar quando este foi ungido: doze pratos de prata, doze bacias de prata para as aspersões e doze vasilhas de ouro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3935, 85, "Cada prato de prata pesava um quilo e quinhentos e sessenta gramas, e cada bacia para as aspersões pesava oitocentos e quarenta gramas. O total de peças de prata pesava vinte e oito quilos e oitocentos gramas, com base no peso padrão do santuário.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3936, 86, "As doze vasilhas de ouro cheias de incenso pesavam cada uma cento e vinte gramas, com base no peso padrão do santuário. O total de vasilhas de ouro pesava um quilo e quatrocentos e quarenta gramas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3937, 87, "O total de animais oferecidos em holocausto foi doze novilhos, doze carneiros e doze cordeiros de um ano, juntamente com as ofertas de cereal. Doze bodes foram trazidos para a oferta pelo pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3938, 88, "O total de animais oferecidos em sacrifício de comunhão foi vinte e quatro bois, sessenta carneiros, sessenta bodes e sessenta cordeiros de um ano. Foram essas as ofertas trazidas para a dedicação do altar depois que este foi ungido.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3939, 89, "Quando entrava na Tenda do Encontro para falar com o Senhor, Moisés ouvia a voz que lhe falava do meio dos dois querubins, de cima da tampa da arca da aliança. Era assim que o Senhor falava com ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3940, 1, "Disse também o Senhor a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3941, 2, """Diga o seguinte a Arão: Quando você preparar as sete lâmpadas, estas deverão iluminar a área da frente do candelabro"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3942, 3, "Arão assim fez; dispôs as lâmpadas de modo que estivessem voltadas para a frente do candelabro, como o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3943, 4, "O candelabro foi feito da seguinte maneira: de ouro batido do pedestal às flores, conforme o modelo que o Senhor tinha mostrado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3944, 5, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3945, 6, """Separe os levitas do meio dos israelitas e os purifique.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3946, 7, "A purificação deles será assim: você aspergirá a água da purificação sobre eles; fará com que rapem o corpo todo e lavem as roupas, para que se purifiquem.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3947, 8, "Depois eles trarão um novilho com a oferta de cereal da melhor farinha amassada com óleo; e você trará um segundo novilho como oferta pelo pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3948, 9, "Você levará os levitas para a frente da Tenda do Encontro e reunirá toda a comunidade de Israel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3949, 10, "Levará os levitas à presença do Senhor, e os israelitas imporão as mãos sobre eles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3950, 11, "Arão apresentará os levitas ao Senhor como oferta ritualmente movida da parte dos israelitas: eles serão dedicados ao trabalho do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3951, 12, """Depois que os levitas impuserem as mãos sobre a cabeça dos novilhos, você oferecerá um novilho como oferta pelo pecado e o outro como holocausto ao Senhor, para fazer propiciação pelos levitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3952, 13, "Disponha os levitas em frente de Arão e dos filhos dele e apresente-os como oferta movida ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3953, 14, "Dessa maneira você separará os levitas do meio dos israelitas, e os levitas serão meus.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3954, 15, """Depois que você purificar os levitas e os apresentar como oferta movida, eles entrarão na Tenda do Encontro para ministrar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3955, 16, "Eles são os israelitas que deverão ser inteiramente dedicados a mim. Eu os separei para serem meus em lugar dos primogênitos, do primeiro filho homem de cada mulher israelita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3956, 17, "Todo primogênito em Israel, entre os homens ou entre os rebanhos, é meu. Eu os separei para mim quando feri todos os primogênitos no Egito", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3957, 18, "e escolhi os levitas em lugar de todos os primogênitos em Israel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3958, 19, "Dentre todos os israelitas, dediquei os levitas como dádivas a Arão e aos seus filhos; eles ministrarão na Tenda do Encontro em nome dos israelitas e farão propiciação por eles, para que nenhuma praga atinja os israelitas quando se aproximarem do santuário"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3959, 20, "Moisés, Arão e toda a comunidade de Israel fizeram com os levitas como o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3960, 21, "Os levitas se purificaram e lavaram suas roupas; e Arão os apresentou como oferta ritualmente movida perante o Senhor e fez propiciação por eles para purificá-los.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3961, 22, "Depois disso os levitas passaram a ministrar na Tenda do Encontro sob a supervisão de Arão e dos seus filhos. Fizeram com os levitas como o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3962, 23, "O Senhor disse ainda a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3963, 24, """Isto diz respeito aos levitas: os homens de vinte e cinco anos para cima, aptos para servir, tomarão parte no trabalho que se faz na Tenda do Encontro,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3964, 25, "mas aos cinqüenta anos deverão afastar-se do serviço regular e nele não mais trabalharão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3965, 26, "Poderão ajudar seus companheiros de ofício na responsabilidade de cuidar da Tenda do Encontro, mas eles mesmos não deverão fazer o trabalho. Assim você designará as responsabilidades dos levitas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3966, 1, "O Senhor falou com Moisés no deserto do Sinai no primeiro mês do segundo ano depois que o povo saiu do Egito. Ele disse:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3967, 2, """Os israelitas devem celebrar a Páscoa na ocasião própria.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3968, 3, "Celebrem-na no tempo determinado, ao pôr-do-sol do dia catorze deste mês, de acordo com todas as suas leis e ordenanças"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3969, 4, "Então Moisés ordenou aos israelitas que celebrassem a Páscoa,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3970, 5, "e eles o fizeram no deserto do Sinai, ao pôr-do-sol do dia catorze do primeiro mês. Os israelitas fizeram tudo conforme o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3971, 6, "Mas alguns deles não puderam celebrar a Páscoa naquele dia porque se haviam tornado impuros por terem tocado num cadáver. Por isso procuraram Moisés e Arão naquele mesmo dia", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3972, 7, "e disseram a Moisés: ""Nós nos tornamos impuros por termos tocado num cadáver, mas por que deveríamos ser impedidos de apresentar a nossa oferta ao Senhor na ocasião própria, como os demais israelitas? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3973, 8, "Moisés respondeu-lhes: ""Esperem até que eu saiba o que o Senhor ordena a respeito de vocês"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3974, 9, "Então o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3975, 10, """Diga o seguinte aos israelitas: Quando algum de vocês ou dos seus descendentes se tornar impuro por tocar algum cadáver ou estiver distante por motivo de viagem, ainda assim poderá celebrar a Páscoa do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3976, 11, "Deverão celebrá-la no dia catorze do segundo mês, ao pôr-do-sol. Comerão o cordeiro com pães sem fermento e com ervas amargas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3977, 12, "Não deixarão sobrar nada até o amanhecer e não quebrarão nenhum osso do cordeiro. Quando a celebrarem, obedeçam a todas as leis da Páscoa.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3978, 13, "Se, porém, um homem estiver puro e não estiver distante por motivo de viagem e ainda assim não celebrar a Páscoa, ele será eliminado do meio do seu povo porque não apresentou a oferta do Senhor na ocasião própria. Ele sofrerá as conseqüências do seu pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3979, 14, """Um estrangeiro residente entre vocês, que queira celebrar a Páscoa do Senhor, deverá fazê-lo de acordo com as leis e ordenanças da Páscoa. Vocês terão as mesmas leis para o estrangeiro e para o natural da terra"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3980, 15, "No dia em que foi armado o tabernáculo, a tenda que guarda as tábuas da aliança, a nuvem o cobriu. Desde o entardecer até o amanhecer a nuvem por cima do tabernáculo tinha a aparência de fogo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3981, 16, "Era assim que sempre acontecia: de dia a nuvem o cobria, e de noite tinha a aparência de fogo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3982, 17, "Sempre que a nuvem se levantava de cima da Tenda, os israelitas partiam; no lugar em que a nuvem descia, ali acampavam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3983, 18, "Conforme a ordem do Senhor os israelitas partiam, e conforme a ordem do Senhor, acampavam. Enquanto a nuvem estivesse por cima do tabernáculo, eles permaneciam acampados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3984, 19, "Enquanto a nuvem ficava sobre o tabernáculo por muito tempo, os israelitas cumpriam suas responsabilidades para com o Senhor, e não partiam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3985, 20, "Às vezes a nuvem ficava sobre o tabernáculo poucos dias; conforme a ordem do Senhor eles acampavam, e também conforme a ordem do Senhor, partiam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3986, 21, "Outras vezes a nuvem permanecia somente desde o entardecer até o amanhecer, e quando se levantava pela manhã, eles partiam. De dia ou de noite, sempre que a nuvem se levantava, eles partiam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3987, 22, "Quer a nuvem ficasse sobre o tabernáculo dois dias, quer um mês, quer mais tempo, os israelitas permaneciam no acampamento e não partiam; mas, quando ela se levantava, partiam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3988, 23, "Conforme a ordem do Senhor acampavam, e conforme a ordem do Senhor partiam. Nesse meio tempo, cumpriam suas responsabilidades para com o Senhor, de acordo com as suas ordens, anunciadas por Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3989, 1, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3990, 2, """Faça duas cornetas de prata batida a fim de usá-las para reunir a comunidade e para dar aos acampamentos o sinal para partirem.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3991, 3, "Quando as duas cornetas tocarem, a comunidade inteira se reunirá diante de você, à entrada da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3992, 4, "Se apenas uma tocar, os líderes, chefes dos clãs de Israel, se reunirão diante de você.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3993, 5, "Quando a corneta der um toque de alerta, as tribos acampadas a leste deverão partir.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3994, 6, "Ao som do segundo toque, os acampamentos do lado sul partirão. O toque de alerta será o sinal para partir.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3995, 7, "Para reunir a assembléia, faça soar as cornetas, mas não com o mesmo toque.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3996, 8, """Os filhos de Arão, os sacerdotes, tocarão as cornetas. Este é um decreto perpétuo para vocês e para as suas gerações.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3997, 9, "Quando em sua terra vocês entrarem em guerra contra um adversário que os esteja oprimindo, toquem as cornetas; e o Senhor, o Deus de vocês se lembrará de vocês e os libertará dos seus inimigos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3998, 10, "Também em seus dias festivos, nas festas fixas e no primeiro dia de cada mês, vocês deverão tocar as cornetas por ocasião dos seus holocaustos e das suas ofertas de comunhão, e elas serão um memorial em favor de vocês perante o seu Deus. Eu sou o Senhor, o Deus de vocês"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (3999, 11, "No vigésimo dia do segundo mês do segundo ano, a nuvem se levantou de cima do tabernáculo que guarda as tábuas da aliança.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4000, 12, "Então os israelitas partiram do deserto do Sinai e viajaram por etapas, até que a nuvem pousou no deserto de Parã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4001, 13, "Assim partiram pela primeira vez, conforme a ordem do Senhor anunciada por Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4002, 14, "Os exércitos do acampamento de Judá partiram primeiro, junto à sua bandeira. Naassom, filho de Aminadabe, estava no comando.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4003, 15, "Natanael, filho de Zuar, comandava os exércitos da tribo de Issacar,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4004, 16, "e Eliabe, filho de Helom, chefiava os exércitos da tribo de Zebulom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4005, 17, "Quando o tabernáculo era desmontado, os gersonitas e os meraritas o carregavam e partiam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4006, 18, "Os exércitos do acampamento de Rúben partiram em seguida, junto à sua bandeira. Elizur, filho de Sedeur, estava no comando.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4007, 19, "Selumiel, filho de Zurisadai, comandava os exércitos da tribo de Simeão,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4008, 20, "e Eliasafe, filho de Deuel, chefiava os exércitos da tribo de Gade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4009, 21, "Então os coatitas partiam carregando as coisas sagradas. Antes que eles chegassem, o tabernáculo já deveria estar armado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4010, 22, "Os exércitos do acampamento de Efraim partiram em seguida, junto à sua bandeira. Elisama, filho de Amiúde, estava no comando.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4011, 23, "Gamaliel, filho de Pedazur, comandava os exércitos da tribo de Manassés,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4012, 24, "e Abidã, filho de Gideoni, os exércitos da tribo de Benjamim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4013, 25, "Finalmente, partiram os exércitos do acampamento de Dã, junto à sua bandeira, como retaguarda para todos os acampamentos. Aieser, filho de Amisadai, estava no comando.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4014, 26, "Pagiel, filho de Ocrã, comandava os exércitos da tribo de Aser,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4015, 27, "e Aira, filho de Enã, a divisão da tribo de Naftali.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4016, 28, "Essa era a ordem que os exércitos israelitas seguiam quando se punham em marcha.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4017, 29, "Então Moisés disse a Hobabe, filho do midianita Reuel, sogro de Moisés: ""Estamos partindo para o lugar sobre o qual o Senhor disse: ‘Eu o darei a vocês’. Venha conosco e lhe trataremos bem, pois o Senhor prometeu boas coisas para Israel"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4018, 30, "Ele respondeu: ""Não, não irei; voltarei para a minha terra e para o meu povo"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4019, 31, "Moisés, porém, disse: ""Por favor, não nos deixe. Você sabe onde devemos acampar no deserto e pode ser o nosso guia.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4020, 32, "Se vier conosco, partilharemos com você todas as coisas boas que o Senhor nos der"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4021, 33, "Então eles partiram do monte do Senhor e viajaram três dias. A arca da aliança do Senhor foi à frente deles durante aqueles três dias para encontrar um lugar para descansarem.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4022, 34, "A nuvem do Senhor estava sobre eles de dia, sempre que partiam de um acampamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4023, 35, "Sempre que a arca partia, Moisés dizia: ""Levanta-te, ó Senhor! Sejam espalhados os teus inimigos e fujam de diante de ti os teus adversários"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4024, 36, "Sempre que a arca parava, ele dizia: ""Volta, ó Senhor, para os incontáveis milhares de Israel"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4025, 1, "Aconteceu que o povo começou a queixar-se das suas dificuldades aos ouvidos do Senhor. Quando ele os ouviu, a sua ira acendeu-se e fogo da parte do Senhor queimou entre eles e consumiu algumas extremidades do acampamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4026, 2, "Então o povo clamou a Moisés, este orou ao Senhor, e o fogo extinguiu-se.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4027, 3, "Por isso aquele lugar foi chamado Taberá, porque o fogo da parte do Senhor queimou entre eles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4028, 4, "Um bando de estrangeiros que havia no meio deles encheu-se de gula, e até os próprios israelitas tornaram a queixar-se, e diziam: ""Ah, se tivéssemos carne para comer!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4029, 5, "Nós nos lembramos dos peixes que comíamos de graça no Egito, e também dos pepinos, das melancias, dos alhos porós, das cebolas e dos alhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4030, 6, "Mas agora perdemos o apetite; nunca vemos nada, a não ser este maná! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4031, 7, "O maná era como semente de coentro e tinha aparência de resina.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4032, 8, "O povo saía recolhendo o maná nas redondezas, e o moía num moinho manual ou socava-o num pilão; depois cozinhava o maná e com ele fazia bolos. Tinha gosto de bolo amassado com azeite de oliva.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4033, 9, "Quando o orvalho caía sobre o acampamento à noite, também caía o maná.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4034, 10, "Moisés ouviu gente de todas as famílias se queixando, cada uma à entrada de sua tenda. Então acendeu-se a ira do Senhor, e isso pareceu mal a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4035, 11, "E ele perguntou ao Senhor: ""Por que trouxeste este mal sobre o teu servo? Foi por não te agradares de mim, que colocaste sobre os meus ombros a responsabilidade de todo esse povo?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4036, 12, "Por acaso fui eu quem o concebeu? Fui eu quem o trouxe à luz? Por que me pedes para carregá-lo nos braços, como uma ama carrega um recém-nascido, a levá-lo à terra que prometeste sob juramento aos seus antepassados?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4037, 13, "Onde conseguirei carne para todo esse povo? Eles ficam se queixando contra mim, dizendo: ‘Dê-nos carne para comer! ’", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4038, 14, "Não posso levar todo esse povo sozinho; essa responsabilidade é grande demais para mim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4039, 15, "Se é assim que vais me tratar, mata-me agora mesmo; se te agradas de mim, não me deixes ver a minha própria ruína"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4040, 16, "E o Senhor disse a Moisés: ""Reúna setenta autoridades de Israel, que você sabe que são líderes e supervisores entre o povo. Leve-os à Tenda do Encontro, para que estejam ali com você.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4041, 17, "Eu descerei e falarei com você; e tirarei do Espírito que está sobre você e o porei sobre eles. Eles o ajudarão na árdua responsabilidade de conduzir o povo, de modo que você não tenha que assumir tudo sozinho.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4042, 18, """Diga ao povo: Consagrem-se para amanhã, pois vocês comerão carne. O Senhor os ouviu quando se queixaram a ele, dizendo: ‘Ah, se tivéssemos carne para comer! Estávamos melhor no Egito! ’ Agora o Senhor lhes dará carne, e vocês a comerão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4043, 19, "Vocês não comerão carne apenas um dia, ou dois, ou cinco, ou dez ou vinte,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4044, 20, "mas um mês inteiro, até que lhes saia carne pelo nariz e vocês tenham nojo dela, porque rejeitaram o Senhor, que está no meio de vocês, e se queixaram a ele, dizendo: ‘Por que saímos do Egito? ’ """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4045, 21, "Disse, porém, Moisés: ""Aqui estou eu no meio de seiscentos mil homens de pé, e dizes: ‘Darei a eles carne para comerem durante um mês inteiro! ’", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4046, 22, "Será que haveria o suficiente para eles se todos os rebanhos fossem abatidos? Será que haveria o suficiente para eles se todos os peixes do mar fossem apanhados? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4047, 23, "O Senhor respondeu a Moisés: ""Estará limitado o poder do Senhor? Agora você verá se a minha palavra se cumprirá ou não"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4048, 24, "Então Moisés saiu e contou ao povo o que o Senhor tinha dito. Reuniu setenta autoridades dentre eles e os dispôs ao redor da Tenda.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4049, 25, "O Senhor desceu na nuvem e lhe falou, e tirou do Espírito que estava sobre ele e o pôs sobre as setenta autoridades. Quando o Espírito veio sobre eles, profetizaram, mas depois nunca mais tornaram a fazê-lo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4050, 26, "Entretanto, dois homens, chamados Eldade e Medade, tinham ficado no acampamento. Ambos estavam na lista das autoridades, mas não tinham ido para a Tenda. O Espírito também veio sobre eles, e profetizaram no acampamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4051, 27, "Então, certo jovem correu e contou a Moisés: ""Eldade e Medade estão profetizando no acampamento"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4052, 28, "Josué, filho de Num, que desde jovem era auxiliar de Moisés, interferiu e disse: ""Moisés, meu senhor, proíba-os! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4053, 29, "Mas Moisés respondeu: ""Você está com ciúmes por mim? Quem dera todo o povo do Senhor fosse profeta e que o Senhor pusesse o seu Espírito sobre eles! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4054, 30, "Então Moisés e as autoridades de Israel voltaram para o acampamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4055, 31, "Depois disso, veio um vento da parte do Senhor que trouxe codornizes do mar e as fez cair por todo o acampamento, a uma altura de noventa centímetros, espalhando-as em todas as direções até num raio de uma caminhada de um dia.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4056, 32, "Durante todo aquele dia e aquela noite e durante todo o dia seguinte, o povo saiu e recolheu codornizes. Ninguém recolheu menos de dez barris. Então eles as estenderam para secar ao redor de todo o acampamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4057, 33, "Mas, enquanto a carne ainda estava entre os seus dentes e antes que a ingerissem, a ira do Senhor acendeu-se contra o povo, e ele o feriu com uma praga terrível.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4058, 34, "Por isso o lugar foi chamado Quibrote-Hataavá, porque ali foram enterrados os que tinham sido dominados pela gula.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4059, 35, "De Quibrote-Hataavá o povo partiu para Hazerote, e lá ficou.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4060, 1, "Miriã e Arão começaram a criticar Moisés porque ele havia se casado com uma mulher cuxita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4061, 2, """Será que o Senhor tem falado apenas por meio de Moisés? "", perguntaram. ""Também não tem ele falado por meio de nós? "" E o Senhor ouviu isso.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4062, 3, "Ora, Moisés era um homem muito paciente, mais do que qualquer outro que havia na terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4063, 4, "Imediatamente o Senhor disse a Moisés, a Arão e a Miriã: ""Dirijam-se à Tenda do Encontro, vocês três"". E os três foram para lá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4064, 5, "Então o Senhor desceu numa coluna de nuvem e, pondo-se à entrada da Tenda, chamou Arão e Miriã. Os dois vieram à frente,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4065, 6, "e ele disse: ""Ouçam as minhas palavras: Quando entre vocês há um profeta do Senhor, a ele me revelo em visões, em sonhos falo com ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4066, 7, "Não é assim, porém, com meu servo Moisés, que é fiel em toda a minha casa.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4067, 8, "Com ele falo face a face, claramente, e não por enigmas; e ele vê a forma do Senhor. Por que não temeram criticar meu servo Moisés? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4068, 9, "Então a ira do Senhor acendeu-se contra eles, e ele os deixou.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4069, 10, "Quando a nuvem se afastou da Tenda, Miriã estava leprosa; sua aparência era como a da neve. Arão voltou-se para ela, viu que ela estava com lepra", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4070, 11, "e disse a Moisés: ""Por favor, meu senhor, não nos castigue pelo pecado que tão tolamente cometemos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4071, 12, "Não permita que ela fique como um feto abortado que sai do ventre de sua mãe com a metade do corpo destruído"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4072, 13, "Então Moisés clamou ao Senhor: ""Ó Deus, por misericórdia, cura-a! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4073, 14, "O Senhor respondeu a Moisés: ""Se o pai dela lhe tivesse cuspido no rosto, não estaria ela envergonhada sete dias? Que fique isolada fora do acampamento sete dias; depois ela poderá ser trazida de volta"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4074, 15, "Então Miriã ficou isolada sete dias fora do acampamento, e o povo não partiu enquanto ela não foi trazida de volta.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4075, 16, "Depois disso, partiram de Hazerote e acamparam no deserto de Parã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4076, 1, "E o Senhor disse a Moisés: """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4077, 2, "Envie alguns homens em missão de reconhecimento à terra de Canaã, terra que dou aos israelitas. Envie um líder de cada tribo dos seus antepassados"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4078, 3, "Assim Moisés os enviou do deserto de Parã, conforme a ordem do Senhor. Todos eles eram chefes dos israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4079, 4, "São estes os seus nomes: da tribo de Rúben, Samua, filho de Zacur;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4080, 5, "da tribo de Simeão, Safate, filho de Hori;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4081, 6, "da tribo de Judá, Calebe, filho de Jefoné;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4082, 7, "da tribo de Issacar, Jigeal, filho de José;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4083, 8, "da tribo de Efraim, Oséias, filho de Num;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4084, 9, "da tribo de Benjamim, Palti, filho de Rafu;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4085, 10, "da tribo de Zebulom, Gadiel, filho de Sodi;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4086, 11, "da tribo de José, isto é, da tribo de Manassés, Gadi, filho de Susi;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4087, 12, "da tribo de Dã, Amiel, filho de Gemali;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4088, 13, "da tribo de Aser, Setur, filho de Micael;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4089, 14, "da tribo de Naftali, Nabi, filho de Vofsi;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4090, 15, "da tribo de Gade, Güel, filho de Maqui.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4091, 16, "São esses os nomes dos homens que Moisés enviou em missão de reconhecimento do território. ( A Oséias, filho de Num, Moisés deu o nome de Josué. )", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4092, 17, "Quando Moisés os enviou para observarem Canaã, disse: ""Subam pelo Neguebe e prossigam até a região montanhosa.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4093, 18, "Vejam como é a terra e se o povo que vive lá é forte ou fraco, se são muitos ou poucos;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4094, 19, "se a terra em que habitam é boa ou ruim; se as cidades em que vivem são cidades sem muros ou fortificadas;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4095, 20, "se o solo é fértil ou pobre; se existe ali floresta ou não. Sejam corajosos! Tragam alguns frutos da terra"". Era a época do início da colheita das uvas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4096, 21, "Eles subiram e observaram a terra desde o deserto de Zim até Reobe, na direção de Lebo-Hamate.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4097, 22, "Subiram do Neguebe e chegaram a Hebrom, onde viviam Aimã, Sesai e Talmai, descendentes de Enaque. ( Hebrom havia sido construída sete anos antes de Zoã, no Egito. )", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4098, 23, "Quando chegaram ao vale de Escol, cortaram um ramo do qual pendia um único cacho de uvas. Dois deles carregaram o cacho, pendurado numa vara. Colheram também romãs e figos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4099, 24, "Aquele lugar foi chamado vale de Escol por causa do cacho de uvas que os israelitas cortaram ali.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4100, 25, "Ao fim de quarenta dias eles voltaram da missão de reconhecimento daquela terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4101, 26, "Eles então retornaram a Moisés e a Arão e a toda a comunidade de Israel em Cades, no deserto de Parã, onde prestaram relatório a eles e a toda a comunidade de Israel, e lhes mostraram os frutos da terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4102, 27, "E deram o seguinte relatório a Moisés: ""Entramos na terra à qual você nos enviou, onde manam leite e mel! Aqui estão alguns frutos dela.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4103, 28, "Mas o povo que lá vive é poderoso, e as cidades são fortificadas e muito grandes. Também vimos descendentes de Enaque.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4104, 29, "Os amalequitas vivem no Neguebe; os hititas, os jebuseus e os amorreus vivem na região montanhosa; os cananeus vivem perto do mar e junto ao Jordão"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4105, 30, "Então Calebe fez o povo calar-se perante Moisés e disse: ""Subamos e tomemos posse da terra. É certo que venceremos! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4106, 31, "Mas os homens que tinham ido com ele disseram: ""Não podemos atacar aquele povo; é mais forte do que nós"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4107, 32, "E espalharam entre os israelitas um relatório negativo acerca daquela terra. Disseram: ""A terra para a qual fomos em missão de reconhecimento devora os que nela vivem. Todos os que vimos são de grande estatura.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4108, 33, "Vimos também os gigantes, os descendentes de Enaque, diante de quem parecíamos gafanhotos, a nós e a eles"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4109, 1, "Naquela noite toda a comunidade começou a chorar em alta voz.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4110, 2, "Todos os israelitas queixaram-se contra Moisés e contra Arão, e toda a comunidade lhes disse: ""Quem dera tivéssemos morrido no Egito! Ou neste deserto!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4111, 3, "Por que o Senhor está nos trazendo para esta terra? Só para nos deixar cair à espada? Nossas mulheres e nossos filhos serão tomados como despojo de guerra. Não seria melhor voltar para o Egito? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4112, 4, "E disseram uns aos outros: ""Escolheremos um chefe e voltaremos para o Egito! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4113, 5, "Então Moisés e Arão prostraram-se, rosto em terra, diante de toda a assembléia dos israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4114, 6, "Josué, filho de Num, e Calebe, filho de Jefoné, dentre os que haviam observado a terra, rasgaram as suas vestes", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4115, 7, "e disseram a toda à comunidade dos israelitas: ""A terra que percorremos em missão de reconhecimento é excelente.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4116, 8, "Se o Senhor se agradar de nós, ele nos fará entrar nessa terra, onde manam leite e mel, e a dará a nós.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4117, 9, "Somente não sejam rebeldes contra o Senhor. E não tenham medo do povo da terra, porque nós os devoraremos como se fossem pão. A proteção deles se foi, mas o Senhor está conosco. Não tenham medo deles! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4118, 10, "Mas a comunidade toda falou em apedrejá-los. Então a glória do Senhor apareceu a todos os israelitas na Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4119, 11, "E o Senhor disse a Moisés: ""Até quando este povo me tratará com pouco caso? Até quando se recusará a crer em mim, apesar de todos os sinais que realizei entre eles?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4120, 12, "Eu os ferirei com praga e os destruirei, mas farei de você uma nação maior e mais forte do que eles"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4121, 13, "Moisés disse ao Senhor: ""Então os egípcios ouvirão que pelo teu poder fizeste este povo sair dentre eles,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4122, 14, "e falarão disso aos habitantes desta terra. Eles ouviram que tu, ó Senhor, estás com este povo e que te vêem face a face, Senhor, e que a tua nuvem paira sobre eles, e que vais adiante deles numa coluna de nuvem de dia e numa coluna de fogo de noite.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4123, 15, "Se exterminares este povo, as nações que ouvirem falar do que fizeste dirão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4124, 16, "‘O Senhor não conseguiu levar esse povo à terra que lhes prometeu em juramento; por isso os matou no deserto’.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4125, 17, """Mas agora, que a força do Senhor se manifeste, segundo prometeste:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4126, 18, "‘O Senhor é muito paciente e grande em fidelidade, e perdoa a iniqüidade e a rebelião, se bem que não deixa o pecado sem punição, e castiga os filhos pela iniqüidade dos pais até a terceira e quarta geração’.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4127, 19, "Segundo a tua grande fidelidade, perdoa a iniqüidade deste povo, assim como a tens perdoado desde que saíram do Egito até agora"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4128, 20, "O Senhor respondeu: ""Eu os perdoei, conforme você pediu.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4129, 21, "No entanto, juro pela glória do Senhor que enche toda a terra,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4130, 22, "que nenhum dos que viram a minha glória e os sinais miraculosos que realizei no Egito e no deserto, e me puseram à prova e me desobedeceram dez vezes —", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4131, 23, "nenhum deles chegará a ver a terra que prometi com juramento aos seus antepassados. Ninguém que me tratou com desprezo a verá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4132, 24, "Mas, como o meu servo Calebe tem outro espírito e me segue com integridade, eu o farei entrar na terra que foi observar, e seus descendentes a herdarão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4133, 25, "Visto que os amalequitas e os cananeus habitam nos vales, amanhã dêem meia-volta e partam em direção ao deserto pelo caminho que vai para o mar Vermelho"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4134, 26, "Disse mais o Senhor a Moisés e a Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4135, 27, """Até quando esta comunidade ímpia se queixará contra mim? Tenho ouvido as queixas desses israelitas murmuradores.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4136, 28, "Diga-lhes: ‘Juro pelo meu nome, declara o Senhor, que farei a vocês tudo o que pediram:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4137, 29, "Cairão neste deserto os cadáveres de todos vocês, de vinte anos para cima, que foram contados no recenseamento e que se queixaram contra mim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4138, 30, "Nenhum de vocês entrará na terra que, com mão levantada, jurei dar-lhes para sua habitação, exceto Calebe, filho de Jefoné, e Josué, filho de Num.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4139, 31, "Mas, quanto aos seus filhos, sobre os quais vocês disseram que seriam tomados como despojo de guerra, eu os farei entrar para desfrutarem a terra que vocês rejeitaram.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4140, 32, "O cadáveres de vocês, porém, cairão neste deserto.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4141, 33, "Os filhos de vocês serão pastores aqui durante quarenta anos, sofrendo pela infidelidade de vocês, até que o último cadáver de vocês seja destruído no deserto.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4142, 34, "Durante quarenta anos vocês sofrerão a conseqüência dos seus pecados e experimentarão a minha rejeição; cada ano corresponderá a cada um dos quarenta dias em que vocês observaram a terra’.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4143, 35, "Eu, o Senhor, falei, e certamente farei essas coisas a toda esta comunidade ímpia, que conspirou contra mim. Encontrarão o seu fim neste deserto; aqui morrerão"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4144, 36, "Os homens enviados por Moisés em missão de reconhecimento daquela terra voltaram e fizeram toda a comunidade queixar-se contra ele ao espalharem um relatório negativo;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4145, 37, "esses homens responsáveis por espalhar o relatório negativo sobre a terra morreram subitamente de praga perante o Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4146, 38, "De todos os que foram observar a terra, somente Josué, filho de Num, e Calebe, filho de Jefoné, sobreviveram.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4147, 39, "Quando Moisés transmitiu essas palavras a todos os israelitas, eles choraram amargamente.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4148, 40, "Na madrugada seguinte subiram para o alto da região montanhosa, e disseram: ""Subiremos ao lugar que o Senhor prometeu, pois cometemos pecado"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4149, 41, "Moisés, porém, disse: ""Por que vocês estão desobedecendo à ordem do Senhor? Isso não terá sucesso!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4150, 42, "Não subam, porque o Senhor não está com vocês. Vocês serão derrotados pelos inimigos,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4151, 43, "pois os amalequitas e os cananeus os enfrentarão ali, e vocês cairão à espada. Visto que deixaram de seguir ao Senhor, ele não estará com vocês"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4152, 44, "Apesar disso, eles subiram desafiadoramente ao alto da região montanhosa, mas nem Moisés nem a arca da aliança do Senhor saíram do acampamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4153, 45, "Então os amalequitas e os cananeus que lá viviam desceram e os derrotaram e os perseguiram até Hormá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4154, 1, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4155, 2, """Diga o seguinte aos israelitas: Quando entrarem na terra que lhes dou para sua habitação,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4156, 3, "e apresentarem ao Senhor uma oferta, de bois ou de ovelhas, preparada no fogo como aroma agradável ao Senhor, seja holocausto, seja sacrifício, para cumprir um voto ou como oferta voluntária ou como oferta relativa a uma festa,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4157, 4, "aquele que trouxer a sua oferta apresentará também ao Senhor uma oferta de cereal de um jarro da melhor farinha amassada com um litro de óleo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4158, 5, "Para cada cordeiro do holocausto ou do sacrifício, prepare um litro de vinho como oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4159, 6, """Para um carneiro, prepare uma oferta de cereal de dois jarros da melhor farinha com um litro de óleo", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4160, 7, "e um litro de vinho como oferta derramada. Apresente-a como aroma agradável ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4161, 8, """Quando algum de vocês preparar um novilho para holocausto ou para sacrifício para cumprir voto especial, ou como oferta de comunhão ao Senhor,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4162, 9, "traga com o novilho uma oferta de cereal de três jarros da melhor farinha amassada com meio galão de óleo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4163, 10, "Traga também meio galão de vinho para a oferta derramada. Será uma oferta preparada no fogo, de aroma agradável ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4164, 11, "Cada novilho ou carneiro, ou cordeiro ou cabrito, deverá ser preparado dessa maneira.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4165, 12, "Façam isso com cada animal, com tantos quantos vocês prepararem.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4166, 13, """Todo o que for natural da terra deverá proceder dessa maneira quando trouxer uma oferta preparada no fogo de aroma agradável ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4167, 14, "E se um estrangeiro que vive entre vocês, ou entre os descendentes de vocês, apresentar uma oferta preparada no fogo, de aroma agradável ao Senhor, deverá fazer o mesmo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4168, 15, "A assembléia deverá ter as mesmas leis, que valerão tanto para vocês como para o estrangeiro que vive entre vocês; este é um decreto perpétuo pelas suas gerações, que, perante o Senhor, valerá tanto para vocês quanto para o estrangeiro residente.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4169, 16, "A mesma lei e ordenança se aplicará tanto a vocês como ao estrangeiro residente"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4170, 17, "O Senhor disse ainda a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4171, 18, """Diga aos israelitas: Quando vocês entrarem na terra para onde os levo", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4172, 19, "e comerem do fruto da terra, apresentem uma porção como contribuição ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4173, 20, "Apresentem um bolo feito das primícias da farinha de vocês. Apresentem-no como contribuição da sua colheita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4174, 21, "Em todas as suas gerações vocês apresentarão das primícias da farinha uma contribuição ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4175, 22, """Mas se vocês pecarem e deixarem de cumprir todos esses mandamentos", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4176, 23, "— tudo o que o Senhor lhes ordenou por meio de Moisés, desde o dia em que os ordenou e pelas suas gerações —", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4177, 24, "e se isso for feito sem intenção e não for do conhecimento da comunidade, toda a comunidade terá que oferecer um novilho para o holocausto de aroma agradável ao Senhor. Também apresentarão com sua oferta de cereal uma oferta derramada, conforme as prescrições, e um bode como oferta pelo pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4178, 25, "O sacerdote fará propiciação por toda a comunidade de Israel, e eles serão perdoados, pois o seu pecado não foi intencional e eles trouxeram ao Senhor uma oferta preparada no fogo e uma oferta pelo pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4179, 26, "A comunidade de Israel toda e os estrangeiros residentes entre eles serão perdoados, porque todo o povo esteve envolvido num pecado involuntário.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4180, 27, """Se, contudo, apenas uma pessoa pecar sem intenção, ela terá que trazer uma cabra de um ano como oferta pelo pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4181, 28, "O sacerdote fará propiciação pela pessoa que pecar, cometendo uma falta involuntária perante o Senhor, e ela será perdoada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4182, 29, "Somente uma lei haverá para todo aquele que pecar sem intenção, seja ele israelita nato, seja estrangeiro residente.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4183, 30, """Mas todo aquele que pecar com atitude desafiadora, seja natural da terra, seja estrangeiro residente, insulta ao Senhor, e será eliminado do meio do seu povo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4184, 31, "Por ter desprezado a palavra do Senhor e quebrado os seus mandamentos, terá que ser eliminado; sua culpa estará sobre ele"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4185, 32, "Certo dia, quando os israelitas estavam no deserto, encontraram um homem recolhendo lenha no dia de sábado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4186, 33, "Aqueles que o encontraram recolhendo lenha levaram-no a Moisés, a Arão e a toda a comunidade,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4187, 34, "que o prenderam, porque não sabiam o que deveria ser feito com ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4188, 35, "Então o Senhor disse a Moisés: ""O homem terá que ser executado. Toda a comunidade o apedrejará fora do acampamento"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4189, 36, "Assim, toda a comunidade o levou para fora do acampamento e o apedrejou até à morte, conforme o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4190, 37, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4191, 38, """Diga o seguinte aos israelitas: Façam borlas nas extremidades das suas roupas e ponham um cordão azul em cada uma delas; façam isso por todas as suas gerações.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4192, 39, "Quando virem essas borlas vocês se lembrarão de todos os mandamentos do Senhor, para que lhes obedeçam e não se prostituam nem sigam as inclinações dos seus corações e dos seus olhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4193, 40, "Assim vocês se lembrarão de obedecer a todos os meus mandamentos, e para o seu Deus vocês serão um povo consagrado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4194, 41, "Eu sou o Senhor, o seu Deus, que os trouxe do Egito para ser o Deus de vocês. Eu sou o Senhor, o seu Deus"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4195, 1, "Corá, filho de Isar, neto de Coate, bisneto de Levi, reuniu Datã e Abirão, filhos de Eliabe, e Om, filho de Pelete, todos da tribo de Rúben,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4196, 2, "e eles se insurgiram contra Moisés. Com eles estavam duzentos e cinqüenta israelitas, líderes bem conhecidos na comunidade e que haviam sido nomeados membros do concílio.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4197, 3, "Eles se ajuntaram contra Moisés e Arão, e lhes disseram: ""Basta! A assembléia toda é santa, cada um deles é santo, e o Senhor está no meio deles. Então, por que vocês se colocam acima da assembléia do Senhor? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4198, 4, "Quando ouviu isso, Moisés prostrou-se, rosto em terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4199, 5, "Depois disse a Corá e a todos os seus seguidores: ""Pela manhã o Senhor mostrará quem lhe pertence e fará aproximar-se dele aquele que é santo, o homem a quem ele escolher.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4200, 6, "Você, Corá, e todos os seus seguidores deverão fazer o seguinte: peguem incensarios", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4201, 7, "e amanhã coloquem neles fogo e incenso perante o Senhor. Quem o Senhor escolher será o homem consagrado. Basta, levitas! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4202, 8, "Moisés disse também a Corá: ""Agora ouçam-me, levitas!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4203, 9, "Não lhes é suficiente que o Deus de Israel os tenha separado do restante da comunidade de Israel e os tenha trazido para junto de si a fim de realizarem o trabalho no tabernáculo do Senhor e para estarem preparados para servir a comunidade?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4204, 10, "Ele trouxe você e todos os seus irmãos levitas para junto dele, e agora vocês querem também o sacerdócio?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4205, 11, "É contra o Senhor que você e todos os seus seguidores se ajuntaram! Quem é Arão, para que se queixem contra ele? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4206, 12, "Então Moisés mandou chamar Datã e Abirão, filhos de Eliabe. Mas eles disseram: ""Nós não iremos!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4207, 13, "Não lhe basta nos ter tirado de uma terra onde manam leite e mel para matar-nos no deserto? E ainda quer se fazer chefe sobre nós?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4208, 14, "Além disso, você não nos levou a uma terra onde manam leite e mel, nem nos deu uma herança de campos e vinhas. Você pensa que pode cegar os olhos destes homens? Nós não iremos! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4209, 15, "Moisés indignou-se e disse ao Senhor: ""Não aceites a oferta deles. Não tomei deles nem sequer um jumento, nem prejudiquei a nenhum deles"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4210, 16, "Moisés disse a Corá: ""Você e todos os seus seguidores terão que apresentar-se amanhã ao Senhor, você, eles e Arão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4211, 17, "Cada homem pegará o seu incensário, nele colocará incenso e o apresentará ao Senhor. Serão duzentos e cinqüenta incensários ao todo. Você e Arão também apresentarão os seus incensários"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4212, 18, "Assim, cada um deles pegou o seu incensário, acendeu o incenso, e se colocou com Moisés e com Arão à entrada da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4213, 19, "Quando Corá reuniu todos os seus seguidores em oposição a eles à entrada da Tenda do Encontro, a glória do Senhor apareceu a toda a comunidade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4214, 20, "E o Senhor disse a Moisés e a Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4215, 21, """Afastem-se dessa comunidade para que eu acabe com eles imediatamente"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4216, 22, "Mas Moisés e Arão prostraram-se, rosto em terra, e disseram: ""Ó Deus, Deus que a todos dá vida, ficarás tu irado contra toda a comunidade quando um só homem pecou? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4217, 23, "Então o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4218, 24, """Diga à comunidade que se afaste das tendas de Corá, Datã e Abirão"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4219, 25, "Moisés levantou-se e foi para onde estavam Datã e Abirão, e as autoridades de Israel o seguiram.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4220, 26, "Ele advertiu a comunidade: ""Afastem-se das tendas desses ímpios! Não toquem em nada do que pertence a eles, senão vocês serão eliminados por causa dos pecados deles"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4221, 27, "Eles se afastaram das tendas de Corá, Datã e Abirão. Datã e Abirão tinham saído e estavam de pé, à entrada de suas tendas, junto com suas mulheres, seus filhos e suas crianças pequenas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4222, 28, "E disse Moisés: ""Assim vocês saberão que o Senhor me enviou para fazer todas essas coisas e que isso não partiu de mim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4223, 29, "Se estes homens tiverem morte natural e experimentarem somente aquilo que normalmente acontece aos homens, então o Senhor não me enviou.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4224, 30, "Mas, se o Senhor fizer acontecer algo totalmente novo, e a terra abrir a sua boca e os engolir, junto com tudo o que é deles, e eles descerem vivos ao Sheol, então vocês saberão que estes homens desprezaram o Senhor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4225, 31, "Assim que Moisés acabou de dizer tudo isso, o chão debaixo deles fendeu-se", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4226, 32, "e a terra abriu a sua boca e os engoliu juntamente com suas famílias, com todos os seguidores de Corá e com todos os seus bens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4227, 33, "Desceram vivos à sepultura, com tudo o que possuíam; a terra fechou-se sobre eles, e pereceram dentre a assembléia.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4228, 34, "Diante dos seus gritos, todos os israelitas ao redor fugiram, gritando: ""A terra vai nos engolir também! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4229, 35, "Então veio fogo da parte do Senhor e consumiu os duzentos e cinqüenta homens que ofereciam incenso.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4230, 36, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4231, 37, """Diga a Eleazar, filho do sacerdote Arão, que apanhe os incensários dentre os restos fumegastes e espalhe as brasas, porque os incensários são santos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4232, 38, "Os incensários dos homens que pelo seu pecado perderam a vida serão batidos em forma de lâminas e servirão de revestimento do altar, pois foram apresentados ao Senhor e se tornaram sagrados. Que sejam um sinal para os israelitas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4233, 39, "O sacerdote Eleazar juntou os incensários de bronze que tinham sido apresentados pelos que foram consumidos pelo fogo. Os incensários foram batidos e serviram de revestimento do altar,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4234, 40, "como o Senhor tinha dito por meio de Moisés. Isso foi feito como memorial para os israelitas, a fim de que ninguém que não fosse descendente de Arão, queimasse incenso perante o Senhor, para não sofrer o que Corá e os seus seguidores sofreram.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4235, 41, "No dia seguinte toda a comunidade de Israel começou a queixar-se contra Moisés e Arão, dizendo: ""Vocês mataram o povo do Senhor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4236, 42, "Quando, porém, a comunidade se ajuntou contra Moisés e contra Arão, e eles se voltaram para a Tenda do Encontro, repentinamente a nuvem a cobriu e a glória do Senhor apareceu.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4237, 43, "Então Moisés e Arão foram para a frente da Tenda do Encontro,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4238, 44, "e o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4239, 45, """Saia do meio dessa comunidade para que eu acabe com eles imediatamente"". Mas eles se prostraram, rosto em terra;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4240, 46, "e Moisés disse a Arão: ""Pegue o seu incensário e ponha incenso nele, com fogo tirado do altar, e vá depressa até a comunidade para fazer propiciação por eles, porque saiu grande ira da parte do Senhor e a praga começou"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4241, 47, "Arão fez o que Moisés ordenou e correu para o meio da assembléia. A praga já havia começado entre o povo, mas Arão ofereceu o incenso e fez propiciação por eles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4242, 48, "Arão se pôs entre os mortos e os vivos, e a praga cessou.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4243, 49, "Foram catorze mil e setecentos os que morreram daquela praga, além dos que haviam morrido por causa de Corá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4244, 50, "Então Arão voltou a Moisés, à entrada da Tenda do Encontro, pois a praga já havia cessado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4245, 1, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4246, 2, """Peça aos israelitas que tragam doze varas, uma de cada líder das tribos. Escreva o nome de cada líder em sua vara.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4247, 3, "Na vara de Levi escreva o nome de Arão, pois é preciso que haja uma vara para cada chefe das tribos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4248, 4, "Deposite-as na Tenda do Encontro, em frente da arca das tábuas da aliança, onde eu me encontro com vocês.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4249, 5, "A vara daquele que eu escolher florescerá, e eu me livrarei dessa constante queixa dos israelitas contra vocês"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4250, 6, "Assim Moisés falou aos israelitas, e seus líderes deram-lhe doze varas, uma de cada líder das tribos, e a vara de Arão estava entre elas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4251, 7, "Moisés depositou as varas perante o Senhor na tenda que guarda as tábuas da aliança.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4252, 8, "No dia seguinte Moisés entrou na tenda e viu que a vara de Arão, que representava a tribo de Levi, tinha brotado, produzindo botões e flores, além de amêndoas maduras.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4253, 9, "Então Moisés retirou todas as varas da presença do Senhor e as levou a todos os israelitas. Eles viram as varas, e cada líder pegou a sua.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4254, 10, "O Senhor disse a Moisés: ""Ponha de volta a vara de Arão em frente da arca das tábuas da aliança, para ser guardada como uma advertência para os rebeldes. Isso porá fim à queixa deles contra mim, para que não morram"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4255, 11, "Moisés fez conforme o Senhor lhe havia ordenado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4256, 12, "Os israelitas disseram a Moisés: ""Nós morreremos! Estamos perdidos, estamos todos perdidos!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4257, 13, "Todo aquele que se aproximar do santuário do Senhor morrerá. Será que todos nós vamos morrer? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4258, 1, "O Senhor disse a Arão: ""Você, os seus filhos e a família de seu pai serão responsáveis pelas ofensas contra o santuário; você e seus filhos serão responsáveis pelas ofensas cometidas no exercício do sacerdócio.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4259, 2, "Traga também os seus irmãos levitas, que pertencem à tribo de seus antepassados, para se unirem a você e o ajudarem quando você e seus filhos ministrarem perante a tenda que guarda as tábuas da aliança.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4260, 3, "Eles ficarão a seu serviço e cuidarão também do serviço da Tenda, mas não poderão aproximar-se dos utensílios do santuário ou do altar; se o fizerem morrerão, tanto eles como vocês.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4261, 4, "Eles se unirão a vocês e terão a responsabilidade de cuidar da Tenda do Encontro, de todo o trabalho que ali se faz. Ninguém mais poderá aproximar-se de vocês.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4262, 5, """Vocês terão a responsabilidade de cuidar do santuário e do altar, para que não torne a cair a ira divina sobre os israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4263, 6, "Eu mesmo escolhi os seus irmãos, os levitas, dentre os israelitas como um presente para vocês, dedicados ao Senhor para fazerem o trabalho da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4264, 7, "Mas somente você e seus filhos poderão servir como sacerdotes em tudo o que se refere ao altar e ao que se encontra além do véu. Dou a vocês o serviço do sacerdócio como um presente. Qualquer pessoa não autorizada que se aproximar do santuário terá que ser executada"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4265, 8, "Então o Senhor disse a Arão: ""Eu mesmo o tornei responsável pelas contribuições trazidas a mim; todas as ofertas sagradas que os israelitas me derem, eu as dou como porção a você e a seus filhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4266, 9, "Das ofertas santíssimas vocês terão a parte que é poupada do fogo. Dentre todas as dádivas que me trouxerem como ofertas santíssimas, seja oferta de cereal, seja pelo pecado, seja de reparação, tal parte pertence a você e a seus filhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4267, 10, "Comam-na como algo santíssimo; todos os do sexo masculino a comerão. Considerem-na santa.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4268, 11, """Também dou a você, e a seus filhos e filhas, por decreto perpétuo, as contribuições de todas as ofertas ritualmente movidas apresentadas pelos israelitas. Todos os da sua família que estiverem cerimonialmente puros poderão comê-las.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4269, 12, """Dou a você o melhor azeite e o melhor vinho novo e o melhor trigo que eles apresentarem ao Senhor como primeiros frutos da colheita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4270, 13, "Todos os primeiros frutos da terra que trouxerem ao Senhor serão de vocês. Todos os da sua família que estiverem cerimonialmente puros, poderão comê-los.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4271, 14, """Tudo o que em Israel for consagrado a Deus pertencerá a você.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4272, 15, "O primeiro nascido de todo ventre, oferecido ao Senhor, seja homem, seja animal, será seu. Mas você deverá resgatar todo filho mais velho, como também toda primeira cria de animais impuros.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4273, 16, "Quando tiverem um mês de idade, você deverá resgatá-los pelo preço de resgate estabelecido em sessenta gramas de prata, com base no peso padrão do santuário, que são doze gramas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4274, 17, """Não resgate, porém, a primeira cria de uma vaca, de uma ovelha ou de uma cabra. Derrame o sangue deles sobre o altar e queime a sua gordura como uma oferta preparada no fogo, de aroma agradável ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4275, 18, "A carne desses animais pertence a você, como também o peito da oferta movida e a coxa direita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4276, 19, "Tudo aquilo que for separado dentre todas as dádivas sagradas que os israelitas apresentarem ao Senhor eu dou a você e a seus filhos e filhas como decreto perpétuo. É uma aliança de sal perpétua perante o Senhor, para você e para os seus descendentes"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4277, 20, "Disse ainda o Senhor a Arão: ""Você não terá herança na terra deles, nem terá porção entre eles; eu sou a sua porção e a sua herança entre os israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4278, 21, """Dou aos levitas todos os dízimos em Israel como retribuição pelo trabalho que fazem ao servirem na Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4279, 22, "De agora em diante os israelitas não poderão aproximar-se da Tenda do Encontro, caso contrário, sofrerão as conseqüências do seu pecado e morrerão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4280, 23, "É dever dos levitas fazer o trabalho na Tenda do Encontro e assumir a responsabilidade pelas ofensas contra ela. Este é um decreto perpétuo pelas suas gerações. Eles não receberão herança alguma entre os israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4281, 24, "Em vez disso, dou como herança aos levitas os dízimos que os israelitas apresentarem como contribuição ao Senhor. É por isso que eu disse que eles não teriam herança alguma entre os israelitas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4282, 25, "O Senhor disse depois a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4283, 26, """Diga o seguinte aos levitas: Quando receberem dos israelitas o dízimo que lhes dou como herança, vocês deverão apresentar um décimo daquele dízimo como contribuição pertencente ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4284, 27, "Essa contribuição será à do trigo tirado da eira e do vinho do tanque de prensar uvas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4285, 28, "Assim, vocês apresentarão uma contribuição ao Senhor de todos os dízimos que receberam dos israelitas. Desses dízimos vocês darão a contribuição do Senhor ao sacerdote Arão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4286, 29, "E deverão apresentar como contribuição ao Senhor a melhor parte, a parte sagrada de tudo o que for dado a vocês.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4287, 30, """Diga aos levitas: Quando vocês apresentarem a melhor parte, ela será considerada equivalente ao produto da eira e do tanque de prensar uvas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4288, 31, "Vocês e suas famílias poderão comer dessa porção em qualquer lugar, pois é o salário pelo trabalho de vocês na Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4289, 32, "Ao apresentarem a melhor parte, vocês não se tornarão culpados e não profanarão as ofertas sagradas dos israelitas, para que não morram"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4290, 1, "Disse também o Senhor a Moisés e a Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4291, 2, """Esta é uma exigência da lei que o Senhor ordenou: Mande os israelitas trazerem uma novilha vermelha, sem defeito e sem mancha, sobre a qual nunca tenha sido colocada uma canga.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4292, 3, "Vocês a darão ao sacerdote Eleazar; ela será levada para fora do acampamento e sacrificada na presença dele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4293, 4, "Então o sacerdote Eleazar pegará um pouco do sangue com o dedo e o aspergirá sete vezes, na direção da entrada da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4294, 5, "Na presença dele a novilha será queimada: o couro, a carne, o sangue e o excremento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4295, 6, "O sacerdote apanhará um pedaço de madeira de cedro, hissopo e lã vermelha e os atirará ao fogo que estiver queimando a novilha.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4296, 7, "Depois disso o sacerdote lavará as suas roupas e se banhará com água. Então poderá entrar no acampamento, mas estará impuro até o cair da tarde.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4297, 8, "Aquele que queimar a novilha também lavará as suas roupas e se banhará com água, e também estará impuro até o cair da tarde.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4298, 9, """Um homem cerimonialmente puro recolherá as cinzas da novilha e as colocará num local puro, fora do acampamento. Serão guardadas pela comunidade de Israel para uso na água da purificação, para a purificação de pecados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4299, 10, "Aquele que recolher as cinzas da novilha também lavará as suas roupas, e ficará impuro até o cair da tarde. Este é um decreto perpétuo, tanto para os israelitas como para os estrangeiros residentes.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4300, 11, """Quem tocar num cadáver humano ficará impuro durante sete dias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4301, 12, "Deverá purificar-se com essa água no terceiro e no sétimo dia; então estará puro. Mas, se não se purificar no terceiro e no sétimo dia, não estará puro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4302, 13, "Quem tocar num cadáver humano e não se purificar, contamina o tabernáculo do Senhor e será eliminado de Israel. Ficará impuro porque a água da purificação não foi derramada sobre ele; sua impureza permanece sobre ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4303, 14, """Esta é a lei que se aplica quando alguém morre numa tenda: quem entrar na tenda e quem nela estiver ficará impuro sete dias,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4304, 15, "e qualquer recipiente que não estiver bem fechado ficará impuro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4305, 16, """Quem estiver no campo e tocar em alguém que tenha sido morto à espada, ou em que tenha sofrido morte natural, ou num osso humano, ou num túmulo, ficará impuro durante sete dias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4306, 17, """Pela pessoa impura, colocarão um pouco das cinzas do holocausto de purificação num jarro e derramarão água da fonte por cima.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4307, 18, "Então um homem cerimonialmente puro pegará hissopo, molhará na água e a aspergirá sobre a tenda, sobre todos os utensílios e sobre todas as pessoas que estavam ali. Também a aspergirá sobre todo aquele que tiver tocado num osso humano, ou num túmulo, ou em alguém que tenha sido morto ou que tenha sofrido morte natural.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4308, 19, "Aquele que estiver puro a aspergirá sobre a pessoa impura no terceiro e no sétimo dia, e no sétimo dia deverá purificá-la. Aquele que estiver sendo purificado lavará as suas roupas e se banhará com água, e naquela tarde estará puro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4309, 20, "Mas, se aquele que estiver impuro não se purificar, será eliminado da assembléia, pois contaminou o santuário do Senhor. A água da purificação não foi aspergida sobre ele, e ele está impuro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4310, 21, "Este é um decreto perpétuo para eles. ""O homem que aspergir a água da purificação também lavará as suas roupas, e todo aquele que tocar na água da purificação ficará impuro até o cair da tarde.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4311, 22, "Qualquer coisa na qual alguém que estiver impuro tocar se tornará impura, e qualquer pessoa que nela tocar ficará impura até o cair da tarde"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4312, 1, "No primeiro mês toda a comunidade de Israel chegou ao deserto de Zim e ficou em Cades. Ali Miriã morreu e foi sepultada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4313, 2, "Não havia água para a comunidade, e o povo se juntou contra Moisés e contra Arão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4314, 3, "Discutiram com Moisés e disseram: ""Quem dera tivéssemos morrido quando os nossos irmãos caíram mortos perante o Senhor!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4315, 4, "Por que vocês trouxeram a assembléia do Senhor a este deserto, para que nós e os nossos rebanhos morrêssemos aqui?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4316, 5, "Por que vocês nos tiraram do Egito e nos trouxeram para este lugar terrível? Aqui não há cereal, nem figos, nem uvas, nem romãs, nem água para beber! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4317, 6, "Moisés e Arão saíram de diante da assembléia para a entrada da Tenda do Encontro e se prostraram, rosto em terra, e a glória do Senhor lhes apareceu.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4318, 7, "E o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4319, 8, """Pegue a vara, e com o seu irmão Arão reúna a comunidade e diante desta fale àquela rocha, e ela verterá água. Vocês tirarão água da rocha para a comunidade e os rebanhos beberem"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4320, 9, "Então Moisés pegou a vara que estava diante do Senhor, como este lhe havia ordenado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4321, 10, "Moisés e Arão reuniram a assembléia em frente da rocha, e Moisés disse: ""Escutem, rebeldes, será que teremos que tirar água desta rocha para lhes dar? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4322, 11, "Então Moisés ergueu o braço e bateu na rocha duas vezes com a vara. Jorrou água, e a comunidade e os rebanhos beberam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4323, 12, "O Senhor, porém, disse a Moisés e a Arão: ""Como vocês não confiaram em mim para honrar minha santidade à vista dos israelitas, vocês não conduzirão esta comunidade para a terra que lhes dou"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4324, 13, "Essas foram as águas de Meribá, onde os israelitas discutiram com o Senhor e onde ele manifestou sua santidade entre eles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4325, 14, "De Cades, Moisés enviou mensageiros ao rei de Edom, dizendo: ""Assim diz o teu irmão Israel: Tu sabes de todas as dificuldades que vieram sobre nós.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4326, 15, "Os nossos antepassados desceram para o Egito, e ali vivemos durante muitos anos. Os egípcios, porém, nos maltrataram, como também a eles,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4327, 16, "mas quando clamamos ao Senhor, ele ouviu o nosso clamor, enviou um anjo e nos tirou do Egito. ""Agora estamos em Cades, cidade na fronteira do teu território.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4328, 17, "Deixa-nos atravessar a tua terra. Não passaremos por nenhuma plantação ou vinha, nem beberemos água de poço algum. Passaremos pela estrada do rei e não nos desviaremos nem para a direita nem para a esquerda, até que tenhamos atravessado o teu território"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4329, 18, "Mas Edom respondeu: ""Vocês não poderão passar por aqui; se tentarem, nós os atacaremos com a espada"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4330, 19, "E os israelitas disseram: ""Iremos pela estrada principal; se nós e os nossos rebanhos bebermos de tua água, pagaremos por ela. Queremos apenas atravessar a pé, e nada mais"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4331, 20, "Mas Edom insistiu: ""Vocês não poderão atravessar"". Então Edom os atacou com um exército grande e poderoso.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4332, 21, "Visto que Edom se recusou a deixá-los atravessar o seu território, Israel desviou-se dele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4333, 22, "Toda a comunidade israelita partiu de Cades e chegou ao monte Hor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4334, 23, "Naquele monte, perto da fronteira de Edom, o Senhor disse a Moisés e a Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4335, 24, """Arão será reunido aos seus antepassados. Não entrará na terra que dou aos israelitas, porque vocês dois se rebelaram contra a minha ordem junto às águas de Meribá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4336, 25, "Leve Arão e seu filho Eleazar para o alto do monte Hor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4337, 26, "Tire as vestes de Arão e coloque-as em seu filho Eleazar, pois Arão será reunido aos seus antepassados; ele morrerá ali"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4338, 27, "Moisés fez conforme o Senhor ordenou; subiram o monte Hor à vista de toda a comunidade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4339, 28, "Moisés tirou as vestes de Arão e as colocou em seu filho Eleazar. E Arão morreu no alto do monte. Depois disso, Moisés e Eleazar desceram do monte,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4340, 29, "e, quando toda a comunidade soube que Arão tinha morrido, toda a nação de Israel pranteou por ele durante trinta dias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4341, 1, "Quando o rei cananeu de Arade, que vivia no Neguebe, soube que Israel vinha pela estrada de Atarim, atacou os israelitas e capturou alguns deles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4342, 2, "Então Israel fez este voto ao Senhor: ""Se entregares este povo em nossas mãos, destruiremos totalmente as suas cidades"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4343, 3, "O Senhor ouviu o pedido de Israel e lhes entregou os cananeus. Israel os destruiu completamente, a eles e às suas cidades; de modo que o lugar foi chamado Hormá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4344, 4, "Partiram eles do monte Hor pelo caminho do mar Vermelho, para contornarem a terra de Edom. Mas o povo ficou impaciente no caminho", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4345, 5, "e falou contra Deus e contra Moisés, dizendo: ""Por que vocês nos tiraram do Egito para morrermos no deserto? Não há pão! Não há água! E nós detestamos esta comida miserável! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4346, 6, "Então o Senhor enviou serpentes venenosas que morderam o povo, e muitos morreram.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4347, 7, "O povo foi a Moisés e disse: ""Pecamos quando falamos contra o Senhor e contra você. Ore pedindo ao Senhor que tire as serpentes do meio de nós"". E Moisés orou pelo povo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4348, 8, "O Senhor disse a Moisés: ""Faça uma serpente e coloque-a no alto de um poste; quem for mordido e olhar para ela viverá"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4349, 9, "Moisés fez então uma serpente de bronze e a colocou num poste. Quando alguém era mordido por uma serpente e olhava para a serpente de bronze, permanecia vivo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4350, 10, "Os israelitas partiram e acamparam em Obote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4351, 11, "Depois partiram de Obote e acamparam em Ijé-Abarim, no deserto defronte de Moabe, ao leste.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4352, 12, "Dali partiram e acamparam no vale de Zerede.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4353, 13, "Partiram dali e acamparam do outro lado do Arnom, que fica no deserto que se estende até o território amorreu. O Arnom é a fronteira de Moabe, entre Moabe e os amorreus.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4354, 14, "É por isso que se diz no Livro das Guerras do Senhor: ""... Vaebe, em Sufá, e os vales, o Arnom", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4355, 15, "e as ravinas dos vales que se estendem até a cidade de Ar e chegam até a fronteira de Moabe"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4356, 16, "De lá prosseguiram até Beer, ao poço do qual o Senhor disse a Moisés: ""Reúna o povo, e eu lhe darei água"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4357, 17, "Então Israel cantou esta canção: ""Brote água, ó poço! Cantem a seu respeito,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4358, 18, "a respeito do poço que os líderes cavaram, que os nobres abriram com cetros e cajados"". Então saíram do deserto para Mataná,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4359, 19, "de Mataná para Naaliel, de Naaliel para Bamote,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4360, 20, "e de Bamote para o vale de Moabe, onde o topo do Pisga defronta com o deserto de Jesimom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4361, 21, "Israel enviou mensageiros para dizer a Seom, rei dos amorreus:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4362, 22, """Deixa-nos atravessar a tua terra. Não entraremos em nenhuma plantação, em nenhuma vinha, nem beberemos água de poço algum. Passaremos pela estrada do rei até que tenhamos atravessado o teu território"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4363, 23, "Seom, porém, não deixou Israel atravessar o seu território. Convocou todo o seu exército e atacou Israel no deserto. Quando chegou a Jaza, lutou contra Israel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4364, 24, "Porém Israel o destruiu com a espada e tomou-lhe as terras desde o Arnom até o Jaboque, até o território dos amonitas, pois Jazar estava na fronteira dos amonitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4365, 25, "Israel capturou todas as cidades dos amorreus e as ocupou, inclusive Hesbom e todos os seus povoados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4366, 26, "Hesbom era a cidade de Seom, rei dos amorreus, que havia lutado contra o antigo rei de Moabe, tendo lhe tomado todas as suas terras até o Arnom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4367, 27, "É por isso que os poetas dizem: ""Venham a Hesbom! Seja ela reconstruída; seja restaurada a cidade de Seom!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4368, 28, """Fogo saiu de Hesbom, uma chama da cidade de Seom; consumiu Ar, de Moabe, os senhores do alto Arnom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4369, 29, "Ai de você, Moabe! Você está destruído, ó povo de Camos! Ele fez de seus filhos, fugitivos e de suas filhas, prisioneiras de Seom, rei dos amorreus.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4370, 30, """Mas nós os derrotamos; Hesbom está destruída por todo o caminho até Dibom. Nós os arrasamos até Nofá, e até Medeba"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4371, 31, "Assim Israel habitou na terra dos amorreus.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4372, 32, "Moisés enviou espiões a Jazar, e os israelitas tomaram os povoados ao redor e expulsaram os amorreus que ali estavam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4373, 33, "Depois voltaram e subiram pelo caminho de Basã, e Ogue, rei de Basã, com todo o seu exército, marchou para enfrentá-los em Edrei.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4374, 34, "Mas o Senhor disse a Moisés: ""Não tenha medo dele, pois eu o entreguei a você, juntamente com todo o seu exército e com a sua terra. Você fará com ele o que fez com Seom, rei dos amorreus, que habitava em Hesbom"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4375, 35, "Então eles o derrotaram, bem como os seus filhos e todo o seu exército, não lhes deixando sobrevivente algum. E tomaram posse da terra dele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4376, 1, "Os israelitas partiram e acamparam nas campinas de Moabe, para além do Jordão, perto de Jericó.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4377, 2, "Balaque, filho de Zipor, viu tudo o que Israel tinha feito aos amorreus,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4378, 3, "e Moabe teve muito medo do povo, porque era muita gente. Moabe teve pavor dos israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4379, 4, "Então os moabitas disseram aos líderes de Midiã: ""Essa multidão devorará tudo o que há ao nosso redor, como o boi devora o capim do pasto"". Balaque, filho de Zipor, rei de Moabe naquela época,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4380, 5, "enviou mensageiros para chamar Balaão, filho de Beor, que estava em Petor, perto do Rio, em sua terra natal. A mensagem de Balaque dizia: ""Um povo que saiu do Egito cobre a face da terra e se estabeleceu perto de mim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4381, 6, "Venha agora lançar uma maldição contra ele, pois é forte demais para mim. Talvez então eu tenha condições de derrotá-lo e de expulsá-lo da terra. Pois sei que quem você abençoa é abençoado, e quem você amaldiçoa é amaldiçoado"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4382, 7, "Os líderes de Moabe e os de Midiã partiram, levando consigo o preço para os encantamentos mágicos. Quando chegaram, comunicaram a Balaão o que Balaque tinha dito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4383, 8, "Disse-lhes Balaão: ""Passem a noite aqui, e eu lhes trarei a resposta que o Senhor me der"". E os líderes moabitas ficaram com ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4384, 9, "Deus veio a Balaão e lhe perguntou: ""Quem são esses homens que estão com você? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4385, 10, "Balaão respondeu a Deus: ""Balaque, filho de Zipor, rei de Moabe, enviou-me esta mensagem:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4386, 11, "‘Um povo que saiu do Egito cobre a face da terra. Venha agora lançar uma maldição contra ele. Talvez então eu tenha condições de derrotá-lo e de expulsá-lo’ "".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4387, 12, "Mas Deus disse a Balaão: ""Não vá com eles. Você não poderá amaldiçoar este povo, porque é povo abençoado"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4388, 13, "Na manhã seguinte Balaão se levantou e disse aos líderes de Balaque: ""Voltem para a sua terra, pois o Senhor não permitiu que eu os acompanhe"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4389, 14, "Os líderes moabitas voltaram a Balaque e lhe disseram: ""Balaão recusou-se a acompanhar-nos"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4390, 15, "Balaque enviou outros líderes, em maior número e mais importantes do que os primeiros.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4391, 16, "Eles foram a Balaão e lhe disseram: ""Assim diz Balaque, filho de Zipor: Que nada o impeça de vir a mim,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4392, 17, "porque o recompensarei generosamente e farei tudo o que você me disser. Venha, por favor, e lance para mim uma maldição contra este povo"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4393, 18, "Balaão, porém, respondeu aos conselheiros de Balaque: ""Mesmo que Balaque me desse o seu palácio cheio de prata e de ouro, eu não poderia fazer coisa alguma, grande ou pequena, que vá além da ordem do Senhor meu Deus.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4394, 19, "Agora, fiquem também vocês aqui esta noite, e eu descobrirei o que mais o Senhor tem para dizer-me"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4395, 20, "Naquela noite Deus veio a Balaão e lhe disse: ""Visto que esses homens vieram chamá-lo, vá com eles, mas faça apenas o que eu lhe disser"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4396, 21, "Balaão levantou-se pela manhã, pôs a sela sobre a sua jumenta e foi com os líderes de Moabe.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4397, 22, "Mas acendeu-se a ira de Deus quando ele foi, e o anjo do Senhor pôs-se no caminho para impedi-lo de prosseguir. Balaão ia montado em sua jumenta, e seus dois servos o acompanhavam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4398, 23, "Quando a jumenta viu o anjo do Senhor parado no caminho, empunhando uma espada, saiu do caminho e foi-se pelo campo. Balaão bateu nela para fazê-la voltar ao caminho.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4399, 24, "Então o anjo do Senhor se pôs num caminho estreito entre duas vinhas, com muros dos dois lados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4400, 25, "Quando a jumenta viu o anjo do Senhor, encostou-se no muro, apertando o pé de Balaão contra ele. Por isso ele bateu nela de novo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4401, 26, "O anjo do Senhor foi adiante e se colocou num lugar estreito, e não havia espaço para desviar, nem para a direita nem para a esquerda.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4402, 27, "Quando a jumenta viu o anjo do Senhor, deitou-se debaixo de Balaão. Acendeu-se a ira de Balaão, que bateu nela com a sua vara.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4403, 28, "Então o Senhor abriu a boca da jumenta, e ela disse a Balaão: ""Que foi que eu lhe fiz, para você bater em mim três vezes? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4404, 29, "Balaão respondeu à jumenta: ""Você me fez de tolo! Quem dera eu tivesse uma espada na mão; eu a mataria agora mesmo"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4405, 30, "Mas a jumenta disse a Balaão: ""Não sou sua jumenta, que você sempre montou até o dia de hoje? Tenho eu o costume de fazer isso com você? "" ""Não"", disse ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4406, 31, "Então o Senhor abriu os olhos de Balaão, e ele viu o anjo do Senhor parado no caminho, empunhando a sua espada. Então Balaão inclinou-se e prostrou-se, rosto em terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4407, 32, "E o anjo do Senhor lhe perguntou: ""Por que você bateu três vezes em sua jumenta? Eu vim aqui para impedi-lo de prosseguir porque o seu caminho me desagrada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4408, 33, "A jumenta me viu e se afastou de mim por três vezes. Se ela não se afastasse a esta altura eu certamente o teria matado; mas a ela eu teria poupado"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4409, 34, "Balaão disse ao anjo do Senhor: ""Pequei. Não percebi que estavas parado no caminho para me impedires de prosseguir. Agora, se o que estou fazendo te desagrada, eu voltarei"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4410, 35, "Então o anjo do Senhor disse a Balaão: ""Vá com os homens, mas fale apenas o que eu lhe disser"". Assim Balaão foi com os príncipes de Balaque.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4411, 36, "Quando Balaque soube que Balaão estava chegando, foi ao seu encontro na cidade moabita da fronteira do Arnom, no limite do seu território.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4412, 37, "E Balaque disse a Balaão: ""Não mandei chamá-lo urgentemente? Por que não veio? Acaso não tenho condições de recompensá-lo? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4413, 38, """Aqui estou! "", respondeu Balaão. ""Mas, seria eu capaz de dizer alguma coisa? Direi somente o que Deus puser em minha boca"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4414, 39, "Então Balaão foi com Balaque até Quiriate-Huzote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4415, 40, "Balaque sacrificou bois e ovelhas, e deu parte da carne a Balaão e aos líderes que com ele estavam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4416, 41, "Na manhã seguinte Balaque levou Balaão até o alto de Bamote-Baal, de onde viu uma parte do povo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4417, 1, "Balaão disse a Balaque: ""Construa para mim aqui sete altares e prepare-me sete novilhos e sete carneiros"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4418, 2, "Balaque fez o que Balaão pediu, e os dois ofereceram um novilho e um carneiro em cada altar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4419, 3, "E Balaão disse a Balaque: ""Fique aqui junto ao seu holocausto, enquanto eu me retiro. Talvez o Senhor venha ao meu encontro. O que ele me revelar eu lhe contarei"". E foi para um monte.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4420, 4, "Deus o encontrou, e Balaão disse: ""Preparei sete altares, e em cada altar ofereci um novilho e um carneiro"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4421, 5, "O Senhor pôs uma mensagem na boca de Balaão e disse: ""Volte a Balaque e dê-lhe essa mensagem"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4422, 6, "Ele voltou a Balaque e o encontrou ao lado de seu holocausto, e com ele todos os líderes de Moabe.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4423, 7, "Então Balaão pronunciou este oráculo: ""Balaque trouxe-me de Arã, o rei de Moabe, buscou-me nas montanhas do Oriente. ‘Venha, amaldiçoe a Jacó para mim’, disse ele, ‘venha, pronuncie ameaças contra Israel! ’", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4424, 8, "Como posso amaldiçoar a quem Deus não amaldiçoou? Como posso pronunciar ameaças quem o Senhor não quis ameaçar?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4425, 9, "Dos cumes rochosos eu os vejo, dos montes eu os avisto. Vejo um povo que vive separado e não se considera como qualquer nação.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4426, 10, "Quem pode contar o pó de Jacó ou o número da quarta parte de Israel? Morra eu a morte dos justos, e seja o meu fim como o deles! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4427, 11, "Então Balaque disse a Balaão: ""Que foi que você me fez? Eu o chamei para amaldiçoar meus inimigos, mas você nada fez senão abençoá-los! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4428, 12, "E ele respondeu: ""Será que não devo dizer o que o Senhor põe em minha boca? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4429, 13, "Balaque lhe disse: ""Venha comigo a outro lugar de onde você poderá vê-los; você verá só uma parte, mas não todos eles. E dali amaldiçoe este povo para mim"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4430, 14, "Então ele o levou para o campo de Zofim, no topo do Pisga, e ali construiu sete altares e ofereceu um novilho e um carneiro em cada altar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4431, 15, "Balaão disse a Balaque: ""Fique aqui ao lado de seu holocausto enquanto vou me encontrar com ele ali adiante"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4432, 16, "Encontrando-se o Senhor com Balaão, pôs uma mensagem em sua boca e disse: ""Volte a Balaque e dê-lhe essa mensagem"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4433, 17, "Ele voltou e o encontrou ao lado de seu holocausto, e com ele os líderes de Moabe. Balaque perguntou-lhe: ""O que o Senhor disse? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4434, 18, "Então ele pronunciou este oráculo: ""Levante-se, Balaque, e ouça-me; escute-me, filho de Zipor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4435, 19, "Deus não é homem para que minta, nem filho de homem para que se arrependa. Acaso ele fala, e deixa de agir? Acaso promete, e deixa de cumprir?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4436, 20, "Recebi uma ordem para abençoar; ele abençoou, e não o posso mudar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4437, 21, "Nenhuma desgraça se vê em Jacó, nenhum sofrimento em Israel. O Senhor, o seu Deus, está com eles; o brado de aclamação do Rei está no meio deles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4438, 22, "Deus os está trazendo do Egito; eles têm a força do boi selvagem.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4439, 23, "Não há magia que possa contra Jacó, nem encantamento contra Israel. Agora se dirá de Jacó e de Israel: ‘Vejam o que Deus tem feito! ’", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4440, 24, "O povo se levanta como leoa; levanta-se como o leão, que não se deita até que devore a sua presa e beba o sangue das suas vítimas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4441, 25, "Balaque disse então a Balaão: ""Não os amaldiçoe nem os abençoe! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4442, 26, "Balaão respondeu: ""Não lhe disse que devo fazer tudo o que o Senhor disser? """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4443, 27, "Balaque disse a Balaão: ""Venha, deixe-me levá-lo a outro lugar. Talvez Deus se agrade que dali você os amaldiçoe para mim"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4444, 28, "E Balaque levou Balaão para o topo do Peor, de onde se vê o deserto de Jesimom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4445, 29, "Balaão disse a Balaque: ""Edifique-me aqui sete altares, e prepare-me sete novilhos e sete carneiros"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4446, 30, "Balaque fez o que Balaão disse, e ofereceu um novilho e um carneiro em cada altar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4447, 1, "Quando Balaão viu que agradava ao Senhor abençoar Israel, não recorreu à magia como nas outras vezes, mas voltou o rosto para o deserto.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4448, 2, "Então viu Israel acampado, tribo por tribo; e o Espírito de Deus veio sobre ele,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4449, 3, "e ele pronunciou este oráculo: ""Palavra de Balaão, filho de Beor, palavra daquele cujos olhos vêem claramente,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4450, 4, "palavra daquele que ouve as palavras de Deus, daquele que vê a visão que vem do Todo-poderoso, daquele que cai prostrado e vê com clareza:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4451, 5, """Quão belas são as suas tendas, ó Jacó, as suas habitações, ó Israel!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4452, 6, "Como vales estendem-se, como jardins que margeiam rios, como aloés plantados pelo Senhor, como cedros junto às águas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4453, 7, "Seus reservatórios de água transbordarão; suas lavouras serão bem irrigadas. ""O seu rei será maior do que Agague; o seu reino será exaltado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4454, 8, "Deus os está trazendo do Egito; eles têm a força do boi selvagem. Devoram nações inimigas e despedaçam seus ossos; com suas flechas os atravessam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4455, 9, "Como o leão e a leoa eles se abaixam e se deitam, quem ousará despertá-los? Sejam abençoados os que os abençoarem, e amaldiçoados os que os amaldiçoarem! """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4456, 10, "Então acendeu-se a ira de Balaque contra Balaão, e, batendo as palmas das mãos, disse: ""Eu o chamei para amaldiçoar meus inimigos, mas você já os abençoou três vezes!", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4457, 11, "Agora, fuja para a sua casa! Eu disse que lhe daria generosa recompensa, mas o Senhor o impediu de recebê-la"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4458, 12, "Mas Balaão respondeu a Balaque: ""Eu não disse aos mensageiros que você me enviou:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4459, 13, "Mesmo que Balaque me desse o seu palácio cheio de prata e de ouro, eu não poderia fazer coisa alguma de minha própria vontade, boa ou má, que vá além da ordem do Senhor, e que devo dizer somente o que o Senhor disser?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4460, 14, "Agora estou voltando para o meu povo, mas venha, deixe-me adverti-lo do que este povo fará ao seu povo nos dias futuros"". O Quarto Oráculo de Balaão", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4461, 15, "Então pronunciou este seu oráculo: ""Palavra de Balaão, filho de Beor, palavra daquele cujos olhos vêem claramente,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4462, 16, "daquele que ouve as palavras de Deus, que possui o conhecimento do Altíssimo, daquele que vê a visão que vem do Todo-poderoso, daquele que cai prostrado, e vê com clareza:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4463, 17, "Eu o vejo, mas não agora; eu o avisto, mas não de perto. Uma estrela surgirá de Jacó; um cetro se levantará de Israel. Ele esmagará as frontes de Moabe e o crânio de todos os descendentes de Sete.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4464, 18, "Edom será dominado; Seir, seu inimigo, também será dominado, mas Israel se fortalecerá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4465, 19, "De Jacó sairá o governo; ele destruirá os sobreviventes das cidades"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4466, 20, "Balaão viu Amaleque e pronunciou este oráculo: ""Amaleque foi o primeiro entre as nações, mas o seu fim será destruição"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4467, 21, "Depois viu os queneus e pronunciou este oráculo: ""Sua habitação é segura, seu ninho está firmado na rocha;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4468, 22, "todavia, vocês, queneus, serão destruídos quando Assur os levar prisioneiros"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4469, 23, "Finalmente pronunciou este oráculo: ""Ah, quem poderá viver quando Deus fizer isto?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4470, 24, "Navios virão da costa de Quitim e subjugarão Assur e Héber, mas o seu fim também será destruição"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4471, 25, "Então Balaão se levantou e voltou para casa, e Balaque seguiu o seu caminho.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4472, 1, "Enquanto Israel estava em Sitim, o povo começou a entregar-se à imoralidade sexual com mulheres moabitas,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4473, 2, "que os convidavam aos sacrifícios de seus deuses. O povo comia e se prostrava perante esses deuses.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4474, 3, "Assim Israel se juntou à adoração de Baal-Peor. E a ira do Senhor acendeu-se contra Israel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4475, 4, "E o Senhor disse a Moisés: ""Prenda todos os chefes desse povo, enforque-os diante do Senhor, à luz do sol, para que o fogo da ira do Senhor se afaste de Israel"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4476, 5, "Então Moisés disse aos juízes de Israel: ""Cada um de vocês terá que matar aqueles que dentre os seus homens se juntaram à adoração de Baal-Peor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4477, 6, "Um israelita trouxe para casa uma mulher midianita, na presença de Moisés e de toda a comunidade de Israel, que choravam à entrada da Tenda do Encontro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4478, 7, "Quando Finéias, filho de Eleazar, neto do sacerdote Arão, viu isso, apanhou uma lança,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4479, 8, "seguiu o israelita até o interior da tenda e atravessou os dois com a lança; atravessou o corpo do israelita e o da mulher. Então cessou a praga contra os israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4480, 9, "Mas os que morreram por causa da praga foram vinte e quatro mil.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4481, 10, "E o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4482, 11, """Finéias, filho de Eleazar, neto do sacerdote Arão, desviou a minha ira de sobre os israelitas, pois foi zeloso, com o mesmo zelo que tenho por eles, para que em meu zelo eu não os consumisse.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4483, 12, "Diga-lhe, pois, que estabeleço com ele a minha aliança de paz.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4484, 13, "Dele e dos seus descendentes será a aliança do sacerdócio perpétuo, porque ele foi zeloso pelo seu Deus e fez propiciação pelos israelitas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4485, 14, "O nome do israelita que foi morto com a midianita era Zinri, filho de Salu, líder de uma família simeonita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4486, 15, "E o nome da mulher midianita que morreu era Cosbi, filha de Zur, chefe de um clã midianita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4487, 16, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4488, 17, """Tratem os midianitas como inimigos e matem-nos,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4489, 18, "porque trataram vocês como inimigos quando os enganaram no caso de Peor e de Cosbi, filha de um líder midianita, mulher do povo deles que foi morta quando a praga que enviei por causa de Peor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4490, 1, "Depois da praga, o Senhor disse a Moisés e a Eleazar, filho do sacerdote Arão:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4491, 2, """Façam um recenseamento de toda a comunidade de Israel, segundo as suas famílias; contem todos os de vinte anos para cima que possam servir no exército de Israel"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4492, 3, "Nas campinas de Moabe, junto ao Jordão, do outro lado de Jericó, Moisés e o sacerdote Eleazar falaram com eles e disseram:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4493, 4, """Façam um recenseamento dos homens de vinte anos para cima"", conforme o Senhor tinha ordenado a Moisés. Estes foram os israelitas que saíram do Egito:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4494, 5, "Os descendentes de Rúben, filho mais velho de Israel, foram: de Enoque, o clã enoquita; de Palu, o clã paluíta;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4495, 6, "de Hezrom, o clã hezronita; de Carmi, o clã carmita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4496, 7, "Esses foram os clãs de Rúben; foram contados 43. 730 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4497, 8, "O filho de Palu foi Eliabe,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4498, 9, "e os filhos de Eliabe foram Nemuel, Datã e Abirão. Estes, Datã e Abirão, foram os líderes da comunidade que se rebelaram contra Moisés e contra Arão, estando entre os seguidores de Corá quando se rebelaram contra o Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4499, 10, "A terra abriu a boca e os engoliu juntamente com Corá, cujos seguidores morreram quando o fogo devorou duzentos e cinqüenta homens, que serviram como sinal de advertência.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4500, 11, "A descendência de Corá, contudo, não desapareceu.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4501, 12, "Os descendentes de Simeão segundo os seus clãs foram: de Nemuel, o clã nemuelita; de Jamim, o clã jaminita; de Jaquim, o clã jaquinita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4502, 13, "de Zerá, o clã zeraíta; de Saul, o clã saulita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4503, 14, "Esses foram os clãs de Simeão; havia 22. 200 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4504, 15, "Os descendentes de Gade segundo os seus clãs foram: de Zefom, o clã zefonita; de Hagi, o clã hagita; de Suni, o clã sunita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4505, 16, "de Ozni, o clã oznita; de Eri, o clã erita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4506, 17, "de Arodi, o clã arodita; de Areli, o clã arelita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4507, 18, "Esses foram os clãs de Gade; foram contados 40. 500 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4508, 19, "Er e Onã eram filhos de Judá, mas morreram em Canaã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4509, 20, "Os descendentes de Judá segundo os seus clãs foram: de Selá, o clã selanita; de Perez, o clã perezita; de Zerá, o clã zeraíta.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4510, 21, "Os descendentes de Perez foram: de Hezrom, o clã hezronita; de Hamul, o clã hamulita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4511, 22, "Esses foram os clãs de Judá; foram contados 76. 500 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4512, 23, "Os descendentes de Issacar segundo os seus clãs foram: de Tolá, o clã tolaíta; de Puá, o clã puvita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4513, 24, "de Jasube, o clã jasubita; de Sinrom, o clã sinronita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4514, 25, "Esses foram os clãs de Issacar; foram contados 64. 300 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4515, 26, "Os descendentes de Zebulom segundo os seus clãs foram: de Serede, o clã seredita; de Elom, o clã elonita; de Jaleel, o clã jaleelita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4516, 27, "Esses foram os clãs de Zebulom; foram contados 60. 500 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4517, 28, "Os descendentes de José segundo os seus clãs, por meio de Manassés e Efraim, foram:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4518, 29, "Os descendentes de Manassés: de Maquir, o clã maquirita ( Maquir foi o pai de Gileade ); de Gileade, o clã gileadita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4519, 30, "Estes foram os descendentes de Gileade: de Jezer, o clã jezerita; de Heleque, o clã helequita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4520, 31, "de Asriel, o clã asrielita; de Siquém, o clã siquemita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4521, 32, "de Semida, o clã semidaíta; de Héfer, o clã heferita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4522, 33, "( Zelofeade, filho de Héfer, não teve filhos; teve somente filhas, cujos nomes eram Maalá, Noa, Hogla, Milca e Tirza. )", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4523, 34, "Esses foram os clãs de Manassés; foram contados 52. 700 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4524, 35, "Os descendentes de Efraim segundo os seus clãs foram: de Sutela, o clã sutelaíta; de Bequer, o clã bequerita; de Taã, o clã taanita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4525, 36, "Estes foram os descendentes de Sutela: de Erã, o clã eranita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4526, 37, "Esses foram os clãs de Efraim; foram contados 32. 500 homens. Esses foram os descendentes de José segundo os seus clãs.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4527, 38, "Os descendentes de Benjamim segundo os seus clãs foram: de Belá, o clã belaíta; de Asbel, o clã asbelita; de Airã, o clã airamita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4528, 39, "de Sufã; o clã sufamita; de Hufã, o clã hufamita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4529, 40, "Os descendentes de Belá por meio de Arde e Naamã foram: de Arde, o clã ardita; de Naamã, o clã naamanita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4530, 41, "Esses foram os clãs de Benjamim; foram contados 45. 600 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4531, 42, "Os descendentes de Dã segundo os seus clãs foram: de Suã, o clã suamita. Esses foram os clãs de Dã:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4532, 43, "Todos eles eram clãs suamitas; foram contados 64. 400 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4533, 44, "Os descendentes de Aser segundo os seus clãs foram: de Imna, o clã imnaíta; de Isvi, o clã isvita; de Berias, o clã beriaíta;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4534, 45, "e dos descendentes de Berias: de Héber, o clã heberita; de Malquiel, o clã malquielita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4535, 46, "Aser teve uma filha chamada Sera.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4536, 47, "Esses foram os clãs de Aser; foram contados 53. 400 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4537, 48, "Os descendentes de Naftali segundo os seus clãs foram: de Jazeel, o clã jazeelita; de Guni, o clã gunita;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4538, 49, "de Jezer, o clã jezerita; de Silém, o clã silemita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4539, 50, "Esses foram os clãs de Naftali; foram contados 45. 400 homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4540, 51, "O número total dos homens de Israel foi 601. 730.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4541, 52, "Disse ainda o Senhor a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4542, 53, """A terra será repartida entre eles como herança, de acordo com o número dos nomes alistados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4543, 54, "A um clã maior dê uma herança maior, e a um clã menor, uma herança menor; cada um receberá a sua herança de acordo com o seu número de recenseados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4544, 55, "A terra, porém, será distribuída por sorteio. Cada um herdará sua parte de acordo com o nome da tribo de seus antepassados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4545, 56, "Cada herança será distribuída por sorteio entre os clãs maiores e entre os clãs menores"". O Segundo Recenseamento dos Levitas", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4546, 57, "Estes foram os levitas contados segundo os seus clãs: de Gérson, o clã gersonita; de Coate, o clã coatita; de Merari, o clã merarita.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4547, 58, "Estes também eram clãs levitas: o clã libnita; o clã hebronita; o clã malita; o clã musita; o clã coreíta. Coate foi o pai de Anrão;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4548, 59, "o nome da mulher de Anrão era Joquebede, descendente de Levi, que nasceu no Egito. Ela lhe deu à luz Arão, Moisés e Miriã, irmã deles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4549, 60, "Arão foi o pai de Nadabe, Abiú, Eleazar e Itamar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4550, 61, "Mas Nadabe e Abiú morreram quando apresentaram uma oferta com fogo profano perante o Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4551, 62, "O total de levitas do sexo masculino de um mês de idade para cima que foram contados foi 23. 000. Não foram contados junto com os outros israelitas porque não receberam herança entre eles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4552, 63, "São esses os que foram recenseados por Moisés e pelo sacerdote Eleazar quando contaram os israelitas nas campinas de Moabe, junto ao Jordão, do outro lado de Jericó.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4553, 64, "Nenhum deles estava entre os que foram contados por Moisés e pelo sacerdote Arão quando contaram os israelitas no deserto do Sinai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4554, 65, "Pois o Senhor tinha dito àqueles israelitas que iriam morrer no deserto, e nenhum deles sobreviveu, exceto Calebe, filho de Jefoné, e Josué, filho de Num.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4555, 1, "Aproximaram-se as filhas de Zelofeade, filho de Héfer, neto de Gileade, bisneto de Maquir, trineto de Manassés; pertencia aos clãs de Manassés, filho de José. Os nomes das suas filhas eram Maalá, Noa, Hogla, Milca e Tirza.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4556, 2, "Elas se prostraram à entrada da Tenda do Encontro diante de Moisés, do sacerdote Eleazar, dos líderes de toda a comunidade, e disseram:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4557, 3, """Nosso pai morreu no deserto. Ele não estava entre os seguidores de Corá, que se ajuntaram contra o Senhor, mas morreu por causa do seu próprio pecado e não deixou filhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4558, 4, "Por que o nome de nosso pai deveria desaparecer de seu clã por não ter tido um filho? Dê-nos propriedade entre os parentes de nosso pai"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4559, 5, "Moisés levou o caso perante o Senhor,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4560, 6, "e o Senhor lhe disse:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4561, 7, """As filhas de Zelofeade têm razão. Você lhes dará propriedade como herança entre os parentes do pai delas, e lhes passará a herança do pai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4562, 8, """Diga aos israelitas: Se um homem morrer e não deixar filho, transfiram a sua herança para a sua filha.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4563, 9, "Se ele não tiver filha, dêem a sua herança aos irmãos dele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4564, 10, "Se não tiver irmãos, dêem-na aos irmãos de seu pai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4565, 11, "Se ainda seu pai não tiver irmãos, dêem a herança ao parente mais próximo em seu clã"". Esta será uma exigência legal para os israelitas, como o Senhor ordenou a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4566, 12, "Então o Senhor disse a Moisés: ""Suba este monte da serra de Abarim e veja a terra que dei aos israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4567, 13, "Depois de vê-la, você também será reunido ao seu povo, como seu irmão Arão,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4568, 14, "pois, quando a comunidade se rebelou nas águas do deserto de Zim, vocês dois desobedeceram à minha ordem de honrar minha santidade perante eles"". Isso aconteceu nas águas de Meribá, em Cades, no deserto de Zim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4569, 15, "Moisés disse ao Senhor:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4570, 16, """Que o Senhor, o Deus que a todos dá vida, designe um homem como líder desta comunidade", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4571, 17, "para conduzi-los em suas batalhas, para que a comunidade do Senhor não seja como ovelhas sem pastor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4572, 18, "Então o Senhor disse a Moisés: ""Chame Josué, filho de Num, homem em quem está o Espírito, e imponha as mãos sobre ele.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4573, 19, "Faça-o apresentar-se ao sacerdote Eleazar e a toda a comunidade e o comissione na presença deles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4574, 20, "Dê-lhe parte da sua autoridade para que toda a comunidade de Israel lhe obedeça.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4575, 21, "Ele deverá apresentar-se ao sacerdote Eleazar, que lhe dará diretrizes ao consultar o Urim perante o Senhor. Josué e toda a comunidade dos israelitas seguirão suas instruções quando saírem para a batalha"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4576, 22, "Moisés fez como o Senhor lhe ordenou. Chamou Josué e o apresentou ao sacerdote Eleazar e a toda a comunidade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4577, 23, "Impôs as mãos sobre ele e o comissionou. Tudo conforme o Senhor tinha dito por meio de Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4578, 1, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4579, 2, """Ordene aos israelitas e diga-lhes: Tenham o cuidado de apresentar-me na época designada a comida para as minhas ofertas preparadas no fogo, como um aroma que me seja agradável.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4580, 3, "Diga-lhes: Esta é a oferta preparada no fogo que vocês apresentarão ao Senhor: dois cordeiros de um ano, sem defeito, como holocausto diário.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4581, 4, "Ofereçam um cordeiro pela manhã e um ao cair da tarde,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4582, 5, "juntamente com uma oferta de cereal de um jarro da melhor farinha amassada com um litro de azeite de olivas batidas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4583, 6, "Este é o holocausto diário instituído no monte Sinai de aroma agradável; é oferta dedicada ao Senhor, preparada no fogo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4584, 7, "A oferta derramada que a acompanha será um litro de bebida fermentada junto com cada cordeiro. Derramem a oferta de bebida para o Senhor no Lugar Santo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4585, 8, "Ofereçam o segundo cordeiro ao cair da tarde, juntamente com o mesmo tipo de oferta de cereal e de oferta derramada que vocês prepararem de manhã. É uma oferta preparada no fogo, de aroma agradável ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4586, 9, """No dia de sábado, façam uma oferta de dois cordeiros de um ano de idade e sem defeito, juntamente com a oferta derramada e com uma oferta de cereal de dois jarros da melhor farinha amassada com óleo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4587, 10, "Este é o holocausto para cada sábado, além do holocausto diário e da oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4588, 11, """No primeiro dia de cada mês, apresentem ao Senhor um holocausto de dois novilhos, um carneiro e sete cordeiros de um ano, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4589, 12, "Com cada novilho deverá haver uma oferta de cereal de três jarros da melhor farinha amassada com óleo; com o carneiro, uma oferta de cereal de dois jarros da melhor farinha amassada com óleo;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4590, 13, "e com cada cordeiro, uma oferta de cereal de um jarro da melhor farinha amassada com óleo. É um holocausto, de aroma agradável, uma oferta dedicada ao Senhor, preparada no fogo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4591, 14, "Com cada novilho deverá haver uma oferta derramada de meio galão de vinho; com o carneiro, um litro; e com cada cordeiro, um litro. É o holocausto mensal que deve ser oferecido cada lua nova durante o ano.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4592, 15, "Além do holocausto diário com a oferta derramada, um bode será oferecido ao Senhor como sacrifício pelo pecado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4593, 16, """No décimo quarto dia do primeiro mês é a Páscoa do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4594, 17, "No décimo quinto dia desse mês haverá uma festa; durante sete dias comam pão sem fermento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4595, 18, "No primeiro dia convoquem uma santa assembléia e não façam trabalho algum.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4596, 19, "Apresentem ao Senhor uma oferta preparada no fogo, um holocausto de dois novilhos, um carneiro e sete cordeiros de um ano, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4597, 20, "Com cada novilho preparem uma oferta de cereal de três jarros da melhor farinha amassada com óleo; com o carneiro, dois jarros;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4598, 21, "e com cada cordeiro, um jarro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4599, 22, "Ofereçam um bode como sacrifício pela culpa, para fazer propiciação por vocês.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4600, 23, "Apresentem essas ofertas além do holocausto diário oferecido pela manhã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4601, 24, "Façam assim diariamente, durante sete dias: apresentem a comida para a oferta preparada no fogo, de aroma agradável ao Senhor; isso será feito além do holocausto diário e da sua oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4602, 25, "No sétimo dia convoquem uma santa reunião e não façam trabalho algum.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4603, 26, """No dia da festa da colheita dos primeiros frutos, a Festa das Semanas, quando apresentarem ao Senhor uma oferta do cereal novo, convoquem uma santa assembléia e não façam trabalho algum.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4604, 27, "Apresentem um holocausto de dois novilhos, de um carneiro e de sete cordeiros de um ano como aroma agradável ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4605, 28, "Com cada novilho deverá haver uma oferta de cereal de três jarros da melhor farinha amassada com óleo; com o carneiro, dois jarros;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4606, 29, "e com cada um dos cordeiros, um jarro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4607, 30, "Ofereçam também um bode para fazer propiciação por vocês.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4608, 31, "Preparem tudo isso junto com a oferta derramada, além do holocausto diário e da oferta de cereal. Verifiquem que os animais sejam sem defeito"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4609, 1, """No dia primeiro do sétimo mês convoquem uma santa assembléia e não façam trabalho algum. Nesse dia vocês tocarão as trombetas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4610, 2, "Como aroma agradável ao Senhor, ofereçam um holocausto de um novilho, um carneiro e sete cordeiros de um ano, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4611, 3, "Com o novilho preparem uma oferta de cereal de três jarros da melhor farinha amassada com óleo; com o carneiro, dois jarros;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4612, 4, "e com cada um dos sete cordeiros, um jarro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4613, 5, "Ofereçam também um bode como sacrifício pelo pecado, para fazer propiciação por vocês,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4614, 6, "além dos holocaustos mensais e diários com as ofertas de cereal e com as ofertas derramadas, conforme prescritas. São ofertas preparadas no fogo, de aroma agradável ao Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4615, 7, """No dia dez desse sétimo mês convoquem uma santa assembléia. Vocês se humilharão a si mesmos e não farão trabalho algum.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4616, 8, "Apresentem como aroma agradável ao Senhor um holocausto de um novilho, de um carneiro e de sete cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4617, 9, "Com o novilho preparem uma oferta de cereais de três jarros da melhor farinha amassada com óleo; com o carneiro, dois jarros;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4618, 10, "e com cada um dos sete cordeiros, um jarro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4619, 11, "Ofereçam também um bode como sacrifício pelo pecado, além do sacrifício pelo pecado para fazer propiciação e o holocausto diário com a oferta de cereal e com as ofertas derramadas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4620, 12, """No décimo quinto dia do sétimo mês convoquem uma santa assembléia e não façam trabalho algum. Celebrem uma festa ao Senhor durante sete dias.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4621, 13, "Apresentem uma oferta preparada no fogo, de aroma agradável ao Senhor, um holocausto de treze novilhos, dois carneiros e catorze cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4622, 14, "Com cada um dos treze novilhos preparem uma oferta de cereal de três jarros da melhor farinha amassada com óleo; com cada um dos carneiros, dois jarros;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4623, 15, "e com cada um dos sete cordeiros, um jarro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4624, 16, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4625, 17, """No segundo dia preparem doze novilhos, dois carneiros e catorze cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4626, 18, "Com os novilhos, carneiros e cordeiros, preparem ofertas de cereal e ofertas derramadas, de acordo com o número especificado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4627, 19, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4628, 20, """No terceiro dia preparem onze novilhos, dois carneiros e catorze cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4629, 21, "Com os novilhos, carneiros e cordeiros, preparem ofertas de cereal e ofertas derramadas, de acordo com o número especificado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4630, 22, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4631, 23, """No quarto dia preparem dez novilhos, dois carneiros e catorze cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4632, 24, "Com os novilhos, carneiros e cordeiros, preparem ofertas de cereal e ofertas derramadas, de acordo com o número especificado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4633, 25, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4634, 26, """No quinto dia preparem nove novilhos, dois carneiros e catorze cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4635, 27, "Com os novilhos, carneiros e cordeiros, preparem ofertas de cereal e ofertas derramadas, de acordo com o número especificado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4636, 28, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4637, 29, """No sexto dia preparem oito novilhos, dois carneiros e catorze cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4638, 30, "Com os novilhos, carneiros e cordeiros, preparem ofertas de cereal e ofertas derramadas, de acordo com o número especificado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4639, 31, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4640, 32, """No sétimo dia preparem sete novilhos, dois carneiros e catorze cordeiros de um ano de idade, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4641, 33, "Com os novilhos, carneiros e cordeiros, preparem ofertas de cereal e ofertas derramadas, de acordo com o número especificado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4642, 34, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4643, 35, """No oitavo dia convoquem uma assembléia e não façam trabalho algum.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4644, 36, "Apresentem uma oferta preparada no fogo, de aroma agradável ao Senhor, um holocausto de um novilho, um carneiro e sete cordeiros de um ano, todos sem defeito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4645, 37, "Com o novilho, o carneiro e os cordeiros, preparem ofertas de cereal e ofertas derramadas, de acordo com o número especificado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4646, 38, "Ofereçam também um bode como sacrifício pelo pecado, além do holocausto diário com a oferta de cereal e com a oferta derramada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4647, 39, """Além dos votos que fizerem e das ofertas voluntárias, preparem isto para o Senhor nas festas que lhes são designadas: os holocaustos, as ofertas de cereal, as ofertas derramadas e as ofertas de comunhão. """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4648, 40, "E Moisés comunicou aos israelitas tudo o que o Senhor lhe tinha ordenado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4649, 1, "Moisés disse aos chefes das tribos de Israel: ""É isto que o Senhor ordena:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4650, 2, "Quando um homem fizer um voto ao Senhor ou um juramento que o obrigar a algum compromisso, não poderá quebrar a sua palavra, mas terá que cumprir tudo o que disse.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4651, 3, """Quando uma moça que ainda vive na casa de seu pai fizer um voto ao Senhor ou obrigar-se por um compromisso", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4652, 4, "e seu pai souber do voto ou compromisso, mas nada lhe disser, então todos os votos e cada um dos compromissos a que se obrigou serão válidos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4653, 5, "Mas, se o pai a proibir quando souber do voto, nenhum dos votos ou dos compromissos a que se obrigou será válido; o Senhor a livrará porque o seu pai a proibiu.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4654, 6, """Se ela se casar depois de fazer um voto ou depois de seus lábios proferirem uma promessa precipitada pela qual se obriga a si mesma", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4655, 7, "e o seu marido o souber, mas nada lhe disser no dia em que ficar sabendo, então os seus votos ou compromissos a que ela se obrigou serão válidos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4656, 8, "Mas, se o seu marido a proibir quando o souber, anulará o voto que a obriga ou a promessa precipitada pela qual ela se obrigou, e o Senhor a livrará.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4657, 9, """Qualquer voto ou compromisso assumido por uma viúva ou por uma mulher divorciada será válido.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4658, 10, """Se uma mulher que vive com o seu marido fizer um voto ou obrigar-se por juramento a um compromisso", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4659, 11, "e o seu marido o souber, mas nada lhe disser e não a proibir, então todos os votos ou compromissos pelos quais ela se obrigou serão válidos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4660, 12, "Mas, se o seu marido os anular quando deles souber, então nenhum dos votos ou compromissos que saíram de seus lábios será válido. Seu marido os anulou, e o Senhor a livrará.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4661, 13, "O marido poderá confirmar ou anular qualquer voto ou qualquer compromisso que a obrigue a humilhar-se a si mesma.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4662, 14, "Mas, se o marido nada lhe disser a respeito disso até o dia seguinte, com isso confirma todos os seus votos ou compromissos que a obrigam. Ele os confirma por nada lhe dizer quando os ouviu.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4663, 15, "Se, contudo, ele os anular algum tempo depois de ouvi-los, ele sofrerá as conseqüências de sua iniqüidade"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4664, 16, "São essas as ordenanças que o Senhor deu a Moisés a respeito do relacionamento entre um homem e sua mulher, e entre um pai e sua filha moça que ainda vive na casa do pai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4665, 1, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4666, 2, """Vingue-se dos midianitas pelo que fizeram aos israelitas. Depois disso você será reunido aos seus antepassados"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4667, 3, "Então Moisés disse ao povo: ""Armem alguns dos homens para irem à guerra contra os midianitas e executarem a vingança do Senhor contra eles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4668, 4, "Enviem à batalha mil homens de cada tribo de Israel"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4669, 5, "Doze mil homens armados para a guerra, mil de cada tribo, foram mandados pelos clãs de Israel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4670, 6, "Moisés os enviou à guerra, mil de cada tribo, juntamente com Finéias, filho do sacerdote Eleazar, que levou consigo objetos do santuário e as cornetas para o toque de guerra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4671, 7, "Lutaram então contra Midiã, conforme o Senhor tinha ordenado a Moisés, e mataram todos os homens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4672, 8, "Entre os mortos estavam os cinco reis de Midiã: Evi, Requém, Zur, Hur e Reba. Também mataram à espada Balaão, filho de Beor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4673, 9, "Os israelitas capturaram as mulheres e as crianças midianitas e tomaram como despojo todos os rebanhos e bens dos midianitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4674, 10, "Queimaram todas as cidades em que os midianitas haviam se estabelecido, bem como todos os seus acampamentos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4675, 11, "Tomaram todos os despojos, incluindo pessoas e animais,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4676, 12, "e levaram os prisioneiros, homens e mulheres, e os despojos a Moisés, ao sacerdote Eleazar e à comunidade de Israel em seu acampamento, nas campinas de Moabe, do outro lado de Jericó.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4677, 13, "Moisés, o sacerdote Eleazar e todos os líderes da comunidade saíram para recebê-los fora do acampamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4678, 14, "Mas Moisés indignou-se contra os oficiais do exército que voltaram da guerra, os líderes de milhares e os líderes de centenas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4679, 15, """Vocês deixaram todas as mulheres vivas? "", perguntou-lhes.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4680, 16, """Foram elas que seguiram o conselho de Balaão e levaram Israel a ser infiel ao Senhor no caso de Peor, de modo que uma praga feriu a comunidade do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4681, 17, "Agora matem todos os meninos. E matem também todas as mulheres que se deitaram com homem,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4682, 18, "mas poupem todas as meninas virgens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4683, 19, """Todos vocês que mataram alguém ou que tocaram em algum morto ficarão sete dias fora do acampamento. No terceiro e no sétimo dia vocês deverão purificar-se a si mesmos e aos seus prisioneiros.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4684, 20, "Purifiquem toda roupa e também tudo o que é feito de couro, de pêlo de bode ou de madeira. """, 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4685, 21, "Depois o sacerdote Eleazar disse aos soldados que tinham ido à guerra: ""Esta é a exigência da lei que o Senhor ordenou a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4686, 22, "Ouro, prata, bronze, ferro, estanho, chumbo", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4687, 23, "e tudo o que resista ao fogo, vocês terão que passar pelo fogo para purificá-las, mas também deverão purificá-las com a água da purificação. E tudo o que não resistir ao fogo terá que passar pela água.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4688, 24, "No sétimo dia lavem as suas roupas, e vocês ficarão puros. Depois poderão entrar no acampamento"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4689, 25, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4690, 26, """Você, o sacerdote Eleazar e os chefes das famílias da comunidade deverão contar todo o povo e os animais capturados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4691, 27, "Dividam os despojos entre os guerreiros que participaram da batalha e o restante da comunidade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4692, 28, "Daquilo que os guerreiros trouxeram da guerra, separem como tributo ao Senhor um de cada quinhentos, sejam pessoas, bois, jumentos, ovelhas ou bodes.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4693, 29, "Tomem esse tributo da metade que foi dada como porção a eles e entreguem-no ao sacerdote Eleazar como a porção do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4694, 30, "Da metade dada aos israelitas, escolham um de cada cinqüenta, sejam pessoas, bois, jumentos, ovelhas ou bodes. Entreguem-nos aos levitas, encarregados de cuidar do tabernáculo do Senhor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4695, 31, "Moisés e o sacerdote Eleazar fizeram como o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4696, 32, "Os despojos que restaram das presas tomadas pelos soldados foram 675. 000 ovelhas,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4697, 33, "72. 000 cabeças de gado,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4698, 34, "61. 000 jumentos", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4699, 35, "e 32. 000 mulheres virgens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4700, 36, "A metade dada aos que lutaram na guerra foi esta: 337. 500 ovelhas,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4701, 37, "das quais o tributo para o Senhor foram 675;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4702, 38, "36. 000 cabeças de gado, das quais o tributo para o Senhor foram 72;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4703, 39, "30. 500 jumentos, dos quais o tributo para o Senhor foram 61;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4704, 40, "16. 000 pessoas, das quais o tributo para o Senhor foram 32.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4705, 41, "Moisés deu o tributo ao sacerdote Eleazar como contribuição ao Senhor, conforme o Senhor tinha ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4706, 42, "A outra metade, pertencente aos israelitas, Moisés separou da dos combatentes;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4707, 43, "essa era a metade pertencente à comunidade, com 337. 500 ovelhas,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4708, 44, "36. 000 cabeças de gado,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4709, 45, "30. 500 jumentos", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4710, 46, "e 16. 000 pessoas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4711, 47, "Da metade pertencente aos israelitas, Moisés escolheu um de cada cinqüenta, tanto de pessoas como de animais, conforme o Senhor lhe tinha ordenado, e os entregou aos levitas, encarregados de cuidar do tabernáculo do Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4712, 48, "Então os oficiais que estavam sobre as unidades do exército, os líderes de milhares e os líderes de centenas foram a Moisés", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4713, 49, "e lhe disseram: ""Seus servos contaram os soldados sob o nosso comando, e não está faltando ninguém.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4714, 50, "Por isso trouxemos como oferta ao Senhor os artigos de ouro dos quais cada um de nós se apossou: braceletes, pulseiras, anéis de sinete, brincos e colares; para fazer propiciação por nós perante o Senhor"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4715, 51, "Moisés e o sacerdote Eleazar receberam deles. Todas as jóias de ouro.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4716, 52, "Todo o ouro dado pelos líderes de milhares e pelos líderes de centenas que Moisés e Eleazar apresentaram como contribuição ao Senhor pesou duzentos quilos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4717, 53, "Cada soldado tinha tomado despojos para si mesmo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4718, 54, "Moisés e o sacerdote Eleazar receberam o ouro dado pelos líderes de milhares e pelos líderes de centenas e o levaram para a Tenda do Encontro como memorial para que o Senhor se lembrasse dos israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4719, 1, "As tribos de Rúben e de Gade, donas de numerosos rebanhos, viram que as terras de Jazar e de Gileade eram próprias para a criação de gado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4720, 2, "Por isso foram a Moisés, ao sacerdote Eleazar e aos líderes da comunidade, e disseram:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4721, 3, """Atarote, Dibom, Jazar, Ninra, Hesbom, Eleale, Sebã, Nebo e Beom,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4722, 4, "terras que o Senhor subjugou perante a comunidade de Israel, são próprias para a criação de gado, e os seus servos possuem gado"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4723, 5, "E acrescentaram: ""Se podemos contar com o favor de vocês, deixem que essa terra seja dada a estes seus servos como nossa herança. Não nos façam atravessar o Jordão"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4724, 6, "Moisés respondeu aos homens de Gade e de Rúben: ""E os seus compatriotas irão à guerra enquanto vocês ficam aqui?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4725, 7, "Por que vocês desencorajam os israelitas de entrar na terra que o Senhor lhes deu?", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4726, 8, "Foi isso que os pais de vocês fizeram quando os enviei de Cades-Barnéia para verem a terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4727, 9, "Depois de subirem ao vale de Escol e examinarem a terra, desencorajaram os israelitas de entrar na terra que o Senhor lhes tinha dado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4728, 10, "A ira do Senhor se acendeu naquele dia, e ele fez este juramento:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4729, 11, "‘Como não me seguiram de coração íntegro, nenhum dos homens de vinte anos para cima que saíram do Egito verá a terra que prometi sob juramento a Abraão, a Isaque e a Jacó,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4730, 12, "com exceção de Calebe, filho de Jefoné, o quenezeu, e Josué, filho de Num, seguiram ao Senhor com integridade de coração’.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4731, 13, "A ira do Senhor acendeu-se contra Israel, e ele os fez andar errantes no deserto durante quarenta anos, até que passou toda a geração daqueles que lhe tinham desagradado com seu mau procedimento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4732, 14, """E aí estão vocês, raça de pecadores, pondo-se no lugar dos seus antepassados e acendendo ainda mais a ira do Senhor contra Israel.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4733, 15, "Se deixarem de segui-lo, de novo ele os abandonará no deserto, e vocês serão o motivo da destruição de todo este povo"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4734, 16, "Então se aproximaram dele e disseram: ""Gostaríamos de construir aqui currais para o nosso gado e cidades para as nossas mulheres e filhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4735, 17, "Mas nós nos armaremos e estaremos prontos para ir à frente dos israelitas até que os tenhamos levado ao seu lugar. Enquanto isso, nossas mulheres e nossos filhos morarão em cidades fortificadas para se proteger dos habitantes da terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4736, 18, "Não retornaremos aos nossos lares enquanto todos os israelitas não receberem a sua herança.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4737, 19, "Não receberemos herança alguma com eles do outro lado do Jordão, uma vez que a nossa herança nos seja dada no lado leste do Jordão"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4738, 20, "Disse-lhes Moisés: ""Se fizerem isso, se perante o Senhor vocês se armarem para a guerra,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4739, 21, "e se, armados, todos vocês atravessarem o Jordão perante o Senhor até que ele tenha expulsado os seus inimigos da frente dele,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4740, 22, "então, quando a terra estiver subjugada perante o Senhor, vocês poderão voltar e estarão livres da sua obrigação para com o Senhor e para com Israel. E esta terra será propriedade de vocês perante o Senhor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4741, 23, """Mas, se vocês não fizerem isso, estarão pecando contra o Senhor; e estejam certos de que vocês não escaparão do pecado que cometeram.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4742, 24, "Construam cidades para as suas mulheres e crianças, e currais para os seus rebanhos, mas façam o que vocês prometeram"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4743, 25, "Então os homens de Gade e de Rúben disseram a Moisés: ""Nós, seus servos, faremos como o meu senhor ordena.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4744, 26, "Nossos filhos e mulheres, todos os nossos rebanhos ficarão aqui nas cidades de Gileade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4745, 27, "Mas os seus servos, todos os homens armados para a batalha, atravessarão para lutar perante o Senhor, assim como o meu senhor está dizendo"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4746, 28, "Moisés deu as seguintes instruções acerca deles ao sacerdote Eleazar, a Josué, filho de Num, e aos chefes de família das tribos israelitas:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4747, 29, """Se os homens de Gade e de Rúben, todos eles armados para a batalha, atravessarem o Jordão com vocês perante o Senhor, então, quando a terra for subjugada perante vocês, entreguem-lhes como propriedade a terra de Gileade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4748, 30, "Mas, se não atravessarem armados com vocês, terão que aceitar a propriedade deles com vocês em Canaã"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4749, 31, "Os homens de Gade e de Rúben responderam: ""Os seus servos farão o que o Senhor disse.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4750, 32, "Atravessaremos o Jordão perante o Senhor e entraremos armados em Canaã, mas a propriedade que vamos herdar estará deste lado do Jordão"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4751, 33, "Então Moisés deu às tribos de Gade e de Rúben e à metade da tribo de Manassés, filho de José, o reino de Seom, rei dos amorreus, e o reino de Ogue, rei de Basã, toda a terra com as suas cidades e o território ao redor delas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4752, 34, "A tribo de Gade construiu Dibom, Atarote, Aroer,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4753, 35, "Atarote-Sofã, Jazar, Jogbeá,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4754, 36, "Bete-Ninra e Bete-Harã como cidades fortificadas, e fez currais para os seus rebanhos.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4755, 37, "E a tribo de Rúben reconstruiu Hesbom, Eleale e Quiriataim,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4756, 38, "bem como Nebo e Baal-Meom ( esses nomes foram mudados ) e Sibma. E deu outros nomes a essas cidades.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4757, 39, "Os descendentes de Maquir, filho de Manassés, foram a Gileade, tomaram posse dela e expulsaram os amorreus que lá estavam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4758, 40, "Então Moisés deu Gileade aos maquiritas, descendentes de Manassés, e eles passaram a habitar ali.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4759, 41, "Jair, descendente de Manassés, conquistou os povoados deles e os chamou Havote-Jair.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4760, 42, "E Noba conquistou Quenate e os seus povoados e a chamou Noba, de acordo com o seu nome.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4761, 1, "Estas são as jornadas dos israelitas quando saíram do Egito, organizados segundo as suas divisões, sob a liderança de Moisés e Arão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4762, 2, "Por ordem do Senhor Moisés registrou as etapas da jornada deles. Esta foi a jornada deles, por etapas:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4763, 3, "Os israelitas partiram de Ramessés no décimo quinto dia do primeiro mês, no dia seguinte ao da Páscoa. Saíram, marchando desafiadoramente à vista de todos os egípcios,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4764, 4, "enquanto estes sepultavam o primeiro filho de cada um deles, que o Senhor matou. O Senhor impôs castigo aos seus deuses.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4765, 5, "Os israelitas partiram de Ramessés e acamparam em Sucote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4766, 6, "Partiram de Sucote e acamparam em Etã, nos limites do deserto.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4767, 7, "Partiram de Etã, voltaram para Pi-Hairote, a leste de Baal-Zefom, e acamparam perto de Migdol.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4768, 8, "Partiram de Pi-Hairote e atravessaram o mar chegando ao deserto, e, depois de viajarem três dias no deserto de Etã, acamparam em Mara.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4769, 9, "Partiram de Mara e foram para Elim, onde havia doze fontes e setenta palmeiras, e acamparam ali.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4770, 10, "Partiram de Elim e acamparam junto ao mar Vermelho.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4771, 11, "Partiram do mar Vermelho e acamparam no deserto de Sim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4772, 12, "Partiram do deserto de Sim e acamparam em Dofca.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4773, 13, "Partiram de Dofca e acamparam em Alus.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4774, 14, "Partiram de Alus e acamparam em Refidim, onde não havia água para o povo beber.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4775, 15, "Partiram de Refidim e acamparam no deserto do Sinai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4776, 16, "Partiram do deserto do Sinai e acamparam em Quibrote-Hataavá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4777, 17, "Partiram de Quibrote-Hataavá e acamparam em Hazerote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4778, 18, "Partiram de Hazerote e acamparam em Ritmá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4779, 19, "Partiram de Ritmá e acamparam em Rimom-Perez.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4780, 20, "Partiram de Rimom-Perez e acamparam em Libna.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4781, 21, "Partiram de Libna e acamparam em Rissa.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4782, 22, "Partiram de Rissa e acamparam em Queelata.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4783, 23, "Partiram de Queelata e acamparam no monte Séfer.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4784, 24, "Partiram do monte Séfer e acamparam em Harada.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4785, 25, "Partiram de Harada e acamparam em Maquelote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4786, 26, "Partiram de Maquelote e acamparam em Taate.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4787, 27, "Partiram de Taate e acamparam em Terá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4788, 28, "Partiram de Terá e acamparam em Mitca.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4789, 29, "Partiram de Mitca e acamparam em Hasmona.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4790, 30, "Partiram de Hasmona e acamparam em Moserote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4791, 31, "Partiram de Moserote e acamparam em Benê-Jaacã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4792, 32, "Partiram de Benê-Jaacã e acamparam em Hor-Gidgade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4793, 33, "Partiram de Hor-Gidgade e acamparam em Jotbatá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4794, 34, "Partiram de Jotbatá e acamparam em Abrona.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4795, 35, "Partiram de Abrona e acamparam em Eziom-Geber.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4796, 36, "Partiram de Eziom-Geber e acamparam em Cades, no deserto de Zim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4797, 37, "Partiram de Cades e acamparam no monte Hor, na fronteira de Edom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4798, 38, "Por ordem do Senhor, o sacerdote Arão subiu o monte Hor, onde morreu no dia primeiro do quinto mês do quadragésimo ano depois que os israelitas saíram do Egito.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4799, 39, "Arão tinha cento e vinte e três anos de idade quando morreu no monte Hor.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4800, 40, "O rei cananeu de Arade, que vivia no Neguebe, na terra de Canaã, soube que os israelitas estavam chegando.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4801, 41, "Eles partiram do monte Hor e acamparam em Zalmona.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4802, 42, "Partiram de Zalmona e acamparam em Punom.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4803, 43, "Partiram de Punom e acamparam em Obote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4804, 44, "Partiram de Obote e acamparam em Ijé-Abarim, na fronteira de Moabe.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4805, 45, "Partiram de Ijim e acamparam em Dibom-Gade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4806, 46, "Partiram de Dibom-Gade e acamparam em Almom-Diblataim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4807, 47, "Partiram de Almom-Diblataim e acamparam nos montes de Abarim, defronte de Nebo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4808, 48, "Partiram dos montes de Abarim e acamparam nas campinas de Moabe junto ao Jordão, do outro lado de Jericó.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4809, 49, "Nas campinas de Moabe eles acamparam junto ao Jordão, desde Bete-Jesimote até Abel-Sitim.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4810, 50, "Nas campinas de Moabe, junto ao Jordão, do outro lado de Jericó, o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4811, 51, """Diga aos israelitas: Quando vocês atravessarem o Jordão para entrar em Canaã,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4812, 52, "expulsem da frente de vocês todos os habitantes da terra. Destruam todas as imagens esculpidas e todos os ídolos fundidos, e derrubem todos os altares idólatras deles.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4813, 53, "Apoderem-se da terra e instalem-se nela, pois eu lhes dei a terra para que dela tomem posse.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4814, 54, "Distribuam a terra por sorteio, de acordo com os seus clãs. Aos clãs vocês darão uma herança maior, e aos menores, uma herança menor. Cada clã receberá a terra que lhe cair por sorte. Distribuam-na entre as tribos dos seus antepassados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4815, 55, """Se, contudo, vocês não expulsarem os habitantes da terra, aqueles que vocês permitirem ficar se tornarão farpas em seus olhos e espinhos em suas costas. Eles lhes causarão problemas na terra em que vocês irão morar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4816, 56, "Então farei a vocês o mesmo que planejo fazer a eles"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4817, 1, "Disse mais o Senhor a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4818, 2, """Dê ordem aos israelitas e diga-lhes: Quando vocês entrarem em Canaã, a terra que lhes será sorteada como herança terá estas fronteiras:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4819, 3, """O lado sul começará no deserto de Zim, junto à fronteira de Edom. No leste, sua fronteira sul começará na extremidade do mar Salgado,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4820, 4, "passará pelo sul da subida de Acrabim, prosseguirá até Zim e irá para o sul de Cades-Barnéia. Depois passará por Hazar-Adar e irá até Azmom,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4821, 5, "onde fará a volta, juntando-se ao ribeiro do Egito para terminar no Mar.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4822, 6, "A fronteira ocidental de vocês será o litoral do mar Grande. Será essa a fronteira do oeste.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4823, 7, "Esta será a fronteira norte: façam uma linha desde o mar Grande até o monte Hor,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4824, 8, "e do monte Hor até Lebo-Hamate. O limite da fronteira será Zedade,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4825, 9, "prosseguirá até Zifrom e terminará em Hazar-Enã. Será essa a fronteira norte de vocês.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4826, 10, "Esta será a fronteira oriental: façam uma linha de Hazar-Enã até Sefã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4827, 11, "A fronteira descerá de Sefã até Ribla, no lado oriental de Aim, e prosseguirá ao longo das encostas a leste do mar de Quinerete.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4828, 12, "A fronteira descerá ao longo do Jordão e terminará no mar Salgado. Será essa a terra de vocês, com as suas fronteiras de todos os lados"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4829, 13, "Moisés ordenou aos israelitas: ""Distribuam a terra por sorteio como herança. O Senhor ordenou que seja dada às nove tribos e meia,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4830, 14, "porque as famílias da tribo de Rúben, da tribo de Gade e da metade da tribo de Manassés já receberam a herança delas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4831, 15, "Estas duas tribos e meia receberam sua herança no lado leste do Jordão, do outro lado de Jericó, na direção do nascer do sol"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4832, 16, "O Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4833, 17, """Estes são os nomes dos homens que deverão distribuir a terra a vocês como herança: o sacerdote Eleazar e Josué, filho de Num.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4834, 18, "Designem um líder de cada tribo para ajudar a distribuir a terra.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4835, 19, "Estes são os seus nomes: ""Calebe, filho de Jefoné, da tribo de Judá;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4836, 20, "Samuel, filho de Amiúde, da tribo de Simeão;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4837, 21, "Elidade, filho de Quislom, da tribo de Benjamim;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4838, 22, "Buqui, filho de Jogli, o líder da tribo de Dã;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4839, 23, "Haniel, filho de Éfode, o líder da tribo de Manassés, filho de José;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4840, 24, "Quemuel, filho de Siftã, o líder da tribo de Efraim, filho de José;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4841, 25, "Elizafã, filho de Parnaque, o líder da tribo de Zebulom;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4842, 26, "Paltiel, filho de Azã, o líder da tribo de Issacar;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4843, 27, "Aiúde, filho de Selomi, o líder da tribo de Aser;", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4844, 28, "Pedael, filho de Amiúde, o líder da tribo de Naftali"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4845, 29, "Foram esses os homens a quem o Senhor ordenou que distribuíssem a herança aos israelitas na terra de Canaã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4846, 1, "Nas campinas de Moabe, junto ao Jordão, do outro lado de Jericó, o Senhor disse a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4847, 2, """Ordene aos israelitas que, da herança que possuem, dêem cidades para os levitas morarem. E dêem-lhes também pastagens ao redor das cidades.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4848, 3, "Assim eles terão cidades para habitar e pastagens para o gado, para os rebanhos e para todos os seus outros animais de criação.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4849, 4, """As pastagens ao redor das cidades que vocês derem aos levitas se estenderão para fora quatrocentos e cinqüenta metros, a partir do muro da cidade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4850, 5, "Do lado de fora da cidade, meçam novecentos metros para o lado leste, para o lado sul, para o lado oeste e para o lado norte, tendo a cidade no centro. Eles terão essa área para pastagens das cidades.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4851, 6, """Seis das cidades que vocês derem aos levitas serão cidades de refúgio, para onde poderá fugir quem tiver matado alguém. Além disso, dêem a eles outras quarenta e duas cidades.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4852, 7, "Ao todo, vocês darão aos levitas quarenta e oito cidades, juntamente com as suas pastagens.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4853, 8, "As cidades que derem aos levitas, das terras dos israelitas, deverão ser dadas proporcionalmente à herança de cada tribo; tomem muitas cidades da tribo que tem muitas, mas poucas da que tem poucas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4854, 9, "Disse também o Senhor a Moisés:", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4855, 10, """Diga aos israelitas: Quando vocês atravessarem o Jordão e entrarem em Canaã,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4856, 11, "escolham algumas cidades para serem suas cidades de refúgio, para onde poderá fugir quem tiver matado alguém sem intenção.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4857, 12, "Elas serão locais de refúgio contra o vingador da vítima, a fim de que alguém acusado de assassinato não morra antes de apresentar-se para julgamento perante a comunidade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4858, 13, "As seis cidades que vocês derem serão suas cidades de refúgio.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4859, 14, "Designem três cidades de refúgio deste lado do Jordão e três outras em Canaã.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4860, 15, "As seis cidades servirão de refúgio para os israelitas, para os estrangeiros residentes e para quaisquer outros estrangeiros que vivam entre eles, para que todo aquele que tiver matado alguém sem intenção possa fugir para lá.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4861, 16, """Se um homem ferir alguém com um objeto de ferro de modo que esta pessoa morra, ele é assassino; o assassino terá que ser executado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4862, 17, "Ou, se alguém tiver nas mãos uma pedra que possa matar, e ferir uma pessoa de modo que ela morra, é assassino; o assassino terá que ser executado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4863, 18, "Ou, se alguém tiver nas mãos um pedaço de madeira que possa matar, e ferir uma pessoa de modo que ela morra, é assassino; o assassino terá que ser executado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4864, 19, "O vingador da vítima matará o assassino; quando o encontrar o matará.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4865, 20, "Se alguém, com ódio, empurrar uma pessoa premeditadamente ou atirar alguma coisa contra ela de modo que ela morra,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4866, 21, "ou se com hostilidade der-lhe um soco provocando a sua morte, ele terá que ser executado; é assassino. O vingador da vítima matará o assassino quando encontrá-lo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4867, 22, """Todavia, se alguém, sem hostilidade, empurrar uma pessoa ou atirar alguma coisa contra ela sem intenção,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4868, 23, "ou se, sem vê-la, deixar cair sobre ela uma pedra que possa matá-la, e ela morrer, então, como não era sua inimiga e não pretendia feri-la,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4869, 24, "a comunidade deverá julgar entre ele e o vingador da vítima de acordo com essas leis.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4870, 25, "A comunidade protegerá o acusado de assassinato do vingador da vítima e o enviará de volta à cidade de refúgio para onde tinha fugido. Ali permanecerá até a morte do sumo sacerdote, que foi ungido com o óleo santo.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4871, 26, """Se, contudo, o acusado sair dos limites da cidade de refúgio para onde fugiu", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4872, 27, "e o vingador da vítima o encontrar fora da cidade, ele poderá matar o acusado sem ser culpado de assassinato.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4873, 28, "O acusado deverá permanecer em sua cidade de refúgio até a morte do sumo sacerdote; somente depois da morte do sumo sacerdote poderá voltar à sua propriedade.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4874, 29, """Estas exigências legais serão para vocês e para as suas futuras gerações, onde quer que vocês vivam.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4875, 30, """Quem matar uma pessoa terá que ser executado como assassino mediante depoimento de testemunhas. Mas ninguém será executado mediante o depoimento de apenas uma testemunha.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4876, 31, """Não aceitem resgate pela vida de um assassino; ele merece morrer. Certamente terá que ser executado.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4877, 32, """Não aceitem resgate por alguém que tenha fugido para uma cidade de refúgio, permitindo que ele retorne e viva em sua própria terra antes da morte do sumo sacerdote.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4878, 33, """Não profanem a terra onde vocês estão. O derramamento de sangue profana a terra, e só se pode fazer propiciação em favor da terra em que se derramou sangue, mediante o sangue do assassino que o derramou.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4879, 34, "Não contaminem a terra onde vocês vivem e onde eu habito, pois eu, o Senhor, habito entre os israelitas"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4880, 1, "Os chefes de família do clã de Gileade, filho de Maquir, neto de Manassés, que pertenciam aos clãs dos descendentes de José, foram falar com Moisés e com os líderes, os chefes das famílias israelitas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4881, 2, "E disseram: ""Quando o Senhor ordenou ao meu senhor que, por sorteio, desse a terra como herança para os israelitas, ordenou que vocês dessem a herança de nosso irmão Zelofeade às suas filhas.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4882, 3, "Agora, suponham que elas se casem com homens de outras tribos israelitas; nesse caso a herança delas será tirada da herança dos nossos antepassados e acrescentada à herança da tribo com a qual se unirem pelo casamento.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4883, 4, "Quando chegar o Ano do Jubileu para os israelitas, a herança delas será acrescentada à da tribo com a qual se unirem pelo casamento, e a propriedade delas será tirada da herança da tribo de nossos antepassados"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4884, 5, "Então, instruído pelo Senhor, Moisés deu esta ordem aos israelitas: ""A tribo dos descendentes de José tem razão.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4885, 6, "É isto que o Senhor ordena quanto às filhas de Zelofeade: Elas poderão casar-se com quem lhes agradar, contanto que se casem dentro do clã da tribo de seu pai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4886, 7, "Nenhuma herança em Israel poderá passar de uma tribo para outra, pois todos os israelitas manterão as terras das tribos que herdaram de seus antepassados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4887, 8, "Toda filha que herdar terras em qualquer tribo israelita se casará com alguém do clã da tribo de seu pai, para que cada israelita possua a herança dos seus antepassados.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4888, 9, "Nenhuma herança poderá passar de uma tribo para outra, pois cada tribo israelita deverá manter as terras que herdou"".", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4889, 10, "As filhas de Zelofeade fizeram conforme o Senhor havia ordenado a Moisés.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4890, 11, "As filhas de Zelofeade, Maalá, Tirza, Hogla, Milca e Noa, casaram-se com seus primos paternos,", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4891, 12, "dentro dos clãs dos descendentes de Manassés, filho de José, e a herança delas permaneceu no clã e na tribo de seu pai.", 3);
INSERT INTO verses (id, number, text, chapter_id) VALUES (4892, 13, "São esses os mandamentos e as ordenanças que o Senhor deu aos israelitas por intermédio de Moisés nas campinas de Moabe, junto ao Jordão, do outro lado de Jericó.", 3);