INSERT INTO verses (id, number, text, chapter_id) VALUES (16402, 1, "Estes são os provérbios de Salomão, filho de Davi, rei de Israel.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16403, 2, "Eles ajudarão a experimentar a sabedoria e a disciplina; a compreender as palavras que dão entendimento;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16404, 3, "a viver com disciplina e sensatez, fazendo o que é justo, direito e correto;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16405, 4, "ajudarão a dar prudência aos inexperientes e conhecimento e bom senso aos jovens.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16406, 5, "Se o sábio der ouvidos, aumentará seu conhecimento, e quem tem discernimento obterá orientação", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16407, 6, "para compreender provérbios e parábolas, ditados e enigmas dos sábios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16408, 7, "O temor do Senhor é o princípio do conhecimento, mas os insensatos desprezam a sabedoria e a disciplina.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16409, 8, "Ouça, meu filho, a instrução de seu pai e não despreze o ensino de sua mãe.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16410, 9, "Eles serão um enfeite para a sua cabeça, um adorno para o seu pescoço.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16411, 10, "Meu filho, se os maus tentarem seduzi-lo, não ceda!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16412, 11, "Se disserem: ""Venha conosco; fiquemos de tocaia para matar alguém, vamos divertir-nos armando emboscada contra quem de nada suspeita!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16413, 12, "Vamos engoli-los vivos, como a sepultura engole os mortos; vamos destruí-los inteiros, como são destruídos os que descem à cova;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16414, 13, "acharemos todo tipo de objetos valiosos e encheremos as nossas casas com o que roubarmos;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16415, 14, "junte-se ao nosso bando; dividiremos em partes iguais tudo o que conseguirmos! """, 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16416, 15, "Meu filho, não vá pela vereda dessa gente! Afaste os pés do caminho que eles seguem,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16417, 16, "pois os pés deles correm para fazer o mal, estão sempre prontos para derramar sangue.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16418, 17, "Assim como é inútil estender a rede se as aves o observam,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16419, 18, "também esses homens não percebem que fazem tocaia contra a própria vida; armam emboscadas contra eles mesmos!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16420, 19, "Tal é o caminho de todos os gananciosos; quem assim procede se destrói.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16421, 20, "A sabedoria clama em voz alta nas ruas, ergue a voz nas praças públicas;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16422, 21, "nas esquinas das ruas barulhentas ela clama, nas portas da cidade faz o seu discurso:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16423, 22, """Até quando vocês, inexperientes, irão contentar-se com a sua inexperiência? Vocês, zombadores, até quando terão prazer na zombaria? E vocês, tolos, até quando desprezarão o conhecimento?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16424, 23, "Se acatarem a minha repreensão, eu lhes darei um espírito de sabedoria e lhes revelarei os meus pensamentos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16425, 24, "Vocês, porém, rejeitaram o meu convite; ninguém se importou quando estendi minha mão!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16426, 25, "Visto que desprezaram totalmente o meu conselho e não quiseram aceitar a minha repreensão,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16427, 26, "eu, de minha parte, vou rir-me da sua desgraça; zombarei quando o que temem se abater sobre vocês,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16428, 27, "quando aquilo que temem abater-se sobre vocês como uma tempestade, quando a desgraça os atingir como um vendaval, quando a angústia e a dor os dominarem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16429, 28, """Então vocês me chamarão, mas não responderei; procurarão por mim, mas não me encontrarão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16430, 29, "Visto que desprezaram o conhecimento e recusaram o temor do Senhor,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16431, 30, "não quiseram aceitar o meu conselho e fizeram pouco caso da minha advertência,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16432, 31, "comerão do fruto da sua conduta e se fartarão de suas próprias maquinações.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16433, 32, "Pois a inconstância dos inexperientes os matará, e a falsa segurança dos tolos os destruirá;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16434, 33, "mas quem me ouvir viverá em segurança e estará tranqüilo, sem temer nenhum mal"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16435, 1, "Meu filho, se você aceitar as minhas palavras e guardar no coração os meus mandamentos;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16436, 2, "se der ouvidos à sabedoria e inclinar o coração para o discernimento;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16437, 3, "se clamar por entendimento e por discernimento gritar bem alto,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16438, 4, "se procurar a sabedoria como se procura a prata e buscá-la como quem busca um tesouro escondido,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16439, 5, "então você entenderá o que é temer ao Senhor e achará o conhecimento de Deus.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16440, 6, "Pois o Senhor é quem dá sabedoria; de sua boca procedem o conhecimento e o discernimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16441, 7, "Ele reserva a sensatez para o justo; como um escudo protege quem anda com integridade,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16442, 8, "pois guarda a vereda do justo e protege o caminho de seus fiéis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16443, 9, "Então você entenderá o que é justo, direito e certo, e aprenderá os caminhos do bem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16444, 10, "Pois a sabedoria entrará em seu coração, e o conhecimento será agradável à sua alma.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16445, 11, "O bom senso o guardará, e o discernimento o protegerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16446, 12, "A sabedoria o livrará do caminho dos maus, dos homens de palavras perversas,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16447, 13, "que abandonam as veredas retas para andar por caminhos de trevas,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16448, 14, "têm prazer em fazer o mal e exultam com a maldade dos perversos,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16449, 15, "gente que anda por veredas tortuosas, e no caminho se extraviam.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16450, 16, "Ela também o livrará da mulher imoral, da pervertida que seduz com suas palavras,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16451, 17, "que abandona aquele que desde a juventude foi seu companheiro e ignora a aliança que fez diante de Deus.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16452, 18, "Ela se dirige para morte, que é a sua casa, e os seus caminhos levam às sombras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16453, 19, "Os que a ela procuram jamais voltarão, nem tornarão a encontrar as veredas da vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16454, 20, "A sabedoria o fará andar nos caminhos dos homens de bem e a manter-se nas veredas dos justos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16455, 21, "Pois os justos habitarão na terra, e os íntegros nela permanecerão;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16456, 22, "mas os ímpios serão eliminados da terra, e dela os infiéis serão arrancados.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16457, 1, "Meu filho, não se esqueça da minha lei, mas guarde no coração os meus mandamentos,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16458, 2, "pois eles prolongarão a sua vida por muitos anos e lhe darão prosperidade e paz.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16459, 3, "Que o amor e a fidelidade jamais o abandonem; prenda-os ao redor do seu pescoço, escreva-os na tábua do seu coração.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16460, 4, "Então você terá o favor de Deus e dos homens, e boa reputação.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16461, 5, "Confie no Senhor de todo o seu coração e não se apóie em seu próprio entendimento;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16462, 6, "reconheça o Senhor em todos os seus caminhos, e ele endireitará as suas veredas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16463, 7, "Não seja sábio aos seus próprios olhos; tema ao Senhor e evite o mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16464, 8, "Isso lhe dará saúde ao corpo e vigor aos ossos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16465, 9, "Honre o Senhor com todos os seus recursos e com os primeiros frutos de todas as suas plantações;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16466, 10, "os seus celeiros ficarão plenamente cheios, e os seus barris transbordarão de vinho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16467, 11, "Meu filho, não despreze a disciplina do Senhor nem se magoe com a sua repreensão,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16468, 12, "pois o Senhor disciplina a quem ama, assim como o pai faz ao filho de quem deseja o bem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16469, 13, "Como é feliz o homem que acha a sabedoria, o homem que obtém entendimento,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16470, 14, "pois a sabedoria é mais proveitosa do que a prata e rende mais do que o ouro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16471, 15, "É mais preciosa do que rubis; nada do que você possa desejar se compara a ela.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16472, 16, "Na mão direita, a sabedoria lhe garante vida longa; na mão esquerda, riquezas e honra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16473, 17, "Os caminhos da sabedoria são caminhos agradáveis, e todas as suas veredas são paz.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16474, 18, "A sabedoria é árvore que dá vida a quem a abraça; quem a ela se apega será abençoado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16475, 19, "Por sua sabedoria o Senhor lançou os alicerces da terra, por seu entendimento fixou no lugar os céus;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16476, 20, "por seu conhecimento as fontes profundas se rompem, e as nuvens gotejam o orvalho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16477, 21, "Meu filho, guarde consigo a sensatez e o equilíbrio, nunca os perca de vista;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16478, 22, "trarão vida a você e serão um enfeite para o seu pescoço.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16479, 23, "Então você seguirá o seu caminho em segurança, e não tropeçará;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16480, 24, "quando se deitar, não terá medo, e o seu sono será tranqüilo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16481, 25, "Não terá medo da calamidade repentina nem da ruína que atinge os ímpios,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16482, 26, "pois o Senhor será a sua segurança e o impedirá de cair em armadilha.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16483, 27, "Quanto lhe for possível, não deixe de fazer o bem a quem dele precisa,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16484, 28, "Não diga ao seu próximo: ""Volte amanhã, e eu lhe darei algo"", se pode ajudá-lo hoje.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16485, 29, "Não planeje o mal contra o seu próximo, que confiantemente mora perto de você.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16486, 30, "Não acuse alguém sem motivo, se ele não lhe fez nenhum mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16487, 31, "Não tenha inveja de quem é violento nem adote nenhum dos seus procedimentos,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16488, 32, "pois o Senhor detesta o perverso, mas o justo é seu grande amigo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16489, 33, "A maldição do Senhor está sobre a casa dos ímpios, mas ele abençoa o lar dos justos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16490, 34, "Ele zomba dos zombadores, mas concede graça aos humildes.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16491, 35, "A honra é herança dos sábios, mas o Senhor expõe os tolos ao ridículo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16492, 1, "Ouçam, meus filhos, a instrução de um pai; estejam atentos, e obterão discernimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16493, 2, "O ensino que lhes ofereço é bom; por isso não abandonem a minha instrução.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16494, 3, "Quando eu era menino, ainda pequeno, em companhia de meu pai, um filho muito especial para minha mãe,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16495, 4, "ele me ensinava e me dizia: ""Apegue-se às minhas palavras de todo o coração; obedeça aos meus mandamentos, e você terá vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16496, 5, "Procure obter sabedoria e entendimento; não se esqueça das minhas palavras nem delas se afaste.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16497, 6, "Não abandone a sabedoria, e ela o protegerá; ame-a, e ela cuidará de você.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16498, 7, "O conselho da sabedoria é: procure obter sabedoria; use tudo que você possui para adquirir entendimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16499, 8, "Dedique alta estima à sabedoria, e ela o exaltará; abrace-a, e ela o honrará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16500, 9, "Ela porá um belo diadema sobre a sua cabeça e lhe dará de presente uma coroa de esplendor"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16501, 10, "Ouça, meu filho, e aceite o que digo, e você terá vida longa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16502, 11, "Eu o conduzi pelo caminho da sabedoria e o encaminhei por veredas retas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16503, 12, "Assim, quando você por elas seguir, não encontrará obstáculos; quando correr, não tropeçará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16504, 13, "Apegue-se à instrução, não a abandone; guarde-a bem, pois dela depende a sua vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16505, 14, "Não siga pela vereda dos ímpios nem ande no caminho dos maus.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16506, 15, "Evite-o, não passe por ele; afaste-se e não se detenha.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16507, 16, "Pois eles não conseguem dormir enquanto não fazem o mal; perdem o sono se não causarem a ruína de alguém.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16508, 17, "Pois eles se alimentam de maldade, e se embriagam de violência.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16509, 18, "A vereda do justo é como a luz da alvorada, que brilha cada vez mais até à plena claridade do dia.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16510, 19, "Mas o caminho dos ímpios é como densas trevas; nem sequer sabem em que tropeçam.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16511, 20, "Meu filho, escute o que lhe digo; preste atenção às minhas palavras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16512, 21, "Nunca as perca de vista; guarde-as no fundo do coração,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16513, 22, "pois são vida para quem as encontra e saúde para todo o seu ser.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16514, 23, "Acima de tudo, guarde o seu coração, pois dele depende toda a sua vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16515, 24, "Afaste da sua boca as palavras perversas; fique longe dos seus lábios a maldade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16516, 25, "Olhe sempre para a frente, mantenha o olhar fixo no que está adiante de você.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16517, 26, "Veja bem por onde anda, e os seus passos serão seguros.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16518, 27, "Não se desvie nem para a direita nem para a esquerda; afaste os seus pés da maldade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16519, 1, "Meu filho, dê atenção à minha sabedoria, incline os ouvidos para perceber o meu discernimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16520, 2, "Assim você manterá o bom senso, e os seus lábios guardarão o conhecimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16521, 3, "Pois os lábios da mulher imoral destilam mel; sua voz é mais suave que o azeite,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16522, 4, "mas no final é amarga como fel, afiada como uma espada de dois gumes.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16523, 5, "Os seus pés descem para a morte; os seus passos conduzem diretamente para a sepultura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16524, 6, "Ela nem percebe que anda por caminhos tortuosos, e não enxerga a vereda da vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16525, 7, "Agora, então, meu filho, ouça-me; não se desvie das minhas palavras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16526, 8, "Fique longe dessa mulher; não se aproxime da porta de sua casa,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16527, 9, "para que você não entregue aos outros o seu vigor nem a sua vida a algum homem cruel,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16528, 10, "para que estranhos não se fartem do seu trabalho e outros não se enriqueçam à custa do seu esforço.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16529, 11, "No final da vida você gemerá, com sua carne e seu corpo desgastados.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16530, 12, "Você dirá: ""Como odiei a disciplina! Como o meu coração rejeitou a repreensão!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16531, 13, "Não ouvi os meus mestres nem escutei os que me ensinavam.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16532, 14, "Cheguei à beira da ruína completa, à vista de toda a comunidade"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16533, 15, "Beba das águas da sua cisterna, das águas que brotam do seu próprio poço.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16534, 16, "Por que deixar que as suas fontes transbordem pelas ruas, e os teus ribeiros pelas praças?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16535, 17, "Que elas sejam exclusivamente suas, nunca repartidas com estranhos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16536, 18, "Seja bendita a sua fonte! Alegre-se com a esposa da sua juventude.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16537, 19, "Gazela amorosa, corça graciosa; que os seios de sua esposa sempre o fartem de prazer, e sempre o embriaguem os carinhos dela.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16538, 20, "Por que, meu filho, ser desencaminhado pela mulher imoral? Por que abraçar o seio de uma leviana?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16539, 21, "O Senhor vê os caminhos do homem e examina todos os seus passos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16540, 22, "As maldades do ímpio o prendem; ele se torna prisioneiro das cordas do seu pecado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16541, 23, "Certamente morrerá por falta de disciplina; andará cambaleando por causa da sua insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16542, 1, "Meu filho, se você serviu de fiador do seu próximo, se, com um aperto de mãos, empenhou-se por um estranho", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16543, 2, "e caiu na armadilha das palavras que você mesmo disse, está prisioneiro do que falou.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16544, 3, "Então, meu filho, uma vez que você caiu nas mãos do seu próximo, vá e humilhe-se; insista, incomode o seu próximo!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16545, 4, "Não se entregue ao sono, não procure descansar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16546, 5, "Livre-se como a gazela se livra do caçador, como a ave do laço que a pode prender.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16547, 6, "Observe a formiga, preguiçoso, reflita nos caminhos dela e seja sábio!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16548, 7, "Ela não tem nem chefe, nem supervisor, nem governante,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16549, 8, "e ainda assim armazena as suas provisões no verão e na época da colheita ajunta o seu alimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16550, 9, "Até quando você vai ficar deitado, preguiçoso? Quando se levantará de seu sono?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16551, 10, "Tirando uma soneca, cochilando um pouco, cruzando um pouco os braços para descansar,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16552, 11, "a sua pobreza o surpreenderá como um assaltante, e a sua necessidade lhe virá como um homem armado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16553, 12, "O perverso não tem caráter. Anda de um lado para o outro dizendo coisas maldosas;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16554, 13, "pisca o olho, arrasta os pés e faz sinais com os dedos;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16555, 14, "tem no coração o propósito de enganar; planeja sempre o mal e semeia discórdia.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16556, 15, "Por isso a desgraça se abaterá repentinamente sobre ele; de um golpe será destruído, irremediavelmente.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16557, 16, "Há seis coisas que o Senhor odeia, sete coisas que ele detesta:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16558, 17, "olhos altivos, língua mentirosa, mãos que derramam sangue inocente,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16559, 18, "coração que traça planos perversos, pés que se apressam para fazer o mal,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16560, 19, "a testemunha falsa que espalha mentiras e aquele que provoca discórdia entre irmãos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16561, 20, "Meu filho, obedeça aos mandamentos de seu pai e não abandone o ensino de sua mãe.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16562, 21, "Amarre-os sempre junto ao coração; ate-os ao redor do pescoço.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16563, 22, "Quando você andar, eles o guiarão; quando dormir, o estarão protegendo; quando acordar, falarão com você.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16564, 23, "Pois o mandamento é lâmpada, a instrução é luz, e as advertências da disciplina são o caminho que conduz à vida,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16565, 24, "eles o protegerão da mulher imoral, e dos falsos elogios da mulher leviana.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16566, 25, "Não cobice em seu coração a sua beleza nem se deixe seduzir por seus olhares,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16567, 26, "pois o preço de uma prostituta é um pedaço de pão, mas a adúltera sai à caça de vidas preciosas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16568, 27, "Pode alguém colocar fogo no peito sem queimar a roupa?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16569, 28, "Pode alguém andar sobre brasas sem queimar os pés?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16570, 29, "Assim acontece com quem se deita com mulher alheia; ninguém que a toque ficará sem castigo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16571, 30, "O ladrão não é desprezado se, faminto, rouba para matar a fome.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16572, 31, "Contudo, se for pego, deverá pagar sete vezes o que roubou, embora isso lhe custe tudo o que tem em casa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16573, 32, "Mas o homem que comete adultério não tem juízo; todo aquele que assim procede a si mesmo se destrói.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16574, 33, "Sofrerá ferimentos e vergonha, e a sua humilhação jamais se apagará,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16575, 34, "pois o ciúme desperta a fúria do marido, que não terá misericórdia quando se vingar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16576, 35, "Não aceitará nenhuma compensação; os melhores presentes não o acalmarão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16577, 1, "Meu filho, obedeça às minhas palavras e no íntimo guarde os meus mandamentos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16578, 2, "Obedeça aos meus mandamentos, e você terá vida; guarde os meus ensinos como a pupila dos seus olhos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16579, 3, "Amarre-os aos dedos; escreva-os na tábua do seu coração.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16580, 4, "Diga à sabedoria: ""Você é minha irmã"", e chame ao entendimento seu parente;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16581, 5, "eles o manterão afastado da mulher imoral, da mulher leviana e suas palavras sedutoras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16582, 6, "Da janela de minha casa olhei através da grade", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16583, 7, "e vi entre os inexperientes, no meio dos jovens, um rapaz sem juízo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16584, 8, "Ele vinha pela rua, próximo à esquina de certa mulher, andando em direção à casa dela", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16585, 9, "Era crepúsculo, o entardecer do dia, chegavam as sombras da noite, crescia a escuridão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16586, 10, "A mulher veio então ao seu encontro, vestida como prostituta, cheia de astúcia no coração.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16587, 11, "( Ela é espalhafatosa e provocadora, seus pés nunca param em casa;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16588, 12, "uma hora na rua, outra nas praças, em cada esquina fica à espreita. )", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16589, 13, "Ela agarrou o rapaz, beijou-o e lhe disse descaradamente:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16590, 14, """Tenho em casa a carne dos sacrifícios de comunhão, que hoje fiz para cumprir os meus votos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16591, 15, "Por isso saí para encontrá-lo; vim à sua procura e o encontrei!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16592, 16, "Estendi sobre o meu leito cobertas de linho fino do Egito.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16593, 17, "Perfumei a minha cama com mirra, aloés e canela.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16594, 18, "Venha, vamos embriagar-nos de carícias até o amanhecer; gozemos as delícias do amor!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16595, 19, "Pois o meu marido não está em casa; partiu para uma longa viagem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16596, 20, "Levou uma bolsa cheia de prata e não voltará antes da lua cheia"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16597, 21, "Com a sedução das palavras o persuadiu, e o atraiu com o dulçor dos lábios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16598, 22, "Imediatamente ele a seguiu como o boi levado ao matadouro, ou como o cervo que vai cair no laço", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16599, 23, "até que uma flecha lhe atravesse o fígado, ou como o pássaro que salta para dentro do alçapão, sem saber que isso lhe custará a vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16600, 24, "Então, meu filho, ouça-me; dê atenção às minhas palavras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16601, 25, "Não deixe que o seu coração se volte para os caminhos dela, nem se perca em tais veredas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16602, 26, "Muitas foram as suas vítimas; os que matou são uma grande multidão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16603, 27, "A casa dela é um caminho que desce para a sepultura, para as moradas da morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16604, 1, "A sabedoria está clamando, o discernimento ergue a sua voz;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16605, 2, "nos lugares altos, junto ao caminho, nos cruzamentos ela se coloca;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16606, 3, "ao lado das portas, à entrada da cidade, portas adentro, ela clama em alta voz:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16607, 4, """A vocês, homens, eu clamo; a todos levanto a minha voz.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16608, 5, "Vocês, inexperientes, adquiram a prudência; e vocês, tolos, tenham bom senso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16609, 6, "Ouçam, pois tenho coisas importantes para dizer; os meus lábios falarão do que é certo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16610, 7, "Minha boca fala a verdade, pois a maldade causa repulsa aos meus lábios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16611, 8, "Todas as minhas palavras são justas; nenhuma delas é distorcida ou perversa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16612, 9, "Para os que têm discernimento, são todas claras, e retas para os que têm conhecimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16613, 10, "Prefiram a minha instrução à prata, e o conhecimento ao ouro puro,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16614, 11, "pois a sabedoria é mais preciosa do que rubis; nada do que vocês possam desejar compara-se a ela.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16615, 12, """Eu, a sabedoria, moro com a prudência, e tenho o conhecimento que vem do bom senso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16616, 13, "Temer ao Senhor é odiar o mal; odeio o orgulho e a arrogância, o mau comportamento e o falar perverso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16617, 14, "Meu é o conselho sensato; a mim pertencem o entendimento e o poder.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16618, 15, "Por meu intermédio os reis governam, e as autoridades exercem a justiça;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16619, 16, "também por meu intermédio governam os nobres, todos os juízes da terra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16620, 17, "Amo os que me amam, e quem me procura me encontra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16621, 18, "Comigo estão riquezas e honra, prosperidade e justiça duradouras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16622, 19, "Meu fruto é melhor do que o ouro, do que o ouro puro; o que ofereço é superior à prata escolhida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16623, 20, "Ando pelo caminho da retidão, pelas veredas da justiça,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16624, 21, "concedendo riqueza aos que me amam e enchendo os seus tesouros.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16625, 22, """O Senhor me criou como o princípio de seu caminho, antes das suas obras mais antigas;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16626, 23, "fui formada desde a eternidade, desde o princípio, antes de existir a terra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16627, 24, "Nasci quando ainda não havia abismos, quando não existiam fontes de águas;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16628, 25, "antes de serem estabelecidos os montes e de existirem colinas eu nasci.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16629, 26, "Ele ainda não havia feito a terra, nem os campos, nem o pó com o qual formou o mundo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16630, 27, "Quando ele estabeleceu os céus, lá estava eu, quando traçou o horizonte sobre a superfície do abismo,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16631, 28, "quando colocou as nuvens em cima e estabeleceu as fontes do abismo,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16632, 29, "quando determinou as fronteiras do mar para que as águas não violassem a sua ordem, quando marcou os limites dos alicerces da terra,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16633, 30, "eu estava ao seu lado, e era o seu arquiteto; dia a dia eu era o seu prazer e me alegrava continuamente com a sua presença.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16634, 31, "Eu me alegrava com o mundo que ele criou, e a humanidade me dava alegria.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16635, 32, """Ouçam-me agora, meus filhos: Como são felizes os que guardam os meus caminhos!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16636, 33, "Ouçam a minha instrução, e serão sábios. Não a desprezem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16637, 34, "Como é feliz o homem que me ouve, vigiando diariamente à minha porta, esperando junto às portas da minha casa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16638, 35, "Pois todo aquele que me encontra, encontra a vida e recebe o favor do Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16639, 36, "Mas aquele que de mim se afasta, a si mesmo se agride; todos os que me odeiam amam a morte"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16640, 1, "A sabedoria construiu sua casa; ergueu suas sete colunas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16641, 2, "Matou animais para a refeição, preparou seu vinho e arrumou sua mesa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16642, 3, "E enviou as servas para fazerem convites desde o ponto mais alto da cidade, clamando:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16643, 4, """Venham todos os inexperientes! "" Aos que não têm bom senso ela diz:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16644, 5, """Venham comer a minha comida e beber o vinho que preparei.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16645, 6, "Deixem a insensatez, e vocês terão vida; andem pelo caminho do entendimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16646, 7, """Quem corrige o zombador traz sobre si o insulto; quem repreende o ímpio mancha o próprio nome.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16647, 8, "Não repreenda o zombador, caso contrário ele o odiará; repreenda o sábio, e ele o amará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16648, 9, "Instrua o homem sábio, e ele será ainda mais sábio; ensine o homem justo, e ele aumentará o seu saber.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16649, 10, """O temor do Senhor é o princípio da sabedoria, e o conhecimento do Santo é entendimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16650, 11, "Pois por meu intermédio os seus dias serão multiplicados, e o tempo da sua vida se prolongará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16651, 12, "Se você for sábio, o benefício será seu; se for zombador, sofrerá as conseqüências"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16652, 13, "A insensatez é pura exibição, sedução e ignorância.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16653, 14, "Sentada à porta de sua casa, no ponto mais alto da cidade,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16654, 15, "clama aos que passam por ali seguindo o seu caminho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16655, 16, """Venham todos os inexperientes! "" Aos que não têm bom senso ela diz:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16656, 17, """A água roubada é doce, e o pão que se come escondido é saboroso! """, 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16657, 18, "Mas eles nem imaginam que ali estão os espíritos dos mortos, que os seus convidados estão nas profundezas da sepultura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16658, 1, "Provérbios de Salomão: O filho sábio dá alegria ao pai; o filho tolo dá tristeza à mãe.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16659, 2, "Os tesouros de origem desonesta não servem para nada, mas a retidão livra da morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16660, 3, "O Senhor não deixa o justo passar fome, mas frustra a ambição dos ímpios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16661, 4, "As mãos preguiçosas empobrecem o homem, porém as mãos diligentes lhe trazem riqueza.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16662, 5, "Aquele que faz a colheita no verão é filho sensato, mas aquele que dorme durante a ceifa é filho que causa vergonha.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16663, 6, "As bênçãos coroam a cabeça dos justos, mas a boca dos ímpios abriga a violência.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16664, 7, "A memória deixada pelos justos será uma bênção, mas o nome dos ímpios apodrecerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16665, 8, "Os sábios de coração aceitam mandamentos, mas a boca do insensato o leva à ruína.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16666, 9, "Quem anda com integridade anda com segurança, mas quem segue veredas tortuosas será descoberto.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16667, 10, "Aquele que pisca maliciosamente causa tristeza, e a boca do insensato o leva à ruína.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16668, 11, "A boca do justo é fonte de vida, mas a boca dos ímpios abriga a violência.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16669, 12, "O ódio provoca dissensão, mas o amor cobre todos os pecados.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16670, 13, "A sabedoria está nos lábios dos que têm discernimento, mas a vara é para as costas daquele que não tem juízo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16671, 14, "Os sábios acumulam conhecimento, mas a boca do insensato é um convite à ruína.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16672, 15, "A riqueza dos ricos é a sua cidade fortificada, mas a pobreza é a ruína dos pobres.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16673, 16, "O salário do justo lhe traz vida, mas a renda do ímpio lhe traz castigo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16674, 17, "Quem acolhe a disciplina mostra o caminho da vida, mas quem ignora a repreensão desencaminha outros.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16675, 18, "Quem esconde o ódio tem lábios mentirosos, e quem espalha calúnia é tolo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16676, 19, "Quando são muitas as palavras o pecado está presente, mas quem controla a língua é sensato.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16677, 20, "A língua dos justos é prata escolhida, mas o coração dos ímpios quase não tem valor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16678, 21, "As palavras dos justos dão sustento a muitos, mas os insensatos morrem por falta de juízo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16679, 22, "A bênção do Senhor traz riqueza, e não inclui dor alguma.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16680, 23, "O tolo encontra prazer na má conduta, mas o homem cheio de entendimento deleita-se na sabedoria.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16681, 24, "O que o ímpio teme lhe acontecerá; o que os justos desejam lhes será concedido.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16682, 25, "Passada a tempestade, o ímpio já não existe, mas o justo permanece firme para sempre.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16683, 26, "Como o vinagre para os dentes e a fumaça para os olhos, assim é o preguiçoso para aqueles que o enviam.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16684, 27, "O temor do Senhor prolonga a vida, mas a vida do ímpio é abreviada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16685, 28, "O que o justo almeja redunda em alegria, mas as esperanças dos ímpios dão em nada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16686, 29, "O caminho do Senhor é o refúgio dos íntegros, mas é a ruína dos que praticam o mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16687, 30, "Os justos jamais serão desarraigados, mas os ímpios pouco duram na terra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16688, 31, "A boca do justo produz sabedoria, mas a língua perversa será extirpada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16689, 32, "Os lábios do justo sabem o que é próprio, mas a boca dos ímpios só conhece a perversidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16690, 1, "O Senhor repudia balanças desonestas, mas os pesos exatos lhe dão prazer.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16691, 2, "Quando vem o orgulho, chega a desgraça, mas a sabedoria está com os humildes.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16692, 3, "A integridade dos justos os guia, mas a falsidade dos infiéis os destrói.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16693, 4, "De nada vale a riqueza no dia da ira divina, mas a retidão livra da morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16694, 5, "A retidão dos irrepreensíveis lhes abre um caminho reto, mas os ímpios são abatidos por sua própria impiedade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16695, 6, "A justiça dos justos os livra, mas o desejo dos infiéis os aprisiona.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16696, 7, "Quando morre o ímpio, sua esperança perece; tudo o que ele esperava do seu poder dá em nada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16697, 8, "O justo é salvo das tribulações, e estas são transferidas para o ímpio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16698, 9, "Com a boca o ímpio pretende destruir o próximo, mas pelo seu conhecimento o justo se livra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16699, 10, "Quando os justos prosperam, a cidade exulta; quando os ímpios perecem, há cantos de alegria.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16700, 11, "Pela bênção dos justos a cidade é exaltada, mas pela boca dos ímpios é destruída.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16701, 12, "O homem que não tem juízo ridiculariza o seu próximo, mas o que tem entendimento refreia a língua.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16702, 13, "Quem muito fala trai a confidência, mas quem merece confiança guarda o segredo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16703, 14, "Sem diretrizes a nação cai; o que a salva é ter muitos conselheiros.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16704, 15, "Quem serve de fiador certamente sofrerá, mas quem se nega a fazê-lo está seguro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16705, 16, "A mulher bondosa conquista o respeito, mas os homens cruéis só conquistam riquezas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16706, 17, "Quem faz o bem aos outros, a si mesmo o faz; o homem cruel causa o seu próprio mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16707, 18, "O ímpio recebe salários enganosos, mas quem semeia a retidão colhe segura recompensa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16708, 19, "Quem permanece na justiça viverá, mas quem sai em busca do mal corre para a morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16709, 20, "O Senhor detesta os perversos de coração, mas os de conduta irrepreensível dão-lhe prazer.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16710, 21, "Esteja certo de que os ímpios não ficarão sem castigo, mas os justos serão poupados.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16711, 22, "Como anel de ouro em focinho de porco, assim é a mulher bonita, mas indiscreta.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16712, 23, "O desejo dos justos resulta em bem; a esperança dos ímpios, em ira.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16713, 24, "Há quem dê generosamente, e vê aumentar suas riquezas; outros retêm o que deveriam dar, e caem na pobreza.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16714, 25, "O generoso prosperará; quem dá alívio aos outros, alívio receberá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16715, 26, "O povo amaldiçoa aquele que esconde o trigo, mas a bênção coroa aquele que logo se dispõe a vendê-lo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16716, 27, "Quem procura o bem será respeitado; já o mal vai de encontro a quem o busca.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16717, 28, "Quem confia em suas riquezas certamente cairá, mas os justos florescerão como a folhagem verdejante.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16718, 29, "Quem causa problemas à sua família herdará somente vento; o insensato será servo do sábio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16719, 30, "O fruto da retidão é árvore de vida, e aquele que conquista almas é sábio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16720, 31, "Se os justos recebem a punição que merecem na terra, quanto mais o ímpio e o pecador!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16721, 1, "Todo o que ama a disciplina ama o conhecimento, mas aquele que odeia a repreensão é tolo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16722, 2, "O homem bom obtém o favor do Senhor, mas o homem que planeja maldades o Senhor condena.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16723, 3, "Ninguém consegue se firmar mediante a impiedade, e não se pode desarraigar o justo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16724, 4, "A mulher exemplar é a coroa do seu marido, mas a de comportamento vergonhoso é como câncer em seus ossos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16725, 5, "Os planos dos justos são retos, mas o conselho dos ímpios é enganoso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16726, 6, "As palavras dos ímpios são emboscadas mortais, mas quando os justos falam há livramento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16727, 7, "Os ímpios são derrubados e desaparecem, mas a casa dos justos permanece firme.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16728, 8, "O homem é louvado segundo a sua sabedoria, mas o que tem o coração perverso é desprezado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16729, 9, "Melhor é não ser ninguém e, ainda assim, ter quem o sirva, do que fingir ser alguém e não ter comida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16730, 10, "O justo cuida bem dos seus rebanhos, mas até os atos mais bondosos dos ímpios são cruéis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16731, 11, "Quem trabalha a sua terra terá fartura de alimento, mas quem vai atrás de fantasias não tem juízo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16732, 12, "Os ímpios cobiçam o despojo tomado pelos maus, mas a raiz do justo floresce.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16733, 13, "O mau se enreda no pecado do falar, mas o justo não cai nessas dificuldades.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16734, 14, "Do fruto de sua boca o homem se beneficia, e o trabalho de suas mãos será recompensado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16735, 15, "O caminho do insensato parece-lhe justo, mas o sábio ouve os conselhos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16736, 16, "O insensato revela de imediato o seu aborrecimento, mas o homem prudente ignora o insulto.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16737, 17, "A testemunha fiel dá testemunho honesto, mas a testemunha falsa conta mentiras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16738, 18, "Há palavras que ferem como espada, mas a língua dos sábios traz a cura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16739, 19, "Os lábios que dizem a verdade permanecem para sempre, mas a língua mentirosa dura apenas um instante.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16740, 20, "O engano está no coração dos que maquinam o mal, mas a alegria está entre os que promovem a paz.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16741, 21, "Nenhum mal atingirá o justo, mas os ímpios estão cobertos de problemas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16742, 22, "O Senhor odeia os lábios mentirosos, mas se deleita com os que falam a verdade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16743, 23, "O homem prudente não alardeia o seu conhecimento, mas o coração dos tolos derrama insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16744, 24, "As mãos diligentes governarão, mas os preguiçosos acabarão escravos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16745, 25, "O coração ansioso deprime o homem, mas uma palavra bondosa o anima.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16746, 26, "O homem honesto é cauteloso em suas amizades, mas o caminho dos ímpios os leva a perder-se.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16747, 27, "O preguiçoso não aproveita a sua caça, mas o diligente dá valor a seus bens.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16748, 28, "No caminho da justiça está a vida; essa é a vereda que preserva da morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16749, 1, "O filho sábio acolhe a instrução do pai, mas o zombador não ouve a repreensão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16750, 2, "Do fruto de sua boca o homem desfruta coisas boas, mas o que os infiéis desejam é violência.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16751, 3, "Quem guarda a sua boca guarda a sua vida, mas quem fala demais acaba se arruinando.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16752, 4, "O preguiçoso deseja e nada consegue, mas os desejos do diligente são amplamente satisfeitos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16753, 5, "Os justos odeiam o que é falso, mas os ímpios trazem vergonha e desgraça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16754, 6, "A retidão protege o homem íntegro, mas a impiedade derruba o pecador.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16755, 7, "Alguns fingem que são ricos e nada têm; outros fingem que são pobres, e têm grande riqueza.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16756, 8, "As riquezas de um homem servem de resgate para a sua vida, mas o pobre nunca recebe ameaças.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16757, 9, "A luz dos justos resplandece esplendidamente, mas a lâmpada dos ímpios apaga-se.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16758, 10, "O orgulho só gera discussões, mas a sabedoria está com os que tomam conselho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16759, 11, "O dinheiro ganho com desonestidade diminuirá, mas quem o ajunta aos poucos terá cada vez mais.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16760, 12, "A esperança que se retarda deixa o coração doente, mas o anseio satisfeito é árvore de vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16761, 13, "Quem zomba da instrução pagará por ela, mas aquele que respeita o mandamento será recompensado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16762, 14, "O ensino dos sábios é fonte de vida, e afasta o homem das armadilhas da morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16763, 15, "O bom entendimento conquista favor, mas o caminho do infiel é áspero.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16764, 16, "Todo homem prudente age com base no conhecimento, mas o tolo expõe a sua insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16765, 17, "O mensageiro ímpio cai em dificuldade, mas o enviado digno de confiança traz a cura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16766, 18, "Quem despreza a disciplina cai na pobreza e na vergonha, mas quem acolhe a repreensão recebe tratamento honroso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16767, 19, "O anseio satisfeito agrada a alma, mas o tolo detesta afastar-se do mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16768, 20, "Aquele que anda com os sábios será cada vez mais sábio, mas o companheiro dos tolos acabará mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16769, 21, "O infortúnio persegue o pecador, mas a prosperidade é a recompensa do justo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16770, 22, "O homem bom deixa herança para os filhos de seus filhos, mas a riqueza do pecador é armazenada para os justos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16771, 23, "A lavoura do pobre produz alimento com fartura, mas por falta de justiça ele o perde.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16772, 24, "Quem se nega a castigar seu filho não o ama; quem o ama não hesita em discipliná-lo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16773, 25, "O justo come até satisfazer o apetite, mas os ímpios permanecem famintos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16774, 1, "A mulher sábia edifica a sua casa, mas com as próprias mãos a insensata derruba a sua.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16775, 2, "Quem anda direito teme o Senhor, mas quem segue caminhos enganosos o despreza.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16776, 3, "A conversa do insensato traz a vara para as suas costas, mas os lábios dos sábios os protegem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16777, 4, "Onde não há bois o celeiro fica vazio, mas da força do boi vem a grande colheita.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16778, 5, "A testemunha sincera não engana, mas a falsa transborda em mentiras.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16779, 6, "O zombador busca sabedoria e nada encontra, mas o conhecimento vem facilmente ao que tem discernimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16780, 7, "Mantenha-se longe do tolo, pois você não achará conhecimento no que ele falar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16781, 8, "A sabedoria do homem prudente é discernir o seu caminho, mas a insensatez dos tolos é enganosa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16782, 9, "Os insensatos zombam da idéia de reparar o pecado cometido, mas a boa vontade está entre os justos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16783, 10, "Cada coração conhece a sua própria amargura, e não há quem possa partilhar sua alegria.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16784, 11, "A casa dos ímpios será destruída, mas a tenda dos justos florescerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16785, 12, "Há caminho que parece certo ao homem, mas no final conduz à morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16786, 13, "Mesmo no riso o coração pode sofrer, e a alegria pode terminar em tristeza.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16787, 14, "Os infiéis receberão a retribuição de sua conduta, mas o homem bom será recompensado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16788, 15, "O inexperiente acredita em qualquer coisa, mas o homem prudente vê bem onde pisa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16789, 16, "O sábio é cauteloso e evita o mal, mas o tolo é impetuoso e irresponsável.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16790, 17, "Quem é irritadiço faz tolices, e o homem cheio de astúcias é odiado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16791, 18, "Os inexperientes herdam a insensatez, mas o conhecimento é a coroa dos prudentes.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16792, 19, "Os maus se inclinarão diante dos homens de bem, e os ímpios, às portas da justiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16793, 20, "Os pobres são evitados até por seus vizinhos, mas os amigos dos ricos são muitos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16794, 21, "Quem despreza o próximo comete pecado, mas como é feliz quem trata com bondade os necessitados!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16795, 22, "Não é certo que se perdem os que só pensam no mal? Mas os que planejam o bem encontram amor e fidelidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16796, 23, "Todo trabalho árduo traz proveito, mas o só falar leva à pobreza.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16797, 24, "A riqueza dos sábios é a sua coroa, mas a insensatez dos tolos produz apenas insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16798, 25, "A testemunha que fala a verdade salva vidas, mas a testemunha falsa é enganosa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16799, 26, "Aquele que teme ao Senhor possui uma fortaleza segura, refúgio para os seus filhos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16800, 27, "O temor do Senhor é fonte de vida, e afasta das armadilhas da morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16801, 28, "Uma grande população é a glória do rei, mas, sem súditos, o príncipe está arruinado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16802, 29, "O homem paciente dá prova de grande entendimento, mas o precipitado revela insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16803, 30, "O coração em paz dá vida ao corpo, mas a inveja apodrece os ossos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16804, 31, "Aquele que oprime o pobre com isso despreza o seu Criador, mas quem ao necessitado trata com bondade honra a Deus.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16805, 32, "Quando chega a calamidade, os ímpios são derrubados; os justos, porém, até em face da morte encontram refúgio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16806, 33, "A sabedoria repousa no coração dos que têm discernimento e mesmo entre os tolos ela se deixa conhecer.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16807, 34, "A justiça engrandece a nação, mas o pecado é uma vergonha para qualquer povo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16808, 35, "O servo sábio agrada o rei, mas o que procede vergonhosamente incorre em sua ira.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16809, 1, "A resposta calma desvia a fúria, mas a palavra ríspida desperta a ira.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16810, 2, "A língua dos sábios torna atraente o conhecimento, mas a boca dos tolos derrama insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16811, 3, "Os olhos do Senhor estão em toda parte, observando atentamente os maus e os bons.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16812, 4, "O falar amável é árvore de vida, mas o falar enganoso esmaga o espírito.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16813, 5, "O insensato faz pouco caso da disciplina de seu pai, mas quem acolhe a repreensão revela prudência.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16814, 6, "A casa do justo contém grande tesouro, mas os rendimentos dos ímpios lhes trazem inquietação.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16815, 7, "As palavras dos sábios espalham conhecimento; mas o coração dos tolos não é assim.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16816, 8, "O Senhor detesta o sacrifício dos ímpios, mas a oração do justo o agrada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16817, 9, "O Senhor detesta o caminho dos ímpios, mas ama quem busca a justiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16818, 10, "Há uma severa lição para quem abandona o seu caminho; quem despreza a repreensão morrerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16819, 11, "A Sepultura e a Destruição estão abertas diante do Senhor; quanto mais os corações dos homens!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16820, 12, "O zombador não gosta de quem o corrige, nem procura a ajuda do sábio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16821, 13, "A alegria do coração transparece no rosto, mas o coração angustiado oprime o espírito.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16822, 14, "O coração que sabe discernir busca o conhecimento, mas a boca dos tolos alimenta-se de insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16823, 15, "Todos os dias do oprimido são infelizes, mas o coração bem disposto está sempre em festa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16824, 16, "É melhor ter pouco com o temor do Senhor do que grande riqueza com inquietação.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16825, 17, "É melhor ter verduras na refeição onde há amor do que um boi gordo acompanhado de ódio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16826, 18, "O homem irritável provoca dissensão, mas quem é paciente acalma a discussão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16827, 19, "O caminho do preguiçoso é cheio de espinhos, mas o caminho do justo é uma estrada plana.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16828, 20, "O filho sábio dá alegria a seu pai, mas o tolo despreza a sua mãe.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16829, 21, "A insensatez alegra quem não tem bom senso, mas o homem de entendimento procede com retidão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16830, 22, "Os planos fracassam por falta de conselho, mas são bem sucedidos quando há muitos conselheiros.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16831, 23, "Dar resposta apropriada é motivo de alegria; e como é bom um conselho na hora certa!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16832, 24, "O caminho da vida conduz para cima quem é sensato, para que ele não desça à sepultura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16833, 25, "O Senhor derruba a casa do orgulhoso, mas mantém intactos os limites da propriedade da viúva.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16834, 26, "O Senhor detesta os pensamentos dos maus, mas se agrada de palavras sem maldade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16835, 27, "O avarento põe sua família em apuros, mas quem repudia o suborno viverá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16836, 28, "O justo pensa bem antes de responder, mas a boca dos ímpios jorra o mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16837, 29, "O Senhor está longe dos ímpios, mas ouve a oração dos justos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16838, 30, "Um olhar animador dá alegria ao coração, e as boas notícias revigoram os ossos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16839, 31, "Quem ouve a repreensão construtiva terá lugar permanente entre os sábios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16840, 32, "Quem recusa a disciplina faz pouco caso de si mesmo, mas quem ouve a repreensão obtém entendimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16841, 33, "O temor do Senhor ensina a sabedoria, e a humildade antecede a honra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16842, 1, "Ao homem pertencem os planos do coração, mas do Senhor vem a resposta da língua.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16843, 2, "Todos os caminhos do homem lhe parecem puros, mas o Senhor avalia o espírito.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16844, 3, "Consagre ao Senhor tudo o que você faz, e os seus planos serão bem-sucedidos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16845, 4, "O Senhor faz tudo com um propósito; até os ímpios para o dia do castigo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16846, 5, "O Senhor detesta os orgulhosos de coração. Sem dúvida serão punidos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16847, 6, "Com amor e fidelidade se faz expiação pelo pecado; com o temor do Senhor o homem evita o mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16848, 7, "Quando os caminhos de um homem são agradáveis ao Senhor, ele faz que até os seus inimigos vivam em paz com ele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16849, 8, "É melhor ter pouco com retidão do que muito com injustiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16850, 9, "Em seu coração o homem planeja o seu caminho, mas o Senhor determina os seus passos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16851, 10, "Os lábios do rei falam com grande autoridade; sua boca não deve trair a justiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16852, 11, "Balanças e pesos honestos vêm do Senhor; todos os pesos da bolsa são feitos por ele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16853, 12, "Os reis detestam a prática da maldade, porquanto o trono se firma pela justiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16854, 13, "O rei se agrada dos lábios honestos; e dá valor ao homem que fala a verdade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16855, 14, "A ira do rei é um mensageiro da morte, mas o homem sábio a acalmará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16856, 15, "Alegria no rosto do rei é sinal de vida; seu favor é como nuvem de chuva na primavera.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16857, 16, "É melhor obter sabedoria do que ouro! É melhor obter entendimento do que prata!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16858, 17, "A vereda do justo evita o mal; quem guarda o seu caminho preserva a sua vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16859, 18, "O orgulho vem antes da destruição; o espírito altivo, antes da queda.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16860, 19, "Melhor é ter espírito humilde entre os oprimidos do que partilhar despojos com os orgulhosos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16861, 20, "Quem examina cada questão com cuidado, prospera, e feliz é aquele que confia no Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16862, 21, "O sábio de coração é considerado prudente; quem fala com equilíbrio promove a instrução.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16863, 22, "O entendimento é fonte de vida para aqueles que o têm, mas a insensatez traz castigo aos insensatos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16864, 23, "O coração do sábio ensina a sua boca, e os seus lábios promovem a instrução.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16865, 24, "As palavras agradáveis são como um favo de mel, são doces para a alma e trazem cura para os ossos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16866, 25, "Há caminho que parece reto ao homem, mas no final conduz à morte.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16867, 26, "O apetite do trabalhador o obriga a trabalhar; a sua fome o impulsiona.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16868, 27, "O homem sem caráter maquina o mal, suas palavras são um fogo devorador.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16869, 28, "O homem perverso provoca dissensão, e o que espalha boatos afasta bons amigos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16870, 29, "O violento recruta o seu próximo e o leva por um caminho ruim.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16871, 30, "Quem pisca os olhos planeja o mal; quem franze os lábios já o vai praticar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16872, 31, "O cabelo grisalho é uma coroa de esplendor, e se obtém mediante uma vida justa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16873, 32, "Melhor é o homem paciente do que o guerreiro, mais vale controlar o seu espírito do que conquistar uma cidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16874, 33, "A sorte é lançada no colo, mas a decisão vem do Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16875, 1, "Melhor é um pedaço de pão seco com paz e tranqüilidade do que uma casa onde há banquetes, e muitas brigas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16876, 2, "O servo sábio dominará sobre o filho de conduta vergonhosa, e participará da herança como um dos irmãos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16877, 3, "O crisol é para a prata e o forno é para o ouro, mas o Senhor prova o coração.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16878, 4, "O ímpio dá atenção aos lábios maus; o mentiroso dá ouvidos à língua destruidora.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16879, 5, "Quem zomba dos pobres mostra desprezo pelo Criador deles; quem se alegra com a desgraça não ficará sem castigo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16880, 6, "Os filhos dos filhos são uma coroa para os idosos, e os pais são o orgulho dos seus filhos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16881, 7, "Os lábios arrogantes não ficam bem ao insensato; muito menos os lábios mentirosos ao governante!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16882, 8, "O suborno é um recurso fascinante para aquele que o oferece; aonde quer que vá, ele tem sucesso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16883, 9, "Aquele que cobre uma ofensa promove amor, mas quem a lança em rosto separa bons amigos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16884, 10, "A repreensão faz marca mais profunda no homem de entendimento do que cem açoites no tolo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16885, 11, "O homem mau só pende para a rebeldia; por isso um oficial impiedoso será enviado contra ele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16886, 12, "Melhor é encontrar uma ursa da qual roubaram os filhotes do que um tolo em sua insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16887, 13, "Quem retribui o bem com o mal, jamais deixará de ter mal no seu lar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16888, 14, "Começar uma discussão é como abrir brecha num dique; por isso resolva a questão antes que surja a contenda.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16889, 15, "Absolver o ímpio e condenar o justo, são coisas que o Senhor odeia.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16890, 16, "De que serve o dinheiro na mão do tolo, já que ele não quer obter sabedoria?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16891, 17, "O amigo ama em todos os momentos; é um irmão na adversidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16892, 18, "O homem sem juízo, com um aperto de mãos se compromete e se torna fiador do seu próximo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16893, 19, "Quem ama a discussão ama o pecado; quem constrói portas altas está procurando a sua ruína.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16894, 20, "O homem de coração perverso não prospera, e o de língua enganosa cai na desgraça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16895, 21, "O filho tolo só dá tristeza, e nenhuma alegria tem o pai do insensato.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16896, 22, "O coração bem disposto é remédio eficiente, mas o espírito oprimido resseca os ossos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16897, 23, "O ímpio aceita às escondidas o suborno para desviar o curso da justiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16898, 24, "O homem de discernimento mantém a sabedoria em vista, mas os olhos do tolo perambulam até os confins da terra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16899, 25, "O filho tolo é a tristeza do seu pai e a amargura daquela que o deu à luz.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16900, 26, "Não é bom castigar o inocente, nem açoitar quem merece ser honrado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16901, 27, "Quem tem conhecimento é comedido no falar, e quem tem entendimento é de espírito sereno.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16902, 28, "Até o insensato passará por sábio, se ficar quieto, e, se contiver a língua, parecerá que tem discernimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16903, 1, "Quem se isola, busca interesses egoístas, e se rebela contra a sensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16904, 2, "O tolo não tem prazer no entendimento, mas sim em expor os seus pensamentos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16905, 3, "Com a impiedade, vem o desprezo, e com a desonra vem a vergonha.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16906, 4, "As palavras do homem são águas profundas, mas a fonte da sabedoria é um ribeiro que transborda.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16907, 5, "Não é bom favorecer os ímpios para privar da justiça o justo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16908, 6, "As palavras do tolo provocam briga, e a sua conversa atrai açoites.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16909, 7, "A conversa do tolo é a sua desgraça, e seus lábios são uma armadilha para a sua alma.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16910, 8, "As palavras do caluniador são como petiscos deliciosos; descem até o íntimo do homem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16911, 9, "Quem relaxa em seu trabalho é irmão do que o destrói.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16912, 10, "O nome do Senhor é uma torre forte; os justos correm para ela e estão seguros.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16913, 11, "A riqueza dos ricos é a sua cidade fortificada, eles a imaginam como um muro que é impossível escalar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16914, 12, "Antes da sua queda o coração do homem se envaidece, mas a humildade antecede a honra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16915, 13, "Quem responde antes de ouvir, comete insensatez e passa vergonha.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16916, 14, "O espírito do homem o sustenta na doença, mas o espírito deprimido, quem o levantará?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16917, 15, "O coração do que tem discernimento adquire conhecimento; os ouvidos dos sábios saem à sua procura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16918, 16, "O presente abre o caminho para aquele que o entrega e o conduz à presença dos grandes.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16919, 17, "O primeiro a apresentar a sua causa parece ter razão, até que outro venha à frente e o questione.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16920, 18, "Lançar sortes resolve contendas e decide questões entre poderosos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16921, 19, "Um irmão ofendido é mais inacessível do que uma cidade fortificada, e as discussões são como as portas trancadas de uma cidadela.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16922, 20, "Do fruto da boca enche-se o estômago do homem; o produto dos lábios o satisfaz.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16923, 21, "A língua tem poder sobre a vida e sobre a morte; os que gostam de usá-la comerão do seu fruto.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16924, 22, "Quem encontra uma esposa encontra algo excelente; recebeu uma bênção do Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16925, 23, "O pobre implora misericórdia, mas o rico responde com aspereza.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16926, 24, "Quem tem muitos amigos pode chegar à ruína, mas existe amigo mais apegado que um irmão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16927, 1, "Melhor é o pobre que vive com integridade do que o tolo que fala perversamente.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16928, 2, "Não é bom ter zelo sem conhecimento, nem ser precipitado e perder o caminho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16929, 3, "É a insensatez do homem que arruína a sua vida, mas o seu coração se ira contra o Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16930, 4, "A riqueza traz muitos amigos, mas até o amigo do pobre o abandona.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16931, 5, "A testemunha falsa não ficará sem castigo, e aquele que despeja mentiras não sairá livre.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16932, 6, "Muitos adulam o governante, e todos são amigos de quem dá presentes.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16933, 7, "O pobre é desprezado por todos os seus parentes, quanto mais por seus amigos! Embora os procure, para pedir-lhes ajuda, não os encontra em lugar nenhum.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16934, 8, "Quem obtém sabedoria ama-se a si mesmo; quem acalenta o entendimento prospera.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16935, 9, "A testemunha falsa não ficará sem castigo, e aquele que despeja mentiras perecerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16936, 10, "Não fica bem o tolo viver no luxo; quanto pior é o servo dominar príncipes!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16937, 11, "A sabedoria do homem lhe dá paciência; sua glória é ignorar as ofensas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16938, 12, "A ira do rei é como o rugido do leão, mas a sua bondade é como o orvalho sobre a relva.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16939, 13, "O filho tolo é a ruína de seu pai, e a esposa briguenta é como uma goteira constante.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16940, 14, "Casas e riquezas herdam-se dos pais, mas a esposa prudente vem do Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16941, 15, "A preguiça leva ao sono profundo, e o preguiçoso passa fome.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16942, 16, "Quem obedece aos mandamentos preserva a sua vida, mas quem despreza os seus caminhos morrerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16943, 17, "Quem trata bem os pobres empresta ao Senhor, e ele o recompensará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16944, 18, "Discipline seu filho, pois nisso há esperança; não queiras a morte dele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16945, 19, "O homem de gênio difícil precisa do castigo; se você o poupar, terá que poupá-lo de novo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16946, 20, "Ouça conselhos e aceite instruções, e acabará sendo sábio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16947, 21, "Muitos são os planos no coração do homem, mas o que prevalece é o propósito do Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16948, 22, "O que se deseja ver num homem é amor perene; melhor é ser pobre do que mentiroso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16949, 23, "O temor do Senhor conduz à vida: Quem o teme pode descansar em paz, livre de problemas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16950, 24, "O preguiçoso põe a mão no prato, e não se dá ao trabalho de levá-la à boca!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16951, 25, "Açoite o zombador, e os inexperientes aprenderão a prudência; repreenda o homem de discernimento, e ele obterá conhecimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16952, 26, "O filho que rouba o pai e expulsa a mãe é causador de vergonha e desonra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16953, 27, "Se você parar de ouvir a instrução, meu filho, irá afastar-se das palavras que dão conhecimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16954, 28, "A testemunha corrupta zomba da justiça, e a boca dos ímpios tem fome de iniqüidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16955, 29, "Os castigos estão preparados para os zombadores, e os açoites para as costas dos tolos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16956, 1, "O vinho é zombadore a bebida fermentada provoca brigas; não é sábio deixar-se dominar por eles.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16957, 2, "O medo que o rei provoca é como o do rugido de um leão; quem o irrita põe em risco a própria vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16958, 3, "É uma honra dar fim a contendas, mas todos insensatos envolvem-se nelas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16959, 4, "O preguiçoso não ara a terra na estação própria; mas na época da colheita procura, e não acha nada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16960, 5, "Os propósitos do coração do homem são águas profundas, mas quem tem discernimento os traz à tona.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16961, 6, "Muitos se dizem amigos leais, mas um homem fiel, quem poderá achar?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16962, 7, "O homem justo leva uma vida íntegra; como são felizes os seus filhos!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16963, 8, "Quando o rei se assenta no trono para julgar, com o olhar esmiúça todo o mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16964, 9, "Quem poderá dizer: ""Purifiquei o coração; estou livre do meu pecado? """, 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16965, 10, "Pesos adulterados e medidas falsificadas, são coisas que o Senhor detesta.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16966, 11, "Até a criança mostra o que é por suas ações; o seu procedimento revelará se ela é pura e justa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16967, 12, "Os ouvidos que ouvem e os olhos que vêem foram feitos pelo Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16968, 13, "Não ame o sono, ou você acabará ficando pobre; fique desperto, e terá alimento de sobra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16969, 14, """Não vale isso! Não vale isso! "", diz o comprador, mas, quando se vai, gaba-se do bom negócio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16970, 15, "Mesmo onde há ouro e rubis em grande quantidade, os lábios que transmitem conhecimento são uma rara preciosidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16971, 16, "Tome-se a veste de quem serve de fiador ao estranho; sirva ela de penhor de quem dá garantia a uma mulher leviana.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16972, 17, "Saborosa é a comida que se obtém com mentiras, mas depois dá areia na boca.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16973, 18, "Os conselhos são importantes para quem quiser fazer planos, e quem sai à guerra precisa de orientação.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16974, 19, "Quem vive contando casos não guarda segredo; por isso, evite quem fala demais.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16975, 20, "Se alguém amaldiçoar seu pai ou sua mãe, a luz de sua vida se extinguirá na mais profunda escuridão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16976, 21, "A herança que se obtém com ganância no princípio, no final não será abençoada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16977, 22, "Não diga: ""Eu o farei pagar pelo mal que me fez! "" Espere pelo Senhor, e ele dará a vitória a você.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16978, 23, "O Senhor detesta pesos adulterados, e balanças falsificadas não o agradam.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16979, 24, "Os passos do homem são dirigidos pelo Senhor. Como poderia alguém discernir o seu próprio caminho?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16980, 25, "É uma armadilha consagrar algo precipitadamente, e só pensar nas conseqüências depois que se fez o voto.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16981, 26, "O rei sábio abana os ímpios, e passa sobre eles a roda de debulhar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16982, 27, "O espírito do homem é a lâmpada do Senhor, e vasculha cada parte do seu ser.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16983, 28, "A bondade e a fidelidade preservam o rei; por sua bondade ele dá firmeza ao seu trono.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16984, 29, "A beleza dos jovens está na sua força; a glória dos idosos, nos seus cabelos brancos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16985, 30, "Os golpes e os ferimentos eliminam o mal; os açoites limpam as profundezas do ser.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16986, 1, "O coração do rei é como um rio controlado pelo Senhor; ele o dirige para onde quer.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16987, 2, "Todos os caminhos do homem lhe parecem justos, mas o Senhor pesa o coração.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16988, 3, "Fazer o que é justo e certo é mais aceitável ao Senhor do que oferecer sacrifícios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16989, 4, "A vida de pecado dos ímpios se vê no olhar orgulhoso e no coração arrogante,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16990, 5, "Os planos bem elaborados levam à fartura; mas o apressado sempre acaba na miséria.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16991, 6, "A fortuna obtida com língua mentirosa é ilusão fugidia e armadilha mortal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16992, 7, "A violência dos ímpios os arrastará, pois recusam-se a agir corretamente.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16993, 8, "O caminho do culpado é tortuoso, mas a conduta do inocente é reta.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16994, 9, "Melhor é viver num canto sob o telhado do que repartir a casa com uma mulher briguenta.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16995, 10, "O desejo do perverso é fazer o mal; ele não tem dó do próximo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16996, 11, "Quando o zombador é castigado, o inexperiente obtém sabedoria; quando o sábio recebe instrução, obtém conhecimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16997, 12, "O justo observa a casa dos ímpios e os faz cair na desgraça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16998, 13, "Quem fecha os ouvidos ao clamor dos pobres também clamará e não terá resposta.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (16999, 14, "O presente que se faz em segredo acalma a ira, e o suborno oferecido às ocultas apazigua a maior fúria.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17000, 15, "Quando se faz justiça, o justo se alegra, mas os malfeitores se apavoram.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17001, 16, "Quem se afasta do caminho da sensatez repousará na companhia dos mortos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17002, 17, "Quem se entrega aos prazeres passará necessidade; quem se apega ao vinho e ao azeite jamais será rico.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17003, 18, "O ímpio serve de resgate para o justo, e o infiel, para o homem íntegro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17004, 19, "Melhor é viver no deserto do que com uma mulher briguenta e amargurada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17005, 20, "Na casa do sábio há comida e azeite armazenados, mas o tolo devora tudo o que pode.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17006, 21, "Quem segue a justiça e a lealdade encontra vida, justiça e honra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17007, 22, "O sábio conquista a cidade dos valentes e derruba a fortaleza em que eles confiam.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17008, 23, "Quem é cuidadoso no que fala evita muito sofrimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17009, 24, "O vaidoso e arrogante, chama-se zombador; ele age com extremo orgulho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17010, 25, "O preguiçoso morre de tanto desejar e de nunca pôr as mãos no trabalho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17011, 26, "O dia inteiro ele deseja mais e mais, enquanto o justo reparte sem cessar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17012, 27, "O sacrifício dos ímpios já por si é detestável; quanto mais quando oferecido com más intenções.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17013, 28, "A testemunha falsa perecerá, mas o testemunho do homem bem informado permanecerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17014, 29, "O ímpio mostra no rosto a sua arrogância, mas o justo mantém em ordem o seu caminho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17015, 30, "Não há sabedoria alguma, nem discernimento algum, nem plano algum que possa opor-se ao Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17016, 31, "Prepara-se o cavalo para o dia da batalha, mas o Senhor é que dá a vitória.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17017, 1, "A boa reputação vale mais que grandes riquezas; desfrutar de boa estima vale mais que prata e ouro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17018, 2, "O rico e o pobre têm isto em comum: O Senhor é o Criador de ambos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17019, 3, "O prudente percebe o perigo e busca refúgio; o inexperiente segue adiante e sofre as conseqüências.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17020, 4, "A recompensa da humildade e do temor do Senhor são a riqueza, a honra e a vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17021, 5, "No caminho do perverso há espinhos e armadilhas; quem quer proteger a própria vida mantém-se longe dele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17022, 6, "Instrua a criança segundo os objetivos que você tem para ela, e mesmo com o passar dos anos não se desviará deles.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17023, 7, "O rico domina sobre o pobre; quem toma emprestado é escravo de quem empresta.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17024, 8, "Quem semeia a injustiça colhe a maldade; o castigo da sua arrogância será completo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17025, 9, "Quem é generoso será abençoado, pois reparte o seu pão com o pobre.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17026, 10, "Quando se manda embora o zombador, a briga acaba; cessam as contendas e os insultos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17027, 11, "Quem ama a sinceridade de coração e se expressa com elegância será amigo do rei.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17028, 12, "Os olhos do Senhor protegem o conhecimento, mas ele frustra as palavras dos infiéis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17029, 13, "O preguiçoso diz: ""Há um leão lá fora! "" ""Serei morto na rua! """, 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17030, 14, "A conversa da mulher imoral é uma cova profunda; nela cairá quem estiver sob a ira do Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17031, 15, "A insensatez está ligada ao coração da criança, mas a vara da disciplina a livrará dela.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17032, 16, "Tanto quem oprime o pobre para enriquecer-se como quem faz cortesia ao rico, com certeza passarão necessidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17033, 17, "Preste atenção e ouça os ditados dos sábios; aplique o coração ao meu ensino.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17034, 18, "Será uma satisfação guardá-los no íntimo e tê-los todos na ponta da língua.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17035, 19, "Para que você confie no Senhor, a você hoje ensinarei.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17036, 20, "Já não lhe escrevi conselhos e instruções,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17037, 21, "ensinando-lhe palavras dignas de confiança, para que você responda com a verdade a quem o enviou?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17038, 22, "Não explore os pobres por serem pobres, nem oprima os necessitados no tribunal,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17039, 23, "pois o Senhor será o advogado deles, e despojará da vida os que os despojarem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17040, 24, "Não se associe com quem vive de mau humor, nem ande em companhia de quem facilmente se ira;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17041, 25, "do contrário você acabará imitando essa conduta e cairá em armadilha mortal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17042, 26, "Não seja como aqueles que, com um aperto de mãos, empenham-se com outros e se tornam fiadores de dívidas;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17043, 27, "se você não tem como pagá-las, por que correr o risco de perder até a cama em que dorme?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17044, 28, "Não mude de lugar os antigos marcos que limitam as propriedades e que foram colocados por seus antepassados.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17045, 29, "Você já observou um homem habilidoso em seu trabalho? Será promovido ao serviço real; não trabalhará para gente obscura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17046, 1, "Quando você se assentar para uma refeição com alguma autoridade, observe com atenção quem está diante de você,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17047, 2, "e encoste a faca à sua própria garganta, se estiver com grande apetite.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17048, 3, "Não deseje as iguarias que lhe oferece, pois podem ser enganosas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17049, 4, "Não esgote suas forças tentando ficar rico; tenha bom senso!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17050, 5, "As riquezas desaparecem assim que você as contempla; elas criam asas e voam como águias pelo céu.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17051, 6, "Não aceite a refeição de um hospedeiro invejoso, nem deseje as iguarias que lhe oferece;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17052, 7, "pois ele só pensa nos gastos. Ele lhe diz: ""Coma e beba! "", mas não fala com sinceridade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17053, 8, "Você vomitará o pouco que comeu, e desperdiçará a sua cordialidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17054, 9, "Não vale a pena conversar com o tolo, pois ele despreza a sabedoria do que você fala.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17055, 10, "Não mude de lugar os antigos marcos de propriedade, nem invada as terras dos órfãos,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17056, 11, "pois Aquele que defende os direitos deles é forte. Ele lutará contra você para defendê-los.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17057, 12, "Dedique à disciplina o seu coração, e os seus ouvidos às palavras que dão conhecimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17058, 13, "Não evite disciplinar a criança; se você a castigar com a vara, ela não morrerá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17059, 14, "Castigue-a, você mesmo, com a vara, e assim a livrará da sepultura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17060, 15, "Meu filho, se o seu coração for sábio, o meu coração se alegrará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17061, 16, "Sentirei grande alegria quando os seus lábios falarem com retidão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17062, 17, "Não inveje os pecadores em seu coração; melhor será que tema sempre ao Senhor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17063, 18, "Se agir assim, certamente haverá bom futuro para você, e a sua esperança não falhará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17064, 19, "Ouça, meu filho, e seja sábio; guie o seu coração pelo bom caminho.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17065, 20, "Não ande com os que se encharcam de vinho, nem com os que se empanturram de carne.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17066, 21, "Pois os bêbados e os glutões se empobrecerão, e a sonolência os vestirá de trapos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17067, 22, "Ouça o seu pai, que o gerou; não despreze sua mãe quando ela envelhecer.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17068, 23, "Compre a verdade e não abra mão dela, nem tampouco da sabedoria, da disciplina e do discernimento.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17069, 24, "O pai do justo exultará de júbilo; quem tem filho sábio nele se alegra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17070, 25, "Bom será que se alegrem seu pai e sua mãe e que exulte a mulher que o deu à luz!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17071, 26, "Meu filho, dê-me o seu coração; mantenha os seus olhos em meus caminhos,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17072, 27, "pois a prostituta é uma cova profunda, e a mulher pervertida é um poço estreito.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17073, 28, "Como o assaltante, ela fica de tocaia, e multiplica entre os homens os infiéis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17074, 29, "De quem são os ais? De quem as tristezas? E as brigas, de quem são? E os ferimentos desnecessários? De quem são os olhos vermelhos?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17075, 30, "Dos que se demoram bebendo vinho, dos que andam à procura de bebida misturada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17076, 31, "Não se deixe atrair pelo vinho quando está vermelho, quando cintila no copo e escorre suavemente!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17077, 32, "No fim, ele morde como serpente e envenena como víbora.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17078, 33, "Seus olhos verão coisas estranhas, e sua mente imaginará coisas distorcidas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17079, 34, "Você será como quem dorme no meio do mar, como quem se deita no alto das cordas do mastro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17080, 35, "E dirá: ""Espancaram-me, mas eu nada senti! Bateram em mim, mas nem percebi! Quando acordarei para que possa beber mais uma vez? """, 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17081, 1, "Não tenha inveja dos ímpios, nem deseje a companhia deles;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17082, 2, "pois destruição é o que planejam no coração, e só falam de violência.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17083, 3, "Com sabedoria se constrói a casa, e com discernimento se consolida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17084, 4, "Pelo conhecimento os seus cômodos se enchem do que é precioso e agradável.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17085, 5, "O homem sábio é poderoso, e quem tem conhecimento aumenta a sua força;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17086, 6, "quem sai à guerra precisa de orientação, e com muitos conselheiros se obtém a vitória.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17087, 7, "A sabedoria é elevada demais para o insensato; ele não sabe o que dizer nas assembléias.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17088, 8, "Quem maquina o mal será conhecido como criador de intrigas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17089, 9, "A intriga do insensato é pecado, e o zombador é detestado pelos homens.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17090, 10, "Se você vacila no dia da dificuldade, como será limitada a sua força!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17091, 11, "Liberte os que estão sendo levados para a morte; socorra os que caminham trêmulos para a matança!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17092, 12, "Mesmo que você diga: ""Não sabíamos o que estava acontecendo! "" Não o perceberia aquele que pesa os corações? Não o saberia aquele que preserva a sua vida? Não retribuirá ele a cada um segundo o seu procedimento?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17093, 13, "Coma mel, meu filho. É bom. O favo é doce ao paladar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17094, 14, "Saiba que a sabedoria também será boa para a sua alma; se você a encontrar, certamente haverá futuro para você, e a sua esperança não vai decepcioná-lo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17095, 15, "Não fique de tocaia, como faz o ímpio, contra a casa do justo, e não destrua o seu local de repouso;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17096, 16, "pois ainda que o justo caia sete vezes, tornará a erguer-se, mas os ímpios são arrastados pela calamidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17097, 17, "Não se alegre quando o seu inimigo cair, nem exulte o seu coração quando ele tropeçar,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17098, 18, "para que o Senhor não veja isso, e se desagrade, e desvie dele a sua ira.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17099, 19, "Não se aborreça por causa dos maus, nem tenha inveja dos ímpios,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17100, 20, "pois não há futuro para o mau, e a lâmpada dos ímpios se apagará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17101, 21, "Tema ao Senhor e ao rei, meu filho, e não se associe aos dissidentes,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17102, 22, "pois terão repentina destruição, e quem pode imaginar a ruína que o Senhor e o rei podem causar?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17103, 23, "Aqui vão outros ditados dos sábios: Agir com parcialidade nos julgamentos não é nada bom.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17104, 24, "Quem disser ao ímpio: ""Você é justo"", será amaldiçoado pelos povos e sofrerá a indignação das nações.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17105, 25, "Mas os que condenam o culpado terão vida agradável; receberão grandes bênçãos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17106, 26, "A resposta sincera é como beijo nos lábios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17107, 27, "Termine primeiro o seu trabalho a céu aberto; deixe pronta a sua lavoura. Depois constitua família.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17108, 28, "Não testemunhe sem motivo contra o seu próximo nem use os seus lábios para enganá-lo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17109, 29, "Não diga: ""Farei com ele o que fez comigo; ele pagará pelo que fez"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17110, 30, "Passei pelo campo do preguiçoso, pela vinha do homem sem juízo;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17111, 31, "havia espinheiros por toda parte, o chão estava coberto de ervas daninhas e o muro de pedra estava em ruínas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17112, 32, "Observei aquilo, e fiquei pensando, olhei e aprendi esta lição:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17113, 33, """Vou dormir um pouco"", você diz. ""Vou cochilar um momento; vou cruzar os braços e descansar mais um pouco"",", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17114, 34, "mas a pobreza lhe virá como um assaltante, e a sua miséria como um homem armado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17115, 1, "Estes são outros provérbios de Salomão, compilados pelos servos de Ezequias, rei de Judá:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17116, 2, "A glória de Deus é ocultar certas coisas; tentar descobri-las é a glória dos reis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17117, 3, "Assim como o céu é elevado e a terra é profunda, também o coração dos reis é insondável.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17118, 4, "Quando se retira a escória da prata, nesta se tem material para o ourives;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17119, 5, "quando os ímpios são retirados da presença do rei, a justiça firma o seu trono.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17120, 6, "Não se engrandeça na presença do rei, e não reivindique lugar entre os homens importantes;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17121, 7, "é melhor que ele lhe diga: ""Suba para cá! "", do que ter que humilhá-lo diante de uma autoridade. O que você viu com os olhos", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17122, 8, "não leve precipitadamente ao tribunal, pois o que você fará, se o seu próximo o desacreditar?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17123, 9, "Procure resolver sua causa diretamente com o seu próximo, e não revele o segredo de outra pessoa,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17124, 10, "caso contrário, quem o ouvir poderá recriminá-lo e você jamais perderá sua má reputação.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17125, 11, "A palavra proferida no tempo certo é como frutas de ouro incrustadas numa escultura de prata.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17126, 12, "Como brinco de ouro e enfeite de ouro fino é a repreensão dada com sabedoria a quem se dispõe a ouvir.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17127, 13, "Como o frescor da neve na época da colheita é o mensageiro de confiança para aqueles que o enviam; ele revigora o ânimo de seus senhores.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17128, 14, "Como nuvens e ventos sem chuva é aquele que se gaba de presentes que não deu.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17129, 15, "Com muita paciência pode-se convencer a autoridade, e a língua branda quebra até ossos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17130, 16, "Se você encontrar mel, coma apenas o suficiente, para que não fique enjoado e vomite.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17131, 17, "Não faça visitas freqüentes à casa do seu vizinho para que ele não se canse de você e passe a odiá-lo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17132, 18, "Como um pedaço de pau, uma espada ou uma flecha aguda é o que dá falso testemunho contra o seu próximo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17133, 19, "Como dente estragado ou pé deslocado é a confiança no hipócrita na hora da dificuldade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17134, 20, "Como tirar a própria roupa num dia de frio, ou derramar vinagre numa ferida, é cantar com o coração entristecido.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17135, 21, "Se o seu inimigo tiver fome, dê-lhe de comer; se tiver sede, dê-lhe de beber.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17136, 22, "Fazendo isso, você amontoará brasas vivas sobre a cabeça dele, e o Senhor recompensará você.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17137, 23, "Como o vento norte traz chuva, assim a língua fingida traz o olhar irado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17138, 24, "Melhor é viver num canto sob o telhado do que repartir a casa com uma mulher briguenta.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17139, 25, "Como água fresca para a garganta sedenta é a boa notícia que chega de uma terra distante.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17140, 26, "Como fonte contaminada ou nascente poluída, assim é o justo que fraqueja diante do ímpio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17141, 27, "Comer mel demais não é bom, nem é honroso buscar a própria honra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17142, 28, "Como a cidade com seus muros derrubados, assim é quem não sabe dominar-se.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17143, 1, "Como neve no verão ou chuva na colheita, assim a honra é imprópria para o tolo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17144, 2, "Como o pardal que voa em fuga, e a andorinha que esvoaça veloz, assim a maldição sem motivo justo não pega.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17145, 3, "O chicote é para o cavalo, o freio, para o jumento, e a vara, para as costas do tolo!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17146, 4, "Não responda ao insensato com igual insensatez, do contrário você se igualará a ele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17147, 5, "Responda ao insensato como a sua insensatez merece, do contrário ele pensará que é mesmo um sábio.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17148, 6, "Como cortar o próprio pé ou beber veneno, assim é enviar mensagem pelas mãos do tolo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17149, 7, "Como pendem inúteis as pernas do coxo, assim é o provérbio na boca do tolo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17150, 8, "Como amarrar uma pedra na atiradeira, assim é prestar honra ao insensato.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17151, 9, "Como ramo de espinhos nas mãos do bêbado, assim é o provérbio na boca do insensato.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17152, 10, "Como o arqueiro que atira ao acaso, assim é quem contrata o tolo ou o primeiro que passa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17153, 11, "Como o cão volta ao seu vômito, assim o insensato repete a sua insensatez.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17154, 12, "Você conhece alguém que se julga sábio? Há mais esperança para o insensato do que para ele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17155, 13, "O preguiçoso diz: ""Lá está um leão no caminho, um leão feroz rugindo nas ruas! """, 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17156, 14, "Como a porta gira em suas dobradiças, assim o preguiçoso se revira em sua cama.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17157, 15, "O preguiçoso coloca a mão no prato, mas acha difícil demais levá-la de volta à boca.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17158, 16, "O preguiçoso considera-se mais sábio do que sete homens que respondem com bom senso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17159, 17, "Como alguém que pega pelas orelhas um cão qualquer, assim é quem se mete em discussão alheia.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17160, 18, "Como o louco que atira brasas e flechas mortais,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17161, 19, "assim é o homem que engana o seu próximo e diz: ""Eu estava só brincando! """, 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17162, 20, "Sem lenha a fogueira se apaga; sem o caluniador morre a contenda.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17163, 21, "O que o carvão é para as brasas e a lenha para a fogueira, o amigo de brigas é para atiçar discórdias.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17164, 22, "As palavras do caluniador são como petiscos deliciosos; descem saborosos até o íntimo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17165, 23, "Como uma camada de esmalte sobre um vaso de barro, os lábios amistosos podem ocultar um coração mau.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17166, 24, "Quem odeia, disfarça as suas intenções com os lábios, mas no coração abriga a falsidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17167, 25, "Embora a sua conversa seja mansa, não acredite nele, pois o seu coração está cheio de maldade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17168, 26, "Ele pode fingir e esconder o seu ódio, mas a sua maldade será exposta em público.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17169, 27, "Quem faz uma cova, nela cairá; se alguém rola uma pedra, esta rolará de volta sobre ele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17170, 28, "A língua mentirosa odeia aqueles a quem fere, e a boca lisonjeira provoca a ruína.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17171, 1, "Não se gabe do dia de amanhã, pois você não sabe o que este ou aquele dia poderá trazer.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17172, 2, "Que outros façam elogios a você, não a sua própria boca; outras pessoas, não os seus próprios lábios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17173, 3, "A pedra é pesada e a areia é um fardo, mas a irritação causada pelo insensato é mais pesada do que as duas juntas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17174, 4, "O rancor é cruel e a fúria é destrutiva, mas quem consegue suportar a inveja?", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17175, 5, "Melhor é a repreensão feita abertamente do que o amor oculto.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17176, 6, "Quem fere por amor mostra lealdade, mas o inimigo multiplica beijos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17177, 7, "Quem está satisfeito despreza o mel, mas para quem tem fome até o amargo é doce.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17178, 8, "Como a ave que vagueia longe do ninho, assim é o homem que vagueia longe do lar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17179, 9, "Perfume e incenso trazem alegria ao coração; do conselho sincero do homem nasce uma bela amizade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17180, 10, "Não abandone o seu amigo nem o amigo de seu pai; quando for atingido pela adversidade não vá para a casa de seu irmão; melhor é o vizinho próximo do que o irmão distante.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17181, 11, "Seja sábio, meu filho, e traga alegria ao meu coração; poderei então responder a quem me desprezar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17182, 12, "O prudente percebe o perigo e busca refúgio; o inexperiente segue adiante e sofre as conseqüências.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17183, 13, "Tome-se a veste de quem serve de fiador ao estranho; sirva ela de penhor de quem dá garantia a uma mulher leviana.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17184, 14, "A bênção dada aos gritos cedo de manhã, como maldição é recebida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17185, 15, "A esposa briguenta é como o gotejar constante num dia chuvoso;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17186, 16, "detê-la é como deter o vento, como apanhar óleo com a mão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17187, 17, "Assim como o ferro afia o ferro, o homem afia o seu companheiro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17188, 18, "Quem cuida de uma figueira comerá de seu fruto, e quem trata bem o seu senhor receberá tratamento de honra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17189, 19, "Assim como a água reflete o rosto, o coração reflete quem somos nós.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17190, 20, "O Sheol e a Destruição são insaciáveis, como insaciáveis são os olhos do homem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17191, 21, "O crisol é para a prata e o forno é para o ouro, mas o que prova o homem são os elogios que recebe.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17192, 22, "Ainda que você moa o insensato, como trigo no pilão, a insensatez não se afastará dele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17193, 23, "Esforce-se para saber bem como suas ovelhas estão, dê cuidadosa atenção aos seus rebanhos,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17194, 24, "pois as riquezas não duram para sempre, e nada garante que a coroa passe de uma geração a outra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17195, 25, "Quando o feno for retirado, surgirem novos brotos e o capim das colinas for colhido,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17196, 26, "os cordeiros lhe fornecerão roupa, e os bodes lhe renderão o preço de um campo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17197, 27, "Haverá fartura de leite de cabra para alimentar você e sua família, e para sustentar as suas servas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17198, 1, "O ímpio foge, embora ninguém o persiga, mas os justos são corajosos como o leão.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17199, 2, "Os pecados de uma nação fazem mudar sempre os seus governantes, mas a ordem se mantém com um líder sábio e sensato.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17200, 3, "O pobre que se torna poderoso e oprime os pobres é como a tempestade súbita que destrói toda a plantação.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17201, 4, "Os que abandonam a lei elogiam os ímpios, mas os que obedecem à lei lutam contra eles.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17202, 5, "Os homens maus não entendem a justiça, mas os que buscam ao Senhor a entendem plenamente.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17203, 6, "Melhor é o pobre íntegro em sua conduta do que o rico perverso em seus caminhos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17204, 7, "Quem obedece à lei é filho sábio, mas o companheiro dos glutões envergonha o pai.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17205, 8, "Quem aumenta sua riqueza com juros exorbitantes ajunta para algum outro, que será bondoso com os pobres.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17206, 9, "Se alguém se recusa a ouvir a lei, até suas orações serão detestáveis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17207, 10, "Quem leva o homem direito pelo mau caminho cairá ele mesmo na armadilha que preparou, mas o que não se deixa corromper terá boa recompensa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17208, 11, "O rico pode até se julgar sábio, mas o pobre que tem discernimento o conhece a fundo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17209, 12, "Quando os justos triunfam, há prosperidade geral, mas, quando os ímpios sobem ao poder, os homens tratam de esconder-se.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17210, 13, "Quem esconde os seus pecados não prospera, mas quem os confessa e os abandona encontra misericórdia.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17211, 14, "Como é feliz o homem constante no temor do Senhor! Mas quem endurece o coração cairá na desgraça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17212, 15, "Como um leão que ruge ou um urso feroz é o ímpio que governa um povo necessitado.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17213, 16, "O governante sem discernimento aumenta as opressões, mas os que odeiam o ganho desonesto prolongarão o seu governo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17214, 17, "O assassino atormentado pela culpa será fugitivo até a morte; que ninguém o proteja!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17215, 18, "Quem procede com integridade viverá seguro, mas quem procede com perversidade de repente cairá.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17216, 19, "Quem lavra sua terra terá comida com fartura, mas quem persegue fantasias se fartará de miséria.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17217, 20, "O fiel será ricamente abençoado, mas quem tenta enriquecer-se depressa não ficará sem castigo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17218, 21, "Agir com parcialidade não é bom; pois até por um pedaço de pão o homem se dispõe a fazer o mal.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17219, 22, "O invejoso é ávido por riquezas, e não percebe que a pobreza o aguarda.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17220, 23, "Quem repreende o próximo obterá por fim mais favor do que aquele que só sabe bajular.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17221, 24, "Quem rouba seu pai ou sua mãe e diz: ""Não é errado"" é amigo de quem destrói.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17222, 25, "O ganancioso provoca brigas, mas quem confia no Senhor prosperará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17223, 26, "Quem confia em si mesmo é insensato, mas quem anda segundo a sabedoria não corre perigo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17224, 27, "Quem dá aos pobres não passará necessidade, mas quem fecha os olhos para não vê-los sofrerá muitas maldições.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17225, 28, "Quando os ímpios sobem ao poder, o povo se esconde; mas, quando eles sucumbem, os justos florescem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17226, 1, "Quem insiste no erro depois de muita repreensão, será destruído, sem aviso e irremediavelmente.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17227, 2, "Quando os justos florescem, o povo se alegra; quando os ímpios governam, o povo geme.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17228, 3, "O homem que ama a sabedoria dá alegria a seu pai, mas quem anda com prostitutas dá fim à sua fortuna.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17229, 4, "O rei que exerce a justiça dá estabilidade ao país, mas o que gosta de subornos o leva à ruína.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17230, 5, "Quem adula seu próximo está armando uma rede para os pés dele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17231, 6, "O pecado do homem mau o apanha na sua própria armadilha, mas o justo pode cantar e alegrar-se.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17232, 7, "Os justos levam em conta os direitos dos pobres, mas os ímpios nem se importam com isso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17233, 8, "Os zombadores agitam a cidade, mas os sábios a apaziguam.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17234, 9, "Se o sábio for ao tribunal contra o insensato, não haverá paz, pois o insensato se enfurecerá e zombará.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17235, 10, "Os violentos odeiam os honestos e procuram matar o homem íntegro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17236, 11, "O tolo dá vazão à sua ira, mas o sábio domina-se.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17237, 12, "Para o governante que dá ouvidos a mentiras, todos os seus oficiais são ímpios.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17238, 13, "O pobre e o opressor têm algo em comum: O Senhor dá vista a ambos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17239, 14, "Se o rei julga os pobres com justiça, seu trono estará sempre seguro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17240, 15, "A vara da correção dá sabedoria, mas a criança entregue a si mesma envergonha a sua mãe.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17241, 16, "Quando os ímpios prosperam, prospera o pecado, mas os justos verão a queda deles.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17242, 17, "Discipline seu filho, e este lhe dará paz; trará grande prazer à sua alma.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17243, 18, "Onde não há revelação divina, o povo se desvia; mas como é feliz quem obedece à lei!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17244, 19, "Meras palavras não bastam para corrigir o escravo; mesmo que entenda, não reagirá bem.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17245, 20, "Você já viu alguém que se precipita no falar? Há mais esperança para o insensato do que para ele.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17246, 21, "Se alguém mima seu escravo desde jovem, no fim terá tristezas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17247, 22, "O homem irado provoca brigas, e o de gênio violento comete muitos pecados.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17248, 23, "O orgulho do homem o humilha, mas o de espírito humilde obtém honra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17249, 24, "O cúmplice do ladrão odeia a si mesmo; posto sob juramento, não ousa testemunhar.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17250, 25, "Quem teme ao homem cai em armadilhas, mas quem confia no Senhor está seguro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17251, 26, "Muitos desejam os favores do governante, mas é do Senhor que procede a justiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17252, 27, "Os justos detestam os desonestos, já os ímpios detestam os íntegros.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17253, 1, "Ditados de Agur, filho de Jaque; oráculo: Este homem declarou a Itiel; a Itiel e a Ucal:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17254, 2, """Sou o mais tolo dos homens; não tenho o entendimento de um ser humano.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17255, 3, "Não aprendi sabedoria, nem tenho conhecimento do Santo.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17256, 4, "Quem subiu aos céus e desceu? Quem ajuntou nas mãos os ventos? Quem embrulhou as águas em sua capa? Quem fixou todos os limites da terra? Qual é o seu nome, e o nome do seu filho? Conte-me, se você sabe!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17257, 5, """Cada palavra de Deus é comprovadamente pura; ele é um escudo para quem nele se refugia.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17258, 6, "Nada acrescente às palavras dele, do contrário, ele o repreenderá e mostrará que você é mentiroso.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17259, 7, """Duas coisas peço que me dês antes que eu morra:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17260, 8, "Mantém longe de mim a falsidade e a mentira; Não me dês nem pobreza nem riqueza; dá-me apenas o alimento necessário.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17261, 9, "Se não, tendo demais, eu te negaria e te deixaria, e diria: ‘Quem é o Senhor? ’ Se eu ficasse pobre, poderia vir a roubar, desonrando assim o nome do meu Deus.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17262, 10, """Não fale mal do servo ao seu senhor; do contrário, o servo o amaldiçoará, e você levará a culpa.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17263, 11, """Existem os que amaldiçoam seu pai e não abençoam sua mãe;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17264, 12, "os que são puros aos seus próprios olhos e que ainda não foram purificados da sua impureza;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17265, 13, "os que têm olhos altivos e olhar desdenhoso;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17266, 14, "pessoas cujos dentes são espadas e cujas mandíbulas estão armadas de facas para devorarem os necessitados desta terra e os pobres da humanidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17267, 15, """Duas filhas tem a sanguessuga. ‘Dê! Dê! ’, gritam elas. ""Há três coisas que nunca estão satisfeitas, quatro que nunca dizem: ‘É o bastante! ’:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17268, 16, "O Sheol, o ventre estéril, a terra, que nunca se dessedenta, e o fogo, que nunca diz: ‘É o bastante! ’", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17269, 17, """Os olhos de quem zomba do pai, e, zombando, nega obediência à mãe, serão arrancados pelos corvos do vale, e serão devorados pelos filhotes do abutre.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17270, 18, """Há três coisas misteriosas demais para mim, quatro que não consigo entender:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17271, 19, "O caminho do abutre no céu, o caminho da serpente sobre a rocha, o caminho do navio em alto mar, e o caminho do homem com uma moça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17272, 20, """Este é o caminho da adúltera: Ela come e limpa a boca, e diz: ‘Não fiz nada de errado’.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17273, 21, """Três coisas fazem tremer a terra, e quatro ela não pode suportar:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17274, 22, "O escravo que se torna rei, o insensato farto de comida,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17275, 23, "a mulher desprezada que por fim se casa, e a escrava que toma o lugar de sua senhora.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17276, 24, """Quatro seres da terra são pequenos, e, no entanto, muito sábios:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17277, 25, "As formigas, criaturas de pouca força, contudo, armazenam sua comida no verão;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17278, 26, "os coelhos, criaturas sem nenhum poder, contudo, habitam nos penhascos;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17279, 27, "os gafanhotos, que não têm rei, contudo, avançam juntos em fileiras;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17280, 28, "a lagartixa, que se pode apanhar com as mãos, contudo, encontra-se nos palácios dos reis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17281, 29, """Há três seres de andar elegante, quatro que se movem com passo garboso:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17282, 30, "O leão, que é poderoso entre os animais e não foge de ninguém;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17283, 31, "o galo de andar altivo; o bode; e o rei à frente do seu exército.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17284, 32, """Se você agiu como tolo e exaltou-se a si mesmo, ou se planejou o mal, tape a boca com a mão!", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17285, 33, "Pois assim como bater o leite produz manteiga, e assim como torcer o nariz produz sangue, também suscitar a raiva produz contenda"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17286, 1, "Ditados do rei Lemuel; uma exortação que sua mãe lhe fez:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17287, 2, """Ó meu filho, filho do meu ventre, filho de meus votos,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17288, 3, "não gaste sua força com mulheres, seu vigor com aquelas que destroem reis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17289, 4, """Não convém aos reis, ó Lemuel; não convém aos reis beber vinho, não convém aos governantes desejar bebida fermentada,", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17290, 5, "para não suceder que bebam e se esqueçam do que a lei determina, e deixem de fazer justiça aos oprimidos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17291, 6, "Dê bebida fermentada aos que estão prestes a morrer, vinho aos que estão angustiados;", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17292, 7, "para que bebam e se esqueçam da sua pobreza, e não mais se lembrem da sua infelicidade.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17293, 8, """Erga a voz em favor dos que não podem defender-se, seja o defensor de todos os desamparados.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17294, 9, "Erga a voz e julgue com justiça; defenda os direitos dos pobres e dos necessitados"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17295, 10, "Uma esposa exemplar; feliz quem a encontrar! É muito mais valiosa que os rubis.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17296, 11, "Seu marido tem plena confiança nela e nunca lhe falta coisa alguma.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17297, 12, "Ela só lhe faz o bem, e nunca o mal, todos os dias da sua vida.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17298, 13, "Escolhe a lã e o linho e com prazer trabalha com as mãos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17299, 14, "Como os navios mercantes, ela traz de longe as suas provisões.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17300, 15, "Antes de clarear o dia ela se levanta, prepara comida para todos os de casa, e dá tarefas as suas servas.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17301, 16, "Ela avalia um campo e o compra; com o que ganha planta uma vinha.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17302, 17, "Entrega-se com vontade ao seu trabalho; seus braços são fortes e vigorosos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17303, 18, "Administra bem o seu comércio lucrativo, e a sua lâmpada fica acesa durante a noite.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17304, 19, "Nas mãos segura o fuso e com os dedos pega a roca.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17305, 20, "Acolhe os necessitados e estende as mãos aos pobres.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17306, 21, "Não receia a neve por seus familiares, pois todos eles vestem agasalhos.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17307, 22, "Faz cobertas para a sua cama; veste-se de linho fino e de púrpura.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17308, 23, "Seu marido é respeitado na porta da cidade, onde toma assento entre as autoridades da sua terra.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17309, 24, "Ela faz vestes de linho e as vende, e fornece cintos aos comerciantes.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17310, 25, "Reveste-se de força e dignidade; sorri diante do futuro.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17311, 26, "Fala com sabedoria e ensina com amor.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17312, 27, "Cuida dos negócios de sua casa e não dá lugar à preguiça.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17313, 28, "Seus filhos se levantam e a elogiam; seu marido também a elogia, dizendo:", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17314, 29, """Muitas mulheres são exemplares, mas você a todas supera"".", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17315, 30, "A beleza é enganosa, e a formosura é passageira; mas a mulher que teme ao Senhor será elogiada.", 19);
INSERT INTO verses (id, number, text, chapter_id) VALUES (17316, 31, "Que ela receba a recompensa merecida, e as suas obras sejam elogiadas à porta da cidade.", 19);