INSERT INTO verses (id, number, text, chapter_id) VALUES (7128, 1, "Na época dos juízes houve fome na terra. Um homem de Belém de Judá, com a mulher e os dois filhos, foi viver por algum tempo nas terras de Moabe.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7129, 2, "O homem chamava-se Elimeleque, sua mulher Noemi e seus dois filhos Malom e Quiliom. Eram efrateus de Belém de Judá. Chegaram a Moabe, e lá ficaram.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7130, 3, "Morreu Elimeleque, marido de Noemi, e ela ficou sozinha, com seus dois filhos.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7131, 4, "Eles se casaram com mulheres moabitas, uma chamada Orfa e a outra Rute. Depois de terem morado lá por quase dez anos,", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7132, 5, "morreram também Malom e Quiliom, e Noemi ficou sozinha, sem os dois filhos e o seu marido.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7133, 6, "Quando Noemi soube em Moabe que o Senhor viera em auxílio do seu povo, dando-lhe alimento, decidiu voltar com suas duas noras para a sua terra.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7134, 7, "Assim ela, com as duas noras, partiu do lugar onde tinha morado. Enquanto voltavam para a terra de Judá,", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7135, 8, "Noemi disse às duas noras: ""Vão! Voltem para a casa de suas mães! Que o Senhor seja leal com vocês, como vocês foram leais com os falecidos e comigo.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7136, 9, "O Senhor conceda que cada uma de vocês encontre segurança no lar doutro marido"". Então deu-lhes beijos de despedida. Mas elas começaram a chorar bem alto", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7137, 10, "e lhe disseram: ""Não! Voltaremos com você para junto de seu povo! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7138, 11, "Disse, porém, Noemi: ""Voltem, minhas filhas! Por que viriam comigo? Poderia eu ainda ter filhos, que viessem a ser seus maridos?", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7139, 12, "Voltem, minhas filhas! Vão! Estou velha demais para ter outro marido. E mesmo que eu pensasse que ainda há esperança para mim — ainda que eu me casasse esta noite e depois desse à luz filhos,", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7140, 13, "iriam vocês esperar até que eles crescessem? Ficariam sem se casar à espera deles? De jeito nenhum minhas filhas! Para mim é mais amargo do que para vocês, pois a mão do Senhor voltou-se contra mim! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7141, 14, "Elas então começaram a choram bem alto de novo. Depois Orfa deu um beijo de despedida em sua sogra, mas Rute ficou com ela.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7142, 15, "Então Noemi a aconselhou: ""Veja, sua concunhada está voltando para o seu povo e para o seu deus. Volte com ela! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7143, 16, "Rute, porém, respondeu: ""Não insistas comigo que te deixe e não mais a acompanhe. Aonde fores irei, onde ficares ficarei! O teu povo será o meu povo e o teu Deus será o meu Deus!", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7144, 17, "Onde morreres morrerei, e ali serei sepultada. Que o Senhor me castigue com todo o rigor, se outra coisa que não a morte me separar de ti! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7145, 18, "Quando Noemi viu que Rute estava de fato decidida a acompanhá-la, não insistiu mais.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7146, 19, "Prosseguiram, pois, as duas até Belém. Ali chegando, todo o povoado ficou alvoroçado por causa delas. ""Será que é Noemi? "", perguntavam as mulheres.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7147, 20, "Mas ela respondeu: ""Não me chamem Noemi, chamem-me Mara, pois o Todo-poderoso tornou minha vida muito amarga!", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7148, 21, "De mãos cheias eu parti; mas de mãos vazias o Senhor me trouxe de volta. Por que me chamam Noemi? O Senhor colocou-se contra mim! O Todo-poderoso me trouxe desgraça! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7149, 22, "Foi assim que Noemi voltou das terras de Moabe, com sua nora Rute, a moabita. Elas chegaram a Belém no início da colheita da cevada.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7150, 1, "Noemi tinha um parente por parte do marido. Era um homem rico e influente, pertencia ao clã de Elimeleque e chamava-se Boaz.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7151, 2, "Rute, a moabita, disse a Noemi: ""Vou recolher espigas no campo daquele que me permitir"". ""Vá, minha filha"", respondeu-lhe Noemi.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7152, 3, "Então ela foi e começou a recolher espigas atrás dos ceifeiros. Por acaso entrou justamente na parte da plantação que pertencia a Boaz, que era do clã de Elimeleque.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7153, 4, "Naquele exato momento, Boaz chegou de Belém e saudou os ceifeiros: ""O Senhor esteja com vocês! "" Eles responderam: ""O Senhor te abençoe! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7154, 5, "Boaz perguntou ao capataz dos ceifeiros: ""A quem pertence aquela moça? """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7155, 6, "O capataz respondeu: ""É uma moabita que voltou de Moabe com Noemi.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7156, 7, "Ela me pediu que a deixasse recolher e juntar espigas entre os feixes, após os ceifeiros. Ela chegou cedo e está de pé até agora. Só sentou-se um pouco no abrigo"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7157, 8, "Disse então Boaz a Rute: ""Ouça bem, minha filha, não vá colher noutra lavoura, nem se afaste daqui. Fique com minhas servas.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7158, 9, "Preste atenção onde os homens estão ceifando, e vá atrás das moças que vão colher. Darei ordem aos rapazes para que não toquem em você. Quando tiver sede, beba da água dos potes que os rapazes encheram"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7159, 10, "Ela se inclinou e, prostrada rosto em terra, exclamou: ""Por que achei favor a seus olhos, a ponto de o senhor se importar comigo, uma estrangeira? """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7160, 11, "Boaz respondeu: ""Contaram-me tudo o que você tem feito por sua sogra, depois que você perdeu o marido: como deixou seu pai, sua mãe e sua terra natal para viver com um povo que pouco conhecia.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7161, 12, "O Senhor lhe retribua o que você tem feito! Que você seja ricamente recompensada pelo Senhor, o Deus de Israel, sob cujas asas você veio buscar refúgio! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7162, 13, "E disse ela: ""Continue eu a ser bem acolhida, meu senhor! O senhor me deu ânimo e encorajou sua serva — e eu sequer sou como uma de suas servas! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7163, 14, "Na hora da refeição, Boaz lhe disse: ""Venha cá! Pegue um pedaço de pão e molhe-o no vinagre"". Quando ela se sentou junto aos ceifeiros, Boaz lhe ofereceu grãos tostados. Ela comeu até ficar satisfeita e ainda sobrou.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7164, 15, "Quando ela se levantou para recolher, Boaz deu estas ordens a seus servos: ""Mesmo que ela recolha entre os feixes, não a repreendam!", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7165, 16, "Pelo contrário, quando estiverem colhendo, tirem para ela algumas espigas dos feixes e deixem-nas cair para que ela as recolha, e não a impeçam"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7166, 17, "E assim Rute colheu na lavoura até o entardecer. Depois debulhou o que tinha ajuntado: quase uma arroba de cevada.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7167, 18, "Carregou-o para o povoado, e sua sogra viu o quanto ela havia recolhido quando Rute trouxe e lhe ofereceu o que havia sobrado da refeição.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7168, 19, """Onde você colheu hoje? "", a sogra lhe perguntou: ""Onde trabalhou? Bendito seja aquele que se importou com você! "" Então Rute contou à sogra com quem tinha trabalhado: ""O nome do homem com quem trabalhei hoje é Boaz"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7169, 20, "E Noemi exclamou: ""Seja ele abençoado pelo Senhor, que não deixa de ser leal e bondoso com os vivos e com os mortos! "" E acrescentou: ""Aquele homem é nosso parente; é um de nossos resgatadores! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7170, 21, "Continuou Rute, a moabita: ""Pois ele mesmo me disse também: ‘Fique com os meus ceifeiros até que terminem toda a minha colheita’ "".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7171, 22, "E Noemi aconselhou à sua nora Rute: ""É melhor mesmo você ir com as servas dele, minha filha. Noutra lavoura poderiam molestá-la"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7172, 23, "Assim Rute ficou com as servas de Boaz para recolher espigas, até acabarem as colheitas de cevada e de trigo. Entretanto, ela ficou morando com a sua sogra.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7173, 1, "Certo dia, Noemi, sua sogra, lhe disse: ""Minha filha, tenho que procurar um lar seguro, para sua felicidade.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7174, 2, "Boaz, aquele com cujas servas você esteve, é nosso parente próximo. Esta noite ele estará limpando cevada na eira.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7175, 3, "Lave-se, perfume-se, vista sua melhor roupa e desça para a eira. Mas não deixe que ele perceba você até que tenha comido e bebido.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7176, 4, "Quando ele for dormir, note bem o lugar em que ele se deitar. Então vá, descubra os pés dele e deite-se. Ele lhe dirá o que fazer"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7177, 5, "Respondeu Rute: ""Farei tudo o que você está me dizendo"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7178, 6, "Então ela desceu para a eira e fez tudo o que a sua sogra lhe tinha recomendado.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7179, 7, "Quando Boaz terminou de comer e beber, ficou alegre e foi deitar-se perto do monte de grãos. Rute aproximou-se sem ser notada, descobriu os pés dele, e deitou-se.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7180, 8, "No meio da noite, o homem acordou de repente. Ele se virou e assustou-se ao ver uma mulher deitada a seus pés.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7181, 9, """Quem é você? "", perguntou ele. ""Sou sua serva Rute"", disse ela. ""Estenda a sua capa sobre a sua serva, pois o senhor é resgatador. """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7182, 10, "Boaz lhe respondeu: ""O Senhor a abençoe, minha filha! Este seu gesto de bondade é ainda maior do que o primeiro, pois você poderia ter ido atrás dos mais jovens, ricos ou pobres!", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7183, 11, "Agora, minha filha, não tenha medo; farei por você tudo o que me pedir. Todos os meus concidadãos sabem que você é mulher virtuosa.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7184, 12, "É verdade que sou resgatador, mas há um outro que é parente mais próximo do que eu.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7185, 13, "Passe a noite aqui. De manhã veremos: se ele quiser resgatá-la, muito bem, que resgate. Se não quiser, juro pelo nome do Senhor que eu a resgatarei. Deite-se aqui até de manhã"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7186, 14, "Ela ficou deitada aos pés dele até de manhã, mas levantou-se antes de clarear a ponto de alguém poder ser reconhecido. Boaz pensou: ""Ninguém deve saber que esta mulher esteve na eira"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7187, 15, "Por isso disse: ""Traga-me o manto que você está usando e segure-o"". Ela o segurou, e o homem despejou nele seis medidas de cevada e o pôs sobre os ombros dela. Depois ele voltou para a cidade.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7188, 16, "Quando Rute voltou à sua sogra, esta lhe perguntou: ""Como foi, minha filha? "" Rute lhe contou tudo o que Boaz lhe tinha feito,", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7189, 17, "e acrescentou: ""Ele me deu estas seis medidas de cevada, dizendo: ‘Não volte para a sua sogra de mãos vazias’ "".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7190, 18, "Disse então Noemi: ""Agora espere, minha filha, até saber o que acontecerá. Sem dúvida aquele homem não descansará enquanto não resolver esta questão hoje mesmo"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7191, 1, "Enquanto isso, Boaz subiu à porta da cidade e sentou-se ali exatamente quando o resgatador que ele havia mencionado estava passando por ali. Boaz chamou-lhe e disse: ""Meu amigo, venha cá e sente-se"". Ele foi e sentou-se.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7192, 2, "Boaz reuniu dez líderes da cidade e disse: ""Sentem-se aqui"". E eles se sentaram.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7193, 3, "Depois disse ao resgatador: ""Noemi, que voltou de Moabe, está vendendo o pedaço de terra que pertencia ao nosso irmão Elimeleque.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7194, 4, "Pensei que devia trazer o assunto para a sua consideração e sugerir-lhe que o adquira, na presença destes que aqui estão sentados e na presença dos líderes do meu povo. Se quer resgatar esta propriedade, resgate-a. Se não, diga-me, para que eu o saiba. Pois ninguém tem esse direito, a não ser você; e depois eu"". ""Eu a resgatarei"", respondeu ele.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7195, 5, "Boaz, porém, lhe disse: ""No dia em que você adquirir as terras de Noemi e da moabita Rute, estará adquirindo também a viúva do falecido, para manter o nome dele em sua herança"".", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7196, 6, "Diante disso, o resgatador respondeu: ""Nesse caso não poderei resgatá-la, pois poria em risco a minha propriedade. Resgate-a você mesmo. Eu não poderei fazê-lo! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7197, 7, "( Antigamente, em Israel, para que o resgate e a transferência de propriedade fossem válidos, a pessoa tirava a sandália e a dava ao outro. Assim oficializavam os negócios em Israel. )", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7198, 8, "Quando, pois, o resgatador disse a Boaz: ""Adquira-a você mesmo! "", ele também tirou a sandália.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7199, 9, "Então Boaz anunciou aos líderes e a todo o povo ali presente: ""Vocês hoje são testemunhas de que estou adquirindo de Noemi toda a propriedade de Elimeleque, de Quiliom e de Malom.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7200, 10, "Também estou adquirindo o direito de ter como mulher a moabita Rute, viúva de Malom, para manter o nome do falecido sobre a sua herança e para que o seu nome não desapareça do meio da sua família ou dos registros da cidade. Vocês hoje são testemunhas disso! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7201, 11, "Os líderes e todos os que estavam na porta confirmaram: ""Somos testemunhas! Faça o Senhor com essa mulher que está entrando em sua família, como fez com Raquel e Lia, que juntas formaram as tribos de Israel. Seja poderoso em Efrata e ganhe fama em Belém!", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7202, 12, "E com os filhos que o Senhor lhe conceder dessa jovem, seja a sua família como a de Perez, que Tamar deu a Judá! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7203, 13, "Boaz casou-se com Rute, e ela se tornou sua mulher. Boaz a possuiu, e o Senhor concedeu que ela engravidasse e desse à luz um filho.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7204, 14, "As mulheres disseram a Noemi: ""Louvado seja o Senhor, que hoje não a deixou sem resgatador! Que o seu nome seja celebrado em Israel!", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7205, 15, "O menino lhe dará nova vida e a sustentará na velhice, pois é filho da sua nora, que a ama e que lhe é melhor do que sete filhos! """, 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7206, 16, "Noemi pôs o menino no colo, e passou a cuidar dele.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7207, 17, "As mulheres da vizinhança celebraram o seu nome e disseram: ""Noemi tem um filho! "" e lhe deram o nome de Obede. Este foi o pai de Jessé, pai de Davi.", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7208, 18, "Esta é a história da descendência de Perez: Perez gerou Hezrom;", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7209, 19, "Hezrom gerou Rão; Rão gerou Aminadabe;", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7210, 20, "Aminadabe gerou Naassom; Naassom gerou Salmom;", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7211, 21, "Salmom gerou Boaz; Boaz gerou Obede;", 7);
INSERT INTO verses (id, number, text, chapter_id) VALUES (7212, 22, "Obede gerou Jessé; e Jessé gerou Davi.", 7);